(3073, NULL, NULL, 26, 141, 'كَذَّبَتْ ثَمُودُ ٱلْمُرْسَلِينَ', 'كذبت ثمود المرسلين', 19, 38, 373, 149, '26:141'),
(3074, NULL, NULL, 26, 142, 'إِذْ قَالَ لَهُمْ أَخُوهُمْ صَٰلِحٌ أَلَا تَتَّقُونَ', 'إذ قال لهم أخوهم صالح ألا تتقون', 19, 38, 373, 149, '26:142'),
(3075, NULL, NULL, 26, 143, 'إِنِّى لَكُمْ رَسُولٌ أَمِينٌ', 'إني لكم رسول أمين', 19, 38, 373, 149, '26:143'),
(3076, NULL, NULL, 26, 144, 'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ', 'فاتقوا الله وأطيعون', 19, 38, 373, 149, '26:144'),
