(3081, NULL, NULL, 26, 149, 'وَتَنْحِتُونَ مِنَ ٱلْجِبَالِ بُيُوتًا فَٰرِهِينَ', 'وتنحتون من الجبال بيوتا فارهين', 19, 38, 373, 149, '26:149'),
(3082, NULL, NULL, 26, 150, 'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ', 'فاتقوا الله وأطيعون', 19, 38, 373, 149, '26:150'),
(3083, NULL, NULL, 26, 151, 'وَلَا تُطِيعُوٓا۟ أَمْرَ ٱلْمُسْرِفِينَ', 'ولا تطيعوا أمر المسرفين', 19, 38, 373, 149, '26:151'),
(3084, NULL, NULL, 26, 152, 'ٱلَّذِينَ يُفْسِدُونَ فِى ٱلْأَرْضِ وَلَا يُصْلِحُونَ', 'الذين يفسدون في الأرض ولا يصلحون', 19, 38, 373, 149, '26:152'),
(3085, NULL, NULL, 26, 153, 'قَالُوٓا۟ إِنَّمَآ أَنتَ مِنَ ٱلْمُسَحَّرِينَ', 'قالوا إنما أنت من المسحرين', 19, 38, 373, 149, '26:153'),
(3086, NULL, NULL, 26, 154, 'مَآ أَنتَ إِلَّا بَشَرٌ مِّثْلُنَا فَأْتِ بِـَٔايَةٍ إِن كُنتَ مِنَ ٱلصَّٰدِقِينَ', 'ما أنت إلا بشر مثلنا فأت بآية إن كنت من الصادقين', 19, 38, 373, 149, '26:154'),
(3087, NULL, NULL, 26, 155, 'قَالَ هَٰذِهِۦ نَاقَةٌ لَّهَا شِرْبٌ وَلَكُمْ شِرْبُ يَوْمٍ مَّعْلُومٍ', 'قال هذه ناقة لها شرب ولكم شرب يوم معلوم', 19, 38, 373, 149, '26:155'),
(3088, NULL, NULL, 26, 156, 'وَلَا تَمَسُّوهَا بِسُوٓءٍ فَيَأْخُذَكُمْ عَذَابُ يَوْمٍ عَظِيمٍ', 'ولا تمسوها بسوء فيأخذكم عذاب يوم عظيم', 19, 38, 373, 149, '26:156'),
(3089, NULL, NULL, 26, 157, 'فَعَقَرُوهَا فَأَصْبَحُوا۟ نَٰدِمِينَ', 'فعقروها فأصبحوا نادمين', 19, 38, 373, 149, '26:157'),
(3090, NULL, NULL, 26, 158, 'فَأَخَذَهُمُ ٱلْعَذَابُ إِنَّ فِى ذَٰلِكَ لَءَايَةً وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ', 'فأخذهم العذاب إن في ذلك لآية وما كان أكثرهم مؤمنين', 19, 38, 373, 149, '26:158'),
(3091, NULL, NULL, 26, 159, 'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ', 'وإن ربك لهو العزيز الرحيم', 19, 38, 373, 149, '26:159'),
(3092, NULL, NULL, 26, 160, 'كَذَّبَتْ قَوْمُ لُوطٍ ٱلْمُرْسَلِينَ', 'كذبت قوم لوط المرسلين', 19, 38, 374, 149, '26:160'),
(3093, NULL, NULL, 26, 161, 'إِذْ قَالَ لَهُمْ أَخُوهُمْ لُوطٌ أَلَا تَتَّقُونَ', 'إذ قال لهم أخوهم لوط ألا تتقون', 19, 38, 374, 149, '26:161'),
(3094, NULL, NULL, 26, 162, 'إِنِّى لَكُمْ رَسُولٌ أَمِينٌ', 'إني لكم رسول أمين', 19, 38, 374, 149, '26:162'),
(3095, NULL, NULL, 26, 163, 'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ', 'فاتقوا الله وأطيعون', 19, 38, 374, 149, '26:163'),
(3096, NULL, NULL, 26, 164, 'وَمَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِىَ إِلَّا عَلَىٰ رَبِّ ٱلْعَٰلَمِينَ', 'وما أسألكم عليه من أجر إن أجري إلا على رب العالمين', 19, 38, 374, 149, '26:164'),
(3097, NULL, NULL, 26, 165, 'أَتَأْتُونَ ٱلذُّكْرَانَ مِنَ ٱلْعَٰلَمِينَ', 'أتأتون الذكران من العالمين', 19, 38, 374, 149, '26:165'),
(3098, NULL, NULL, 26, 166, 'وَتَذَرُونَ مَا خَلَقَ لَكُمْ رَبُّكُم مِّنْ أَزْوَٰجِكُم بَلْ أَنتُمْ قَوْمٌ عَادُونَ', 'وتذرون ما خلق لكم ربكم من أزواجكم بل أنتم قوم عادون', 19, 38, 374, 149, '26:166'),
(3099, NULL, NULL, 26, 167, 'قَالُوا۟ لَئِن لَّمْ تَنتَهِ يَٰلُوطُ لَتَكُونَنَّ مِنَ ٱلْمُخْرَجِينَ', 'قالوا لئن لم تنته يا لوط لتكونن من المخرجين', 19, 38, 374, 149, '26:167'),
(3100, NULL, NULL, 26, 168, 'قَالَ إِنِّى لِعَمَلِكُم مِّنَ ٱلْقَالِينَ', 'قال إني لعملكم من القالين', 19, 38, 374, 149, '26:168'),
(3101, NULL, NULL, 26, 169, 'رَبِّ نَجِّنِى وَأَهْلِى مِمَّا يَعْمَلُونَ', 'رب نجني وأهلي مما يعملون', 19, 38, 374, 149, '26:169'),
(3102, NULL, NULL, 26, 170, 'فَنَجَّيْنَٰهُ وَأَهْلَهُۥٓ أَجْمَعِينَ', 'فنجيناه وأهله أجمعين', 19, 38, 374, 149, '26:170'),
(3103, NULL, NULL, 26, 171, 'إِلَّا عَجُوزًا فِى ٱلْغَٰبِرِينَ', 'إلا عجوزا في الغابرين', 19, 38, 374, 149, '26:171'),
(3104, NULL, NULL, 26, 172, 'ثُمَّ دَمَّرْنَا ٱلْءَاخَرِينَ', 'ثم دمرنا الآخرين', 19, 38, 374, 149, '26:172'),
(3105, NULL, NULL, 26, 173, 'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًا فَسَآءَ مَطَرُ ٱلْمُنذَرِينَ', 'وأمطرنا عليهم مطرا فساء مطر المنذرين', 19, 38, 374, 149, '26:173'),
(3106, NULL, NULL, 26, 174, 'إِنَّ فِى ذَٰلِكَ لَءَايَةً وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ', 'إن في ذلك لآية وما كان أكثرهم مؤمنين', 19, 38, 374, 149, '26:174'),
(3107, NULL, NULL, 26, 175, 'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ', 'وإن ربك لهو العزيز الرحيم', 19, 38, 374, 149, '26:175'),
(3108, NULL, NULL, 26, 176, 'كَذَّبَ أَصْحَٰبُ لْـَٔيْكَةِ ٱلْمُرْسَلِينَ', 'كذب أصحاب الأيكة المرسلين', 19, 38, 374, 149, '26:176'),
(3109, NULL, NULL, 26, 177, 'إِذْ قَالَ لَهُمْ شُعَيْبٌ أَلَا تَتَّقُونَ', 'إذ قال لهم شعيب ألا تتقون', 19, 38, 374, 149, '26:177'),
(3110, NULL, NULL, 26, 178, 'إِنِّى لَكُمْ رَسُولٌ أَمِينٌ', 'إني لكم رسول أمين', 19, 38, 374, 149, '26:178'),
(3111, NULL, NULL, 26, 179, 'فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ', 'فاتقوا الله وأطيعون', 19, 38, 374, 149, '26:179'),
(3112, NULL, NULL, 26, 180, 'وَمَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍ إِنْ أَجْرِىَ إِلَّا عَلَىٰ رَبِّ ٱلْعَٰلَمِينَ', 'وما أسألكم عليه من أجر إن أجري إلا على رب العالمين', 19, 38, 374, 149, '26:180'),
(3113, NULL, NULL, 26, 181, 'أَوْفُوا۟ ٱلْكَيْلَ وَلَا تَكُونُوا۟ مِنَ ٱلْمُخْسِرِينَ', 'أوفوا الكيل ولا تكونوا من المخسرين', 19, 38, 374, 150, '26:181'),
(3114, NULL, NULL, 26, 182, 'وَزِنُوا۟ بِٱلْقِسْطَاسِ ٱلْمُسْتَقِيمِ', 'وزنوا بالقسطاس المستقيم', 19, 38, 374, 150, '26:182'),
(3115, NULL, NULL, 26, 183, 'وَلَا تَبْخَسُوا۟ ٱلنَّاسَ أَشْيَآءَهُمْ وَلَا تَعْثَوْا۟ فِى ٱلْأَرْضِ مُفْسِدِينَ', 'ولا تبخسوا الناس أشياءهم ولا تعثوا في الأرض مفسدين', 19, 38, 374, 150, '26:183'),
(3116, NULL, NULL, 26, 184, 'وَٱتَّقُوا۟ ٱلَّذِى خَلَقَكُمْ وَٱلْجِبِلَّةَ ٱلْأَوَّلِينَ', 'واتقوا الذي خلقكم والجبلة الأولين', 19, 38, 375, 150, '26:184'),
(3117, NULL, NULL, 26, 185, 'قَالُوٓا۟ إِنَّمَآ أَنتَ مِنَ ٱلْمُسَحَّرِينَ', 'قالوا إنما أنت من المسحرين', 19, 38, 375, 150, '26:185'),
(3118, NULL, NULL, 26, 186, 'وَمَآ أَنتَ إِلَّا بَشَرٌ مِّثْلُنَا وَإِن نَّظُنُّكَ لَمِنَ ٱلْكَٰذِبِينَ', 'وما أنت إلا بشر مثلنا وإن نظنك لمن الكاذبين', 19, 38, 375, 150, '26:186'),
(3119, NULL, NULL, 26, 187, 'فَأَسْقِطْ عَلَيْنَا كِسَفًا مِّنَ ٱلسَّمَآءِ إِن كُنتَ مِنَ ٱلصَّٰدِقِينَ', 'فأسقط علينا كسفا من السماء إن كنت من الصادقين', 19, 38, 375, 150, '26:187'),
(3120, NULL, NULL, 26, 188, 'قَالَ رَبِّىٓ أَعْلَمُ بِمَا تَعْمَلُونَ', 'قال ربي أعلم بما تعملون', 19, 38, 375, 150, '26:188'),
(3121, NULL, NULL, 26, 189, 'فَكَذَّبُوهُ فَأَخَذَهُمْ عَذَابُ يَوْمِ ٱلظُّلَّةِ إِنَّهُۥ كَانَ عَذَابَ يَوْمٍ عَظِيمٍ', 'فكذبوه فأخذهم عذاب يوم الظلة إنه كان عذاب يوم عظيم', 19, 38, 375, 150, '26:189'),
(3122, NULL, NULL, 26, 190, 'إِنَّ فِى ذَٰلِكَ لَءَايَةً وَمَا كَانَ أَكْثَرُهُم مُّؤْمِنِينَ', 'إن في ذلك لآية وما كان أكثرهم مؤمنين', 19, 38, 375, 150, '26:190'),
(3123, NULL, NULL, 26, 191, 'وَإِنَّ رَبَّكَ لَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ', 'وإن ربك لهو العزيز الرحيم', 19, 38, 375, 150, '26:191'),
(3124, NULL, NULL, 26, 192, 'وَإِنَّهُۥ لَتَنزِيلُ رَبِّ ٱلْعَٰلَمِينَ', 'وإنه لتنزيل رب العالمين', 19, 38, 375, 150, '26:192'),
(3125, NULL, NULL, 26, 193, 'نَزَلَ بِهِ ٱلرُّوحُ ٱلْأَمِينُ', 'نزل به الروح الأمين', 19, 38, 375, 150, '26:193'),
(3126, NULL, NULL, 26, 194, 'عَلَىٰ قَلْبِكَ لِتَكُونَ مِنَ ٱلْمُنذِرِينَ', 'على قلبك لتكون من المنذرين', 19, 38, 375, 150, '26:194'),
(3127, NULL, NULL, 26, 195, 'بِلِسَانٍ عَرَبِىٍّ مُّبِينٍ', 'بلسان عربي مبين', 19, 38, 375, 150, '26:195'),
(3128, NULL, NULL, 26, 196, 'وَإِنَّهُۥ لَفِى زُبُرِ ٱلْأَوَّلِينَ', 'وإنه لفي زبر الأولين', 19, 38, 375, 150, '26:196'),
(3129, NULL, NULL, 26, 197, 'أَوَلَمْ يَكُن لَّهُمْ ءَايَةً أَن يَعْلَمَهُۥ عُلَمَٰٓؤُا۟ بَنِىٓ إِسْرَٰٓءِيلَ', 'أولم يكن لهم آية أن يعلمه علماء بني إسرائيل', 19, 38, 375, 150, '26:197'),
(3130, NULL, NULL, 26, 198, 'وَلَوْ نَزَّلْنَٰهُ عَلَىٰ بَعْضِ ٱلْأَعْجَمِينَ', 'ولو نزلناه على بعض الأعجمين', 19, 38, 375, 150, '26:198'),
(3131, NULL, NULL, 26, 199, 'فَقَرَأَهُۥ عَلَيْهِم مَّا كَانُوا۟ بِهِۦ مُؤْمِنِينَ', 'فقرأه عليهم ما كانوا به مؤمنين', 19, 38, 375, 150, '26:199'),
(3132, NULL, NULL, 26, 200, 'كَذَٰلِكَ سَلَكْنَٰهُ فِى قُلُوبِ ٱلْمُجْرِمِينَ', 'كذلك سلكناه في قلوب المجرمين', 19, 38, 375, 150, '26:200'),
(3133, NULL, NULL, 26, 201, 'لَا يُؤْمِنُونَ بِهِۦ حَتَّىٰ يَرَوُا۟ ٱلْعَذَابَ ٱلْأَلِيمَ', 'لا يؤمنون به حتى يروا العذاب الأليم', 19, 38, 375, 150, '26:201'),
(3134, NULL, NULL, 26, 202, 'فَيَأْتِيَهُم بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 'فيأتيهم بغتة وهم لا يشعرون', 19, 38, 375, 150, '26:202'),
(3135, NULL, NULL, 26, 203, 'فَيَقُولُوا۟ هَلْ نَحْنُ مُنظَرُونَ', 'فيقولوا هل نحن منظرون', 19, 38, 375, 150, '26:203'),
(3136, NULL, NULL, 26, 204, 'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ', 'أفبعذابنا يستعجلون', 19, 38, 375, 150, '26:204'),
(3137, NULL, NULL, 26, 205, 'أَفَرَءَيْتَ إِن مَّتَّعْنَٰهُمْ سِنِينَ', 'أفرأيت إن متعناهم سنين', 19, 38, 375, 150, '26:205'),
(3138, NULL, NULL, 26, 206, 'ثُمَّ جَآءَهُم مَّا كَانُوا۟ يُوعَدُونَ', 'ثم جاءهم ما كانوا يوعدون', 19, 38, 375, 150, '26:206'),
(3139, NULL, NULL, 26, 207, 'مَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يُمَتَّعُونَ', 'ما أغنى عنهم ما كانوا يمتعون', 19, 38, 376, 150, '26:207'),
(3140, NULL, NULL, 26, 208, 'وَمَآ أَهْلَكْنَا مِن قَرْيَةٍ إِلَّا لَهَا مُنذِرُونَ', 'وما أهلكنا من قرية إلا لها منذرون', 19, 38, 376, 150, '26:208'),
(3141, NULL, NULL, 26, 209, 'ذِكْرَىٰ وَمَا كُنَّا ظَٰلِمِينَ', 'ذكرى وما كنا ظالمين', 19, 38, 376, 150, '26:209'),
(3142, NULL, NULL, 26, 210, 'وَمَا تَنَزَّلَتْ بِهِ ٱلشَّيَٰطِينُ', 'وما تنزلت به الشياطين', 19, 38, 376, 150, '26:210'),
(3143, NULL, NULL, 26, 211, 'وَمَا يَنۢبَغِى لَهُمْ وَمَا يَسْتَطِيعُونَ', 'وما ينبغي لهم وما يستطيعون', 19, 38, 376, 150, '26:211'),
(3144, NULL, NULL, 26, 212, 'إِنَّهُمْ عَنِ ٱلسَّمْعِ لَمَعْزُولُونَ', 'إنهم عن السمع لمعزولون', 19, 38, 376, 150, '26:212'),
(3145, NULL, NULL, 26, 213, 'فَلَا تَدْعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَتَكُونَ مِنَ ٱلْمُعَذَّبِينَ', 'فلا تدع مع الله إلها آخر فتكون من المعذبين', 19, 38, 376, 150, '26:213'),
(3146, NULL, NULL, 26, 214, 'وَأَنذِرْ عَشِيرَتَكَ ٱلْأَقْرَبِينَ', 'وأنذر عشيرتك الأقربين', 19, 38, 376, 150, '26:214'),
(3147, NULL, NULL, 26, 215, 'وَٱخْفِضْ جَنَاحَكَ لِمَنِ ٱتَّبَعَكَ مِنَ ٱلْمُؤْمِنِينَ', 'واخفض جناحك لمن اتبعك من المؤمنين', 19, 38, 376, 150, '26:215'),
(3148, NULL, NULL, 26, 216, 'فَإِنْ عَصَوْكَ فَقُلْ إِنِّى بَرِىٓءٌ مِّمَّا تَعْمَلُونَ', 'فإن عصوك فقل إني بريء مما تعملون', 19, 38, 376, 150, '26:216'),
(3149, NULL, NULL, 26, 217, 'وَتَوَكَّلْ عَلَى ٱلْعَزِيزِ ٱلرَّحِيمِ', 'وتوكل على العزيز الرحيم', 19, 38, 376, 150, '26:217'),
(3150, NULL, NULL, 26, 218, 'ٱلَّذِى يَرَىٰكَ حِينَ تَقُومُ', 'الذي يراك حين تقوم', 19, 38, 376, 150, '26:218'),
(3151, NULL, NULL, 26, 219, 'وَتَقَلُّبَكَ فِى ٱلسَّٰجِدِينَ', 'وتقلبك في الساجدين', 19, 38, 376, 150, '26:219'),
(3152, NULL, NULL, 26, 220, 'إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ', 'إنه هو السميع العليم', 19, 38, 376, 150, '26:220'),
(3153, NULL, NULL, 26, 221, 'هَلْ أُنَبِّئُكُمْ عَلَىٰ مَن تَنَزَّلُ ٱلشَّيَٰطِينُ', 'هل أنبئكم على من تنزل الشياطين', 19, 38, 376, 150, '26:221'),
(3154, NULL, NULL, 26, 222, 'تَنَزَّلُ عَلَىٰ كُلِّ أَفَّاكٍ أَثِيمٍ', 'تنزل على كل أفاك أثيم', 19, 38, 376, 150, '26:222'),
(3155, NULL, NULL, 26, 223, 'يُلْقُونَ ٱلسَّمْعَ وَأَكْثَرُهُمْ كَٰذِبُونَ', 'يلقون السمع وأكثرهم كاذبون', 19, 38, 376, 150, '26:223'),
(3156, NULL, NULL, 26, 224, 'وَٱلشُّعَرَآءُ يَتَّبِعُهُمُ ٱلْغَاوُۥنَ', 'والشعراء يتبعهم الغاوون', 19, 38, 376, 150, '26:224'),
(3157, NULL, NULL, 26, 225, 'أَلَمْ تَرَ أَنَّهُمْ فِى كُلِّ وَادٍ يَهِيمُونَ', 'ألم تر أنهم في كل واد يهيمون', 19, 38, 376, 150, '26:225'),
(3158, NULL, NULL, 26, 226, 'وَأَنَّهُمْ يَقُولُونَ مَا لَا يَفْعَلُونَ', 'وأنهم يقولون ما لا يفعلون', 19, 38, 376, 150, '26:226'),
(3159, NULL, NULL, 26, 227, 'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَذَكَرُوا۟ ٱللَّهَ كَثِيرًا وَٱنتَصَرُوا۟ مِنۢ بَعْدِ مَا ظُلِمُوا۟ وَسَيَعْلَمُ ٱلَّذِينَ ظَلَمُوٓا۟ أَىَّ مُنقَلَبٍ يَنقَلِبُونَ', 'إلا الذين آمنوا وعملوا الصالحات وذكروا الله كثيرا وانتصروا من بعد ما ظلموا وسيعلم الذين ظلموا أي منقلب ينقلبون', 19, 38, 376, 150, '26:227'),
(3160, NULL, NULL, 27, 1, 'طسٓ تِلْكَ ءَايَٰتُ ٱلْقُرْءَانِ وَكِتَابٍ مُّبِينٍ', 'طس تلك آيات القرآن وكتاب مبين', 19, 38, 377, 151, '27:1'),
(3161, NULL, NULL, 27, 2, 'هُدًى وَبُشْرَىٰ لِلْمُؤْمِنِينَ', 'هدى وبشرى للمؤمنين', 19, 38, 377, 151, '27:2'),
(3162, NULL, NULL, 27, 3, 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْءَاخِرَةِ هُمْ يُوقِنُونَ', 'الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون', 19, 38, 377, 151, '27:3'),
(3163, NULL, NULL, 27, 4, 'إِنَّ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْءَاخِرَةِ زَيَّنَّا لَهُمْ أَعْمَٰلَهُمْ فَهُمْ يَعْمَهُونَ', 'إن الذين لا يؤمنون بالآخرة زينا لهم أعمالهم فهم يعمهون', 19, 38, 377, 151, '27:4'),
(3164, NULL, NULL, 27, 5, 'أُو۟لَٰٓئِكَ ٱلَّذِينَ لَهُمْ سُوٓءُ ٱلْعَذَابِ وَهُمْ فِى ٱلْءَاخِرَةِ هُمُ ٱلْأَخْسَرُونَ', 'أولئك الذين لهم سوء العذاب وهم في الآخرة هم الأخسرون', 19, 38, 377, 151, '27:5'),
(3165, NULL, NULL, 27, 6, 'وَإِنَّكَ لَتُلَقَّى ٱلْقُرْءَانَ مِن لَّدُنْ حَكِيمٍ عَلِيمٍ', 'وإنك لتلقى القرآن من لدن حكيم عليم', 19, 38, 377, 151, '27:6'),
(3166, NULL, NULL, 27, 7, 'إِذْ قَالَ مُوسَىٰ لِأَهْلِهِۦٓ إِنِّىٓ ءَانَسْتُ نَارًا سَـَٔاتِيكُم مِّنْهَا بِخَبَرٍ أَوْ ءَاتِيكُم بِشِهَابٍ قَبَسٍ لَّعَلَّكُمْ تَصْطَلُونَ', 'إذ قال موسى لأهله إني آنست نارا سآتيكم منها بخبر أو آتيكم بشهاب قبس لعلكم تصطلون', 19, 38, 377, 151, '27:7'),
(3167, NULL, NULL, 27, 8, 'فَلَمَّا جَآءَهَا نُودِىَ أَنۢ بُورِكَ مَن فِى ٱلنَّارِ وَمَنْ حَوْلَهَا وَسُبْحَٰنَ ٱللَّهِ رَبِّ ٱلْعَٰلَمِينَ', 'فلما جاءها نودي أن بورك من في النار ومن حولها وسبحان الله رب العالمين', 19, 38, 377, 151, '27:8'),
(3168, NULL, NULL, 27, 9, 'يَٰمُوسَىٰٓ إِنَّهُۥٓ أَنَا ٱللَّهُ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'يا موسى إنه أنا الله العزيز الحكيم', 19, 38, 377, 151, '27:9'),
(3169, NULL, NULL, 27, 10, 'وَأَلْقِ عَصَاكَ فَلَمَّا رَءَاهَا تَهْتَزُّ كَأَنَّهَا جَآنٌّ وَلَّىٰ مُدْبِرًا وَلَمْ يُعَقِّبْ يَٰمُوسَىٰ لَا تَخَفْ إِنِّى لَا يَخَافُ لَدَىَّ ٱلْمُرْسَلُونَ', 'وألق عصاك فلما رآها تهتز كأنها جان ولى مدبرا ولم يعقب يا موسى لا تخف إني لا يخاف لدي المرسلون', 19, 38, 377, 151, '27:10'),
(3170, NULL, NULL, 27, 11, 'إِلَّا مَن ظَلَمَ ثُمَّ بَدَّلَ حُسْنًۢا بَعْدَ سُوٓءٍ فَإِنِّى غَفُورٌ رَّحِيمٌ', 'إلا من ظلم ثم بدل حسنا بعد سوء فإني غفور رحيم', 19, 38, 377, 151, '27:11'),
(3171, NULL, NULL, 27, 12, 'وَأَدْخِلْ يَدَكَ فِى جَيْبِكَ تَخْرُجْ بَيْضَآءَ مِنْ غَيْرِ سُوٓءٍ فِى تِسْعِ ءَايَٰتٍ إِلَىٰ فِرْعَوْنَ وَقَوْمِهِۦٓ إِنَّهُمْ كَانُوا۟ قَوْمًا فَٰسِقِينَ', 'وأدخل يدك في جيبك تخرج بيضاء من غير سوء في تسع آيات إلى فرعون وقومه إنهم كانوا قوما فاسقين', 19, 38, 377, 151, '27:12'),
(3172, NULL, NULL, 27, 13, 'فَلَمَّا جَآءَتْهُمْ ءَايَٰتُنَا مُبْصِرَةً قَالُوا۟ هَٰذَا سِحْرٌ مُّبِينٌ', 'فلما جاءتهم آياتنا مبصرة قالوا هذا سحر مبين', 19, 38, 377, 151, '27:13'),
(3173, NULL, NULL, 27, 14, 'وَجَحَدُوا۟ بِهَا وَٱسْتَيْقَنَتْهَآ أَنفُسُهُمْ ظُلْمًا وَعُلُوًّا فَٱنظُرْ كَيْفَ كَانَ عَٰقِبَةُ ٱلْمُفْسِدِينَ', 'وجحدوا بها واستيقنتها أنفسهم ظلما وعلوا فانظر كيف كان عاقبة المفسدين', 19, 38, 378, 151, '27:14'),
(3174, NULL, NULL, 27, 15, 'وَلَقَدْ ءَاتَيْنَا دَاوُۥدَ وَسُلَيْمَٰنَ عِلْمًا وَقَالَا ٱلْحَمْدُ لِلَّهِ ٱلَّذِى فَضَّلَنَا عَلَىٰ كَثِيرٍ مِّنْ عِبَادِهِ ٱلْمُؤْمِنِينَ', 'ولقد آتينا داوود وسليمان علما وقالا الحمد لله الذي فضلنا على كثير من عباده المؤمنين', 19, 38, 378, 151, '27:15'),
(3175, NULL, NULL, 27, 16, 'وَوَرِثَ سُلَيْمَٰنُ دَاوُۥدَ وَقَالَ يَٰٓأَيُّهَا ٱلنَّاسُ عُلِّمْنَا مَنطِقَ ٱلطَّيْرِ وَأُوتِينَا مِن كُلِّ شَىْءٍ إِنَّ هَٰذَا لَهُوَ ٱلْفَضْلُ ٱلْمُبِينُ', 'وورث سليمان داوود وقال يا أيها الناس علمنا منطق الطير وأوتينا من كل شيء إن هذا لهو الفضل المبين', 19, 38, 378, 151, '27:16'),
(3176, NULL, NULL, 27, 17, 'وَحُشِرَ لِسُلَيْمَٰنَ جُنُودُهُۥ مِنَ ٱلْجِنِّ وَٱلْإِنسِ وَٱلطَّيْرِ فَهُمْ يُوزَعُونَ', 'وحشر لسليمان جنوده من الجن والإنس والطير فهم يوزعون', 19, 38, 378, 151, '27:17'),
(3177, NULL, NULL, 27, 18, 'حَتَّىٰٓ إِذَآ أَتَوْا۟ عَلَىٰ وَادِ ٱلنَّمْلِ قَالَتْ نَمْلَةٌ يَٰٓأَيُّهَا ٱلنَّمْلُ ٱدْخُلُوا۟ مَسَٰكِنَكُمْ لَا يَحْطِمَنَّكُمْ سُلَيْمَٰنُ وَجُنُودُهُۥ وَهُمْ لَا يَشْعُرُونَ', 'حتى إذا أتوا على واد النمل قالت نملة يا أيها النمل ادخلوا مساكنكم لا يحطمنكم سليمان وجنوده وهم لا يشعرون', 19, 38, 378, 151, '27:18'),
(3178, NULL, NULL, 27, 19, 'فَتَبَسَّمَ ضَاحِكًا مِّن قَوْلِهَا وَقَالَ رَبِّ أَوْزِعْنِىٓ أَنْ أَشْكُرَ نِعْمَتَكَ ٱلَّتِىٓ أَنْعَمْتَ عَلَىَّ وَعَلَىٰ وَٰلِدَىَّ وَأَنْ أَعْمَلَ صَٰلِحًا تَرْضَىٰهُ وَأَدْخِلْنِى بِرَحْمَتِكَ فِى عِبَادِكَ ٱلصَّٰلِحِينَ', 'فتبسم ضاحكا من قولها وقال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأدخلني برحمتك في عبادك الصالحين', 19, 38, 378, 151, '27:19'),
(3179, NULL, NULL, 27, 20, 'وَتَفَقَّدَ ٱلطَّيْرَ فَقَالَ مَا لِىَ لَآ أَرَى ٱلْهُدْهُدَ أَمْ كَانَ مِنَ ٱلْغَآئِبِينَ', 'وتفقد الطير فقال ما لي لا أرى الهدهد أم كان من الغائبين', 19, 38, 378, 151, '27:20'),
(3180, NULL, NULL, 27, 21, 'لَأُعَذِّبَنَّهُۥ عَذَابًا شَدِيدًا أَوْ لَأَا۟ذْبَحَنَّهُۥٓ أَوْ لَيَأْتِيَنِّى بِسُلْطَٰنٍ مُّبِينٍ', 'لأعذبنه عذابا شديدا أو لأذبحنه أو ليأتيني بسلطان مبين', 19, 38, 378, 151, '27:21'),
(3181, NULL, NULL, 27, 22, 'فَمَكَثَ غَيْرَ بَعِيدٍ فَقَالَ أَحَطتُ بِمَا لَمْ تُحِطْ بِهِۦ وَجِئْتُكَ مِن سَبَإٍۭ بِنَبَإٍ يَقِينٍ', 'فمكث غير بعيد فقال أحطت بما لم تحط به وجئتك من سبإ بنبإ يقين', 19, 38, 378, 151, '27:22'),
(3182, NULL, NULL, 27, 23, 'إِنِّى وَجَدتُّ ٱمْرَأَةً تَمْلِكُهُمْ وَأُوتِيَتْ مِن كُلِّ شَىْءٍ وَلَهَا عَرْشٌ عَظِيمٌ', 'إني وجدت امرأة تملكهم وأوتيت من كل شيء ولها عرش عظيم', 19, 38, 379, 151, '27:23'),
(3183, NULL, NULL, 27, 24, 'وَجَدتُّهَا وَقَوْمَهَا يَسْجُدُونَ لِلشَّمْسِ مِن دُونِ ٱللَّهِ وَزَيَّنَ لَهُمُ ٱلشَّيْطَٰنُ أَعْمَٰلَهُمْ فَصَدَّهُمْ عَنِ ٱلسَّبِيلِ فَهُمْ لَا يَهْتَدُونَ', 'وجدتها وقومها يسجدون للشمس من دون الله وزين لهم الشيطان أعمالهم فصدهم عن السبيل فهم لا يهتدون', 19, 38, 379, 151, '27:24'),
(3184, NULL, NULL, 27, 25, 'أَلَّا يَسْجُدُوا۟ لِلَّهِ ٱلَّذِى يُخْرِجُ ٱلْخَبْءَ فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَيَعْلَمُ مَا تُخْفُونَ وَمَا تُعْلِنُونَ', 'ألا يسجدوا لله الذي يخرج الخبء في السماوات والأرض ويعلم ما تخفون وما تعلنون', 19, 38, 379, 151, '27:25'),
(3185, NULL, NULL, 27, 26, 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ رَبُّ ٱلْعَرْشِ ٱلْعَظِيمِ', 'الله لا إله إلا هو رب العرش العظيم', 19, 38, 379, 151, '27:26'),
(3186, NULL, NULL, 27, 27, 'قَالَ سَنَنظُرُ أَصَدَقْتَ أَمْ كُنتَ مِنَ ٱلْكَٰذِبِينَ', 'قال سننظر أصدقت أم كنت من الكاذبين', 19, 38, 379, 152, '27:27'),
(3187, NULL, NULL, 27, 28, 'ٱذْهَب بِّكِتَٰبِى هَٰذَا فَأَلْقِهْ إِلَيْهِمْ ثُمَّ تَوَلَّ عَنْهُمْ فَٱنظُرْ مَاذَا يَرْجِعُونَ', 'اذهب بكتابي هذا فألقه إليهم ثم تول عنهم فانظر ماذا يرجعون', 19, 38, 379, 152, '27:28'),
(3188, NULL, NULL, 27, 29, 'قَالَتْ يَٰٓأَيُّهَا ٱلْمَلَؤُا۟ إِنِّىٓ أُلْقِىَ إِلَىَّ كِتَٰبٌ كَرِيمٌ', 'قالت يا أيها الملأ إني ألقي إلي كتاب كريم', 19, 38, 379, 152, '27:29'),
(3189, NULL, NULL, 27, 30, 'إِنَّهُۥ مِن سُلَيْمَٰنَ وَإِنَّهُۥ بِسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ', 'إنه من سليمان وإنه بسم الله الرحمن الرحيم', 19, 38, 379, 152, '27:30'),
(3190, NULL, NULL, 27, 31, 'أَلَّا تَعْلُوا۟ عَلَىَّ وَأْتُونِى مُسْلِمِينَ', 'ألا تعلوا علي وأتوني مسلمين', 19, 38, 379, 152, '27:31'),
(3191, NULL, NULL, 27, 32, 'قَالَتْ يَٰٓأَيُّهَا ٱلْمَلَؤُا۟ أَفْتُونِى فِىٓ أَمْرِى مَا كُنتُ قَاطِعَةً أَمْرًا حَتَّىٰ تَشْهَدُونِ', 'قالت يا أيها الملأ أفتوني في أمري ما كنت قاطعة أمرا حتى تشهدون', 19, 38, 379, 152, '27:32'),
(3192, NULL, NULL, 27, 33, 'قَالُوا۟ نَحْنُ أُو۟لُوا۟ قُوَّةٍ وَأُو۟لُوا۟ بَأْسٍ شَدِيدٍ وَٱلْأَمْرُ إِلَيْكِ فَٱنظُرِى مَاذَا تَأْمُرِينَ', 'قالوا نحن أولو قوة وأولو بأس شديد والأمر إليك فانظري ماذا تأمرين', 19, 38, 379, 152, '27:33'),
(3193, NULL, NULL, 27, 34, 'قَالَتْ إِنَّ ٱلْمُلُوكَ إِذَا دَخَلُوا۟ قَرْيَةً أَفْسَدُوهَا وَجَعَلُوٓا۟ أَعِزَّةَ أَهْلِهَآ أَذِلَّةً وَكَذَٰلِكَ يَفْعَلُونَ', 'قالت إن الملوك إذا دخلوا قرية أفسدوها وجعلوا أعزة أهلها أذلة وكذلك يفعلون', 19, 38, 379, 152, '27:34'),
(3194, NULL, NULL, 27, 35, 'وَإِنِّى مُرْسِلَةٌ إِلَيْهِم بِهَدِيَّةٍ فَنَاظِرَةٌۢ بِمَ يَرْجِعُ ٱلْمُرْسَلُونَ', 'وإني مرسلة إليهم بهدية فناظرة بم يرجع المرسلون', 19, 38, 379, 152, '27:35'),
(3195, NULL, NULL, 27, 36, 'فَلَمَّا جَآءَ سُلَيْمَٰنَ قَالَ أَتُمِدُّونَنِ بِمَالٍ فَمَآ ءَاتَىٰنِۦَ ٱللَّهُ خَيْرٌ مِّمَّآ ءَاتَىٰكُم بَلْ أَنتُم بِهَدِيَّتِكُمْ تَفْرَحُونَ', 'فلما جاء سليمان قال أتمدونن بمال فما آتاني الله خير مما آتاكم بل أنتم بهديتكم تفرحون', 19, 38, 380, 152, '27:36'),
(3196, NULL, NULL, 27, 37, 'ٱرْجِعْ إِلَيْهِمْ فَلَنَأْتِيَنَّهُم بِجُنُودٍ لَّا قِبَلَ لَهُم بِهَا وَلَنُخْرِجَنَّهُم مِّنْهَآ أَذِلَّةً وَهُمْ صَٰغِرُونَ', 'ارجع إليهم فلنأتينهم بجنود لا قبل لهم بها ولنخرجنهم منها أذلة وهم صاغرون', 19, 38, 380, 152, '27:37'),
(3197, NULL, NULL, 27, 38, 'قَالَ يَٰٓأَيُّهَا ٱلْمَلَؤُا۟ أَيُّكُمْ يَأْتِينِى بِعَرْشِهَا قَبْلَ أَن يَأْتُونِى مُسْلِمِينَ', 'قال يا أيها الملأ أيكم يأتيني بعرشها قبل أن يأتوني مسلمين', 19, 38, 380, 152, '27:38'),
(3198, NULL, NULL, 27, 39, 'قَالَ عِفْرِيتٌ مِّنَ ٱلْجِنِّ أَنَا۠ ءَاتِيكَ بِهِۦ قَبْلَ أَن تَقُومَ مِن مَّقَامِكَ وَإِنِّى عَلَيْهِ لَقَوِىٌّ أَمِينٌ', 'قال عفريت من الجن أنا آتيك به قبل أن تقوم من مقامك وإني عليه لقوي أمين', 19, 38, 380, 152, '27:39'),
(3199, NULL, NULL, 27, 40, 'قَالَ ٱلَّذِى عِندَهُۥ عِلْمٌ مِّنَ ٱلْكِتَٰبِ أَنَا۠ ءَاتِيكَ بِهِۦ قَبْلَ أَن يَرْتَدَّ إِلَيْكَ طَرْفُكَ فَلَمَّا رَءَاهُ مُسْتَقِرًّا عِندَهُۥ قَالَ هَٰذَا مِن فَضْلِ رَبِّى لِيَبْلُوَنِىٓ ءَأَشْكُرُ أَمْ أَكْفُرُ وَمَن شَكَرَ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِۦ وَمَن كَفَرَ فَإِنَّ رَبِّى غَنِىٌّ كَرِيمٌ', 'قال الذي عنده علم من الكتاب أنا آتيك به قبل أن يرتد إليك طرفك فلما رآه مستقرا عنده قال هذا من فضل ربي ليبلوني أأشكر أم أكفر ومن شكر فإنما يشكر لنفسه ومن كفر فإن ربي غني كريم', 19, 38, 380, 152, '27:40'),
(3200, NULL, NULL, 27, 41, 'قَالَ نَكِّرُوا۟ لَهَا عَرْشَهَا نَنظُرْ أَتَهْتَدِىٓ أَمْ تَكُونُ مِنَ ٱلَّذِينَ لَا يَهْتَدُونَ', 'قال نكروا لها عرشها ننظر أتهتدي أم تكون من الذين لا يهتدون', 19, 38, 380, 152, '27:41'),
(3201, NULL, NULL, 27, 42, 'فَلَمَّا جَآءَتْ قِيلَ أَهَٰكَذَا عَرْشُكِ قَالَتْ كَأَنَّهُۥ هُوَ وَأُوتِينَا ٱلْعِلْمَ مِن قَبْلِهَا وَكُنَّا مُسْلِمِينَ', 'فلما جاءت قيل أهكذا عرشك قالت كأنه هو وأوتينا العلم من قبلها وكنا مسلمين', 19, 38, 380, 152, '27:42'),
(3202, NULL, NULL, 27, 43, 'وَصَدَّهَا مَا كَانَت تَّعْبُدُ مِن دُونِ ٱللَّهِ إِنَّهَا كَانَتْ مِن قَوْمٍ كَٰفِرِينَ', 'وصدها ما كانت تعبد من دون الله إنها كانت من قوم كافرين', 19, 38, 380, 152, '27:43'),
(3203, NULL, NULL, 27, 44, 'قِيلَ لَهَا ٱدْخُلِى ٱلصَّرْحَ فَلَمَّا رَأَتْهُ حَسِبَتْهُ لُجَّةً وَكَشَفَتْ عَن سَاقَيْهَا قَالَ إِنَّهُۥ صَرْحٌ مُّمَرَّدٌ مِّن قَوَارِيرَ قَالَتْ رَبِّ إِنِّى ظَلَمْتُ نَفْسِى وَأَسْلَمْتُ مَعَ سُلَيْمَٰنَ لِلَّهِ رَبِّ ٱلْعَٰلَمِينَ', 'قيل لها ادخلي الصرح فلما رأته حسبته لجة وكشفت عن ساقيها قال إنه صرح ممرد من قوارير قالت رب إني ظلمت نفسي وأسلمت مع سليمان لله رب العالمين', 19, 38, 380, 152, '27:44'),
(3204, NULL, NULL, 27, 45, 'وَلَقَدْ أَرْسَلْنَآ إِلَىٰ ثَمُودَ أَخَاهُمْ صَٰلِحًا أَنِ ٱعْبُدُوا۟ ٱللَّهَ فَإِذَا هُمْ فَرِيقَانِ يَخْتَصِمُونَ', 'ولقد أرسلنا إلى ثمود أخاهم صالحا أن اعبدوا الله فإذا هم فريقان يختصمون', 19, 38, 381, 152, '27:45'),
(3205, NULL, NULL, 27, 46, 'قَالَ يَٰقَوْمِ لِمَ تَسْتَعْجِلُونَ بِٱلسَّيِّئَةِ قَبْلَ ٱلْحَسَنَةِ لَوْلَا تَسْتَغْفِرُونَ ٱللَّهَ لَعَلَّكُمْ تُرْحَمُونَ', 'قال يا قوم لم تستعجلون بالسيئة قبل الحسنة لولا تستغفرون الله لعلكم ترحمون', 19, 38, 381, 152, '27:46'),
(3206, NULL, NULL, 27, 47, 'قَالُوا۟ ٱطَّيَّرْنَا بِكَ وَبِمَن مَّعَكَ قَالَ طَٰٓئِرُكُمْ عِندَ ٱللَّهِ بَلْ أَنتُمْ قَوْمٌ تُفْتَنُونَ', 'قالوا اطيرنا بك وبمن معك قال طائركم عند الله بل أنتم قوم تفتنون', 19, 38, 381, 152, '27:47'),
(3207, NULL, NULL, 27, 48, 'وَكَانَ فِى ٱلْمَدِينَةِ تِسْعَةُ رَهْطٍ يُفْسِدُونَ فِى ٱلْأَرْضِ وَلَا يُصْلِحُونَ', 'وكان في المدينة تسعة رهط يفسدون في الأرض ولا يصلحون', 19, 38, 381, 152, '27:48'),
(3208, NULL, NULL, 27, 49, 'قَالُوا۟ تَقَاسَمُوا۟ بِٱللَّهِ لَنُبَيِّتَنَّهُۥ وَأَهْلَهُۥ ثُمَّ لَنَقُولَنَّ لِوَلِيِّهِۦ مَا شَهِدْنَا مَهْلِكَ أَهْلِهِۦ وَإِنَّا لَصَٰدِقُونَ', 'قالوا تقاسموا بالله لنبيتنه وأهله ثم لنقولن لوليه ما شهدنا مهلك أهله وإنا لصادقون', 19, 38, 381, 152, '27:49'),
(3209, NULL, NULL, 27, 50, 'وَمَكَرُوا۟ مَكْرًا وَمَكَرْنَا مَكْرًا وَهُمْ لَا يَشْعُرُونَ', 'ومكروا مكرا ومكرنا مكرا وهم لا يشعرون', 19, 38, 381, 152, '27:50'),
(3210, NULL, NULL, 27, 51, 'فَٱنظُرْ كَيْفَ كَانَ عَٰقِبَةُ مَكْرِهِمْ أَنَّا دَمَّرْنَٰهُمْ وَقَوْمَهُمْ أَجْمَعِينَ', 'فانظر كيف كان عاقبة مكرهم أنا دمرناهم وقومهم أجمعين', 19, 38, 381, 152, '27:51'),
(3211, NULL, NULL, 27, 52, 'فَتِلْكَ بُيُوتُهُمْ خَاوِيَةًۢ بِمَا ظَلَمُوٓا۟ إِنَّ فِى ذَٰلِكَ لَءَايَةً لِّقَوْمٍ يَعْلَمُونَ', 'فتلك بيوتهم خاوية بما ظلموا إن في ذلك لآية لقوم يعلمون', 19, 38, 381, 152, '27:52'),
(3212, NULL, NULL, 27, 53, 'وَأَنجَيْنَا ٱلَّذِينَ ءَامَنُوا۟ وَكَانُوا۟ يَتَّقُونَ', 'وأنجينا الذين آمنوا وكانوا يتقون', 19, 38, 381, 152, '27:53'),
(3213, NULL, NULL, 27, 54, 'وَلُوطًا إِذْ قَالَ لِقَوْمِهِۦٓ أَتَأْتُونَ ٱلْفَٰحِشَةَ وَأَنتُمْ تُبْصِرُونَ', 'ولوطا إذ قال لقومه أتأتون الفاحشة وأنتم تبصرون', 19, 38, 381, 152, '27:54'),
(3214, NULL, NULL, 27, 55, 'أَئِنَّكُمْ لَتَأْتُونَ ٱلرِّجَالَ شَهْوَةً مِّن دُونِ ٱلنِّسَآءِ بَلْ أَنتُمْ قَوْمٌ تَجْهَلُونَ', 'أئنكم لتأتون الرجال شهوة من دون النساء بل أنتم قوم تجهلون', 19, 38, 381, 152, '27:55'),
(3215, NULL, NULL, 27, 56, 'فَمَا كَانَ جَوَابَ قَوْمِهِۦٓ إِلَّآ أَن قَالُوٓا۟ أَخْرِجُوٓا۟ ءَالَ لُوطٍ مِّن قَرْيَتِكُمْ إِنَّهُمْ أُنَاسٌ يَتَطَهَّرُونَ', 'فما كان جواب قومه إلا أن قالوا أخرجوا آل لوط من قريتكم إنهم أناس يتطهرون', 20, 39, 382, 153, '27:56'),
(3216, NULL, NULL, 27, 57, 'فَأَنجَيْنَٰهُ وَأَهْلَهُۥٓ إِلَّا ٱمْرَأَتَهُۥ قَدَّرْنَٰهَا مِنَ ٱلْغَٰبِرِينَ', 'فأنجيناه وأهله إلا امرأته قدرناها من الغابرين', 20, 39, 382, 153, '27:57'),
(3217, NULL, NULL, 27, 58, 'وَأَمْطَرْنَا عَلَيْهِم مَّطَرًا فَسَآءَ مَطَرُ ٱلْمُنذَرِينَ', 'وأمطرنا عليهم مطرا فساء مطر المنذرين', 20, 39, 382, 153, '27:58'),
(3218, NULL, NULL, 27, 59, 'قُلِ ٱلْحَمْدُ لِلَّهِ وَسَلَٰمٌ عَلَىٰ عِبَادِهِ ٱلَّذِينَ ٱصْطَفَىٰٓ ءَآللَّهُ خَيْرٌ أَمَّا يُشْرِكُونَ', 'قل الحمد لله وسلام على عباده الذين اصطفى آلله خير أما يشركون', 20, 39, 382, 153, '27:59'),
(3219, NULL, NULL, 27, 60, 'أَمَّنْ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَأَنزَلَ لَكُم مِّنَ ٱلسَّمَآءِ مَآءً فَأَنۢبَتْنَا بِهِۦ حَدَآئِقَ ذَاتَ بَهْجَةٍ مَّا كَانَ لَكُمْ أَن تُنۢبِتُوا۟ شَجَرَهَآ أَءِلَٰهٌ مَّعَ ٱللَّهِ بَلْ هُمْ قَوْمٌ يَعْدِلُونَ', 'أمن خلق السماوات والأرض وأنزل لكم من السماء ماء فأنبتنا به حدائق ذات بهجة ما كان لكم أن تنبتوا شجرها أإله مع الله بل هم قوم يعدلون', 20, 39, 382, 153, '27:60'),
(3220, NULL, NULL, 27, 61, 'أَمَّن جَعَلَ ٱلْأَرْضَ قَرَارًا وَجَعَلَ خِلَٰلَهَآ أَنْهَٰرًا وَجَعَلَ لَهَا رَوَٰسِىَ وَجَعَلَ بَيْنَ ٱلْبَحْرَيْنِ حَاجِزًا أَءِلَٰهٌ مَّعَ ٱللَّهِ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 'أمن جعل الأرض قرارا وجعل خلالها أنهارا وجعل لها رواسي وجعل بين البحرين حاجزا أإله مع الله بل أكثرهم لا يعلمون', 20, 39, 382, 153, '27:61'),
(3221, NULL, NULL, 27, 62, 'أَمَّن يُجِيبُ ٱلْمُضْطَرَّ إِذَا دَعَاهُ وَيَكْشِفُ ٱلسُّوٓءَ وَيَجْعَلُكُمْ خُلَفَآءَ ٱلْأَرْضِ أَءِلَٰهٌ مَّعَ ٱللَّهِ قَلِيلًا مَّا تَذَكَّرُونَ', 'أمن يجيب المضطر إذا دعاه ويكشف السوء ويجعلكم خلفاء الأرض أإله مع الله قليلا ما تذكرون', 20, 39, 382, 153, '27:62'),
(3222, NULL, NULL, 27, 63, 'أَمَّن يَهْدِيكُمْ فِى ظُلُمَٰتِ ٱلْبَرِّ وَٱلْبَحْرِ وَمَن يُرْسِلُ ٱلرِّيَٰحَ بُشْرًۢا بَيْنَ يَدَىْ رَحْمَتِهِۦٓ أَءِلَٰهٌ مَّعَ ٱللَّهِ تَعَٰلَى ٱللَّهُ عَمَّا يُشْرِكُونَ', 'أمن يهديكم في ظلمات البر والبحر ومن يرسل الرياح بشرا بين يدي رحمته أإله مع الله تعالى الله عما يشركون', 20, 39, 382, 153, '27:63'),
(3223, NULL, NULL, 27, 64, 'أَمَّن يَبْدَؤُا۟ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥ وَمَن يَرْزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلْأَرْضِ أَءِلَٰهٌ مَّعَ ٱللَّهِ قُلْ هَاتُوا۟ بُرْهَٰنَكُمْ إِن كُنتُمْ صَٰدِقِينَ', 'أمن يبدأ الخلق ثم يعيده ومن يرزقكم من السماء والأرض أإله مع الله قل هاتوا برهانكم إن كنتم صادقين', 20, 39, 383, 153, '27:64'),
(3224, NULL, NULL, 27, 65, 'قُل لَّا يَعْلَمُ مَن فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ٱلْغَيْبَ إِلَّا ٱللَّهُ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ', 'قل لا يعلم من في السماوات والأرض الغيب إلا الله وما يشعرون أيان يبعثون', 20, 39, 383, 153, '27:65'),
(3225, NULL, NULL, 27, 66, 'بَلِ ٱدَّٰرَكَ عِلْمُهُمْ فِى ٱلْءَاخِرَةِ بَلْ هُمْ فِى شَكٍّ مِّنْهَا بَلْ هُم مِّنْهَا عَمُونَ', 'بل ادارك علمهم في الآخرة بل هم في شك منها بل هم منها عمون', 20, 39, 383, 153, '27:66'),
(3226, NULL, NULL, 27, 67, 'وَقَالَ ٱلَّذِينَ كَفَرُوٓا۟ أَءِذَا كُنَّا تُرَٰبًا وَءَابَآؤُنَآ أَئِنَّا لَمُخْرَجُونَ', 'وقال الذين كفروا أإذا كنا ترابا وآباؤنا أئنا لمخرجون', 20, 39, 383, 153, '27:67'),
(3227, NULL, NULL, 27, 68, 'لَقَدْ وُعِدْنَا هَٰذَا نَحْنُ وَءَابَآؤُنَا مِن قَبْلُ إِنْ هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلْأَوَّلِينَ', 'لقد وعدنا هذا نحن وآباؤنا من قبل إن هذا إلا أساطير الأولين', 20, 39, 383, 153, '27:68'),
(3228, NULL, NULL, 27, 69, 'قُلْ سِيرُوا۟ فِى ٱلْأَرْضِ فَٱنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلْمُجْرِمِينَ', 'قل سيروا في الأرض فانظروا كيف كان عاقبة المجرمين', 20, 39, 383, 153, '27:69'),
(3229, NULL, NULL, 27, 70, 'وَلَا تَحْزَنْ عَلَيْهِمْ وَلَا تَكُن فِى ضَيْقٍ مِّمَّا يَمْكُرُونَ', 'ولا تحزن عليهم ولا تكن في ضيق مما يمكرون', 20, 39, 383, 153, '27:70'),
(3230, NULL, NULL, 27, 71, 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَٰدِقِينَ', 'ويقولون متى هذا الوعد إن كنتم صادقين', 20, 39, 383, 153, '27:71'),
(3231, NULL, NULL, 27, 72, 'قُلْ عَسَىٰٓ أَن يَكُونَ رَدِفَ لَكُم بَعْضُ ٱلَّذِى تَسْتَعْجِلُونَ', 'قل عسى أن يكون ردف لكم بعض الذي تستعجلون', 20, 39, 383, 153, '27:72'),
(3232, NULL, NULL, 27, 73, 'وَإِنَّ رَبَّكَ لَذُو فَضْلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَشْكُرُونَ', 'وإن ربك لذو فضل على الناس ولكن أكثرهم لا يشكرون', 20, 39, 383, 153, '27:73'),
(3233, NULL, NULL, 27, 74, 'وَإِنَّ رَبَّكَ لَيَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ', 'وإن ربك ليعلم ما تكن صدورهم وما يعلنون', 20, 39, 383, 153, '27:74'),
(3234, NULL, NULL, 27, 75, 'وَمَا مِنْ غَآئِبَةٍ فِى ٱلسَّمَآءِ وَٱلْأَرْضِ إِلَّا فِى كِتَٰبٍ مُّبِينٍ', 'وما من غائبة في السماء والأرض إلا في كتاب مبين', 20, 39, 383, 153, '27:75'),
(3235, NULL, NULL, 27, 76, 'إِنَّ هَٰذَا ٱلْقُرْءَانَ يَقُصُّ عَلَىٰ بَنِىٓ إِسْرَٰٓءِيلَ أَكْثَرَ ٱلَّذِى هُمْ فِيهِ يَخْتَلِفُونَ', 'إن هذا القرآن يقص على بني إسرائيل أكثر الذي هم فيه يختلفون', 20, 39, 383, 153, '27:76'),
(3236, NULL, NULL, 27, 77, 'وَإِنَّهُۥ لَهُدًى وَرَحْمَةٌ لِّلْمُؤْمِنِينَ', 'وإنه لهدى ورحمة للمؤمنين', 20, 39, 384, 153, '27:77'),
(3237, NULL, NULL, 27, 78, 'إِنَّ رَبَّكَ يَقْضِى بَيْنَهُم بِحُكْمِهِۦ وَهُوَ ٱلْعَزِيزُ ٱلْعَلِيمُ', 'إن ربك يقضي بينهم بحكمه وهو العزيز العليم', 20, 39, 384, 153, '27:78'),
(3238, NULL, NULL, 27, 79, 'فَتَوَكَّلْ عَلَى ٱللَّهِ إِنَّكَ عَلَى ٱلْحَقِّ ٱلْمُبِينِ', 'فتوكل على الله إنك على الحق المبين', 20, 39, 384, 153, '27:79'),
(3239, NULL, NULL, 27, 80, 'إِنَّكَ لَا تُسْمِعُ ٱلْمَوْتَىٰ وَلَا تُسْمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوْا۟ مُدْبِرِينَ', 'إنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين', 20, 39, 384, 153, '27:80'),
(3240, NULL, NULL, 27, 81, 'وَمَآ أَنتَ بِهَٰدِى ٱلْعُمْىِ عَن ضَلَٰلَتِهِمْ إِن تُسْمِعُ إِلَّا مَن يُؤْمِنُ بِـَٔايَٰتِنَا فَهُم مُّسْلِمُونَ', 'وما أنت بهادي العمي عن ضلالتهم إن تسمع إلا من يؤمن بآياتنا فهم مسلمون', 20, 39, 384, 153, '27:81'),
(3241, NULL, NULL, 27, 82, 'وَإِذَا وَقَعَ ٱلْقَوْلُ عَلَيْهِمْ أَخْرَجْنَا لَهُمْ دَآبَّةً مِّنَ ٱلْأَرْضِ تُكَلِّمُهُمْ أَنَّ ٱلنَّاسَ كَانُوا۟ بِـَٔايَٰتِنَا لَا يُوقِنُونَ', 'وإذا وقع القول عليهم أخرجنا لهم دابة من الأرض تكلمهم أن الناس كانوا بآياتنا لا يوقنون', 20, 39, 384, 154, '27:82'),
(3242, NULL, NULL, 27, 83, 'وَيَوْمَ نَحْشُرُ مِن كُلِّ أُمَّةٍ فَوْجًا مِّمَّن يُكَذِّبُ بِـَٔايَٰتِنَا فَهُمْ يُوزَعُونَ', 'ويوم نحشر من كل أمة فوجا ممن يكذب بآياتنا فهم يوزعون', 20, 39, 384, 154, '27:83'),
(3243, NULL, NULL, 27, 84, 'حَتَّىٰٓ إِذَا جَآءُو قَالَ أَكَذَّبْتُم بِـَٔايَٰتِى وَلَمْ تُحِيطُوا۟ بِهَا عِلْمًا أَمَّاذَا كُنتُمْ تَعْمَلُونَ', 'حتى إذا جاءوا قال أكذبتم بآياتي ولم تحيطوا بها علما أماذا كنتم تعملون', 20, 39, 384, 154, '27:84'),
(3244, NULL, NULL, 27, 85, 'وَوَقَعَ ٱلْقَوْلُ عَلَيْهِم بِمَا ظَلَمُوا۟ فَهُمْ لَا يَنطِقُونَ', 'ووقع القول عليهم بما ظلموا فهم لا ينطقون', 20, 39, 384, 154, '27:85'),
(3245, NULL, NULL, 27, 86, 'أَلَمْ يَرَوْا۟ أَنَّا جَعَلْنَا ٱلَّيْلَ لِيَسْكُنُوا۟ فِيهِ وَٱلنَّهَارَ مُبْصِرًا إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يُؤْمِنُونَ', 'ألم يروا أنا جعلنا الليل ليسكنوا فيه والنهار مبصرا إن في ذلك لآيات لقوم يؤمنون', 20, 39, 384, 154, '27:86'),
(3246, NULL, NULL, 27, 87, 'وَيَوْمَ يُنفَخُ فِى ٱلصُّورِ فَفَزِعَ مَن فِى ٱلسَّمَٰوَٰتِ وَمَن فِى ٱلْأَرْضِ إِلَّا مَن شَآءَ ٱللَّهُ وَكُلٌّ أَتَوْهُ دَٰخِرِينَ', 'ويوم ينفخ في الصور ففزع من في السماوات ومن في الأرض إلا من شاء الله وكل أتوه داخرين', 20, 39, 384, 154, '27:87'),
(3247, NULL, NULL, 27, 88, 'وَتَرَى ٱلْجِبَالَ تَحْسَبُهَا جَامِدَةً وَهِىَ تَمُرُّ مَرَّ ٱلسَّحَابِ صُنْعَ ٱللَّهِ ٱلَّذِىٓ أَتْقَنَ كُلَّ شَىْءٍ إِنَّهُۥ خَبِيرٌۢ بِمَا تَفْعَلُونَ', 'وترى الجبال تحسبها جامدة وهي تمر مر السحاب صنع الله الذي أتقن كل شيء إنه خبير بما تفعلون', 20, 39, 384, 154, '27:88'),
(3248, NULL, NULL, 27, 89, 'مَن جَآءَ بِٱلْحَسَنَةِ فَلَهُۥ خَيْرٌ مِّنْهَا وَهُم مِّن فَزَعٍ يَوْمَئِذٍ ءَامِنُونَ', 'من جاء بالحسنة فله خير منها وهم من فزع يومئذ آمنون', 20, 39, 385, 154, '27:89'),
(3249, NULL, NULL, 27, 90, 'وَمَن جَآءَ بِٱلسَّيِّئَةِ فَكُبَّتْ وُجُوهُهُمْ فِى ٱلنَّارِ هَلْ تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ', 'ومن جاء بالسيئة فكبت وجوههم في النار هل تجزون إلا ما كنتم تعملون', 20, 39, 385, 154, '27:90');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(3250, NULL, NULL, 27, 91, 'إِنَّمَآ أُمِرْتُ أَنْ أَعْبُدَ رَبَّ هَٰذِهِ ٱلْبَلْدَةِ ٱلَّذِى حَرَّمَهَا وَلَهُۥ كُلُّ شَىْءٍ وَأُمِرْتُ أَنْ أَكُونَ مِنَ ٱلْمُسْلِمِينَ', 'إنما أمرت أن أعبد رب هذه البلدة الذي حرمها وله كل شيء وأمرت أن أكون من المسلمين', 20, 39, 385, 154, '27:91'),
(3251, NULL, NULL, 27, 92, 'وَأَنْ أَتْلُوَا۟ ٱلْقُرْءَانَ فَمَنِ ٱهْتَدَىٰ فَإِنَّمَا يَهْتَدِى لِنَفْسِهِۦ وَمَن ضَلَّ فَقُلْ إِنَّمَآ أَنَا۠ مِنَ ٱلْمُنذِرِينَ', 'وأن أتلو القرآن فمن اهتدى فإنما يهتدي لنفسه ومن ضل فقل إنما أنا من المنذرين', 20, 39, 385, 154, '27:92'),
(3252, NULL, NULL, 27, 93, 'وَقُلِ ٱلْحَمْدُ لِلَّهِ سَيُرِيكُمْ ءَايَٰتِهِۦ فَتَعْرِفُونَهَا وَمَا رَبُّكَ بِغَٰفِلٍ عَمَّا تَعْمَلُونَ', 'وقل الحمد لله سيريكم آياته فتعرفونها وما ربك بغافل عما تعملون', 20, 39, 385, 154, '27:93'),
(3253, NULL, NULL, 28, 1, 'طسٓمٓ', 'طسم', 20, 39, 385, 154, '28:1'),
(3254, NULL, NULL, 28, 2, 'تِلْكَ ءَايَٰتُ ٱلْكِتَٰبِ ٱلْمُبِينِ', 'تلك آيات الكتاب المبين', 20, 39, 385, 154, '28:2'),
(3255, NULL, NULL, 28, 3, 'نَتْلُوا۟ عَلَيْكَ مِن نَّبَإِ مُوسَىٰ وَفِرْعَوْنَ بِٱلْحَقِّ لِقَوْمٍ يُؤْمِنُونَ', 'نتلو عليك من نبإ موسى وفرعون بالحق لقوم يؤمنون', 20, 39, 385, 154, '28:3'),
(3256, NULL, NULL, 28, 4, 'إِنَّ فِرْعَوْنَ عَلَا فِى ٱلْأَرْضِ وَجَعَلَ أَهْلَهَا شِيَعًا يَسْتَضْعِفُ طَآئِفَةً مِّنْهُمْ يُذَبِّحُ أَبْنَآءَهُمْ وَيَسْتَحْىِۦ نِسَآءَهُمْ إِنَّهُۥ كَانَ مِنَ ٱلْمُفْسِدِينَ', 'إن فرعون علا في الأرض وجعل أهلها شيعا يستضعف طائفة منهم يذبح أبناءهم ويستحيي نساءهم إنه كان من المفسدين', 20, 39, 385, 154, '28:4'),
(3257, NULL, NULL, 28, 5, 'وَنُرِيدُ أَن نَّمُنَّ عَلَى ٱلَّذِينَ ٱسْتُضْعِفُوا۟ فِى ٱلْأَرْضِ وَنَجْعَلَهُمْ أَئِمَّةً وَنَجْعَلَهُمُ ٱلْوَٰرِثِينَ', 'ونريد أن نمن على الذين استضعفوا في الأرض ونجعلهم أئمة ونجعلهم الوارثين', 20, 39, 385, 154, '28:5'),
(3258, NULL, NULL, 28, 6, 'وَنُمَكِّنَ لَهُمْ فِى ٱلْأَرْضِ وَنُرِىَ فِرْعَوْنَ وَهَٰمَٰنَ وَجُنُودَهُمَا مِنْهُم مَّا كَانُوا۟ يَحْذَرُونَ', 'ونمكن لهم في الأرض ونري فرعون وهامان وجنودهما منهم ما كانوا يحذرون', 20, 39, 386, 154, '28:6'),
(3259, NULL, NULL, 28, 7, 'وَأَوْحَيْنَآ إِلَىٰٓ أُمِّ مُوسَىٰٓ أَنْ أَرْضِعِيهِ فَإِذَا خِفْتِ عَلَيْهِ فَأَلْقِيهِ فِى ٱلْيَمِّ وَلَا تَخَافِى وَلَا تَحْزَنِىٓ إِنَّا رَآدُّوهُ إِلَيْكِ وَجَاعِلُوهُ مِنَ ٱلْمُرْسَلِينَ', 'وأوحينا إلى أم موسى أن أرضعيه فإذا خفت عليه فألقيه في اليم ولا تخافي ولا تحزني إنا رادوه إليك وجاعلوه من المرسلين', 20, 39, 386, 154, '28:7'),
(3260, NULL, NULL, 28, 8, 'فَٱلْتَقَطَهُۥٓ ءَالُ فِرْعَوْنَ لِيَكُونَ لَهُمْ عَدُوًّا وَحَزَنًا إِنَّ فِرْعَوْنَ وَهَٰمَٰنَ وَجُنُودَهُمَا كَانُوا۟ خَٰطِـِٔينَ', 'فالتقطه آل فرعون ليكون لهم عدوا وحزنا إن فرعون وهامان وجنودهما كانوا خاطئين', 20, 39, 386, 154, '28:8'),
(3261, NULL, NULL, 28, 9, 'وَقَالَتِ ٱمْرَأَتُ فِرْعَوْنَ قُرَّتُ عَيْنٍ لِّى وَلَكَ لَا تَقْتُلُوهُ عَسَىٰٓ أَن يَنفَعَنَآ أَوْ نَتَّخِذَهُۥ وَلَدًا وَهُمْ لَا يَشْعُرُونَ', 'وقالت امرأت فرعون قرت عين لي ولك لا تقتلوه عسى أن ينفعنا أو نتخذه ولدا وهم لا يشعرون', 20, 39, 386, 154, '28:9'),
(3262, NULL, NULL, 28, 10, 'وَأَصْبَحَ فُؤَادُ أُمِّ مُوسَىٰ فَٰرِغًا إِن كَادَتْ لَتُبْدِى بِهِۦ لَوْلَآ أَن رَّبَطْنَا عَلَىٰ قَلْبِهَا لِتَكُونَ مِنَ ٱلْمُؤْمِنِينَ', 'وأصبح فؤاد أم موسى فارغا إن كادت لتبدي به لولا أن ربطنا على قلبها لتكون من المؤمنين', 20, 39, 386, 154, '28:10'),
(3263, NULL, NULL, 28, 11, 'وَقَالَتْ لِأُخْتِهِۦ قُصِّيهِ فَبَصُرَتْ بِهِۦ عَن جُنُبٍ وَهُمْ لَا يَشْعُرُونَ', 'وقالت لأخته قصيه فبصرت به عن جنب وهم لا يشعرون', 20, 39, 386, 154, '28:11'),
(3264, NULL, NULL, 28, 12, 'وَحَرَّمْنَا عَلَيْهِ ٱلْمَرَاضِعَ مِن قَبْلُ فَقَالَتْ هَلْ أَدُلُّكُمْ عَلَىٰٓ أَهْلِ بَيْتٍ يَكْفُلُونَهُۥ لَكُمْ وَهُمْ لَهُۥ نَٰصِحُونَ', 'وحرمنا عليه المراضع من قبل فقالت هل أدلكم على أهل بيت يكفلونه لكم وهم له ناصحون', 20, 39, 386, 155, '28:12'),
(3265, NULL, NULL, 28, 13, 'فَرَدَدْنَٰهُ إِلَىٰٓ أُمِّهِۦ كَىْ تَقَرَّ عَيْنُهَا وَلَا تَحْزَنَ وَلِتَعْلَمَ أَنَّ وَعْدَ ٱللَّهِ حَقٌّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 'فرددناه إلى أمه كي تقر عينها ولا تحزن ولتعلم أن وعد الله حق ولكن أكثرهم لا يعلمون', 20, 39, 386, 155, '28:13'),
(3266, NULL, NULL, 28, 14, 'وَلَمَّا بَلَغَ أَشُدَّهُۥ وَٱسْتَوَىٰٓ ءَاتَيْنَٰهُ حُكْمًا وَعِلْمًا وَكَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ', 'ولما بلغ أشده واستوى آتيناه حكما وعلما وكذلك نجزي المحسنين', 20, 39, 387, 155, '28:14'),
(3267, NULL, NULL, 28, 15, 'وَدَخَلَ ٱلْمَدِينَةَ عَلَىٰ حِينِ غَفْلَةٍ مِّنْ أَهْلِهَا فَوَجَدَ فِيهَا رَجُلَيْنِ يَقْتَتِلَانِ هَٰذَا مِن شِيعَتِهِۦ وَهَٰذَا مِنْ عَدُوِّهِۦ فَٱسْتَغَٰثَهُ ٱلَّذِى مِن شِيعَتِهِۦ عَلَى ٱلَّذِى مِنْ عَدُوِّهِۦ فَوَكَزَهُۥ مُوسَىٰ فَقَضَىٰ عَلَيْهِ قَالَ هَٰذَا مِنْ عَمَلِ ٱلشَّيْطَٰنِ إِنَّهُۥ عَدُوٌّ مُّضِلٌّ مُّبِينٌ', 'ودخل المدينة على حين غفلة من أهلها فوجد فيها رجلين يقتتلان هذا من شيعته وهذا من عدوه فاستغاثه الذي من شيعته على الذي من عدوه فوكزه موسى فقضى عليه قال هذا من عمل الشيطان إنه عدو مضل مبين', 20, 39, 387, 155, '28:15'),
(3268, NULL, NULL, 28, 16, 'قَالَ رَبِّ إِنِّى ظَلَمْتُ نَفْسِى فَٱغْفِرْ لِى فَغَفَرَ لَهُۥٓ إِنَّهُۥ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ', 'قال رب إني ظلمت نفسي فاغفر لي فغفر له إنه هو الغفور الرحيم', 20, 39, 387, 155, '28:16'),
(3269, NULL, NULL, 28, 17, 'قَالَ رَبِّ بِمَآ أَنْعَمْتَ عَلَىَّ فَلَنْ أَكُونَ ظَهِيرًا لِّلْمُجْرِمِينَ', 'قال رب بما أنعمت علي فلن أكون ظهيرا للمجرمين', 20, 39, 387, 155, '28:17'),
(3270, NULL, NULL, 28, 18, 'فَأَصْبَحَ فِى ٱلْمَدِينَةِ خَآئِفًا يَتَرَقَّبُ فَإِذَا ٱلَّذِى ٱسْتَنصَرَهُۥ بِٱلْأَمْسِ يَسْتَصْرِخُهُۥ قَالَ لَهُۥ مُوسَىٰٓ إِنَّكَ لَغَوِىٌّ مُّبِينٌ', 'فأصبح في المدينة خائفا يترقب فإذا الذي استنصره بالأمس يستصرخه قال له موسى إنك لغوي مبين', 20, 39, 387, 155, '28:18'),
(3271, NULL, NULL, 28, 19, 'فَلَمَّآ أَنْ أَرَادَ أَن يَبْطِشَ بِٱلَّذِى هُوَ عَدُوٌّ لَّهُمَا قَالَ يَٰمُوسَىٰٓ أَتُرِيدُ أَن تَقْتُلَنِى كَمَا قَتَلْتَ نَفْسًۢا بِٱلْأَمْسِ إِن تُرِيدُ إِلَّآ أَن تَكُونَ جَبَّارًا فِى ٱلْأَرْضِ وَمَا تُرِيدُ أَن تَكُونَ مِنَ ٱلْمُصْلِحِينَ', 'فلما أن أراد أن يبطش بالذي هو عدو لهما قال يا موسى أتريد أن تقتلني كما قتلت نفسا بالأمس إن تريد إلا أن تكون جبارا في الأرض وما تريد أن تكون من المصلحين', 20, 39, 387, 155, '28:19'),
(3272, NULL, NULL, 28, 20, 'وَجَآءَ رَجُلٌ مِّنْ أَقْصَا ٱلْمَدِينَةِ يَسْعَىٰ قَالَ يَٰمُوسَىٰٓ إِنَّ ٱلْمَلَأَ يَأْتَمِرُونَ بِكَ لِيَقْتُلُوكَ فَٱخْرُجْ إِنِّى لَكَ مِنَ ٱلنَّٰصِحِينَ', 'وجاء رجل من أقصى المدينة يسعى قال يا موسى إن الملأ يأتمرون بك ليقتلوك فاخرج إني لك من الناصحين', 20, 39, 387, 155, '28:20'),
(3273, NULL, NULL, 28, 21, 'فَخَرَجَ مِنْهَا خَآئِفًا يَتَرَقَّبُ قَالَ رَبِّ نَجِّنِى مِنَ ٱلْقَوْمِ ٱلظَّٰلِمِينَ', 'فخرج منها خائفا يترقب قال رب نجني من القوم الظالمين', 20, 39, 387, 155, '28:21'),
(3274, NULL, NULL, 28, 22, 'وَلَمَّا تَوَجَّهَ تِلْقَآءَ مَدْيَنَ قَالَ عَسَىٰ رَبِّىٓ أَن يَهْدِيَنِى سَوَآءَ ٱلسَّبِيلِ', 'ولما توجه تلقاء مدين قال عسى ربي أن يهديني سواء السبيل', 20, 39, 388, 155, '28:22'),
(3275, NULL, NULL, 28, 23, 'وَلَمَّا وَرَدَ مَآءَ مَدْيَنَ وَجَدَ عَلَيْهِ أُمَّةً مِّنَ ٱلنَّاسِ يَسْقُونَ وَوَجَدَ مِن دُونِهِمُ ٱمْرَأَتَيْنِ تَذُودَانِ قَالَ مَا خَطْبُكُمَا قَالَتَا لَا نَسْقِى حَتَّىٰ يُصْدِرَ ٱلرِّعَآءُ وَأَبُونَا شَيْخٌ كَبِيرٌ', 'ولما ورد ماء مدين وجد عليه أمة من الناس يسقون ووجد من دونهم امرأتين تذودان قال ما خطبكما قالتا لا نسقي حتى يصدر الرعاء وأبونا شيخ كبير', 20, 39, 388, 155, '28:23'),
(3276, NULL, NULL, 28, 24, 'فَسَقَىٰ لَهُمَا ثُمَّ تَوَلَّىٰٓ إِلَى ٱلظِّلِّ فَقَالَ رَبِّ إِنِّى لِمَآ أَنزَلْتَ إِلَىَّ مِنْ خَيْرٍ فَقِيرٌ', 'فسقى لهما ثم تولى إلى الظل فقال رب إني لما أنزلت إلي من خير فقير', 20, 39, 388, 155, '28:24'),
(3277, NULL, NULL, 28, 25, 'فَجَآءَتْهُ إِحْدَىٰهُمَا تَمْشِى عَلَى ٱسْتِحْيَآءٍ قَالَتْ إِنَّ أَبِى يَدْعُوكَ لِيَجْزِيَكَ أَجْرَ مَا سَقَيْتَ لَنَا فَلَمَّا جَآءَهُۥ وَقَصَّ عَلَيْهِ ٱلْقَصَصَ قَالَ لَا تَخَفْ نَجَوْتَ مِنَ ٱلْقَوْمِ ٱلظَّٰلِمِينَ', 'فجاءته إحداهما تمشي على استحياء قالت إن أبي يدعوك ليجزيك أجر ما سقيت لنا فلما جاءه وقص عليه القصص قال لا تخف نجوت من القوم الظالمين', 20, 39, 388, 155, '28:25'),
(3278, NULL, NULL, 28, 26, 'قَالَتْ إِحْدَىٰهُمَا يَٰٓأَبَتِ ٱسْتَـْٔجِرْهُ إِنَّ خَيْرَ مَنِ ٱسْتَـْٔجَرْتَ ٱلْقَوِىُّ ٱلْأَمِينُ', 'قالت إحداهما يا أبت استأجره إن خير من استأجرت القوي الأمين', 20, 39, 388, 155, '28:26'),
(3279, NULL, NULL, 28, 27, 'قَالَ إِنِّىٓ أُرِيدُ أَنْ أُنكِحَكَ إِحْدَى ٱبْنَتَىَّ هَٰتَيْنِ عَلَىٰٓ أَن تَأْجُرَنِى ثَمَٰنِىَ حِجَجٍ فَإِنْ أَتْمَمْتَ عَشْرًا فَمِنْ عِندِكَ وَمَآ أُرِيدُ أَنْ أَشُقَّ عَلَيْكَ سَتَجِدُنِىٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّٰلِحِينَ', 'قال إني أريد أن أنكحك إحدى ابنتي هاتين على أن تأجرني ثماني حجج فإن أتممت عشرا فمن عندك وما أريد أن أشق عليك ستجدني إن شاء الله من الصالحين', 20, 39, 388, 155, '28:27'),
(3280, NULL, NULL, 28, 28, 'قَالَ ذَٰلِكَ بَيْنِى وَبَيْنَكَ أَيَّمَا ٱلْأَجَلَيْنِ قَضَيْتُ فَلَا عُدْوَٰنَ عَلَىَّ وَٱللَّهُ عَلَىٰ مَا نَقُولُ وَكِيلٌ', 'قال ذلك بيني وبينك أيما الأجلين قضيت فلا عدوان علي والله على ما نقول وكيل', 20, 39, 388, 155, '28:28'),
(3281, NULL, NULL, 28, 29, 'فَلَمَّا قَضَىٰ مُوسَى ٱلْأَجَلَ وَسَارَ بِأَهْلِهِۦٓ ءَانَسَ مِن جَانِبِ ٱلطُّورِ نَارًا قَالَ لِأَهْلِهِ ٱمْكُثُوٓا۟ إِنِّىٓ ءَانَسْتُ نَارًا لَّعَلِّىٓ ءَاتِيكُم مِّنْهَا بِخَبَرٍ أَوْ جَذْوَةٍ مِّنَ ٱلنَّارِ لَعَلَّكُمْ تَصْطَلُونَ', 'فلما قضى موسى الأجل وسار بأهله آنس من جانب الطور نارا قال لأهله امكثوا إني آنست نارا لعلي آتيكم منها بخبر أو جذوة من النار لعلكم تصطلون', 20, 39, 389, 156, '28:29'),
(3282, NULL, NULL, 28, 30, 'فَلَمَّآ أَتَىٰهَا نُودِىَ مِن شَٰطِئِ ٱلْوَادِ ٱلْأَيْمَنِ فِى ٱلْبُقْعَةِ ٱلْمُبَٰرَكَةِ مِنَ ٱلشَّجَرَةِ أَن يَٰمُوسَىٰٓ إِنِّىٓ أَنَا ٱللَّهُ رَبُّ ٱلْعَٰلَمِينَ', 'فلما أتاها نودي من شاطئ الواد الأيمن في البقعة المباركة من الشجرة أن يا موسى إني أنا الله رب العالمين', 20, 39, 389, 156, '28:30'),
(3283, NULL, NULL, 28, 31, 'وَأَنْ أَلْقِ عَصَاكَ فَلَمَّا رَءَاهَا تَهْتَزُّ كَأَنَّهَا جَآنٌّ وَلَّىٰ مُدْبِرًا وَلَمْ يُعَقِّبْ يَٰمُوسَىٰٓ أَقْبِلْ وَلَا تَخَفْ إِنَّكَ مِنَ ٱلْءَامِنِينَ', 'وأن ألق عصاك فلما رآها تهتز كأنها جان ولى مدبرا ولم يعقب يا موسى أقبل ولا تخف إنك من الآمنين', 20, 39, 389, 156, '28:31'),
(3284, NULL, NULL, 28, 32, 'ٱسْلُكْ يَدَكَ فِى جَيْبِكَ تَخْرُجْ بَيْضَآءَ مِنْ غَيْرِ سُوٓءٍ وَٱضْمُمْ إِلَيْكَ جَنَاحَكَ مِنَ ٱلرَّهْبِ فَذَٰنِكَ بُرْهَٰنَانِ مِن رَّبِّكَ إِلَىٰ فِرْعَوْنَ وَمَلَإِي۟هِۦٓ إِنَّهُمْ كَانُوا۟ قَوْمًا فَٰسِقِينَ', 'اسلك يدك في جيبك تخرج بيضاء من غير سوء واضمم إليك جناحك من الرهب فذانك برهانان من ربك إلى فرعون وملئه إنهم كانوا قوما فاسقين', 20, 39, 389, 156, '28:32'),
(3285, NULL, NULL, 28, 33, 'قَالَ رَبِّ إِنِّى قَتَلْتُ مِنْهُمْ نَفْسًا فَأَخَافُ أَن يَقْتُلُونِ', 'قال رب إني قتلت منهم نفسا فأخاف أن يقتلون', 20, 39, 389, 156, '28:33'),
(3286, NULL, NULL, 28, 34, 'وَأَخِى هَٰرُونُ هُوَ أَفْصَحُ مِنِّى لِسَانًا فَأَرْسِلْهُ مَعِىَ رِدْءًا يُصَدِّقُنِىٓ إِنِّىٓ أَخَافُ أَن يُكَذِّبُونِ', 'وأخي هارون هو أفصح مني لسانا فأرسله معي ردءا يصدقني إني أخاف أن يكذبون', 20, 39, 389, 156, '28:34'),
(3287, NULL, NULL, 28, 35, 'قَالَ سَنَشُدُّ عَضُدَكَ بِأَخِيكَ وَنَجْعَلُ لَكُمَا سُلْطَٰنًا فَلَا يَصِلُونَ إِلَيْكُمَا بِـَٔايَٰتِنَآ أَنتُمَا وَمَنِ ٱتَّبَعَكُمَا ٱلْغَٰلِبُونَ', 'قال سنشد عضدك بأخيك ونجعل لكما سلطانا فلا يصلون إليكما بآياتنا أنتما ومن اتبعكما الغالبون', 20, 39, 389, 156, '28:35'),
(3288, NULL, NULL, 28, 36, 'فَلَمَّا جَآءَهُم مُّوسَىٰ بِـَٔايَٰتِنَا بَيِّنَٰتٍ قَالُوا۟ مَا هَٰذَآ إِلَّا سِحْرٌ مُّفْتَرًى وَمَا سَمِعْنَا بِهَٰذَا فِىٓ ءَابَآئِنَا ٱلْأَوَّلِينَ', 'فلما جاءهم موسى بآياتنا بينات قالوا ما هذا إلا سحر مفترى وما سمعنا بهذا في آبائنا الأولين', 20, 39, 390, 156, '28:36'),
(3289, NULL, NULL, 28, 37, 'وَقَالَ مُوسَىٰ رَبِّىٓ أَعْلَمُ بِمَن جَآءَ بِٱلْهُدَىٰ مِنْ عِندِهِۦ وَمَن تَكُونُ لَهُۥ عَٰقِبَةُ ٱلدَّارِ إِنَّهُۥ لَا يُفْلِحُ ٱلظَّٰلِمُونَ', 'وقال موسى ربي أعلم بمن جاء بالهدى من عنده ومن تكون له عاقبة الدار إنه لا يفلح الظالمون', 20, 39, 390, 156, '28:37'),
(3290, NULL, NULL, 28, 38, 'وَقَالَ فِرْعَوْنُ يَٰٓأَيُّهَا ٱلْمَلَأُ مَا عَلِمْتُ لَكُم مِّنْ إِلَٰهٍ غَيْرِى فَأَوْقِدْ لِى يَٰهَٰمَٰنُ عَلَى ٱلطِّينِ فَٱجْعَل لِّى صَرْحًا لَّعَلِّىٓ أَطَّلِعُ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّى لَأَظُنُّهُۥ مِنَ ٱلْكَٰذِبِينَ', 'وقال فرعون يا أيها الملأ ما علمت لكم من إله غيري فأوقد لي يا هامان على الطين فاجعل لي صرحا لعلي أطلع إلى إله موسى وإني لأظنه من الكاذبين', 20, 39, 390, 156, '28:38'),
(3291, NULL, NULL, 28, 39, 'وَٱسْتَكْبَرَ هُوَ وَجُنُودُهُۥ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَظَنُّوٓا۟ أَنَّهُمْ إِلَيْنَا لَا يُرْجَعُونَ', 'واستكبر هو وجنوده في الأرض بغير الحق وظنوا أنهم إلينا لا يرجعون', 20, 39, 390, 156, '28:39'),
(3292, NULL, NULL, 28, 40, 'فَأَخَذْنَٰهُ وَجُنُودَهُۥ فَنَبَذْنَٰهُمْ فِى ٱلْيَمِّ فَٱنظُرْ كَيْفَ كَانَ عَٰقِبَةُ ٱلظَّٰلِمِينَ', 'فأخذناه وجنوده فنبذناهم في اليم فانظر كيف كان عاقبة الظالمين', 20, 39, 390, 156, '28:40'),
(3293, NULL, NULL, 28, 41, 'وَجَعَلْنَٰهُمْ أَئِمَّةً يَدْعُونَ إِلَى ٱلنَّارِ وَيَوْمَ ٱلْقِيَٰمَةِ لَا يُنصَرُونَ', 'وجعلناهم أئمة يدعون إلى النار ويوم القيامة لا ينصرون', 20, 39, 390, 156, '28:41'),
(3294, NULL, NULL, 28, 42, 'وَأَتْبَعْنَٰهُمْ فِى هَٰذِهِ ٱلدُّنْيَا لَعْنَةً وَيَوْمَ ٱلْقِيَٰمَةِ هُم مِّنَ ٱلْمَقْبُوحِينَ', 'وأتبعناهم في هذه الدنيا لعنة ويوم القيامة هم من المقبوحين', 20, 39, 390, 156, '28:42'),
(3295, NULL, NULL, 28, 43, 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَٰبَ مِنۢ بَعْدِ مَآ أَهْلَكْنَا ٱلْقُرُونَ ٱلْأُولَىٰ بَصَآئِرَ لِلنَّاسِ وَهُدًى وَرَحْمَةً لَّعَلَّهُمْ يَتَذَكَّرُونَ', 'ولقد آتينا موسى الكتاب من بعد ما أهلكنا القرون الأولى بصائر للناس وهدى ورحمة لعلهم يتذكرون', 20, 39, 390, 156, '28:43'),
(3296, NULL, NULL, 28, 44, 'وَمَا كُنتَ بِجَانِبِ ٱلْغَرْبِىِّ إِذْ قَضَيْنَآ إِلَىٰ مُوسَى ٱلْأَمْرَ وَمَا كُنتَ مِنَ ٱلشَّٰهِدِينَ', 'وما كنت بجانب الغربي إذ قضينا إلى موسى الأمر وما كنت من الشاهدين', 20, 39, 391, 156, '28:44'),
(3297, NULL, NULL, 28, 45, 'وَلَٰكِنَّآ أَنشَأْنَا قُرُونًا فَتَطَاوَلَ عَلَيْهِمُ ٱلْعُمُرُ وَمَا كُنتَ ثَاوِيًا فِىٓ أَهْلِ مَدْيَنَ تَتْلُوا۟ عَلَيْهِمْ ءَايَٰتِنَا وَلَٰكِنَّا كُنَّا مُرْسِلِينَ', 'ولكنا أنشأنا قرونا فتطاول عليهم العمر وما كنت ثاويا في أهل مدين تتلو عليهم آياتنا ولكنا كنا مرسلين', 20, 39, 391, 156, '28:45'),
(3298, NULL, NULL, 28, 46, 'وَمَا كُنتَ بِجَانِبِ ٱلطُّورِ إِذْ نَادَيْنَا وَلَٰكِن رَّحْمَةً مِّن رَّبِّكَ لِتُنذِرَ قَوْمًا مَّآ أَتَىٰهُم مِّن نَّذِيرٍ مِّن قَبْلِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ', 'وما كنت بجانب الطور إذ نادينا ولكن رحمة من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يتذكرون', 20, 39, 391, 156, '28:46'),
(3299, NULL, NULL, 28, 47, 'وَلَوْلَآ أَن تُصِيبَهُم مُّصِيبَةٌۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَيَقُولُوا۟ رَبَّنَا لَوْلَآ أَرْسَلْتَ إِلَيْنَا رَسُولًا فَنَتَّبِعَ ءَايَٰتِكَ وَنَكُونَ مِنَ ٱلْمُؤْمِنِينَ', 'ولولا أن تصيبهم مصيبة بما قدمت أيديهم فيقولوا ربنا لولا أرسلت إلينا رسولا فنتبع آياتك ونكون من المؤمنين', 20, 39, 391, 156, '28:47'),
(3300, NULL, NULL, 28, 48, 'فَلَمَّا جَآءَهُمُ ٱلْحَقُّ مِنْ عِندِنَا قَالُوا۟ لَوْلَآ أُوتِىَ مِثْلَ مَآ أُوتِىَ مُوسَىٰٓ أَوَلَمْ يَكْفُرُوا۟ بِمَآ أُوتِىَ مُوسَىٰ مِن قَبْلُ قَالُوا۟ سِحْرَانِ تَظَٰهَرَا وَقَالُوٓا۟ إِنَّا بِكُلٍّ كَٰفِرُونَ', 'فلما جاءهم الحق من عندنا قالوا لولا أوتي مثل ما أوتي موسى أولم يكفروا بما أوتي موسى من قبل قالوا سحران تظاهرا وقالوا إنا بكل كافرون', 20, 39, 391, 156, '28:48'),
(3301, NULL, NULL, 28, 49, 'قُلْ فَأْتُوا۟ بِكِتَٰبٍ مِّنْ عِندِ ٱللَّهِ هُوَ أَهْدَىٰ مِنْهُمَآ أَتَّبِعْهُ إِن كُنتُمْ صَٰدِقِينَ', 'قل فأتوا بكتاب من عند الله هو أهدى منهما أتبعه إن كنتم صادقين', 20, 39, 391, 156, '28:49'),
(3302, NULL, NULL, 28, 50, 'فَإِن لَّمْ يَسْتَجِيبُوا۟ لَكَ فَٱعْلَمْ أَنَّمَا يَتَّبِعُونَ أَهْوَآءَهُمْ وَمَنْ أَضَلُّ مِمَّنِ ٱتَّبَعَ هَوَىٰهُ بِغَيْرِ هُدًى مِّنَ ٱللَّهِ إِنَّ ٱللَّهَ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّٰلِمِينَ', 'فإن لم يستجيبوا لك فاعلم أنما يتبعون أهواءهم ومن أضل ممن اتبع هواه بغير هدى من الله إن الله لا يهدي القوم الظالمين', 20, 39, 391, 156, '28:50'),
(3303, NULL, NULL, 28, 51, 'وَلَقَدْ وَصَّلْنَا لَهُمُ ٱلْقَوْلَ لَعَلَّهُمْ يَتَذَكَّرُونَ', 'ولقد وصلنا لهم القول لعلهم يتذكرون', 20, 40, 392, 157, '28:51'),
(3304, NULL, NULL, 28, 52, 'ٱلَّذِينَ ءَاتَيْنَٰهُمُ ٱلْكِتَٰبَ مِن قَبْلِهِۦ هُم بِهِۦ يُؤْمِنُونَ', 'الذين آتيناهم الكتاب من قبله هم به يؤمنون', 20, 40, 392, 157, '28:52'),
(3305, NULL, NULL, 28, 53, 'وَإِذَا يُتْلَىٰ عَلَيْهِمْ قَالُوٓا۟ ءَامَنَّا بِهِۦٓ إِنَّهُ ٱلْحَقُّ مِن رَّبِّنَآ إِنَّا كُنَّا مِن قَبْلِهِۦ مُسْلِمِينَ', 'وإذا يتلى عليهم قالوا آمنا به إنه الحق من ربنا إنا كنا من قبله مسلمين', 20, 40, 392, 157, '28:53'),
(3306, NULL, NULL, 28, 54, 'أُو۟لَٰٓئِكَ يُؤْتَوْنَ أَجْرَهُم مَّرَّتَيْنِ بِمَا صَبَرُوا۟ وَيَدْرَءُونَ بِٱلْحَسَنَةِ ٱلسَّيِّئَةَ وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَ', 'أولئك يؤتون أجرهم مرتين بما صبروا ويدرءون بالحسنة السيئة ومما رزقناهم ينفقون', 20, 40, 392, 157, '28:54'),
(3307, NULL, NULL, 28, 55, 'وَإِذَا سَمِعُوا۟ ٱللَّغْوَ أَعْرَضُوا۟ عَنْهُ وَقَالُوا۟ لَنَآ أَعْمَٰلُنَا وَلَكُمْ أَعْمَٰلُكُمْ سَلَٰمٌ عَلَيْكُمْ لَا نَبْتَغِى ٱلْجَٰهِلِينَ', 'وإذا سمعوا اللغو أعرضوا عنه وقالوا لنا أعمالنا ولكم أعمالكم سلام عليكم لا نبتغي الجاهلين', 20, 40, 392, 157, '28:55'),
(3308, NULL, NULL, 28, 56, 'إِنَّكَ لَا تَهْدِى مَنْ أَحْبَبْتَ وَلَٰكِنَّ ٱللَّهَ يَهْدِى مَن يَشَآءُ وَهُوَ أَعْلَمُ بِٱلْمُهْتَدِينَ', 'إنك لا تهدي من أحببت ولكن الله يهدي من يشاء وهو أعلم بالمهتدين', 20, 40, 392, 157, '28:56'),
(3309, NULL, NULL, 28, 57, 'وَقَالُوٓا۟ إِن نَّتَّبِعِ ٱلْهُدَىٰ مَعَكَ نُتَخَطَّفْ مِنْ أَرْضِنَآ أَوَلَمْ نُمَكِّن لَّهُمْ حَرَمًا ءَامِنًا يُجْبَىٰٓ إِلَيْهِ ثَمَرَٰتُ كُلِّ شَىْءٍ رِّزْقًا مِّن لَّدُنَّا وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 'وقالوا إن نتبع الهدى معك نتخطف من أرضنا أولم نمكن لهم حرما آمنا يجبى إليه ثمرات كل شيء رزقا من لدنا ولكن أكثرهم لا يعلمون', 20, 40, 392, 157, '28:57'),
(3310, NULL, NULL, 28, 58, 'وَكَمْ أَهْلَكْنَا مِن قَرْيَةٍۭ بَطِرَتْ مَعِيشَتَهَا فَتِلْكَ مَسَٰكِنُهُمْ لَمْ تُسْكَن مِّنۢ بَعْدِهِمْ إِلَّا قَلِيلًا وَكُنَّا نَحْنُ ٱلْوَٰرِثِينَ', 'وكم أهلكنا من قرية بطرت معيشتها فتلك مساكنهم لم تسكن من بعدهم إلا قليلا وكنا نحن الوارثين', 20, 40, 392, 157, '28:58'),
(3311, NULL, NULL, 28, 59, 'وَمَا كَانَ رَبُّكَ مُهْلِكَ ٱلْقُرَىٰ حَتَّىٰ يَبْعَثَ فِىٓ أُمِّهَا رَسُولًا يَتْلُوا۟ عَلَيْهِمْ ءَايَٰتِنَا وَمَا كُنَّا مُهْلِكِى ٱلْقُرَىٰٓ إِلَّا وَأَهْلُهَا ظَٰلِمُونَ', 'وما كان ربك مهلك القرى حتى يبعث في أمها رسولا يتلو عليهم آياتنا وما كنا مهلكي القرى إلا وأهلها ظالمون', 20, 40, 392, 157, '28:59'),
(3312, NULL, NULL, 28, 60, 'وَمَآ أُوتِيتُم مِّن شَىْءٍ فَمَتَٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا وَزِينَتُهَا وَمَا عِندَ ٱللَّهِ خَيْرٌ وَأَبْقَىٰٓ أَفَلَا تَعْقِلُونَ', 'وما أوتيتم من شيء فمتاع الحياة الدنيا وزينتها وما عند الله خير وأبقى أفلا تعقلون', 20, 40, 393, 157, '28:60'),
(3313, NULL, NULL, 28, 61, 'أَفَمَن وَعَدْنَٰهُ وَعْدًا حَسَنًا فَهُوَ لَٰقِيهِ كَمَن مَّتَّعْنَٰهُ مَتَٰعَ ٱلْحَيَوٰةِ ٱلدُّنْيَا ثُمَّ هُوَ يَوْمَ ٱلْقِيَٰمَةِ مِنَ ٱلْمُحْضَرِينَ', 'أفمن وعدناه وعدا حسنا فهو لاقيه كمن متعناه متاع الحياة الدنيا ثم هو يوم القيامة من المحضرين', 20, 40, 393, 157, '28:61'),
(3314, NULL, NULL, 28, 62, 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَآءِىَ ٱلَّذِينَ كُنتُمْ تَزْعُمُونَ', 'ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون', 20, 40, 393, 157, '28:62'),
(3315, NULL, NULL, 28, 63, 'قَالَ ٱلَّذِينَ حَقَّ عَلَيْهِمُ ٱلْقَوْلُ رَبَّنَا هَٰٓؤُلَآءِ ٱلَّذِينَ أَغْوَيْنَآ أَغْوَيْنَٰهُمْ كَمَا غَوَيْنَا تَبَرَّأْنَآ إِلَيْكَ مَا كَانُوٓا۟ إِيَّانَا يَعْبُدُونَ', 'قال الذين حق عليهم القول ربنا هؤلاء الذين أغوينا أغويناهم كما غوينا تبرأنا إليك ما كانوا إيانا يعبدون', 20, 40, 393, 157, '28:63'),
(3316, NULL, NULL, 28, 64, 'وَقِيلَ ٱدْعُوا۟ شُرَكَآءَكُمْ فَدَعَوْهُمْ فَلَمْ يَسْتَجِيبُوا۟ لَهُمْ وَرَأَوُا۟ ٱلْعَذَابَ لَوْ أَنَّهُمْ كَانُوا۟ يَهْتَدُونَ', 'وقيل ادعوا شركاءكم فدعوهم فلم يستجيبوا لهم ورأوا العذاب لو أنهم كانوا يهتدون', 20, 40, 393, 157, '28:64'),
(3317, NULL, NULL, 28, 65, 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ مَاذَآ أَجَبْتُمُ ٱلْمُرْسَلِينَ', 'ويوم يناديهم فيقول ماذا أجبتم المرسلين', 20, 40, 393, 157, '28:65'),
(3318, NULL, NULL, 28, 66, 'فَعَمِيَتْ عَلَيْهِمُ ٱلْأَنۢبَآءُ يَوْمَئِذٍ فَهُمْ لَا يَتَسَآءَلُونَ', 'فعميت عليهم الأنباء يومئذ فهم لا يتساءلون', 20, 40, 393, 157, '28:66'),
(3319, NULL, NULL, 28, 67, 'فَأَمَّا مَن تَابَ وَءَامَنَ وَعَمِلَ صَٰلِحًا فَعَسَىٰٓ أَن يَكُونَ مِنَ ٱلْمُفْلِحِينَ', 'فأما من تاب وآمن وعمل صالحا فعسى أن يكون من المفلحين', 20, 40, 393, 157, '28:67'),
(3320, NULL, NULL, 28, 68, 'وَرَبُّكَ يَخْلُقُ مَا يَشَآءُ وَيَخْتَارُ مَا كَانَ لَهُمُ ٱلْخِيَرَةُ سُبْحَٰنَ ٱللَّهِ وَتَعَٰلَىٰ عَمَّا يُشْرِكُونَ', 'وربك يخلق ما يشاء ويختار ما كان لهم الخيرة سبحان الله وتعالى عما يشركون', 20, 40, 393, 157, '28:68'),
(3321, NULL, NULL, 28, 69, 'وَرَبُّكَ يَعْلَمُ مَا تُكِنُّ صُدُورُهُمْ وَمَا يُعْلِنُونَ', 'وربك يعلم ما تكن صدورهم وما يعلنون', 20, 40, 393, 157, '28:69'),
(3322, NULL, NULL, 28, 70, 'وَهُوَ ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ لَهُ ٱلْحَمْدُ فِى ٱلْأُولَىٰ وَٱلْءَاخِرَةِ وَلَهُ ٱلْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ', 'وهو الله لا إله إلا هو له الحمد في الأولى والآخرة وله الحكم وإليه ترجعون', 20, 40, 393, 157, '28:70'),
(3323, NULL, NULL, 28, 71, 'قُلْ أَرَءَيْتُمْ إِن جَعَلَ ٱللَّهُ عَلَيْكُمُ ٱلَّيْلَ سَرْمَدًا إِلَىٰ يَوْمِ ٱلْقِيَٰمَةِ مَنْ إِلَٰهٌ غَيْرُ ٱللَّهِ يَأْتِيكُم بِضِيَآءٍ أَفَلَا تَسْمَعُونَ', 'قل أرأيتم إن جعل الله عليكم الليل سرمدا إلى يوم القيامة من إله غير الله يأتيكم بضياء أفلا تسمعون', 20, 40, 394, 157, '28:71'),
(3324, NULL, NULL, 28, 72, 'قُلْ أَرَءَيْتُمْ إِن جَعَلَ ٱللَّهُ عَلَيْكُمُ ٱلنَّهَارَ سَرْمَدًا إِلَىٰ يَوْمِ ٱلْقِيَٰمَةِ مَنْ إِلَٰهٌ غَيْرُ ٱللَّهِ يَأْتِيكُم بِلَيْلٍ تَسْكُنُونَ فِيهِ أَفَلَا تُبْصِرُونَ', 'قل أرأيتم إن جعل الله عليكم النهار سرمدا إلى يوم القيامة من إله غير الله يأتيكم بليل تسكنون فيه أفلا تبصرون', 20, 40, 394, 157, '28:72'),
(3325, NULL, NULL, 28, 73, 'وَمِن رَّحْمَتِهِۦ جَعَلَ لَكُمُ ٱلَّيْلَ وَٱلنَّهَارَ لِتَسْكُنُوا۟ فِيهِ وَلِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ', 'ومن رحمته جعل لكم الليل والنهار لتسكنوا فيه ولتبتغوا من فضله ولعلكم تشكرون', 20, 40, 394, 157, '28:73'),
(3326, NULL, NULL, 28, 74, 'وَيَوْمَ يُنَادِيهِمْ فَيَقُولُ أَيْنَ شُرَكَآءِىَ ٱلَّذِينَ كُنتُمْ تَزْعُمُونَ', 'ويوم يناديهم فيقول أين شركائي الذين كنتم تزعمون', 20, 40, 394, 157, '28:74'),
(3327, NULL, NULL, 28, 75, 'وَنَزَعْنَا مِن كُلِّ أُمَّةٍ شَهِيدًا فَقُلْنَا هَاتُوا۟ بُرْهَٰنَكُمْ فَعَلِمُوٓا۟ أَنَّ ٱلْحَقَّ لِلَّهِ وَضَلَّ عَنْهُم مَّا كَانُوا۟ يَفْتَرُونَ', 'ونزعنا من كل أمة شهيدا فقلنا هاتوا برهانكم فعلموا أن الحق لله وضل عنهم ما كانوا يفترون', 20, 40, 394, 157, '28:75'),
(3328, NULL, NULL, 28, 76, 'إِنَّ قَٰرُونَ كَانَ مِن قَوْمِ مُوسَىٰ فَبَغَىٰ عَلَيْهِمْ وَءَاتَيْنَٰهُ مِنَ ٱلْكُنُوزِ مَآ إِنَّ مَفَاتِحَهُۥ لَتَنُوٓأُ بِٱلْعُصْبَةِ أُو۟لِى ٱلْقُوَّةِ إِذْ قَالَ لَهُۥ قَوْمُهُۥ لَا تَفْرَحْ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلْفَرِحِينَ', 'إن قارون كان من قوم موسى فبغى عليهم وآتيناه من الكنوز ما إن مفاتحه لتنوء بالعصبة أولي القوة إذ قال له قومه لا تفرح إن الله لا يحب الفرحين', 20, 40, 394, 158, '28:76'),
(3329, NULL, NULL, 28, 77, 'وَٱبْتَغِ فِيمَآ ءَاتَىٰكَ ٱللَّهُ ٱلدَّارَ ٱلْءَاخِرَةَ وَلَا تَنسَ نَصِيبَكَ مِنَ ٱلدُّنْيَا وَأَحْسِن كَمَآ أَحْسَنَ ٱللَّهُ إِلَيْكَ وَلَا تَبْغِ ٱلْفَسَادَ فِى ٱلْأَرْضِ إِنَّ ٱللَّهَ لَا يُحِبُّ ٱلْمُفْسِدِينَ', 'وابتغ فيما آتاك الله الدار الآخرة ولا تنس نصيبك من الدنيا وأحسن كما أحسن الله إليك ولا تبغ الفساد في الأرض إن الله لا يحب المفسدين', 20, 40, 394, 158, '28:77'),
(3330, NULL, NULL, 28, 78, 'قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلْمٍ عِندِىٓ أَوَلَمْ يَعْلَمْ أَنَّ ٱللَّهَ قَدْ أَهْلَكَ مِن قَبْلِهِۦ مِنَ ٱلْقُرُونِ مَنْ هُوَ أَشَدُّ مِنْهُ قُوَّةً وَأَكْثَرُ جَمْعًا وَلَا يُسْـَٔلُ عَن ذُنُوبِهِمُ ٱلْمُجْرِمُونَ', 'قال إنما أوتيته على علم عندي أولم يعلم أن الله قد أهلك من قبله من القرون من هو أشد منه قوة وأكثر جمعا ولا يسأل عن ذنوبهم المجرمون', 20, 40, 395, 158, '28:78'),
(3331, NULL, NULL, 28, 79, 'فَخَرَجَ عَلَىٰ قَوْمِهِۦ فِى زِينَتِهِۦ قَالَ ٱلَّذِينَ يُرِيدُونَ ٱلْحَيَوٰةَ ٱلدُّنْيَا يَٰلَيْتَ لَنَا مِثْلَ مَآ أُوتِىَ قَٰرُونُ إِنَّهُۥ لَذُو حَظٍّ عَظِيمٍ', 'فخرج على قومه في زينته قال الذين يريدون الحياة الدنيا يا ليت لنا مثل ما أوتي قارون إنه لذو حظ عظيم', 20, 40, 395, 158, '28:79'),
(3332, NULL, NULL, 28, 80, 'وَقَالَ ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ وَيْلَكُمْ ثَوَابُ ٱللَّهِ خَيْرٌ لِّمَنْ ءَامَنَ وَعَمِلَ صَٰلِحًا وَلَا يُلَقَّىٰهَآ إِلَّا ٱلصَّٰبِرُونَ', 'وقال الذين أوتوا العلم ويلكم ثواب الله خير لمن آمن وعمل صالحا ولا يلقاها إلا الصابرون', 20, 40, 395, 158, '28:80'),
(3333, NULL, NULL, 28, 81, 'فَخَسَفْنَا بِهِۦ وَبِدَارِهِ ٱلْأَرْضَ فَمَا كَانَ لَهُۥ مِن فِئَةٍ يَنصُرُونَهُۥ مِن دُونِ ٱللَّهِ وَمَا كَانَ مِنَ ٱلْمُنتَصِرِينَ', 'فخسفنا به وبداره الأرض فما كان له من فئة ينصرونه من دون الله وما كان من المنتصرين', 20, 40, 395, 158, '28:81'),
(3334, NULL, NULL, 28, 82, 'وَأَصْبَحَ ٱلَّذِينَ تَمَنَّوْا۟ مَكَانَهُۥ بِٱلْأَمْسِ يَقُولُونَ وَيْكَأَنَّ ٱللَّهَ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ مِنْ عِبَادِهِۦ وَيَقْدِرُ لَوْلَآ أَن مَّنَّ ٱللَّهُ عَلَيْنَا لَخَسَفَ بِنَا وَيْكَأَنَّهُۥ لَا يُفْلِحُ ٱلْكَٰفِرُونَ', 'وأصبح الذين تمنوا مكانه بالأمس يقولون ويكأن الله يبسط الرزق لمن يشاء من عباده ويقدر لولا أن من الله علينا لخسف بنا ويكأنه لا يفلح الكافرون', 20, 40, 395, 158, '28:82'),
(3335, NULL, NULL, 28, 83, 'تِلْكَ ٱلدَّارُ ٱلْءَاخِرَةُ نَجْعَلُهَا لِلَّذِينَ لَا يُرِيدُونَ عُلُوًّا فِى ٱلْأَرْضِ وَلَا فَسَادًا وَٱلْعَٰقِبَةُ لِلْمُتَّقِينَ', 'تلك الدار الآخرة نجعلها للذين لا يريدون علوا في الأرض ولا فسادا والعاقبة للمتقين', 20, 40, 395, 158, '28:83'),
(3336, NULL, NULL, 28, 84, 'مَن جَآءَ بِٱلْحَسَنَةِ فَلَهُۥ خَيْرٌ مِّنْهَا وَمَن جَآءَ بِٱلسَّيِّئَةِ فَلَا يُجْزَى ٱلَّذِينَ عَمِلُوا۟ ٱلسَّيِّـَٔاتِ إِلَّا مَا كَانُوا۟ يَعْمَلُونَ', 'من جاء بالحسنة فله خير منها ومن جاء بالسيئة فلا يجزى الذين عملوا السيئات إلا ما كانوا يعملون', 20, 40, 395, 158, '28:84'),
(3337, NULL, NULL, 28, 85, 'إِنَّ ٱلَّذِى فَرَضَ عَلَيْكَ ٱلْقُرْءَانَ لَرَآدُّكَ إِلَىٰ مَعَادٍ قُل رَّبِّىٓ أَعْلَمُ مَن جَآءَ بِٱلْهُدَىٰ وَمَنْ هُوَ فِى ضَلَٰلٍ مُّبِينٍ', 'إن الذي فرض عليك القرآن لرادك إلى معاد قل ربي أعلم من جاء بالهدى ومن هو في ضلال مبين', 20, 40, 396, 158, '28:85'),
(3338, NULL, NULL, 28, 86, 'وَمَا كُنتَ تَرْجُوٓا۟ أَن يُلْقَىٰٓ إِلَيْكَ ٱلْكِتَٰبُ إِلَّا رَحْمَةً مِّن رَّبِّكَ فَلَا تَكُونَنَّ ظَهِيرًا لِّلْكَٰفِرِينَ', 'وما كنت ترجو أن يلقى إليك الكتاب إلا رحمة من ربك فلا تكونن ظهيرا للكافرين', 20, 40, 396, 158, '28:86'),
(3339, NULL, NULL, 28, 87, 'وَلَا يَصُدُّنَّكَ عَنْ ءَايَٰتِ ٱللَّهِ بَعْدَ إِذْ أُنزِلَتْ إِلَيْكَ وَٱدْعُ إِلَىٰ رَبِّكَ وَلَا تَكُونَنَّ مِنَ ٱلْمُشْرِكِينَ', 'ولا يصدنك عن آيات الله بعد إذ أنزلت إليك وادع إلى ربك ولا تكونن من المشركين', 20, 40, 396, 158, '28:87'),
(3340, NULL, NULL, 28, 88, 'وَلَا تَدْعُ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ لَآ إِلَٰهَ إِلَّا هُوَ كُلُّ شَىْءٍ هَالِكٌ إِلَّا وَجْهَهُۥ لَهُ ٱلْحُكْمُ وَإِلَيْهِ تُرْجَعُونَ', 'ولا تدع مع الله إلها آخر لا إله إلا هو كل شيء هالك إلا وجهه له الحكم وإليه ترجعون', 20, 40, 396, 158, '28:88'),
(3341, NULL, NULL, 29, 1, 'الٓمٓ', 'الم', 20, 40, 396, 159, '29:1'),
(3342, NULL, NULL, 29, 2, 'أَحَسِبَ ٱلنَّاسُ أَن يُتْرَكُوٓا۟ أَن يَقُولُوٓا۟ ءَامَنَّا وَهُمْ لَا يُفْتَنُونَ', 'أحسب الناس أن يتركوا أن يقولوا آمنا وهم لا يفتنون', 20, 40, 396, 159, '29:2'),
(3343, NULL, NULL, 29, 3, 'وَلَقَدْ فَتَنَّا ٱلَّذِينَ مِن قَبْلِهِمْ فَلَيَعْلَمَنَّ ٱللَّهُ ٱلَّذِينَ صَدَقُوا۟ وَلَيَعْلَمَنَّ ٱلْكَٰذِبِينَ', 'ولقد فتنا الذين من قبلهم فليعلمن الله الذين صدقوا وليعلمن الكاذبين', 20, 40, 396, 159, '29:3'),
(3344, NULL, NULL, 29, 4, 'أَمْ حَسِبَ ٱلَّذِينَ يَعْمَلُونَ ٱلسَّيِّـَٔاتِ أَن يَسْبِقُونَا سَآءَ مَا يَحْكُمُونَ', 'أم حسب الذين يعملون السيئات أن يسبقونا ساء ما يحكمون', 20, 40, 396, 159, '29:4'),
(3345, NULL, NULL, 29, 5, 'مَن كَانَ يَرْجُوا۟ لِقَآءَ ٱللَّهِ فَإِنَّ أَجَلَ ٱللَّهِ لَءَاتٍ وَهُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ', 'من كان يرجو لقاء الله فإن أجل الله لآت وهو السميع العليم', 20, 40, 396, 159, '29:5'),
(3346, NULL, NULL, 29, 6, 'وَمَن جَٰهَدَ فَإِنَّمَا يُجَٰهِدُ لِنَفْسِهِۦٓ إِنَّ ٱللَّهَ لَغَنِىٌّ عَنِ ٱلْعَٰلَمِينَ', 'ومن جاهد فإنما يجاهد لنفسه إن الله لغني عن العالمين', 20, 40, 396, 159, '29:6'),
(3347, NULL, NULL, 29, 7, 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَنُكَفِّرَنَّ عَنْهُمْ سَيِّـَٔاتِهِمْ وَلَنَجْزِيَنَّهُمْ أَحْسَنَ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ', 'والذين آمنوا وعملوا الصالحات لنكفرن عنهم سيئاتهم ولنجزينهم أحسن الذي كانوا يعملون', 20, 40, 397, 159, '29:7'),
(3348, NULL, NULL, 29, 8, 'وَوَصَّيْنَا ٱلْإِنسَٰنَ بِوَٰلِدَيْهِ حُسْنًا وَإِن جَٰهَدَاكَ لِتُشْرِكَ بِى مَا لَيْسَ لَكَ بِهِۦ عِلْمٌ فَلَا تُطِعْهُمَآ إِلَىَّ مَرْجِعُكُمْ فَأُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ', 'ووصينا الإنسان بوالديه حسنا وإن جاهداك لتشرك بي ما ليس لك به علم فلا تطعهما إلي مرجعكم فأنبئكم بما كنتم تعملون', 20, 40, 397, 159, '29:8'),
(3349, NULL, NULL, 29, 9, 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَنُدْخِلَنَّهُمْ فِى ٱلصَّٰلِحِينَ', 'والذين آمنوا وعملوا الصالحات لندخلنهم في الصالحين', 20, 40, 397, 159, '29:9'),
(3350, NULL, NULL, 29, 10, 'وَمِنَ ٱلنَّاسِ مَن يَقُولُ ءَامَنَّا بِٱللَّهِ فَإِذَآ أُوذِىَ فِى ٱللَّهِ جَعَلَ فِتْنَةَ ٱلنَّاسِ كَعَذَابِ ٱللَّهِ وَلَئِن جَآءَ نَصْرٌ مِّن رَّبِّكَ لَيَقُولُنَّ إِنَّا كُنَّا مَعَكُمْ أَوَلَيْسَ ٱللَّهُ بِأَعْلَمَ بِمَا فِى صُدُورِ ٱلْعَٰلَمِينَ', 'ومن الناس من يقول آمنا بالله فإذا أوذي في الله جعل فتنة الناس كعذاب الله ولئن جاء نصر من ربك ليقولن إنا كنا معكم أوليس الله بأعلم بما في صدور العالمين', 20, 40, 397, 159, '29:10'),
(3351, NULL, NULL, 29, 11, 'وَلَيَعْلَمَنَّ ٱللَّهُ ٱلَّذِينَ ءَامَنُوا۟ وَلَيَعْلَمَنَّ ٱلْمُنَٰفِقِينَ', 'وليعلمن الله الذين آمنوا وليعلمن المنافقين', 20, 40, 397, 159, '29:11'),
(3352, NULL, NULL, 29, 12, 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لِلَّذِينَ ءَامَنُوا۟ ٱتَّبِعُوا۟ سَبِيلَنَا وَلْنَحْمِلْ خَطَٰيَٰكُمْ وَمَا هُم بِحَٰمِلِينَ مِنْ خَطَٰيَٰهُم مِّن شَىْءٍ إِنَّهُمْ لَكَٰذِبُونَ', 'وقال الذين كفروا للذين آمنوا اتبعوا سبيلنا ولنحمل خطاياكم وما هم بحاملين من خطاياهم من شيء إنهم لكاذبون', 20, 40, 397, 159, '29:12'),
(3353, NULL, NULL, 29, 13, 'وَلَيَحْمِلُنَّ أَثْقَالَهُمْ وَأَثْقَالًا مَّعَ أَثْقَالِهِمْ وَلَيُسْـَٔلُنَّ يَوْمَ ٱلْقِيَٰمَةِ عَمَّا كَانُوا۟ يَفْتَرُونَ', 'وليحملن أثقالهم وأثقالا مع أثقالهم وليسألن يوم القيامة عما كانوا يفترون', 20, 40, 397, 159, '29:13'),
(3354, NULL, NULL, 29, 14, 'وَلَقَدْ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِۦ فَلَبِثَ فِيهِمْ أَلْفَ سَنَةٍ إِلَّا خَمْسِينَ عَامًا فَأَخَذَهُمُ ٱلطُّوفَانُ وَهُمْ ظَٰلِمُونَ', 'ولقد أرسلنا نوحا إلى قومه فلبث فيهم ألف سنة إلا خمسين عاما فأخذهم الطوفان وهم ظالمون', 20, 40, 397, 159, '29:14'),
(3355, NULL, NULL, 29, 15, 'فَأَنجَيْنَٰهُ وَأَصْحَٰبَ ٱلسَّفِينَةِ وَجَعَلْنَٰهَآ ءَايَةً لِّلْعَٰلَمِينَ', 'فأنجيناه وأصحاب السفينة وجعلناها آية للعالمين', 20, 40, 398, 159, '29:15'),
(3356, NULL, NULL, 29, 16, 'وَإِبْرَٰهِيمَ إِذْ قَالَ لِقَوْمِهِ ٱعْبُدُوا۟ ٱللَّهَ وَٱتَّقُوهُ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ', 'وإبراهيم إذ قال لقومه اعبدوا الله واتقوه ذلكم خير لكم إن كنتم تعلمون', 20, 40, 398, 159, '29:16'),
(3357, NULL, NULL, 29, 17, 'إِنَّمَا تَعْبُدُونَ مِن دُونِ ٱللَّهِ أَوْثَٰنًا وَتَخْلُقُونَ إِفْكًا إِنَّ ٱلَّذِينَ تَعْبُدُونَ مِن دُونِ ٱللَّهِ لَا يَمْلِكُونَ لَكُمْ رِزْقًا فَٱبْتَغُوا۟ عِندَ ٱللَّهِ ٱلرِّزْقَ وَٱعْبُدُوهُ وَٱشْكُرُوا۟ لَهُۥٓ إِلَيْهِ تُرْجَعُونَ', 'إنما تعبدون من دون الله أوثانا وتخلقون إفكا إن الذين تعبدون من دون الله لا يملكون لكم رزقا فابتغوا عند الله الرزق واعبدوه واشكروا له إليه ترجعون', 20, 40, 398, 159, '29:17'),
(3358, NULL, NULL, 29, 18, 'وَإِن تُكَذِّبُوا۟ فَقَدْ كَذَّبَ أُمَمٌ مِّن قَبْلِكُمْ وَمَا عَلَى ٱلرَّسُولِ إِلَّا ٱلْبَلَٰغُ ٱلْمُبِينُ', 'وإن تكذبوا فقد كذب أمم من قبلكم وما على الرسول إلا البلاغ المبين', 20, 40, 398, 159, '29:18'),
(3359, NULL, NULL, 29, 19, 'أَوَلَمْ يَرَوْا۟ كَيْفَ يُبْدِئُ ٱللَّهُ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥٓ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌ', 'أولم يروا كيف يبدئ الله الخلق ثم يعيده إن ذلك على الله يسير', 20, 40, 398, 159, '29:19'),
(3360, NULL, NULL, 29, 20, 'قُلْ سِيرُوا۟ فِى ٱلْأَرْضِ فَٱنظُرُوا۟ كَيْفَ بَدَأَ ٱلْخَلْقَ ثُمَّ ٱللَّهُ يُنشِئُ ٱلنَّشْأَةَ ٱلْءَاخِرَةَ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'قل سيروا في الأرض فانظروا كيف بدأ الخلق ثم الله ينشئ النشأة الآخرة إن الله على كل شيء قدير', 20, 40, 398, 159, '29:20'),
(3361, NULL, NULL, 29, 21, 'يُعَذِّبُ مَن يَشَآءُ وَيَرْحَمُ مَن يَشَآءُ وَإِلَيْهِ تُقْلَبُونَ', 'يعذب من يشاء ويرحم من يشاء وإليه تقلبون', 20, 40, 398, 159, '29:21'),
(3362, NULL, NULL, 29, 22, 'وَمَآ أَنتُم بِمُعْجِزِينَ فِى ٱلْأَرْضِ وَلَا فِى ٱلسَّمَآءِ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِىٍّ وَلَا نَصِيرٍ', 'وما أنتم بمعجزين في الأرض ولا في السماء وما لكم من دون الله من ولي ولا نصير', 20, 40, 398, 159, '29:22'),
(3363, NULL, NULL, 29, 23, 'وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَٰتِ ٱللَّهِ وَلِقَآئِهِۦٓ أُو۟لَٰٓئِكَ يَئِسُوا۟ مِن رَّحْمَتِى وَأُو۟لَٰٓئِكَ لَهُمْ عَذَابٌ أَلِيمٌ', 'والذين كفروا بآيات الله ولقائه أولئك يئسوا من رحمتي وأولئك لهم عذاب أليم', 20, 40, 398, 159, '29:23'),
(3364, NULL, NULL, 29, 24, 'فَمَا كَانَ جَوَابَ قَوْمِهِۦٓ إِلَّآ أَن قَالُوا۟ ٱقْتُلُوهُ أَوْ حَرِّقُوهُ فَأَنجَىٰهُ ٱللَّهُ مِنَ ٱلنَّارِ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يُؤْمِنُونَ', 'فما كان جواب قومه إلا أن قالوا اقتلوه أو حرقوه فأنجاه الله من النار إن في ذلك لآيات لقوم يؤمنون', 20, 40, 399, 159, '29:24'),
(3365, NULL, NULL, 29, 25, 'وَقَالَ إِنَّمَا ٱتَّخَذْتُم مِّن دُونِ ٱللَّهِ أَوْثَٰنًا مَّوَدَّةَ بَيْنِكُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا ثُمَّ يَوْمَ ٱلْقِيَٰمَةِ يَكْفُرُ بَعْضُكُم بِبَعْضٍ وَيَلْعَنُ بَعْضُكُم بَعْضًا وَمَأْوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّٰصِرِينَ', 'وقال إنما اتخذتم من دون الله أوثانا مودة بينكم في الحياة الدنيا ثم يوم القيامة يكفر بعضكم ببعض ويلعن بعضكم بعضا ومأواكم النار وما لكم من ناصرين', 20, 40, 399, 159, '29:25'),
(3366, NULL, NULL, 29, 26, 'فَـَٔامَنَ لَهُۥ لُوطٌ وَقَالَ إِنِّى مُهَاجِرٌ إِلَىٰ رَبِّىٓ إِنَّهُۥ هُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'فآمن له لوط وقال إني مهاجر إلى ربي إنه هو العزيز الحكيم', 20, 40, 399, 160, '29:26'),
(3367, NULL, NULL, 29, 27, 'وَوَهَبْنَا لَهُۥٓ إِسْحَٰقَ وَيَعْقُوبَ وَجَعَلْنَا فِى ذُرِّيَّتِهِ ٱلنُّبُوَّةَ وَٱلْكِتَٰبَ وَءَاتَيْنَٰهُ أَجْرَهُۥ فِى ٱلدُّنْيَا وَإِنَّهُۥ فِى ٱلْءَاخِرَةِ لَمِنَ ٱلصَّٰلِحِينَ', 'ووهبنا له إسحاق ويعقوب وجعلنا في ذريته النبوة والكتاب وآتيناه أجره في الدنيا وإنه في الآخرة لمن الصالحين', 20, 40, 399, 160, '29:27'),
(3368, NULL, NULL, 29, 28, 'وَلُوطًا إِذْ قَالَ لِقَوْمِهِۦٓ إِنَّكُمْ لَتَأْتُونَ ٱلْفَٰحِشَةَ مَا سَبَقَكُم بِهَا مِنْ أَحَدٍ مِّنَ ٱلْعَٰلَمِينَ', 'ولوطا إذ قال لقومه إنكم لتأتون الفاحشة ما سبقكم بها من أحد من العالمين', 20, 40, 399, 160, '29:28'),
(3369, NULL, NULL, 29, 29, 'أَئِنَّكُمْ لَتَأْتُونَ ٱلرِّجَالَ وَتَقْطَعُونَ ٱلسَّبِيلَ وَتَأْتُونَ فِى نَادِيكُمُ ٱلْمُنكَرَ فَمَا كَانَ جَوَابَ قَوْمِهِۦٓ إِلَّآ أَن قَالُوا۟ ٱئْتِنَا بِعَذَابِ ٱللَّهِ إِن كُنتَ مِنَ ٱلصَّٰدِقِينَ', 'أئنكم لتأتون الرجال وتقطعون السبيل وتأتون في ناديكم المنكر فما كان جواب قومه إلا أن قالوا ائتنا بعذاب الله إن كنت من الصادقين', 20, 40, 399, 160, '29:29'),
(3370, NULL, NULL, 29, 30, 'قَالَ رَبِّ ٱنصُرْنِى عَلَى ٱلْقَوْمِ ٱلْمُفْسِدِينَ', 'قال رب انصرني على القوم المفسدين', 20, 40, 399, 160, '29:30'),
(3371, NULL, NULL, 29, 31, 'وَلَمَّا جَآءَتْ رُسُلُنَآ إِبْرَٰهِيمَ بِٱلْبُشْرَىٰ قَالُوٓا۟ إِنَّا مُهْلِكُوٓا۟ أَهْلِ هَٰذِهِ ٱلْقَرْيَةِ إِنَّ أَهْلَهَا كَانُوا۟ ظَٰلِمِينَ', 'ولما جاءت رسلنا إبراهيم بالبشرى قالوا إنا مهلكو أهل هذه القرية إن أهلها كانوا ظالمين', 20, 40, 400, 160, '29:31'),
(3372, NULL, NULL, 29, 32, 'قَالَ إِنَّ فِيهَا لُوطًا قَالُوا۟ نَحْنُ أَعْلَمُ بِمَن فِيهَا لَنُنَجِّيَنَّهُۥ وَأَهْلَهُۥٓ إِلَّا ٱمْرَأَتَهُۥ كَانَتْ مِنَ ٱلْغَٰبِرِينَ', 'قال إن فيها لوطا قالوا نحن أعلم بمن فيها لننجينه وأهله إلا امرأته كانت من الغابرين', 20, 40, 400, 160, '29:32'),
(3373, NULL, NULL, 29, 33, 'وَلَمَّآ أَن جَآءَتْ رُسُلُنَا لُوطًا سِىٓءَ بِهِمْ وَضَاقَ بِهِمْ ذَرْعًا وَقَالُوا۟ لَا تَخَفْ وَلَا تَحْزَنْ إِنَّا مُنَجُّوكَ وَأَهْلَكَ إِلَّا ٱمْرَأَتَكَ كَانَتْ مِنَ ٱلْغَٰبِرِينَ', 'ولما أن جاءت رسلنا لوطا سيء بهم وضاق بهم ذرعا وقالوا لا تخف ولا تحزن إنا منجوك وأهلك إلا امرأتك كانت من الغابرين', 20, 40, 400, 160, '29:33'),
(3374, NULL, NULL, 29, 34, 'إِنَّا مُنزِلُونَ عَلَىٰٓ أَهْلِ هَٰذِهِ ٱلْقَرْيَةِ رِجْزًا مِّنَ ٱلسَّمَآءِ بِمَا كَانُوا۟ يَفْسُقُونَ', 'إنا منزلون على أهل هذه القرية رجزا من السماء بما كانوا يفسقون', 20, 40, 400, 160, '29:34'),
(3375, NULL, NULL, 29, 35, 'وَلَقَد تَّرَكْنَا مِنْهَآ ءَايَةًۢ بَيِّنَةً لِّقَوْمٍ يَعْقِلُونَ', 'ولقد تركنا منها آية بينة لقوم يعقلون', 20, 40, 400, 160, '29:35'),
(3376, NULL, NULL, 29, 36, 'وَإِلَىٰ مَدْيَنَ أَخَاهُمْ شُعَيْبًا فَقَالَ يَٰقَوْمِ ٱعْبُدُوا۟ ٱللَّهَ وَٱرْجُوا۟ ٱلْيَوْمَ ٱلْءَاخِرَ وَلَا تَعْثَوْا۟ فِى ٱلْأَرْضِ مُفْسِدِينَ', 'وإلى مدين أخاهم شعيبا فقال يا قوم اعبدوا الله وارجوا اليوم الآخر ولا تعثوا في الأرض مفسدين', 20, 40, 400, 160, '29:36'),
(3377, NULL, NULL, 29, 37, 'فَكَذَّبُوهُ فَأَخَذَتْهُمُ ٱلرَّجْفَةُ فَأَصْبَحُوا۟ فِى دَارِهِمْ جَٰثِمِينَ', 'فكذبوه فأخذتهم الرجفة فأصبحوا في دارهم جاثمين', 20, 40, 400, 160, '29:37'),
(3378, NULL, NULL, 29, 38, 'وَعَادًا وَثَمُودَا۟ وَقَد تَّبَيَّنَ لَكُم مِّن مَّسَٰكِنِهِمْ وَزَيَّنَ لَهُمُ ٱلشَّيْطَٰنُ أَعْمَٰلَهُمْ فَصَدَّهُمْ عَنِ ٱلسَّبِيلِ وَكَانُوا۟ مُسْتَبْصِرِينَ', 'وعادا وثمود وقد تبين لكم من مساكنهم وزين لهم الشيطان أعمالهم فصدهم عن السبيل وكانوا مستبصرين', 20, 40, 400, 160, '29:38'),
(3379, NULL, NULL, 29, 39, 'وَقَٰرُونَ وَفِرْعَوْنَ وَهَٰمَٰنَ وَلَقَدْ جَآءَهُم مُّوسَىٰ بِٱلْبَيِّنَٰتِ فَٱسْتَكْبَرُوا۟ فِى ٱلْأَرْضِ وَمَا كَانُوا۟ سَٰبِقِينَ', 'وقارون وفرعون وهامان ولقد جاءهم موسى بالبينات فاستكبروا في الأرض وما كانوا سابقين', 20, 40, 401, 160, '29:39'),
(3380, NULL, NULL, 29, 40, 'فَكُلًّا أَخَذْنَا بِذَنۢبِهِۦ فَمِنْهُم مَّنْ أَرْسَلْنَا عَلَيْهِ حَاصِبًا وَمِنْهُم مَّنْ أَخَذَتْهُ ٱلصَّيْحَةُ وَمِنْهُم مَّنْ خَسَفْنَا بِهِ ٱلْأَرْضَ وَمِنْهُم مَّنْ أَغْرَقْنَا وَمَا كَانَ ٱللَّهُ لِيَظْلِمَهُمْ وَلَٰكِن كَانُوٓا۟ أَنفُسَهُمْ يَظْلِمُونَ', 'فكلا أخذنا بذنبه فمنهم من أرسلنا عليه حاصبا ومنهم من أخذته الصيحة ومنهم من خسفنا به الأرض ومنهم من أغرقنا وما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون', 20, 40, 401, 160, '29:40'),
(3381, NULL, NULL, 29, 41, 'مَثَلُ ٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ أَوْلِيَآءَ كَمَثَلِ ٱلْعَنكَبُوتِ ٱتَّخَذَتْ بَيْتًا وَإِنَّ أَوْهَنَ ٱلْبُيُوتِ لَبَيْتُ ٱلْعَنكَبُوتِ لَوْ كَانُوا۟ يَعْلَمُونَ', 'مثل الذين اتخذوا من دون الله أولياء كمثل العنكبوت اتخذت بيتا وإن أوهن البيوت لبيت العنكبوت لو كانوا يعلمون', 20, 40, 401, 160, '29:41'),
(3382, NULL, NULL, 29, 42, 'إِنَّ ٱللَّهَ يَعْلَمُ مَا يَدْعُونَ مِن دُونِهِۦ مِن شَىْءٍ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'إن الله يعلم ما يدعون من دونه من شيء وهو العزيز الحكيم', 20, 40, 401, 160, '29:42'),
(3383, NULL, NULL, 29, 43, 'وَتِلْكَ ٱلْأَمْثَٰلُ نَضْرِبُهَا لِلنَّاسِ وَمَا يَعْقِلُهَآ إِلَّا ٱلْعَٰلِمُونَ', 'وتلك الأمثال نضربها للناس وما يعقلها إلا العالمون', 20, 40, 401, 160, '29:43'),
(3384, NULL, NULL, 29, 44, 'خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ إِنَّ فِى ذَٰلِكَ لَءَايَةً لِّلْمُؤْمِنِينَ', 'خلق الله السماوات والأرض بالحق إن في ذلك لآية للمؤمنين', 20, 40, 401, 160, '29:44'),
(3385, NULL, NULL, 29, 45, 'ٱتْلُ مَآ أُوحِىَ إِلَيْكَ مِنَ ٱلْكِتَٰبِ وَأَقِمِ ٱلصَّلَوٰةَ إِنَّ ٱلصَّلَوٰةَ تَنْهَىٰ عَنِ ٱلْفَحْشَآءِ وَٱلْمُنكَرِ وَلَذِكْرُ ٱللَّهِ أَكْبَرُ وَٱللَّهُ يَعْلَمُ مَا تَصْنَعُونَ', 'اتل ما أوحي إليك من الكتاب وأقم الصلاة إن الصلاة تنهى عن الفحشاء والمنكر ولذكر الله أكبر والله يعلم ما تصنعون', 20, 40, 401, 160, '29:45'),
(3386, NULL, NULL, 29, 46, 'وَلَا تُجَٰدِلُوٓا۟ أَهْلَ ٱلْكِتَٰبِ إِلَّا بِٱلَّتِى هِىَ أَحْسَنُ إِلَّا ٱلَّذِينَ ظَلَمُوا۟ مِنْهُمْ وَقُولُوٓا۟ ءَامَنَّا بِٱلَّذِىٓ أُنزِلَ إِلَيْنَا وَأُنزِلَ إِلَيْكُمْ وَإِلَٰهُنَا وَإِلَٰهُكُمْ وَٰحِدٌ وَنَحْنُ لَهُۥ مُسْلِمُونَ', 'ولا تجادلوا أهل الكتاب إلا بالتي هي أحسن إلا الذين ظلموا منهم وقولوا آمنا بالذي أنزل إلينا وأنزل إليكم وإلهنا وإلهكم واحد ونحن له مسلمون', 21, 41, 402, 161, '29:46'),
(3387, NULL, NULL, 29, 47, 'وَكَذَٰلِكَ أَنزَلْنَآ إِلَيْكَ ٱلْكِتَٰبَ فَٱلَّذِينَ ءَاتَيْنَٰهُمُ ٱلْكِتَٰبَ يُؤْمِنُونَ بِهِۦ وَمِنْ هَٰٓؤُلَآءِ مَن يُؤْمِنُ بِهِۦ وَمَا يَجْحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلْكَٰفِرُونَ', 'وكذلك أنزلنا إليك الكتاب فالذين آتيناهم الكتاب يؤمنون به ومن هؤلاء من يؤمن به وما يجحد بآياتنا إلا الكافرون', 21, 41, 402, 161, '29:47'),
(3388, NULL, NULL, 29, 48, 'وَمَا كُنتَ تَتْلُوا۟ مِن قَبْلِهِۦ مِن كِتَٰبٍ وَلَا تَخُطُّهُۥ بِيَمِينِكَ إِذًا لَّٱرْتَابَ ٱلْمُبْطِلُونَ', 'وما كنت تتلو من قبله من كتاب ولا تخطه بيمينك إذا لارتاب المبطلون', 21, 41, 402, 161, '29:48'),
(3389, NULL, NULL, 29, 49, 'بَلْ هُوَ ءَايَٰتٌۢ بَيِّنَٰتٌ فِى صُدُورِ ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ وَمَا يَجْحَدُ بِـَٔايَٰتِنَآ إِلَّا ٱلظَّٰلِمُونَ', 'بل هو آيات بينات في صدور الذين أوتوا العلم وما يجحد بآياتنا إلا الظالمون', 21, 41, 402, 161, '29:49'),
(3390, NULL, NULL, 29, 50, 'وَقَالُوا۟ لَوْلَآ أُنزِلَ عَلَيْهِ ءَايَٰتٌ مِّن رَّبِّهِۦ قُلْ إِنَّمَا ٱلْءَايَٰتُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٌ مُّبِينٌ', 'وقالوا لولا أنزل عليه آيات من ربه قل إنما الآيات عند الله وإنما أنا نذير مبين', 21, 41, 402, 161, '29:50'),
(3391, NULL, NULL, 29, 51, 'أَوَلَمْ يَكْفِهِمْ أَنَّآ أَنزَلْنَا عَلَيْكَ ٱلْكِتَٰبَ يُتْلَىٰ عَلَيْهِمْ إِنَّ فِى ذَٰلِكَ لَرَحْمَةً وَذِكْرَىٰ لِقَوْمٍ يُؤْمِنُونَ', 'أولم يكفهم أنا أنزلنا عليك الكتاب يتلى عليهم إن في ذلك لرحمة وذكرى لقوم يؤمنون', 21, 41, 402, 161, '29:51'),
(3392, NULL, NULL, 29, 52, 'قُلْ كَفَىٰ بِٱللَّهِ بَيْنِى وَبَيْنَكُمْ شَهِيدًا يَعْلَمُ مَا فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَٱلَّذِينَ ءَامَنُوا۟ بِٱلْبَٰطِلِ وَكَفَرُوا۟ بِٱللَّهِ أُو۟لَٰٓئِكَ هُمُ ٱلْخَٰسِرُونَ', 'قل كفى بالله بيني وبينكم شهيدا يعلم ما في السماوات والأرض والذين آمنوا بالباطل وكفروا بالله أولئك هم الخاسرون', 21, 41, 402, 161, '29:52'),
(3393, NULL, NULL, 29, 53, 'وَيَسْتَعْجِلُونَكَ بِٱلْعَذَابِ وَلَوْلَآ أَجَلٌ مُّسَمًّى لَّجَآءَهُمُ ٱلْعَذَابُ وَلَيَأْتِيَنَّهُم بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 'ويستعجلونك بالعذاب ولولا أجل مسمى لجاءهم العذاب وليأتينهم بغتة وهم لا يشعرون', 21, 41, 403, 161, '29:53'),
(3394, NULL, NULL, 29, 54, 'يَسْتَعْجِلُونَكَ بِٱلْعَذَابِ وَإِنَّ جَهَنَّمَ لَمُحِيطَةٌۢ بِٱلْكَٰفِرِينَ', 'يستعجلونك بالعذاب وإن جهنم لمحيطة بالكافرين', 21, 41, 403, 161, '29:54'),
(3395, NULL, NULL, 29, 55, 'يَوْمَ يَغْشَىٰهُمُ ٱلْعَذَابُ مِن فَوْقِهِمْ وَمِن تَحْتِ أَرْجُلِهِمْ وَيَقُولُ ذُوقُوا۟ مَا كُنتُمْ تَعْمَلُونَ', 'يوم يغشاهم العذاب من فوقهم ومن تحت أرجلهم ويقول ذوقوا ما كنتم تعملون', 21, 41, 403, 161, '29:55'),
(3396, NULL, NULL, 29, 56, 'يَٰعِبَادِىَ ٱلَّذِينَ ءَامَنُوٓا۟ إِنَّ أَرْضِى وَٰسِعَةٌ فَإِيَّٰىَ فَٱعْبُدُونِ', 'يا عبادي الذين آمنوا إن أرضي واسعة فإياي فاعبدون', 21, 41, 403, 161, '29:56'),
(3397, NULL, NULL, 29, 57, 'كُلُّ نَفْسٍ ذَآئِقَةُ ٱلْمَوْتِ ثُمَّ إِلَيْنَا تُرْجَعُونَ', 'كل نفس ذائقة الموت ثم إلينا ترجعون', 21, 41, 403, 161, '29:57'),
(3398, NULL, NULL, 29, 58, 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَنُبَوِّئَنَّهُم مِّنَ ٱلْجَنَّةِ غُرَفًا تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَا نِعْمَ أَجْرُ ٱلْعَٰمِلِينَ', 'والذين آمنوا وعملوا الصالحات لنبوئنهم من الجنة غرفا تجري من تحتها الأنهار خالدين فيها نعم أجر العاملين', 21, 41, 403, 161, '29:58'),
(3399, NULL, NULL, 29, 59, 'ٱلَّذِينَ صَبَرُوا۟ وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ', 'الذين صبروا وعلى ربهم يتوكلون', 21, 41, 403, 161, '29:59'),
(3400, NULL, NULL, 29, 60, 'وَكَأَيِّن مِّن دَآبَّةٍ لَّا تَحْمِلُ رِزْقَهَا ٱللَّهُ يَرْزُقُهَا وَإِيَّاكُمْ وَهُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ', 'وكأين من دابة لا تحمل رزقها الله يرزقها وإياكم وهو السميع العليم', 21, 41, 403, 161, '29:60'),
(3401, NULL, NULL, 29, 61, 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ لَيَقُولُنَّ ٱللَّهُ فَأَنَّىٰ يُؤْفَكُونَ', 'ولئن سألتهم من خلق السماوات والأرض وسخر الشمس والقمر ليقولن الله فأنى يؤفكون', 21, 41, 403, 161, '29:61'),
(3402, NULL, NULL, 29, 62, 'ٱللَّهُ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ مِنْ عِبَادِهِۦ وَيَقْدِرُ لَهُۥٓ إِنَّ ٱللَّهَ بِكُلِّ شَىْءٍ عَلِيمٌ', 'الله يبسط الرزق لمن يشاء من عباده ويقدر له إن الله بكل شيء عليم', 21, 41, 403, 161, '29:62'),
(3403, NULL, NULL, 29, 63, 'وَلَئِن سَأَلْتَهُم مَّن نَّزَّلَ مِنَ ٱلسَّمَآءِ مَآءً فَأَحْيَا بِهِ ٱلْأَرْضَ مِنۢ بَعْدِ مَوْتِهَا لَيَقُولُنَّ ٱللَّهُ قُلِ ٱلْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لَا يَعْقِلُونَ', 'ولئن سألتهم من نزل من السماء ماء فأحيا به الأرض من بعد موتها ليقولن الله قل الحمد لله بل أكثرهم لا يعقلون', 21, 41, 403, 161, '29:63'),
(3404, NULL, NULL, 29, 64, 'وَمَا هَٰذِهِ ٱلْحَيَوٰةُ ٱلدُّنْيَآ إِلَّا لَهْوٌ وَلَعِبٌ وَإِنَّ ٱلدَّارَ ٱلْءَاخِرَةَ لَهِىَ ٱلْحَيَوَانُ لَوْ كَانُوا۟ يَعْلَمُونَ', 'وما هذه الحياة الدنيا إلا لهو ولعب وإن الدار الآخرة لهي الحيوان لو كانوا يعلمون', 21, 41, 404, 161, '29:64'),
(3405, NULL, NULL, 29, 65, 'فَإِذَا رَكِبُوا۟ فِى ٱلْفُلْكِ دَعَوُا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمْ إِلَى ٱلْبَرِّ إِذَا هُمْ يُشْرِكُونَ', 'فإذا ركبوا في الفلك دعوا الله مخلصين له الدين فلما نجاهم إلى البر إذا هم يشركون', 21, 41, 404, 161, '29:65'),
(3406, NULL, NULL, 29, 66, 'لِيَكْفُرُوا۟ بِمَآ ءَاتَيْنَٰهُمْ وَلِيَتَمَتَّعُوا۟ فَسَوْفَ يَعْلَمُونَ', 'ليكفروا بما آتيناهم وليتمتعوا فسوف يعلمون', 21, 41, 404, 161, '29:66'),
(3407, NULL, NULL, 29, 67, 'أَوَلَمْ يَرَوْا۟ أَنَّا جَعَلْنَا حَرَمًا ءَامِنًا وَيُتَخَطَّفُ ٱلنَّاسُ مِنْ حَوْلِهِمْ أَفَبِٱلْبَٰطِلِ يُؤْمِنُونَ وَبِنِعْمَةِ ٱللَّهِ يَكْفُرُونَ', 'أولم يروا أنا جعلنا حرما آمنا ويتخطف الناس من حولهم أفبالباطل يؤمنون وبنعمة الله يكفرون', 21, 41, 404, 161, '29:67'),
(3408, NULL, NULL, 29, 68, 'وَمَنْ أَظْلَمُ مِمَّنِ ٱفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَوْ كَذَّبَ بِٱلْحَقِّ لَمَّا جَآءَهُۥٓ أَلَيْسَ فِى جَهَنَّمَ مَثْوًى لِّلْكَٰفِرِينَ', 'ومن أظلم ممن افترى على الله كذبا أو كذب بالحق لما جاءه أليس في جهنم مثوى للكافرين', 21, 41, 404, 161, '29:68'),
(3409, NULL, NULL, 29, 69, 'وَٱلَّذِينَ جَٰهَدُوا۟ فِينَا لَنَهْدِيَنَّهُمْ سُبُلَنَا وَإِنَّ ٱللَّهَ لَمَعَ ٱلْمُحْسِنِينَ', 'والذين جاهدوا فينا لنهدينهم سبلنا وإن الله لمع المحسنين', 21, 41, 404, 161, '29:69'),
(3410, NULL, NULL, 30, 1, 'الٓمٓ', 'الم', 21, 41, 404, 162, '30:1'),
(3411, NULL, NULL, 30, 2, 'غُلِبَتِ ٱلرُّومُ', 'غلبت الروم', 21, 41, 404, 162, '30:2'),
(3412, NULL, NULL, 30, 3, 'فِىٓ أَدْنَى ٱلْأَرْضِ وَهُم مِّنۢ بَعْدِ غَلَبِهِمْ سَيَغْلِبُونَ', 'في أدنى الأرض وهم من بعد غلبهم سيغلبون', 21, 41, 404, 162, '30:3'),
(3413, NULL, NULL, 30, 4, 'فِى بِضْعِ سِنِينَ لِلَّهِ ٱلْأَمْرُ مِن قَبْلُ وَمِنۢ بَعْدُ وَيَوْمَئِذٍ يَفْرَحُ ٱلْمُؤْمِنُونَ', 'في بضع سنين لله الأمر من قبل ومن بعد ويومئذ يفرح المؤمنون', 21, 41, 404, 162, '30:4'),
(3414, NULL, NULL, 30, 5, 'بِنَصْرِ ٱللَّهِ يَنصُرُ مَن يَشَآءُ وَهُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ', 'بنصر الله ينصر من يشاء وهو العزيز الرحيم', 21, 41, 404, 162, '30:5'),
(3415, NULL, NULL, 30, 6, 'وَعْدَ ٱللَّهِ لَا يُخْلِفُ ٱللَّهُ وَعْدَهُۥ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ', 'وعد الله لا يخلف الله وعده ولكن أكثر الناس لا يعلمون', 21, 41, 405, 162, '30:6'),
(3416, NULL, NULL, 30, 7, 'يَعْلَمُونَ ظَٰهِرًا مِّنَ ٱلْحَيَوٰةِ ٱلدُّنْيَا وَهُمْ عَنِ ٱلْءَاخِرَةِ هُمْ غَٰفِلُونَ', 'يعلمون ظاهرا من الحياة الدنيا وهم عن الآخرة هم غافلون', 21, 41, 405, 162, '30:7'),
(3417, NULL, NULL, 30, 8, 'أَوَلَمْ يَتَفَكَّرُوا۟ فِىٓ أَنفُسِهِم مَّا خَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَآ إِلَّا بِٱلْحَقِّ وَأَجَلٍ مُّسَمًّى وَإِنَّ كَثِيرًا مِّنَ ٱلنَّاسِ بِلِقَآئِ رَبِّهِمْ لَكَٰفِرُونَ', 'أولم يتفكروا في أنفسهم ما خلق الله السماوات والأرض وما بينهما إلا بالحق وأجل مسمى وإن كثيرا من الناس بلقاء ربهم لكافرون', 21, 41, 405, 162, '30:8'),
(3418, NULL, NULL, 30, 9, 'أَوَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ كَانُوٓا۟ أَشَدَّ مِنْهُمْ قُوَّةً وَأَثَارُوا۟ ٱلْأَرْضَ وَعَمَرُوهَآ أَكْثَرَ مِمَّا عَمَرُوهَا وَجَآءَتْهُمْ رُسُلُهُم بِٱلْبَيِّنَٰتِ فَمَا كَانَ ٱللَّهُ لِيَظْلِمَهُمْ وَلَٰكِن كَانُوٓا۟ أَنفُسَهُمْ يَظْلِمُونَ', 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم كانوا أشد منهم قوة وأثاروا الأرض وعمروها أكثر مما عمروها وجاءتهم رسلهم بالبينات فما كان الله ليظلمهم ولكن كانوا أنفسهم يظلمون', 21, 41, 405, 162, '30:9'),
(3419, NULL, NULL, 30, 10, 'ثُمَّ كَانَ عَٰقِبَةَ ٱلَّذِينَ أَسَٰٓـُٔوا۟ ٱلسُّوٓأَىٰٓ أَن كَذَّبُوا۟ بِـَٔايَٰتِ ٱللَّهِ وَكَانُوا۟ بِهَا يَسْتَهْزِءُونَ', 'ثم كان عاقبة الذين أساءوا السوأى أن كذبوا بآيات الله وكانوا بها يستهزئون', 21, 41, 405, 162, '30:10'),
(3420, NULL, NULL, 30, 11, 'ٱللَّهُ يَبْدَؤُا۟ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥ ثُمَّ إِلَيْهِ تُرْجَعُونَ', 'الله يبدأ الخلق ثم يعيده ثم إليه ترجعون', 21, 41, 405, 162, '30:11'),
(3421, NULL, NULL, 30, 12, 'وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يُبْلِسُ ٱلْمُجْرِمُونَ', 'ويوم تقوم الساعة يبلس المجرمون', 21, 41, 405, 162, '30:12'),
(3422, NULL, NULL, 30, 13, 'وَلَمْ يَكُن لَّهُم مِّن شُرَكَآئِهِمْ شُفَعَٰٓؤُا۟ وَكَانُوا۟ بِشُرَكَآئِهِمْ كَٰفِرِينَ', 'ولم يكن لهم من شركائهم شفعاء وكانوا بشركائهم كافرين', 21, 41, 405, 162, '30:13'),
(3423, NULL, NULL, 30, 14, 'وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يَوْمَئِذٍ يَتَفَرَّقُونَ', 'ويوم تقوم الساعة يومئذ يتفرقون', 21, 41, 405, 162, '30:14'),
(3424, NULL, NULL, 30, 15, 'فَأَمَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ فَهُمْ فِى رَوْضَةٍ يُحْبَرُونَ', 'فأما الذين آمنوا وعملوا الصالحات فهم في روضة يحبرون', 21, 41, 405, 162, '30:15'),
(3425, NULL, NULL, 30, 16, 'وَأَمَّا ٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔايَٰتِنَا وَلِقَآئِ ٱلْءَاخِرَةِ فَأُو۟لَٰٓئِكَ فِى ٱلْعَذَابِ مُحْضَرُونَ', 'وأما الذين كفروا وكذبوا بآياتنا ولقاء الآخرة فأولئك في العذاب محضرون', 21, 41, 406, 162, '30:16'),
(3426, NULL, NULL, 30, 17, 'فَسُبْحَٰنَ ٱللَّهِ حِينَ تُمْسُونَ وَحِينَ تُصْبِحُونَ', 'فسبحان الله حين تمسون وحين تصبحون', 21, 41, 406, 162, '30:17'),
(3427, NULL, NULL, 30, 18, 'وَلَهُ ٱلْحَمْدُ فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَعَشِيًّا وَحِينَ تُظْهِرُونَ', 'وله الحمد في السماوات والأرض وعشيا وحين تظهرون', 21, 41, 406, 162, '30:18'),
(3428, NULL, NULL, 30, 19, 'يُخْرِجُ ٱلْحَىَّ مِنَ ٱلْمَيِّتِ وَيُخْرِجُ ٱلْمَيِّتَ مِنَ ٱلْحَىِّ وَيُحْىِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا وَكَذَٰلِكَ تُخْرَجُونَ', 'يخرج الحي من الميت ويخرج الميت من الحي ويحيي الأرض بعد موتها وكذلك تخرجون', 21, 41, 406, 162, '30:19'),
(3429, NULL, NULL, 30, 20, 'وَمِنْ ءَايَٰتِهِۦٓ أَنْ خَلَقَكُم مِّن تُرَابٍ ثُمَّ إِذَآ أَنتُم بَشَرٌ تَنتَشِرُونَ', 'ومن آياته أن خلقكم من تراب ثم إذا أنتم بشر تنتشرون', 21, 41, 406, 162, '30:20'),
(3430, NULL, NULL, 30, 21, 'وَمِنْ ءَايَٰتِهِۦٓ أَنْ خَلَقَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَٰجًا لِّتَسْكُنُوٓا۟ إِلَيْهَا وَجَعَلَ بَيْنَكُم مَّوَدَّةً وَرَحْمَةً إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يَتَفَكَّرُونَ', 'ومن آياته أن خلق لكم من أنفسكم أزواجا لتسكنوا إليها وجعل بينكم مودة ورحمة إن في ذلك لآيات لقوم يتفكرون', 21, 41, 406, 162, '30:21'),
(3431, NULL, NULL, 30, 22, 'وَمِنْ ءَايَٰتِهِۦ خَلْقُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَٱخْتِلَٰفُ أَلْسِنَتِكُمْ وَأَلْوَٰنِكُمْ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّلْعَٰلِمِينَ', 'ومن آياته خلق السماوات والأرض واختلاف ألسنتكم وألوانكم إن في ذلك لآيات للعالمين', 21, 41, 406, 162, '30:22'),
(3432, NULL, NULL, 30, 23, 'وَمِنْ ءَايَٰتِهِۦ مَنَامُكُم بِٱلَّيْلِ وَٱلنَّهَارِ وَٱبْتِغَآؤُكُم مِّن فَضْلِهِۦٓ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يَسْمَعُونَ', 'ومن آياته منامكم بالليل والنهار وابتغاؤكم من فضله إن في ذلك لآيات لقوم يسمعون', 21, 41, 406, 162, '30:23');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(3433, NULL, NULL, 30, 24, 'وَمِنْ ءَايَٰتِهِۦ يُرِيكُمُ ٱلْبَرْقَ خَوْفًا وَطَمَعًا وَيُنَزِّلُ مِنَ ٱلسَّمَآءِ مَآءً فَيُحْىِۦ بِهِ ٱلْأَرْضَ بَعْدَ مَوْتِهَآ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يَعْقِلُونَ', 'ومن آياته يريكم البرق خوفا وطمعا وينزل من السماء ماء فيحيي به الأرض بعد موتها إن في ذلك لآيات لقوم يعقلون', 21, 41, 406, 162, '30:24'),
(3434, NULL, NULL, 30, 25, 'وَمِنْ ءَايَٰتِهِۦٓ أَن تَقُومَ ٱلسَّمَآءُ وَٱلْأَرْضُ بِأَمْرِهِۦ ثُمَّ إِذَا دَعَاكُمْ دَعْوَةً مِّنَ ٱلْأَرْضِ إِذَآ أَنتُمْ تَخْرُجُونَ', 'ومن آياته أن تقوم السماء والأرض بأمره ثم إذا دعاكم دعوة من الأرض إذا أنتم تخرجون', 21, 41, 407, 162, '30:25'),
(3435, NULL, NULL, 30, 26, 'وَلَهُۥ مَن فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ كُلٌّ لَّهُۥ قَٰنِتُونَ', 'وله من في السماوات والأرض كل له قانتون', 21, 41, 407, 162, '30:26'),
(3436, NULL, NULL, 30, 27, 'وَهُوَ ٱلَّذِى يَبْدَؤُا۟ ٱلْخَلْقَ ثُمَّ يُعِيدُهُۥ وَهُوَ أَهْوَنُ عَلَيْهِ وَلَهُ ٱلْمَثَلُ ٱلْأَعْلَىٰ فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'وهو الذي يبدأ الخلق ثم يعيده وهو أهون عليه وله المثل الأعلى في السماوات والأرض وهو العزيز الحكيم', 21, 41, 407, 162, '30:27'),
(3437, NULL, NULL, 30, 28, 'ضَرَبَ لَكُم مَّثَلًا مِّنْ أَنفُسِكُمْ هَل لَّكُم مِّن مَّا مَلَكَتْ أَيْمَٰنُكُم مِّن شُرَكَآءَ فِى مَا رَزَقْنَٰكُمْ فَأَنتُمْ فِيهِ سَوَآءٌ تَخَافُونَهُمْ كَخِيفَتِكُمْ أَنفُسَكُمْ كَذَٰلِكَ نُفَصِّلُ ٱلْءَايَٰتِ لِقَوْمٍ يَعْقِلُونَ', 'ضرب لكم مثلا من أنفسكم هل لكم من ما ملكت أيمانكم من شركاء في ما رزقناكم فأنتم فيه سواء تخافونهم كخيفتكم أنفسكم كذلك نفصل الآيات لقوم يعقلون', 21, 41, 407, 162, '30:28'),
(3438, NULL, NULL, 30, 29, 'بَلِ ٱتَّبَعَ ٱلَّذِينَ ظَلَمُوٓا۟ أَهْوَآءَهُم بِغَيْرِ عِلْمٍ فَمَن يَهْدِى مَنْ أَضَلَّ ٱللَّهُ وَمَا لَهُم مِّن نَّٰصِرِينَ', 'بل اتبع الذين ظلموا أهواءهم بغير علم فمن يهدي من أضل الله وما لهم من ناصرين', 21, 41, 407, 162, '30:29'),
(3439, NULL, NULL, 30, 30, 'فَأَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا فِطْرَتَ ٱللَّهِ ٱلَّتِى فَطَرَ ٱلنَّاسَ عَلَيْهَا لَا تَبْدِيلَ لِخَلْقِ ٱللَّهِ ذَٰلِكَ ٱلدِّينُ ٱلْقَيِّمُ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ', 'فأقم وجهك للدين حنيفا فطرت الله التي فطر الناس عليها لا تبديل لخلق الله ذلك الدين القيم ولكن أكثر الناس لا يعلمون', 21, 41, 407, 162, '30:30'),
(3440, NULL, NULL, 30, 31, 'مُنِيبِينَ إِلَيْهِ وَٱتَّقُوهُ وَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَلَا تَكُونُوا۟ مِنَ ٱلْمُشْرِكِينَ', 'منيبين إليه واتقوه وأقيموا الصلاة ولا تكونوا من المشركين', 21, 41, 407, 163, '30:31'),
(3441, NULL, NULL, 30, 32, 'مِنَ ٱلَّذِينَ فَرَّقُوا۟ دِينَهُمْ وَكَانُوا۟ شِيَعًا كُلُّ حِزْبٍۭ بِمَا لَدَيْهِمْ فَرِحُونَ', 'من الذين فرقوا دينهم وكانوا شيعا كل حزب بما لديهم فرحون', 21, 41, 407, 163, '30:32'),
(3442, NULL, NULL, 30, 33, 'وَإِذَا مَسَّ ٱلنَّاسَ ضُرٌّ دَعَوْا۟ رَبَّهُم مُّنِيبِينَ إِلَيْهِ ثُمَّ إِذَآ أَذَاقَهُم مِّنْهُ رَحْمَةً إِذَا فَرِيقٌ مِّنْهُم بِرَبِّهِمْ يُشْرِكُونَ', 'وإذا مس الناس ضر دعوا ربهم منيبين إليه ثم إذا أذاقهم منه رحمة إذا فريق منهم بربهم يشركون', 21, 41, 408, 163, '30:33'),
(3443, NULL, NULL, 30, 34, 'لِيَكْفُرُوا۟ بِمَآ ءَاتَيْنَٰهُمْ فَتَمَتَّعُوا۟ فَسَوْفَ تَعْلَمُونَ', 'ليكفروا بما آتيناهم فتمتعوا فسوف تعلمون', 21, 41, 408, 163, '30:34'),
(3444, NULL, NULL, 30, 35, 'أَمْ أَنزَلْنَا عَلَيْهِمْ سُلْطَٰنًا فَهُوَ يَتَكَلَّمُ بِمَا كَانُوا۟ بِهِۦ يُشْرِكُونَ', 'أم أنزلنا عليهم سلطانا فهو يتكلم بما كانوا به يشركون', 21, 41, 408, 163, '30:35'),
(3445, NULL, NULL, 30, 36, 'وَإِذَآ أَذَقْنَا ٱلنَّاسَ رَحْمَةً فَرِحُوا۟ بِهَا وَإِن تُصِبْهُمْ سَيِّئَةٌۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ إِذَا هُمْ يَقْنَطُونَ', 'وإذا أذقنا الناس رحمة فرحوا بها وإن تصبهم سيئة بما قدمت أيديهم إذا هم يقنطون', 21, 41, 408, 163, '30:36'),
(3446, NULL, NULL, 30, 37, 'أَوَلَمْ يَرَوْا۟ أَنَّ ٱللَّهَ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يُؤْمِنُونَ', 'أولم يروا أن الله يبسط الرزق لمن يشاء ويقدر إن في ذلك لآيات لقوم يؤمنون', 21, 41, 408, 163, '30:37'),
(3447, NULL, NULL, 30, 38, 'فَـَٔاتِ ذَا ٱلْقُرْبَىٰ حَقَّهُۥ وَٱلْمِسْكِينَ وَٱبْنَ ٱلسَّبِيلِ ذَٰلِكَ خَيْرٌ لِّلَّذِينَ يُرِيدُونَ وَجْهَ ٱللَّهِ وَأُو۟لَٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ', 'فآت ذا القربى حقه والمسكين وابن السبيل ذلك خير للذين يريدون وجه الله وأولئك هم المفلحون', 21, 41, 408, 163, '30:38'),
(3448, NULL, NULL, 30, 39, 'وَمَآ ءَاتَيْتُم مِّن رِّبًا لِّيَرْبُوَا۟ فِىٓ أَمْوَٰلِ ٱلنَّاسِ فَلَا يَرْبُوا۟ عِندَ ٱللَّهِ وَمَآ ءَاتَيْتُم مِّن زَكَوٰةٍ تُرِيدُونَ وَجْهَ ٱللَّهِ فَأُو۟لَٰٓئِكَ هُمُ ٱلْمُضْعِفُونَ', 'وما آتيتم من ربا ليربو في أموال الناس فلا يربو عند الله وما آتيتم من زكاة تريدون وجه الله فأولئك هم المضعفون', 21, 41, 408, 163, '30:39'),
(3449, NULL, NULL, 30, 40, 'ٱللَّهُ ٱلَّذِى خَلَقَكُمْ ثُمَّ رَزَقَكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يُحْيِيكُمْ هَلْ مِن شُرَكَآئِكُم مَّن يَفْعَلُ مِن ذَٰلِكُم مِّن شَىْءٍ سُبْحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشْرِكُونَ', 'الله الذي خلقكم ثم رزقكم ثم يميتكم ثم يحييكم هل من شركائكم من يفعل من ذلكم من شيء سبحانه وتعالى عما يشركون', 21, 41, 408, 163, '30:40'),
(3450, NULL, NULL, 30, 41, 'ظَهَرَ ٱلْفَسَادُ فِى ٱلْبَرِّ وَٱلْبَحْرِ بِمَا كَسَبَتْ أَيْدِى ٱلنَّاسِ لِيُذِيقَهُم بَعْضَ ٱلَّذِى عَمِلُوا۟ لَعَلَّهُمْ يَرْجِعُونَ', 'ظهر الفساد في البر والبحر بما كسبت أيدي الناس ليذيقهم بعض الذي عملوا لعلهم يرجعون', 21, 41, 408, 163, '30:41'),
(3451, NULL, NULL, 30, 42, 'قُلْ سِيرُوا۟ فِى ٱلْأَرْضِ فَٱنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبْلُ كَانَ أَكْثَرُهُم مُّشْرِكِينَ', 'قل سيروا في الأرض فانظروا كيف كان عاقبة الذين من قبل كان أكثرهم مشركين', 21, 41, 409, 163, '30:42'),
(3452, NULL, NULL, 30, 43, 'فَأَقِمْ وَجْهَكَ لِلدِّينِ ٱلْقَيِّمِ مِن قَبْلِ أَن يَأْتِىَ يَوْمٌ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِ يَوْمَئِذٍ يَصَّدَّعُونَ', 'فأقم وجهك للدين القيم من قبل أن يأتي يوم لا مرد له من الله يومئذ يصدعون', 21, 41, 409, 163, '30:43'),
(3453, NULL, NULL, 30, 44, 'مَن كَفَرَ فَعَلَيْهِ كُفْرُهُۥ وَمَنْ عَمِلَ صَٰلِحًا فَلِأَنفُسِهِمْ يَمْهَدُونَ', 'من كفر فعليه كفره ومن عمل صالحا فلأنفسهم يمهدون', 21, 41, 409, 163, '30:44'),
(3454, NULL, NULL, 30, 45, 'لِيَجْزِىَ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ مِن فَضْلِهِۦٓ إِنَّهُۥ لَا يُحِبُّ ٱلْكَٰفِرِينَ', 'ليجزي الذين آمنوا وعملوا الصالحات من فضله إنه لا يحب الكافرين', 21, 41, 409, 163, '30:45'),
(3455, NULL, NULL, 30, 46, 'وَمِنْ ءَايَٰتِهِۦٓ أَن يُرْسِلَ ٱلرِّيَاحَ مُبَشِّرَٰتٍ وَلِيُذِيقَكُم مِّن رَّحْمَتِهِۦ وَلِتَجْرِىَ ٱلْفُلْكُ بِأَمْرِهِۦ وَلِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ', 'ومن آياته أن يرسل الرياح مبشرات وليذيقكم من رحمته ولتجري الفلك بأمره ولتبتغوا من فضله ولعلكم تشكرون', 21, 41, 409, 163, '30:46'),
(3456, NULL, NULL, 30, 47, 'وَلَقَدْ أَرْسَلْنَا مِن قَبْلِكَ رُسُلًا إِلَىٰ قَوْمِهِمْ فَجَآءُوهُم بِٱلْبَيِّنَٰتِ فَٱنتَقَمْنَا مِنَ ٱلَّذِينَ أَجْرَمُوا۟ وَكَانَ حَقًّا عَلَيْنَا نَصْرُ ٱلْمُؤْمِنِينَ', 'ولقد أرسلنا من قبلك رسلا إلى قومهم فجاءوهم بالبينات فانتقمنا من الذين أجرموا وكان حقا علينا نصر المؤمنين', 21, 41, 409, 163, '30:47'),
(3457, NULL, NULL, 30, 48, 'ٱللَّهُ ٱلَّذِى يُرْسِلُ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابًا فَيَبْسُطُهُۥ فِى ٱلسَّمَآءِ كَيْفَ يَشَآءُ وَيَجْعَلُهُۥ كِسَفًا فَتَرَى ٱلْوَدْقَ يَخْرُجُ مِنْ خِلَٰلِهِۦ فَإِذَآ أَصَابَ بِهِۦ مَن يَشَآءُ مِنْ عِبَادِهِۦٓ إِذَا هُمْ يَسْتَبْشِرُونَ', 'الله الذي يرسل الرياح فتثير سحابا فيبسطه في السماء كيف يشاء ويجعله كسفا فترى الودق يخرج من خلاله فإذا أصاب به من يشاء من عباده إذا هم يستبشرون', 21, 41, 409, 163, '30:48'),
(3458, NULL, NULL, 30, 49, 'وَإِن كَانُوا۟ مِن قَبْلِ أَن يُنَزَّلَ عَلَيْهِم مِّن قَبْلِهِۦ لَمُبْلِسِينَ', 'وإن كانوا من قبل أن ينزل عليهم من قبله لمبلسين', 21, 41, 409, 163, '30:49'),
(3459, NULL, NULL, 30, 50, 'فَٱنظُرْ إِلَىٰٓ ءَاثَٰرِ رَحْمَتِ ٱللَّهِ كَيْفَ يُحْىِ ٱلْأَرْضَ بَعْدَ مَوْتِهَآ إِنَّ ذَٰلِكَ لَمُحْىِ ٱلْمَوْتَىٰ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'فانظر إلى آثار رحمت الله كيف يحيي الأرض بعد موتها إن ذلك لمحيي الموتى وهو على كل شيء قدير', 21, 41, 409, 163, '30:50'),
(3460, NULL, NULL, 30, 51, 'وَلَئِنْ أَرْسَلْنَا رِيحًا فَرَأَوْهُ مُصْفَرًّا لَّظَلُّوا۟ مِنۢ بَعْدِهِۦ يَكْفُرُونَ', 'ولئن أرسلنا ريحا فرأوه مصفرا لظلوا من بعده يكفرون', 21, 41, 410, 163, '30:51'),
(3461, NULL, NULL, 30, 52, 'فَإِنَّكَ لَا تُسْمِعُ ٱلْمَوْتَىٰ وَلَا تُسْمِعُ ٱلصُّمَّ ٱلدُّعَآءَ إِذَا وَلَّوْا۟ مُدْبِرِينَ', 'فإنك لا تسمع الموتى ولا تسمع الصم الدعاء إذا ولوا مدبرين', 21, 41, 410, 163, '30:52'),
(3462, NULL, NULL, 30, 53, 'وَمَآ أَنتَ بِهَٰدِ ٱلْعُمْىِ عَن ضَلَٰلَتِهِمْ إِن تُسْمِعُ إِلَّا مَن يُؤْمِنُ بِـَٔايَٰتِنَا فَهُم مُّسْلِمُونَ', 'وما أنت بهاد العمي عن ضلالتهم إن تسمع إلا من يؤمن بآياتنا فهم مسلمون', 21, 41, 410, 163, '30:53'),
(3463, NULL, NULL, 30, 54, 'ٱللَّهُ ٱلَّذِى خَلَقَكُم مِّن ضَعْفٍ ثُمَّ جَعَلَ مِنۢ بَعْدِ ضَعْفٍ قُوَّةً ثُمَّ جَعَلَ مِنۢ بَعْدِ قُوَّةٍ ضَعْفًا وَشَيْبَةً يَخْلُقُ مَا يَشَآءُ وَهُوَ ٱلْعَلِيمُ ٱلْقَدِيرُ', 'الله الذي خلقكم من ضعف ثم جعل من بعد ضعف قوة ثم جعل من بعد قوة ضعفا وشيبة يخلق ما يشاء وهو العليم القدير', 21, 41, 410, 164, '30:54'),
(3464, NULL, NULL, 30, 55, 'وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يُقْسِمُ ٱلْمُجْرِمُونَ مَا لَبِثُوا۟ غَيْرَ سَاعَةٍ كَذَٰلِكَ كَانُوا۟ يُؤْفَكُونَ', 'ويوم تقوم الساعة يقسم المجرمون ما لبثوا غير ساعة كذلك كانوا يؤفكون', 21, 41, 410, 164, '30:55'),
(3465, NULL, NULL, 30, 56, 'وَقَالَ ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ وَٱلْإِيمَٰنَ لَقَدْ لَبِثْتُمْ فِى كِتَٰبِ ٱللَّهِ إِلَىٰ يَوْمِ ٱلْبَعْثِ فَهَٰذَا يَوْمُ ٱلْبَعْثِ وَلَٰكِنَّكُمْ كُنتُمْ لَا تَعْلَمُونَ', 'وقال الذين أوتوا العلم والإيمان لقد لبثتم في كتاب الله إلى يوم البعث فهذا يوم البعث ولكنكم كنتم لا تعلمون', 21, 41, 410, 164, '30:56'),
(3466, NULL, NULL, 30, 57, 'فَيَوْمَئِذٍ لَّا يَنفَعُ ٱلَّذِينَ ظَلَمُوا۟ مَعْذِرَتُهُمْ وَلَا هُمْ يُسْتَعْتَبُونَ', 'فيومئذ لا ينفع الذين ظلموا معذرتهم ولا هم يستعتبون', 21, 41, 410, 164, '30:57'),
(3467, NULL, NULL, 30, 58, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِى هَٰذَا ٱلْقُرْءَانِ مِن كُلِّ مَثَلٍ وَلَئِن جِئْتَهُم بِـَٔايَةٍ لَّيَقُولَنَّ ٱلَّذِينَ كَفَرُوٓا۟ إِنْ أَنتُمْ إِلَّا مُبْطِلُونَ', 'ولقد ضربنا للناس في هذا القرآن من كل مثل ولئن جئتهم بآية ليقولن الذين كفروا إن أنتم إلا مبطلون', 21, 41, 410, 164, '30:58'),
(3468, NULL, NULL, 30, 59, 'كَذَٰلِكَ يَطْبَعُ ٱللَّهُ عَلَىٰ قُلُوبِ ٱلَّذِينَ لَا يَعْلَمُونَ', 'كذلك يطبع الله على قلوب الذين لا يعلمون', 21, 41, 410, 164, '30:59'),
(3469, NULL, NULL, 30, 60, 'فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّ وَلَا يَسْتَخِفَّنَّكَ ٱلَّذِينَ لَا يُوقِنُونَ', 'فاصبر إن وعد الله حق ولا يستخفنك الذين لا يوقنون', 21, 41, 410, 164, '30:60'),
(3470, NULL, NULL, 31, 1, 'الٓمٓ', 'الم', 21, 41, 411, 164, '31:1'),
(3471, NULL, NULL, 31, 2, 'تِلْكَ ءَايَٰتُ ٱلْكِتَٰبِ ٱلْحَكِيمِ', 'تلك آيات الكتاب الحكيم', 21, 41, 411, 164, '31:2'),
(3472, NULL, NULL, 31, 3, 'هُدًى وَرَحْمَةً لِّلْمُحْسِنِينَ', 'هدى ورحمة للمحسنين', 21, 41, 411, 164, '31:3'),
(3473, NULL, NULL, 31, 4, 'ٱلَّذِينَ يُقِيمُونَ ٱلصَّلَوٰةَ وَيُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْءَاخِرَةِ هُمْ يُوقِنُونَ', 'الذين يقيمون الصلاة ويؤتون الزكاة وهم بالآخرة هم يوقنون', 21, 41, 411, 164, '31:4'),
(3474, NULL, NULL, 31, 5, 'أُو۟لَٰٓئِكَ عَلَىٰ هُدًى مِّن رَّبِّهِمْ وَأُو۟لَٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ', 'أولئك على هدى من ربهم وأولئك هم المفلحون', 21, 41, 411, 164, '31:5'),
(3475, NULL, NULL, 31, 6, 'وَمِنَ ٱلنَّاسِ مَن يَشْتَرِى لَهْوَ ٱلْحَدِيثِ لِيُضِلَّ عَن سَبِيلِ ٱللَّهِ بِغَيْرِ عِلْمٍ وَيَتَّخِذَهَا هُزُوًا أُو۟لَٰٓئِكَ لَهُمْ عَذَابٌ مُّهِينٌ', 'ومن الناس من يشتري لهو الحديث ليضل عن سبيل الله بغير علم ويتخذها هزوا أولئك لهم عذاب مهين', 21, 41, 411, 164, '31:6'),
(3476, NULL, NULL, 31, 7, 'وَإِذَا تُتْلَىٰ عَلَيْهِ ءَايَٰتُنَا وَلَّىٰ مُسْتَكْبِرًا كَأَن لَّمْ يَسْمَعْهَا كَأَنَّ فِىٓ أُذُنَيْهِ وَقْرًا فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ', 'وإذا تتلى عليه آياتنا ولى مستكبرا كأن لم يسمعها كأن في أذنيه وقرا فبشره بعذاب أليم', 21, 41, 411, 164, '31:7'),
(3477, NULL, NULL, 31, 8, 'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُمْ جَنَّٰتُ ٱلنَّعِيمِ', 'إن الذين آمنوا وعملوا الصالحات لهم جنات النعيم', 21, 41, 411, 164, '31:8'),
(3478, NULL, NULL, 31, 9, 'خَٰلِدِينَ فِيهَا وَعْدَ ٱللَّهِ حَقًّا وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'خالدين فيها وعد الله حقا وهو العزيز الحكيم', 21, 41, 411, 164, '31:9'),
(3479, NULL, NULL, 31, 10, 'خَلَقَ ٱلسَّمَٰوَٰتِ بِغَيْرِ عَمَدٍ تَرَوْنَهَا وَأَلْقَىٰ فِى ٱلْأَرْضِ رَوَٰسِىَ أَن تَمِيدَ بِكُمْ وَبَثَّ فِيهَا مِن كُلِّ دَآبَّةٍ وَأَنزَلْنَا مِنَ ٱلسَّمَآءِ مَآءً فَأَنۢبَتْنَا فِيهَا مِن كُلِّ زَوْجٍ كَرِيمٍ', 'خلق السماوات بغير عمد ترونها وألقى في الأرض رواسي أن تميد بكم وبث فيها من كل دابة وأنزلنا من السماء ماء فأنبتنا فيها من كل زوج كريم', 21, 41, 411, 164, '31:10'),
(3480, NULL, NULL, 31, 11, 'هَٰذَا خَلْقُ ٱللَّهِ فَأَرُونِى مَاذَا خَلَقَ ٱلَّذِينَ مِن دُونِهِۦ بَلِ ٱلظَّٰلِمُونَ فِى ضَلَٰلٍ مُّبِينٍ', 'هذا خلق الله فأروني ماذا خلق الذين من دونه بل الظالمون في ضلال مبين', 21, 41, 411, 164, '31:11'),
(3481, NULL, NULL, 31, 12, 'وَلَقَدْ ءَاتَيْنَا لُقْمَٰنَ ٱلْحِكْمَةَ أَنِ ٱشْكُرْ لِلَّهِ وَمَن يَشْكُرْ فَإِنَّمَا يَشْكُرُ لِنَفْسِهِۦ وَمَن كَفَرَ فَإِنَّ ٱللَّهَ غَنِىٌّ حَمِيدٌ', 'ولقد آتينا لقمان الحكمة أن اشكر لله ومن يشكر فإنما يشكر لنفسه ومن كفر فإن الله غني حميد', 21, 41, 412, 164, '31:12'),
(3482, NULL, NULL, 31, 13, 'وَإِذْ قَالَ لُقْمَٰنُ لِٱبْنِهِۦ وَهُوَ يَعِظُهُۥ يَٰبُنَىَّ لَا تُشْرِكْ بِٱللَّهِ إِنَّ ٱلشِّرْكَ لَظُلْمٌ عَظِيمٌ', 'وإذ قال لقمان لابنه وهو يعظه يا بني لا تشرك بالله إن الشرك لظلم عظيم', 21, 41, 412, 164, '31:13'),
(3483, NULL, NULL, 31, 14, 'وَوَصَّيْنَا ٱلْإِنسَٰنَ بِوَٰلِدَيْهِ حَمَلَتْهُ أُمُّهُۥ وَهْنًا عَلَىٰ وَهْنٍ وَفِصَٰلُهُۥ فِى عَامَيْنِ أَنِ ٱشْكُرْ لِى وَلِوَٰلِدَيْكَ إِلَىَّ ٱلْمَصِيرُ', 'ووصينا الإنسان بوالديه حملته أمه وهنا على وهن وفصاله في عامين أن اشكر لي ولوالديك إلي المصير', 21, 41, 412, 164, '31:14'),
(3484, NULL, NULL, 31, 15, 'وَإِن جَٰهَدَاكَ عَلَىٰٓ أَن تُشْرِكَ بِى مَا لَيْسَ لَكَ بِهِۦ عِلْمٌ فَلَا تُطِعْهُمَا وَصَاحِبْهُمَا فِى ٱلدُّنْيَا مَعْرُوفًا وَٱتَّبِعْ سَبِيلَ مَنْ أَنَابَ إِلَىَّ ثُمَّ إِلَىَّ مَرْجِعُكُمْ فَأُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ', 'وإن جاهداك على أن تشرك بي ما ليس لك به علم فلا تطعهما وصاحبهما في الدنيا معروفا واتبع سبيل من أناب إلي ثم إلي مرجعكم فأنبئكم بما كنتم تعملون', 21, 41, 412, 164, '31:15'),
(3485, NULL, NULL, 31, 16, 'يَٰبُنَىَّ إِنَّهَآ إِن تَكُ مِثْقَالَ حَبَّةٍ مِّنْ خَرْدَلٍ فَتَكُن فِى صَخْرَةٍ أَوْ فِى ٱلسَّمَٰوَٰتِ أَوْ فِى ٱلْأَرْضِ يَأْتِ بِهَا ٱللَّهُ إِنَّ ٱللَّهَ لَطِيفٌ خَبِيرٌ', 'يا بني إنها إن تك مثقال حبة من خردل فتكن في صخرة أو في السماوات أو في الأرض يأت بها الله إن الله لطيف خبير', 21, 41, 412, 164, '31:16'),
(3486, NULL, NULL, 31, 17, 'يَٰبُنَىَّ أَقِمِ ٱلصَّلَوٰةَ وَأْمُرْ بِٱلْمَعْرُوفِ وَٱنْهَ عَنِ ٱلْمُنكَرِ وَٱصْبِرْ عَلَىٰ مَآ أَصَابَكَ إِنَّ ذَٰلِكَ مِنْ عَزْمِ ٱلْأُمُورِ', 'يا بني أقم الصلاة وأمر بالمعروف وانه عن المنكر واصبر على ما أصابك إن ذلك من عزم الأمور', 21, 41, 412, 164, '31:17'),
(3487, NULL, NULL, 31, 18, 'وَلَا تُصَعِّرْ خَدَّكَ لِلنَّاسِ وَلَا تَمْشِ فِى ٱلْأَرْضِ مَرَحًا إِنَّ ٱللَّهَ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ', 'ولا تصعر خدك للناس ولا تمش في الأرض مرحا إن الله لا يحب كل مختال فخور', 21, 41, 412, 164, '31:18'),
(3488, NULL, NULL, 31, 19, 'وَٱقْصِدْ فِى مَشْيِكَ وَٱغْضُضْ مِن صَوْتِكَ إِنَّ أَنكَرَ ٱلْأَصْوَٰتِ لَصَوْتُ ٱلْحَمِيرِ', 'واقصد في مشيك واغضض من صوتك إن أنكر الأصوات لصوت الحمير', 21, 41, 412, 164, '31:19'),
(3489, NULL, NULL, 31, 20, 'أَلَمْ تَرَوْا۟ أَنَّ ٱللَّهَ سَخَّرَ لَكُم مَّا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَأَسْبَغَ عَلَيْكُمْ نِعَمَهُۥ ظَٰهِرَةً وَبَاطِنَةً وَمِنَ ٱلنَّاسِ مَن يُجَٰدِلُ فِى ٱللَّهِ بِغَيْرِ عِلْمٍ وَلَا هُدًى وَلَا كِتَٰبٍ مُّنِيرٍ', 'ألم تروا أن الله سخر لكم ما في السماوات وما في الأرض وأسبغ عليكم نعمه ظاهرة وباطنة ومن الناس من يجادل في الله بغير علم ولا هدى ولا كتاب منير', 21, 41, 413, 164, '31:20'),
(3490, NULL, NULL, 31, 21, 'وَإِذَا قِيلَ لَهُمُ ٱتَّبِعُوا۟ مَآ أَنزَلَ ٱللَّهُ قَالُوا۟ بَلْ نَتَّبِعُ مَا وَجَدْنَا عَلَيْهِ ءَابَآءَنَآ أَوَلَوْ كَانَ ٱلشَّيْطَٰنُ يَدْعُوهُمْ إِلَىٰ عَذَابِ ٱلسَّعِيرِ', 'وإذا قيل لهم اتبعوا ما أنزل الله قالوا بل نتبع ما وجدنا عليه آباءنا أولو كان الشيطان يدعوهم إلى عذاب السعير', 21, 41, 413, 164, '31:21'),
(3491, NULL, NULL, 31, 22, 'وَمَن يُسْلِمْ وَجْهَهُۥٓ إِلَى ٱللَّهِ وَهُوَ مُحْسِنٌ فَقَدِ ٱسْتَمْسَكَ بِٱلْعُرْوَةِ ٱلْوُثْقَىٰ وَإِلَى ٱللَّهِ عَٰقِبَةُ ٱلْأُمُورِ', 'ومن يسلم وجهه إلى الله وهو محسن فقد استمسك بالعروة الوثقى وإلى الله عاقبة الأمور', 21, 42, 413, 165, '31:22'),
(3492, NULL, NULL, 31, 23, 'وَمَن كَفَرَ فَلَا يَحْزُنكَ كُفْرُهُۥٓ إِلَيْنَا مَرْجِعُهُمْ فَنُنَبِّئُهُم بِمَا عَمِلُوٓا۟ إِنَّ ٱللَّهَ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ', 'ومن كفر فلا يحزنك كفره إلينا مرجعهم فننبئهم بما عملوا إن الله عليم بذات الصدور', 21, 42, 413, 165, '31:23'),
(3493, NULL, NULL, 31, 24, 'نُمَتِّعُهُمْ قَلِيلًا ثُمَّ نَضْطَرُّهُمْ إِلَىٰ عَذَابٍ غَلِيظٍ', 'نمتعهم قليلا ثم نضطرهم إلى عذاب غليظ', 21, 42, 413, 165, '31:24'),
(3494, NULL, NULL, 31, 25, 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ ٱللَّهُ قُلِ ٱلْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 'ولئن سألتهم من خلق السماوات والأرض ليقولن الله قل الحمد لله بل أكثرهم لا يعلمون', 21, 42, 413, 165, '31:25'),
(3495, NULL, NULL, 31, 26, 'لِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ إِنَّ ٱللَّهَ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ', 'لله ما في السماوات والأرض إن الله هو الغني الحميد', 21, 42, 413, 165, '31:26'),
(3496, NULL, NULL, 31, 27, 'وَلَوْ أَنَّمَا فِى ٱلْأَرْضِ مِن شَجَرَةٍ أَقْلَٰمٌ وَٱلْبَحْرُ يَمُدُّهُۥ مِنۢ بَعْدِهِۦ سَبْعَةُ أَبْحُرٍ مَّا نَفِدَتْ كَلِمَٰتُ ٱللَّهِ إِنَّ ٱللَّهَ عَزِيزٌ حَكِيمٌ', 'ولو أنما في الأرض من شجرة أقلام والبحر يمده من بعده سبعة أبحر ما نفدت كلمات الله إن الله عزيز حكيم', 21, 42, 413, 165, '31:27'),
(3497, NULL, NULL, 31, 28, 'مَّا خَلْقُكُمْ وَلَا بَعْثُكُمْ إِلَّا كَنَفْسٍ وَٰحِدَةٍ إِنَّ ٱللَّهَ سَمِيعٌۢ بَصِيرٌ', 'ما خلقكم ولا بعثكم إلا كنفس واحدة إن الله سميع بصير', 21, 42, 413, 165, '31:28'),
(3498, NULL, NULL, 31, 29, 'أَلَمْ تَرَ أَنَّ ٱللَّهَ يُولِجُ ٱلَّيْلَ فِى ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِى ٱلَّيْلِ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ كُلٌّ يَجْرِىٓ إِلَىٰٓ أَجَلٍ مُّسَمًّى وَأَنَّ ٱللَّهَ بِمَا تَعْمَلُونَ خَبِيرٌ', 'ألم تر أن الله يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري إلى أجل مسمى وأن الله بما تعملون خبير', 21, 42, 414, 165, '31:29'),
(3499, NULL, NULL, 31, 30, 'ذَٰلِكَ بِأَنَّ ٱللَّهَ هُوَ ٱلْحَقُّ وَأَنَّ مَا يَدْعُونَ مِن دُونِهِ ٱلْبَٰطِلُ وَأَنَّ ٱللَّهَ هُوَ ٱلْعَلِىُّ ٱلْكَبِيرُ', 'ذلك بأن الله هو الحق وأن ما يدعون من دونه الباطل وأن الله هو العلي الكبير', 21, 42, 414, 165, '31:30'),
(3500, NULL, NULL, 31, 31, 'أَلَمْ تَرَ أَنَّ ٱلْفُلْكَ تَجْرِى فِى ٱلْبَحْرِ بِنِعْمَتِ ٱللَّهِ لِيُرِيَكُم مِّنْ ءَايَٰتِهِۦٓ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ', 'ألم تر أن الفلك تجري في البحر بنعمت الله ليريكم من آياته إن في ذلك لآيات لكل صبار شكور', 21, 42, 414, 165, '31:31'),
(3501, NULL, NULL, 31, 32, 'وَإِذَا غَشِيَهُم مَّوْجٌ كَٱلظُّلَلِ دَعَوُا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ فَلَمَّا نَجَّىٰهُمْ إِلَى ٱلْبَرِّ فَمِنْهُم مُّقْتَصِدٌ وَمَا يَجْحَدُ بِـَٔايَٰتِنَآ إِلَّا كُلُّ خَتَّارٍ كَفُورٍ', 'وإذا غشيهم موج كالظلل دعوا الله مخلصين له الدين فلما نجاهم إلى البر فمنهم مقتصد وما يجحد بآياتنا إلا كل ختار كفور', 21, 42, 414, 165, '31:32'),
(3502, NULL, NULL, 31, 33, 'يَٰٓأَيُّهَا ٱلنَّاسُ ٱتَّقُوا۟ رَبَّكُمْ وَٱخْشَوْا۟ يَوْمًا لَّا يَجْزِى وَالِدٌ عَن وَلَدِهِۦ وَلَا مَوْلُودٌ هُوَ جَازٍ عَن وَالِدِهِۦ شَيْـًٔا إِنَّ وَعْدَ ٱللَّهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ ٱلْحَيَوٰةُ ٱلدُّنْيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلْغَرُورُ', 'يا أيها الناس اتقوا ربكم واخشوا يوما لا يجزي والد عن ولده ولا مولود هو جاز عن والده شيئا إن وعد الله حق فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور', 21, 42, 414, 165, '31:33'),
(3503, NULL, NULL, 31, 34, 'إِنَّ ٱللَّهَ عِندَهُۥ عِلْمُ ٱلسَّاعَةِ وَيُنَزِّلُ ٱلْغَيْثَ وَيَعْلَمُ مَا فِى ٱلْأَرْحَامِ وَمَا تَدْرِى نَفْسٌ مَّاذَا تَكْسِبُ غَدًا وَمَا تَدْرِى نَفْسٌۢ بِأَىِّ أَرْضٍ تَمُوتُ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٌۢ', 'إن الله عنده علم الساعة وينزل الغيث ويعلم ما في الأرحام وما تدري نفس ماذا تكسب غدا وما تدري نفس بأي أرض تموت إن الله عليم خبير', 21, 42, 414, 165, '31:34'),
(3504, NULL, NULL, 32, 1, 'الٓمٓ', 'الم', 21, 42, 415, 165, '32:1'),
(3505, NULL, NULL, 32, 2, 'تَنزِيلُ ٱلْكِتَٰبِ لَا رَيْبَ فِيهِ مِن رَّبِّ ٱلْعَٰلَمِينَ', 'تنزيل الكتاب لا ريب فيه من رب العالمين', 21, 42, 415, 165, '32:2'),
(3506, NULL, NULL, 32, 3, 'أَمْ يَقُولُونَ ٱفْتَرَىٰهُ بَلْ هُوَ ٱلْحَقُّ مِن رَّبِّكَ لِتُنذِرَ قَوْمًا مَّآ أَتَىٰهُم مِّن نَّذِيرٍ مِّن قَبْلِكَ لَعَلَّهُمْ يَهْتَدُونَ', 'أم يقولون افتراه بل هو الحق من ربك لتنذر قوما ما أتاهم من نذير من قبلك لعلهم يهتدون', 21, 42, 415, 165, '32:3'),
(3507, NULL, NULL, 32, 4, 'ٱللَّهُ ٱلَّذِى خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا فِى سِتَّةِ أَيَّامٍ ثُمَّ ٱسْتَوَىٰ عَلَى ٱلْعَرْشِ مَا لَكُم مِّن دُونِهِۦ مِن وَلِىٍّ وَلَا شَفِيعٍ أَفَلَا تَتَذَكَّرُونَ', 'الله الذي خلق السماوات والأرض وما بينهما في ستة أيام ثم استوى على العرش ما لكم من دونه من ولي ولا شفيع أفلا تتذكرون', 21, 42, 415, 165, '32:4'),
(3508, NULL, NULL, 32, 5, 'يُدَبِّرُ ٱلْأَمْرَ مِنَ ٱلسَّمَآءِ إِلَى ٱلْأَرْضِ ثُمَّ يَعْرُجُ إِلَيْهِ فِى يَوْمٍ كَانَ مِقْدَارُهُۥٓ أَلْفَ سَنَةٍ مِّمَّا تَعُدُّونَ', 'يدبر الأمر من السماء إلى الأرض ثم يعرج إليه في يوم كان مقداره ألف سنة مما تعدون', 21, 42, 415, 165, '32:5'),
(3509, NULL, NULL, 32, 6, 'ذَٰلِكَ عَٰلِمُ ٱلْغَيْبِ وَٱلشَّهَٰدَةِ ٱلْعَزِيزُ ٱلرَّحِيمُ', 'ذلك عالم الغيب والشهادة العزيز الرحيم', 21, 42, 415, 165, '32:6'),
(3510, NULL, NULL, 32, 7, 'ٱلَّذِىٓ أَحْسَنَ كُلَّ شَىْءٍ خَلَقَهُۥ وَبَدَأَ خَلْقَ ٱلْإِنسَٰنِ مِن طِينٍ', 'الذي أحسن كل شيء خلقه وبدأ خلق الإنسان من طين', 21, 42, 415, 165, '32:7'),
(3511, NULL, NULL, 32, 8, 'ثُمَّ جَعَلَ نَسْلَهُۥ مِن سُلَٰلَةٍ مِّن مَّآءٍ مَّهِينٍ', 'ثم جعل نسله من سلالة من ماء مهين', 21, 42, 415, 165, '32:8'),
(3512, NULL, NULL, 32, 9, 'ثُمَّ سَوَّىٰهُ وَنَفَخَ فِيهِ مِن رُّوحِهِۦ وَجَعَلَ لَكُمُ ٱلسَّمْعَ وَٱلْأَبْصَٰرَ وَٱلْأَفْـِٔدَةَ قَلِيلًا مَّا تَشْكُرُونَ', 'ثم سواه ونفخ فيه من روحه وجعل لكم السمع والأبصار والأفئدة قليلا ما تشكرون', 21, 42, 415, 165, '32:9'),
(3513, NULL, NULL, 32, 10, 'وَقَالُوٓا۟ أَءِذَا ضَلَلْنَا فِى ٱلْأَرْضِ أَءِنَّا لَفِى خَلْقٍ جَدِيدٍۭ بَلْ هُم بِلِقَآءِ رَبِّهِمْ كَٰفِرُونَ', 'وقالوا أإذا ضللنا في الأرض أإنا لفي خلق جديد بل هم بلقاء ربهم كافرون', 21, 42, 415, 165, '32:10'),
(3514, NULL, NULL, 32, 11, 'قُلْ يَتَوَفَّىٰكُم مَّلَكُ ٱلْمَوْتِ ٱلَّذِى وُكِّلَ بِكُمْ ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ', 'قل يتوفاكم ملك الموت الذي وكل بكم ثم إلى ربكم ترجعون', 21, 42, 415, 166, '32:11'),
(3515, NULL, NULL, 32, 12, 'وَلَوْ تَرَىٰٓ إِذِ ٱلْمُجْرِمُونَ نَاكِسُوا۟ رُءُوسِهِمْ عِندَ رَبِّهِمْ رَبَّنَآ أَبْصَرْنَا وَسَمِعْنَا فَٱرْجِعْنَا نَعْمَلْ صَٰلِحًا إِنَّا مُوقِنُونَ', 'ولو ترى إذ المجرمون ناكسو رءوسهم عند ربهم ربنا أبصرنا وسمعنا فارجعنا نعمل صالحا إنا موقنون', 21, 42, 416, 166, '32:12'),
(3516, NULL, NULL, 32, 13, 'وَلَوْ شِئْنَا لَءَاتَيْنَا كُلَّ نَفْسٍ هُدَىٰهَا وَلَٰكِنْ حَقَّ ٱلْقَوْلُ مِنِّى لَأَمْلَأَنَّ جَهَنَّمَ مِنَ ٱلْجِنَّةِ وَٱلنَّاسِ أَجْمَعِينَ', 'ولو شئنا لآتينا كل نفس هداها ولكن حق القول مني لأملأن جهنم من الجنة والناس أجمعين', 21, 42, 416, 166, '32:13'),
(3517, NULL, NULL, 32, 14, 'فَذُوقُوا۟ بِمَا نَسِيتُمْ لِقَآءَ يَوْمِكُمْ هَٰذَآ إِنَّا نَسِينَٰكُمْ وَذُوقُوا۟ عَذَابَ ٱلْخُلْدِ بِمَا كُنتُمْ تَعْمَلُونَ', 'فذوقوا بما نسيتم لقاء يومكم هذا إنا نسيناكم وذوقوا عذاب الخلد بما كنتم تعملون', 21, 42, 416, 166, '32:14'),
(3518, NULL, NULL, 32, 15, 'إِنَّمَا يُؤْمِنُ بِـَٔايَٰتِنَا ٱلَّذِينَ إِذَا ذُكِّرُوا۟ بِهَا خَرُّوا۟ سُجَّدًا وَسَبَّحُوا۟ بِحَمْدِ رَبِّهِمْ وَهُمْ لَا يَسْتَكْبِرُونَ', 'إنما يؤمن بآياتنا الذين إذا ذكروا بها خروا سجدا وسبحوا بحمد ربهم وهم لا يستكبرون', 21, 42, 416, 166, '32:15'),
(3519, NULL, NULL, 32, 16, 'تَتَجَافَىٰ جُنُوبُهُمْ عَنِ ٱلْمَضَاجِعِ يَدْعُونَ رَبَّهُمْ خَوْفًا وَطَمَعًا وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَ', 'تتجافى جنوبهم عن المضاجع يدعون ربهم خوفا وطمعا ومما رزقناهم ينفقون', 21, 42, 416, 166, '32:16'),
(3520, NULL, NULL, 32, 17, 'فَلَا تَعْلَمُ نَفْسٌ مَّآ أُخْفِىَ لَهُم مِّن قُرَّةِ أَعْيُنٍ جَزَآءًۢ بِمَا كَانُوا۟ يَعْمَلُونَ', 'فلا تعلم نفس ما أخفي لهم من قرة أعين جزاء بما كانوا يعملون', 21, 42, 416, 166, '32:17'),
(3521, NULL, NULL, 32, 18, 'أَفَمَن كَانَ مُؤْمِنًا كَمَن كَانَ فَاسِقًا لَّا يَسْتَوُۥنَ', 'أفمن كان مؤمنا كمن كان فاسقا لا يستوون', 21, 42, 416, 166, '32:18'),
(3522, NULL, NULL, 32, 19, 'أَمَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ فَلَهُمْ جَنَّٰتُ ٱلْمَأْوَىٰ نُزُلًۢا بِمَا كَانُوا۟ يَعْمَلُونَ', 'أما الذين آمنوا وعملوا الصالحات فلهم جنات المأوى نزلا بما كانوا يعملون', 21, 42, 416, 166, '32:19'),
(3523, NULL, NULL, 32, 20, 'وَأَمَّا ٱلَّذِينَ فَسَقُوا۟ فَمَأْوَىٰهُمُ ٱلنَّارُ كُلَّمَآ أَرَادُوٓا۟ أَن يَخْرُجُوا۟ مِنْهَآ أُعِيدُوا۟ فِيهَا وَقِيلَ لَهُمْ ذُوقُوا۟ عَذَابَ ٱلنَّارِ ٱلَّذِى كُنتُم بِهِۦ تُكَذِّبُونَ', 'وأما الذين فسقوا فمأواهم النار كلما أرادوا أن يخرجوا منها أعيدوا فيها وقيل لهم ذوقوا عذاب النار الذي كنتم به تكذبون', 21, 42, 416, 166, '32:20'),
(3524, NULL, NULL, 32, 21, 'وَلَنُذِيقَنَّهُم مِّنَ ٱلْعَذَابِ ٱلْأَدْنَىٰ دُونَ ٱلْعَذَابِ ٱلْأَكْبَرِ لَعَلَّهُمْ يَرْجِعُونَ', 'ولنذيقنهم من العذاب الأدنى دون العذاب الأكبر لعلهم يرجعون', 21, 42, 417, 166, '32:21'),
(3525, NULL, NULL, 32, 22, 'وَمَنْ أَظْلَمُ مِمَّن ذُكِّرَ بِـَٔايَٰتِ رَبِّهِۦ ثُمَّ أَعْرَضَ عَنْهَآ إِنَّا مِنَ ٱلْمُجْرِمِينَ مُنتَقِمُونَ', 'ومن أظلم ممن ذكر بآيات ربه ثم أعرض عنها إنا من المجرمين منتقمون', 21, 42, 417, 166, '32:22'),
(3526, NULL, NULL, 32, 23, 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَٰبَ فَلَا تَكُن فِى مِرْيَةٍ مِّن لِّقَآئِهِۦ وَجَعَلْنَٰهُ هُدًى لِّبَنِىٓ إِسْرَٰٓءِيلَ', 'ولقد آتينا موسى الكتاب فلا تكن في مرية من لقائه وجعلناه هدى لبني إسرائيل', 21, 42, 417, 166, '32:23'),
(3527, NULL, NULL, 32, 24, 'وَجَعَلْنَا مِنْهُمْ أَئِمَّةً يَهْدُونَ بِأَمْرِنَا لَمَّا صَبَرُوا۟ وَكَانُوا۟ بِـَٔايَٰتِنَا يُوقِنُونَ', 'وجعلنا منهم أئمة يهدون بأمرنا لما صبروا وكانوا بآياتنا يوقنون', 21, 42, 417, 166, '32:24'),
(3528, NULL, NULL, 32, 25, 'إِنَّ رَبَّكَ هُوَ يَفْصِلُ بَيْنَهُمْ يَوْمَ ٱلْقِيَٰمَةِ فِيمَا كَانُوا۟ فِيهِ يَخْتَلِفُونَ', 'إن ربك هو يفصل بينهم يوم القيامة فيما كانوا فيه يختلفون', 21, 42, 417, 166, '32:25'),
(3529, NULL, NULL, 32, 26, 'أَوَلَمْ يَهْدِ لَهُمْ كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّنَ ٱلْقُرُونِ يَمْشُونَ فِى مَسَٰكِنِهِمْ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ أَفَلَا يَسْمَعُونَ', 'أولم يهد لهم كم أهلكنا من قبلهم من القرون يمشون في مساكنهم إن في ذلك لآيات أفلا يسمعون', 21, 42, 417, 166, '32:26'),
(3530, NULL, NULL, 32, 27, 'أَوَلَمْ يَرَوْا۟ أَنَّا نَسُوقُ ٱلْمَآءَ إِلَى ٱلْأَرْضِ ٱلْجُرُزِ فَنُخْرِجُ بِهِۦ زَرْعًا تَأْكُلُ مِنْهُ أَنْعَٰمُهُمْ وَأَنفُسُهُمْ أَفَلَا يُبْصِرُونَ', 'أولم يروا أنا نسوق الماء إلى الأرض الجرز فنخرج به زرعا تأكل منه أنعامهم وأنفسهم أفلا يبصرون', 21, 42, 417, 166, '32:27'),
(3531, NULL, NULL, 32, 28, 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلْفَتْحُ إِن كُنتُمْ صَٰدِقِينَ', 'ويقولون متى هذا الفتح إن كنتم صادقين', 21, 42, 417, 166, '32:28'),
(3532, NULL, NULL, 32, 29, 'قُلْ يَوْمَ ٱلْفَتْحِ لَا يَنفَعُ ٱلَّذِينَ كَفَرُوٓا۟ إِيمَٰنُهُمْ وَلَا هُمْ يُنظَرُونَ', 'قل يوم الفتح لا ينفع الذين كفروا إيمانهم ولا هم ينظرون', 21, 42, 417, 166, '32:29'),
(3533, NULL, NULL, 32, 30, 'فَأَعْرِضْ عَنْهُمْ وَٱنتَظِرْ إِنَّهُم مُّنتَظِرُونَ', 'فأعرض عنهم وانتظر إنهم منتظرون', 21, 42, 417, 166, '32:30'),
(3534, NULL, NULL, 33, 1, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ ٱتَّقِ ٱللَّهَ وَلَا تُطِعِ ٱلْكَٰفِرِينَ وَٱلْمُنَٰفِقِينَ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمًا', 'يا أيها النبي اتق الله ولا تطع الكافرين والمنافقين إن الله كان عليما حكيما', 21, 42, 418, 167, '33:1'),
(3535, NULL, NULL, 33, 2, 'وَٱتَّبِعْ مَا يُوحَىٰٓ إِلَيْكَ مِن رَّبِّكَ إِنَّ ٱللَّهَ كَانَ بِمَا تَعْمَلُونَ خَبِيرًا', 'واتبع ما يوحى إليك من ربك إن الله كان بما تعملون خبيرا', 21, 42, 418, 167, '33:2'),
(3536, NULL, NULL, 33, 3, 'وَتَوَكَّلْ عَلَى ٱللَّهِ وَكَفَىٰ بِٱللَّهِ وَكِيلًا', 'وتوكل على الله وكفى بالله وكيلا', 21, 42, 418, 167, '33:3'),
(3537, NULL, NULL, 33, 4, 'مَّا جَعَلَ ٱللَّهُ لِرَجُلٍ مِّن قَلْبَيْنِ فِى جَوْفِهِۦ وَمَا جَعَلَ أَزْوَٰجَكُمُ ٱلَّٰٓـِٔى تُظَٰهِرُونَ مِنْهُنَّ أُمَّهَٰتِكُمْ وَمَا جَعَلَ أَدْعِيَآءَكُمْ أَبْنَآءَكُمْ ذَٰلِكُمْ قَوْلُكُم بِأَفْوَٰهِكُمْ وَٱللَّهُ يَقُولُ ٱلْحَقَّ وَهُوَ يَهْدِى ٱلسَّبِيلَ', 'ما جعل الله لرجل من قلبين في جوفه وما جعل أزواجكم اللائي تظاهرون منهن أمهاتكم وما جعل أدعياءكم أبناءكم ذلكم قولكم بأفواهكم والله يقول الحق وهو يهدي السبيل', 21, 42, 418, 167, '33:4'),
(3538, NULL, NULL, 33, 5, 'ٱدْعُوهُمْ لِءَابَآئِهِمْ هُوَ أَقْسَطُ عِندَ ٱللَّهِ فَإِن لَّمْ تَعْلَمُوٓا۟ ءَابَآءَهُمْ فَإِخْوَٰنُكُمْ فِى ٱلدِّينِ وَمَوَٰلِيكُمْ وَلَيْسَ عَلَيْكُمْ جُنَاحٌ فِيمَآ أَخْطَأْتُم بِهِۦ وَلَٰكِن مَّا تَعَمَّدَتْ قُلُوبُكُمْ وَكَانَ ٱللَّهُ غَفُورًا رَّحِيمًا', 'ادعوهم لآبائهم هو أقسط عند الله فإن لم تعلموا آباءهم فإخوانكم في الدين ومواليكم وليس عليكم جناح فيما أخطأتم به ولكن ما تعمدت قلوبكم وكان الله غفورا رحيما', 21, 42, 418, 167, '33:5'),
(3539, NULL, NULL, 33, 6, 'ٱلنَّبِىُّ أَوْلَىٰ بِٱلْمُؤْمِنِينَ مِنْ أَنفُسِهِمْ وَأَزْوَٰجُهُۥٓ أُمَّهَٰتُهُمْ وَأُو۟لُوا۟ ٱلْأَرْحَامِ بَعْضُهُمْ أَوْلَىٰ بِبَعْضٍ فِى كِتَٰبِ ٱللَّهِ مِنَ ٱلْمُؤْمِنِينَ وَٱلْمُهَٰجِرِينَ إِلَّآ أَن تَفْعَلُوٓا۟ إِلَىٰٓ أَوْلِيَآئِكُم مَّعْرُوفًا كَانَ ذَٰلِكَ فِى ٱلْكِتَٰبِ مَسْطُورًا', 'النبي أولى بالمؤمنين من أنفسهم وأزواجه أمهاتهم وأولو الأرحام بعضهم أولى ببعض في كتاب الله من المؤمنين والمهاجرين إلا أن تفعلوا إلى أوليائكم معروفا كان ذلك في الكتاب مسطورا', 21, 42, 418, 167, '33:6'),
(3540, NULL, NULL, 33, 7, 'وَإِذْ أَخَذْنَا مِنَ ٱلنَّبِيِّۦنَ مِيثَٰقَهُمْ وَمِنكَ وَمِن نُّوحٍ وَإِبْرَٰهِيمَ وَمُوسَىٰ وَعِيسَى ٱبْنِ مَرْيَمَ وَأَخَذْنَا مِنْهُم مِّيثَٰقًا غَلِيظًا', 'وإذ أخذنا من النبيين ميثاقهم ومنك ومن نوح وإبراهيم وموسى وعيسى ابن مريم وأخذنا منهم ميثاقا غليظا', 21, 42, 419, 167, '33:7'),
(3541, NULL, NULL, 33, 8, 'لِّيَسْـَٔلَ ٱلصَّٰدِقِينَ عَن صِدْقِهِمْ وَأَعَدَّ لِلْكَٰفِرِينَ عَذَابًا أَلِيمًا', 'ليسأل الصادقين عن صدقهم وأعد للكافرين عذابا أليما', 21, 42, 419, 167, '33:8'),
(3542, NULL, NULL, 33, 9, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱذْكُرُوا۟ نِعْمَةَ ٱللَّهِ عَلَيْكُمْ إِذْ جَآءَتْكُمْ جُنُودٌ فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا وَجُنُودًا لَّمْ تَرَوْهَا وَكَانَ ٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا', 'يا أيها الذين آمنوا اذكروا نعمة الله عليكم إذ جاءتكم جنود فأرسلنا عليهم ريحا وجنودا لم تروها وكان الله بما تعملون بصيرا', 21, 42, 419, 167, '33:9'),
(3543, NULL, NULL, 33, 10, 'إِذْ جَآءُوكُم مِّن فَوْقِكُمْ وَمِنْ أَسْفَلَ مِنكُمْ وَإِذْ زَاغَتِ ٱلْأَبْصَٰرُ وَبَلَغَتِ ٱلْقُلُوبُ ٱلْحَنَاجِرَ وَتَظُنُّونَ بِٱللَّهِ ٱلظُّنُونَا۠', 'إذ جاءوكم من فوقكم ومن أسفل منكم وإذ زاغت الأبصار وبلغت القلوب الحناجر وتظنون بالله الظنونا', 21, 42, 419, 167, '33:10'),
(3544, NULL, NULL, 33, 11, 'هُنَالِكَ ٱبْتُلِىَ ٱلْمُؤْمِنُونَ وَزُلْزِلُوا۟ زِلْزَالًا شَدِيدًا', 'هنالك ابتلي المؤمنون وزلزلوا زلزالا شديدا', 21, 42, 419, 167, '33:11'),
(3545, NULL, NULL, 33, 12, 'وَإِذْ يَقُولُ ٱلْمُنَٰفِقُونَ وَٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌ مَّا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥٓ إِلَّا غُرُورًا', 'وإذ يقول المنافقون والذين في قلوبهم مرض ما وعدنا الله ورسوله إلا غرورا', 21, 42, 419, 167, '33:12'),
(3546, NULL, NULL, 33, 13, 'وَإِذْ قَالَت طَّآئِفَةٌ مِّنْهُمْ يَٰٓأَهْلَ يَثْرِبَ لَا مُقَامَ لَكُمْ فَٱرْجِعُوا۟ وَيَسْتَـْٔذِنُ فَرِيقٌ مِّنْهُمُ ٱلنَّبِىَّ يَقُولُونَ إِنَّ بُيُوتَنَا عَوْرَةٌ وَمَا هِىَ بِعَوْرَةٍ إِن يُرِيدُونَ إِلَّا فِرَارًا', 'وإذ قالت طائفة منهم يا أهل يثرب لا مقام لكم فارجعوا ويستأذن فريق منهم النبي يقولون إن بيوتنا عورة وما هي بعورة إن يريدون إلا فرارا', 21, 42, 419, 167, '33:13'),
(3547, NULL, NULL, 33, 14, 'وَلَوْ دُخِلَتْ عَلَيْهِم مِّنْ أَقْطَارِهَا ثُمَّ سُئِلُوا۟ ٱلْفِتْنَةَ لَءَاتَوْهَا وَمَا تَلَبَّثُوا۟ بِهَآ إِلَّا يَسِيرًا', 'ولو دخلت عليهم من أقطارها ثم سئلوا الفتنة لآتوها وما تلبثوا بها إلا يسيرا', 21, 42, 419, 167, '33:14'),
(3548, NULL, NULL, 33, 15, 'وَلَقَدْ كَانُوا۟ عَٰهَدُوا۟ ٱللَّهَ مِن قَبْلُ لَا يُوَلُّونَ ٱلْأَدْبَٰرَ وَكَانَ عَهْدُ ٱللَّهِ مَسْـُٔولًا', 'ولقد كانوا عاهدوا الله من قبل لا يولون الأدبار وكان عهد الله مسئولا', 21, 42, 419, 167, '33:15'),
(3549, NULL, NULL, 33, 16, 'قُل لَّن يَنفَعَكُمُ ٱلْفِرَارُ إِن فَرَرْتُم مِّنَ ٱلْمَوْتِ أَوِ ٱلْقَتْلِ وَإِذًا لَّا تُمَتَّعُونَ إِلَّا قَلِيلًا', 'قل لن ينفعكم الفرار إن فررتم من الموت أو القتل وإذا لا تمتعون إلا قليلا', 21, 42, 420, 167, '33:16'),
(3550, NULL, NULL, 33, 17, 'قُلْ مَن ذَا ٱلَّذِى يَعْصِمُكُم مِّنَ ٱللَّهِ إِنْ أَرَادَ بِكُمْ سُوٓءًا أَوْ أَرَادَ بِكُمْ رَحْمَةً وَلَا يَجِدُونَ لَهُم مِّن دُونِ ٱللَّهِ وَلِيًّا وَلَا نَصِيرًا', 'قل من ذا الذي يعصمكم من الله إن أراد بكم سوءا أو أراد بكم رحمة ولا يجدون لهم من دون الله وليا ولا نصيرا', 21, 42, 420, 167, '33:17'),
(3551, NULL, NULL, 33, 18, 'قَدْ يَعْلَمُ ٱللَّهُ ٱلْمُعَوِّقِينَ مِنكُمْ وَٱلْقَآئِلِينَ لِإِخْوَٰنِهِمْ هَلُمَّ إِلَيْنَا وَلَا يَأْتُونَ ٱلْبَأْسَ إِلَّا قَلِيلًا', 'قد يعلم الله المعوقين منكم والقائلين لإخوانهم هلم إلينا ولا يأتون البأس إلا قليلا', 21, 42, 420, 168, '33:18'),
(3552, NULL, NULL, 33, 19, 'أَشِحَّةً عَلَيْكُمْ فَإِذَا جَآءَ ٱلْخَوْفُ رَأَيْتَهُمْ يَنظُرُونَ إِلَيْكَ تَدُورُ أَعْيُنُهُمْ كَٱلَّذِى يُغْشَىٰ عَلَيْهِ مِنَ ٱلْمَوْتِ فَإِذَا ذَهَبَ ٱلْخَوْفُ سَلَقُوكُم بِأَلْسِنَةٍ حِدَادٍ أَشِحَّةً عَلَى ٱلْخَيْرِ أُو۟لَٰٓئِكَ لَمْ يُؤْمِنُوا۟ فَأَحْبَطَ ٱللَّهُ أَعْمَٰلَهُمْ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرًا', 'أشحة عليكم فإذا جاء الخوف رأيتهم ينظرون إليك تدور أعينهم كالذي يغشى عليه من الموت فإذا ذهب الخوف سلقوكم بألسنة حداد أشحة على الخير أولئك لم يؤمنوا فأحبط الله أعمالهم وكان ذلك على الله يسيرا', 21, 42, 420, 168, '33:19'),
(3553, NULL, NULL, 33, 20, 'يَحْسَبُونَ ٱلْأَحْزَابَ لَمْ يَذْهَبُوا۟ وَإِن يَأْتِ ٱلْأَحْزَابُ يَوَدُّوا۟ لَوْ أَنَّهُم بَادُونَ فِى ٱلْأَعْرَابِ يَسْـَٔلُونَ عَنْ أَنۢبَآئِكُمْ وَلَوْ كَانُوا۟ فِيكُم مَّا قَٰتَلُوٓا۟ إِلَّا قَلِيلًا', 'يحسبون الأحزاب لم يذهبوا وإن يأت الأحزاب يودوا لو أنهم بادون في الأعراب يسألون عن أنبائكم ولو كانوا فيكم ما قاتلوا إلا قليلا', 21, 42, 420, 168, '33:20'),
(3554, NULL, NULL, 33, 21, 'لَّقَدْ كَانَ لَكُمْ فِى رَسُولِ ٱللَّهِ أُسْوَةٌ حَسَنَةٌ لِّمَن كَانَ يَرْجُوا۟ ٱللَّهَ وَٱلْيَوْمَ ٱلْءَاخِرَ وَذَكَرَ ٱللَّهَ كَثِيرًا', 'لقد كان لكم في رسول الله أسوة حسنة لمن كان يرجو الله واليوم الآخر وذكر الله كثيرا', 21, 42, 420, 168, '33:21'),
(3555, NULL, NULL, 33, 22, 'وَلَمَّا رَءَا ٱلْمُؤْمِنُونَ ٱلْأَحْزَابَ قَالُوا۟ هَٰذَا مَا وَعَدَنَا ٱللَّهُ وَرَسُولُهُۥ وَصَدَقَ ٱللَّهُ وَرَسُولُهُۥ وَمَا زَادَهُمْ إِلَّآ إِيمَٰنًا وَتَسْلِيمًا', 'ولما رأى المؤمنون الأحزاب قالوا هذا ما وعدنا الله ورسوله وصدق الله ورسوله وما زادهم إلا إيمانا وتسليما', 21, 42, 420, 168, '33:22'),
(3556, NULL, NULL, 33, 23, 'مِّنَ ٱلْمُؤْمِنِينَ رِجَالٌ صَدَقُوا۟ مَا عَٰهَدُوا۟ ٱللَّهَ عَلَيْهِ فَمِنْهُم مَّن قَضَىٰ نَحْبَهُۥ وَمِنْهُم مَّن يَنتَظِرُ وَمَا بَدَّلُوا۟ تَبْدِيلًا', 'من المؤمنين رجال صدقوا ما عاهدوا الله عليه فمنهم من قضى نحبه ومنهم من ينتظر وما بدلوا تبديلا', 21, 42, 421, 168, '33:23'),
(3557, NULL, NULL, 33, 24, 'لِّيَجْزِىَ ٱللَّهُ ٱلصَّٰدِقِينَ بِصِدْقِهِمْ وَيُعَذِّبَ ٱلْمُنَٰفِقِينَ إِن شَآءَ أَوْ يَتُوبَ عَلَيْهِمْ إِنَّ ٱللَّهَ كَانَ غَفُورًا رَّحِيمًا', 'ليجزي الله الصادقين بصدقهم ويعذب المنافقين إن شاء أو يتوب عليهم إن الله كان غفورا رحيما', 21, 42, 421, 168, '33:24'),
(3558, NULL, NULL, 33, 25, 'وَرَدَّ ٱللَّهُ ٱلَّذِينَ كَفَرُوا۟ بِغَيْظِهِمْ لَمْ يَنَالُوا۟ خَيْرًا وَكَفَى ٱللَّهُ ٱلْمُؤْمِنِينَ ٱلْقِتَالَ وَكَانَ ٱللَّهُ قَوِيًّا عَزِيزًا', 'ورد الله الذين كفروا بغيظهم لم ينالوا خيرا وكفى الله المؤمنين القتال وكان الله قويا عزيزا', 21, 42, 421, 168, '33:25'),
(3559, NULL, NULL, 33, 26, 'وَأَنزَلَ ٱلَّذِينَ ظَٰهَرُوهُم مِّنْ أَهْلِ ٱلْكِتَٰبِ مِن صَيَاصِيهِمْ وَقَذَفَ فِى قُلُوبِهِمُ ٱلرُّعْبَ فَرِيقًا تَقْتُلُونَ وَتَأْسِرُونَ فَرِيقًا', 'وأنزل الذين ظاهروهم من أهل الكتاب من صياصيهم وقذف في قلوبهم الرعب فريقا تقتلون وتأسرون فريقا', 21, 42, 421, 168, '33:26'),
(3560, NULL, NULL, 33, 27, 'وَأَوْرَثَكُمْ أَرْضَهُمْ وَدِيَٰرَهُمْ وَأَمْوَٰلَهُمْ وَأَرْضًا لَّمْ تَطَـُٔوهَا وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ قَدِيرًا', 'وأورثكم أرضهم وديارهم وأموالهم وأرضا لم تطئوها وكان الله على كل شيء قديرا', 21, 42, 421, 168, '33:27'),
(3561, NULL, NULL, 33, 28, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ قُل لِّأَزْوَٰجِكَ إِن كُنتُنَّ تُرِدْنَ ٱلْحَيَوٰةَ ٱلدُّنْيَا وَزِينَتَهَا فَتَعَالَيْنَ أُمَتِّعْكُنَّ وَأُسَرِّحْكُنَّ سَرَاحًا جَمِيلًا', 'يا أيها النبي قل لأزواجك إن كنتن تردن الحياة الدنيا وزينتها فتعالين أمتعكن وأسرحكن سراحا جميلا', 21, 42, 421, 168, '33:28'),
(3562, NULL, NULL, 33, 29, 'وَإِن كُنتُنَّ تُرِدْنَ ٱللَّهَ وَرَسُولَهُۥ وَٱلدَّارَ ٱلْءَاخِرَةَ فَإِنَّ ٱللَّهَ أَعَدَّ لِلْمُحْسِنَٰتِ مِنكُنَّ أَجْرًا عَظِيمًا', 'وإن كنتن تردن الله ورسوله والدار الآخرة فإن الله أعد للمحسنات منكن أجرا عظيما', 21, 42, 421, 168, '33:29'),
(3563, NULL, NULL, 33, 30, 'يَٰنِسَآءَ ٱلنَّبِىِّ مَن يَأْتِ مِنكُنَّ بِفَٰحِشَةٍ مُّبَيِّنَةٍ يُضَٰعَفْ لَهَا ٱلْعَذَابُ ضِعْفَيْنِ وَكَانَ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرًا', 'يا نساء النبي من يأت منكن بفاحشة مبينة يضاعف لها العذاب ضعفين وكان ذلك على الله يسيرا', 21, 42, 421, 168, '33:30'),
(3564, NULL, NULL, 33, 31, 'وَمَن يَقْنُتْ مِنكُنَّ لِلَّهِ وَرَسُولِهِۦ وَتَعْمَلْ صَٰلِحًا نُّؤْتِهَآ أَجْرَهَا مَرَّتَيْنِ وَأَعْتَدْنَا لَهَا رِزْقًا كَرِيمًا', 'ومن يقنت منكن لله ورسوله وتعمل صالحا نؤتها أجرها مرتين وأعتدنا لها رزقا كريما', 22, 43, 422, 169, '33:31'),
(3565, NULL, NULL, 33, 32, 'يَٰنِسَآءَ ٱلنَّبِىِّ لَسْتُنَّ كَأَحَدٍ مِّنَ ٱلنِّسَآءِ إِنِ ٱتَّقَيْتُنَّ فَلَا تَخْضَعْنَ بِٱلْقَوْلِ فَيَطْمَعَ ٱلَّذِى فِى قَلْبِهِۦ مَرَضٌ وَقُلْنَ قَوْلًا مَّعْرُوفًا', 'يا نساء النبي لستن كأحد من النساء إن اتقيتن فلا تخضعن بالقول فيطمع الذي في قلبه مرض وقلن قولا معروفا', 22, 43, 422, 169, '33:32'),
(3566, NULL, NULL, 33, 33, 'وَقَرْنَ فِى بُيُوتِكُنَّ وَلَا تَبَرَّجْنَ تَبَرُّجَ ٱلْجَٰهِلِيَّةِ ٱلْأُولَىٰ وَأَقِمْنَ ٱلصَّلَوٰةَ وَءَاتِينَ ٱلزَّكَوٰةَ وَأَطِعْنَ ٱللَّهَ وَرَسُولَهُۥٓ إِنَّمَا يُرِيدُ ٱللَّهُ لِيُذْهِبَ عَنكُمُ ٱلرِّجْسَ أَهْلَ ٱلْبَيْتِ وَيُطَهِّرَكُمْ تَطْهِيرًا', 'وقرن في بيوتكن ولا تبرجن تبرج الجاهلية الأولى وأقمن الصلاة وآتين الزكاة وأطعن الله ورسوله إنما يريد الله ليذهب عنكم الرجس أهل البيت ويطهركم تطهيرا', 22, 43, 422, 169, '33:33'),
(3567, NULL, NULL, 33, 34, 'وَٱذْكُرْنَ مَا يُتْلَىٰ فِى بُيُوتِكُنَّ مِنْ ءَايَٰتِ ٱللَّهِ وَٱلْحِكْمَةِ إِنَّ ٱللَّهَ كَانَ لَطِيفًا خَبِيرًا', 'واذكرن ما يتلى في بيوتكن من آيات الله والحكمة إن الله كان لطيفا خبيرا', 22, 43, 422, 169, '33:34'),
(3568, NULL, NULL, 33, 35, 'إِنَّ ٱلْمُسْلِمِينَ وَٱلْمُسْلِمَٰتِ وَٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ وَٱلْقَٰنِتِينَ وَٱلْقَٰنِتَٰتِ وَٱلصَّٰدِقِينَ وَٱلصَّٰدِقَٰتِ وَٱلصَّٰبِرِينَ وَٱلصَّٰبِرَٰتِ وَٱلْخَٰشِعِينَ وَٱلْخَٰشِعَٰتِ وَٱلْمُتَصَدِّقِينَ وَٱلْمُتَصَدِّقَٰتِ وَٱلصَّٰٓئِمِينَ وَٱلصَّٰٓئِمَٰتِ وَٱلْحَٰفِظِينَ فُرُوجَهُمْ وَٱلْحَٰفِظَٰتِ وَٱلذَّٰكِرِينَ ٱللَّهَ كَثِيرًا وَٱلذَّٰكِرَٰتِ أَعَدَّ ٱللَّهُ لَهُم مَّغْفِرَةً وَأَجْرًا عَظِيمًا', 'إن المسلمين والمسلمات والمؤمنين والمؤمنات والقانتين والقانتات والصادقين والصادقات والصابرين والصابرات والخاشعين والخاشعات والمتصدقين والمتصدقات والصائمين والصائمات والحافظين فروجهم والحافظات والذاكرين الله كثيرا والذاكرات أعد الله لهم مغفرة وأجرا عظيما', 22, 43, 422, 169, '33:35'),
(3569, NULL, NULL, 33, 36, 'وَمَا كَانَ لِمُؤْمِنٍ وَلَا مُؤْمِنَةٍ إِذَا قَضَى ٱللَّهُ وَرَسُولُهُۥٓ أَمْرًا أَن يَكُونَ لَهُمُ ٱلْخِيَرَةُ مِنْ أَمْرِهِمْ وَمَن يَعْصِ ٱللَّهَ وَرَسُولَهُۥ فَقَدْ ضَلَّ ضَلَٰلًا مُّبِينًا', 'وما كان لمؤمن ولا مؤمنة إذا قضى الله ورسوله أمرا أن يكون لهم الخيرة من أمرهم ومن يعص الله ورسوله فقد ضل ضلالا مبينا', 22, 43, 423, 169, '33:36'),
(3570, NULL, NULL, 33, 37, 'وَإِذْ تَقُولُ لِلَّذِىٓ أَنْعَمَ ٱللَّهُ عَلَيْهِ وَأَنْعَمْتَ عَلَيْهِ أَمْسِكْ عَلَيْكَ زَوْجَكَ وَٱتَّقِ ٱللَّهَ وَتُخْفِى فِى نَفْسِكَ مَا ٱللَّهُ مُبْدِيهِ وَتَخْشَى ٱلنَّاسَ وَٱللَّهُ أَحَقُّ أَن تَخْشَىٰهُ فَلَمَّا قَضَىٰ زَيْدٌ مِّنْهَا وَطَرًا زَوَّجْنَٰكَهَا لِكَىْ لَا يَكُونَ عَلَى ٱلْمُؤْمِنِينَ حَرَجٌ فِىٓ أَزْوَٰجِ أَدْعِيَآئِهِمْ إِذَا قَضَوْا۟ مِنْهُنَّ وَطَرًا وَكَانَ أَمْرُ ٱللَّهِ مَفْعُولًا', 'وإذ تقول للذي أنعم الله عليه وأنعمت عليه أمسك عليك زوجك واتق الله وتخفي في نفسك ما الله مبديه وتخشى الناس والله أحق أن تخشاه فلما قضى زيد منها وطرا زوجناكها لكي لا يكون على المؤمنين حرج في أزواج أدعيائهم إذا قضوا منهن وطرا وكان أمر الله مفعولا', 22, 43, 423, 169, '33:37'),
(3571, NULL, NULL, 33, 38, 'مَّا كَانَ عَلَى ٱلنَّبِىِّ مِنْ حَرَجٍ فِيمَا فَرَضَ ٱللَّهُ لَهُۥ سُنَّةَ ٱللَّهِ فِى ٱلَّذِينَ خَلَوْا۟ مِن قَبْلُ وَكَانَ أَمْرُ ٱللَّهِ قَدَرًا مَّقْدُورًا', 'ما كان على النبي من حرج فيما فرض الله له سنة الله في الذين خلوا من قبل وكان أمر الله قدرا مقدورا', 22, 43, 423, 169, '33:38'),
(3572, NULL, NULL, 33, 39, 'ٱلَّذِينَ يُبَلِّغُونَ رِسَٰلَٰتِ ٱللَّهِ وَيَخْشَوْنَهُۥ وَلَا يَخْشَوْنَ أَحَدًا إِلَّا ٱللَّهَ وَكَفَىٰ بِٱللَّهِ حَسِيبًا', 'الذين يبلغون رسالات الله ويخشونه ولا يخشون أحدا إلا الله وكفى بالله حسيبا', 22, 43, 423, 169, '33:39'),
(3573, NULL, NULL, 33, 40, 'مَّا كَانَ مُحَمَّدٌ أَبَآ أَحَدٍ مِّن رِّجَالِكُمْ وَلَٰكِن رَّسُولَ ٱللَّهِ وَخَاتَمَ ٱلنَّبِيِّۦنَ وَكَانَ ٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمًا', 'ما كان محمد أبا أحد من رجالكم ولكن رسول الله وخاتم النبيين وكان الله بكل شيء عليما', 22, 43, 423, 169, '33:40'),
(3574, NULL, NULL, 33, 41, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱذْكُرُوا۟ ٱللَّهَ ذِكْرًا كَثِيرًا', 'يا أيها الذين آمنوا اذكروا الله ذكرا كثيرا', 22, 43, 423, 169, '33:41'),
(3575, NULL, NULL, 33, 42, 'وَسَبِّحُوهُ بُكْرَةً وَأَصِيلًا', 'وسبحوه بكرة وأصيلا', 22, 43, 423, 169, '33:42'),
(3576, NULL, NULL, 33, 43, 'هُوَ ٱلَّذِى يُصَلِّى عَلَيْكُمْ وَمَلَٰٓئِكَتُهُۥ لِيُخْرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ وَكَانَ بِٱلْمُؤْمِنِينَ رَحِيمًا', 'هو الذي يصلي عليكم وملائكته ليخرجكم من الظلمات إلى النور وكان بالمؤمنين رحيما', 22, 43, 423, 169, '33:43'),
(3577, NULL, NULL, 33, 44, 'تَحِيَّتُهُمْ يَوْمَ يَلْقَوْنَهُۥ سَلَٰمٌ وَأَعَدَّ لَهُمْ أَجْرًا كَرِيمًا', 'تحيتهم يوم يلقونه سلام وأعد لهم أجرا كريما', 22, 43, 424, 169, '33:44'),
(3578, NULL, NULL, 33, 45, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ إِنَّآ أَرْسَلْنَٰكَ شَٰهِدًا وَمُبَشِّرًا وَنَذِيرًا', 'يا أيها النبي إنا أرسلناك شاهدا ومبشرا ونذيرا', 22, 43, 424, 169, '33:45'),
(3579, NULL, NULL, 33, 46, 'وَدَاعِيًا إِلَى ٱللَّهِ بِإِذْنِهِۦ وَسِرَاجًا مُّنِيرًا', 'وداعيا إلى الله بإذنه وسراجا منيرا', 22, 43, 424, 169, '33:46'),
(3580, NULL, NULL, 33, 47, 'وَبَشِّرِ ٱلْمُؤْمِنِينَ بِأَنَّ لَهُم مِّنَ ٱللَّهِ فَضْلًا كَبِيرًا', 'وبشر المؤمنين بأن لهم من الله فضلا كبيرا', 22, 43, 424, 169, '33:47'),
(3581, NULL, NULL, 33, 48, 'وَلَا تُطِعِ ٱلْكَٰفِرِينَ وَٱلْمُنَٰفِقِينَ وَدَعْ أَذَىٰهُمْ وَتَوَكَّلْ عَلَى ٱللَّهِ وَكَفَىٰ بِٱللَّهِ وَكِيلًا', 'ولا تطع الكافرين والمنافقين ودع أذاهم وتوكل على الله وكفى بالله وكيلا', 22, 43, 424, 169, '33:48'),
(3582, NULL, NULL, 33, 49, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا نَكَحْتُمُ ٱلْمُؤْمِنَٰتِ ثُمَّ طَلَّقْتُمُوهُنَّ مِن قَبْلِ أَن تَمَسُّوهُنَّ فَمَا لَكُمْ عَلَيْهِنَّ مِنْ عِدَّةٍ تَعْتَدُّونَهَا فَمَتِّعُوهُنَّ وَسَرِّحُوهُنَّ سَرَاحًا جَمِيلًا', 'يا أيها الذين آمنوا إذا نكحتم المؤمنات ثم طلقتموهن من قبل أن تمسوهن فما لكم عليهن من عدة تعتدونها فمتعوهن وسرحوهن سراحا جميلا', 22, 43, 424, 169, '33:49'),
(3583, NULL, NULL, 33, 50, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ إِنَّآ أَحْلَلْنَا لَكَ أَزْوَٰجَكَ ٱلَّٰتِىٓ ءَاتَيْتَ أُجُورَهُنَّ وَمَا مَلَكَتْ يَمِينُكَ مِمَّآ أَفَآءَ ٱللَّهُ عَلَيْكَ وَبَنَاتِ عَمِّكَ وَبَنَاتِ عَمَّٰتِكَ وَبَنَاتِ خَالِكَ وَبَنَاتِ خَٰلَٰتِكَ ٱلَّٰتِى هَاجَرْنَ مَعَكَ وَٱمْرَأَةً مُّؤْمِنَةً إِن وَهَبَتْ نَفْسَهَا لِلنَّبِىِّ إِنْ أَرَادَ ٱلنَّبِىُّ أَن يَسْتَنكِحَهَا خَالِصَةً لَّكَ مِن دُونِ ٱلْمُؤْمِنِينَ قَدْ عَلِمْنَا مَا فَرَضْنَا عَلَيْهِمْ فِىٓ أَزْوَٰجِهِمْ وَمَا مَلَكَتْ أَيْمَٰنُهُمْ لِكَيْلَا يَكُونَ عَلَيْكَ حَرَجٌ وَكَانَ ٱللَّهُ غَفُورًا رَّحِيمًا', 'يا أيها النبي إنا أحللنا لك أزواجك اللاتي آتيت أجورهن وما ملكت يمينك مما أفاء الله عليك وبنات عمك وبنات عماتك وبنات خالك وبنات خالاتك اللاتي هاجرن معك وامرأة مؤمنة إن وهبت نفسها للنبي إن أراد النبي أن يستنكحها خالصة لك من دون المؤمنين قد علمنا ما فرضنا عليهم في أزواجهم وما ملكت أيمانهم لكيلا يكون عليك حرج وكان الله غفورا رحيما', 22, 43, 424, 169, '33:50'),
(3584, NULL, NULL, 33, 51, 'تُرْجِى مَن تَشَآءُ مِنْهُنَّ وَتُـْٔوِىٓ إِلَيْكَ مَن تَشَآءُ وَمَنِ ٱبْتَغَيْتَ مِمَّنْ عَزَلْتَ فَلَا جُنَاحَ عَلَيْكَ ذَٰلِكَ أَدْنَىٰٓ أَن تَقَرَّ أَعْيُنُهُنَّ وَلَا يَحْزَنَّ وَيَرْضَيْنَ بِمَآ ءَاتَيْتَهُنَّ كُلُّهُنَّ وَٱللَّهُ يَعْلَمُ مَا فِى قُلُوبِكُمْ وَكَانَ ٱللَّهُ عَلِيمًا حَلِيمًا', 'ترجي من تشاء منهن وتؤوي إليك من تشاء ومن ابتغيت ممن عزلت فلا جناح عليك ذلك أدنى أن تقر أعينهن ولا يحزن ويرضين بما آتيتهن كلهن والله يعلم ما في قلوبكم وكان الله عليما حليما', 22, 43, 425, 170, '33:51'),
(3585, NULL, NULL, 33, 52, 'لَّا يَحِلُّ لَكَ ٱلنِّسَآءُ مِنۢ بَعْدُ وَلَآ أَن تَبَدَّلَ بِهِنَّ مِنْ أَزْوَٰجٍ وَلَوْ أَعْجَبَكَ حُسْنُهُنَّ إِلَّا مَا مَلَكَتْ يَمِينُكَ وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ رَّقِيبًا', 'لا يحل لك النساء من بعد ولا أن تبدل بهن من أزواج ولو أعجبك حسنهن إلا ما ملكت يمينك وكان الله على كل شيء رقيبا', 22, 43, 425, 170, '33:52'),
(3586, NULL, NULL, 33, 53, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَدْخُلُوا۟ بُيُوتَ ٱلنَّبِىِّ إِلَّآ أَن يُؤْذَنَ لَكُمْ إِلَىٰ طَعَامٍ غَيْرَ نَٰظِرِينَ إِنَىٰهُ وَلَٰكِنْ إِذَا دُعِيتُمْ فَٱدْخُلُوا۟ فَإِذَا طَعِمْتُمْ فَٱنتَشِرُوا۟ وَلَا مُسْتَـْٔنِسِينَ لِحَدِيثٍ إِنَّ ذَٰلِكُمْ كَانَ يُؤْذِى ٱلنَّبِىَّ فَيَسْتَحْىِۦ مِنكُمْ وَٱللَّهُ لَا يَسْتَحْىِۦ مِنَ ٱلْحَقِّ وَإِذَا سَأَلْتُمُوهُنَّ مَتَٰعًا فَسْـَٔلُوهُنَّ مِن وَرَآءِ حِجَابٍ ذَٰلِكُمْ أَطْهَرُ لِقُلُوبِكُمْ وَقُلُوبِهِنَّ وَمَا كَانَ لَكُمْ أَن تُؤْذُوا۟ رَسُولَ ٱللَّهِ وَلَآ أَن تَنكِحُوٓا۟ أَزْوَٰجَهُۥ مِنۢ بَعْدِهِۦٓ أَبَدًا إِنَّ ذَٰلِكُمْ كَانَ عِندَ ٱللَّهِ عَظِيمًا', 'يا أيها الذين آمنوا لا تدخلوا بيوت النبي إلا أن يؤذن لكم إلى طعام غير ناظرين إناه ولكن إذا دعيتم فادخلوا فإذا طعمتم فانتشروا ولا مستأنسين لحديث إن ذلكم كان يؤذي النبي فيستحيي منكم والله لا يستحيي من الحق وإذا سألتموهن متاعا فاسألوهن من وراء حجاب ذلكم أطهر لقلوبكم وقلوبهن وما كان لكم أن تؤذوا رسول الله ولا أن تنكحوا أزواجه من بعده أبدا إن ذلكم كان عند الله عظيما', 22, 43, 425, 170, '33:53'),
(3587, NULL, NULL, 33, 54, 'إِن تُبْدُوا۟ شَيْـًٔا أَوْ تُخْفُوهُ فَإِنَّ ٱللَّهَ كَانَ بِكُلِّ شَىْءٍ عَلِيمًا', 'إن تبدوا شيئا أو تخفوه فإن الله كان بكل شيء عليما', 22, 43, 425, 170, '33:54'),
(3588, NULL, NULL, 33, 55, 'لَّا جُنَاحَ عَلَيْهِنَّ فِىٓ ءَابَآئِهِنَّ وَلَآ أَبْنَآئِهِنَّ وَلَآ إِخْوَٰنِهِنَّ وَلَآ أَبْنَآءِ إِخْوَٰنِهِنَّ وَلَآ أَبْنَآءِ أَخَوَٰتِهِنَّ وَلَا نِسَآئِهِنَّ وَلَا مَا مَلَكَتْ أَيْمَٰنُهُنَّ وَٱتَّقِينَ ٱللَّهَ إِنَّ ٱللَّهَ كَانَ عَلَىٰ كُلِّ شَىْءٍ شَهِيدًا', 'لا جناح عليهن في آبائهن ولا أبنائهن ولا إخوانهن ولا أبناء إخوانهن ولا أبناء أخواتهن ولا نسائهن ولا ما ملكت أيمانهن واتقين الله إن الله كان على كل شيء شهيدا', 22, 43, 426, 170, '33:55'),
(3589, NULL, NULL, 33, 56, 'إِنَّ ٱللَّهَ وَمَلَٰٓئِكَتَهُۥ يُصَلُّونَ عَلَى ٱلنَّبِىِّ يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ صَلُّوا۟ عَلَيْهِ وَسَلِّمُوا۟ تَسْلِيمًا', 'إن الله وملائكته يصلون على النبي يا أيها الذين آمنوا صلوا عليه وسلموا تسليما', 22, 43, 426, 170, '33:56'),
(3590, NULL, NULL, 33, 57, 'إِنَّ ٱلَّذِينَ يُؤْذُونَ ٱللَّهَ وَرَسُولَهُۥ لَعَنَهُمُ ٱللَّهُ فِى ٱلدُّنْيَا وَٱلْءَاخِرَةِ وَأَعَدَّ لَهُمْ عَذَابًا مُّهِينًا', 'إن الذين يؤذون الله ورسوله لعنهم الله في الدنيا والآخرة وأعد لهم عذابا مهينا', 22, 43, 426, 170, '33:57'),
(3591, NULL, NULL, 33, 58, 'وَٱلَّذِينَ يُؤْذُونَ ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ بِغَيْرِ مَا ٱكْتَسَبُوا۟ فَقَدِ ٱحْتَمَلُوا۟ بُهْتَٰنًا وَإِثْمًا مُّبِينًا', 'والذين يؤذون المؤمنين والمؤمنات بغير ما اكتسبوا فقد احتملوا بهتانا وإثما مبينا', 22, 43, 426, 170, '33:58'),
(3592, NULL, NULL, 33, 59, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ قُل لِّأَزْوَٰجِكَ وَبَنَاتِكَ وَنِسَآءِ ٱلْمُؤْمِنِينَ يُدْنِينَ عَلَيْهِنَّ مِن جَلَٰبِيبِهِنَّ ذَٰلِكَ أَدْنَىٰٓ أَن يُعْرَفْنَ فَلَا يُؤْذَيْنَ وَكَانَ ٱللَّهُ غَفُورًا رَّحِيمًا', 'يا أيها النبي قل لأزواجك وبناتك ونساء المؤمنين يدنين عليهن من جلابيبهن ذلك أدنى أن يعرفن فلا يؤذين وكان الله غفورا رحيما', 22, 43, 426, 170, '33:59'),
(3593, NULL, NULL, 33, 60, 'لَّئِن لَّمْ يَنتَهِ ٱلْمُنَٰفِقُونَ وَٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌ وَٱلْمُرْجِفُونَ فِى ٱلْمَدِينَةِ لَنُغْرِيَنَّكَ بِهِمْ ثُمَّ لَا يُجَاوِرُونَكَ فِيهَآ إِلَّا قَلِيلًا', 'لئن لم ينته المنافقون والذين في قلوبهم مرض والمرجفون في المدينة لنغرينك بهم ثم لا يجاورونك فيها إلا قليلا', 22, 43, 426, 171, '33:60'),
(3594, NULL, NULL, 33, 61, 'مَّلْعُونِينَ أَيْنَمَا ثُقِفُوٓا۟ أُخِذُوا۟ وَقُتِّلُوا۟ تَقْتِيلًا', 'ملعونين أينما ثقفوا أخذوا وقتلوا تقتيلا', 22, 43, 426, 171, '33:61'),
(3595, NULL, NULL, 33, 62, 'سُنَّةَ ٱللَّهِ فِى ٱلَّذِينَ خَلَوْا۟ مِن قَبْلُ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبْدِيلًا', 'سنة الله في الذين خلوا من قبل ولن تجد لسنة الله تبديلا', 22, 43, 426, 171, '33:62'),
(3596, NULL, NULL, 33, 63, 'يَسْـَٔلُكَ ٱلنَّاسُ عَنِ ٱلسَّاعَةِ قُلْ إِنَّمَا عِلْمُهَا عِندَ ٱللَّهِ وَمَا يُدْرِيكَ لَعَلَّ ٱلسَّاعَةَ تَكُونُ قَرِيبًا', 'يسألك الناس عن الساعة قل إنما علمها عند الله وما يدريك لعل الساعة تكون قريبا', 22, 43, 427, 171, '33:63'),
(3597, NULL, NULL, 33, 64, 'إِنَّ ٱللَّهَ لَعَنَ ٱلْكَٰفِرِينَ وَأَعَدَّ لَهُمْ سَعِيرًا', 'إن الله لعن الكافرين وأعد لهم سعيرا', 22, 43, 427, 171, '33:64'),
(3598, NULL, NULL, 33, 65, 'خَٰلِدِينَ فِيهَآ أَبَدًا لَّا يَجِدُونَ وَلِيًّا وَلَا نَصِيرًا', 'خالدين فيها أبدا لا يجدون وليا ولا نصيرا', 22, 43, 427, 171, '33:65'),
(3599, NULL, NULL, 33, 66, 'يَوْمَ تُقَلَّبُ وُجُوهُهُمْ فِى ٱلنَّارِ يَقُولُونَ يَٰلَيْتَنَآ أَطَعْنَا ٱللَّهَ وَأَطَعْنَا ٱلرَّسُولَا۠', 'يوم تقلب وجوههم في النار يقولون يا ليتنا أطعنا الله وأطعنا الرسولا', 22, 43, 427, 171, '33:66'),
(3600, NULL, NULL, 33, 67, 'وَقَالُوا۟ رَبَّنَآ إِنَّآ أَطَعْنَا سَادَتَنَا وَكُبَرَآءَنَا فَأَضَلُّونَا ٱلسَّبِيلَا۠', 'وقالوا ربنا إنا أطعنا سادتنا وكبراءنا فأضلونا السبيلا', 22, 43, 427, 171, '33:67'),
(3601, NULL, NULL, 33, 68, 'رَبَّنَآ ءَاتِهِمْ ضِعْفَيْنِ مِنَ ٱلْعَذَابِ وَٱلْعَنْهُمْ لَعْنًا كَبِيرًا', 'ربنا آتهم ضعفين من العذاب والعنهم لعنا كبيرا', 22, 43, 427, 171, '33:68'),
(3602, NULL, NULL, 33, 69, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَكُونُوا۟ كَٱلَّذِينَ ءَاذَوْا۟ مُوسَىٰ فَبَرَّأَهُ ٱللَّهُ مِمَّا قَالُوا۟ وَكَانَ عِندَ ٱللَّهِ وَجِيهًا', 'يا أيها الذين آمنوا لا تكونوا كالذين آذوا موسى فبرأه الله مما قالوا وكان عند الله وجيها', 22, 43, 427, 171, '33:69'),
(3603, NULL, NULL, 33, 70, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَقُولُوا۟ قَوْلًا سَدِيدًا', 'يا أيها الذين آمنوا اتقوا الله وقولوا قولا سديدا', 22, 43, 427, 171, '33:70'),
(3604, NULL, NULL, 33, 71, 'يُصْلِحْ لَكُمْ أَعْمَٰلَكُمْ وَيَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ فَقَدْ فَازَ فَوْزًا عَظِيمًا', 'يصلح لكم أعمالكم ويغفر لكم ذنوبكم ومن يطع الله ورسوله فقد فاز فوزا عظيما', 22, 43, 427, 171, '33:71'),
(3605, NULL, NULL, 33, 72, 'إِنَّا عَرَضْنَا ٱلْأَمَانَةَ عَلَى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَٱلْجِبَالِ فَأَبَيْنَ أَن يَحْمِلْنَهَا وَأَشْفَقْنَ مِنْهَا وَحَمَلَهَا ٱلْإِنسَٰنُ إِنَّهُۥ كَانَ ظَلُومًا جَهُولًا', 'إنا عرضنا الأمانة على السماوات والأرض والجبال فأبين أن يحملنها وأشفقن منها وحملها الإنسان إنه كان ظلوما جهولا', 22, 43, 427, 171, '33:72');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(3606, NULL, NULL, 33, 73, 'لِّيُعَذِّبَ ٱللَّهُ ٱلْمُنَٰفِقِينَ وَٱلْمُنَٰفِقَٰتِ وَٱلْمُشْرِكِينَ وَٱلْمُشْرِكَٰتِ وَيَتُوبَ ٱللَّهُ عَلَى ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ وَكَانَ ٱللَّهُ غَفُورًا رَّحِيمًۢا', 'ليعذب الله المنافقين والمنافقات والمشركين والمشركات ويتوب الله على المؤمنين والمؤمنات وكان الله غفورا رحيما', 22, 43, 427, 171, '33:73'),
(3607, NULL, NULL, 34, 1, 'ٱلْحَمْدُ لِلَّهِ ٱلَّذِى لَهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَلَهُ ٱلْحَمْدُ فِى ٱلْءَاخِرَةِ وَهُوَ ٱلْحَكِيمُ ٱلْخَبِيرُ', 'الحمد لله الذي له ما في السماوات وما في الأرض وله الحمد في الآخرة وهو الحكيم الخبير', 22, 43, 428, 171, '34:1'),
(3608, NULL, NULL, 34, 2, 'يَعْلَمُ مَا يَلِجُ فِى ٱلْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعْرُجُ فِيهَا وَهُوَ ٱلرَّحِيمُ ٱلْغَفُورُ', 'يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو الرحيم الغفور', 22, 43, 428, 171, '34:2'),
(3609, NULL, NULL, 34, 3, 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَا تَأْتِينَا ٱلسَّاعَةُ قُلْ بَلَىٰ وَرَبِّى لَتَأْتِيَنَّكُمْ عَٰلِمِ ٱلْغَيْبِ لَا يَعْزُبُ عَنْهُ مِثْقَالُ ذَرَّةٍ فِى ٱلسَّمَٰوَٰتِ وَلَا فِى ٱلْأَرْضِ وَلَآ أَصْغَرُ مِن ذَٰلِكَ وَلَآ أَكْبَرُ إِلَّا فِى كِتَٰبٍ مُّبِينٍ', 'وقال الذين كفروا لا تأتينا الساعة قل بلى وربي لتأتينكم عالم الغيب لا يعزب عنه مثقال ذرة في السماوات ولا في الأرض ولا أصغر من ذلك ولا أكبر إلا في كتاب مبين', 22, 43, 428, 171, '34:3'),
(3610, NULL, NULL, 34, 4, 'لِّيَجْزِىَ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ أُو۟لَٰٓئِكَ لَهُم مَّغْفِرَةٌ وَرِزْقٌ كَرِيمٌ', 'ليجزي الذين آمنوا وعملوا الصالحات أولئك لهم مغفرة ورزق كريم', 22, 43, 428, 171, '34:4'),
(3611, NULL, NULL, 34, 5, 'وَٱلَّذِينَ سَعَوْ فِىٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُو۟لَٰٓئِكَ لَهُمْ عَذَابٌ مِّن رِّجْزٍ أَلِيمٌ', 'والذين سعوا في آياتنا معاجزين أولئك لهم عذاب من رجز أليم', 22, 43, 428, 171, '34:5'),
(3612, NULL, NULL, 34, 6, 'وَيَرَى ٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ ٱلَّذِىٓ أُنزِلَ إِلَيْكَ مِن رَّبِّكَ هُوَ ٱلْحَقَّ وَيَهْدِىٓ إِلَىٰ صِرَٰطِ ٱلْعَزِيزِ ٱلْحَمِيدِ', 'ويرى الذين أوتوا العلم الذي أنزل إليك من ربك هو الحق ويهدي إلى صراط العزيز الحميد', 22, 43, 428, 171, '34:6'),
(3613, NULL, NULL, 34, 7, 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ هَلْ نَدُلُّكُمْ عَلَىٰ رَجُلٍ يُنَبِّئُكُمْ إِذَا مُزِّقْتُمْ كُلَّ مُمَزَّقٍ إِنَّكُمْ لَفِى خَلْقٍ جَدِيدٍ', 'وقال الذين كفروا هل ندلكم على رجل ينبئكم إذا مزقتم كل ممزق إنكم لفي خلق جديد', 22, 43, 428, 171, '34:7'),
(3614, NULL, NULL, 34, 8, 'أَفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًا أَم بِهِۦ جِنَّةٌۢ بَلِ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْءَاخِرَةِ فِى ٱلْعَذَابِ وَٱلضَّلَٰلِ ٱلْبَعِيدِ', 'أفترى على الله كذبا أم به جنة بل الذين لا يؤمنون بالآخرة في العذاب والضلال البعيد', 22, 43, 429, 171, '34:8'),
(3615, NULL, NULL, 34, 9, 'أَفَلَمْ يَرَوْا۟ إِلَىٰ مَا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُم مِّنَ ٱلسَّمَآءِ وَٱلْأَرْضِ إِن نَّشَأْ نَخْسِفْ بِهِمُ ٱلْأَرْضَ أَوْ نُسْقِطْ عَلَيْهِمْ كِسَفًا مِّنَ ٱلسَّمَآءِ إِنَّ فِى ذَٰلِكَ لَءَايَةً لِّكُلِّ عَبْدٍ مُّنِيبٍ', 'أفلم يروا إلى ما بين أيديهم وما خلفهم من السماء والأرض إن نشأ نخسف بهم الأرض أو نسقط عليهم كسفا من السماء إن في ذلك لآية لكل عبد منيب', 22, 43, 429, 171, '34:9'),
(3616, NULL, NULL, 34, 10, 'وَلَقَدْ ءَاتَيْنَا دَاوُۥدَ مِنَّا فَضْلًا يَٰجِبَالُ أَوِّبِى مَعَهُۥ وَٱلطَّيْرَ وَأَلَنَّا لَهُ ٱلْحَدِيدَ', 'ولقد آتينا داوود منا فضلا يا جبال أوبي معه والطير وألنا له الحديد', 22, 43, 429, 172, '34:10'),
(3617, NULL, NULL, 34, 11, 'أَنِ ٱعْمَلْ سَٰبِغَٰتٍ وَقَدِّرْ فِى ٱلسَّرْدِ وَٱعْمَلُوا۟ صَٰلِحًا إِنِّى بِمَا تَعْمَلُونَ بَصِيرٌ', 'أن اعمل سابغات وقدر في السرد واعملوا صالحا إني بما تعملون بصير', 22, 43, 429, 172, '34:11'),
(3618, NULL, NULL, 34, 12, 'وَلِسُلَيْمَٰنَ ٱلرِّيحَ غُدُوُّهَا شَهْرٌ وَرَوَاحُهَا شَهْرٌ وَأَسَلْنَا لَهُۥ عَيْنَ ٱلْقِطْرِ وَمِنَ ٱلْجِنِّ مَن يَعْمَلُ بَيْنَ يَدَيْهِ بِإِذْنِ رَبِّهِۦ وَمَن يَزِغْ مِنْهُمْ عَنْ أَمْرِنَا نُذِقْهُ مِنْ عَذَابِ ٱلسَّعِيرِ', 'ولسليمان الريح غدوها شهر ورواحها شهر وأسلنا له عين القطر ومن الجن من يعمل بين يديه بإذن ربه ومن يزغ منهم عن أمرنا نذقه من عذاب السعير', 22, 43, 429, 172, '34:12'),
(3619, NULL, NULL, 34, 13, 'يَعْمَلُونَ لَهُۥ مَا يَشَآءُ مِن مَّحَٰرِيبَ وَتَمَٰثِيلَ وَجِفَانٍ كَٱلْجَوَابِ وَقُدُورٍ رَّاسِيَٰتٍ ٱعْمَلُوٓا۟ ءَالَ دَاوُۥدَ شُكْرًا وَقَلِيلٌ مِّنْ عِبَادِىَ ٱلشَّكُورُ', 'يعملون له ما يشاء من محاريب وتماثيل وجفان كالجواب وقدور راسيات اعملوا آل داوود شكرا وقليل من عبادي الشكور', 22, 43, 429, 172, '34:13'),
(3620, NULL, NULL, 34, 14, 'فَلَمَّا قَضَيْنَا عَلَيْهِ ٱلْمَوْتَ مَا دَلَّهُمْ عَلَىٰ مَوْتِهِۦٓ إِلَّا دَآبَّةُ ٱلْأَرْضِ تَأْكُلُ مِنسَأَتَهُۥ فَلَمَّا خَرَّ تَبَيَّنَتِ ٱلْجِنُّ أَن لَّوْ كَانُوا۟ يَعْلَمُونَ ٱلْغَيْبَ مَا لَبِثُوا۟ فِى ٱلْعَذَابِ ٱلْمُهِينِ', 'فلما قضينا عليه الموت ما دلهم على موته إلا دابة الأرض تأكل منسأته فلما خر تبينت الجن أن لو كانوا يعلمون الغيب ما لبثوا في العذاب المهين', 22, 43, 429, 172, '34:14'),
(3621, NULL, NULL, 34, 15, 'لَقَدْ كَانَ لِسَبَإٍ فِى مَسْكَنِهِمْ ءَايَةٌ جَنَّتَانِ عَن يَمِينٍ وَشِمَالٍ كُلُوا۟ مِن رِّزْقِ رَبِّكُمْ وَٱشْكُرُوا۟ لَهُۥ بَلْدَةٌ طَيِّبَةٌ وَرَبٌّ غَفُورٌ', 'لقد كان لسبإ في مسكنهم آية جنتان عن يمين وشمال كلوا من رزق ربكم واشكروا له بلدة طيبة ورب غفور', 22, 43, 430, 172, '34:15'),
(3622, NULL, NULL, 34, 16, 'فَأَعْرَضُوا۟ فَأَرْسَلْنَا عَلَيْهِمْ سَيْلَ ٱلْعَرِمِ وَبَدَّلْنَٰهُم بِجَنَّتَيْهِمْ جَنَّتَيْنِ ذَوَاتَىْ أُكُلٍ خَمْطٍ وَأَثْلٍ وَشَىْءٍ مِّن سِدْرٍ قَلِيلٍ', 'فأعرضوا فأرسلنا عليهم سيل العرم وبدلناهم بجنتيهم جنتين ذواتي أكل خمط وأثل وشيء من سدر قليل', 22, 43, 430, 172, '34:16'),
(3623, NULL, NULL, 34, 17, 'ذَٰلِكَ جَزَيْنَٰهُم بِمَا كَفَرُوا۟ وَهَلْ نُجَٰزِىٓ إِلَّا ٱلْكَفُورَ', 'ذلك جزيناهم بما كفروا وهل نجازي إلا الكفور', 22, 43, 430, 172, '34:17'),
(3624, NULL, NULL, 34, 18, 'وَجَعَلْنَا بَيْنَهُمْ وَبَيْنَ ٱلْقُرَى ٱلَّتِى بَٰرَكْنَا فِيهَا قُرًى ظَٰهِرَةً وَقَدَّرْنَا فِيهَا ٱلسَّيْرَ سِيرُوا۟ فِيهَا لَيَالِىَ وَأَيَّامًا ءَامِنِينَ', 'وجعلنا بينهم وبين القرى التي باركنا فيها قرى ظاهرة وقدرنا فيها السير سيروا فيها ليالي وأياما آمنين', 22, 43, 430, 172, '34:18'),
(3625, NULL, NULL, 34, 19, 'فَقَالُوا۟ رَبَّنَا بَٰعِدْ بَيْنَ أَسْفَارِنَا وَظَلَمُوٓا۟ أَنفُسَهُمْ فَجَعَلْنَٰهُمْ أَحَادِيثَ وَمَزَّقْنَٰهُمْ كُلَّ مُمَزَّقٍ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ', 'فقالوا ربنا باعد بين أسفارنا وظلموا أنفسهم فجعلناهم أحاديث ومزقناهم كل ممزق إن في ذلك لآيات لكل صبار شكور', 22, 43, 430, 172, '34:19'),
(3626, NULL, NULL, 34, 20, 'وَلَقَدْ صَدَّقَ عَلَيْهِمْ إِبْلِيسُ ظَنَّهُۥ فَٱتَّبَعُوهُ إِلَّا فَرِيقًا مِّنَ ٱلْمُؤْمِنِينَ', 'ولقد صدق عليهم إبليس ظنه فاتبعوه إلا فريقا من المؤمنين', 22, 43, 430, 172, '34:20'),
(3627, NULL, NULL, 34, 21, 'وَمَا كَانَ لَهُۥ عَلَيْهِم مِّن سُلْطَٰنٍ إِلَّا لِنَعْلَمَ مَن يُؤْمِنُ بِٱلْءَاخِرَةِ مِمَّنْ هُوَ مِنْهَا فِى شَكٍّ وَرَبُّكَ عَلَىٰ كُلِّ شَىْءٍ حَفِيظٌ', 'وما كان له عليهم من سلطان إلا لنعلم من يؤمن بالآخرة ممن هو منها في شك وربك على كل شيء حفيظ', 22, 43, 430, 172, '34:21'),
(3628, NULL, NULL, 34, 22, 'قُلِ ٱدْعُوا۟ ٱلَّذِينَ زَعَمْتُم مِّن دُونِ ٱللَّهِ لَا يَمْلِكُونَ مِثْقَالَ ذَرَّةٍ فِى ٱلسَّمَٰوَٰتِ وَلَا فِى ٱلْأَرْضِ وَمَا لَهُمْ فِيهِمَا مِن شِرْكٍ وَمَا لَهُۥ مِنْهُم مِّن ظَهِيرٍ', 'قل ادعوا الذين زعمتم من دون الله لا يملكون مثقال ذرة في السماوات ولا في الأرض وما لهم فيهما من شرك وما له منهم من ظهير', 22, 43, 430, 172, '34:22'),
(3629, NULL, NULL, 34, 23, 'وَلَا تَنفَعُ ٱلشَّفَٰعَةُ عِندَهُۥٓ إِلَّا لِمَنْ أَذِنَ لَهُۥ حَتَّىٰٓ إِذَا فُزِّعَ عَن قُلُوبِهِمْ قَالُوا۟ مَاذَا قَالَ رَبُّكُمْ قَالُوا۟ ٱلْحَقَّ وَهُوَ ٱلْعَلِىُّ ٱلْكَبِيرُ', 'ولا تنفع الشفاعة عنده إلا لمن أذن له حتى إذا فزع عن قلوبهم قالوا ماذا قال ربكم قالوا الحق وهو العلي الكبير', 22, 43, 431, 172, '34:23'),
(3630, NULL, NULL, 34, 24, 'قُلْ مَن يَرْزُقُكُم مِّنَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ قُلِ ٱللَّهُ وَإِنَّآ أَوْ إِيَّاكُمْ لَعَلَىٰ هُدًى أَوْ فِى ضَلَٰلٍ مُّبِينٍ', 'قل من يرزقكم من السماوات والأرض قل الله وإنا أو إياكم لعلى هدى أو في ضلال مبين', 22, 44, 431, 173, '34:24'),
(3631, NULL, NULL, 34, 25, 'قُل لَّا تُسْـَٔلُونَ عَمَّآ أَجْرَمْنَا وَلَا نُسْـَٔلُ عَمَّا تَعْمَلُونَ', 'قل لا تسألون عما أجرمنا ولا نسأل عما تعملون', 22, 44, 431, 173, '34:25'),
(3632, NULL, NULL, 34, 26, 'قُلْ يَجْمَعُ بَيْنَنَا رَبُّنَا ثُمَّ يَفْتَحُ بَيْنَنَا بِٱلْحَقِّ وَهُوَ ٱلْفَتَّاحُ ٱلْعَلِيمُ', 'قل يجمع بيننا ربنا ثم يفتح بيننا بالحق وهو الفتاح العليم', 22, 44, 431, 173, '34:26'),
(3633, NULL, NULL, 34, 27, 'قُلْ أَرُونِىَ ٱلَّذِينَ أَلْحَقْتُم بِهِۦ شُرَكَآءَ كَلَّا بَلْ هُوَ ٱللَّهُ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'قل أروني الذين ألحقتم به شركاء كلا بل هو الله العزيز الحكيم', 22, 44, 431, 173, '34:27'),
(3634, NULL, NULL, 34, 28, 'وَمَآ أَرْسَلْنَٰكَ إِلَّا كَآفَّةً لِّلنَّاسِ بَشِيرًا وَنَذِيرًا وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ', 'وما أرسلناك إلا كافة للناس بشيرا ونذيرا ولكن أكثر الناس لا يعلمون', 22, 44, 431, 173, '34:28'),
(3635, NULL, NULL, 34, 29, 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَٰدِقِينَ', 'ويقولون متى هذا الوعد إن كنتم صادقين', 22, 44, 431, 173, '34:29'),
(3636, NULL, NULL, 34, 30, 'قُل لَّكُم مِّيعَادُ يَوْمٍ لَّا تَسْتَـْٔخِرُونَ عَنْهُ سَاعَةً وَلَا تَسْتَقْدِمُونَ', 'قل لكم ميعاد يوم لا تستأخرون عنه ساعة ولا تستقدمون', 22, 44, 431, 173, '34:30'),
(3637, NULL, NULL, 34, 31, 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَن نُّؤْمِنَ بِهَٰذَا ٱلْقُرْءَانِ وَلَا بِٱلَّذِى بَيْنَ يَدَيْهِ وَلَوْ تَرَىٰٓ إِذِ ٱلظَّٰلِمُونَ مَوْقُوفُونَ عِندَ رَبِّهِمْ يَرْجِعُ بَعْضُهُمْ إِلَىٰ بَعْضٍ ٱلْقَوْلَ يَقُولُ ٱلَّذِينَ ٱسْتُضْعِفُوا۟ لِلَّذِينَ ٱسْتَكْبَرُوا۟ لَوْلَآ أَنتُمْ لَكُنَّا مُؤْمِنِينَ', 'وقال الذين كفروا لن نؤمن بهذا القرآن ولا بالذي بين يديه ولو ترى إذ الظالمون موقوفون عند ربهم يرجع بعضهم إلى بعض القول يقول الذين استضعفوا للذين استكبروا لولا أنتم لكنا مؤمنين', 22, 44, 431, 173, '34:31'),
(3638, NULL, NULL, 34, 32, 'قَالَ ٱلَّذِينَ ٱسْتَكْبَرُوا۟ لِلَّذِينَ ٱسْتُضْعِفُوٓا۟ أَنَحْنُ صَدَدْنَٰكُمْ عَنِ ٱلْهُدَىٰ بَعْدَ إِذْ جَآءَكُم بَلْ كُنتُم مُّجْرِمِينَ', 'قال الذين استكبروا للذين استضعفوا أنحن صددناكم عن الهدى بعد إذ جاءكم بل كنتم مجرمين', 22, 44, 432, 173, '34:32'),
(3639, NULL, NULL, 34, 33, 'وَقَالَ ٱلَّذِينَ ٱسْتُضْعِفُوا۟ لِلَّذِينَ ٱسْتَكْبَرُوا۟ بَلْ مَكْرُ ٱلَّيْلِ وَٱلنَّهَارِ إِذْ تَأْمُرُونَنَآ أَن نَّكْفُرَ بِٱللَّهِ وَنَجْعَلَ لَهُۥٓ أَندَادًا وَأَسَرُّوا۟ ٱلنَّدَامَةَ لَمَّا رَأَوُا۟ ٱلْعَذَابَ وَجَعَلْنَا ٱلْأَغْلَٰلَ فِىٓ أَعْنَاقِ ٱلَّذِينَ كَفَرُوا۟ هَلْ يُجْزَوْنَ إِلَّا مَا كَانُوا۟ يَعْمَلُونَ', 'وقال الذين استضعفوا للذين استكبروا بل مكر الليل والنهار إذ تأمروننا أن نكفر بالله ونجعل له أندادا وأسروا الندامة لما رأوا العذاب وجعلنا الأغلال في أعناق الذين كفروا هل يجزون إلا ما كانوا يعملون', 22, 44, 432, 173, '34:33'),
(3640, NULL, NULL, 34, 34, 'وَمَآ أَرْسَلْنَا فِى قَرْيَةٍ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَآ إِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَٰفِرُونَ', 'وما أرسلنا في قرية من نذير إلا قال مترفوها إنا بما أرسلتم به كافرون', 22, 44, 432, 173, '34:34'),
(3641, NULL, NULL, 34, 35, 'وَقَالُوا۟ نَحْنُ أَكْثَرُ أَمْوَٰلًا وَأَوْلَٰدًا وَمَا نَحْنُ بِمُعَذَّبِينَ', 'وقالوا نحن أكثر أموالا وأولادا وما نحن بمعذبين', 22, 44, 432, 173, '34:35'),
(3642, NULL, NULL, 34, 36, 'قُلْ إِنَّ رَبِّى يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ', 'قل إن ربي يبسط الرزق لمن يشاء ويقدر ولكن أكثر الناس لا يعلمون', 22, 44, 432, 173, '34:36'),
(3643, NULL, NULL, 34, 37, 'وَمَآ أَمْوَٰلُكُمْ وَلَآ أَوْلَٰدُكُم بِٱلَّتِى تُقَرِّبُكُمْ عِندَنَا زُلْفَىٰٓ إِلَّا مَنْ ءَامَنَ وَعَمِلَ صَٰلِحًا فَأُو۟لَٰٓئِكَ لَهُمْ جَزَآءُ ٱلضِّعْفِ بِمَا عَمِلُوا۟ وَهُمْ فِى ٱلْغُرُفَٰتِ ءَامِنُونَ', 'وما أموالكم ولا أولادكم بالتي تقربكم عندنا زلفى إلا من آمن وعمل صالحا فأولئك لهم جزاء الضعف بما عملوا وهم في الغرفات آمنون', 22, 44, 432, 173, '34:37'),
(3644, NULL, NULL, 34, 38, 'وَٱلَّذِينَ يَسْعَوْنَ فِىٓ ءَايَٰتِنَا مُعَٰجِزِينَ أُو۟لَٰٓئِكَ فِى ٱلْعَذَابِ مُحْضَرُونَ', 'والذين يسعون في آياتنا معاجزين أولئك في العذاب محضرون', 22, 44, 432, 173, '34:38'),
(3645, NULL, NULL, 34, 39, 'قُلْ إِنَّ رَبِّى يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ مِنْ عِبَادِهِۦ وَيَقْدِرُ لَهُۥ وَمَآ أَنفَقْتُم مِّن شَىْءٍ فَهُوَ يُخْلِفُهُۥ وَهُوَ خَيْرُ ٱلرَّٰزِقِينَ', 'قل إن ربي يبسط الرزق لمن يشاء من عباده ويقدر له وما أنفقتم من شيء فهو يخلفه وهو خير الرازقين', 22, 44, 432, 173, '34:39'),
(3646, NULL, NULL, 34, 40, 'وَيَوْمَ يَحْشُرُهُمْ جَمِيعًا ثُمَّ يَقُولُ لِلْمَلَٰٓئِكَةِ أَهَٰٓؤُلَآءِ إِيَّاكُمْ كَانُوا۟ يَعْبُدُونَ', 'ويوم يحشرهم جميعا ثم يقول للملائكة أهؤلاء إياكم كانوا يعبدون', 22, 44, 433, 173, '34:40'),
(3647, NULL, NULL, 34, 41, 'قَالُوا۟ سُبْحَٰنَكَ أَنتَ وَلِيُّنَا مِن دُونِهِم بَلْ كَانُوا۟ يَعْبُدُونَ ٱلْجِنَّ أَكْثَرُهُم بِهِم مُّؤْمِنُونَ', 'قالوا سبحانك أنت ولينا من دونهم بل كانوا يعبدون الجن أكثرهم بهم مؤمنون', 22, 44, 433, 173, '34:41'),
(3648, NULL, NULL, 34, 42, 'فَٱلْيَوْمَ لَا يَمْلِكُ بَعْضُكُمْ لِبَعْضٍ نَّفْعًا وَلَا ضَرًّا وَنَقُولُ لِلَّذِينَ ظَلَمُوا۟ ذُوقُوا۟ عَذَابَ ٱلنَّارِ ٱلَّتِى كُنتُم بِهَا تُكَذِّبُونَ', 'فاليوم لا يملك بعضكم لبعض نفعا ولا ضرا ونقول للذين ظلموا ذوقوا عذاب النار التي كنتم بها تكذبون', 22, 44, 433, 173, '34:42'),
(3649, NULL, NULL, 34, 43, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَٰتُنَا بَيِّنَٰتٍ قَالُوا۟ مَا هَٰذَآ إِلَّا رَجُلٌ يُرِيدُ أَن يَصُدَّكُمْ عَمَّا كَانَ يَعْبُدُ ءَابَآؤُكُمْ وَقَالُوا۟ مَا هَٰذَآ إِلَّآ إِفْكٌ مُّفْتَرًى وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لِلْحَقِّ لَمَّا جَآءَهُمْ إِنْ هَٰذَآ إِلَّا سِحْرٌ مُّبِينٌ', 'وإذا تتلى عليهم آياتنا بينات قالوا ما هذا إلا رجل يريد أن يصدكم عما كان يعبد آباؤكم وقالوا ما هذا إلا إفك مفترى وقال الذين كفروا للحق لما جاءهم إن هذا إلا سحر مبين', 22, 44, 433, 173, '34:43'),
(3650, NULL, NULL, 34, 44, 'وَمَآ ءَاتَيْنَٰهُم مِّن كُتُبٍ يَدْرُسُونَهَا وَمَآ أَرْسَلْنَآ إِلَيْهِمْ قَبْلَكَ مِن نَّذِيرٍ', 'وما آتيناهم من كتب يدرسونها وما أرسلنا إليهم قبلك من نذير', 22, 44, 433, 173, '34:44'),
(3651, NULL, NULL, 34, 45, 'وَكَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ وَمَا بَلَغُوا۟ مِعْشَارَ مَآ ءَاتَيْنَٰهُمْ فَكَذَّبُوا۟ رُسُلِى فَكَيْفَ كَانَ نَكِيرِ', 'وكذب الذين من قبلهم وما بلغوا معشار ما آتيناهم فكذبوا رسلي فكيف كان نكير', 22, 44, 433, 173, '34:45'),
(3652, NULL, NULL, 34, 46, 'قُلْ إِنَّمَآ أَعِظُكُم بِوَٰحِدَةٍ أَن تَقُومُوا۟ لِلَّهِ مَثْنَىٰ وَفُرَٰدَىٰ ثُمَّ تَتَفَكَّرُوا۟ مَا بِصَاحِبِكُم مِّن جِنَّةٍ إِنْ هُوَ إِلَّا نَذِيرٌ لَّكُم بَيْنَ يَدَىْ عَذَابٍ شَدِيدٍ', 'قل إنما أعظكم بواحدة أن تقوموا لله مثنى وفرادى ثم تتفكروا ما بصاحبكم من جنة إن هو إلا نذير لكم بين يدي عذاب شديد', 22, 44, 433, 174, '34:46'),
(3653, NULL, NULL, 34, 47, 'قُلْ مَا سَأَلْتُكُم مِّنْ أَجْرٍ فَهُوَ لَكُمْ إِنْ أَجْرِىَ إِلَّا عَلَى ٱللَّهِ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ شَهِيدٌ', 'قل ما سألتكم من أجر فهو لكم إن أجري إلا على الله وهو على كل شيء شهيد', 22, 44, 433, 174, '34:47'),
(3654, NULL, NULL, 34, 48, 'قُلْ إِنَّ رَبِّى يَقْذِفُ بِٱلْحَقِّ عَلَّٰمُ ٱلْغُيُوبِ', 'قل إن ربي يقذف بالحق علام الغيوب', 22, 44, 433, 174, '34:48'),
(3655, NULL, NULL, 34, 49, 'قُلْ جَآءَ ٱلْحَقُّ وَمَا يُبْدِئُ ٱلْبَٰطِلُ وَمَا يُعِيدُ', 'قل جاء الحق وما يبدئ الباطل وما يعيد', 22, 44, 434, 174, '34:49'),
(3656, NULL, NULL, 34, 50, 'قُلْ إِن ضَلَلْتُ فَإِنَّمَآ أَضِلُّ عَلَىٰ نَفْسِى وَإِنِ ٱهْتَدَيْتُ فَبِمَا يُوحِىٓ إِلَىَّ رَبِّىٓ إِنَّهُۥ سَمِيعٌ قَرِيبٌ', 'قل إن ضللت فإنما أضل على نفسي وإن اهتديت فبما يوحي إلي ربي إنه سميع قريب', 22, 44, 434, 174, '34:50'),
(3657, NULL, NULL, 34, 51, 'وَلَوْ تَرَىٰٓ إِذْ فَزِعُوا۟ فَلَا فَوْتَ وَأُخِذُوا۟ مِن مَّكَانٍ قَرِيبٍ', 'ولو ترى إذ فزعوا فلا فوت وأخذوا من مكان قريب', 22, 44, 434, 174, '34:51'),
(3658, NULL, NULL, 34, 52, 'وَقَالُوٓا۟ ءَامَنَّا بِهِۦ وَأَنَّىٰ لَهُمُ ٱلتَّنَاوُشُ مِن مَّكَانٍۭ بَعِيدٍ', 'وقالوا آمنا به وأنى لهم التناوش من مكان بعيد', 22, 44, 434, 174, '34:52'),
(3659, NULL, NULL, 34, 53, 'وَقَدْ كَفَرُوا۟ بِهِۦ مِن قَبْلُ وَيَقْذِفُونَ بِٱلْغَيْبِ مِن مَّكَانٍۭ بَعِيدٍ', 'وقد كفروا به من قبل ويقذفون بالغيب من مكان بعيد', 22, 44, 434, 174, '34:53'),
(3660, NULL, NULL, 34, 54, 'وَحِيلَ بَيْنَهُمْ وَبَيْنَ مَا يَشْتَهُونَ كَمَا فُعِلَ بِأَشْيَاعِهِم مِّن قَبْلُ إِنَّهُمْ كَانُوا۟ فِى شَكٍّ مُّرِيبٍۭ', 'وحيل بينهم وبين ما يشتهون كما فعل بأشياعهم من قبل إنهم كانوا في شك مريب', 22, 44, 434, 174, '34:54'),
(3661, NULL, NULL, 35, 1, 'ٱلْحَمْدُ لِلَّهِ فَاطِرِ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ جَاعِلِ ٱلْمَلَٰٓئِكَةِ رُسُلًا أُو۟لِىٓ أَجْنِحَةٍ مَّثْنَىٰ وَثُلَٰثَ وَرُبَٰعَ يَزِيدُ فِى ٱلْخَلْقِ مَا يَشَآءُ إِنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'الحمد لله فاطر السماوات والأرض جاعل الملائكة رسلا أولي أجنحة مثنى وثلاث ورباع يزيد في الخلق ما يشاء إن الله على كل شيء قدير', 22, 44, 434, 174, '35:1'),
(3662, NULL, NULL, 35, 2, 'مَّا يَفْتَحِ ٱللَّهُ لِلنَّاسِ مِن رَّحْمَةٍ فَلَا مُمْسِكَ لَهَا وَمَا يُمْسِكْ فَلَا مُرْسِلَ لَهُۥ مِنۢ بَعْدِهِۦ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'ما يفتح الله للناس من رحمة فلا ممسك لها وما يمسك فلا مرسل له من بعده وهو العزيز الحكيم', 22, 44, 434, 174, '35:2'),
(3663, NULL, NULL, 35, 3, 'يَٰٓأَيُّهَا ٱلنَّاسُ ٱذْكُرُوا۟ نِعْمَتَ ٱللَّهِ عَلَيْكُمْ هَلْ مِنْ خَٰلِقٍ غَيْرُ ٱللَّهِ يَرْزُقُكُم مِّنَ ٱلسَّمَآءِ وَٱلْأَرْضِ لَآ إِلَٰهَ إِلَّا هُوَ فَأَنَّىٰ تُؤْفَكُونَ', 'يا أيها الناس اذكروا نعمت الله عليكم هل من خالق غير الله يرزقكم من السماء والأرض لا إله إلا هو فأنى تؤفكون', 22, 44, 434, 174, '35:3'),
(3664, NULL, NULL, 35, 4, 'وَإِن يُكَذِّبُوكَ فَقَدْ كُذِّبَتْ رُسُلٌ مِّن قَبْلِكَ وَإِلَى ٱللَّهِ تُرْجَعُ ٱلْأُمُورُ', 'وإن يكذبوك فقد كذبت رسل من قبلك وإلى الله ترجع الأمور', 22, 44, 435, 174, '35:4'),
(3665, NULL, NULL, 35, 5, 'يَٰٓأَيُّهَا ٱلنَّاسُ إِنَّ وَعْدَ ٱللَّهِ حَقٌّ فَلَا تَغُرَّنَّكُمُ ٱلْحَيَوٰةُ ٱلدُّنْيَا وَلَا يَغُرَّنَّكُم بِٱللَّهِ ٱلْغَرُورُ', 'يا أيها الناس إن وعد الله حق فلا تغرنكم الحياة الدنيا ولا يغرنكم بالله الغرور', 22, 44, 435, 174, '35:5'),
(3666, NULL, NULL, 35, 6, 'إِنَّ ٱلشَّيْطَٰنَ لَكُمْ عَدُوٌّ فَٱتَّخِذُوهُ عَدُوًّا إِنَّمَا يَدْعُوا۟ حِزْبَهُۥ لِيَكُونُوا۟ مِنْ أَصْحَٰبِ ٱلسَّعِيرِ', 'إن الشيطان لكم عدو فاتخذوه عدوا إنما يدعو حزبه ليكونوا من أصحاب السعير', 22, 44, 435, 174, '35:6'),
(3667, NULL, NULL, 35, 7, 'ٱلَّذِينَ كَفَرُوا۟ لَهُمْ عَذَابٌ شَدِيدٌ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُم مَّغْفِرَةٌ وَأَجْرٌ كَبِيرٌ', 'الذين كفروا لهم عذاب شديد والذين آمنوا وعملوا الصالحات لهم مغفرة وأجر كبير', 22, 44, 435, 174, '35:7'),
(3668, NULL, NULL, 35, 8, 'أَفَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ فَرَءَاهُ حَسَنًا فَإِنَّ ٱللَّهَ يُضِلُّ مَن يَشَآءُ وَيَهْدِى مَن يَشَآءُ فَلَا تَذْهَبْ نَفْسُكَ عَلَيْهِمْ حَسَرَٰتٍ إِنَّ ٱللَّهَ عَلِيمٌۢ بِمَا يَصْنَعُونَ', 'أفمن زين له سوء عمله فرآه حسنا فإن الله يضل من يشاء ويهدي من يشاء فلا تذهب نفسك عليهم حسرات إن الله عليم بما يصنعون', 22, 44, 435, 174, '35:8'),
(3669, NULL, NULL, 35, 9, 'وَٱللَّهُ ٱلَّذِىٓ أَرْسَلَ ٱلرِّيَٰحَ فَتُثِيرُ سَحَابًا فَسُقْنَٰهُ إِلَىٰ بَلَدٍ مَّيِّتٍ فَأَحْيَيْنَا بِهِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا كَذَٰلِكَ ٱلنُّشُورُ', 'والله الذي أرسل الرياح فتثير سحابا فسقناه إلى بلد ميت فأحيينا به الأرض بعد موتها كذلك النشور', 22, 44, 435, 174, '35:9'),
(3670, NULL, NULL, 35, 10, 'مَن كَانَ يُرِيدُ ٱلْعِزَّةَ فَلِلَّهِ ٱلْعِزَّةُ جَمِيعًا إِلَيْهِ يَصْعَدُ ٱلْكَلِمُ ٱلطَّيِّبُ وَٱلْعَمَلُ ٱلصَّٰلِحُ يَرْفَعُهُۥ وَٱلَّذِينَ يَمْكُرُونَ ٱلسَّيِّـَٔاتِ لَهُمْ عَذَابٌ شَدِيدٌ وَمَكْرُ أُو۟لَٰٓئِكَ هُوَ يَبُورُ', 'من كان يريد العزة فلله العزة جميعا إليه يصعد الكلم الطيب والعمل الصالح يرفعه والذين يمكرون السيئات لهم عذاب شديد ومكر أولئك هو يبور', 22, 44, 435, 174, '35:10'),
(3671, NULL, NULL, 35, 11, 'وَٱللَّهُ خَلَقَكُم مِّن تُرَابٍ ثُمَّ مِن نُّطْفَةٍ ثُمَّ جَعَلَكُمْ أَزْوَٰجًا وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِۦ وَمَا يُعَمَّرُ مِن مُّعَمَّرٍ وَلَا يُنقَصُ مِنْ عُمُرِهِۦٓ إِلَّا فِى كِتَٰبٍ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌ', 'والله خلقكم من تراب ثم من نطفة ثم جعلكم أزواجا وما تحمل من أنثى ولا تضع إلا بعلمه وما يعمر من معمر ولا ينقص من عمره إلا في كتاب إن ذلك على الله يسير', 22, 44, 435, 174, '35:11'),
(3672, NULL, NULL, 35, 12, 'وَمَا يَسْتَوِى ٱلْبَحْرَانِ هَٰذَا عَذْبٌ فُرَاتٌ سَآئِغٌ شَرَابُهُۥ وَهَٰذَا مِلْحٌ أُجَاجٌ وَمِن كُلٍّ تَأْكُلُونَ لَحْمًا طَرِيًّا وَتَسْتَخْرِجُونَ حِلْيَةً تَلْبَسُونَهَا وَتَرَى ٱلْفُلْكَ فِيهِ مَوَاخِرَ لِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ', 'وما يستوي البحران هذا عذب فرات سائغ شرابه وهذا ملح أجاج ومن كل تأكلون لحما طريا وتستخرجون حلية تلبسونها وترى الفلك فيه مواخر لتبتغوا من فضله ولعلكم تشكرون', 22, 44, 436, 174, '35:12'),
(3673, NULL, NULL, 35, 13, 'يُولِجُ ٱلَّيْلَ فِى ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِى ٱلَّيْلِ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ كُلٌّ يَجْرِى لِأَجَلٍ مُّسَمًّى ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ لَهُ ٱلْمُلْكُ وَٱلَّذِينَ تَدْعُونَ مِن دُونِهِۦ مَا يَمْلِكُونَ مِن قِطْمِيرٍ', 'يولج الليل في النهار ويولج النهار في الليل وسخر الشمس والقمر كل يجري لأجل مسمى ذلكم الله ربكم له الملك والذين تدعون من دونه ما يملكون من قطمير', 22, 44, 436, 174, '35:13'),
(3674, NULL, NULL, 35, 14, 'إِن تَدْعُوهُمْ لَا يَسْمَعُوا۟ دُعَآءَكُمْ وَلَوْ سَمِعُوا۟ مَا ٱسْتَجَابُوا۟ لَكُمْ وَيَوْمَ ٱلْقِيَٰمَةِ يَكْفُرُونَ بِشِرْكِكُمْ وَلَا يُنَبِّئُكَ مِثْلُ خَبِيرٍ', 'إن تدعوهم لا يسمعوا دعاءكم ولو سمعوا ما استجابوا لكم ويوم القيامة يكفرون بشرككم ولا ينبئك مثل خبير', 22, 44, 436, 174, '35:14'),
(3675, NULL, NULL, 35, 15, 'يَٰٓأَيُّهَا ٱلنَّاسُ أَنتُمُ ٱلْفُقَرَآءُ إِلَى ٱللَّهِ وَٱللَّهُ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ', 'يا أيها الناس أنتم الفقراء إلى الله والله هو الغني الحميد', 22, 44, 436, 175, '35:15'),
(3676, NULL, NULL, 35, 16, 'إِن يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ', 'إن يشأ يذهبكم ويأت بخلق جديد', 22, 44, 436, 175, '35:16'),
(3677, NULL, NULL, 35, 17, 'وَمَا ذَٰلِكَ عَلَى ٱللَّهِ بِعَزِيزٍ', 'وما ذلك على الله بعزيز', 22, 44, 436, 175, '35:17'),
(3678, NULL, NULL, 35, 18, 'وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ وَإِن تَدْعُ مُثْقَلَةٌ إِلَىٰ حِمْلِهَا لَا يُحْمَلْ مِنْهُ شَىْءٌ وَلَوْ كَانَ ذَا قُرْبَىٰٓ إِنَّمَا تُنذِرُ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُم بِٱلْغَيْبِ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَمَن تَزَكَّىٰ فَإِنَّمَا يَتَزَكَّىٰ لِنَفْسِهِۦ وَإِلَى ٱللَّهِ ٱلْمَصِيرُ', 'ولا تزر وازرة وزر أخرى وإن تدع مثقلة إلى حملها لا يحمل منه شيء ولو كان ذا قربى إنما تنذر الذين يخشون ربهم بالغيب وأقاموا الصلاة ومن تزكى فإنما يتزكى لنفسه وإلى الله المصير', 22, 44, 436, 175, '35:18'),
(3679, NULL, NULL, 35, 19, 'وَمَا يَسْتَوِى ٱلْأَعْمَىٰ وَٱلْبَصِيرُ', 'وما يستوي الأعمى والبصير', 22, 44, 437, 175, '35:19'),
(3680, NULL, NULL, 35, 20, 'وَلَا ٱلظُّلُمَٰتُ وَلَا ٱلنُّورُ', 'ولا الظلمات ولا النور', 22, 44, 437, 175, '35:20'),
(3681, NULL, NULL, 35, 21, 'وَلَا ٱلظِّلُّ وَلَا ٱلْحَرُورُ', 'ولا الظل ولا الحرور', 22, 44, 437, 175, '35:21'),
(3682, NULL, NULL, 35, 22, 'وَمَا يَسْتَوِى ٱلْأَحْيَآءُ وَلَا ٱلْأَمْوَٰتُ إِنَّ ٱللَّهَ يُسْمِعُ مَن يَشَآءُ وَمَآ أَنتَ بِمُسْمِعٍ مَّن فِى ٱلْقُبُورِ', 'وما يستوي الأحياء ولا الأموات إن الله يسمع من يشاء وما أنت بمسمع من في القبور', 22, 44, 437, 175, '35:22'),
(3683, NULL, NULL, 35, 23, 'إِنْ أَنتَ إِلَّا نَذِيرٌ', 'إن أنت إلا نذير', 22, 44, 437, 175, '35:23'),
(3684, NULL, NULL, 35, 24, 'إِنَّآ أَرْسَلْنَٰكَ بِٱلْحَقِّ بَشِيرًا وَنَذِيرًا وَإِن مِّنْ أُمَّةٍ إِلَّا خَلَا فِيهَا نَذِيرٌ', 'إنا أرسلناك بالحق بشيرا ونذيرا وإن من أمة إلا خلا فيها نذير', 22, 44, 437, 175, '35:24'),
(3685, NULL, NULL, 35, 25, 'وَإِن يُكَذِّبُوكَ فَقَدْ كَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ جَآءَتْهُمْ رُسُلُهُم بِٱلْبَيِّنَٰتِ وَبِٱلزُّبُرِ وَبِٱلْكِتَٰبِ ٱلْمُنِيرِ', 'وإن يكذبوك فقد كذب الذين من قبلهم جاءتهم رسلهم بالبينات وبالزبر وبالكتاب المنير', 22, 44, 437, 175, '35:25'),
(3686, NULL, NULL, 35, 26, 'ثُمَّ أَخَذْتُ ٱلَّذِينَ كَفَرُوا۟ فَكَيْفَ كَانَ نَكِيرِ', 'ثم أخذت الذين كفروا فكيف كان نكير', 22, 44, 437, 175, '35:26'),
(3687, NULL, NULL, 35, 27, 'أَلَمْ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءً فَأَخْرَجْنَا بِهِۦ ثَمَرَٰتٍ مُّخْتَلِفًا أَلْوَٰنُهَا وَمِنَ ٱلْجِبَالِ جُدَدٌۢ بِيضٌ وَحُمْرٌ مُّخْتَلِفٌ أَلْوَٰنُهَا وَغَرَابِيبُ سُودٌ', 'ألم تر أن الله أنزل من السماء ماء فأخرجنا به ثمرات مختلفا ألوانها ومن الجبال جدد بيض وحمر مختلف ألوانها وغرابيب سود', 22, 44, 437, 175, '35:27'),
(3688, NULL, NULL, 35, 28, 'وَمِنَ ٱلنَّاسِ وَٱلدَّوَآبِّ وَٱلْأَنْعَٰمِ مُخْتَلِفٌ أَلْوَٰنُهُۥ كَذَٰلِكَ إِنَّمَا يَخْشَى ٱللَّهَ مِنْ عِبَادِهِ ٱلْعُلَمَٰٓؤُا۟ إِنَّ ٱللَّهَ عَزِيزٌ غَفُورٌ', 'ومن الناس والدواب والأنعام مختلف ألوانه كذلك إنما يخشى الله من عباده العلماء إن الله عزيز غفور', 22, 44, 437, 175, '35:28'),
(3689, NULL, NULL, 35, 29, 'إِنَّ ٱلَّذِينَ يَتْلُونَ كِتَٰبَ ٱللَّهِ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَنفَقُوا۟ مِمَّا رَزَقْنَٰهُمْ سِرًّا وَعَلَانِيَةً يَرْجُونَ تِجَٰرَةً لَّن تَبُورَ', 'إن الذين يتلون كتاب الله وأقاموا الصلاة وأنفقوا مما رزقناهم سرا وعلانية يرجون تجارة لن تبور', 22, 44, 437, 175, '35:29'),
(3690, NULL, NULL, 35, 30, 'لِيُوَفِّيَهُمْ أُجُورَهُمْ وَيَزِيدَهُم مِّن فَضْلِهِۦٓ إِنَّهُۥ غَفُورٌ شَكُورٌ', 'ليوفيهم أجورهم ويزيدهم من فضله إنه غفور شكور', 22, 44, 437, 175, '35:30'),
(3691, NULL, NULL, 35, 31, 'وَٱلَّذِىٓ أَوْحَيْنَآ إِلَيْكَ مِنَ ٱلْكِتَٰبِ هُوَ ٱلْحَقُّ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ إِنَّ ٱللَّهَ بِعِبَادِهِۦ لَخَبِيرٌۢ بَصِيرٌ', 'والذي أوحينا إليك من الكتاب هو الحق مصدقا لما بين يديه إن الله بعباده لخبير بصير', 22, 44, 438, 175, '35:31'),
(3692, NULL, NULL, 35, 32, 'ثُمَّ أَوْرَثْنَا ٱلْكِتَٰبَ ٱلَّذِينَ ٱصْطَفَيْنَا مِنْ عِبَادِنَا فَمِنْهُمْ ظَالِمٌ لِّنَفْسِهِۦ وَمِنْهُم مُّقْتَصِدٌ وَمِنْهُمْ سَابِقٌۢ بِٱلْخَيْرَٰتِ بِإِذْنِ ٱللَّهِ ذَٰلِكَ هُوَ ٱلْفَضْلُ ٱلْكَبِيرُ', 'ثم أورثنا الكتاب الذين اصطفينا من عبادنا فمنهم ظالم لنفسه ومنهم مقتصد ومنهم سابق بالخيرات بإذن الله ذلك هو الفضل الكبير', 22, 44, 438, 175, '35:32'),
(3693, NULL, NULL, 35, 33, 'جَنَّٰتُ عَدْنٍ يَدْخُلُونَهَا يُحَلَّوْنَ فِيهَا مِنْ أَسَاوِرَ مِن ذَهَبٍ وَلُؤْلُؤًا وَلِبَاسُهُمْ فِيهَا حَرِيرٌ', 'جنات عدن يدخلونها يحلون فيها من أساور من ذهب ولؤلؤا ولباسهم فيها حرير', 22, 44, 438, 175, '35:33'),
(3694, NULL, NULL, 35, 34, 'وَقَالُوا۟ ٱلْحَمْدُ لِلَّهِ ٱلَّذِىٓ أَذْهَبَ عَنَّا ٱلْحَزَنَ إِنَّ رَبَّنَا لَغَفُورٌ شَكُورٌ', 'وقالوا الحمد لله الذي أذهب عنا الحزن إن ربنا لغفور شكور', 22, 44, 438, 175, '35:34'),
(3695, NULL, NULL, 35, 35, 'ٱلَّذِىٓ أَحَلَّنَا دَارَ ٱلْمُقَامَةِ مِن فَضْلِهِۦ لَا يَمَسُّنَا فِيهَا نَصَبٌ وَلَا يَمَسُّنَا فِيهَا لُغُوبٌ', 'الذي أحلنا دار المقامة من فضله لا يمسنا فيها نصب ولا يمسنا فيها لغوب', 22, 44, 438, 175, '35:35'),
(3696, NULL, NULL, 35, 36, 'وَٱلَّذِينَ كَفَرُوا۟ لَهُمْ نَارُ جَهَنَّمَ لَا يُقْضَىٰ عَلَيْهِمْ فَيَمُوتُوا۟ وَلَا يُخَفَّفُ عَنْهُم مِّنْ عَذَابِهَا كَذَٰلِكَ نَجْزِى كُلَّ كَفُورٍ', 'والذين كفروا لهم نار جهنم لا يقضى عليهم فيموتوا ولا يخفف عنهم من عذابها كذلك نجزي كل كفور', 22, 44, 438, 175, '35:36'),
(3697, NULL, NULL, 35, 37, 'وَهُمْ يَصْطَرِخُونَ فِيهَا رَبَّنَآ أَخْرِجْنَا نَعْمَلْ صَٰلِحًا غَيْرَ ٱلَّذِى كُنَّا نَعْمَلُ أَوَلَمْ نُعَمِّرْكُم مَّا يَتَذَكَّرُ فِيهِ مَن تَذَكَّرَ وَجَآءَكُمُ ٱلنَّذِيرُ فَذُوقُوا۟ فَمَا لِلظَّٰلِمِينَ مِن نَّصِيرٍ', 'وهم يصطرخون فيها ربنا أخرجنا نعمل صالحا غير الذي كنا نعمل أولم نعمركم ما يتذكر فيه من تذكر وجاءكم النذير فذوقوا فما للظالمين من نصير', 22, 44, 438, 175, '35:37'),
(3698, NULL, NULL, 35, 38, 'إِنَّ ٱللَّهَ عَٰلِمُ غَيْبِ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ', 'إن الله عالم غيب السماوات والأرض إنه عليم بذات الصدور', 22, 44, 438, 175, '35:38'),
(3699, NULL, NULL, 35, 39, 'هُوَ ٱلَّذِى جَعَلَكُمْ خَلَٰٓئِفَ فِى ٱلْأَرْضِ فَمَن كَفَرَ فَعَلَيْهِ كُفْرُهُۥ وَلَا يَزِيدُ ٱلْكَٰفِرِينَ كُفْرُهُمْ عِندَ رَبِّهِمْ إِلَّا مَقْتًا وَلَا يَزِيدُ ٱلْكَٰفِرِينَ كُفْرُهُمْ إِلَّا خَسَارًا', 'هو الذي جعلكم خلائف في الأرض فمن كفر فعليه كفره ولا يزيد الكافرين كفرهم عند ربهم إلا مقتا ولا يزيد الكافرين كفرهم إلا خسارا', 22, 44, 439, 175, '35:39'),
(3700, NULL, NULL, 35, 40, 'قُلْ أَرَءَيْتُمْ شُرَكَآءَكُمُ ٱلَّذِينَ تَدْعُونَ مِن دُونِ ٱللَّهِ أَرُونِى مَاذَا خَلَقُوا۟ مِنَ ٱلْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِى ٱلسَّمَٰوَٰتِ أَمْ ءَاتَيْنَٰهُمْ كِتَٰبًا فَهُمْ عَلَىٰ بَيِّنَتٍ مِّنْهُ بَلْ إِن يَعِدُ ٱلظَّٰلِمُونَ بَعْضُهُم بَعْضًا إِلَّا غُرُورًا', 'قل أرأيتم شركاءكم الذين تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات أم آتيناهم كتابا فهم على بينت منه بل إن يعد الظالمون بعضهم بعضا إلا غرورا', 22, 44, 439, 175, '35:40'),
(3701, NULL, NULL, 35, 41, 'إِنَّ ٱللَّهَ يُمْسِكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ أَن تَزُولَا وَلَئِن زَالَتَآ إِنْ أَمْسَكَهُمَا مِنْ أَحَدٍ مِّنۢ بَعْدِهِۦٓ إِنَّهُۥ كَانَ حَلِيمًا غَفُورًا', 'إن الله يمسك السماوات والأرض أن تزولا ولئن زالتا إن أمسكهما من أحد من بعده إنه كان حليما غفورا', 22, 44, 439, 176, '35:41'),
(3702, NULL, NULL, 35, 42, 'وَأَقْسَمُوا۟ بِٱللَّهِ جَهْدَ أَيْمَٰنِهِمْ لَئِن جَآءَهُمْ نَذِيرٌ لَّيَكُونُنَّ أَهْدَىٰ مِنْ إِحْدَى ٱلْأُمَمِ فَلَمَّا جَآءَهُمْ نَذِيرٌ مَّا زَادَهُمْ إِلَّا نُفُورًا', 'وأقسموا بالله جهد أيمانهم لئن جاءهم نذير ليكونن أهدى من إحدى الأمم فلما جاءهم نذير ما زادهم إلا نفورا', 22, 44, 439, 176, '35:42'),
(3703, NULL, NULL, 35, 43, 'ٱسْتِكْبَارًا فِى ٱلْأَرْضِ وَمَكْرَ ٱلسَّيِّئِ وَلَا يَحِيقُ ٱلْمَكْرُ ٱلسَّيِّئُ إِلَّا بِأَهْلِهِۦ فَهَلْ يَنظُرُونَ إِلَّا سُنَّتَ ٱلْأَوَّلِينَ فَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَبْدِيلًا وَلَن تَجِدَ لِسُنَّتِ ٱللَّهِ تَحْوِيلًا', 'استكبارا في الأرض ومكر السيئ ولا يحيق المكر السيئ إلا بأهله فهل ينظرون إلا سنت الأولين فلن تجد لسنت الله تبديلا ولن تجد لسنت الله تحويلا', 22, 44, 439, 176, '35:43'),
(3704, NULL, NULL, 35, 44, 'أَوَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ وَكَانُوٓا۟ أَشَدَّ مِنْهُمْ قُوَّةً وَمَا كَانَ ٱللَّهُ لِيُعْجِزَهُۥ مِن شَىْءٍ فِى ٱلسَّمَٰوَٰتِ وَلَا فِى ٱلْأَرْضِ إِنَّهُۥ كَانَ عَلِيمًا قَدِيرًا', 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم وكانوا أشد منهم قوة وما كان الله ليعجزه من شيء في السماوات ولا في الأرض إنه كان عليما قديرا', 22, 44, 439, 176, '35:44'),
(3705, NULL, NULL, 35, 45, 'وَلَوْ يُؤَاخِذُ ٱللَّهُ ٱلنَّاسَ بِمَا كَسَبُوا۟ مَا تَرَكَ عَلَىٰ ظَهْرِهَا مِن دَآبَّةٍ وَلَٰكِن يُؤَخِّرُهُمْ إِلَىٰٓ أَجَلٍ مُّسَمًّى فَإِذَا جَآءَ أَجَلُهُمْ فَإِنَّ ٱللَّهَ كَانَ بِعِبَادِهِۦ بَصِيرًۢا', 'ولو يؤاخذ الله الناس بما كسبوا ما ترك على ظهرها من دابة ولكن يؤخرهم إلى أجل مسمى فإذا جاء أجلهم فإن الله كان بعباده بصيرا', 22, 44, 440, 176, '35:45'),
(3706, NULL, NULL, 36, 1, 'يسٓ', 'يس', 22, 44, 440, 176, '36:1'),
(3707, NULL, NULL, 36, 2, 'وَٱلْقُرْءَانِ ٱلْحَكِيمِ', 'والقرآن الحكيم', 22, 44, 440, 176, '36:2'),
(3708, NULL, NULL, 36, 3, 'إِنَّكَ لَمِنَ ٱلْمُرْسَلِينَ', 'إنك لمن المرسلين', 22, 44, 440, 176, '36:3'),
(3709, NULL, NULL, 36, 4, 'عَلَىٰ صِرَٰطٍ مُّسْتَقِيمٍ', 'على صراط مستقيم', 22, 44, 440, 176, '36:4'),
(3710, NULL, NULL, 36, 5, 'تَنزِيلَ ٱلْعَزِيزِ ٱلرَّحِيمِ', 'تنزيل العزيز الرحيم', 22, 44, 440, 176, '36:5'),
(3711, NULL, NULL, 36, 6, 'لِتُنذِرَ قَوْمًا مَّآ أُنذِرَ ءَابَآؤُهُمْ فَهُمْ غَٰفِلُونَ', 'لتنذر قوما ما أنذر آباؤهم فهم غافلون', 22, 44, 440, 176, '36:6'),
(3712, NULL, NULL, 36, 7, 'لَقَدْ حَقَّ ٱلْقَوْلُ عَلَىٰٓ أَكْثَرِهِمْ فَهُمْ لَا يُؤْمِنُونَ', 'لقد حق القول على أكثرهم فهم لا يؤمنون', 22, 44, 440, 176, '36:7'),
(3713, NULL, NULL, 36, 8, 'إِنَّا جَعَلْنَا فِىٓ أَعْنَٰقِهِمْ أَغْلَٰلًا فَهِىَ إِلَى ٱلْأَذْقَانِ فَهُم مُّقْمَحُونَ', 'إنا جعلنا في أعناقهم أغلالا فهي إلى الأذقان فهم مقمحون', 22, 44, 440, 176, '36:8'),
(3714, NULL, NULL, 36, 9, 'وَجَعَلْنَا مِنۢ بَيْنِ أَيْدِيهِمْ سَدًّا وَمِنْ خَلْفِهِمْ سَدًّا فَأَغْشَيْنَٰهُمْ فَهُمْ لَا يُبْصِرُونَ', 'وجعلنا من بين أيديهم سدا ومن خلفهم سدا فأغشيناهم فهم لا يبصرون', 22, 44, 440, 176, '36:9'),
(3715, NULL, NULL, 36, 10, 'وَسَوَآءٌ عَلَيْهِمْ ءَأَنذَرْتَهُمْ أَمْ لَمْ تُنذِرْهُمْ لَا يُؤْمِنُونَ', 'وسواء عليهم أأنذرتهم أم لم تنذرهم لا يؤمنون', 22, 44, 440, 176, '36:10'),
(3716, NULL, NULL, 36, 11, 'إِنَّمَا تُنذِرُ مَنِ ٱتَّبَعَ ٱلذِّكْرَ وَخَشِىَ ٱلرَّحْمَٰنَ بِٱلْغَيْبِ فَبَشِّرْهُ بِمَغْفِرَةٍ وَأَجْرٍ كَرِيمٍ', 'إنما تنذر من اتبع الذكر وخشي الرحمن بالغيب فبشره بمغفرة وأجر كريم', 22, 44, 440, 176, '36:11'),
(3717, NULL, NULL, 36, 12, 'إِنَّا نَحْنُ نُحْىِ ٱلْمَوْتَىٰ وَنَكْتُبُ مَا قَدَّمُوا۟ وَءَاثَٰرَهُمْ وَكُلَّ شَىْءٍ أَحْصَيْنَٰهُ فِىٓ إِمَامٍ مُّبِينٍ', 'إنا نحن نحيي الموتى ونكتب ما قدموا وآثارهم وكل شيء أحصيناه في إمام مبين', 22, 44, 440, 176, '36:12'),
(3718, NULL, NULL, 36, 13, 'وَٱضْرِبْ لَهُم مَّثَلًا أَصْحَٰبَ ٱلْقَرْيَةِ إِذْ جَآءَهَا ٱلْمُرْسَلُونَ', 'واضرب لهم مثلا أصحاب القرية إذ جاءها المرسلون', 22, 44, 441, 176, '36:13'),
(3719, NULL, NULL, 36, 14, 'إِذْ أَرْسَلْنَآ إِلَيْهِمُ ٱثْنَيْنِ فَكَذَّبُوهُمَا فَعَزَّزْنَا بِثَالِثٍ فَقَالُوٓا۟ إِنَّآ إِلَيْكُم مُّرْسَلُونَ', 'إذ أرسلنا إليهم اثنين فكذبوهما فعززنا بثالث فقالوا إنا إليكم مرسلون', 22, 44, 441, 176, '36:14'),
(3720, NULL, NULL, 36, 15, 'قَالُوا۟ مَآ أَنتُمْ إِلَّا بَشَرٌ مِّثْلُنَا وَمَآ أَنزَلَ ٱلرَّحْمَٰنُ مِن شَىْءٍ إِنْ أَنتُمْ إِلَّا تَكْذِبُونَ', 'قالوا ما أنتم إلا بشر مثلنا وما أنزل الرحمن من شيء إن أنتم إلا تكذبون', 22, 44, 441, 176, '36:15'),
(3721, NULL, NULL, 36, 16, 'قَالُوا۟ رَبُّنَا يَعْلَمُ إِنَّآ إِلَيْكُمْ لَمُرْسَلُونَ', 'قالوا ربنا يعلم إنا إليكم لمرسلون', 22, 44, 441, 176, '36:16'),
(3722, NULL, NULL, 36, 17, 'وَمَا عَلَيْنَآ إِلَّا ٱلْبَلَٰغُ ٱلْمُبِينُ', 'وما علينا إلا البلاغ المبين', 22, 44, 441, 176, '36:17'),
(3723, NULL, NULL, 36, 18, 'قَالُوٓا۟ إِنَّا تَطَيَّرْنَا بِكُمْ لَئِن لَّمْ تَنتَهُوا۟ لَنَرْجُمَنَّكُمْ وَلَيَمَسَّنَّكُم مِّنَّا عَذَابٌ أَلِيمٌ', 'قالوا إنا تطيرنا بكم لئن لم تنتهوا لنرجمنكم وليمسنكم منا عذاب أليم', 22, 44, 441, 176, '36:18'),
(3724, NULL, NULL, 36, 19, 'قَالُوا۟ طَٰٓئِرُكُم مَّعَكُمْ أَئِن ذُكِّرْتُم بَلْ أَنتُمْ قَوْمٌ مُّسْرِفُونَ', 'قالوا طائركم معكم أئن ذكرتم بل أنتم قوم مسرفون', 22, 44, 441, 176, '36:19'),
(3725, NULL, NULL, 36, 20, 'وَجَآءَ مِنْ أَقْصَا ٱلْمَدِينَةِ رَجُلٌ يَسْعَىٰ قَالَ يَٰقَوْمِ ٱتَّبِعُوا۟ ٱلْمُرْسَلِينَ', 'وجاء من أقصى المدينة رجل يسعى قال يا قوم اتبعوا المرسلين', 22, 44, 441, 176, '36:20'),
(3726, NULL, NULL, 36, 21, 'ٱتَّبِعُوا۟ مَن لَّا يَسْـَٔلُكُمْ أَجْرًا وَهُم مُّهْتَدُونَ', 'اتبعوا من لا يسألكم أجرا وهم مهتدون', 22, 44, 441, 176, '36:21'),
(3727, NULL, NULL, 36, 22, 'وَمَا لِىَ لَآ أَعْبُدُ ٱلَّذِى فَطَرَنِى وَإِلَيْهِ تُرْجَعُونَ', 'وما لي لا أعبد الذي فطرني وإليه ترجعون', 22, 44, 441, 176, '36:22'),
(3728, NULL, NULL, 36, 23, 'ءَأَتَّخِذُ مِن دُونِهِۦٓ ءَالِهَةً إِن يُرِدْنِ ٱلرَّحْمَٰنُ بِضُرٍّ لَّا تُغْنِ عَنِّى شَفَٰعَتُهُمْ شَيْـًٔا وَلَا يُنقِذُونِ', 'أأتخذ من دونه آلهة إن يردن الرحمن بضر لا تغن عني شفاعتهم شيئا ولا ينقذون', 22, 44, 441, 176, '36:23'),
(3729, NULL, NULL, 36, 24, 'إِنِّىٓ إِذًا لَّفِى ضَلَٰلٍ مُّبِينٍ', 'إني إذا لفي ضلال مبين', 22, 44, 441, 176, '36:24'),
(3730, NULL, NULL, 36, 25, 'إِنِّىٓ ءَامَنتُ بِرَبِّكُمْ فَٱسْمَعُونِ', 'إني آمنت بربكم فاسمعون', 22, 44, 441, 176, '36:25'),
(3731, NULL, NULL, 36, 26, 'قِيلَ ٱدْخُلِ ٱلْجَنَّةَ قَالَ يَٰلَيْتَ قَوْمِى يَعْلَمُونَ', 'قيل ادخل الجنة قال يا ليت قومي يعلمون', 22, 44, 441, 176, '36:26'),
(3732, NULL, NULL, 36, 27, 'بِمَا غَفَرَ لِى رَبِّى وَجَعَلَنِى مِنَ ٱلْمُكْرَمِينَ', 'بما غفر لي ربي وجعلني من المكرمين', 22, 44, 441, 176, '36:27'),
(3733, NULL, NULL, 36, 28, 'وَمَآ أَنزَلْنَا عَلَىٰ قَوْمِهِۦ مِنۢ بَعْدِهِۦ مِن جُندٍ مِّنَ ٱلسَّمَآءِ وَمَا كُنَّا مُنزِلِينَ', 'وما أنزلنا على قومه من بعده من جند من السماء وما كنا منزلين', 23, 45, 442, 177, '36:28'),
(3734, NULL, NULL, 36, 29, 'إِن كَانَتْ إِلَّا صَيْحَةً وَٰحِدَةً فَإِذَا هُمْ خَٰمِدُونَ', 'إن كانت إلا صيحة واحدة فإذا هم خامدون', 23, 45, 442, 177, '36:29'),
(3735, NULL, NULL, 36, 30, 'يَٰحَسْرَةً عَلَى ٱلْعِبَادِ مَا يَأْتِيهِم مِّن رَّسُولٍ إِلَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ', 'يا حسرة على العباد ما يأتيهم من رسول إلا كانوا به يستهزئون', 23, 45, 442, 177, '36:30'),
(3736, NULL, NULL, 36, 31, 'أَلَمْ يَرَوْا۟ كَمْ أَهْلَكْنَا قَبْلَهُم مِّنَ ٱلْقُرُونِ أَنَّهُمْ إِلَيْهِمْ لَا يَرْجِعُونَ', 'ألم يروا كم أهلكنا قبلهم من القرون أنهم إليهم لا يرجعون', 23, 45, 442, 177, '36:31'),
(3737, NULL, NULL, 36, 32, 'وَإِن كُلٌّ لَّمَّا جَمِيعٌ لَّدَيْنَا مُحْضَرُونَ', 'وإن كل لما جميع لدينا محضرون', 23, 45, 442, 177, '36:32'),
(3738, NULL, NULL, 36, 33, 'وَءَايَةٌ لَّهُمُ ٱلْأَرْضُ ٱلْمَيْتَةُ أَحْيَيْنَٰهَا وَأَخْرَجْنَا مِنْهَا حَبًّا فَمِنْهُ يَأْكُلُونَ', 'وآية لهم الأرض الميتة أحييناها وأخرجنا منها حبا فمنه يأكلون', 23, 45, 442, 177, '36:33'),
(3739, NULL, NULL, 36, 34, 'وَجَعَلْنَا فِيهَا جَنَّٰتٍ مِّن نَّخِيلٍ وَأَعْنَٰبٍ وَفَجَّرْنَا فِيهَا مِنَ ٱلْعُيُونِ', 'وجعلنا فيها جنات من نخيل وأعناب وفجرنا فيها من العيون', 23, 45, 442, 177, '36:34'),
(3740, NULL, NULL, 36, 35, 'لِيَأْكُلُوا۟ مِن ثَمَرِهِۦ وَمَا عَمِلَتْهُ أَيْدِيهِمْ أَفَلَا يَشْكُرُونَ', 'ليأكلوا من ثمره وما عملته أيديهم أفلا يشكرون', 23, 45, 442, 177, '36:35'),
(3741, NULL, NULL, 36, 36, 'سُبْحَٰنَ ٱلَّذِى خَلَقَ ٱلْأَزْوَٰجَ كُلَّهَا مِمَّا تُنۢبِتُ ٱلْأَرْضُ وَمِنْ أَنفُسِهِمْ وَمِمَّا لَا يَعْلَمُونَ', 'سبحان الذي خلق الأزواج كلها مما تنبت الأرض ومن أنفسهم ومما لا يعلمون', 23, 45, 442, 177, '36:36'),
(3742, NULL, NULL, 36, 37, 'وَءَايَةٌ لَّهُمُ ٱلَّيْلُ نَسْلَخُ مِنْهُ ٱلنَّهَارَ فَإِذَا هُم مُّظْلِمُونَ', 'وآية لهم الليل نسلخ منه النهار فإذا هم مظلمون', 23, 45, 442, 177, '36:37'),
(3743, NULL, NULL, 36, 38, 'وَٱلشَّمْسُ تَجْرِى لِمُسْتَقَرٍّ لَّهَا ذَٰلِكَ تَقْدِيرُ ٱلْعَزِيزِ ٱلْعَلِيمِ', 'والشمس تجري لمستقر لها ذلك تقدير العزيز العليم', 23, 45, 442, 177, '36:38'),
(3744, NULL, NULL, 36, 39, 'وَٱلْقَمَرَ قَدَّرْنَٰهُ مَنَازِلَ حَتَّىٰ عَادَ كَٱلْعُرْجُونِ ٱلْقَدِيمِ', 'والقمر قدرناه منازل حتى عاد كالعرجون القديم', 23, 45, 442, 177, '36:39'),
(3745, NULL, NULL, 36, 40, 'لَا ٱلشَّمْسُ يَنۢبَغِى لَهَآ أَن تُدْرِكَ ٱلْقَمَرَ وَلَا ٱلَّيْلُ سَابِقُ ٱلنَّهَارِ وَكُلٌّ فِى فَلَكٍ يَسْبَحُونَ', 'لا الشمس ينبغي لها أن تدرك القمر ولا الليل سابق النهار وكل في فلك يسبحون', 23, 45, 442, 177, '36:40'),
(3746, NULL, NULL, 36, 41, 'وَءَايَةٌ لَّهُمْ أَنَّا حَمَلْنَا ذُرِّيَّتَهُمْ فِى ٱلْفُلْكِ ٱلْمَشْحُونِ', 'وآية لهم أنا حملنا ذريتهم في الفلك المشحون', 23, 45, 443, 177, '36:41'),
(3747, NULL, NULL, 36, 42, 'وَخَلَقْنَا لَهُم مِّن مِّثْلِهِۦ مَا يَرْكَبُونَ', 'وخلقنا لهم من مثله ما يركبون', 23, 45, 443, 177, '36:42'),
(3748, NULL, NULL, 36, 43, 'وَإِن نَّشَأْ نُغْرِقْهُمْ فَلَا صَرِيخَ لَهُمْ وَلَا هُمْ يُنقَذُونَ', 'وإن نشأ نغرقهم فلا صريخ لهم ولا هم ينقذون', 23, 45, 443, 177, '36:43'),
(3749, NULL, NULL, 36, 44, 'إِلَّا رَحْمَةً مِّنَّا وَمَتَٰعًا إِلَىٰ حِينٍ', 'إلا رحمة منا ومتاعا إلى حين', 23, 45, 443, 177, '36:44'),
(3750, NULL, NULL, 36, 45, 'وَإِذَا قِيلَ لَهُمُ ٱتَّقُوا۟ مَا بَيْنَ أَيْدِيكُمْ وَمَا خَلْفَكُمْ لَعَلَّكُمْ تُرْحَمُونَ', 'وإذا قيل لهم اتقوا ما بين أيديكم وما خلفكم لعلكم ترحمون', 23, 45, 443, 177, '36:45'),
(3751, NULL, NULL, 36, 46, 'وَمَا تَأْتِيهِم مِّنْ ءَايَةٍ مِّنْ ءَايَٰتِ رَبِّهِمْ إِلَّا كَانُوا۟ عَنْهَا مُعْرِضِينَ', 'وما تأتيهم من آية من آيات ربهم إلا كانوا عنها معرضين', 23, 45, 443, 177, '36:46'),
(3752, NULL, NULL, 36, 47, 'وَإِذَا قِيلَ لَهُمْ أَنفِقُوا۟ مِمَّا رَزَقَكُمُ ٱللَّهُ قَالَ ٱلَّذِينَ كَفَرُوا۟ لِلَّذِينَ ءَامَنُوٓا۟ أَنُطْعِمُ مَن لَّوْ يَشَآءُ ٱللَّهُ أَطْعَمَهُۥٓ إِنْ أَنتُمْ إِلَّا فِى ضَلَٰلٍ مُّبِينٍ', 'وإذا قيل لهم أنفقوا مما رزقكم الله قال الذين كفروا للذين آمنوا أنطعم من لو يشاء الله أطعمه إن أنتم إلا في ضلال مبين', 23, 45, 443, 177, '36:47'),
(3753, NULL, NULL, 36, 48, 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَٰدِقِينَ', 'ويقولون متى هذا الوعد إن كنتم صادقين', 23, 45, 443, 177, '36:48'),
(3754, NULL, NULL, 36, 49, 'مَا يَنظُرُونَ إِلَّا صَيْحَةً وَٰحِدَةً تَأْخُذُهُمْ وَهُمْ يَخِصِّمُونَ', 'ما ينظرون إلا صيحة واحدة تأخذهم وهم يخصمون', 23, 45, 443, 177, '36:49'),
(3755, NULL, NULL, 36, 50, 'فَلَا يَسْتَطِيعُونَ تَوْصِيَةً وَلَآ إِلَىٰٓ أَهْلِهِمْ يَرْجِعُونَ', 'فلا يستطيعون توصية ولا إلى أهلهم يرجعون', 23, 45, 443, 177, '36:50'),
(3756, NULL, NULL, 36, 51, 'وَنُفِخَ فِى ٱلصُّورِ فَإِذَا هُم مِّنَ ٱلْأَجْدَاثِ إِلَىٰ رَبِّهِمْ يَنسِلُونَ', 'ونفخ في الصور فإذا هم من الأجداث إلى ربهم ينسلون', 23, 45, 443, 177, '36:51'),
(3757, NULL, NULL, 36, 52, 'قَالُوا۟ يَٰوَيْلَنَا مَنۢ بَعَثَنَا مِن مَّرْقَدِنَا هَٰذَا مَا وَعَدَ ٱلرَّحْمَٰنُ وَصَدَقَ ٱلْمُرْسَلُونَ', 'قالوا يا ويلنا من بعثنا من مرقدنا هذا ما وعد الرحمن وصدق المرسلون', 23, 45, 443, 177, '36:52'),
(3758, NULL, NULL, 36, 53, 'إِن كَانَتْ إِلَّا صَيْحَةً وَٰحِدَةً فَإِذَا هُمْ جَمِيعٌ لَّدَيْنَا مُحْضَرُونَ', 'إن كانت إلا صيحة واحدة فإذا هم جميع لدينا محضرون', 23, 45, 443, 177, '36:53'),
(3759, NULL, NULL, 36, 54, 'فَٱلْيَوْمَ لَا تُظْلَمُ نَفْسٌ شَيْـًٔا وَلَا تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ', 'فاليوم لا تظلم نفس شيئا ولا تجزون إلا ما كنتم تعملون', 23, 45, 443, 177, '36:54'),
(3760, NULL, NULL, 36, 55, 'إِنَّ أَصْحَٰبَ ٱلْجَنَّةِ ٱلْيَوْمَ فِى شُغُلٍ فَٰكِهُونَ', 'إن أصحاب الجنة اليوم في شغل فاكهون', 23, 45, 444, 177, '36:55'),
(3761, NULL, NULL, 36, 56, 'هُمْ وَأَزْوَٰجُهُمْ فِى ظِلَٰلٍ عَلَى ٱلْأَرَآئِكِ مُتَّكِـُٔونَ', 'هم وأزواجهم في ظلال على الأرائك متكئون', 23, 45, 444, 177, '36:56'),
(3762, NULL, NULL, 36, 57, 'لَهُمْ فِيهَا فَٰكِهَةٌ وَلَهُم مَّا يَدَّعُونَ', 'لهم فيها فاكهة ولهم ما يدعون', 23, 45, 444, 177, '36:57'),
(3763, NULL, NULL, 36, 58, 'سَلَٰمٌ قَوْلًا مِّن رَّبٍّ رَّحِيمٍ', 'سلام قولا من رب رحيم', 23, 45, 444, 177, '36:58'),
(3764, NULL, NULL, 36, 59, 'وَٱمْتَٰزُوا۟ ٱلْيَوْمَ أَيُّهَا ٱلْمُجْرِمُونَ', 'وامتازوا اليوم أيها المجرمون', 23, 45, 444, 177, '36:59'),
(3765, NULL, NULL, 36, 60, 'أَلَمْ أَعْهَدْ إِلَيْكُمْ يَٰبَنِىٓ ءَادَمَ أَن لَّا تَعْبُدُوا۟ ٱلشَّيْطَٰنَ إِنَّهُۥ لَكُمْ عَدُوٌّ مُّبِينٌ', 'ألم أعهد إليكم يا بني آدم أن لا تعبدوا الشيطان إنه لكم عدو مبين', 23, 45, 444, 178, '36:60'),
(3766, NULL, NULL, 36, 61, 'وَأَنِ ٱعْبُدُونِى هَٰذَا صِرَٰطٌ مُّسْتَقِيمٌ', 'وأن اعبدوني هذا صراط مستقيم', 23, 45, 444, 178, '36:61'),
(3767, NULL, NULL, 36, 62, 'وَلَقَدْ أَضَلَّ مِنكُمْ جِبِلًّا كَثِيرًا أَفَلَمْ تَكُونُوا۟ تَعْقِلُونَ', 'ولقد أضل منكم جبلا كثيرا أفلم تكونوا تعقلون', 23, 45, 444, 178, '36:62'),
(3768, NULL, NULL, 36, 63, 'هَٰذِهِۦ جَهَنَّمُ ٱلَّتِى كُنتُمْ تُوعَدُونَ', 'هذه جهنم التي كنتم توعدون', 23, 45, 444, 178, '36:63'),
(3769, NULL, NULL, 36, 64, 'ٱصْلَوْهَا ٱلْيَوْمَ بِمَا كُنتُمْ تَكْفُرُونَ', 'اصلوها اليوم بما كنتم تكفرون', 23, 45, 444, 178, '36:64'),
(3770, NULL, NULL, 36, 65, 'ٱلْيَوْمَ نَخْتِمُ عَلَىٰٓ أَفْوَٰهِهِمْ وَتُكَلِّمُنَآ أَيْدِيهِمْ وَتَشْهَدُ أَرْجُلُهُم بِمَا كَانُوا۟ يَكْسِبُونَ', 'اليوم نختم على أفواههم وتكلمنا أيديهم وتشهد أرجلهم بما كانوا يكسبون', 23, 45, 444, 178, '36:65'),
(3771, NULL, NULL, 36, 66, 'وَلَوْ نَشَآءُ لَطَمَسْنَا عَلَىٰٓ أَعْيُنِهِمْ فَٱسْتَبَقُوا۟ ٱلصِّرَٰطَ فَأَنَّىٰ يُبْصِرُونَ', 'ولو نشاء لطمسنا على أعينهم فاستبقوا الصراط فأنى يبصرون', 23, 45, 444, 178, '36:66'),
(3772, NULL, NULL, 36, 67, 'وَلَوْ نَشَآءُ لَمَسَخْنَٰهُمْ عَلَىٰ مَكَانَتِهِمْ فَمَا ٱسْتَطَٰعُوا۟ مُضِيًّا وَلَا يَرْجِعُونَ', 'ولو نشاء لمسخناهم على مكانتهم فما استطاعوا مضيا ولا يرجعون', 23, 45, 444, 178, '36:67'),
(3773, NULL, NULL, 36, 68, 'وَمَن نُّعَمِّرْهُ نُنَكِّسْهُ فِى ٱلْخَلْقِ أَفَلَا يَعْقِلُونَ', 'ومن نعمره ننكسه في الخلق أفلا يعقلون', 23, 45, 444, 178, '36:68'),
(3774, NULL, NULL, 36, 69, 'وَمَا عَلَّمْنَٰهُ ٱلشِّعْرَ وَمَا يَنۢبَغِى لَهُۥٓ إِنْ هُوَ إِلَّا ذِكْرٌ وَقُرْءَانٌ مُّبِينٌ', 'وما علمناه الشعر وما ينبغي له إن هو إلا ذكر وقرآن مبين', 23, 45, 444, 178, '36:69'),
(3775, NULL, NULL, 36, 70, 'لِّيُنذِرَ مَن كَانَ حَيًّا وَيَحِقَّ ٱلْقَوْلُ عَلَى ٱلْكَٰفِرِينَ', 'لينذر من كان حيا ويحق القول على الكافرين', 23, 45, 444, 178, '36:70'),
(3776, NULL, NULL, 36, 71, 'أَوَلَمْ يَرَوْا۟ أَنَّا خَلَقْنَا لَهُم مِّمَّا عَمِلَتْ أَيْدِينَآ أَنْعَٰمًا فَهُمْ لَهَا مَٰلِكُونَ', 'أولم يروا أنا خلقنا لهم مما عملت أيدينا أنعاما فهم لها مالكون', 23, 45, 445, 178, '36:71'),
(3777, NULL, NULL, 36, 72, 'وَذَلَّلْنَٰهَا لَهُمْ فَمِنْهَا رَكُوبُهُمْ وَمِنْهَا يَأْكُلُونَ', 'وذللناها لهم فمنها ركوبهم ومنها يأكلون', 23, 45, 445, 178, '36:72'),
(3778, NULL, NULL, 36, 73, 'وَلَهُمْ فِيهَا مَنَٰفِعُ وَمَشَارِبُ أَفَلَا يَشْكُرُونَ', 'ولهم فيها منافع ومشارب أفلا يشكرون', 23, 45, 445, 178, '36:73'),
(3779, NULL, NULL, 36, 74, 'وَٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ ءَالِهَةً لَّعَلَّهُمْ يُنصَرُونَ', 'واتخذوا من دون الله آلهة لعلهم ينصرون', 23, 45, 445, 178, '36:74'),
(3780, NULL, NULL, 36, 75, 'لَا يَسْتَطِيعُونَ نَصْرَهُمْ وَهُمْ لَهُمْ جُندٌ مُّحْضَرُونَ', 'لا يستطيعون نصرهم وهم لهم جند محضرون', 23, 45, 445, 178, '36:75'),
(3781, NULL, NULL, 36, 76, 'فَلَا يَحْزُنكَ قَوْلُهُمْ إِنَّا نَعْلَمُ مَا يُسِرُّونَ وَمَا يُعْلِنُونَ', 'فلا يحزنك قولهم إنا نعلم ما يسرون وما يعلنون', 23, 45, 445, 178, '36:76'),
(3782, NULL, NULL, 36, 77, 'أَوَلَمْ يَرَ ٱلْإِنسَٰنُ أَنَّا خَلَقْنَٰهُ مِن نُّطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُّبِينٌ', 'أولم ير الإنسان أنا خلقناه من نطفة فإذا هو خصيم مبين', 23, 45, 445, 178, '36:77'),
(3783, NULL, NULL, 36, 78, 'وَضَرَبَ لَنَا مَثَلًا وَنَسِىَ خَلْقَهُۥ قَالَ مَن يُحْىِ ٱلْعِظَٰمَ وَهِىَ رَمِيمٌ', 'وضرب لنا مثلا ونسي خلقه قال من يحيي العظام وهي رميم', 23, 45, 445, 178, '36:78'),
(3784, NULL, NULL, 36, 79, 'قُلْ يُحْيِيهَا ٱلَّذِىٓ أَنشَأَهَآ أَوَّلَ مَرَّةٍ وَهُوَ بِكُلِّ خَلْقٍ عَلِيمٌ', 'قل يحييها الذي أنشأها أول مرة وهو بكل خلق عليم', 23, 45, 445, 178, '36:79'),
(3785, NULL, NULL, 36, 80, 'ٱلَّذِى جَعَلَ لَكُم مِّنَ ٱلشَّجَرِ ٱلْأَخْضَرِ نَارًا فَإِذَآ أَنتُم مِّنْهُ تُوقِدُونَ', 'الذي جعل لكم من الشجر الأخضر نارا فإذا أنتم منه توقدون', 23, 45, 445, 178, '36:80'),
(3786, NULL, NULL, 36, 81, 'أَوَلَيْسَ ٱلَّذِى خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ بِقَٰدِرٍ عَلَىٰٓ أَن يَخْلُقَ مِثْلَهُم بَلَىٰ وَهُوَ ٱلْخَلَّٰقُ ٱلْعَلِيمُ', 'أوليس الذي خلق السماوات والأرض بقادر على أن يخلق مثلهم بلى وهو الخلاق العليم', 23, 45, 445, 178, '36:81'),
(3787, NULL, NULL, 36, 82, 'إِنَّمَآ أَمْرُهُۥٓ إِذَآ أَرَادَ شَيْـًٔا أَن يَقُولَ لَهُۥ كُن فَيَكُونُ', 'إنما أمره إذا أراد شيئا أن يقول له كن فيكون', 23, 45, 445, 178, '36:82'),
(3788, NULL, NULL, 36, 83, 'فَسُبْحَٰنَ ٱلَّذِى بِيَدِهِۦ مَلَكُوتُ كُلِّ شَىْءٍ وَإِلَيْهِ تُرْجَعُونَ', 'فسبحان الذي بيده ملكوت كل شيء وإليه ترجعون', 23, 45, 445, 178, '36:83'),
(3789, NULL, NULL, 37, 1, 'وَٱلصَّٰٓفَّٰتِ صَفًّا', 'والصافات صفا', 23, 45, 446, 178, '37:1'),
(3790, NULL, NULL, 37, 2, 'فَٱلزَّٰجِرَٰتِ زَجْرًا', 'فالزاجرات زجرا', 23, 45, 446, 178, '37:2'),
(3791, NULL, NULL, 37, 3, 'فَٱلتَّٰلِيَٰتِ ذِكْرًا', 'فالتاليات ذكرا', 23, 45, 446, 178, '37:3'),
(3792, NULL, NULL, 37, 4, 'إِنَّ إِلَٰهَكُمْ لَوَٰحِدٌ', 'إن إلهكم لواحد', 23, 45, 446, 178, '37:4'),
(3793, NULL, NULL, 37, 5, 'رَّبُّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا وَرَبُّ ٱلْمَشَٰرِقِ', 'رب السماوات والأرض وما بينهما ورب المشارق', 23, 45, 446, 178, '37:5'),
(3794, NULL, NULL, 37, 6, 'إِنَّا زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِزِينَةٍ ٱلْكَوَاكِبِ', 'إنا زينا السماء الدنيا بزينة الكواكب', 23, 45, 446, 178, '37:6'),
(3795, NULL, NULL, 37, 7, 'وَحِفْظًا مِّن كُلِّ شَيْطَٰنٍ مَّارِدٍ', 'وحفظا من كل شيطان مارد', 23, 45, 446, 178, '37:7'),
(3796, NULL, NULL, 37, 8, 'لَّا يَسَّمَّعُونَ إِلَى ٱلْمَلَإِ ٱلْأَعْلَىٰ وَيُقْذَفُونَ مِن كُلِّ جَانِبٍ', 'لا يسمعون إلى الملإ الأعلى ويقذفون من كل جانب', 23, 45, 446, 178, '37:8'),
(3797, NULL, NULL, 37, 9, 'دُحُورًا وَلَهُمْ عَذَابٌ وَاصِبٌ', 'دحورا ولهم عذاب واصب', 23, 45, 446, 178, '37:9'),
(3798, NULL, NULL, 37, 10, 'إِلَّا مَنْ خَطِفَ ٱلْخَطْفَةَ فَأَتْبَعَهُۥ شِهَابٌ ثَاقِبٌ', 'إلا من خطف الخطفة فأتبعه شهاب ثاقب', 23, 45, 446, 178, '37:10'),
(3799, NULL, NULL, 37, 11, 'فَٱسْتَفْتِهِمْ أَهُمْ أَشَدُّ خَلْقًا أَم مَّنْ خَلَقْنَآ إِنَّا خَلَقْنَٰهُم مِّن طِينٍ لَّازِبٍۭ', 'فاستفتهم أهم أشد خلقا أم من خلقنا إنا خلقناهم من طين لازب', 23, 45, 446, 178, '37:11'),
(3800, NULL, NULL, 37, 12, 'بَلْ عَجِبْتَ وَيَسْخَرُونَ', 'بل عجبت ويسخرون', 23, 45, 446, 178, '37:12'),
(3801, NULL, NULL, 37, 13, 'وَإِذَا ذُكِّرُوا۟ لَا يَذْكُرُونَ', 'وإذا ذكروا لا يذكرون', 23, 45, 446, 178, '37:13'),
(3802, NULL, NULL, 37, 14, 'وَإِذَا رَأَوْا۟ ءَايَةً يَسْتَسْخِرُونَ', 'وإذا رأوا آية يستسخرون', 23, 45, 446, 178, '37:14'),
(3803, NULL, NULL, 37, 15, 'وَقَالُوٓا۟ إِنْ هَٰذَآ إِلَّا سِحْرٌ مُّبِينٌ', 'وقالوا إن هذا إلا سحر مبين', 23, 45, 446, 178, '37:15'),
(3804, NULL, NULL, 37, 16, 'أَءِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَٰمًا أَءِنَّا لَمَبْعُوثُونَ', 'أإذا متنا وكنا ترابا وعظاما أإنا لمبعوثون', 23, 45, 446, 178, '37:16'),
(3805, NULL, NULL, 37, 17, 'أَوَءَابَآؤُنَا ٱلْأَوَّلُونَ', 'أوآباؤنا الأولون', 23, 45, 446, 178, '37:17'),
(3806, NULL, NULL, 37, 18, 'قُلْ نَعَمْ وَأَنتُمْ دَٰخِرُونَ', 'قل نعم وأنتم داخرون', 23, 45, 446, 178, '37:18'),
(3807, NULL, NULL, 37, 19, 'فَإِنَّمَا هِىَ زَجْرَةٌ وَٰحِدَةٌ فَإِذَا هُمْ يَنظُرُونَ', 'فإنما هي زجرة واحدة فإذا هم ينظرون', 23, 45, 446, 178, '37:19'),
(3808, NULL, NULL, 37, 20, 'وَقَالُوا۟ يَٰوَيْلَنَا هَٰذَا يَوْمُ ٱلدِّينِ', 'وقالوا يا ويلنا هذا يوم الدين', 23, 45, 446, 178, '37:20'),
(3809, NULL, NULL, 37, 21, 'هَٰذَا يَوْمُ ٱلْفَصْلِ ٱلَّذِى كُنتُم بِهِۦ تُكَذِّبُونَ', 'هذا يوم الفصل الذي كنتم به تكذبون', 23, 45, 446, 178, '37:21'),
(3810, NULL, NULL, 37, 22, 'ٱحْشُرُوا۟ ٱلَّذِينَ ظَلَمُوا۟ وَأَزْوَٰجَهُمْ وَمَا كَانُوا۟ يَعْبُدُونَ', 'احشروا الذين ظلموا وأزواجهم وما كانوا يعبدون', 23, 45, 446, 179, '37:22'),
(3811, NULL, NULL, 37, 23, 'مِن دُونِ ٱللَّهِ فَٱهْدُوهُمْ إِلَىٰ صِرَٰطِ ٱلْجَحِيمِ', 'من دون الله فاهدوهم إلى صراط الجحيم', 23, 45, 446, 179, '37:23'),
(3812, NULL, NULL, 37, 24, 'وَقِفُوهُمْ إِنَّهُم مَّسْـُٔولُونَ', 'وقفوهم إنهم مسئولون', 23, 45, 446, 179, '37:24'),
(3813, NULL, NULL, 37, 25, 'مَا لَكُمْ لَا تَنَاصَرُونَ', 'ما لكم لا تناصرون', 23, 45, 447, 179, '37:25'),
(3814, NULL, NULL, 37, 26, 'بَلْ هُمُ ٱلْيَوْمَ مُسْتَسْلِمُونَ', 'بل هم اليوم مستسلمون', 23, 45, 447, 179, '37:26'),
(3815, NULL, NULL, 37, 27, 'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَآءَلُونَ', 'وأقبل بعضهم على بعض يتساءلون', 23, 45, 447, 179, '37:27'),
(3816, NULL, NULL, 37, 28, 'قَالُوٓا۟ إِنَّكُمْ كُنتُمْ تَأْتُونَنَا عَنِ ٱلْيَمِينِ', 'قالوا إنكم كنتم تأتوننا عن اليمين', 23, 45, 447, 179, '37:28'),
(3817, NULL, NULL, 37, 29, 'قَالُوا۟ بَل لَّمْ تَكُونُوا۟ مُؤْمِنِينَ', 'قالوا بل لم تكونوا مؤمنين', 23, 45, 447, 179, '37:29'),
(3818, NULL, NULL, 37, 30, 'وَمَا كَانَ لَنَا عَلَيْكُم مِّن سُلْطَٰنٍۭ بَلْ كُنتُمْ قَوْمًا طَٰغِينَ', 'وما كان لنا عليكم من سلطان بل كنتم قوما طاغين', 23, 45, 447, 179, '37:30'),
(3819, NULL, NULL, 37, 31, 'فَحَقَّ عَلَيْنَا قَوْلُ رَبِّنَآ إِنَّا لَذَآئِقُونَ', 'فحق علينا قول ربنا إنا لذائقون', 23, 45, 447, 179, '37:31'),
(3820, NULL, NULL, 37, 32, 'فَأَغْوَيْنَٰكُمْ إِنَّا كُنَّا غَٰوِينَ', 'فأغويناكم إنا كنا غاوين', 23, 45, 447, 179, '37:32'),
(3821, NULL, NULL, 37, 33, 'فَإِنَّهُمْ يَوْمَئِذٍ فِى ٱلْعَذَابِ مُشْتَرِكُونَ', 'فإنهم يومئذ في العذاب مشتركون', 23, 45, 447, 179, '37:33'),
(3822, NULL, NULL, 37, 34, 'إِنَّا كَذَٰلِكَ نَفْعَلُ بِٱلْمُجْرِمِينَ', 'إنا كذلك نفعل بالمجرمين', 23, 45, 447, 179, '37:34'),
(3823, NULL, NULL, 37, 35, 'إِنَّهُمْ كَانُوٓا۟ إِذَا قِيلَ لَهُمْ لَآ إِلَٰهَ إِلَّا ٱللَّهُ يَسْتَكْبِرُونَ', 'إنهم كانوا إذا قيل لهم لا إله إلا الله يستكبرون', 23, 45, 447, 179, '37:35'),
(3824, NULL, NULL, 37, 36, 'وَيَقُولُونَ أَئِنَّا لَتَارِكُوٓا۟ ءَالِهَتِنَا لِشَاعِرٍ مَّجْنُونٍۭ', 'ويقولون أئنا لتاركو آلهتنا لشاعر مجنون', 23, 45, 447, 179, '37:36'),
(3825, NULL, NULL, 37, 37, 'بَلْ جَآءَ بِٱلْحَقِّ وَصَدَّقَ ٱلْمُرْسَلِينَ', 'بل جاء بالحق وصدق المرسلين', 23, 45, 447, 179, '37:37'),
(3826, NULL, NULL, 37, 38, 'إِنَّكُمْ لَذَآئِقُوا۟ ٱلْعَذَابِ ٱلْأَلِيمِ', 'إنكم لذائقو العذاب الأليم', 23, 45, 447, 179, '37:38'),
(3827, NULL, NULL, 37, 39, 'وَمَا تُجْزَوْنَ إِلَّا مَا كُنتُمْ تَعْمَلُونَ', 'وما تجزون إلا ما كنتم تعملون', 23, 45, 447, 179, '37:39'),
(3828, NULL, NULL, 37, 40, 'إِلَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ', 'إلا عباد الله المخلصين', 23, 45, 447, 179, '37:40');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(3829, NULL, NULL, 37, 41, 'أُو۟لَٰٓئِكَ لَهُمْ رِزْقٌ مَّعْلُومٌ', 'أولئك لهم رزق معلوم', 23, 45, 447, 179, '37:41'),
(3830, NULL, NULL, 37, 42, 'فَوَٰكِهُ وَهُم مُّكْرَمُونَ', 'فواكه وهم مكرمون', 23, 45, 447, 179, '37:42'),
(3831, NULL, NULL, 37, 43, 'فِى جَنَّٰتِ ٱلنَّعِيمِ', 'في جنات النعيم', 23, 45, 447, 179, '37:43'),
(3832, NULL, NULL, 37, 44, 'عَلَىٰ سُرُرٍ مُّتَقَٰبِلِينَ', 'على سرر متقابلين', 23, 45, 447, 179, '37:44'),
(3833, NULL, NULL, 37, 45, 'يُطَافُ عَلَيْهِم بِكَأْسٍ مِّن مَّعِينٍۭ', 'يطاف عليهم بكأس من معين', 23, 45, 447, 179, '37:45'),
(3834, NULL, NULL, 37, 46, 'بَيْضَآءَ لَذَّةٍ لِّلشَّٰرِبِينَ', 'بيضاء لذة للشاربين', 23, 45, 447, 179, '37:46'),
(3835, NULL, NULL, 37, 47, 'لَا فِيهَا غَوْلٌ وَلَا هُمْ عَنْهَا يُنزَفُونَ', 'لا فيها غول ولا هم عنها ينزفون', 23, 45, 447, 179, '37:47'),
(3836, NULL, NULL, 37, 48, 'وَعِندَهُمْ قَٰصِرَٰتُ ٱلطَّرْفِ عِينٌ', 'وعندهم قاصرات الطرف عين', 23, 45, 447, 179, '37:48'),
(3837, NULL, NULL, 37, 49, 'كَأَنَّهُنَّ بَيْضٌ مَّكْنُونٌ', 'كأنهن بيض مكنون', 23, 45, 447, 179, '37:49'),
(3838, NULL, NULL, 37, 50, 'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَآءَلُونَ', 'فأقبل بعضهم على بعض يتساءلون', 23, 45, 447, 179, '37:50'),
(3839, NULL, NULL, 37, 51, 'قَالَ قَآئِلٌ مِّنْهُمْ إِنِّى كَانَ لِى قَرِينٌ', 'قال قائل منهم إني كان لي قرين', 23, 45, 447, 179, '37:51'),
(3840, NULL, NULL, 37, 52, 'يَقُولُ أَءِنَّكَ لَمِنَ ٱلْمُصَدِّقِينَ', 'يقول أإنك لمن المصدقين', 23, 45, 448, 179, '37:52'),
(3841, NULL, NULL, 37, 53, 'أَءِذَا مِتْنَا وَكُنَّا تُرَابًا وَعِظَٰمًا أَءِنَّا لَمَدِينُونَ', 'أإذا متنا وكنا ترابا وعظاما أإنا لمدينون', 23, 45, 448, 179, '37:53'),
(3842, NULL, NULL, 37, 54, 'قَالَ هَلْ أَنتُم مُّطَّلِعُونَ', 'قال هل أنتم مطلعون', 23, 45, 448, 179, '37:54'),
(3843, NULL, NULL, 37, 55, 'فَٱطَّلَعَ فَرَءَاهُ فِى سَوَآءِ ٱلْجَحِيمِ', 'فاطلع فرآه في سواء الجحيم', 23, 45, 448, 179, '37:55'),
(3844, NULL, NULL, 37, 56, 'قَالَ تَٱللَّهِ إِن كِدتَّ لَتُرْدِينِ', 'قال تالله إن كدت لتردين', 23, 45, 448, 179, '37:56'),
(3845, NULL, NULL, 37, 57, 'وَلَوْلَا نِعْمَةُ رَبِّى لَكُنتُ مِنَ ٱلْمُحْضَرِينَ', 'ولولا نعمة ربي لكنت من المحضرين', 23, 45, 448, 179, '37:57'),
(3846, NULL, NULL, 37, 58, 'أَفَمَا نَحْنُ بِمَيِّتِينَ', 'أفما نحن بميتين', 23, 45, 448, 179, '37:58'),
(3847, NULL, NULL, 37, 59, 'إِلَّا مَوْتَتَنَا ٱلْأُولَىٰ وَمَا نَحْنُ بِمُعَذَّبِينَ', 'إلا موتتنا الأولى وما نحن بمعذبين', 23, 45, 448, 179, '37:59'),
(3848, NULL, NULL, 37, 60, 'إِنَّ هَٰذَا لَهُوَ ٱلْفَوْزُ ٱلْعَظِيمُ', 'إن هذا لهو الفوز العظيم', 23, 45, 448, 179, '37:60'),
(3849, NULL, NULL, 37, 61, 'لِمِثْلِ هَٰذَا فَلْيَعْمَلِ ٱلْعَٰمِلُونَ', 'لمثل هذا فليعمل العاملون', 23, 45, 448, 179, '37:61'),
(3850, NULL, NULL, 37, 62, 'أَذَٰلِكَ خَيْرٌ نُّزُلًا أَمْ شَجَرَةُ ٱلزَّقُّومِ', 'أذلك خير نزلا أم شجرة الزقوم', 23, 45, 448, 179, '37:62'),
(3851, NULL, NULL, 37, 63, 'إِنَّا جَعَلْنَٰهَا فِتْنَةً لِّلظَّٰلِمِينَ', 'إنا جعلناها فتنة للظالمين', 23, 45, 448, 179, '37:63'),
(3852, NULL, NULL, 37, 64, 'إِنَّهَا شَجَرَةٌ تَخْرُجُ فِىٓ أَصْلِ ٱلْجَحِيمِ', 'إنها شجرة تخرج في أصل الجحيم', 23, 45, 448, 179, '37:64'),
(3853, NULL, NULL, 37, 65, 'طَلْعُهَا كَأَنَّهُۥ رُءُوسُ ٱلشَّيَٰطِينِ', 'طلعها كأنه رءوس الشياطين', 23, 45, 448, 179, '37:65'),
(3854, NULL, NULL, 37, 66, 'فَإِنَّهُمْ لَءَاكِلُونَ مِنْهَا فَمَالِـُٔونَ مِنْهَا ٱلْبُطُونَ', 'فإنهم لآكلون منها فمالئون منها البطون', 23, 45, 448, 179, '37:66'),
(3855, NULL, NULL, 37, 67, 'ثُمَّ إِنَّ لَهُمْ عَلَيْهَا لَشَوْبًا مِّنْ حَمِيمٍ', 'ثم إن لهم عليها لشوبا من حميم', 23, 45, 448, 179, '37:67'),
(3856, NULL, NULL, 37, 68, 'ثُمَّ إِنَّ مَرْجِعَهُمْ لَإِلَى ٱلْجَحِيمِ', 'ثم إن مرجعهم لإلى الجحيم', 23, 45, 448, 179, '37:68'),
(3857, NULL, NULL, 37, 69, 'إِنَّهُمْ أَلْفَوْا۟ ءَابَآءَهُمْ ضَآلِّينَ', 'إنهم ألفوا آباءهم ضالين', 23, 45, 448, 179, '37:69'),
(3858, NULL, NULL, 37, 70, 'فَهُمْ عَلَىٰٓ ءَاثَٰرِهِمْ يُهْرَعُونَ', 'فهم على آثارهم يهرعون', 23, 45, 448, 179, '37:70'),
(3859, NULL, NULL, 37, 71, 'وَلَقَدْ ضَلَّ قَبْلَهُمْ أَكْثَرُ ٱلْأَوَّلِينَ', 'ولقد ضل قبلهم أكثر الأولين', 23, 45, 448, 179, '37:71'),
(3860, NULL, NULL, 37, 72, 'وَلَقَدْ أَرْسَلْنَا فِيهِم مُّنذِرِينَ', 'ولقد أرسلنا فيهم منذرين', 23, 45, 448, 179, '37:72'),
(3861, NULL, NULL, 37, 73, 'فَٱنظُرْ كَيْفَ كَانَ عَٰقِبَةُ ٱلْمُنذَرِينَ', 'فانظر كيف كان عاقبة المنذرين', 23, 45, 448, 179, '37:73'),
(3862, NULL, NULL, 37, 74, 'إِلَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ', 'إلا عباد الله المخلصين', 23, 45, 448, 179, '37:74'),
(3863, NULL, NULL, 37, 75, 'وَلَقَدْ نَادَىٰنَا نُوحٌ فَلَنِعْمَ ٱلْمُجِيبُونَ', 'ولقد نادانا نوح فلنعم المجيبون', 23, 45, 448, 179, '37:75'),
(3864, NULL, NULL, 37, 76, 'وَنَجَّيْنَٰهُ وَأَهْلَهُۥ مِنَ ٱلْكَرْبِ ٱلْعَظِيمِ', 'ونجيناه وأهله من الكرب العظيم', 23, 45, 448, 179, '37:76'),
(3865, NULL, NULL, 37, 77, 'وَجَعَلْنَا ذُرِّيَّتَهُۥ هُمُ ٱلْبَاقِينَ', 'وجعلنا ذريته هم الباقين', 23, 45, 449, 179, '37:77'),
(3866, NULL, NULL, 37, 78, 'وَتَرَكْنَا عَلَيْهِ فِى ٱلْءَاخِرِينَ', 'وتركنا عليه في الآخرين', 23, 45, 449, 179, '37:78'),
(3867, NULL, NULL, 37, 79, 'سَلَٰمٌ عَلَىٰ نُوحٍ فِى ٱلْعَٰلَمِينَ', 'سلام على نوح في العالمين', 23, 45, 449, 179, '37:79'),
(3868, NULL, NULL, 37, 80, 'إِنَّا كَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ', 'إنا كذلك نجزي المحسنين', 23, 45, 449, 179, '37:80'),
(3869, NULL, NULL, 37, 81, 'إِنَّهُۥ مِنْ عِبَادِنَا ٱلْمُؤْمِنِينَ', 'إنه من عبادنا المؤمنين', 23, 45, 449, 179, '37:81'),
(3870, NULL, NULL, 37, 82, 'ثُمَّ أَغْرَقْنَا ٱلْءَاخَرِينَ', 'ثم أغرقنا الآخرين', 23, 45, 449, 179, '37:82'),
(3871, NULL, NULL, 37, 83, 'وَإِنَّ مِن شِيعَتِهِۦ لَإِبْرَٰهِيمَ', 'وإن من شيعته لإبراهيم', 23, 45, 449, 180, '37:83'),
(3872, NULL, NULL, 37, 84, 'إِذْ جَآءَ رَبَّهُۥ بِقَلْبٍ سَلِيمٍ', 'إذ جاء ربه بقلب سليم', 23, 45, 449, 180, '37:84'),
(3873, NULL, NULL, 37, 85, 'إِذْ قَالَ لِأَبِيهِ وَقَوْمِهِۦ مَاذَا تَعْبُدُونَ', 'إذ قال لأبيه وقومه ماذا تعبدون', 23, 45, 449, 180, '37:85'),
(3874, NULL, NULL, 37, 86, 'أَئِفْكًا ءَالِهَةً دُونَ ٱللَّهِ تُرِيدُونَ', 'أئفكا آلهة دون الله تريدون', 23, 45, 449, 180, '37:86'),
(3875, NULL, NULL, 37, 87, 'فَمَا ظَنُّكُم بِرَبِّ ٱلْعَٰلَمِينَ', 'فما ظنكم برب العالمين', 23, 45, 449, 180, '37:87'),
(3876, NULL, NULL, 37, 88, 'فَنَظَرَ نَظْرَةً فِى ٱلنُّجُومِ', 'فنظر نظرة في النجوم', 23, 45, 449, 180, '37:88'),
(3877, NULL, NULL, 37, 89, 'فَقَالَ إِنِّى سَقِيمٌ', 'فقال إني سقيم', 23, 45, 449, 180, '37:89'),
(3878, NULL, NULL, 37, 90, 'فَتَوَلَّوْا۟ عَنْهُ مُدْبِرِينَ', 'فتولوا عنه مدبرين', 23, 45, 449, 180, '37:90'),
(3879, NULL, NULL, 37, 91, 'فَرَاغَ إِلَىٰٓ ءَالِهَتِهِمْ فَقَالَ أَلَا تَأْكُلُونَ', 'فراغ إلى آلهتهم فقال ألا تأكلون', 23, 45, 449, 180, '37:91'),
(3880, NULL, NULL, 37, 92, 'مَا لَكُمْ لَا تَنطِقُونَ', 'ما لكم لا تنطقون', 23, 45, 449, 180, '37:92'),
(3881, NULL, NULL, 37, 93, 'فَرَاغَ عَلَيْهِمْ ضَرْبًۢا بِٱلْيَمِينِ', 'فراغ عليهم ضربا باليمين', 23, 45, 449, 180, '37:93'),
(3882, NULL, NULL, 37, 94, 'فَأَقْبَلُوٓا۟ إِلَيْهِ يَزِفُّونَ', 'فأقبلوا إليه يزفون', 23, 45, 449, 180, '37:94'),
(3883, NULL, NULL, 37, 95, 'قَالَ أَتَعْبُدُونَ مَا تَنْحِتُونَ', 'قال أتعبدون ما تنحتون', 23, 45, 449, 180, '37:95'),
(3884, NULL, NULL, 37, 96, 'وَٱللَّهُ خَلَقَكُمْ وَمَا تَعْمَلُونَ', 'والله خلقكم وما تعملون', 23, 45, 449, 180, '37:96'),
(3885, NULL, NULL, 37, 97, 'قَالُوا۟ ٱبْنُوا۟ لَهُۥ بُنْيَٰنًا فَأَلْقُوهُ فِى ٱلْجَحِيمِ', 'قالوا ابنوا له بنيانا فألقوه في الجحيم', 23, 45, 449, 180, '37:97'),
(3886, NULL, NULL, 37, 98, 'فَأَرَادُوا۟ بِهِۦ كَيْدًا فَجَعَلْنَٰهُمُ ٱلْأَسْفَلِينَ', 'فأرادوا به كيدا فجعلناهم الأسفلين', 23, 45, 449, 180, '37:98'),
(3887, NULL, NULL, 37, 99, 'وَقَالَ إِنِّى ذَاهِبٌ إِلَىٰ رَبِّى سَيَهْدِينِ', 'وقال إني ذاهب إلى ربي سيهدين', 23, 45, 449, 180, '37:99'),
(3888, NULL, NULL, 37, 100, 'رَبِّ هَبْ لِى مِنَ ٱلصَّٰلِحِينَ', 'رب هب لي من الصالحين', 23, 45, 449, 180, '37:100'),
(3889, NULL, NULL, 37, 101, 'فَبَشَّرْنَٰهُ بِغُلَٰمٍ حَلِيمٍ', 'فبشرناه بغلام حليم', 23, 45, 449, 180, '37:101'),
(3890, NULL, NULL, 37, 102, 'فَلَمَّا بَلَغَ مَعَهُ ٱلسَّعْىَ قَالَ يَٰبُنَىَّ إِنِّىٓ أَرَىٰ فِى ٱلْمَنَامِ أَنِّىٓ أَذْبَحُكَ فَٱنظُرْ مَاذَا تَرَىٰ قَالَ يَٰٓأَبَتِ ٱفْعَلْ مَا تُؤْمَرُ سَتَجِدُنِىٓ إِن شَآءَ ٱللَّهُ مِنَ ٱلصَّٰبِرِينَ', 'فلما بلغ معه السعي قال يا بني إني أرى في المنام أني أذبحك فانظر ماذا ترى قال يا أبت افعل ما تؤمر ستجدني إن شاء الله من الصابرين', 23, 45, 449, 180, '37:102'),
(3891, NULL, NULL, 37, 103, 'فَلَمَّآ أَسْلَمَا وَتَلَّهُۥ لِلْجَبِينِ', 'فلما أسلما وتله للجبين', 23, 45, 450, 180, '37:103'),
(3892, NULL, NULL, 37, 104, 'وَنَٰدَيْنَٰهُ أَن يَٰٓإِبْرَٰهِيمُ', 'وناديناه أن يا إبراهيم', 23, 45, 450, 180, '37:104'),
(3893, NULL, NULL, 37, 105, 'قَدْ صَدَّقْتَ ٱلرُّءْيَآ إِنَّا كَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ', 'قد صدقت الرؤيا إنا كذلك نجزي المحسنين', 23, 45, 450, 180, '37:105'),
(3894, NULL, NULL, 37, 106, 'إِنَّ هَٰذَا لَهُوَ ٱلْبَلَٰٓؤُا۟ ٱلْمُبِينُ', 'إن هذا لهو البلاء المبين', 23, 45, 450, 180, '37:106'),
(3895, NULL, NULL, 37, 107, 'وَفَدَيْنَٰهُ بِذِبْحٍ عَظِيمٍ', 'وفديناه بذبح عظيم', 23, 45, 450, 180, '37:107'),
(3896, NULL, NULL, 37, 108, 'وَتَرَكْنَا عَلَيْهِ فِى ٱلْءَاخِرِينَ', 'وتركنا عليه في الآخرين', 23, 45, 450, 180, '37:108'),
(3897, NULL, NULL, 37, 109, 'سَلَٰمٌ عَلَىٰٓ إِبْرَٰهِيمَ', 'سلام على إبراهيم', 23, 45, 450, 180, '37:109'),
(3898, NULL, NULL, 37, 110, 'كَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ', 'كذلك نجزي المحسنين', 23, 45, 450, 180, '37:110'),
(3899, NULL, NULL, 37, 111, 'إِنَّهُۥ مِنْ عِبَادِنَا ٱلْمُؤْمِنِينَ', 'إنه من عبادنا المؤمنين', 23, 45, 450, 180, '37:111'),
(3900, NULL, NULL, 37, 112, 'وَبَشَّرْنَٰهُ بِإِسْحَٰقَ نَبِيًّا مِّنَ ٱلصَّٰلِحِينَ', 'وبشرناه بإسحاق نبيا من الصالحين', 23, 45, 450, 180, '37:112'),
(3901, NULL, NULL, 37, 113, 'وَبَٰرَكْنَا عَلَيْهِ وَعَلَىٰٓ إِسْحَٰقَ وَمِن ذُرِّيَّتِهِمَا مُحْسِنٌ وَظَالِمٌ لِّنَفْسِهِۦ مُبِينٌ', 'وباركنا عليه وعلى إسحاق ومن ذريتهما محسن وظالم لنفسه مبين', 23, 45, 450, 180, '37:113'),
(3902, NULL, NULL, 37, 114, 'وَلَقَدْ مَنَنَّا عَلَىٰ مُوسَىٰ وَهَٰرُونَ', 'ولقد مننا على موسى وهارون', 23, 45, 450, 180, '37:114'),
(3903, NULL, NULL, 37, 115, 'وَنَجَّيْنَٰهُمَا وَقَوْمَهُمَا مِنَ ٱلْكَرْبِ ٱلْعَظِيمِ', 'ونجيناهما وقومهما من الكرب العظيم', 23, 45, 450, 180, '37:115'),
(3904, NULL, NULL, 37, 116, 'وَنَصَرْنَٰهُمْ فَكَانُوا۟ هُمُ ٱلْغَٰلِبِينَ', 'ونصرناهم فكانوا هم الغالبين', 23, 45, 450, 180, '37:116'),
(3905, NULL, NULL, 37, 117, 'وَءَاتَيْنَٰهُمَا ٱلْكِتَٰبَ ٱلْمُسْتَبِينَ', 'وآتيناهما الكتاب المستبين', 23, 45, 450, 180, '37:117'),
(3906, NULL, NULL, 37, 118, 'وَهَدَيْنَٰهُمَا ٱلصِّرَٰطَ ٱلْمُسْتَقِيمَ', 'وهديناهما الصراط المستقيم', 23, 45, 450, 180, '37:118'),
(3907, NULL, NULL, 37, 119, 'وَتَرَكْنَا عَلَيْهِمَا فِى ٱلْءَاخِرِينَ', 'وتركنا عليهما في الآخرين', 23, 45, 450, 180, '37:119'),
(3908, NULL, NULL, 37, 120, 'سَلَٰمٌ عَلَىٰ مُوسَىٰ وَهَٰرُونَ', 'سلام على موسى وهارون', 23, 45, 450, 180, '37:120'),
(3909, NULL, NULL, 37, 121, 'إِنَّا كَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ', 'إنا كذلك نجزي المحسنين', 23, 45, 450, 180, '37:121'),
(3910, NULL, NULL, 37, 122, 'إِنَّهُمَا مِنْ عِبَادِنَا ٱلْمُؤْمِنِينَ', 'إنهما من عبادنا المؤمنين', 23, 45, 450, 180, '37:122'),
(3911, NULL, NULL, 37, 123, 'وَإِنَّ إِلْيَاسَ لَمِنَ ٱلْمُرْسَلِينَ', 'وإن إلياس لمن المرسلين', 23, 45, 450, 180, '37:123'),
(3912, NULL, NULL, 37, 124, 'إِذْ قَالَ لِقَوْمِهِۦٓ أَلَا تَتَّقُونَ', 'إذ قال لقومه ألا تتقون', 23, 45, 450, 180, '37:124'),
(3913, NULL, NULL, 37, 125, 'أَتَدْعُونَ بَعْلًا وَتَذَرُونَ أَحْسَنَ ٱلْخَٰلِقِينَ', 'أتدعون بعلا وتذرون أحسن الخالقين', 23, 45, 450, 180, '37:125'),
(3914, NULL, NULL, 37, 126, 'ٱللَّهَ رَبَّكُمْ وَرَبَّ ءَابَآئِكُمُ ٱلْأَوَّلِينَ', 'الله ربكم ورب آبائكم الأولين', 23, 45, 450, 180, '37:126'),
(3915, NULL, NULL, 37, 127, 'فَكَذَّبُوهُ فَإِنَّهُمْ لَمُحْضَرُونَ', 'فكذبوه فإنهم لمحضرون', 23, 45, 451, 180, '37:127'),
(3916, NULL, NULL, 37, 128, 'إِلَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ', 'إلا عباد الله المخلصين', 23, 45, 451, 180, '37:128'),
(3917, NULL, NULL, 37, 129, 'وَتَرَكْنَا عَلَيْهِ فِى ٱلْءَاخِرِينَ', 'وتركنا عليه في الآخرين', 23, 45, 451, 180, '37:129'),
(3918, NULL, NULL, 37, 130, 'سَلَٰمٌ عَلَىٰٓ إِلْ يَاسِينَ', 'سلام على إل ياسين', 23, 45, 451, 180, '37:130'),
(3919, NULL, NULL, 37, 131, 'إِنَّا كَذَٰلِكَ نَجْزِى ٱلْمُحْسِنِينَ', 'إنا كذلك نجزي المحسنين', 23, 45, 451, 180, '37:131'),
(3920, NULL, NULL, 37, 132, 'إِنَّهُۥ مِنْ عِبَادِنَا ٱلْمُؤْمِنِينَ', 'إنه من عبادنا المؤمنين', 23, 45, 451, 180, '37:132'),
(3921, NULL, NULL, 37, 133, 'وَإِنَّ لُوطًا لَّمِنَ ٱلْمُرْسَلِينَ', 'وإن لوطا لمن المرسلين', 23, 45, 451, 180, '37:133'),
(3922, NULL, NULL, 37, 134, 'إِذْ نَجَّيْنَٰهُ وَأَهْلَهُۥٓ أَجْمَعِينَ', 'إذ نجيناه وأهله أجمعين', 23, 45, 451, 180, '37:134'),
(3923, NULL, NULL, 37, 135, 'إِلَّا عَجُوزًا فِى ٱلْغَٰبِرِينَ', 'إلا عجوزا في الغابرين', 23, 45, 451, 180, '37:135'),
(3924, NULL, NULL, 37, 136, 'ثُمَّ دَمَّرْنَا ٱلْءَاخَرِينَ', 'ثم دمرنا الآخرين', 23, 45, 451, 180, '37:136'),
(3925, NULL, NULL, 37, 137, 'وَإِنَّكُمْ لَتَمُرُّونَ عَلَيْهِم مُّصْبِحِينَ', 'وإنكم لتمرون عليهم مصبحين', 23, 45, 451, 180, '37:137'),
(3926, NULL, NULL, 37, 138, 'وَبِٱلَّيْلِ أَفَلَا تَعْقِلُونَ', 'وبالليل أفلا تعقلون', 23, 45, 451, 180, '37:138'),
(3927, NULL, NULL, 37, 139, 'وَإِنَّ يُونُسَ لَمِنَ ٱلْمُرْسَلِينَ', 'وإن يونس لمن المرسلين', 23, 45, 451, 180, '37:139'),
(3928, NULL, NULL, 37, 140, 'إِذْ أَبَقَ إِلَى ٱلْفُلْكِ ٱلْمَشْحُونِ', 'إذ أبق إلى الفلك المشحون', 23, 45, 451, 180, '37:140'),
(3929, NULL, NULL, 37, 141, 'فَسَاهَمَ فَكَانَ مِنَ ٱلْمُدْحَضِينَ', 'فساهم فكان من المدحضين', 23, 45, 451, 180, '37:141'),
(3930, NULL, NULL, 37, 142, 'فَٱلْتَقَمَهُ ٱلْحُوتُ وَهُوَ مُلِيمٌ', 'فالتقمه الحوت وهو مليم', 23, 45, 451, 180, '37:142'),
(3931, NULL, NULL, 37, 143, 'فَلَوْلَآ أَنَّهُۥ كَانَ مِنَ ٱلْمُسَبِّحِينَ', 'فلولا أنه كان من المسبحين', 23, 45, 451, 180, '37:143'),
(3932, NULL, NULL, 37, 144, 'لَلَبِثَ فِى بَطْنِهِۦٓ إِلَىٰ يَوْمِ يُبْعَثُونَ', 'للبث في بطنه إلى يوم يبعثون', 23, 45, 451, 180, '37:144'),
(3933, NULL, NULL, 37, 145, 'فَنَبَذْنَٰهُ بِٱلْعَرَآءِ وَهُوَ سَقِيمٌ', 'فنبذناه بالعراء وهو سقيم', 23, 46, 451, 181, '37:145'),
(3934, NULL, NULL, 37, 146, 'وَأَنۢبَتْنَا عَلَيْهِ شَجَرَةً مِّن يَقْطِينٍ', 'وأنبتنا عليه شجرة من يقطين', 23, 46, 451, 181, '37:146'),
(3935, NULL, NULL, 37, 147, 'وَأَرْسَلْنَٰهُ إِلَىٰ مِا۟ئَةِ أَلْفٍ أَوْ يَزِيدُونَ', 'وأرسلناه إلى مائة ألف أو يزيدون', 23, 46, 451, 181, '37:147'),
(3936, NULL, NULL, 37, 148, 'فَـَٔامَنُوا۟ فَمَتَّعْنَٰهُمْ إِلَىٰ حِينٍ', 'فآمنوا فمتعناهم إلى حين', 23, 46, 451, 181, '37:148'),
(3937, NULL, NULL, 37, 149, 'فَٱسْتَفْتِهِمْ أَلِرَبِّكَ ٱلْبَنَاتُ وَلَهُمُ ٱلْبَنُونَ', 'فاستفتهم ألربك البنات ولهم البنون', 23, 46, 451, 181, '37:149'),
(3938, NULL, NULL, 37, 150, 'أَمْ خَلَقْنَا ٱلْمَلَٰٓئِكَةَ إِنَٰثًا وَهُمْ شَٰهِدُونَ', 'أم خلقنا الملائكة إناثا وهم شاهدون', 23, 46, 451, 181, '37:150'),
(3939, NULL, NULL, 37, 151, 'أَلَآ إِنَّهُم مِّنْ إِفْكِهِمْ لَيَقُولُونَ', 'ألا إنهم من إفكهم ليقولون', 23, 46, 451, 181, '37:151'),
(3940, NULL, NULL, 37, 152, 'وَلَدَ ٱللَّهُ وَإِنَّهُمْ لَكَٰذِبُونَ', 'ولد الله وإنهم لكاذبون', 23, 46, 451, 181, '37:152'),
(3941, NULL, NULL, 37, 153, 'أَصْطَفَى ٱلْبَنَاتِ عَلَى ٱلْبَنِينَ', 'أصطفى البنات على البنين', 23, 46, 451, 181, '37:153'),
(3942, NULL, NULL, 37, 154, 'مَا لَكُمْ كَيْفَ تَحْكُمُونَ', 'ما لكم كيف تحكمون', 23, 46, 452, 181, '37:154'),
(3943, NULL, NULL, 37, 155, 'أَفَلَا تَذَكَّرُونَ', 'أفلا تذكرون', 23, 46, 452, 181, '37:155'),
(3944, NULL, NULL, 37, 156, 'أَمْ لَكُمْ سُلْطَٰنٌ مُّبِينٌ', 'أم لكم سلطان مبين', 23, 46, 452, 181, '37:156'),
(3945, NULL, NULL, 37, 157, 'فَأْتُوا۟ بِكِتَٰبِكُمْ إِن كُنتُمْ صَٰدِقِينَ', 'فأتوا بكتابكم إن كنتم صادقين', 23, 46, 452, 181, '37:157'),
(3946, NULL, NULL, 37, 158, 'وَجَعَلُوا۟ بَيْنَهُۥ وَبَيْنَ ٱلْجِنَّةِ نَسَبًا وَلَقَدْ عَلِمَتِ ٱلْجِنَّةُ إِنَّهُمْ لَمُحْضَرُونَ', 'وجعلوا بينه وبين الجنة نسبا ولقد علمت الجنة إنهم لمحضرون', 23, 46, 452, 181, '37:158'),
(3947, NULL, NULL, 37, 159, 'سُبْحَٰنَ ٱللَّهِ عَمَّا يَصِفُونَ', 'سبحان الله عما يصفون', 23, 46, 452, 181, '37:159'),
(3948, NULL, NULL, 37, 160, 'إِلَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ', 'إلا عباد الله المخلصين', 23, 46, 452, 181, '37:160'),
(3949, NULL, NULL, 37, 161, 'فَإِنَّكُمْ وَمَا تَعْبُدُونَ', 'فإنكم وما تعبدون', 23, 46, 452, 181, '37:161'),
(3950, NULL, NULL, 37, 162, 'مَآ أَنتُمْ عَلَيْهِ بِفَٰتِنِينَ', 'ما أنتم عليه بفاتنين', 23, 46, 452, 181, '37:162'),
(3951, NULL, NULL, 37, 163, 'إِلَّا مَنْ هُوَ صَالِ ٱلْجَحِيمِ', 'إلا من هو صال الجحيم', 23, 46, 452, 181, '37:163'),
(3952, NULL, NULL, 37, 164, 'وَمَا مِنَّآ إِلَّا لَهُۥ مَقَامٌ مَّعْلُومٌ', 'وما منا إلا له مقام معلوم', 23, 46, 452, 181, '37:164'),
(3953, NULL, NULL, 37, 165, 'وَإِنَّا لَنَحْنُ ٱلصَّآفُّونَ', 'وإنا لنحن الصافون', 23, 46, 452, 181, '37:165'),
(3954, NULL, NULL, 37, 166, 'وَإِنَّا لَنَحْنُ ٱلْمُسَبِّحُونَ', 'وإنا لنحن المسبحون', 23, 46, 452, 181, '37:166'),
(3955, NULL, NULL, 37, 167, 'وَإِن كَانُوا۟ لَيَقُولُونَ', 'وإن كانوا ليقولون', 23, 46, 452, 181, '37:167'),
(3956, NULL, NULL, 37, 168, 'لَوْ أَنَّ عِندَنَا ذِكْرًا مِّنَ ٱلْأَوَّلِينَ', 'لو أن عندنا ذكرا من الأولين', 23, 46, 452, 181, '37:168'),
(3957, NULL, NULL, 37, 169, 'لَكُنَّا عِبَادَ ٱللَّهِ ٱلْمُخْلَصِينَ', 'لكنا عباد الله المخلصين', 23, 46, 452, 181, '37:169'),
(3958, NULL, NULL, 37, 170, 'فَكَفَرُوا۟ بِهِۦ فَسَوْفَ يَعْلَمُونَ', 'فكفروا به فسوف يعلمون', 23, 46, 452, 181, '37:170'),
(3959, NULL, NULL, 37, 171, 'وَلَقَدْ سَبَقَتْ كَلِمَتُنَا لِعِبَادِنَا ٱلْمُرْسَلِينَ', 'ولقد سبقت كلمتنا لعبادنا المرسلين', 23, 46, 452, 181, '37:171'),
(3960, NULL, NULL, 37, 172, 'إِنَّهُمْ لَهُمُ ٱلْمَنصُورُونَ', 'إنهم لهم المنصورون', 23, 46, 452, 181, '37:172'),
(3961, NULL, NULL, 37, 173, 'وَإِنَّ جُندَنَا لَهُمُ ٱلْغَٰلِبُونَ', 'وإن جندنا لهم الغالبون', 23, 46, 452, 181, '37:173'),
(3962, NULL, NULL, 37, 174, 'فَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍ', 'فتول عنهم حتى حين', 23, 46, 452, 181, '37:174'),
(3963, NULL, NULL, 37, 175, 'وَأَبْصِرْهُمْ فَسَوْفَ يُبْصِرُونَ', 'وأبصرهم فسوف يبصرون', 23, 46, 452, 181, '37:175'),
(3964, NULL, NULL, 37, 176, 'أَفَبِعَذَابِنَا يَسْتَعْجِلُونَ', 'أفبعذابنا يستعجلون', 23, 46, 452, 181, '37:176'),
(3965, NULL, NULL, 37, 177, 'فَإِذَا نَزَلَ بِسَاحَتِهِمْ فَسَآءَ صَبَاحُ ٱلْمُنذَرِينَ', 'فإذا نزل بساحتهم فساء صباح المنذرين', 23, 46, 452, 181, '37:177'),
(3966, NULL, NULL, 37, 178, 'وَتَوَلَّ عَنْهُمْ حَتَّىٰ حِينٍ', 'وتول عنهم حتى حين', 23, 46, 452, 181, '37:178'),
(3967, NULL, NULL, 37, 179, 'وَأَبْصِرْ فَسَوْفَ يُبْصِرُونَ', 'وأبصر فسوف يبصرون', 23, 46, 452, 181, '37:179'),
(3968, NULL, NULL, 37, 180, 'سُبْحَٰنَ رَبِّكَ رَبِّ ٱلْعِزَّةِ عَمَّا يَصِفُونَ', 'سبحان ربك رب العزة عما يصفون', 23, 46, 452, 181, '37:180'),
(3969, NULL, NULL, 37, 181, 'وَسَلَٰمٌ عَلَى ٱلْمُرْسَلِينَ', 'وسلام على المرسلين', 23, 46, 452, 181, '37:181'),
(3970, NULL, NULL, 37, 182, 'وَٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَٰلَمِينَ', 'والحمد لله رب العالمين', 23, 46, 452, 181, '37:182'),
(3971, NULL, NULL, 38, 1, 'صٓ وَٱلْقُرْءَانِ ذِى ٱلذِّكْرِ', 'ص والقرآن ذي الذكر', 23, 46, 453, 181, '38:1'),
(3972, NULL, NULL, 38, 2, 'بَلِ ٱلَّذِينَ كَفَرُوا۟ فِى عِزَّةٍ وَشِقَاقٍ', 'بل الذين كفروا في عزة وشقاق', 23, 46, 453, 181, '38:2'),
(3973, NULL, NULL, 38, 3, 'كَمْ أَهْلَكْنَا مِن قَبْلِهِم مِّن قَرْنٍ فَنَادَوا۟ وَّلَاتَ حِينَ مَنَاصٍ', 'كم أهلكنا من قبلهم من قرن فنادوا ولات حين مناص', 23, 46, 453, 181, '38:3'),
(3974, NULL, NULL, 38, 4, 'وَعَجِبُوٓا۟ أَن جَآءَهُم مُّنذِرٌ مِّنْهُمْ وَقَالَ ٱلْكَٰفِرُونَ هَٰذَا سَٰحِرٌ كَذَّابٌ', 'وعجبوا أن جاءهم منذر منهم وقال الكافرون هذا ساحر كذاب', 23, 46, 453, 181, '38:4'),
(3975, NULL, NULL, 38, 5, 'أَجَعَلَ ٱلْءَالِهَةَ إِلَٰهًا وَٰحِدًا إِنَّ هَٰذَا لَشَىْءٌ عُجَابٌ', 'أجعل الآلهة إلها واحدا إن هذا لشيء عجاب', 23, 46, 453, 181, '38:5'),
(3976, NULL, NULL, 38, 6, 'وَٱنطَلَقَ ٱلْمَلَأُ مِنْهُمْ أَنِ ٱمْشُوا۟ وَٱصْبِرُوا۟ عَلَىٰٓ ءَالِهَتِكُمْ إِنَّ هَٰذَا لَشَىْءٌ يُرَادُ', 'وانطلق الملأ منهم أن امشوا واصبروا على آلهتكم إن هذا لشيء يراد', 23, 46, 453, 181, '38:6'),
(3977, NULL, NULL, 38, 7, 'مَا سَمِعْنَا بِهَٰذَا فِى ٱلْمِلَّةِ ٱلْءَاخِرَةِ إِنْ هَٰذَآ إِلَّا ٱخْتِلَٰقٌ', 'ما سمعنا بهذا في الملة الآخرة إن هذا إلا اختلاق', 23, 46, 453, 181, '38:7'),
(3978, NULL, NULL, 38, 8, 'أَءُنزِلَ عَلَيْهِ ٱلذِّكْرُ مِنۢ بَيْنِنَا بَلْ هُمْ فِى شَكٍّ مِّن ذِكْرِى بَل لَّمَّا يَذُوقُوا۟ عَذَابِ', 'أأنزل عليه الذكر من بيننا بل هم في شك من ذكري بل لما يذوقوا عذاب', 23, 46, 453, 181, '38:8'),
(3979, NULL, NULL, 38, 9, 'أَمْ عِندَهُمْ خَزَآئِنُ رَحْمَةِ رَبِّكَ ٱلْعَزِيزِ ٱلْوَهَّابِ', 'أم عندهم خزائن رحمة ربك العزيز الوهاب', 23, 46, 453, 181, '38:9'),
(3980, NULL, NULL, 38, 10, 'أَمْ لَهُم مُّلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا فَلْيَرْتَقُوا۟ فِى ٱلْأَسْبَٰبِ', 'أم لهم ملك السماوات والأرض وما بينهما فليرتقوا في الأسباب', 23, 46, 453, 181, '38:10'),
(3981, NULL, NULL, 38, 11, 'جُندٌ مَّا هُنَالِكَ مَهْزُومٌ مِّنَ ٱلْأَحْزَابِ', 'جند ما هنالك مهزوم من الأحزاب', 23, 46, 453, 181, '38:11'),
(3982, NULL, NULL, 38, 12, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَعَادٌ وَفِرْعَوْنُ ذُو ٱلْأَوْتَادِ', 'كذبت قبلهم قوم نوح وعاد وفرعون ذو الأوتاد', 23, 46, 453, 181, '38:12'),
(3983, NULL, NULL, 38, 13, 'وَثَمُودُ وَقَوْمُ لُوطٍ وَأَصْحَٰبُ لْـَٔيْكَةِ أُو۟لَٰٓئِكَ ٱلْأَحْزَابُ', 'وثمود وقوم لوط وأصحاب الأيكة أولئك الأحزاب', 23, 46, 453, 181, '38:13'),
(3984, NULL, NULL, 38, 14, 'إِن كُلٌّ إِلَّا كَذَّبَ ٱلرُّسُلَ فَحَقَّ عِقَابِ', 'إن كل إلا كذب الرسل فحق عقاب', 23, 46, 453, 181, '38:14'),
(3985, NULL, NULL, 38, 15, 'وَمَا يَنظُرُ هَٰٓؤُلَآءِ إِلَّا صَيْحَةً وَٰحِدَةً مَّا لَهَا مِن فَوَاقٍ', 'وما ينظر هؤلاء إلا صيحة واحدة ما لها من فواق', 23, 46, 453, 181, '38:15'),
(3986, NULL, NULL, 38, 16, 'وَقَالُوا۟ رَبَّنَا عَجِّل لَّنَا قِطَّنَا قَبْلَ يَوْمِ ٱلْحِسَابِ', 'وقالوا ربنا عجل لنا قطنا قبل يوم الحساب', 23, 46, 453, 181, '38:16'),
(3987, NULL, NULL, 38, 17, 'ٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَٱذْكُرْ عَبْدَنَا دَاوُۥدَ ذَا ٱلْأَيْدِ إِنَّهُۥٓ أَوَّابٌ', 'اصبر على ما يقولون واذكر عبدنا داوود ذا الأيد إنه أواب', 23, 46, 454, 181, '38:17'),
(3988, NULL, NULL, 38, 18, 'إِنَّا سَخَّرْنَا ٱلْجِبَالَ مَعَهُۥ يُسَبِّحْنَ بِٱلْعَشِىِّ وَٱلْإِشْرَاقِ', 'إنا سخرنا الجبال معه يسبحن بالعشي والإشراق', 23, 46, 454, 181, '38:18'),
(3989, NULL, NULL, 38, 19, 'وَٱلطَّيْرَ مَحْشُورَةً كُلٌّ لَّهُۥٓ أَوَّابٌ', 'والطير محشورة كل له أواب', 23, 46, 454, 181, '38:19'),
(3990, NULL, NULL, 38, 20, 'وَشَدَدْنَا مُلْكَهُۥ وَءَاتَيْنَٰهُ ٱلْحِكْمَةَ وَفَصْلَ ٱلْخِطَابِ', 'وشددنا ملكه وآتيناه الحكمة وفصل الخطاب', 23, 46, 454, 181, '38:20'),
(3991, NULL, NULL, 38, 21, 'وَهَلْ أَتَىٰكَ نَبَؤُا۟ ٱلْخَصْمِ إِذْ تَسَوَّرُوا۟ ٱلْمِحْرَابَ', 'وهل أتاك نبأ الخصم إذ تسوروا المحراب', 23, 46, 454, 182, '38:21'),
(3992, NULL, NULL, 38, 22, 'إِذْ دَخَلُوا۟ عَلَىٰ دَاوُۥدَ فَفَزِعَ مِنْهُمْ قَالُوا۟ لَا تَخَفْ خَصْمَانِ بَغَىٰ بَعْضُنَا عَلَىٰ بَعْضٍ فَٱحْكُم بَيْنَنَا بِٱلْحَقِّ وَلَا تُشْطِطْ وَٱهْدِنَآ إِلَىٰ سَوَآءِ ٱلصِّرَٰطِ', 'إذ دخلوا على داوود ففزع منهم قالوا لا تخف خصمان بغى بعضنا على بعض فاحكم بيننا بالحق ولا تشطط واهدنا إلى سواء الصراط', 23, 46, 454, 182, '38:22'),
(3993, NULL, NULL, 38, 23, 'إِنَّ هَٰذَآ أَخِى لَهُۥ تِسْعٌ وَتِسْعُونَ نَعْجَةً وَلِىَ نَعْجَةٌ وَٰحِدَةٌ فَقَالَ أَكْفِلْنِيهَا وَعَزَّنِى فِى ٱلْخِطَابِ', 'إن هذا أخي له تسع وتسعون نعجة ولي نعجة واحدة فقال أكفلنيها وعزني في الخطاب', 23, 46, 454, 182, '38:23'),
(3994, NULL, NULL, 38, 24, 'قَالَ لَقَدْ ظَلَمَكَ بِسُؤَالِ نَعْجَتِكَ إِلَىٰ نِعَاجِهِۦ وَإِنَّ كَثِيرًا مِّنَ ٱلْخُلَطَآءِ لَيَبْغِى بَعْضُهُمْ عَلَىٰ بَعْضٍ إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَقَلِيلٌ مَّا هُمْ وَظَنَّ دَاوُۥدُ أَنَّمَا فَتَنَّٰهُ فَٱسْتَغْفَرَ رَبَّهُۥ وَخَرَّ رَاكِعًا وَأَنَابَ', 'قال لقد ظلمك بسؤال نعجتك إلى نعاجه وإن كثيرا من الخلطاء ليبغي بعضهم على بعض إلا الذين آمنوا وعملوا الصالحات وقليل ما هم وظن داوود أنما فتناه فاستغفر ربه وخر راكعا وأناب', 23, 46, 454, 182, '38:24'),
(3995, NULL, NULL, 38, 25, 'فَغَفَرْنَا لَهُۥ ذَٰلِكَ وَإِنَّ لَهُۥ عِندَنَا لَزُلْفَىٰ وَحُسْنَ مَـَٔابٍ', 'فغفرنا له ذلك وإن له عندنا لزلفى وحسن مآب', 23, 46, 454, 182, '38:25'),
(3996, NULL, NULL, 38, 26, 'يَٰدَاوُۥدُ إِنَّا جَعَلْنَٰكَ خَلِيفَةً فِى ٱلْأَرْضِ فَٱحْكُم بَيْنَ ٱلنَّاسِ بِٱلْحَقِّ وَلَا تَتَّبِعِ ٱلْهَوَىٰ فَيُضِلَّكَ عَن سَبِيلِ ٱللَّهِ إِنَّ ٱلَّذِينَ يَضِلُّونَ عَن سَبِيلِ ٱللَّهِ لَهُمْ عَذَابٌ شَدِيدٌۢ بِمَا نَسُوا۟ يَوْمَ ٱلْحِسَابِ', 'يا داوود إنا جعلناك خليفة في الأرض فاحكم بين الناس بالحق ولا تتبع الهوى فيضلك عن سبيل الله إن الذين يضلون عن سبيل الله لهم عذاب شديد بما نسوا يوم الحساب', 23, 46, 454, 182, '38:26'),
(3997, NULL, NULL, 38, 27, 'وَمَا خَلَقْنَا ٱلسَّمَآءَ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا بَٰطِلًا ذَٰلِكَ ظَنُّ ٱلَّذِينَ كَفَرُوا۟ فَوَيْلٌ لِّلَّذِينَ كَفَرُوا۟ مِنَ ٱلنَّارِ', 'وما خلقنا السماء والأرض وما بينهما باطلا ذلك ظن الذين كفروا فويل للذين كفروا من النار', 23, 46, 455, 182, '38:27'),
(3998, NULL, NULL, 38, 28, 'أَمْ نَجْعَلُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ كَٱلْمُفْسِدِينَ فِى ٱلْأَرْضِ أَمْ نَجْعَلُ ٱلْمُتَّقِينَ كَٱلْفُجَّارِ', 'أم نجعل الذين آمنوا وعملوا الصالحات كالمفسدين في الأرض أم نجعل المتقين كالفجار', 23, 46, 455, 182, '38:28'),
(3999, NULL, NULL, 38, 29, 'كِتَٰبٌ أَنزَلْنَٰهُ إِلَيْكَ مُبَٰرَكٌ لِّيَدَّبَّرُوٓا۟ ءَايَٰتِهِۦ وَلِيَتَذَكَّرَ أُو۟لُوا۟ ٱلْأَلْبَٰبِ', 'كتاب أنزلناه إليك مبارك ليدبروا آياته وليتذكر أولو الألباب', 23, 46, 455, 182, '38:29'),
(4000, NULL, NULL, 38, 30, 'وَوَهَبْنَا لِدَاوُۥدَ سُلَيْمَٰنَ نِعْمَ ٱلْعَبْدُ إِنَّهُۥٓ أَوَّابٌ', 'ووهبنا لداوود سليمان نعم العبد إنه أواب', 23, 46, 455, 182, '38:30'),
(4001, NULL, NULL, 38, 31, 'إِذْ عُرِضَ عَلَيْهِ بِٱلْعَشِىِّ ٱلصَّٰفِنَٰتُ ٱلْجِيَادُ', 'إذ عرض عليه بالعشي الصافنات الجياد', 23, 46, 455, 182, '38:31'),
(4002, NULL, NULL, 38, 32, 'فَقَالَ إِنِّىٓ أَحْبَبْتُ حُبَّ ٱلْخَيْرِ عَن ذِكْرِ رَبِّى حَتَّىٰ تَوَارَتْ بِٱلْحِجَابِ', 'فقال إني أحببت حب الخير عن ذكر ربي حتى توارت بالحجاب', 23, 46, 455, 182, '38:32'),
(4003, NULL, NULL, 38, 33, 'رُدُّوهَا عَلَىَّ فَطَفِقَ مَسْحًۢا بِٱلسُّوقِ وَٱلْأَعْنَاقِ', 'ردوها علي فطفق مسحا بالسوق والأعناق', 23, 46, 455, 182, '38:33'),
(4004, NULL, NULL, 38, 34, 'وَلَقَدْ فَتَنَّا سُلَيْمَٰنَ وَأَلْقَيْنَا عَلَىٰ كُرْسِيِّهِۦ جَسَدًا ثُمَّ أَنَابَ', 'ولقد فتنا سليمان وألقينا على كرسيه جسدا ثم أناب', 23, 46, 455, 182, '38:34'),
(4005, NULL, NULL, 38, 35, 'قَالَ رَبِّ ٱغْفِرْ لِى وَهَبْ لِى مُلْكًا لَّا يَنۢبَغِى لِأَحَدٍ مِّنۢ بَعْدِىٓ إِنَّكَ أَنتَ ٱلْوَهَّابُ', 'قال رب اغفر لي وهب لي ملكا لا ينبغي لأحد من بعدي إنك أنت الوهاب', 23, 46, 455, 182, '38:35'),
(4006, NULL, NULL, 38, 36, 'فَسَخَّرْنَا لَهُ ٱلرِّيحَ تَجْرِى بِأَمْرِهِۦ رُخَآءً حَيْثُ أَصَابَ', 'فسخرنا له الريح تجري بأمره رخاء حيث أصاب', 23, 46, 455, 182, '38:36'),
(4007, NULL, NULL, 38, 37, 'وَٱلشَّيَٰطِينَ كُلَّ بَنَّآءٍ وَغَوَّاصٍ', 'والشياطين كل بناء وغواص', 23, 46, 455, 182, '38:37'),
(4008, NULL, NULL, 38, 38, 'وَءَاخَرِينَ مُقَرَّنِينَ فِى ٱلْأَصْفَادِ', 'وآخرين مقرنين في الأصفاد', 23, 46, 455, 182, '38:38'),
(4009, NULL, NULL, 38, 39, 'هَٰذَا عَطَآؤُنَا فَٱمْنُنْ أَوْ أَمْسِكْ بِغَيْرِ حِسَابٍ', 'هذا عطاؤنا فامنن أو أمسك بغير حساب', 23, 46, 455, 182, '38:39'),
(4010, NULL, NULL, 38, 40, 'وَإِنَّ لَهُۥ عِندَنَا لَزُلْفَىٰ وَحُسْنَ مَـَٔابٍ', 'وإن له عندنا لزلفى وحسن مآب', 23, 46, 455, 182, '38:40'),
(4011, NULL, NULL, 38, 41, 'وَٱذْكُرْ عَبْدَنَآ أَيُّوبَ إِذْ نَادَىٰ رَبَّهُۥٓ أَنِّى مَسَّنِىَ ٱلشَّيْطَٰنُ بِنُصْبٍ وَعَذَابٍ', 'واذكر عبدنا أيوب إذ نادى ربه أني مسني الشيطان بنصب وعذاب', 23, 46, 455, 182, '38:41'),
(4012, NULL, NULL, 38, 42, 'ٱرْكُضْ بِرِجْلِكَ هَٰذَا مُغْتَسَلٌۢ بَارِدٌ وَشَرَابٌ', 'اركض برجلك هذا مغتسل بارد وشراب', 23, 46, 455, 182, '38:42'),
(4013, NULL, NULL, 38, 43, 'وَوَهَبْنَا لَهُۥٓ أَهْلَهُۥ وَمِثْلَهُم مَّعَهُمْ رَحْمَةً مِّنَّا وَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَٰبِ', 'ووهبنا له أهله ومثلهم معهم رحمة منا وذكرى لأولي الألباب', 23, 46, 456, 182, '38:43'),
(4014, NULL, NULL, 38, 44, 'وَخُذْ بِيَدِكَ ضِغْثًا فَٱضْرِب بِّهِۦ وَلَا تَحْنَثْ إِنَّا وَجَدْنَٰهُ صَابِرًا نِّعْمَ ٱلْعَبْدُ إِنَّهُۥٓ أَوَّابٌ', 'وخذ بيدك ضغثا فاضرب به ولا تحنث إنا وجدناه صابرا نعم العبد إنه أواب', 23, 46, 456, 182, '38:44'),
(4015, NULL, NULL, 38, 45, 'وَٱذْكُرْ عِبَٰدَنَآ إِبْرَٰهِيمَ وَإِسْحَٰقَ وَيَعْقُوبَ أُو۟لِى ٱلْأَيْدِى وَٱلْأَبْصَٰرِ', 'واذكر عبادنا إبراهيم وإسحاق ويعقوب أولي الأيدي والأبصار', 23, 46, 456, 182, '38:45'),
(4016, NULL, NULL, 38, 46, 'إِنَّآ أَخْلَصْنَٰهُم بِخَالِصَةٍ ذِكْرَى ٱلدَّارِ', 'إنا أخلصناهم بخالصة ذكرى الدار', 23, 46, 456, 182, '38:46'),
(4017, NULL, NULL, 38, 47, 'وَإِنَّهُمْ عِندَنَا لَمِنَ ٱلْمُصْطَفَيْنَ ٱلْأَخْيَارِ', 'وإنهم عندنا لمن المصطفين الأخيار', 23, 46, 456, 182, '38:47'),
(4018, NULL, NULL, 38, 48, 'وَٱذْكُرْ إِسْمَٰعِيلَ وَٱلْيَسَعَ وَذَا ٱلْكِفْلِ وَكُلٌّ مِّنَ ٱلْأَخْيَارِ', 'واذكر إسماعيل واليسع وذا الكفل وكل من الأخيار', 23, 46, 456, 182, '38:48'),
(4019, NULL, NULL, 38, 49, 'هَٰذَا ذِكْرٌ وَإِنَّ لِلْمُتَّقِينَ لَحُسْنَ مَـَٔابٍ', 'هذا ذكر وإن للمتقين لحسن مآب', 23, 46, 456, 182, '38:49'),
(4020, NULL, NULL, 38, 50, 'جَنَّٰتِ عَدْنٍ مُّفَتَّحَةً لَّهُمُ ٱلْأَبْوَٰبُ', 'جنات عدن مفتحة لهم الأبواب', 23, 46, 456, 182, '38:50'),
(4021, NULL, NULL, 38, 51, 'مُتَّكِـِٔينَ فِيهَا يَدْعُونَ فِيهَا بِفَٰكِهَةٍ كَثِيرَةٍ وَشَرَابٍ', 'متكئين فيها يدعون فيها بفاكهة كثيرة وشراب', 23, 46, 456, 182, '38:51'),
(4022, NULL, NULL, 38, 52, 'وَعِندَهُمْ قَٰصِرَٰتُ ٱلطَّرْفِ أَتْرَابٌ', 'وعندهم قاصرات الطرف أتراب', 23, 46, 456, 183, '38:52'),
(4023, NULL, NULL, 38, 53, 'هَٰذَا مَا تُوعَدُونَ لِيَوْمِ ٱلْحِسَابِ', 'هذا ما توعدون ليوم الحساب', 23, 46, 456, 183, '38:53'),
(4024, NULL, NULL, 38, 54, 'إِنَّ هَٰذَا لَرِزْقُنَا مَا لَهُۥ مِن نَّفَادٍ', 'إن هذا لرزقنا ما له من نفاد', 23, 46, 456, 183, '38:54'),
(4025, NULL, NULL, 38, 55, 'هَٰذَا وَإِنَّ لِلطَّٰغِينَ لَشَرَّ مَـَٔابٍ', 'هذا وإن للطاغين لشر مآب', 23, 46, 456, 183, '38:55'),
(4026, NULL, NULL, 38, 56, 'جَهَنَّمَ يَصْلَوْنَهَا فَبِئْسَ ٱلْمِهَادُ', 'جهنم يصلونها فبئس المهاد', 23, 46, 456, 183, '38:56'),
(4027, NULL, NULL, 38, 57, 'هَٰذَا فَلْيَذُوقُوهُ حَمِيمٌ وَغَسَّاقٌ', 'هذا فليذوقوه حميم وغساق', 23, 46, 456, 183, '38:57'),
(4028, NULL, NULL, 38, 58, 'وَءَاخَرُ مِن شَكْلِهِۦٓ أَزْوَٰجٌ', 'وآخر من شكله أزواج', 23, 46, 456, 183, '38:58'),
(4029, NULL, NULL, 38, 59, 'هَٰذَا فَوْجٌ مُّقْتَحِمٌ مَّعَكُمْ لَا مَرْحَبًۢا بِهِمْ إِنَّهُمْ صَالُوا۟ ٱلنَّارِ', 'هذا فوج مقتحم معكم لا مرحبا بهم إنهم صالو النار', 23, 46, 456, 183, '38:59'),
(4030, NULL, NULL, 38, 60, 'قَالُوا۟ بَلْ أَنتُمْ لَا مَرْحَبًۢا بِكُمْ أَنتُمْ قَدَّمْتُمُوهُ لَنَا فَبِئْسَ ٱلْقَرَارُ', 'قالوا بل أنتم لا مرحبا بكم أنتم قدمتموه لنا فبئس القرار', 23, 46, 456, 183, '38:60'),
(4031, NULL, NULL, 38, 61, 'قَالُوا۟ رَبَّنَا مَن قَدَّمَ لَنَا هَٰذَا فَزِدْهُ عَذَابًا ضِعْفًا فِى ٱلنَّارِ', 'قالوا ربنا من قدم لنا هذا فزده عذابا ضعفا في النار', 23, 46, 456, 183, '38:61'),
(4032, NULL, NULL, 38, 62, 'وَقَالُوا۟ مَا لَنَا لَا نَرَىٰ رِجَالًا كُنَّا نَعُدُّهُم مِّنَ ٱلْأَشْرَارِ', 'وقالوا ما لنا لا نرى رجالا كنا نعدهم من الأشرار', 23, 46, 457, 183, '38:62'),
(4033, NULL, NULL, 38, 63, 'أَتَّخَذْنَٰهُمْ سِخْرِيًّا أَمْ زَاغَتْ عَنْهُمُ ٱلْأَبْصَٰرُ', 'أتخذناهم سخريا أم زاغت عنهم الأبصار', 23, 46, 457, 183, '38:63'),
(4034, NULL, NULL, 38, 64, 'إِنَّ ذَٰلِكَ لَحَقٌّ تَخَاصُمُ أَهْلِ ٱلنَّارِ', 'إن ذلك لحق تخاصم أهل النار', 23, 46, 457, 183, '38:64'),
(4035, NULL, NULL, 38, 65, 'قُلْ إِنَّمَآ أَنَا۠ مُنذِرٌ وَمَا مِنْ إِلَٰهٍ إِلَّا ٱللَّهُ ٱلْوَٰحِدُ ٱلْقَهَّارُ', 'قل إنما أنا منذر وما من إله إلا الله الواحد القهار', 23, 46, 457, 183, '38:65'),
(4036, NULL, NULL, 38, 66, 'رَبُّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا ٱلْعَزِيزُ ٱلْغَفَّٰرُ', 'رب السماوات والأرض وما بينهما العزيز الغفار', 23, 46, 457, 183, '38:66'),
(4037, NULL, NULL, 38, 67, 'قُلْ هُوَ نَبَؤٌا۟ عَظِيمٌ', 'قل هو نبأ عظيم', 23, 46, 457, 183, '38:67'),
(4038, NULL, NULL, 38, 68, 'أَنتُمْ عَنْهُ مُعْرِضُونَ', 'أنتم عنه معرضون', 23, 46, 457, 183, '38:68'),
(4039, NULL, NULL, 38, 69, 'مَا كَانَ لِىَ مِنْ عِلْمٍۭ بِٱلْمَلَإِ ٱلْأَعْلَىٰٓ إِذْ يَخْتَصِمُونَ', 'ما كان لي من علم بالملإ الأعلى إذ يختصمون', 23, 46, 457, 183, '38:69'),
(4040, NULL, NULL, 38, 70, 'إِن يُوحَىٰٓ إِلَىَّ إِلَّآ أَنَّمَآ أَنَا۠ نَذِيرٌ مُّبِينٌ', 'إن يوحى إلي إلا أنما أنا نذير مبين', 23, 46, 457, 183, '38:70'),
(4041, NULL, NULL, 38, 71, 'إِذْ قَالَ رَبُّكَ لِلْمَلَٰٓئِكَةِ إِنِّى خَٰلِقٌۢ بَشَرًا مِّن طِينٍ', 'إذ قال ربك للملائكة إني خالق بشرا من طين', 23, 46, 457, 183, '38:71'),
(4042, NULL, NULL, 38, 72, 'فَإِذَا سَوَّيْتُهُۥ وَنَفَخْتُ فِيهِ مِن رُّوحِى فَقَعُوا۟ لَهُۥ سَٰجِدِينَ', 'فإذا سويته ونفخت فيه من روحي فقعوا له ساجدين', 23, 46, 457, 183, '38:72'),
(4043, NULL, NULL, 38, 73, 'فَسَجَدَ ٱلْمَلَٰٓئِكَةُ كُلُّهُمْ أَجْمَعُونَ', 'فسجد الملائكة كلهم أجمعون', 23, 46, 457, 183, '38:73'),
(4044, NULL, NULL, 38, 74, 'إِلَّآ إِبْلِيسَ ٱسْتَكْبَرَ وَكَانَ مِنَ ٱلْكَٰفِرِينَ', 'إلا إبليس استكبر وكان من الكافرين', 23, 46, 457, 183, '38:74'),
(4045, NULL, NULL, 38, 75, 'قَالَ يَٰٓإِبْلِيسُ مَا مَنَعَكَ أَن تَسْجُدَ لِمَا خَلَقْتُ بِيَدَىَّ أَسْتَكْبَرْتَ أَمْ كُنتَ مِنَ ٱلْعَالِينَ', 'قال يا إبليس ما منعك أن تسجد لما خلقت بيدي أستكبرت أم كنت من العالين', 23, 46, 457, 183, '38:75'),
(4046, NULL, NULL, 38, 76, 'قَالَ أَنَا۠ خَيْرٌ مِّنْهُ خَلَقْتَنِى مِن نَّارٍ وَخَلَقْتَهُۥ مِن طِينٍ', 'قال أنا خير منه خلقتني من نار وخلقته من طين', 23, 46, 457, 183, '38:76'),
(4047, NULL, NULL, 38, 77, 'قَالَ فَٱخْرُجْ مِنْهَا فَإِنَّكَ رَجِيمٌ', 'قال فاخرج منها فإنك رجيم', 23, 46, 457, 183, '38:77'),
(4048, NULL, NULL, 38, 78, 'وَإِنَّ عَلَيْكَ لَعْنَتِىٓ إِلَىٰ يَوْمِ ٱلدِّينِ', 'وإن عليك لعنتي إلى يوم الدين', 23, 46, 457, 183, '38:78'),
(4049, NULL, NULL, 38, 79, 'قَالَ رَبِّ فَأَنظِرْنِىٓ إِلَىٰ يَوْمِ يُبْعَثُونَ', 'قال رب فأنظرني إلى يوم يبعثون', 23, 46, 457, 183, '38:79'),
(4050, NULL, NULL, 38, 80, 'قَالَ فَإِنَّكَ مِنَ ٱلْمُنظَرِينَ', 'قال فإنك من المنظرين', 23, 46, 457, 183, '38:80'),
(4051, NULL, NULL, 38, 81, 'إِلَىٰ يَوْمِ ٱلْوَقْتِ ٱلْمَعْلُومِ', 'إلى يوم الوقت المعلوم', 23, 46, 457, 183, '38:81'),
(4052, NULL, NULL, 38, 82, 'قَالَ فَبِعِزَّتِكَ لَأُغْوِيَنَّهُمْ أَجْمَعِينَ', 'قال فبعزتك لأغوينهم أجمعين', 23, 46, 457, 183, '38:82'),
(4053, NULL, NULL, 38, 83, 'إِلَّا عِبَادَكَ مِنْهُمُ ٱلْمُخْلَصِينَ', 'إلا عبادك منهم المخلصين', 23, 46, 457, 183, '38:83'),
(4054, NULL, NULL, 38, 84, 'قَالَ فَٱلْحَقُّ وَٱلْحَقَّ أَقُولُ', 'قال فالحق والحق أقول', 23, 46, 458, 183, '38:84'),
(4055, NULL, NULL, 38, 85, 'لَأَمْلَأَنَّ جَهَنَّمَ مِنكَ وَمِمَّن تَبِعَكَ مِنْهُمْ أَجْمَعِينَ', 'لأملأن جهنم منك وممن تبعك منهم أجمعين', 23, 46, 458, 183, '38:85'),
(4056, NULL, NULL, 38, 86, 'قُلْ مَآ أَسْـَٔلُكُمْ عَلَيْهِ مِنْ أَجْرٍ وَمَآ أَنَا۠ مِنَ ٱلْمُتَكَلِّفِينَ', 'قل ما أسألكم عليه من أجر وما أنا من المتكلفين', 23, 46, 458, 183, '38:86'),
(4057, NULL, NULL, 38, 87, 'إِنْ هُوَ إِلَّا ذِكْرٌ لِّلْعَٰلَمِينَ', 'إن هو إلا ذكر للعالمين', 23, 46, 458, 183, '38:87'),
(4058, NULL, NULL, 38, 88, 'وَلَتَعْلَمُنَّ نَبَأَهُۥ بَعْدَ حِينٍۭ', 'ولتعلمن نبأه بعد حين', 23, 46, 458, 183, '38:88'),
(4059, NULL, NULL, 39, 1, 'تَنزِيلُ ٱلْكِتَٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْحَكِيمِ', 'تنزيل الكتاب من الله العزيز الحكيم', 23, 46, 458, 183, '39:1'),
(4060, NULL, NULL, 39, 2, 'إِنَّآ أَنزَلْنَآ إِلَيْكَ ٱلْكِتَٰبَ بِٱلْحَقِّ فَٱعْبُدِ ٱللَّهَ مُخْلِصًا لَّهُ ٱلدِّينَ', 'إنا أنزلنا إليك الكتاب بالحق فاعبد الله مخلصا له الدين', 23, 46, 458, 183, '39:2'),
(4061, NULL, NULL, 39, 3, 'أَلَا لِلَّهِ ٱلدِّينُ ٱلْخَالِصُ وَٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ مَا نَعْبُدُهُمْ إِلَّا لِيُقَرِّبُونَآ إِلَى ٱللَّهِ زُلْفَىٰٓ إِنَّ ٱللَّهَ يَحْكُمُ بَيْنَهُمْ فِى مَا هُمْ فِيهِ يَخْتَلِفُونَ إِنَّ ٱللَّهَ لَا يَهْدِى مَنْ هُوَ كَٰذِبٌ كَفَّارٌ', 'ألا لله الدين الخالص والذين اتخذوا من دونه أولياء ما نعبدهم إلا ليقربونا إلى الله زلفى إن الله يحكم بينهم في ما هم فيه يختلفون إن الله لا يهدي من هو كاذب كفار', 23, 46, 458, 183, '39:3'),
(4062, NULL, NULL, 39, 4, 'لَّوْ أَرَادَ ٱللَّهُ أَن يَتَّخِذَ وَلَدًا لَّٱصْطَفَىٰ مِمَّا يَخْلُقُ مَا يَشَآءُ سُبْحَٰنَهُۥ هُوَ ٱللَّهُ ٱلْوَٰحِدُ ٱلْقَهَّارُ', 'لو أراد الله أن يتخذ ولدا لاصطفى مما يخلق ما يشاء سبحانه هو الله الواحد القهار', 23, 46, 458, 183, '39:4'),
(4063, NULL, NULL, 39, 5, 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ يُكَوِّرُ ٱلَّيْلَ عَلَى ٱلنَّهَارِ وَيُكَوِّرُ ٱلنَّهَارَ عَلَى ٱلَّيْلِ وَسَخَّرَ ٱلشَّمْسَ وَٱلْقَمَرَ كُلٌّ يَجْرِى لِأَجَلٍ مُّسَمًّى أَلَا هُوَ ٱلْعَزِيزُ ٱلْغَفَّٰرُ', 'خلق السماوات والأرض بالحق يكور الليل على النهار ويكور النهار على الليل وسخر الشمس والقمر كل يجري لأجل مسمى ألا هو العزيز الغفار', 23, 46, 458, 183, '39:5'),
(4064, NULL, NULL, 39, 6, 'خَلَقَكُم مِّن نَّفْسٍ وَٰحِدَةٍ ثُمَّ جَعَلَ مِنْهَا زَوْجَهَا وَأَنزَلَ لَكُم مِّنَ ٱلْأَنْعَٰمِ ثَمَٰنِيَةَ أَزْوَٰجٍ يَخْلُقُكُمْ فِى بُطُونِ أُمَّهَٰتِكُمْ خَلْقًا مِّنۢ بَعْدِ خَلْقٍ فِى ظُلُمَٰتٍ ثَلَٰثٍ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ لَهُ ٱلْمُلْكُ لَآ إِلَٰهَ إِلَّا هُوَ فَأَنَّىٰ تُصْرَفُونَ', 'خلقكم من نفس واحدة ثم جعل منها زوجها وأنزل لكم من الأنعام ثمانية أزواج يخلقكم في بطون أمهاتكم خلقا من بعد خلق في ظلمات ثلاث ذلكم الله ربكم له الملك لا إله إلا هو فأنى تصرفون', 23, 46, 459, 183, '39:6'),
(4065, NULL, NULL, 39, 7, 'إِن تَكْفُرُوا۟ فَإِنَّ ٱللَّهَ غَنِىٌّ عَنكُمْ وَلَا يَرْضَىٰ لِعِبَادِهِ ٱلْكُفْرَ وَإِن تَشْكُرُوا۟ يَرْضَهُ لَكُمْ وَلَا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ ثُمَّ إِلَىٰ رَبِّكُم مَّرْجِعُكُمْ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ', 'إن تكفروا فإن الله غني عنكم ولا يرضى لعباده الكفر وإن تشكروا يرضه لكم ولا تزر وازرة وزر أخرى ثم إلى ربكم مرجعكم فينبئكم بما كنتم تعملون إنه عليم بذات الصدور', 23, 46, 459, 183, '39:7'),
(4066, NULL, NULL, 39, 8, 'وَإِذَا مَسَّ ٱلْإِنسَٰنَ ضُرٌّ دَعَا رَبَّهُۥ مُنِيبًا إِلَيْهِ ثُمَّ إِذَا خَوَّلَهُۥ نِعْمَةً مِّنْهُ نَسِىَ مَا كَانَ يَدْعُوٓا۟ إِلَيْهِ مِن قَبْلُ وَجَعَلَ لِلَّهِ أَندَادًا لِّيُضِلَّ عَن سَبِيلِهِۦ قُلْ تَمَتَّعْ بِكُفْرِكَ قَلِيلًا إِنَّكَ مِنْ أَصْحَٰبِ ٱلنَّارِ', 'وإذا مس الإنسان ضر دعا ربه منيبا إليه ثم إذا خوله نعمة منه نسي ما كان يدعو إليه من قبل وجعل لله أندادا ليضل عن سبيله قل تمتع بكفرك قليلا إنك من أصحاب النار', 23, 46, 459, 184, '39:8'),
(4067, NULL, NULL, 39, 9, 'أَمَّنْ هُوَ قَٰنِتٌ ءَانَآءَ ٱلَّيْلِ سَاجِدًا وَقَآئِمًا يَحْذَرُ ٱلْءَاخِرَةَ وَيَرْجُوا۟ رَحْمَةَ رَبِّهِۦ قُلْ هَلْ يَسْتَوِى ٱلَّذِينَ يَعْلَمُونَ وَٱلَّذِينَ لَا يَعْلَمُونَ إِنَّمَا يَتَذَكَّرُ أُو۟لُوا۟ ٱلْأَلْبَٰبِ', 'أمن هو قانت آناء الليل ساجدا وقائما يحذر الآخرة ويرجو رحمة ربه قل هل يستوي الذين يعلمون والذين لا يعلمون إنما يتذكر أولو الألباب', 23, 46, 459, 184, '39:9'),
(4068, NULL, NULL, 39, 10, 'قُلْ يَٰعِبَادِ ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ رَبَّكُمْ لِلَّذِينَ أَحْسَنُوا۟ فِى هَٰذِهِ ٱلدُّنْيَا حَسَنَةٌ وَأَرْضُ ٱللَّهِ وَٰسِعَةٌ إِنَّمَا يُوَفَّى ٱلصَّٰبِرُونَ أَجْرَهُم بِغَيْرِ حِسَابٍ', 'قل يا عباد الذين آمنوا اتقوا ربكم للذين أحسنوا في هذه الدنيا حسنة وأرض الله واسعة إنما يوفى الصابرون أجرهم بغير حساب', 23, 46, 459, 184, '39:10'),
(4069, NULL, NULL, 39, 11, 'قُلْ إِنِّىٓ أُمِرْتُ أَنْ أَعْبُدَ ٱللَّهَ مُخْلِصًا لَّهُ ٱلدِّينَ', 'قل إني أمرت أن أعبد الله مخلصا له الدين', 23, 46, 460, 184, '39:11'),
(4070, NULL, NULL, 39, 12, 'وَأُمِرْتُ لِأَنْ أَكُونَ أَوَّلَ ٱلْمُسْلِمِينَ', 'وأمرت لأن أكون أول المسلمين', 23, 46, 460, 184, '39:12'),
(4071, NULL, NULL, 39, 13, 'قُلْ إِنِّىٓ أَخَافُ إِنْ عَصَيْتُ رَبِّى عَذَابَ يَوْمٍ عَظِيمٍ', 'قل إني أخاف إن عصيت ربي عذاب يوم عظيم', 23, 46, 460, 184, '39:13'),
(4072, NULL, NULL, 39, 14, 'قُلِ ٱللَّهَ أَعْبُدُ مُخْلِصًا لَّهُۥ دِينِى', 'قل الله أعبد مخلصا له ديني', 23, 46, 460, 184, '39:14'),
(4073, NULL, NULL, 39, 15, 'فَٱعْبُدُوا۟ مَا شِئْتُم مِّن دُونِهِۦ قُلْ إِنَّ ٱلْخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓا۟ أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ ٱلْقِيَٰمَةِ أَلَا ذَٰلِكَ هُوَ ٱلْخُسْرَانُ ٱلْمُبِينُ', 'فاعبدوا ما شئتم من دونه قل إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ألا ذلك هو الخسران المبين', 23, 46, 460, 184, '39:15'),
(4074, NULL, NULL, 39, 16, 'لَهُم مِّن فَوْقِهِمْ ظُلَلٌ مِّنَ ٱلنَّارِ وَمِن تَحْتِهِمْ ظُلَلٌ ذَٰلِكَ يُخَوِّفُ ٱللَّهُ بِهِۦ عِبَادَهُۥ يَٰعِبَادِ فَٱتَّقُونِ', 'لهم من فوقهم ظلل من النار ومن تحتهم ظلل ذلك يخوف الله به عباده يا عباد فاتقون', 23, 46, 460, 184, '39:16'),
(4075, NULL, NULL, 39, 17, 'وَٱلَّذِينَ ٱجْتَنَبُوا۟ ٱلطَّٰغُوتَ أَن يَعْبُدُوهَا وَأَنَابُوٓا۟ إِلَى ٱللَّهِ لَهُمُ ٱلْبُشْرَىٰ فَبَشِّرْ عِبَادِ', 'والذين اجتنبوا الطاغوت أن يعبدوها وأنابوا إلى الله لهم البشرى فبشر عباد', 23, 46, 460, 184, '39:17'),
(4076, NULL, NULL, 39, 18, 'ٱلَّذِينَ يَسْتَمِعُونَ ٱلْقَوْلَ فَيَتَّبِعُونَ أَحْسَنَهُۥٓ أُو۟لَٰٓئِكَ ٱلَّذِينَ هَدَىٰهُمُ ٱللَّهُ وَأُو۟لَٰٓئِكَ هُمْ أُو۟لُوا۟ ٱلْأَلْبَٰبِ', 'الذين يستمعون القول فيتبعون أحسنه أولئك الذين هداهم الله وأولئك هم أولو الألباب', 23, 46, 460, 184, '39:18'),
(4077, NULL, NULL, 39, 19, 'أَفَمَنْ حَقَّ عَلَيْهِ كَلِمَةُ ٱلْعَذَابِ أَفَأَنتَ تُنقِذُ مَن فِى ٱلنَّارِ', 'أفمن حق عليه كلمة العذاب أفأنت تنقذ من في النار', 23, 46, 460, 184, '39:19'),
(4078, NULL, NULL, 39, 20, 'لَٰكِنِ ٱلَّذِينَ ٱتَّقَوْا۟ رَبَّهُمْ لَهُمْ غُرَفٌ مِّن فَوْقِهَا غُرَفٌ مَّبْنِيَّةٌ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ وَعْدَ ٱللَّهِ لَا يُخْلِفُ ٱللَّهُ ٱلْمِيعَادَ', 'لكن الذين اتقوا ربهم لهم غرف من فوقها غرف مبنية تجري من تحتها الأنهار وعد الله لا يخلف الله الميعاد', 23, 46, 460, 184, '39:20'),
(4079, NULL, NULL, 39, 21, 'أَلَمْ تَرَ أَنَّ ٱللَّهَ أَنزَلَ مِنَ ٱلسَّمَآءِ مَآءً فَسَلَكَهُۥ يَنَٰبِيعَ فِى ٱلْأَرْضِ ثُمَّ يُخْرِجُ بِهِۦ زَرْعًا مُّخْتَلِفًا أَلْوَٰنُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصْفَرًّا ثُمَّ يَجْعَلُهُۥ حُطَٰمًا إِنَّ فِى ذَٰلِكَ لَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَٰبِ', 'ألم تر أن الله أنزل من السماء ماء فسلكه ينابيع في الأرض ثم يخرج به زرعا مختلفا ألوانه ثم يهيج فتراه مصفرا ثم يجعله حطاما إن في ذلك لذكرى لأولي الألباب', 23, 46, 460, 184, '39:21'),
(4080, NULL, NULL, 39, 22, 'أَفَمَن شَرَحَ ٱللَّهُ صَدْرَهُۥ لِلْإِسْلَٰمِ فَهُوَ عَلَىٰ نُورٍ مِّن رَّبِّهِۦ فَوَيْلٌ لِّلْقَٰسِيَةِ قُلُوبُهُم مِّن ذِكْرِ ٱللَّهِ أُو۟لَٰٓئِكَ فِى ضَلَٰلٍ مُّبِينٍ', 'أفمن شرح الله صدره للإسلام فهو على نور من ربه فويل للقاسية قلوبهم من ذكر الله أولئك في ضلال مبين', 23, 46, 461, 184, '39:22'),
(4081, NULL, NULL, 39, 23, 'ٱللَّهُ نَزَّلَ أَحْسَنَ ٱلْحَدِيثِ كِتَٰبًا مُّتَشَٰبِهًا مَّثَانِىَ تَقْشَعِرُّ مِنْهُ جُلُودُ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُمْ ثُمَّ تَلِينُ جُلُودُهُمْ وَقُلُوبُهُمْ إِلَىٰ ذِكْرِ ٱللَّهِ ذَٰلِكَ هُدَى ٱللَّهِ يَهْدِى بِهِۦ مَن يَشَآءُ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍ', 'الله نزل أحسن الحديث كتابا متشابها مثاني تقشعر منه جلود الذين يخشون ربهم ثم تلين جلودهم وقلوبهم إلى ذكر الله ذلك هدى الله يهدي به من يشاء ومن يضلل الله فما له من هاد', 23, 46, 461, 184, '39:23'),
(4082, NULL, NULL, 39, 24, 'أَفَمَن يَتَّقِى بِوَجْهِهِۦ سُوٓءَ ٱلْعَذَابِ يَوْمَ ٱلْقِيَٰمَةِ وَقِيلَ لِلظَّٰلِمِينَ ذُوقُوا۟ مَا كُنتُمْ تَكْسِبُونَ', 'أفمن يتقي بوجهه سوء العذاب يوم القيامة وقيل للظالمين ذوقوا ما كنتم تكسبون', 23, 46, 461, 184, '39:24'),
(4083, NULL, NULL, 39, 25, 'كَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ فَأَتَىٰهُمُ ٱلْعَذَابُ مِنْ حَيْثُ لَا يَشْعُرُونَ', 'كذب الذين من قبلهم فأتاهم العذاب من حيث لا يشعرون', 23, 46, 461, 184, '39:25'),
(4084, NULL, NULL, 39, 26, 'فَأَذَاقَهُمُ ٱللَّهُ ٱلْخِزْىَ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَلَعَذَابُ ٱلْءَاخِرَةِ أَكْبَرُ لَوْ كَانُوا۟ يَعْلَمُونَ', 'فأذاقهم الله الخزي في الحياة الدنيا ولعذاب الآخرة أكبر لو كانوا يعلمون', 23, 46, 461, 184, '39:26'),
(4085, NULL, NULL, 39, 27, 'وَلَقَدْ ضَرَبْنَا لِلنَّاسِ فِى هَٰذَا ٱلْقُرْءَانِ مِن كُلِّ مَثَلٍ لَّعَلَّهُمْ يَتَذَكَّرُونَ', 'ولقد ضربنا للناس في هذا القرآن من كل مثل لعلهم يتذكرون', 23, 46, 461, 184, '39:27'),
(4086, NULL, NULL, 39, 28, 'قُرْءَانًا عَرَبِيًّا غَيْرَ ذِى عِوَجٍ لَّعَلَّهُمْ يَتَّقُونَ', 'قرآنا عربيا غير ذي عوج لعلهم يتقون', 23, 46, 461, 184, '39:28'),
(4087, NULL, NULL, 39, 29, 'ضَرَبَ ٱللَّهُ مَثَلًا رَّجُلًا فِيهِ شُرَكَآءُ مُتَشَٰكِسُونَ وَرَجُلًا سَلَمًا لِّرَجُلٍ هَلْ يَسْتَوِيَانِ مَثَلًا ٱلْحَمْدُ لِلَّهِ بَلْ أَكْثَرُهُمْ لَا يَعْلَمُونَ', 'ضرب الله مثلا رجلا فيه شركاء متشاكسون ورجلا سلما لرجل هل يستويان مثلا الحمد لله بل أكثرهم لا يعلمون', 23, 46, 461, 184, '39:29'),
(4088, NULL, NULL, 39, 30, 'إِنَّكَ مَيِّتٌ وَإِنَّهُم مَّيِّتُونَ', 'إنك ميت وإنهم ميتون', 23, 46, 461, 184, '39:30'),
(4089, NULL, NULL, 39, 31, 'ثُمَّ إِنَّكُمْ يَوْمَ ٱلْقِيَٰمَةِ عِندَ رَبِّكُمْ تَخْتَصِمُونَ', 'ثم إنكم يوم القيامة عند ربكم تختصمون', 23, 46, 461, 184, '39:31'),
(4090, NULL, NULL, 39, 32, 'فَمَنْ أَظْلَمُ مِمَّن كَذَبَ عَلَى ٱللَّهِ وَكَذَّبَ بِٱلصِّدْقِ إِذْ جَآءَهُۥٓ أَلَيْسَ فِى جَهَنَّمَ مَثْوًى لِّلْكَٰفِرِينَ', 'فمن أظلم ممن كذب على الله وكذب بالصدق إذ جاءه أليس في جهنم مثوى للكافرين', 24, 47, 462, 185, '39:32'),
(4091, NULL, NULL, 39, 33, 'وَٱلَّذِى جَآءَ بِٱلصِّدْقِ وَصَدَّقَ بِهِۦٓ أُو۟لَٰٓئِكَ هُمُ ٱلْمُتَّقُونَ', 'والذي جاء بالصدق وصدق به أولئك هم المتقون', 24, 47, 462, 185, '39:33'),
(4092, NULL, NULL, 39, 34, 'لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمْ ذَٰلِكَ جَزَآءُ ٱلْمُحْسِنِينَ', 'لهم ما يشاءون عند ربهم ذلك جزاء المحسنين', 24, 47, 462, 185, '39:34'),
(4093, NULL, NULL, 39, 35, 'لِيُكَفِّرَ ٱللَّهُ عَنْهُمْ أَسْوَأَ ٱلَّذِى عَمِلُوا۟ وَيَجْزِيَهُمْ أَجْرَهُم بِأَحْسَنِ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ', 'ليكفر الله عنهم أسوأ الذي عملوا ويجزيهم أجرهم بأحسن الذي كانوا يعملون', 24, 47, 462, 185, '39:35'),
(4094, NULL, NULL, 39, 36, 'أَلَيْسَ ٱللَّهُ بِكَافٍ عَبْدَهُۥ وَيُخَوِّفُونَكَ بِٱلَّذِينَ مِن دُونِهِۦ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍ', 'أليس الله بكاف عبده ويخوفونك بالذين من دونه ومن يضلل الله فما له من هاد', 24, 47, 462, 185, '39:36'),
(4095, NULL, NULL, 39, 37, 'وَمَن يَهْدِ ٱللَّهُ فَمَا لَهُۥ مِن مُّضِلٍّ أَلَيْسَ ٱللَّهُ بِعَزِيزٍ ذِى ٱنتِقَامٍ', 'ومن يهد الله فما له من مضل أليس الله بعزيز ذي انتقام', 24, 47, 462, 185, '39:37'),
(4096, NULL, NULL, 39, 38, 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ ٱللَّهُ قُلْ أَفَرَءَيْتُم مَّا تَدْعُونَ مِن دُونِ ٱللَّهِ إِنْ أَرَادَنِىَ ٱللَّهُ بِضُرٍّ هَلْ هُنَّ كَٰشِفَٰتُ ضُرِّهِۦٓ أَوْ أَرَادَنِى بِرَحْمَةٍ هَلْ هُنَّ مُمْسِكَٰتُ رَحْمَتِهِۦ قُلْ حَسْبِىَ ٱللَّهُ عَلَيْهِ يَتَوَكَّلُ ٱلْمُتَوَكِّلُونَ', 'ولئن سألتهم من خلق السماوات والأرض ليقولن الله قل أفرأيتم ما تدعون من دون الله إن أرادني الله بضر هل هن كاشفات ضره أو أرادني برحمة هل هن ممسكات رحمته قل حسبي الله عليه يتوكل المتوكلون', 24, 47, 462, 185, '39:38'),
(4097, NULL, NULL, 39, 39, 'قُلْ يَٰقَوْمِ ٱعْمَلُوا۟ عَلَىٰ مَكَانَتِكُمْ إِنِّى عَٰمِلٌ فَسَوْفَ تَعْلَمُونَ', 'قل يا قوم اعملوا على مكانتكم إني عامل فسوف تعلمون', 24, 47, 462, 185, '39:39'),
(4098, NULL, NULL, 39, 40, 'مَن يَأْتِيهِ عَذَابٌ يُخْزِيهِ وَيَحِلُّ عَلَيْهِ عَذَابٌ مُّقِيمٌ', 'من يأتيه عذاب يخزيه ويحل عليه عذاب مقيم', 24, 47, 462, 185, '39:40'),
(4099, NULL, NULL, 39, 41, 'إِنَّآ أَنزَلْنَا عَلَيْكَ ٱلْكِتَٰبَ لِلنَّاسِ بِٱلْحَقِّ فَمَنِ ٱهْتَدَىٰ فَلِنَفْسِهِۦ وَمَن ضَلَّ فَإِنَّمَا يَضِلُّ عَلَيْهَا وَمَآ أَنتَ عَلَيْهِم بِوَكِيلٍ', 'إنا أنزلنا عليك الكتاب للناس بالحق فمن اهتدى فلنفسه ومن ضل فإنما يضل عليها وما أنت عليهم بوكيل', 24, 47, 463, 185, '39:41'),
(4100, NULL, NULL, 39, 42, 'ٱللَّهُ يَتَوَفَّى ٱلْأَنفُسَ حِينَ مَوْتِهَا وَٱلَّتِى لَمْ تَمُتْ فِى مَنَامِهَا فَيُمْسِكُ ٱلَّتِى قَضَىٰ عَلَيْهَا ٱلْمَوْتَ وَيُرْسِلُ ٱلْأُخْرَىٰٓ إِلَىٰٓ أَجَلٍ مُّسَمًّى إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يَتَفَكَّرُونَ', 'الله يتوفى الأنفس حين موتها والتي لم تمت في منامها فيمسك التي قضى عليها الموت ويرسل الأخرى إلى أجل مسمى إن في ذلك لآيات لقوم يتفكرون', 24, 47, 463, 185, '39:42'),
(4101, NULL, NULL, 39, 43, 'أَمِ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ شُفَعَآءَ قُلْ أَوَلَوْ كَانُوا۟ لَا يَمْلِكُونَ شَيْـًٔا وَلَا يَعْقِلُونَ', 'أم اتخذوا من دون الله شفعاء قل أولو كانوا لا يملكون شيئا ولا يعقلون', 24, 47, 463, 185, '39:43'),
(4102, NULL, NULL, 39, 44, 'قُل لِّلَّهِ ٱلشَّفَٰعَةُ جَمِيعًا لَّهُۥ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ ثُمَّ إِلَيْهِ تُرْجَعُونَ', 'قل لله الشفاعة جميعا له ملك السماوات والأرض ثم إليه ترجعون', 24, 47, 463, 185, '39:44'),
(4103, NULL, NULL, 39, 45, 'وَإِذَا ذُكِرَ ٱللَّهُ وَحْدَهُ ٱشْمَأَزَّتْ قُلُوبُ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْءَاخِرَةِ وَإِذَا ذُكِرَ ٱلَّذِينَ مِن دُونِهِۦٓ إِذَا هُمْ يَسْتَبْشِرُونَ', 'وإذا ذكر الله وحده اشمأزت قلوب الذين لا يؤمنون بالآخرة وإذا ذكر الذين من دونه إذا هم يستبشرون', 24, 47, 463, 185, '39:45'),
(4104, NULL, NULL, 39, 46, 'قُلِ ٱللَّهُمَّ فَاطِرَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ عَٰلِمَ ٱلْغَيْبِ وَٱلشَّهَٰدَةِ أَنتَ تَحْكُمُ بَيْنَ عِبَادِكَ فِى مَا كَانُوا۟ فِيهِ يَخْتَلِفُونَ', 'قل اللهم فاطر السماوات والأرض عالم الغيب والشهادة أنت تحكم بين عبادك في ما كانوا فيه يختلفون', 24, 47, 463, 185, '39:46'),
(4105, NULL, NULL, 39, 47, 'وَلَوْ أَنَّ لِلَّذِينَ ظَلَمُوا۟ مَا فِى ٱلْأَرْضِ جَمِيعًا وَمِثْلَهُۥ مَعَهُۥ لَٱفْتَدَوْا۟ بِهِۦ مِن سُوٓءِ ٱلْعَذَابِ يَوْمَ ٱلْقِيَٰمَةِ وَبَدَا لَهُم مِّنَ ٱللَّهِ مَا لَمْ يَكُونُوا۟ يَحْتَسِبُونَ', 'ولو أن للذين ظلموا ما في الأرض جميعا ومثله معه لافتدوا به من سوء العذاب يوم القيامة وبدا لهم من الله ما لم يكونوا يحتسبون', 24, 47, 463, 185, '39:47'),
(4106, NULL, NULL, 39, 48, 'وَبَدَا لَهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ', 'وبدا لهم سيئات ما كسبوا وحاق بهم ما كانوا به يستهزئون', 24, 47, 464, 185, '39:48'),
(4107, NULL, NULL, 39, 49, 'فَإِذَا مَسَّ ٱلْإِنسَٰنَ ضُرٌّ دَعَانَا ثُمَّ إِذَا خَوَّلْنَٰهُ نِعْمَةً مِّنَّا قَالَ إِنَّمَآ أُوتِيتُهُۥ عَلَىٰ عِلْمٍۭ بَلْ هِىَ فِتْنَةٌ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 'فإذا مس الإنسان ضر دعانا ثم إذا خولناه نعمة منا قال إنما أوتيته على علم بل هي فتنة ولكن أكثرهم لا يعلمون', 24, 47, 464, 185, '39:49'),
(4108, NULL, NULL, 39, 50, 'قَدْ قَالَهَا ٱلَّذِينَ مِن قَبْلِهِمْ فَمَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يَكْسِبُونَ', 'قد قالها الذين من قبلهم فما أغنى عنهم ما كانوا يكسبون', 24, 47, 464, 185, '39:50'),
(4109, NULL, NULL, 39, 51, 'فَأَصَابَهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ وَٱلَّذِينَ ظَلَمُوا۟ مِنْ هَٰٓؤُلَآءِ سَيُصِيبُهُمْ سَيِّـَٔاتُ مَا كَسَبُوا۟ وَمَا هُم بِمُعْجِزِينَ', 'فأصابهم سيئات ما كسبوا والذين ظلموا من هؤلاء سيصيبهم سيئات ما كسبوا وما هم بمعجزين', 24, 47, 464, 185, '39:51'),
(4110, NULL, NULL, 39, 52, 'أَوَلَمْ يَعْلَمُوٓا۟ أَنَّ ٱللَّهَ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يُؤْمِنُونَ', 'أولم يعلموا أن الله يبسط الرزق لمن يشاء ويقدر إن في ذلك لآيات لقوم يؤمنون', 24, 47, 464, 185, '39:52'),
(4111, NULL, NULL, 39, 53, 'قُلْ يَٰعِبَادِىَ ٱلَّذِينَ أَسْرَفُوا۟ عَلَىٰٓ أَنفُسِهِمْ لَا تَقْنَطُوا۟ مِن رَّحْمَةِ ٱللَّهِ إِنَّ ٱللَّهَ يَغْفِرُ ٱلذُّنُوبَ جَمِيعًا إِنَّهُۥ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ', 'قل يا عبادي الذين أسرفوا على أنفسهم لا تقنطوا من رحمة الله إن الله يغفر الذنوب جميعا إنه هو الغفور الرحيم', 24, 47, 464, 186, '39:53'),
(4112, NULL, NULL, 39, 54, 'وَأَنِيبُوٓا۟ إِلَىٰ رَبِّكُمْ وَأَسْلِمُوا۟ لَهُۥ مِن قَبْلِ أَن يَأْتِيَكُمُ ٱلْعَذَابُ ثُمَّ لَا تُنصَرُونَ', 'وأنيبوا إلى ربكم وأسلموا له من قبل أن يأتيكم العذاب ثم لا تنصرون', 24, 47, 464, 186, '39:54'),
(4113, NULL, NULL, 39, 55, 'وَٱتَّبِعُوٓا۟ أَحْسَنَ مَآ أُنزِلَ إِلَيْكُم مِّن رَّبِّكُم مِّن قَبْلِ أَن يَأْتِيَكُمُ ٱلْعَذَابُ بَغْتَةً وَأَنتُمْ لَا تَشْعُرُونَ', 'واتبعوا أحسن ما أنزل إليكم من ربكم من قبل أن يأتيكم العذاب بغتة وأنتم لا تشعرون', 24, 47, 464, 186, '39:55'),
(4114, NULL, NULL, 39, 56, 'أَن تَقُولَ نَفْسٌ يَٰحَسْرَتَىٰ عَلَىٰ مَا فَرَّطتُ فِى جَنۢبِ ٱللَّهِ وَإِن كُنتُ لَمِنَ ٱلسَّٰخِرِينَ', 'أن تقول نفس يا حسرتا على ما فرطت في جنب الله وإن كنت لمن الساخرين', 24, 47, 464, 186, '39:56');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(4115, NULL, NULL, 39, 57, 'أَوْ تَقُولَ لَوْ أَنَّ ٱللَّهَ هَدَىٰنِى لَكُنتُ مِنَ ٱلْمُتَّقِينَ', 'أو تقول لو أن الله هداني لكنت من المتقين', 24, 47, 465, 186, '39:57'),
(4116, NULL, NULL, 39, 58, 'أَوْ تَقُولَ حِينَ تَرَى ٱلْعَذَابَ لَوْ أَنَّ لِى كَرَّةً فَأَكُونَ مِنَ ٱلْمُحْسِنِينَ', 'أو تقول حين ترى العذاب لو أن لي كرة فأكون من المحسنين', 24, 47, 465, 186, '39:58'),
(4117, NULL, NULL, 39, 59, 'بَلَىٰ قَدْ جَآءَتْكَ ءَايَٰتِى فَكَذَّبْتَ بِهَا وَٱسْتَكْبَرْتَ وَكُنتَ مِنَ ٱلْكَٰفِرِينَ', 'بلى قد جاءتك آياتي فكذبت بها واستكبرت وكنت من الكافرين', 24, 47, 465, 186, '39:59'),
(4118, NULL, NULL, 39, 60, 'وَيَوْمَ ٱلْقِيَٰمَةِ تَرَى ٱلَّذِينَ كَذَبُوا۟ عَلَى ٱللَّهِ وُجُوهُهُم مُّسْوَدَّةٌ أَلَيْسَ فِى جَهَنَّمَ مَثْوًى لِّلْمُتَكَبِّرِينَ', 'ويوم القيامة ترى الذين كذبوا على الله وجوههم مسودة أليس في جهنم مثوى للمتكبرين', 24, 47, 465, 186, '39:60'),
(4119, NULL, NULL, 39, 61, 'وَيُنَجِّى ٱللَّهُ ٱلَّذِينَ ٱتَّقَوْا۟ بِمَفَازَتِهِمْ لَا يَمَسُّهُمُ ٱلسُّوٓءُ وَلَا هُمْ يَحْزَنُونَ', 'وينجي الله الذين اتقوا بمفازتهم لا يمسهم السوء ولا هم يحزنون', 24, 47, 465, 186, '39:61'),
(4120, NULL, NULL, 39, 62, 'ٱللَّهُ خَٰلِقُ كُلِّ شَىْءٍ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ وَكِيلٌ', 'الله خالق كل شيء وهو على كل شيء وكيل', 24, 47, 465, 186, '39:62'),
(4121, NULL, NULL, 39, 63, 'لَّهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَٰتِ ٱللَّهِ أُو۟لَٰٓئِكَ هُمُ ٱلْخَٰسِرُونَ', 'له مقاليد السماوات والأرض والذين كفروا بآيات الله أولئك هم الخاسرون', 24, 47, 465, 186, '39:63'),
(4122, NULL, NULL, 39, 64, 'قُلْ أَفَغَيْرَ ٱللَّهِ تَأْمُرُوٓنِّىٓ أَعْبُدُ أَيُّهَا ٱلْجَٰهِلُونَ', 'قل أفغير الله تأمروني أعبد أيها الجاهلون', 24, 47, 465, 186, '39:64'),
(4123, NULL, NULL, 39, 65, 'وَلَقَدْ أُوحِىَ إِلَيْكَ وَإِلَى ٱلَّذِينَ مِن قَبْلِكَ لَئِنْ أَشْرَكْتَ لَيَحْبَطَنَّ عَمَلُكَ وَلَتَكُونَنَّ مِنَ ٱلْخَٰسِرِينَ', 'ولقد أوحي إليك وإلى الذين من قبلك لئن أشركت ليحبطن عملك ولتكونن من الخاسرين', 24, 47, 465, 186, '39:65'),
(4124, NULL, NULL, 39, 66, 'بَلِ ٱللَّهَ فَٱعْبُدْ وَكُن مِّنَ ٱلشَّٰكِرِينَ', 'بل الله فاعبد وكن من الشاكرين', 24, 47, 465, 186, '39:66'),
(4125, NULL, NULL, 39, 67, 'وَمَا قَدَرُوا۟ ٱللَّهَ حَقَّ قَدْرِهِۦ وَٱلْأَرْضُ جَمِيعًا قَبْضَتُهُۥ يَوْمَ ٱلْقِيَٰمَةِ وَٱلسَّمَٰوَٰتُ مَطْوِيَّٰتٌۢ بِيَمِينِهِۦ سُبْحَٰنَهُۥ وَتَعَٰلَىٰ عَمَّا يُشْرِكُونَ', 'وما قدروا الله حق قدره والأرض جميعا قبضته يوم القيامة والسماوات مطويات بيمينه سبحانه وتعالى عما يشركون', 24, 47, 465, 186, '39:67'),
(4126, NULL, NULL, 39, 68, 'وَنُفِخَ فِى ٱلصُّورِ فَصَعِقَ مَن فِى ٱلسَّمَٰوَٰتِ وَمَن فِى ٱلْأَرْضِ إِلَّا مَن شَآءَ ٱللَّهُ ثُمَّ نُفِخَ فِيهِ أُخْرَىٰ فَإِذَا هُمْ قِيَامٌ يَنظُرُونَ', 'ونفخ في الصور فصعق من في السماوات ومن في الأرض إلا من شاء الله ثم نفخ فيه أخرى فإذا هم قيام ينظرون', 24, 47, 466, 186, '39:68'),
(4127, NULL, NULL, 39, 69, 'وَأَشْرَقَتِ ٱلْأَرْضُ بِنُورِ رَبِّهَا وَوُضِعَ ٱلْكِتَٰبُ وَجِا۟ىٓءَ بِٱلنَّبِيِّۦنَ وَٱلشُّهَدَآءِ وَقُضِىَ بَيْنَهُم بِٱلْحَقِّ وَهُمْ لَا يُظْلَمُونَ', 'وأشرقت الأرض بنور ربها ووضع الكتاب وجيء بالنبيين والشهداء وقضي بينهم بالحق وهم لا يظلمون', 24, 47, 466, 186, '39:69'),
(4128, NULL, NULL, 39, 70, 'وَوُفِّيَتْ كُلُّ نَفْسٍ مَّا عَمِلَتْ وَهُوَ أَعْلَمُ بِمَا يَفْعَلُونَ', 'ووفيت كل نفس ما عملت وهو أعلم بما يفعلون', 24, 47, 466, 186, '39:70'),
(4129, NULL, NULL, 39, 71, 'وَسِيقَ ٱلَّذِينَ كَفَرُوٓا۟ إِلَىٰ جَهَنَّمَ زُمَرًا حَتَّىٰٓ إِذَا جَآءُوهَا فُتِحَتْ أَبْوَٰبُهَا وَقَالَ لَهُمْ خَزَنَتُهَآ أَلَمْ يَأْتِكُمْ رُسُلٌ مِّنكُمْ يَتْلُونَ عَلَيْكُمْ ءَايَٰتِ رَبِّكُمْ وَيُنذِرُونَكُمْ لِقَآءَ يَوْمِكُمْ هَٰذَا قَالُوا۟ بَلَىٰ وَلَٰكِنْ حَقَّتْ كَلِمَةُ ٱلْعَذَابِ عَلَى ٱلْكَٰفِرِينَ', 'وسيق الذين كفروا إلى جهنم زمرا حتى إذا جاءوها فتحت أبوابها وقال لهم خزنتها ألم يأتكم رسل منكم يتلون عليكم آيات ربكم وينذرونكم لقاء يومكم هذا قالوا بلى ولكن حقت كلمة العذاب على الكافرين', 24, 47, 466, 186, '39:71'),
(4130, NULL, NULL, 39, 72, 'قِيلَ ٱدْخُلُوٓا۟ أَبْوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَا فَبِئْسَ مَثْوَى ٱلْمُتَكَبِّرِينَ', 'قيل ادخلوا أبواب جهنم خالدين فيها فبئس مثوى المتكبرين', 24, 47, 466, 186, '39:72'),
(4131, NULL, NULL, 39, 73, 'وَسِيقَ ٱلَّذِينَ ٱتَّقَوْا۟ رَبَّهُمْ إِلَى ٱلْجَنَّةِ زُمَرًا حَتَّىٰٓ إِذَا جَآءُوهَا وَفُتِحَتْ أَبْوَٰبُهَا وَقَالَ لَهُمْ خَزَنَتُهَا سَلَٰمٌ عَلَيْكُمْ طِبْتُمْ فَٱدْخُلُوهَا خَٰلِدِينَ', 'وسيق الذين اتقوا ربهم إلى الجنة زمرا حتى إذا جاءوها وفتحت أبوابها وقال لهم خزنتها سلام عليكم طبتم فادخلوها خالدين', 24, 47, 466, 186, '39:73'),
(4132, NULL, NULL, 39, 74, 'وَقَالُوا۟ ٱلْحَمْدُ لِلَّهِ ٱلَّذِى صَدَقَنَا وَعْدَهُۥ وَأَوْرَثَنَا ٱلْأَرْضَ نَتَبَوَّأُ مِنَ ٱلْجَنَّةِ حَيْثُ نَشَآءُ فَنِعْمَ أَجْرُ ٱلْعَٰمِلِينَ', 'وقالوا الحمد لله الذي صدقنا وعده وأورثنا الأرض نتبوأ من الجنة حيث نشاء فنعم أجر العاملين', 24, 47, 466, 186, '39:74'),
(4133, NULL, NULL, 39, 75, 'وَتَرَى ٱلْمَلَٰٓئِكَةَ حَآفِّينَ مِنْ حَوْلِ ٱلْعَرْشِ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَقُضِىَ بَيْنَهُم بِٱلْحَقِّ وَقِيلَ ٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَٰلَمِينَ', 'وترى الملائكة حافين من حول العرش يسبحون بحمد ربهم وقضي بينهم بالحق وقيل الحمد لله رب العالمين', 24, 47, 467, 186, '39:75'),
(4134, NULL, NULL, 40, 1, 'حمٓ', 'حم', 24, 47, 467, 187, '40:1'),
(4135, NULL, NULL, 40, 2, 'تَنزِيلُ ٱلْكِتَٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْعَلِيمِ', 'تنزيل الكتاب من الله العزيز العليم', 24, 47, 467, 187, '40:2'),
(4136, NULL, NULL, 40, 3, 'غَافِرِ ٱلذَّنۢبِ وَقَابِلِ ٱلتَّوْبِ شَدِيدِ ٱلْعِقَابِ ذِى ٱلطَّوْلِ لَآ إِلَٰهَ إِلَّا هُوَ إِلَيْهِ ٱلْمَصِيرُ', 'غافر الذنب وقابل التوب شديد العقاب ذي الطول لا إله إلا هو إليه المصير', 24, 47, 467, 187, '40:3'),
(4137, NULL, NULL, 40, 4, 'مَا يُجَٰدِلُ فِىٓ ءَايَٰتِ ٱللَّهِ إِلَّا ٱلَّذِينَ كَفَرُوا۟ فَلَا يَغْرُرْكَ تَقَلُّبُهُمْ فِى ٱلْبِلَٰدِ', 'ما يجادل في آيات الله إلا الذين كفروا فلا يغررك تقلبهم في البلاد', 24, 47, 467, 187, '40:4'),
(4138, NULL, NULL, 40, 5, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَٱلْأَحْزَابُ مِنۢ بَعْدِهِمْ وَهَمَّتْ كُلُّ أُمَّةٍۭ بِرَسُولِهِمْ لِيَأْخُذُوهُ وَجَٰدَلُوا۟ بِٱلْبَٰطِلِ لِيُدْحِضُوا۟ بِهِ ٱلْحَقَّ فَأَخَذْتُهُمْ فَكَيْفَ كَانَ عِقَابِ', 'كذبت قبلهم قوم نوح والأحزاب من بعدهم وهمت كل أمة برسولهم ليأخذوه وجادلوا بالباطل ليدحضوا به الحق فأخذتهم فكيف كان عقاب', 24, 47, 467, 187, '40:5'),
(4139, NULL, NULL, 40, 6, 'وَكَذَٰلِكَ حَقَّتْ كَلِمَتُ رَبِّكَ عَلَى ٱلَّذِينَ كَفَرُوٓا۟ أَنَّهُمْ أَصْحَٰبُ ٱلنَّارِ', 'وكذلك حقت كلمت ربك على الذين كفروا أنهم أصحاب النار', 24, 47, 467, 187, '40:6'),
(4140, NULL, NULL, 40, 7, 'ٱلَّذِينَ يَحْمِلُونَ ٱلْعَرْشَ وَمَنْ حَوْلَهُۥ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيُؤْمِنُونَ بِهِۦ وَيَسْتَغْفِرُونَ لِلَّذِينَ ءَامَنُوا۟ رَبَّنَا وَسِعْتَ كُلَّ شَىْءٍ رَّحْمَةً وَعِلْمًا فَٱغْفِرْ لِلَّذِينَ تَابُوا۟ وَٱتَّبَعُوا۟ سَبِيلَكَ وَقِهِمْ عَذَابَ ٱلْجَحِيمِ', 'الذين يحملون العرش ومن حوله يسبحون بحمد ربهم ويؤمنون به ويستغفرون للذين آمنوا ربنا وسعت كل شيء رحمة وعلما فاغفر للذين تابوا واتبعوا سبيلك وقهم عذاب الجحيم', 24, 47, 467, 187, '40:7'),
(4141, NULL, NULL, 40, 8, 'رَبَّنَا وَأَدْخِلْهُمْ جَنَّٰتِ عَدْنٍ ٱلَّتِى وَعَدتَّهُمْ وَمَن صَلَحَ مِنْ ءَابَآئِهِمْ وَأَزْوَٰجِهِمْ وَذُرِّيَّٰتِهِمْ إِنَّكَ أَنتَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'ربنا وأدخلهم جنات عدن التي وعدتهم ومن صلح من آبائهم وأزواجهم وذرياتهم إنك أنت العزيز الحكيم', 24, 47, 468, 187, '40:8'),
(4142, NULL, NULL, 40, 9, 'وَقِهِمُ ٱلسَّيِّـَٔاتِ وَمَن تَقِ ٱلسَّيِّـَٔاتِ يَوْمَئِذٍ فَقَدْ رَحِمْتَهُۥ وَذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْعَظِيمُ', 'وقهم السيئات ومن تق السيئات يومئذ فقد رحمته وذلك هو الفوز العظيم', 24, 47, 468, 187, '40:9'),
(4143, NULL, NULL, 40, 10, 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ يُنَادَوْنَ لَمَقْتُ ٱللَّهِ أَكْبَرُ مِن مَّقْتِكُمْ أَنفُسَكُمْ إِذْ تُدْعَوْنَ إِلَى ٱلْإِيمَٰنِ فَتَكْفُرُونَ', 'إن الذين كفروا ينادون لمقت الله أكبر من مقتكم أنفسكم إذ تدعون إلى الإيمان فتكفرون', 24, 47, 468, 187, '40:10'),
(4144, NULL, NULL, 40, 11, 'قَالُوا۟ رَبَّنَآ أَمَتَّنَا ٱثْنَتَيْنِ وَأَحْيَيْتَنَا ٱثْنَتَيْنِ فَٱعْتَرَفْنَا بِذُنُوبِنَا فَهَلْ إِلَىٰ خُرُوجٍ مِّن سَبِيلٍ', 'قالوا ربنا أمتنا اثنتين وأحييتنا اثنتين فاعترفنا بذنوبنا فهل إلى خروج من سبيل', 24, 47, 468, 187, '40:11'),
(4145, NULL, NULL, 40, 12, 'ذَٰلِكُم بِأَنَّهُۥٓ إِذَا دُعِىَ ٱللَّهُ وَحْدَهُۥ كَفَرْتُمْ وَإِن يُشْرَكْ بِهِۦ تُؤْمِنُوا۟ فَٱلْحُكْمُ لِلَّهِ ٱلْعَلِىِّ ٱلْكَبِيرِ', 'ذلكم بأنه إذا دعي الله وحده كفرتم وإن يشرك به تؤمنوا فالحكم لله العلي الكبير', 24, 47, 468, 187, '40:12'),
(4146, NULL, NULL, 40, 13, 'هُوَ ٱلَّذِى يُرِيكُمْ ءَايَٰتِهِۦ وَيُنَزِّلُ لَكُم مِّنَ ٱلسَّمَآءِ رِزْقًا وَمَا يَتَذَكَّرُ إِلَّا مَن يُنِيبُ', 'هو الذي يريكم آياته وينزل لكم من السماء رزقا وما يتذكر إلا من ينيب', 24, 47, 468, 187, '40:13'),
(4147, NULL, NULL, 40, 14, 'فَٱدْعُوا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ وَلَوْ كَرِهَ ٱلْكَٰفِرُونَ', 'فادعوا الله مخلصين له الدين ولو كره الكافرون', 24, 47, 468, 187, '40:14'),
(4148, NULL, NULL, 40, 15, 'رَفِيعُ ٱلدَّرَجَٰتِ ذُو ٱلْعَرْشِ يُلْقِى ٱلرُّوحَ مِنْ أَمْرِهِۦ عَلَىٰ مَن يَشَآءُ مِنْ عِبَادِهِۦ لِيُنذِرَ يَوْمَ ٱلتَّلَاقِ', 'رفيع الدرجات ذو العرش يلقي الروح من أمره على من يشاء من عباده لينذر يوم التلاق', 24, 47, 468, 187, '40:15'),
(4149, NULL, NULL, 40, 16, 'يَوْمَ هُم بَٰرِزُونَ لَا يَخْفَىٰ عَلَى ٱللَّهِ مِنْهُمْ شَىْءٌ لِّمَنِ ٱلْمُلْكُ ٱلْيَوْمَ لِلَّهِ ٱلْوَٰحِدِ ٱلْقَهَّارِ', 'يوم هم بارزون لا يخفى على الله منهم شيء لمن الملك اليوم لله الواحد القهار', 24, 47, 468, 187, '40:16'),
(4150, NULL, NULL, 40, 17, 'ٱلْيَوْمَ تُجْزَىٰ كُلُّ نَفْسٍۭ بِمَا كَسَبَتْ لَا ظُلْمَ ٱلْيَوْمَ إِنَّ ٱللَّهَ سَرِيعُ ٱلْحِسَابِ', 'اليوم تجزى كل نفس بما كسبت لا ظلم اليوم إن الله سريع الحساب', 24, 47, 469, 187, '40:17'),
(4151, NULL, NULL, 40, 18, 'وَأَنذِرْهُمْ يَوْمَ ٱلْءَازِفَةِ إِذِ ٱلْقُلُوبُ لَدَى ٱلْحَنَاجِرِ كَٰظِمِينَ مَا لِلظَّٰلِمِينَ مِنْ حَمِيمٍ وَلَا شَفِيعٍ يُطَاعُ', 'وأنذرهم يوم الآزفة إذ القلوب لدى الحناجر كاظمين ما للظالمين من حميم ولا شفيع يطاع', 24, 47, 469, 187, '40:18'),
(4152, NULL, NULL, 40, 19, 'يَعْلَمُ خَآئِنَةَ ٱلْأَعْيُنِ وَمَا تُخْفِى ٱلصُّدُورُ', 'يعلم خائنة الأعين وما تخفي الصدور', 24, 47, 469, 187, '40:19'),
(4153, NULL, NULL, 40, 20, 'وَٱللَّهُ يَقْضِى بِٱلْحَقِّ وَٱلَّذِينَ يَدْعُونَ مِن دُونِهِۦ لَا يَقْضُونَ بِشَىْءٍ إِنَّ ٱللَّهَ هُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ', 'والله يقضي بالحق والذين يدعون من دونه لا يقضون بشيء إن الله هو السميع البصير', 24, 47, 469, 187, '40:20'),
(4154, NULL, NULL, 40, 21, 'أَوَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ كَانُوا۟ مِن قَبْلِهِمْ كَانُوا۟ هُمْ أَشَدَّ مِنْهُمْ قُوَّةً وَءَاثَارًا فِى ٱلْأَرْضِ فَأَخَذَهُمُ ٱللَّهُ بِذُنُوبِهِمْ وَمَا كَانَ لَهُم مِّنَ ٱللَّهِ مِن وَاقٍ', 'أولم يسيروا في الأرض فينظروا كيف كان عاقبة الذين كانوا من قبلهم كانوا هم أشد منهم قوة وآثارا في الأرض فأخذهم الله بذنوبهم وما كان لهم من الله من واق', 24, 47, 469, 188, '40:21'),
(4155, NULL, NULL, 40, 22, 'ذَٰلِكَ بِأَنَّهُمْ كَانَت تَّأْتِيهِمْ رُسُلُهُم بِٱلْبَيِّنَٰتِ فَكَفَرُوا۟ فَأَخَذَهُمُ ٱللَّهُ إِنَّهُۥ قَوِىٌّ شَدِيدُ ٱلْعِقَابِ', 'ذلك بأنهم كانت تأتيهم رسلهم بالبينات فكفروا فأخذهم الله إنه قوي شديد العقاب', 24, 47, 469, 188, '40:22'),
(4156, NULL, NULL, 40, 23, 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَٰتِنَا وَسُلْطَٰنٍ مُّبِينٍ', 'ولقد أرسلنا موسى بآياتنا وسلطان مبين', 24, 47, 469, 188, '40:23'),
(4157, NULL, NULL, 40, 24, 'إِلَىٰ فِرْعَوْنَ وَهَٰمَٰنَ وَقَٰرُونَ فَقَالُوا۟ سَٰحِرٌ كَذَّابٌ', 'إلى فرعون وهامان وقارون فقالوا ساحر كذاب', 24, 47, 469, 188, '40:24'),
(4158, NULL, NULL, 40, 25, 'فَلَمَّا جَآءَهُم بِٱلْحَقِّ مِنْ عِندِنَا قَالُوا۟ ٱقْتُلُوٓا۟ أَبْنَآءَ ٱلَّذِينَ ءَامَنُوا۟ مَعَهُۥ وَٱسْتَحْيُوا۟ نِسَآءَهُمْ وَمَا كَيْدُ ٱلْكَٰفِرِينَ إِلَّا فِى ضَلَٰلٍ', 'فلما جاءهم بالحق من عندنا قالوا اقتلوا أبناء الذين آمنوا معه واستحيوا نساءهم وما كيد الكافرين إلا في ضلال', 24, 47, 469, 188, '40:25'),
(4159, NULL, NULL, 40, 26, 'وَقَالَ فِرْعَوْنُ ذَرُونِىٓ أَقْتُلْ مُوسَىٰ وَلْيَدْعُ رَبَّهُۥٓ إِنِّىٓ أَخَافُ أَن يُبَدِّلَ دِينَكُمْ أَوْ أَن يُظْهِرَ فِى ٱلْأَرْضِ ٱلْفَسَادَ', 'وقال فرعون ذروني أقتل موسى وليدع ربه إني أخاف أن يبدل دينكم أو أن يظهر في الأرض الفساد', 24, 47, 470, 188, '40:26'),
(4160, NULL, NULL, 40, 27, 'وَقَالَ مُوسَىٰٓ إِنِّى عُذْتُ بِرَبِّى وَرَبِّكُم مِّن كُلِّ مُتَكَبِّرٍ لَّا يُؤْمِنُ بِيَوْمِ ٱلْحِسَابِ', 'وقال موسى إني عذت بربي وربكم من كل متكبر لا يؤمن بيوم الحساب', 24, 47, 470, 188, '40:27'),
(4161, NULL, NULL, 40, 28, 'وَقَالَ رَجُلٌ مُّؤْمِنٌ مِّنْ ءَالِ فِرْعَوْنَ يَكْتُمُ إِيمَٰنَهُۥٓ أَتَقْتُلُونَ رَجُلًا أَن يَقُولَ رَبِّىَ ٱللَّهُ وَقَدْ جَآءَكُم بِٱلْبَيِّنَٰتِ مِن رَّبِّكُمْ وَإِن يَكُ كَٰذِبًا فَعَلَيْهِ كَذِبُهُۥ وَإِن يَكُ صَادِقًا يُصِبْكُم بَعْضُ ٱلَّذِى يَعِدُكُمْ إِنَّ ٱللَّهَ لَا يَهْدِى مَنْ هُوَ مُسْرِفٌ كَذَّابٌ', 'وقال رجل مؤمن من آل فرعون يكتم إيمانه أتقتلون رجلا أن يقول ربي الله وقد جاءكم بالبينات من ربكم وإن يك كاذبا فعليه كذبه وإن يك صادقا يصبكم بعض الذي يعدكم إن الله لا يهدي من هو مسرف كذاب', 24, 47, 470, 188, '40:28'),
(4162, NULL, NULL, 40, 29, 'يَٰقَوْمِ لَكُمُ ٱلْمُلْكُ ٱلْيَوْمَ ظَٰهِرِينَ فِى ٱلْأَرْضِ فَمَن يَنصُرُنَا مِنۢ بَأْسِ ٱللَّهِ إِن جَآءَنَا قَالَ فِرْعَوْنُ مَآ أُرِيكُمْ إِلَّا مَآ أَرَىٰ وَمَآ أَهْدِيكُمْ إِلَّا سَبِيلَ ٱلرَّشَادِ', 'يا قوم لكم الملك اليوم ظاهرين في الأرض فمن ينصرنا من بأس الله إن جاءنا قال فرعون ما أريكم إلا ما أرى وما أهديكم إلا سبيل الرشاد', 24, 47, 470, 188, '40:29'),
(4163, NULL, NULL, 40, 30, 'وَقَالَ ٱلَّذِىٓ ءَامَنَ يَٰقَوْمِ إِنِّىٓ أَخَافُ عَلَيْكُم مِّثْلَ يَوْمِ ٱلْأَحْزَابِ', 'وقال الذي آمن يا قوم إني أخاف عليكم مثل يوم الأحزاب', 24, 47, 470, 188, '40:30'),
(4164, NULL, NULL, 40, 31, 'مِثْلَ دَأْبِ قَوْمِ نُوحٍ وَعَادٍ وَثَمُودَ وَٱلَّذِينَ مِنۢ بَعْدِهِمْ وَمَا ٱللَّهُ يُرِيدُ ظُلْمًا لِّلْعِبَادِ', 'مثل دأب قوم نوح وعاد وثمود والذين من بعدهم وما الله يريد ظلما للعباد', 24, 47, 470, 188, '40:31'),
(4165, NULL, NULL, 40, 32, 'وَيَٰقَوْمِ إِنِّىٓ أَخَافُ عَلَيْكُمْ يَوْمَ ٱلتَّنَادِ', 'ويا قوم إني أخاف عليكم يوم التناد', 24, 47, 470, 188, '40:32'),
(4166, NULL, NULL, 40, 33, 'يَوْمَ تُوَلُّونَ مُدْبِرِينَ مَا لَكُم مِّنَ ٱللَّهِ مِنْ عَاصِمٍ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِنْ هَادٍ', 'يوم تولون مدبرين ما لكم من الله من عاصم ومن يضلل الله فما له من هاد', 24, 47, 470, 188, '40:33'),
(4167, NULL, NULL, 40, 34, 'وَلَقَدْ جَآءَكُمْ يُوسُفُ مِن قَبْلُ بِٱلْبَيِّنَٰتِ فَمَا زِلْتُمْ فِى شَكٍّ مِّمَّا جَآءَكُم بِهِۦ حَتَّىٰٓ إِذَا هَلَكَ قُلْتُمْ لَن يَبْعَثَ ٱللَّهُ مِنۢ بَعْدِهِۦ رَسُولًا كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَنْ هُوَ مُسْرِفٌ مُّرْتَابٌ', 'ولقد جاءكم يوسف من قبل بالبينات فما زلتم في شك مما جاءكم به حتى إذا هلك قلتم لن يبعث الله من بعده رسولا كذلك يضل الله من هو مسرف مرتاب', 24, 47, 471, 188, '40:34'),
(4168, NULL, NULL, 40, 35, 'ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِ ٱللَّهِ بِغَيْرِ سُلْطَٰنٍ أَتَىٰهُمْ كَبُرَ مَقْتًا عِندَ ٱللَّهِ وَعِندَ ٱلَّذِينَ ءَامَنُوا۟ كَذَٰلِكَ يَطْبَعُ ٱللَّهُ عَلَىٰ كُلِّ قَلْبِ مُتَكَبِّرٍ جَبَّارٍ', 'الذين يجادلون في آيات الله بغير سلطان أتاهم كبر مقتا عند الله وعند الذين آمنوا كذلك يطبع الله على كل قلب متكبر جبار', 24, 47, 471, 188, '40:35'),
(4169, NULL, NULL, 40, 36, 'وَقَالَ فِرْعَوْنُ يَٰهَٰمَٰنُ ٱبْنِ لِى صَرْحًا لَّعَلِّىٓ أَبْلُغُ ٱلْأَسْبَٰبَ', 'وقال فرعون يا هامان ابن لي صرحا لعلي أبلغ الأسباب', 24, 47, 471, 188, '40:36'),
(4170, NULL, NULL, 40, 37, 'أَسْبَٰبَ ٱلسَّمَٰوَٰتِ فَأَطَّلِعَ إِلَىٰٓ إِلَٰهِ مُوسَىٰ وَإِنِّى لَأَظُنُّهُۥ كَٰذِبًا وَكَذَٰلِكَ زُيِّنَ لِفِرْعَوْنَ سُوٓءُ عَمَلِهِۦ وَصُدَّ عَنِ ٱلسَّبِيلِ وَمَا كَيْدُ فِرْعَوْنَ إِلَّا فِى تَبَابٍ', 'أسباب السماوات فأطلع إلى إله موسى وإني لأظنه كاذبا وكذلك زين لفرعون سوء عمله وصد عن السبيل وما كيد فرعون إلا في تباب', 24, 47, 471, 188, '40:37'),
(4171, NULL, NULL, 40, 38, 'وَقَالَ ٱلَّذِىٓ ءَامَنَ يَٰقَوْمِ ٱتَّبِعُونِ أَهْدِكُمْ سَبِيلَ ٱلرَّشَادِ', 'وقال الذي آمن يا قوم اتبعون أهدكم سبيل الرشاد', 24, 47, 471, 188, '40:38'),
(4172, NULL, NULL, 40, 39, 'يَٰقَوْمِ إِنَّمَا هَٰذِهِ ٱلْحَيَوٰةُ ٱلدُّنْيَا مَتَٰعٌ وَإِنَّ ٱلْءَاخِرَةَ هِىَ دَارُ ٱلْقَرَارِ', 'يا قوم إنما هذه الحياة الدنيا متاع وإن الآخرة هي دار القرار', 24, 47, 471, 188, '40:39'),
(4173, NULL, NULL, 40, 40, 'مَنْ عَمِلَ سَيِّئَةً فَلَا يُجْزَىٰٓ إِلَّا مِثْلَهَا وَمَنْ عَمِلَ صَٰلِحًا مِّن ذَكَرٍ أَوْ أُنثَىٰ وَهُوَ مُؤْمِنٌ فَأُو۟لَٰٓئِكَ يَدْخُلُونَ ٱلْجَنَّةَ يُرْزَقُونَ فِيهَا بِغَيْرِ حِسَابٍ', 'من عمل سيئة فلا يجزى إلا مثلها ومن عمل صالحا من ذكر أو أنثى وهو مؤمن فأولئك يدخلون الجنة يرزقون فيها بغير حساب', 24, 47, 471, 188, '40:40'),
(4174, NULL, NULL, 40, 41, 'وَيَٰقَوْمِ مَا لِىٓ أَدْعُوكُمْ إِلَى ٱلنَّجَوٰةِ وَتَدْعُونَنِىٓ إِلَى ٱلنَّارِ', 'ويا قوم ما لي أدعوكم إلى النجاة وتدعونني إلى النار', 24, 48, 472, 189, '40:41'),
(4175, NULL, NULL, 40, 42, 'تَدْعُونَنِى لِأَكْفُرَ بِٱللَّهِ وَأُشْرِكَ بِهِۦ مَا لَيْسَ لِى بِهِۦ عِلْمٌ وَأَنَا۠ أَدْعُوكُمْ إِلَى ٱلْعَزِيزِ ٱلْغَفَّٰرِ', 'تدعونني لأكفر بالله وأشرك به ما ليس لي به علم وأنا أدعوكم إلى العزيز الغفار', 24, 48, 472, 189, '40:42'),
(4176, NULL, NULL, 40, 43, 'لَا جَرَمَ أَنَّمَا تَدْعُونَنِىٓ إِلَيْهِ لَيْسَ لَهُۥ دَعْوَةٌ فِى ٱلدُّنْيَا وَلَا فِى ٱلْءَاخِرَةِ وَأَنَّ مَرَدَّنَآ إِلَى ٱللَّهِ وَأَنَّ ٱلْمُسْرِفِينَ هُمْ أَصْحَٰبُ ٱلنَّارِ', 'لا جرم أنما تدعونني إليه ليس له دعوة في الدنيا ولا في الآخرة وأن مردنا إلى الله وأن المسرفين هم أصحاب النار', 24, 48, 472, 189, '40:43'),
(4177, NULL, NULL, 40, 44, 'فَسَتَذْكُرُونَ مَآ أَقُولُ لَكُمْ وَأُفَوِّضُ أَمْرِىٓ إِلَى ٱللَّهِ إِنَّ ٱللَّهَ بَصِيرٌۢ بِٱلْعِبَادِ', 'فستذكرون ما أقول لكم وأفوض أمري إلى الله إن الله بصير بالعباد', 24, 48, 472, 189, '40:44'),
(4178, NULL, NULL, 40, 45, 'فَوَقَىٰهُ ٱللَّهُ سَيِّـَٔاتِ مَا مَكَرُوا۟ وَحَاقَ بِـَٔالِ فِرْعَوْنَ سُوٓءُ ٱلْعَذَابِ', 'فوقاه الله سيئات ما مكروا وحاق بآل فرعون سوء العذاب', 24, 48, 472, 189, '40:45'),
(4179, NULL, NULL, 40, 46, 'ٱلنَّارُ يُعْرَضُونَ عَلَيْهَا غُدُوًّا وَعَشِيًّا وَيَوْمَ تَقُومُ ٱلسَّاعَةُ أَدْخِلُوٓا۟ ءَالَ فِرْعَوْنَ أَشَدَّ ٱلْعَذَابِ', 'النار يعرضون عليها غدوا وعشيا ويوم تقوم الساعة أدخلوا آل فرعون أشد العذاب', 24, 48, 472, 189, '40:46'),
(4180, NULL, NULL, 40, 47, 'وَإِذْ يَتَحَآجُّونَ فِى ٱلنَّارِ فَيَقُولُ ٱلضُّعَفَٰٓؤُا۟ لِلَّذِينَ ٱسْتَكْبَرُوٓا۟ إِنَّا كُنَّا لَكُمْ تَبَعًا فَهَلْ أَنتُم مُّغْنُونَ عَنَّا نَصِيبًا مِّنَ ٱلنَّارِ', 'وإذ يتحاجون في النار فيقول الضعفاء للذين استكبروا إنا كنا لكم تبعا فهل أنتم مغنون عنا نصيبا من النار', 24, 48, 472, 189, '40:47'),
(4181, NULL, NULL, 40, 48, 'قَالَ ٱلَّذِينَ ٱسْتَكْبَرُوٓا۟ إِنَّا كُلٌّ فِيهَآ إِنَّ ٱللَّهَ قَدْ حَكَمَ بَيْنَ ٱلْعِبَادِ', 'قال الذين استكبروا إنا كل فيها إن الله قد حكم بين العباد', 24, 48, 472, 189, '40:48'),
(4182, NULL, NULL, 40, 49, 'وَقَالَ ٱلَّذِينَ فِى ٱلنَّارِ لِخَزَنَةِ جَهَنَّمَ ٱدْعُوا۟ رَبَّكُمْ يُخَفِّفْ عَنَّا يَوْمًا مِّنَ ٱلْعَذَابِ', 'وقال الذين في النار لخزنة جهنم ادعوا ربكم يخفف عنا يوما من العذاب', 24, 48, 472, 189, '40:49'),
(4183, NULL, NULL, 40, 50, 'قَالُوٓا۟ أَوَلَمْ تَكُ تَأْتِيكُمْ رُسُلُكُم بِٱلْبَيِّنَٰتِ قَالُوا۟ بَلَىٰ قَالُوا۟ فَٱدْعُوا۟ وَمَا دُعَٰٓؤُا۟ ٱلْكَٰفِرِينَ إِلَّا فِى ضَلَٰلٍ', 'قالوا أولم تك تأتيكم رسلكم بالبينات قالوا بلى قالوا فادعوا وما دعاء الكافرين إلا في ضلال', 24, 48, 473, 189, '40:50'),
(4184, NULL, NULL, 40, 51, 'إِنَّا لَنَنصُرُ رُسُلَنَا وَٱلَّذِينَ ءَامَنُوا۟ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَيَوْمَ يَقُومُ ٱلْأَشْهَٰدُ', 'إنا لننصر رسلنا والذين آمنوا في الحياة الدنيا ويوم يقوم الأشهاد', 24, 48, 473, 189, '40:51'),
(4185, NULL, NULL, 40, 52, 'يَوْمَ لَا يَنفَعُ ٱلظَّٰلِمِينَ مَعْذِرَتُهُمْ وَلَهُمُ ٱللَّعْنَةُ وَلَهُمْ سُوٓءُ ٱلدَّارِ', 'يوم لا ينفع الظالمين معذرتهم ولهم اللعنة ولهم سوء الدار', 24, 48, 473, 189, '40:52'),
(4186, NULL, NULL, 40, 53, 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْهُدَىٰ وَأَوْرَثْنَا بَنِىٓ إِسْرَٰٓءِيلَ ٱلْكِتَٰبَ', 'ولقد آتينا موسى الهدى وأورثنا بني إسرائيل الكتاب', 24, 48, 473, 189, '40:53'),
(4187, NULL, NULL, 40, 54, 'هُدًى وَذِكْرَىٰ لِأُو۟لِى ٱلْأَلْبَٰبِ', 'هدى وذكرى لأولي الألباب', 24, 48, 473, 189, '40:54'),
(4188, NULL, NULL, 40, 55, 'فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّ وَٱسْتَغْفِرْ لِذَنۢبِكَ وَسَبِّحْ بِحَمْدِ رَبِّكَ بِٱلْعَشِىِّ وَٱلْإِبْكَٰرِ', 'فاصبر إن وعد الله حق واستغفر لذنبك وسبح بحمد ربك بالعشي والإبكار', 24, 48, 473, 189, '40:55'),
(4189, NULL, NULL, 40, 56, 'إِنَّ ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِ ٱللَّهِ بِغَيْرِ سُلْطَٰنٍ أَتَىٰهُمْ إِن فِى صُدُورِهِمْ إِلَّا كِبْرٌ مَّا هُم بِبَٰلِغِيهِ فَٱسْتَعِذْ بِٱللَّهِ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ', 'إن الذين يجادلون في آيات الله بغير سلطان أتاهم إن في صدورهم إلا كبر ما هم ببالغيه فاستعذ بالله إنه هو السميع البصير', 24, 48, 473, 189, '40:56'),
(4190, NULL, NULL, 40, 57, 'لَخَلْقُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ أَكْبَرُ مِنْ خَلْقِ ٱلنَّاسِ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ', 'لخلق السماوات والأرض أكبر من خلق الناس ولكن أكثر الناس لا يعلمون', 24, 48, 473, 189, '40:57'),
(4191, NULL, NULL, 40, 58, 'وَمَا يَسْتَوِى ٱلْأَعْمَىٰ وَٱلْبَصِيرُ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَلَا ٱلْمُسِىٓءُ قَلِيلًا مَّا تَتَذَكَّرُونَ', 'وما يستوي الأعمى والبصير والذين آمنوا وعملوا الصالحات ولا المسيء قليلا ما تتذكرون', 24, 48, 473, 189, '40:58'),
(4192, NULL, NULL, 40, 59, 'إِنَّ ٱلسَّاعَةَ لَءَاتِيَةٌ لَّا رَيْبَ فِيهَا وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يُؤْمِنُونَ', 'إن الساعة لآتية لا ريب فيها ولكن أكثر الناس لا يؤمنون', 24, 48, 474, 189, '40:59'),
(4193, NULL, NULL, 40, 60, 'وَقَالَ رَبُّكُمُ ٱدْعُونِىٓ أَسْتَجِبْ لَكُمْ إِنَّ ٱلَّذِينَ يَسْتَكْبِرُونَ عَنْ عِبَادَتِى سَيَدْخُلُونَ جَهَنَّمَ دَاخِرِينَ', 'وقال ربكم ادعوني أستجب لكم إن الذين يستكبرون عن عبادتي سيدخلون جهنم داخرين', 24, 48, 474, 189, '40:60'),
(4194, NULL, NULL, 40, 61, 'ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلَّيْلَ لِتَسْكُنُوا۟ فِيهِ وَٱلنَّهَارَ مُبْصِرًا إِنَّ ٱللَّهَ لَذُو فَضْلٍ عَلَى ٱلنَّاسِ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَشْكُرُونَ', 'الله الذي جعل لكم الليل لتسكنوا فيه والنهار مبصرا إن الله لذو فضل على الناس ولكن أكثر الناس لا يشكرون', 24, 48, 474, 189, '40:61'),
(4195, NULL, NULL, 40, 62, 'ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ خَٰلِقُ كُلِّ شَىْءٍ لَّآ إِلَٰهَ إِلَّا هُوَ فَأَنَّىٰ تُؤْفَكُونَ', 'ذلكم الله ربكم خالق كل شيء لا إله إلا هو فأنى تؤفكون', 24, 48, 474, 189, '40:62'),
(4196, NULL, NULL, 40, 63, 'كَذَٰلِكَ يُؤْفَكُ ٱلَّذِينَ كَانُوا۟ بِـَٔايَٰتِ ٱللَّهِ يَجْحَدُونَ', 'كذلك يؤفك الذين كانوا بآيات الله يجحدون', 24, 48, 474, 189, '40:63'),
(4197, NULL, NULL, 40, 64, 'ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ قَرَارًا وَٱلسَّمَآءَ بِنَآءً وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَرَزَقَكُم مِّنَ ٱلطَّيِّبَٰتِ ذَٰلِكُمُ ٱللَّهُ رَبُّكُمْ فَتَبَارَكَ ٱللَّهُ رَبُّ ٱلْعَٰلَمِينَ', 'الله الذي جعل لكم الأرض قرارا والسماء بناء وصوركم فأحسن صوركم ورزقكم من الطيبات ذلكم الله ربكم فتبارك الله رب العالمين', 24, 48, 474, 189, '40:64'),
(4198, NULL, NULL, 40, 65, 'هُوَ ٱلْحَىُّ لَآ إِلَٰهَ إِلَّا هُوَ فَٱدْعُوهُ مُخْلِصِينَ لَهُ ٱلدِّينَ ٱلْحَمْدُ لِلَّهِ رَبِّ ٱلْعَٰلَمِينَ', 'هو الحي لا إله إلا هو فادعوه مخلصين له الدين الحمد لله رب العالمين', 24, 48, 474, 189, '40:65'),
(4199, NULL, NULL, 40, 66, 'قُلْ إِنِّى نُهِيتُ أَنْ أَعْبُدَ ٱلَّذِينَ تَدْعُونَ مِن دُونِ ٱللَّهِ لَمَّا جَآءَنِىَ ٱلْبَيِّنَٰتُ مِن رَّبِّى وَأُمِرْتُ أَنْ أُسْلِمَ لِرَبِّ ٱلْعَٰلَمِينَ', 'قل إني نهيت أن أعبد الذين تدعون من دون الله لما جاءني البينات من ربي وأمرت أن أسلم لرب العالمين', 24, 48, 474, 190, '40:66'),
(4200, NULL, NULL, 40, 67, 'هُوَ ٱلَّذِى خَلَقَكُم مِّن تُرَابٍ ثُمَّ مِن نُّطْفَةٍ ثُمَّ مِنْ عَلَقَةٍ ثُمَّ يُخْرِجُكُمْ طِفْلًا ثُمَّ لِتَبْلُغُوٓا۟ أَشُدَّكُمْ ثُمَّ لِتَكُونُوا۟ شُيُوخًا وَمِنكُم مَّن يُتَوَفَّىٰ مِن قَبْلُ وَلِتَبْلُغُوٓا۟ أَجَلًا مُّسَمًّى وَلَعَلَّكُمْ تَعْقِلُونَ', 'هو الذي خلقكم من تراب ثم من نطفة ثم من علقة ثم يخرجكم طفلا ثم لتبلغوا أشدكم ثم لتكونوا شيوخا ومنكم من يتوفى من قبل ولتبلغوا أجلا مسمى ولعلكم تعقلون', 24, 48, 475, 190, '40:67'),
(4201, NULL, NULL, 40, 68, 'هُوَ ٱلَّذِى يُحْىِۦ وَيُمِيتُ فَإِذَا قَضَىٰٓ أَمْرًا فَإِنَّمَا يَقُولُ لَهُۥ كُن فَيَكُونُ', 'هو الذي يحيي ويميت فإذا قضى أمرا فإنما يقول له كن فيكون', 24, 48, 475, 190, '40:68'),
(4202, NULL, NULL, 40, 69, 'أَلَمْ تَرَ إِلَى ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِ ٱللَّهِ أَنَّىٰ يُصْرَفُونَ', 'ألم تر إلى الذين يجادلون في آيات الله أنى يصرفون', 24, 48, 475, 190, '40:69'),
(4203, NULL, NULL, 40, 70, 'ٱلَّذِينَ كَذَّبُوا۟ بِٱلْكِتَٰبِ وَبِمَآ أَرْسَلْنَا بِهِۦ رُسُلَنَا فَسَوْفَ يَعْلَمُونَ', 'الذين كذبوا بالكتاب وبما أرسلنا به رسلنا فسوف يعلمون', 24, 48, 475, 190, '40:70'),
(4204, NULL, NULL, 40, 71, 'إِذِ ٱلْأَغْلَٰلُ فِىٓ أَعْنَٰقِهِمْ وَٱلسَّلَٰسِلُ يُسْحَبُونَ', 'إذ الأغلال في أعناقهم والسلاسل يسحبون', 24, 48, 475, 190, '40:71'),
(4205, NULL, NULL, 40, 72, 'فِى ٱلْحَمِيمِ ثُمَّ فِى ٱلنَّارِ يُسْجَرُونَ', 'في الحميم ثم في النار يسجرون', 24, 48, 475, 190, '40:72'),
(4206, NULL, NULL, 40, 73, 'ثُمَّ قِيلَ لَهُمْ أَيْنَ مَا كُنتُمْ تُشْرِكُونَ', 'ثم قيل لهم أين ما كنتم تشركون', 24, 48, 475, 190, '40:73'),
(4207, NULL, NULL, 40, 74, 'مِن دُونِ ٱللَّهِ قَالُوا۟ ضَلُّوا۟ عَنَّا بَل لَّمْ نَكُن نَّدْعُوا۟ مِن قَبْلُ شَيْـًٔا كَذَٰلِكَ يُضِلُّ ٱللَّهُ ٱلْكَٰفِرِينَ', 'من دون الله قالوا ضلوا عنا بل لم نكن ندعو من قبل شيئا كذلك يضل الله الكافرين', 24, 48, 475, 190, '40:74'),
(4208, NULL, NULL, 40, 75, 'ذَٰلِكُم بِمَا كُنتُمْ تَفْرَحُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَبِمَا كُنتُمْ تَمْرَحُونَ', 'ذلكم بما كنتم تفرحون في الأرض بغير الحق وبما كنتم تمرحون', 24, 48, 475, 190, '40:75'),
(4209, NULL, NULL, 40, 76, 'ٱدْخُلُوٓا۟ أَبْوَٰبَ جَهَنَّمَ خَٰلِدِينَ فِيهَا فَبِئْسَ مَثْوَى ٱلْمُتَكَبِّرِينَ', 'ادخلوا أبواب جهنم خالدين فيها فبئس مثوى المتكبرين', 24, 48, 475, 190, '40:76'),
(4210, NULL, NULL, 40, 77, 'فَٱصْبِرْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّ فَإِمَّا نُرِيَنَّكَ بَعْضَ ٱلَّذِى نَعِدُهُمْ أَوْ نَتَوَفَّيَنَّكَ فَإِلَيْنَا يُرْجَعُونَ', 'فاصبر إن وعد الله حق فإما نرينك بعض الذي نعدهم أو نتوفينك فإلينا يرجعون', 24, 48, 475, 190, '40:77'),
(4211, NULL, NULL, 40, 78, 'وَلَقَدْ أَرْسَلْنَا رُسُلًا مِّن قَبْلِكَ مِنْهُم مَّن قَصَصْنَا عَلَيْكَ وَمِنْهُم مَّن لَّمْ نَقْصُصْ عَلَيْكَ وَمَا كَانَ لِرَسُولٍ أَن يَأْتِىَ بِـَٔايَةٍ إِلَّا بِإِذْنِ ٱللَّهِ فَإِذَا جَآءَ أَمْرُ ٱللَّهِ قُضِىَ بِٱلْحَقِّ وَخَسِرَ هُنَالِكَ ٱلْمُبْطِلُونَ', 'ولقد أرسلنا رسلا من قبلك منهم من قصصنا عليك ومنهم من لم نقصص عليك وما كان لرسول أن يأتي بآية إلا بإذن الله فإذا جاء أمر الله قضي بالحق وخسر هنالك المبطلون', 24, 48, 476, 190, '40:78'),
(4212, NULL, NULL, 40, 79, 'ٱللَّهُ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَنْعَٰمَ لِتَرْكَبُوا۟ مِنْهَا وَمِنْهَا تَأْكُلُونَ', 'الله الذي جعل لكم الأنعام لتركبوا منها ومنها تأكلون', 24, 48, 476, 190, '40:79'),
(4213, NULL, NULL, 40, 80, 'وَلَكُمْ فِيهَا مَنَٰفِعُ وَلِتَبْلُغُوا۟ عَلَيْهَا حَاجَةً فِى صُدُورِكُمْ وَعَلَيْهَا وَعَلَى ٱلْفُلْكِ تُحْمَلُونَ', 'ولكم فيها منافع ولتبلغوا عليها حاجة في صدوركم وعليها وعلى الفلك تحملون', 24, 48, 476, 190, '40:80'),
(4214, NULL, NULL, 40, 81, 'وَيُرِيكُمْ ءَايَٰتِهِۦ فَأَىَّ ءَايَٰتِ ٱللَّهِ تُنكِرُونَ', 'ويريكم آياته فأي آيات الله تنكرون', 24, 48, 476, 190, '40:81'),
(4215, NULL, NULL, 40, 82, 'أَفَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ كَانُوٓا۟ أَكْثَرَ مِنْهُمْ وَأَشَدَّ قُوَّةً وَءَاثَارًا فِى ٱلْأَرْضِ فَمَآ أَغْنَىٰ عَنْهُم مَّا كَانُوا۟ يَكْسِبُونَ', 'أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم كانوا أكثر منهم وأشد قوة وآثارا في الأرض فما أغنى عنهم ما كانوا يكسبون', 24, 48, 476, 190, '40:82'),
(4216, NULL, NULL, 40, 83, 'فَلَمَّا جَآءَتْهُمْ رُسُلُهُم بِٱلْبَيِّنَٰتِ فَرِحُوا۟ بِمَا عِندَهُم مِّنَ ٱلْعِلْمِ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ', 'فلما جاءتهم رسلهم بالبينات فرحوا بما عندهم من العلم وحاق بهم ما كانوا به يستهزئون', 24, 48, 476, 190, '40:83'),
(4217, NULL, NULL, 40, 84, 'فَلَمَّا رَأَوْا۟ بَأْسَنَا قَالُوٓا۟ ءَامَنَّا بِٱللَّهِ وَحْدَهُۥ وَكَفَرْنَا بِمَا كُنَّا بِهِۦ مُشْرِكِينَ', 'فلما رأوا بأسنا قالوا آمنا بالله وحده وكفرنا بما كنا به مشركين', 24, 48, 476, 190, '40:84'),
(4218, NULL, NULL, 40, 85, 'فَلَمْ يَكُ يَنفَعُهُمْ إِيمَٰنُهُمْ لَمَّا رَأَوْا۟ بَأْسَنَا سُنَّتَ ٱللَّهِ ٱلَّتِى قَدْ خَلَتْ فِى عِبَادِهِۦ وَخَسِرَ هُنَالِكَ ٱلْكَٰفِرُونَ', 'فلم يك ينفعهم إيمانهم لما رأوا بأسنا سنت الله التي قد خلت في عباده وخسر هنالك الكافرون', 24, 48, 476, 190, '40:85'),
(4219, NULL, NULL, 41, 1, 'حمٓ', 'حم', 24, 48, 477, 190, '41:1'),
(4220, NULL, NULL, 41, 2, 'تَنزِيلٌ مِّنَ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ', 'تنزيل من الرحمن الرحيم', 24, 48, 477, 190, '41:2'),
(4221, NULL, NULL, 41, 3, 'كِتَٰبٌ فُصِّلَتْ ءَايَٰتُهُۥ قُرْءَانًا عَرَبِيًّا لِّقَوْمٍ يَعْلَمُونَ', 'كتاب فصلت آياته قرآنا عربيا لقوم يعلمون', 24, 48, 477, 190, '41:3'),
(4222, NULL, NULL, 41, 4, 'بَشِيرًا وَنَذِيرًا فَأَعْرَضَ أَكْثَرُهُمْ فَهُمْ لَا يَسْمَعُونَ', 'بشيرا ونذيرا فأعرض أكثرهم فهم لا يسمعون', 24, 48, 477, 190, '41:4'),
(4223, NULL, NULL, 41, 5, 'وَقَالُوا۟ قُلُوبُنَا فِىٓ أَكِنَّةٍ مِّمَّا تَدْعُونَآ إِلَيْهِ وَفِىٓ ءَاذَانِنَا وَقْرٌ وَمِنۢ بَيْنِنَا وَبَيْنِكَ حِجَابٌ فَٱعْمَلْ إِنَّنَا عَٰمِلُونَ', 'وقالوا قلوبنا في أكنة مما تدعونا إليه وفي آذاننا وقر ومن بيننا وبينك حجاب فاعمل إننا عاملون', 24, 48, 477, 190, '41:5'),
(4224, NULL, NULL, 41, 6, 'قُلْ إِنَّمَآ أَنَا۠ بَشَرٌ مِّثْلُكُمْ يُوحَىٰٓ إِلَىَّ أَنَّمَآ إِلَٰهُكُمْ إِلَٰهٌ وَٰحِدٌ فَٱسْتَقِيمُوٓا۟ إِلَيْهِ وَٱسْتَغْفِرُوهُ وَوَيْلٌ لِّلْمُشْرِكِينَ', 'قل إنما أنا بشر مثلكم يوحى إلي أنما إلهكم إله واحد فاستقيموا إليه واستغفروه وويل للمشركين', 24, 48, 477, 190, '41:6'),
(4225, NULL, NULL, 41, 7, 'ٱلَّذِينَ لَا يُؤْتُونَ ٱلزَّكَوٰةَ وَهُم بِٱلْءَاخِرَةِ هُمْ كَٰفِرُونَ', 'الذين لا يؤتون الزكاة وهم بالآخرة هم كافرون', 24, 48, 477, 190, '41:7'),
(4226, NULL, NULL, 41, 8, 'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ', 'إن الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون', 24, 48, 477, 190, '41:8'),
(4227, NULL, NULL, 41, 9, 'قُلْ أَئِنَّكُمْ لَتَكْفُرُونَ بِٱلَّذِى خَلَقَ ٱلْأَرْضَ فِى يَوْمَيْنِ وَتَجْعَلُونَ لَهُۥٓ أَندَادًا ذَٰلِكَ رَبُّ ٱلْعَٰلَمِينَ', 'قل أئنكم لتكفرون بالذي خلق الأرض في يومين وتجعلون له أندادا ذلك رب العالمين', 24, 48, 477, 191, '41:9'),
(4228, NULL, NULL, 41, 10, 'وَجَعَلَ فِيهَا رَوَٰسِىَ مِن فَوْقِهَا وَبَٰرَكَ فِيهَا وَقَدَّرَ فِيهَآ أَقْوَٰتَهَا فِىٓ أَرْبَعَةِ أَيَّامٍ سَوَآءً لِّلسَّآئِلِينَ', 'وجعل فيها رواسي من فوقها وبارك فيها وقدر فيها أقواتها في أربعة أيام سواء للسائلين', 24, 48, 477, 191, '41:10'),
(4229, NULL, NULL, 41, 11, 'ثُمَّ ٱسْتَوَىٰٓ إِلَى ٱلسَّمَآءِ وَهِىَ دُخَانٌ فَقَالَ لَهَا وَلِلْأَرْضِ ٱئْتِيَا طَوْعًا أَوْ كَرْهًا قَالَتَآ أَتَيْنَا طَآئِعِينَ', 'ثم استوى إلى السماء وهي دخان فقال لها وللأرض ائتيا طوعا أو كرها قالتا أتينا طائعين', 24, 48, 477, 191, '41:11'),
(4230, NULL, NULL, 41, 12, 'فَقَضَىٰهُنَّ سَبْعَ سَمَٰوَاتٍ فِى يَوْمَيْنِ وَأَوْحَىٰ فِى كُلِّ سَمَآءٍ أَمْرَهَا وَزَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِمَصَٰبِيحَ وَحِفْظًا ذَٰلِكَ تَقْدِيرُ ٱلْعَزِيزِ ٱلْعَلِيمِ', 'فقضاهن سبع سماوات في يومين وأوحى في كل سماء أمرها وزينا السماء الدنيا بمصابيح وحفظا ذلك تقدير العزيز العليم', 24, 48, 478, 191, '41:12'),
(4231, NULL, NULL, 41, 13, 'فَإِنْ أَعْرَضُوا۟ فَقُلْ أَنذَرْتُكُمْ صَٰعِقَةً مِّثْلَ صَٰعِقَةِ عَادٍ وَثَمُودَ', 'فإن أعرضوا فقل أنذرتكم صاعقة مثل صاعقة عاد وثمود', 24, 48, 478, 191, '41:13'),
(4232, NULL, NULL, 41, 14, 'إِذْ جَآءَتْهُمُ ٱلرُّسُلُ مِنۢ بَيْنِ أَيْدِيهِمْ وَمِنْ خَلْفِهِمْ أَلَّا تَعْبُدُوٓا۟ إِلَّا ٱللَّهَ قَالُوا۟ لَوْ شَآءَ رَبُّنَا لَأَنزَلَ مَلَٰٓئِكَةً فَإِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَٰفِرُونَ', 'إذ جاءتهم الرسل من بين أيديهم ومن خلفهم ألا تعبدوا إلا الله قالوا لو شاء ربنا لأنزل ملائكة فإنا بما أرسلتم به كافرون', 24, 48, 478, 191, '41:14'),
(4233, NULL, NULL, 41, 15, 'فَأَمَّا عَادٌ فَٱسْتَكْبَرُوا۟ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَقَالُوا۟ مَنْ أَشَدُّ مِنَّا قُوَّةً أَوَلَمْ يَرَوْا۟ أَنَّ ٱللَّهَ ٱلَّذِى خَلَقَهُمْ هُوَ أَشَدُّ مِنْهُمْ قُوَّةً وَكَانُوا۟ بِـَٔايَٰتِنَا يَجْحَدُونَ', 'فأما عاد فاستكبروا في الأرض بغير الحق وقالوا من أشد منا قوة أولم يروا أن الله الذي خلقهم هو أشد منهم قوة وكانوا بآياتنا يجحدون', 24, 48, 478, 191, '41:15'),
(4234, NULL, NULL, 41, 16, 'فَأَرْسَلْنَا عَلَيْهِمْ رِيحًا صَرْصَرًا فِىٓ أَيَّامٍ نَّحِسَاتٍ لِّنُذِيقَهُمْ عَذَابَ ٱلْخِزْىِ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَلَعَذَابُ ٱلْءَاخِرَةِ أَخْزَىٰ وَهُمْ لَا يُنصَرُونَ', 'فأرسلنا عليهم ريحا صرصرا في أيام نحسات لنذيقهم عذاب الخزي في الحياة الدنيا ولعذاب الآخرة أخزى وهم لا ينصرون', 24, 48, 478, 191, '41:16'),
(4235, NULL, NULL, 41, 17, 'وَأَمَّا ثَمُودُ فَهَدَيْنَٰهُمْ فَٱسْتَحَبُّوا۟ ٱلْعَمَىٰ عَلَى ٱلْهُدَىٰ فَأَخَذَتْهُمْ صَٰعِقَةُ ٱلْعَذَابِ ٱلْهُونِ بِمَا كَانُوا۟ يَكْسِبُونَ', 'وأما ثمود فهديناهم فاستحبوا العمى على الهدى فأخذتهم صاعقة العذاب الهون بما كانوا يكسبون', 24, 48, 478, 191, '41:17'),
(4236, NULL, NULL, 41, 18, 'وَنَجَّيْنَا ٱلَّذِينَ ءَامَنُوا۟ وَكَانُوا۟ يَتَّقُونَ', 'ونجينا الذين آمنوا وكانوا يتقون', 24, 48, 478, 191, '41:18'),
(4237, NULL, NULL, 41, 19, 'وَيَوْمَ يُحْشَرُ أَعْدَآءُ ٱللَّهِ إِلَى ٱلنَّارِ فَهُمْ يُوزَعُونَ', 'ويوم يحشر أعداء الله إلى النار فهم يوزعون', 24, 48, 478, 191, '41:19'),
(4238, NULL, NULL, 41, 20, 'حَتَّىٰٓ إِذَا مَا جَآءُوهَا شَهِدَ عَلَيْهِمْ سَمْعُهُمْ وَأَبْصَٰرُهُمْ وَجُلُودُهُم بِمَا كَانُوا۟ يَعْمَلُونَ', 'حتى إذا ما جاءوها شهد عليهم سمعهم وأبصارهم وجلودهم بما كانوا يعملون', 24, 48, 478, 191, '41:20'),
(4239, NULL, NULL, 41, 21, 'وَقَالُوا۟ لِجُلُودِهِمْ لِمَ شَهِدتُّمْ عَلَيْنَا قَالُوٓا۟ أَنطَقَنَا ٱللَّهُ ٱلَّذِىٓ أَنطَقَ كُلَّ شَىْءٍ وَهُوَ خَلَقَكُمْ أَوَّلَ مَرَّةٍ وَإِلَيْهِ تُرْجَعُونَ', 'وقالوا لجلودهم لم شهدتم علينا قالوا أنطقنا الله الذي أنطق كل شيء وهو خلقكم أول مرة وإليه ترجعون', 24, 48, 479, 191, '41:21'),
(4240, NULL, NULL, 41, 22, 'وَمَا كُنتُمْ تَسْتَتِرُونَ أَن يَشْهَدَ عَلَيْكُمْ سَمْعُكُمْ وَلَآ أَبْصَٰرُكُمْ وَلَا جُلُودُكُمْ وَلَٰكِن ظَنَنتُمْ أَنَّ ٱللَّهَ لَا يَعْلَمُ كَثِيرًا مِّمَّا تَعْمَلُونَ', 'وما كنتم تستترون أن يشهد عليكم سمعكم ولا أبصاركم ولا جلودكم ولكن ظننتم أن الله لا يعلم كثيرا مما تعملون', 24, 48, 479, 191, '41:22'),
(4241, NULL, NULL, 41, 23, 'وَذَٰلِكُمْ ظَنُّكُمُ ٱلَّذِى ظَنَنتُم بِرَبِّكُمْ أَرْدَىٰكُمْ فَأَصْبَحْتُم مِّنَ ٱلْخَٰسِرِينَ', 'وذلكم ظنكم الذي ظننتم بربكم أرداكم فأصبحتم من الخاسرين', 24, 48, 479, 191, '41:23'),
(4242, NULL, NULL, 41, 24, 'فَإِن يَصْبِرُوا۟ فَٱلنَّارُ مَثْوًى لَّهُمْ وَإِن يَسْتَعْتِبُوا۟ فَمَا هُم مِّنَ ٱلْمُعْتَبِينَ', 'فإن يصبروا فالنار مثوى لهم وإن يستعتبوا فما هم من المعتبين', 24, 48, 479, 191, '41:24'),
(4243, NULL, NULL, 41, 25, 'وَقَيَّضْنَا لَهُمْ قُرَنَآءَ فَزَيَّنُوا۟ لَهُم مَّا بَيْنَ أَيْدِيهِمْ وَمَا خَلْفَهُمْ وَحَقَّ عَلَيْهِمُ ٱلْقَوْلُ فِىٓ أُمَمٍ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ ٱلْجِنِّ وَٱلْإِنسِ إِنَّهُمْ كَانُوا۟ خَٰسِرِينَ', 'وقيضنا لهم قرناء فزينوا لهم ما بين أيديهم وما خلفهم وحق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس إنهم كانوا خاسرين', 24, 48, 479, 192, '41:25'),
(4244, NULL, NULL, 41, 26, 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لَا تَسْمَعُوا۟ لِهَٰذَا ٱلْقُرْءَانِ وَٱلْغَوْا۟ فِيهِ لَعَلَّكُمْ تَغْلِبُونَ', 'وقال الذين كفروا لا تسمعوا لهذا القرآن والغوا فيه لعلكم تغلبون', 24, 48, 479, 192, '41:26'),
(4245, NULL, NULL, 41, 27, 'فَلَنُذِيقَنَّ ٱلَّذِينَ كَفَرُوا۟ عَذَابًا شَدِيدًا وَلَنَجْزِيَنَّهُمْ أَسْوَأَ ٱلَّذِى كَانُوا۟ يَعْمَلُونَ', 'فلنذيقن الذين كفروا عذابا شديدا ولنجزينهم أسوأ الذي كانوا يعملون', 24, 48, 479, 192, '41:27'),
(4246, NULL, NULL, 41, 28, 'ذَٰلِكَ جَزَآءُ أَعْدَآءِ ٱللَّهِ ٱلنَّارُ لَهُمْ فِيهَا دَارُ ٱلْخُلْدِ جَزَآءًۢ بِمَا كَانُوا۟ بِـَٔايَٰتِنَا يَجْحَدُونَ', 'ذلك جزاء أعداء الله النار لهم فيها دار الخلد جزاء بما كانوا بآياتنا يجحدون', 24, 48, 479, 192, '41:28'),
(4247, NULL, NULL, 41, 29, 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ رَبَّنَآ أَرِنَا ٱلَّذَيْنِ أَضَلَّانَا مِنَ ٱلْجِنِّ وَٱلْإِنسِ نَجْعَلْهُمَا تَحْتَ أَقْدَامِنَا لِيَكُونَا مِنَ ٱلْأَسْفَلِينَ', 'وقال الذين كفروا ربنا أرنا اللذين أضلانا من الجن والإنس نجعلهما تحت أقدامنا ليكونا من الأسفلين', 24, 48, 479, 192, '41:29'),
(4248, NULL, NULL, 41, 30, 'إِنَّ ٱلَّذِينَ قَالُوا۟ رَبُّنَا ٱللَّهُ ثُمَّ ٱسْتَقَٰمُوا۟ تَتَنَزَّلُ عَلَيْهِمُ ٱلْمَلَٰٓئِكَةُ أَلَّا تَخَافُوا۟ وَلَا تَحْزَنُوا۟ وَأَبْشِرُوا۟ بِٱلْجَنَّةِ ٱلَّتِى كُنتُمْ تُوعَدُونَ', 'إن الذين قالوا ربنا الله ثم استقاموا تتنزل عليهم الملائكة ألا تخافوا ولا تحزنوا وأبشروا بالجنة التي كنتم توعدون', 24, 48, 480, 192, '41:30'),
(4249, NULL, NULL, 41, 31, 'نَحْنُ أَوْلِيَآؤُكُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَفِى ٱلْءَاخِرَةِ وَلَكُمْ فِيهَا مَا تَشْتَهِىٓ أَنفُسُكُمْ وَلَكُمْ فِيهَا مَا تَدَّعُونَ', 'نحن أولياؤكم في الحياة الدنيا وفي الآخرة ولكم فيها ما تشتهي أنفسكم ولكم فيها ما تدعون', 24, 48, 480, 192, '41:31'),
(4250, NULL, NULL, 41, 32, 'نُزُلًا مِّنْ غَفُورٍ رَّحِيمٍ', 'نزلا من غفور رحيم', 24, 48, 480, 192, '41:32'),
(4251, NULL, NULL, 41, 33, 'وَمَنْ أَحْسَنُ قَوْلًا مِّمَّن دَعَآ إِلَى ٱللَّهِ وَعَمِلَ صَٰلِحًا وَقَالَ إِنَّنِى مِنَ ٱلْمُسْلِمِينَ', 'ومن أحسن قولا ممن دعا إلى الله وعمل صالحا وقال إنني من المسلمين', 24, 48, 480, 192, '41:33'),
(4252, NULL, NULL, 41, 34, 'وَلَا تَسْتَوِى ٱلْحَسَنَةُ وَلَا ٱلسَّيِّئَةُ ٱدْفَعْ بِٱلَّتِى هِىَ أَحْسَنُ فَإِذَا ٱلَّذِى بَيْنَكَ وَبَيْنَهُۥ عَدَٰوَةٌ كَأَنَّهُۥ وَلِىٌّ حَمِيمٌ', 'ولا تستوي الحسنة ولا السيئة ادفع بالتي هي أحسن فإذا الذي بينك وبينه عداوة كأنه ولي حميم', 24, 48, 480, 192, '41:34'),
(4253, NULL, NULL, 41, 35, 'وَمَا يُلَقَّىٰهَآ إِلَّا ٱلَّذِينَ صَبَرُوا۟ وَمَا يُلَقَّىٰهَآ إِلَّا ذُو حَظٍّ عَظِيمٍ', 'وما يلقاها إلا الذين صبروا وما يلقاها إلا ذو حظ عظيم', 24, 48, 480, 192, '41:35'),
(4254, NULL, NULL, 41, 36, 'وَإِمَّا يَنزَغَنَّكَ مِنَ ٱلشَّيْطَٰنِ نَزْغٌ فَٱسْتَعِذْ بِٱللَّهِ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ', 'وإما ينزغنك من الشيطان نزغ فاستعذ بالله إنه هو السميع العليم', 24, 48, 480, 192, '41:36'),
(4255, NULL, NULL, 41, 37, 'وَمِنْ ءَايَٰتِهِ ٱلَّيْلُ وَٱلنَّهَارُ وَٱلشَّمْسُ وَٱلْقَمَرُ لَا تَسْجُدُوا۟ لِلشَّمْسِ وَلَا لِلْقَمَرِ وَٱسْجُدُوا۟ لِلَّهِ ٱلَّذِى خَلَقَهُنَّ إِن كُنتُمْ إِيَّاهُ تَعْبُدُونَ', 'ومن آياته الليل والنهار والشمس والقمر لا تسجدوا للشمس ولا للقمر واسجدوا لله الذي خلقهن إن كنتم إياه تعبدون', 24, 48, 480, 192, '41:37'),
(4256, NULL, NULL, 41, 38, 'فَإِنِ ٱسْتَكْبَرُوا۟ فَٱلَّذِينَ عِندَ رَبِّكَ يُسَبِّحُونَ لَهُۥ بِٱلَّيْلِ وَٱلنَّهَارِ وَهُمْ لَا يَسْـَٔمُونَ', 'فإن استكبروا فالذين عند ربك يسبحون له بالليل والنهار وهم لا يسأمون', 24, 48, 480, 192, '41:38'),
(4257, NULL, NULL, 41, 39, 'وَمِنْ ءَايَٰتِهِۦٓ أَنَّكَ تَرَى ٱلْأَرْضَ خَٰشِعَةً فَإِذَآ أَنزَلْنَا عَلَيْهَا ٱلْمَآءَ ٱهْتَزَّتْ وَرَبَتْ إِنَّ ٱلَّذِىٓ أَحْيَاهَا لَمُحْىِ ٱلْمَوْتَىٰٓ إِنَّهُۥ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'ومن آياته أنك ترى الأرض خاشعة فإذا أنزلنا عليها الماء اهتزت وربت إن الذي أحياها لمحيي الموتى إنه على كل شيء قدير', 24, 48, 481, 192, '41:39'),
(4258, NULL, NULL, 41, 40, 'إِنَّ ٱلَّذِينَ يُلْحِدُونَ فِىٓ ءَايَٰتِنَا لَا يَخْفَوْنَ عَلَيْنَآ أَفَمَن يُلْقَىٰ فِى ٱلنَّارِ خَيْرٌ أَم مَّن يَأْتِىٓ ءَامِنًا يَوْمَ ٱلْقِيَٰمَةِ ٱعْمَلُوا۟ مَا شِئْتُمْ إِنَّهُۥ بِمَا تَعْمَلُونَ بَصِيرٌ', 'إن الذين يلحدون في آياتنا لا يخفون علينا أفمن يلقى في النار خير أم من يأتي آمنا يوم القيامة اعملوا ما شئتم إنه بما تعملون بصير', 24, 48, 481, 192, '41:40'),
(4259, NULL, NULL, 41, 41, 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ بِٱلذِّكْرِ لَمَّا جَآءَهُمْ وَإِنَّهُۥ لَكِتَٰبٌ عَزِيزٌ', 'إن الذين كفروا بالذكر لما جاءهم وإنه لكتاب عزيز', 24, 48, 481, 192, '41:41'),
(4260, NULL, NULL, 41, 42, 'لَّا يَأْتِيهِ ٱلْبَٰطِلُ مِنۢ بَيْنِ يَدَيْهِ وَلَا مِنْ خَلْفِهِۦ تَنزِيلٌ مِّنْ حَكِيمٍ حَمِيدٍ', 'لا يأتيه الباطل من بين يديه ولا من خلفه تنزيل من حكيم حميد', 24, 48, 481, 192, '41:42'),
(4261, NULL, NULL, 41, 43, 'مَّا يُقَالُ لَكَ إِلَّا مَا قَدْ قِيلَ لِلرُّسُلِ مِن قَبْلِكَ إِنَّ رَبَّكَ لَذُو مَغْفِرَةٍ وَذُو عِقَابٍ أَلِيمٍ', 'ما يقال لك إلا ما قد قيل للرسل من قبلك إن ربك لذو مغفرة وذو عقاب أليم', 24, 48, 481, 192, '41:43'),
(4262, NULL, NULL, 41, 44, 'وَلَوْ جَعَلْنَٰهُ قُرْءَانًا أَعْجَمِيًّا لَّقَالُوا۟ لَوْلَا فُصِّلَتْ ءَايَٰتُهُۥٓ ءَا۬عْجَمِىٌّ وَعَرَبِىٌّ قُلْ هُوَ لِلَّذِينَ ءَامَنُوا۟ هُدًى وَشِفَآءٌ وَٱلَّذِينَ لَا يُؤْمِنُونَ فِىٓ ءَاذَانِهِمْ وَقْرٌ وَهُوَ عَلَيْهِمْ عَمًى أُو۟لَٰٓئِكَ يُنَادَوْنَ مِن مَّكَانٍۭ بَعِيدٍ', 'ولو جعلناه قرآنا أعجميا لقالوا لولا فصلت آياته أأعجمي وعربي قل هو للذين آمنوا هدى وشفاء والذين لا يؤمنون في آذانهم وقر وهو عليهم عمى أولئك ينادون من مكان بعيد', 24, 48, 481, 192, '41:44'),
(4263, NULL, NULL, 41, 45, 'وَلَقَدْ ءَاتَيْنَا مُوسَى ٱلْكِتَٰبَ فَٱخْتُلِفَ فِيهِ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ لَقُضِىَ بَيْنَهُمْ وَإِنَّهُمْ لَفِى شَكٍّ مِّنْهُ مُرِيبٍ', 'ولقد آتينا موسى الكتاب فاختلف فيه ولولا كلمة سبقت من ربك لقضي بينهم وإنهم لفي شك منه مريب', 24, 48, 481, 192, '41:45'),
(4264, NULL, NULL, 41, 46, 'مَّنْ عَمِلَ صَٰلِحًا فَلِنَفْسِهِۦ وَمَنْ أَسَآءَ فَعَلَيْهَا وَمَا رَبُّكَ بِظَلَّٰمٍ لِّلْعَبِيدِ', 'من عمل صالحا فلنفسه ومن أساء فعليها وما ربك بظلام للعبيد', 24, 48, 481, 192, '41:46'),
(4265, NULL, NULL, 41, 47, 'إِلَيْهِ يُرَدُّ عِلْمُ ٱلسَّاعَةِ وَمَا تَخْرُجُ مِن ثَمَرَٰتٍ مِّنْ أَكْمَامِهَا وَمَا تَحْمِلُ مِنْ أُنثَىٰ وَلَا تَضَعُ إِلَّا بِعِلْمِهِۦ وَيَوْمَ يُنَادِيهِمْ أَيْنَ شُرَكَآءِى قَالُوٓا۟ ءَاذَنَّٰكَ مَا مِنَّا مِن شَهِيدٍ', 'إليه يرد علم الساعة وما تخرج من ثمرات من أكمامها وما تحمل من أنثى ولا تضع إلا بعلمه ويوم يناديهم أين شركائي قالوا آذناك ما منا من شهيد', 25, 49, 482, 193, '41:47'),
(4266, NULL, NULL, 41, 48, 'وَضَلَّ عَنْهُم مَّا كَانُوا۟ يَدْعُونَ مِن قَبْلُ وَظَنُّوا۟ مَا لَهُم مِّن مَّحِيصٍ', 'وضل عنهم ما كانوا يدعون من قبل وظنوا ما لهم من محيص', 25, 49, 482, 193, '41:48'),
(4267, NULL, NULL, 41, 49, 'لَّا يَسْـَٔمُ ٱلْإِنسَٰنُ مِن دُعَآءِ ٱلْخَيْرِ وَإِن مَّسَّهُ ٱلشَّرُّ فَيَـُٔوسٌ قَنُوطٌ', 'لا يسأم الإنسان من دعاء الخير وإن مسه الشر فيئوس قنوط', 25, 49, 482, 193, '41:49'),
(4268, NULL, NULL, 41, 50, 'وَلَئِنْ أَذَقْنَٰهُ رَحْمَةً مِّنَّا مِنۢ بَعْدِ ضَرَّآءَ مَسَّتْهُ لَيَقُولَنَّ هَٰذَا لِى وَمَآ أَظُنُّ ٱلسَّاعَةَ قَآئِمَةً وَلَئِن رُّجِعْتُ إِلَىٰ رَبِّىٓ إِنَّ لِى عِندَهُۥ لَلْحُسْنَىٰ فَلَنُنَبِّئَنَّ ٱلَّذِينَ كَفَرُوا۟ بِمَا عَمِلُوا۟ وَلَنُذِيقَنَّهُم مِّنْ عَذَابٍ غَلِيظٍ', 'ولئن أذقناه رحمة منا من بعد ضراء مسته ليقولن هذا لي وما أظن الساعة قائمة ولئن رجعت إلى ربي إن لي عنده للحسنى فلننبئن الذين كفروا بما عملوا ولنذيقنهم من عذاب غليظ', 25, 49, 482, 193, '41:50'),
(4269, NULL, NULL, 41, 51, 'وَإِذَآ أَنْعَمْنَا عَلَى ٱلْإِنسَٰنِ أَعْرَضَ وَنَـَٔا بِجَانِبِهِۦ وَإِذَا مَسَّهُ ٱلشَّرُّ فَذُو دُعَآءٍ عَرِيضٍ', 'وإذا أنعمنا على الإنسان أعرض ونأى بجانبه وإذا مسه الشر فذو دعاء عريض', 25, 49, 482, 193, '41:51'),
(4270, NULL, NULL, 41, 52, 'قُلْ أَرَءَيْتُمْ إِن كَانَ مِنْ عِندِ ٱللَّهِ ثُمَّ كَفَرْتُم بِهِۦ مَنْ أَضَلُّ مِمَّنْ هُوَ فِى شِقَاقٍۭ بَعِيدٍ', 'قل أرأيتم إن كان من عند الله ثم كفرتم به من أضل ممن هو في شقاق بعيد', 25, 49, 482, 193, '41:52'),
(4271, NULL, NULL, 41, 53, 'سَنُرِيهِمْ ءَايَٰتِنَا فِى ٱلْءَافَاقِ وَفِىٓ أَنفُسِهِمْ حَتَّىٰ يَتَبَيَّنَ لَهُمْ أَنَّهُ ٱلْحَقُّ أَوَلَمْ يَكْفِ بِرَبِّكَ أَنَّهُۥ عَلَىٰ كُلِّ شَىْءٍ شَهِيدٌ', 'سنريهم آياتنا في الآفاق وفي أنفسهم حتى يتبين لهم أنه الحق أولم يكف بربك أنه على كل شيء شهيد', 25, 49, 482, 193, '41:53'),
(4272, NULL, NULL, 41, 54, 'أَلَآ إِنَّهُمْ فِى مِرْيَةٍ مِّن لِّقَآءِ رَبِّهِمْ أَلَآ إِنَّهُۥ بِكُلِّ شَىْءٍ مُّحِيطٌۢ', 'ألا إنهم في مرية من لقاء ربهم ألا إنه بكل شيء محيط', 25, 49, 482, 193, '41:54'),
(4273, NULL, NULL, 42, 1, 'حمٓ', 'حم', 25, 49, 483, 193, '42:1'),
(4274, NULL, NULL, 42, 2, 'عٓسٓقٓ', 'عسق', 25, 49, 483, 193, '42:2'),
(4275, NULL, NULL, 42, 3, 'كَذَٰلِكَ يُوحِىٓ إِلَيْكَ وَإِلَى ٱلَّذِينَ مِن قَبْلِكَ ٱللَّهُ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'كذلك يوحي إليك وإلى الذين من قبلك الله العزيز الحكيم', 25, 49, 483, 193, '42:3'),
(4276, NULL, NULL, 42, 4, 'لَهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَهُوَ ٱلْعَلِىُّ ٱلْعَظِيمُ', 'له ما في السماوات وما في الأرض وهو العلي العظيم', 25, 49, 483, 193, '42:4'),
(4277, NULL, NULL, 42, 5, 'تَكَادُ ٱلسَّمَٰوَٰتُ يَتَفَطَّرْنَ مِن فَوْقِهِنَّ وَٱلْمَلَٰٓئِكَةُ يُسَبِّحُونَ بِحَمْدِ رَبِّهِمْ وَيَسْتَغْفِرُونَ لِمَن فِى ٱلْأَرْضِ أَلَآ إِنَّ ٱللَّهَ هُوَ ٱلْغَفُورُ ٱلرَّحِيمُ', 'تكاد السماوات يتفطرن من فوقهن والملائكة يسبحون بحمد ربهم ويستغفرون لمن في الأرض ألا إن الله هو الغفور الرحيم', 25, 49, 483, 193, '42:5'),
(4278, NULL, NULL, 42, 6, 'وَٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ ٱللَّهُ حَفِيظٌ عَلَيْهِمْ وَمَآ أَنتَ عَلَيْهِم بِوَكِيلٍ', 'والذين اتخذوا من دونه أولياء الله حفيظ عليهم وما أنت عليهم بوكيل', 25, 49, 483, 193, '42:6'),
(4279, NULL, NULL, 42, 7, 'وَكَذَٰلِكَ أَوْحَيْنَآ إِلَيْكَ قُرْءَانًا عَرَبِيًّا لِّتُنذِرَ أُمَّ ٱلْقُرَىٰ وَمَنْ حَوْلَهَا وَتُنذِرَ يَوْمَ ٱلْجَمْعِ لَا رَيْبَ فِيهِ فَرِيقٌ فِى ٱلْجَنَّةِ وَفَرِيقٌ فِى ٱلسَّعِيرِ', 'وكذلك أوحينا إليك قرآنا عربيا لتنذر أم القرى ومن حولها وتنذر يوم الجمع لا ريب فيه فريق في الجنة وفريق في السعير', 25, 49, 483, 193, '42:7'),
(4280, NULL, NULL, 42, 8, 'وَلَوْ شَآءَ ٱللَّهُ لَجَعَلَهُمْ أُمَّةً وَٰحِدَةً وَلَٰكِن يُدْخِلُ مَن يَشَآءُ فِى رَحْمَتِهِۦ وَٱلظَّٰلِمُونَ مَا لَهُم مِّن وَلِىٍّ وَلَا نَصِيرٍ', 'ولو شاء الله لجعلهم أمة واحدة ولكن يدخل من يشاء في رحمته والظالمون ما لهم من ولي ولا نصير', 25, 49, 483, 193, '42:8'),
(4281, NULL, NULL, 42, 9, 'أَمِ ٱتَّخَذُوا۟ مِن دُونِهِۦٓ أَوْلِيَآءَ فَٱللَّهُ هُوَ ٱلْوَلِىُّ وَهُوَ يُحْىِ ٱلْمَوْتَىٰ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'أم اتخذوا من دونه أولياء فالله هو الولي وهو يحيي الموتى وهو على كل شيء قدير', 25, 49, 483, 193, '42:9'),
(4282, NULL, NULL, 42, 10, 'وَمَا ٱخْتَلَفْتُمْ فِيهِ مِن شَىْءٍ فَحُكْمُهُۥٓ إِلَى ٱللَّهِ ذَٰلِكُمُ ٱللَّهُ رَبِّى عَلَيْهِ تَوَكَّلْتُ وَإِلَيْهِ أُنِيبُ', 'وما اختلفتم فيه من شيء فحكمه إلى الله ذلكم الله ربي عليه توكلت وإليه أنيب', 25, 49, 483, 193, '42:10'),
(4283, NULL, NULL, 42, 11, 'فَاطِرُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ جَعَلَ لَكُم مِّنْ أَنفُسِكُمْ أَزْوَٰجًا وَمِنَ ٱلْأَنْعَٰمِ أَزْوَٰجًا يَذْرَؤُكُمْ فِيهِ لَيْسَ كَمِثْلِهِۦ شَىْءٌ وَهُوَ ٱلسَّمِيعُ ٱلْبَصِيرُ', 'فاطر السماوات والأرض جعل لكم من أنفسكم أزواجا ومن الأنعام أزواجا يذرؤكم فيه ليس كمثله شيء وهو السميع البصير', 25, 49, 484, 193, '42:11'),
(4284, NULL, NULL, 42, 12, 'لَهُۥ مَقَالِيدُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ يَبْسُطُ ٱلرِّزْقَ لِمَن يَشَآءُ وَيَقْدِرُ إِنَّهُۥ بِكُلِّ شَىْءٍ عَلِيمٌ', 'له مقاليد السماوات والأرض يبسط الرزق لمن يشاء ويقدر إنه بكل شيء عليم', 25, 49, 484, 193, '42:12'),
(4285, NULL, NULL, 42, 13, 'شَرَعَ لَكُم مِّنَ ٱلدِّينِ مَا وَصَّىٰ بِهِۦ نُوحًا وَٱلَّذِىٓ أَوْحَيْنَآ إِلَيْكَ وَمَا وَصَّيْنَا بِهِۦٓ إِبْرَٰهِيمَ وَمُوسَىٰ وَعِيسَىٰٓ أَنْ أَقِيمُوا۟ ٱلدِّينَ وَلَا تَتَفَرَّقُوا۟ فِيهِ كَبُرَ عَلَى ٱلْمُشْرِكِينَ مَا تَدْعُوهُمْ إِلَيْهِ ٱللَّهُ يَجْتَبِىٓ إِلَيْهِ مَن يَشَآءُ وَيَهْدِىٓ إِلَيْهِ مَن يُنِيبُ', 'شرع لكم من الدين ما وصى به نوحا والذي أوحينا إليك وما وصينا به إبراهيم وموسى وعيسى أن أقيموا الدين ولا تتفرقوا فيه كبر على المشركين ما تدعوهم إليه الله يجتبي إليه من يشاء ويهدي إليه من ينيب', 25, 49, 484, 194, '42:13'),
(4286, NULL, NULL, 42, 14, 'وَمَا تَفَرَّقُوٓا۟ إِلَّا مِنۢ بَعْدِ مَا جَآءَهُمُ ٱلْعِلْمُ بَغْيًۢا بَيْنَهُمْ وَلَوْلَا كَلِمَةٌ سَبَقَتْ مِن رَّبِّكَ إِلَىٰٓ أَجَلٍ مُّسَمًّى لَّقُضِىَ بَيْنَهُمْ وَإِنَّ ٱلَّذِينَ أُورِثُوا۟ ٱلْكِتَٰبَ مِنۢ بَعْدِهِمْ لَفِى شَكٍّ مِّنْهُ مُرِيبٍ', 'وما تفرقوا إلا من بعد ما جاءهم العلم بغيا بينهم ولولا كلمة سبقت من ربك إلى أجل مسمى لقضي بينهم وإن الذين أورثوا الكتاب من بعدهم لفي شك منه مريب', 25, 49, 484, 194, '42:14'),
(4287, NULL, NULL, 42, 15, 'فَلِذَٰلِكَ فَٱدْعُ وَٱسْتَقِمْ كَمَآ أُمِرْتَ وَلَا تَتَّبِعْ أَهْوَآءَهُمْ وَقُلْ ءَامَنتُ بِمَآ أَنزَلَ ٱللَّهُ مِن كِتَٰبٍ وَأُمِرْتُ لِأَعْدِلَ بَيْنَكُمُ ٱللَّهُ رَبُّنَا وَرَبُّكُمْ لَنَآ أَعْمَٰلُنَا وَلَكُمْ أَعْمَٰلُكُمْ لَا حُجَّةَ بَيْنَنَا وَبَيْنَكُمُ ٱللَّهُ يَجْمَعُ بَيْنَنَا وَإِلَيْهِ ٱلْمَصِيرُ', 'فلذلك فادع واستقم كما أمرت ولا تتبع أهواءهم وقل آمنت بما أنزل الله من كتاب وأمرت لأعدل بينكم الله ربنا وربكم لنا أعمالنا ولكم أعمالكم لا حجة بيننا وبينكم الله يجمع بيننا وإليه المصير', 25, 49, 484, 194, '42:15'),
(4288, NULL, NULL, 42, 16, 'وَٱلَّذِينَ يُحَآجُّونَ فِى ٱللَّهِ مِنۢ بَعْدِ مَا ٱسْتُجِيبَ لَهُۥ حُجَّتُهُمْ دَاحِضَةٌ عِندَ رَبِّهِمْ وَعَلَيْهِمْ غَضَبٌ وَلَهُمْ عَذَابٌ شَدِيدٌ', 'والذين يحاجون في الله من بعد ما استجيب له حجتهم داحضة عند ربهم وعليهم غضب ولهم عذاب شديد', 25, 49, 485, 194, '42:16'),
(4289, NULL, NULL, 42, 17, 'ٱللَّهُ ٱلَّذِىٓ أَنزَلَ ٱلْكِتَٰبَ بِٱلْحَقِّ وَٱلْمِيزَانَ وَمَا يُدْرِيكَ لَعَلَّ ٱلسَّاعَةَ قَرِيبٌ', 'الله الذي أنزل الكتاب بالحق والميزان وما يدريك لعل الساعة قريب', 25, 49, 485, 194, '42:17'),
(4290, NULL, NULL, 42, 18, 'يَسْتَعْجِلُ بِهَا ٱلَّذِينَ لَا يُؤْمِنُونَ بِهَا وَٱلَّذِينَ ءَامَنُوا۟ مُشْفِقُونَ مِنْهَا وَيَعْلَمُونَ أَنَّهَا ٱلْحَقُّ أَلَآ إِنَّ ٱلَّذِينَ يُمَارُونَ فِى ٱلسَّاعَةِ لَفِى ضَلَٰلٍۭ بَعِيدٍ', 'يستعجل بها الذين لا يؤمنون بها والذين آمنوا مشفقون منها ويعلمون أنها الحق ألا إن الذين يمارون في الساعة لفي ضلال بعيد', 25, 49, 485, 194, '42:18'),
(4291, NULL, NULL, 42, 19, 'ٱللَّهُ لَطِيفٌۢ بِعِبَادِهِۦ يَرْزُقُ مَن يَشَآءُ وَهُوَ ٱلْقَوِىُّ ٱلْعَزِيزُ', 'الله لطيف بعباده يرزق من يشاء وهو القوي العزيز', 25, 49, 485, 194, '42:19'),
(4292, NULL, NULL, 42, 20, 'مَن كَانَ يُرِيدُ حَرْثَ ٱلْءَاخِرَةِ نَزِدْ لَهُۥ فِى حَرْثِهِۦ وَمَن كَانَ يُرِيدُ حَرْثَ ٱلدُّنْيَا نُؤْتِهِۦ مِنْهَا وَمَا لَهُۥ فِى ٱلْءَاخِرَةِ مِن نَّصِيبٍ', 'من كان يريد حرث الآخرة نزد له في حرثه ومن كان يريد حرث الدنيا نؤته منها وما له في الآخرة من نصيب', 25, 49, 485, 194, '42:20'),
(4293, NULL, NULL, 42, 21, 'أَمْ لَهُمْ شُرَكَٰٓؤُا۟ شَرَعُوا۟ لَهُم مِّنَ ٱلدِّينِ مَا لَمْ يَأْذَنۢ بِهِ ٱللَّهُ وَلَوْلَا كَلِمَةُ ٱلْفَصْلِ لَقُضِىَ بَيْنَهُمْ وَإِنَّ ٱلظَّٰلِمِينَ لَهُمْ عَذَابٌ أَلِيمٌ', 'أم لهم شركاء شرعوا لهم من الدين ما لم يأذن به الله ولولا كلمة الفصل لقضي بينهم وإن الظالمين لهم عذاب أليم', 25, 49, 485, 194, '42:21'),
(4294, NULL, NULL, 42, 22, 'تَرَى ٱلظَّٰلِمِينَ مُشْفِقِينَ مِمَّا كَسَبُوا۟ وَهُوَ وَاقِعٌۢ بِهِمْ وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ فِى رَوْضَاتِ ٱلْجَنَّاتِ لَهُم مَّا يَشَآءُونَ عِندَ رَبِّهِمْ ذَٰلِكَ هُوَ ٱلْفَضْلُ ٱلْكَبِيرُ', 'ترى الظالمين مشفقين مما كسبوا وهو واقع بهم والذين آمنوا وعملوا الصالحات في روضات الجنات لهم ما يشاءون عند ربهم ذلك هو الفضل الكبير', 25, 49, 485, 194, '42:22'),
(4295, NULL, NULL, 42, 23, 'ذَٰلِكَ ٱلَّذِى يُبَشِّرُ ٱللَّهُ عِبَادَهُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ قُل لَّآ أَسْـَٔلُكُمْ عَلَيْهِ أَجْرًا إِلَّا ٱلْمَوَدَّةَ فِى ٱلْقُرْبَىٰ وَمَن يَقْتَرِفْ حَسَنَةً نَّزِدْ لَهُۥ فِيهَا حُسْنًا إِنَّ ٱللَّهَ غَفُورٌ شَكُورٌ', 'ذلك الذي يبشر الله عباده الذين آمنوا وعملوا الصالحات قل لا أسألكم عليه أجرا إلا المودة في القربى ومن يقترف حسنة نزد له فيها حسنا إن الله غفور شكور', 25, 49, 486, 194, '42:23'),
(4296, NULL, NULL, 42, 24, 'أَمْ يَقُولُونَ ٱفْتَرَىٰ عَلَى ٱللَّهِ كَذِبًا فَإِن يَشَإِ ٱللَّهُ يَخْتِمْ عَلَىٰ قَلْبِكَ وَيَمْحُ ٱللَّهُ ٱلْبَٰطِلَ وَيُحِقُّ ٱلْحَقَّ بِكَلِمَٰتِهِۦٓ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ', 'أم يقولون افترى على الله كذبا فإن يشإ الله يختم على قلبك ويمح الله الباطل ويحق الحق بكلماته إنه عليم بذات الصدور', 25, 49, 486, 194, '42:24'),
(4297, NULL, NULL, 42, 25, 'وَهُوَ ٱلَّذِى يَقْبَلُ ٱلتَّوْبَةَ عَنْ عِبَادِهِۦ وَيَعْفُوا۟ عَنِ ٱلسَّيِّـَٔاتِ وَيَعْلَمُ مَا تَفْعَلُونَ', 'وهو الذي يقبل التوبة عن عباده ويعفو عن السيئات ويعلم ما تفعلون', 25, 49, 486, 194, '42:25'),
(4298, NULL, NULL, 42, 26, 'وَيَسْتَجِيبُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَيَزِيدُهُم مِّن فَضْلِهِۦ وَٱلْكَٰفِرُونَ لَهُمْ عَذَابٌ شَدِيدٌ', 'ويستجيب الذين آمنوا وعملوا الصالحات ويزيدهم من فضله والكافرون لهم عذاب شديد', 25, 49, 486, 194, '42:26');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(4299, NULL, NULL, 42, 27, 'وَلَوْ بَسَطَ ٱللَّهُ ٱلرِّزْقَ لِعِبَادِهِۦ لَبَغَوْا۟ فِى ٱلْأَرْضِ وَلَٰكِن يُنَزِّلُ بِقَدَرٍ مَّا يَشَآءُ إِنَّهُۥ بِعِبَادِهِۦ خَبِيرٌۢ بَصِيرٌ', 'ولو بسط الله الرزق لعباده لبغوا في الأرض ولكن ينزل بقدر ما يشاء إنه بعباده خبير بصير', 25, 49, 486, 195, '42:27'),
(4300, NULL, NULL, 42, 28, 'وَهُوَ ٱلَّذِى يُنَزِّلُ ٱلْغَيْثَ مِنۢ بَعْدِ مَا قَنَطُوا۟ وَيَنشُرُ رَحْمَتَهُۥ وَهُوَ ٱلْوَلِىُّ ٱلْحَمِيدُ', 'وهو الذي ينزل الغيث من بعد ما قنطوا وينشر رحمته وهو الولي الحميد', 25, 49, 486, 195, '42:28'),
(4301, NULL, NULL, 42, 29, 'وَمِنْ ءَايَٰتِهِۦ خَلْقُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَثَّ فِيهِمَا مِن دَآبَّةٍ وَهُوَ عَلَىٰ جَمْعِهِمْ إِذَا يَشَآءُ قَدِيرٌ', 'ومن آياته خلق السماوات والأرض وما بث فيهما من دابة وهو على جمعهم إذا يشاء قدير', 25, 49, 486, 195, '42:29'),
(4302, NULL, NULL, 42, 30, 'وَمَآ أَصَٰبَكُم مِّن مُّصِيبَةٍ فَبِمَا كَسَبَتْ أَيْدِيكُمْ وَيَعْفُوا۟ عَن كَثِيرٍ', 'وما أصابكم من مصيبة فبما كسبت أيديكم ويعفو عن كثير', 25, 49, 486, 195, '42:30'),
(4303, NULL, NULL, 42, 31, 'وَمَآ أَنتُم بِمُعْجِزِينَ فِى ٱلْأَرْضِ وَمَا لَكُم مِّن دُونِ ٱللَّهِ مِن وَلِىٍّ وَلَا نَصِيرٍ', 'وما أنتم بمعجزين في الأرض وما لكم من دون الله من ولي ولا نصير', 25, 49, 486, 195, '42:31'),
(4304, NULL, NULL, 42, 32, 'وَمِنْ ءَايَٰتِهِ ٱلْجَوَارِ فِى ٱلْبَحْرِ كَٱلْأَعْلَٰمِ', 'ومن آياته الجوار في البحر كالأعلام', 25, 49, 487, 195, '42:32'),
(4305, NULL, NULL, 42, 33, 'إِن يَشَأْ يُسْكِنِ ٱلرِّيحَ فَيَظْلَلْنَ رَوَاكِدَ عَلَىٰ ظَهْرِهِۦٓ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّكُلِّ صَبَّارٍ شَكُورٍ', 'إن يشأ يسكن الريح فيظللن رواكد على ظهره إن في ذلك لآيات لكل صبار شكور', 25, 49, 487, 195, '42:33'),
(4306, NULL, NULL, 42, 34, 'أَوْ يُوبِقْهُنَّ بِمَا كَسَبُوا۟ وَيَعْفُ عَن كَثِيرٍ', 'أو يوبقهن بما كسبوا ويعف عن كثير', 25, 49, 487, 195, '42:34'),
(4307, NULL, NULL, 42, 35, 'وَيَعْلَمَ ٱلَّذِينَ يُجَٰدِلُونَ فِىٓ ءَايَٰتِنَا مَا لَهُم مِّن مَّحِيصٍ', 'ويعلم الذين يجادلون في آياتنا ما لهم من محيص', 25, 49, 487, 195, '42:35'),
(4308, NULL, NULL, 42, 36, 'فَمَآ أُوتِيتُم مِّن شَىْءٍ فَمَتَٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا وَمَا عِندَ ٱللَّهِ خَيْرٌ وَأَبْقَىٰ لِلَّذِينَ ءَامَنُوا۟ وَعَلَىٰ رَبِّهِمْ يَتَوَكَّلُونَ', 'فما أوتيتم من شيء فمتاع الحياة الدنيا وما عند الله خير وأبقى للذين آمنوا وعلى ربهم يتوكلون', 25, 49, 487, 195, '42:36'),
(4309, NULL, NULL, 42, 37, 'وَٱلَّذِينَ يَجْتَنِبُونَ كَبَٰٓئِرَ ٱلْإِثْمِ وَٱلْفَوَٰحِشَ وَإِذَا مَا غَضِبُوا۟ هُمْ يَغْفِرُونَ', 'والذين يجتنبون كبائر الإثم والفواحش وإذا ما غضبوا هم يغفرون', 25, 49, 487, 195, '42:37'),
(4310, NULL, NULL, 42, 38, 'وَٱلَّذِينَ ٱسْتَجَابُوا۟ لِرَبِّهِمْ وَأَقَامُوا۟ ٱلصَّلَوٰةَ وَأَمْرُهُمْ شُورَىٰ بَيْنَهُمْ وَمِمَّا رَزَقْنَٰهُمْ يُنفِقُونَ', 'والذين استجابوا لربهم وأقاموا الصلاة وأمرهم شورى بينهم ومما رزقناهم ينفقون', 25, 49, 487, 195, '42:38'),
(4311, NULL, NULL, 42, 39, 'وَٱلَّذِينَ إِذَآ أَصَابَهُمُ ٱلْبَغْىُ هُمْ يَنتَصِرُونَ', 'والذين إذا أصابهم البغي هم ينتصرون', 25, 49, 487, 195, '42:39'),
(4312, NULL, NULL, 42, 40, 'وَجَزَٰٓؤُا۟ سَيِّئَةٍ سَيِّئَةٌ مِّثْلُهَا فَمَنْ عَفَا وَأَصْلَحَ فَأَجْرُهُۥ عَلَى ٱللَّهِ إِنَّهُۥ لَا يُحِبُّ ٱلظَّٰلِمِينَ', 'وجزاء سيئة سيئة مثلها فمن عفا وأصلح فأجره على الله إنه لا يحب الظالمين', 25, 49, 487, 195, '42:40'),
(4313, NULL, NULL, 42, 41, 'وَلَمَنِ ٱنتَصَرَ بَعْدَ ظُلْمِهِۦ فَأُو۟لَٰٓئِكَ مَا عَلَيْهِم مِّن سَبِيلٍ', 'ولمن انتصر بعد ظلمه فأولئك ما عليهم من سبيل', 25, 49, 487, 195, '42:41'),
(4314, NULL, NULL, 42, 42, 'إِنَّمَا ٱلسَّبِيلُ عَلَى ٱلَّذِينَ يَظْلِمُونَ ٱلنَّاسَ وَيَبْغُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ أُو۟لَٰٓئِكَ لَهُمْ عَذَابٌ أَلِيمٌ', 'إنما السبيل على الذين يظلمون الناس ويبغون في الأرض بغير الحق أولئك لهم عذاب أليم', 25, 49, 487, 195, '42:42'),
(4315, NULL, NULL, 42, 43, 'وَلَمَن صَبَرَ وَغَفَرَ إِنَّ ذَٰلِكَ لَمِنْ عَزْمِ ٱلْأُمُورِ', 'ولمن صبر وغفر إن ذلك لمن عزم الأمور', 25, 49, 487, 195, '42:43'),
(4316, NULL, NULL, 42, 44, 'وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِن وَلِىٍّ مِّنۢ بَعْدِهِۦ وَتَرَى ٱلظَّٰلِمِينَ لَمَّا رَأَوُا۟ ٱلْعَذَابَ يَقُولُونَ هَلْ إِلَىٰ مَرَدٍّ مِّن سَبِيلٍ', 'ومن يضلل الله فما له من ولي من بعده وترى الظالمين لما رأوا العذاب يقولون هل إلى مرد من سبيل', 25, 49, 487, 195, '42:44'),
(4317, NULL, NULL, 42, 45, 'وَتَرَىٰهُمْ يُعْرَضُونَ عَلَيْهَا خَٰشِعِينَ مِنَ ٱلذُّلِّ يَنظُرُونَ مِن طَرْفٍ خَفِىٍّ وَقَالَ ٱلَّذِينَ ءَامَنُوٓا۟ إِنَّ ٱلْخَٰسِرِينَ ٱلَّذِينَ خَسِرُوٓا۟ أَنفُسَهُمْ وَأَهْلِيهِمْ يَوْمَ ٱلْقِيَٰمَةِ أَلَآ إِنَّ ٱلظَّٰلِمِينَ فِى عَذَابٍ مُّقِيمٍ', 'وتراهم يعرضون عليها خاشعين من الذل ينظرون من طرف خفي وقال الذين آمنوا إن الخاسرين الذين خسروا أنفسهم وأهليهم يوم القيامة ألا إن الظالمين في عذاب مقيم', 25, 49, 488, 195, '42:45'),
(4318, NULL, NULL, 42, 46, 'وَمَا كَانَ لَهُم مِّنْ أَوْلِيَآءَ يَنصُرُونَهُم مِّن دُونِ ٱللَّهِ وَمَن يُضْلِلِ ٱللَّهُ فَمَا لَهُۥ مِن سَبِيلٍ', 'وما كان لهم من أولياء ينصرونهم من دون الله ومن يضلل الله فما له من سبيل', 25, 49, 488, 195, '42:46'),
(4319, NULL, NULL, 42, 47, 'ٱسْتَجِيبُوا۟ لِرَبِّكُم مِّن قَبْلِ أَن يَأْتِىَ يَوْمٌ لَّا مَرَدَّ لَهُۥ مِنَ ٱللَّهِ مَا لَكُم مِّن مَّلْجَإٍ يَوْمَئِذٍ وَمَا لَكُم مِّن نَّكِيرٍ', 'استجيبوا لربكم من قبل أن يأتي يوم لا مرد له من الله ما لكم من ملجإ يومئذ وما لكم من نكير', 25, 49, 488, 195, '42:47'),
(4320, NULL, NULL, 42, 48, 'فَإِنْ أَعْرَضُوا۟ فَمَآ أَرْسَلْنَٰكَ عَلَيْهِمْ حَفِيظًا إِنْ عَلَيْكَ إِلَّا ٱلْبَلَٰغُ وَإِنَّآ إِذَآ أَذَقْنَا ٱلْإِنسَٰنَ مِنَّا رَحْمَةً فَرِحَ بِهَا وَإِن تُصِبْهُمْ سَيِّئَةٌۢ بِمَا قَدَّمَتْ أَيْدِيهِمْ فَإِنَّ ٱلْإِنسَٰنَ كَفُورٌ', 'فإن أعرضوا فما أرسلناك عليهم حفيظا إن عليك إلا البلاغ وإنا إذا أذقنا الإنسان منا رحمة فرح بها وإن تصبهم سيئة بما قدمت أيديهم فإن الإنسان كفور', 25, 49, 488, 195, '42:48'),
(4321, NULL, NULL, 42, 49, 'لِّلَّهِ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ يَخْلُقُ مَا يَشَآءُ يَهَبُ لِمَن يَشَآءُ إِنَٰثًا وَيَهَبُ لِمَن يَشَآءُ ٱلذُّكُورَ', 'لله ملك السماوات والأرض يخلق ما يشاء يهب لمن يشاء إناثا ويهب لمن يشاء الذكور', 25, 49, 488, 195, '42:49'),
(4322, NULL, NULL, 42, 50, 'أَوْ يُزَوِّجُهُمْ ذُكْرَانًا وَإِنَٰثًا وَيَجْعَلُ مَن يَشَآءُ عَقِيمًا إِنَّهُۥ عَلِيمٌ قَدِيرٌ', 'أو يزوجهم ذكرانا وإناثا ويجعل من يشاء عقيما إنه عليم قدير', 25, 49, 488, 195, '42:50'),
(4323, NULL, NULL, 42, 51, 'وَمَا كَانَ لِبَشَرٍ أَن يُكَلِّمَهُ ٱللَّهُ إِلَّا وَحْيًا أَوْ مِن وَرَآئِ حِجَابٍ أَوْ يُرْسِلَ رَسُولًا فَيُوحِىَ بِإِذْنِهِۦ مَا يَشَآءُ إِنَّهُۥ عَلِىٌّ حَكِيمٌ', 'وما كان لبشر أن يكلمه الله إلا وحيا أو من وراء حجاب أو يرسل رسولا فيوحي بإذنه ما يشاء إنه علي حكيم', 25, 49, 488, 196, '42:51'),
(4324, NULL, NULL, 42, 52, 'وَكَذَٰلِكَ أَوْحَيْنَآ إِلَيْكَ رُوحًا مِّنْ أَمْرِنَا مَا كُنتَ تَدْرِى مَا ٱلْكِتَٰبُ وَلَا ٱلْإِيمَٰنُ وَلَٰكِن جَعَلْنَٰهُ نُورًا نَّهْدِى بِهِۦ مَن نَّشَآءُ مِنْ عِبَادِنَا وَإِنَّكَ لَتَهْدِىٓ إِلَىٰ صِرَٰطٍ مُّسْتَقِيمٍ', 'وكذلك أوحينا إليك روحا من أمرنا ما كنت تدري ما الكتاب ولا الإيمان ولكن جعلناه نورا نهدي به من نشاء من عبادنا وإنك لتهدي إلى صراط مستقيم', 25, 49, 489, 196, '42:52'),
(4325, NULL, NULL, 42, 53, 'صِرَٰطِ ٱللَّهِ ٱلَّذِى لَهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ أَلَآ إِلَى ٱللَّهِ تَصِيرُ ٱلْأُمُورُ', 'صراط الله الذي له ما في السماوات وما في الأرض ألا إلى الله تصير الأمور', 25, 49, 489, 196, '42:53'),
(4326, NULL, NULL, 43, 1, 'حمٓ', 'حم', 25, 49, 489, 196, '43:1'),
(4327, NULL, NULL, 43, 2, 'وَٱلْكِتَٰبِ ٱلْمُبِينِ', 'والكتاب المبين', 25, 49, 489, 196, '43:2'),
(4328, NULL, NULL, 43, 3, 'إِنَّا جَعَلْنَٰهُ قُرْءَٰنًا عَرَبِيًّا لَّعَلَّكُمْ تَعْقِلُونَ', 'إنا جعلناه قرآنا عربيا لعلكم تعقلون', 25, 49, 489, 196, '43:3'),
(4329, NULL, NULL, 43, 4, 'وَإِنَّهُۥ فِىٓ أُمِّ ٱلْكِتَٰبِ لَدَيْنَا لَعَلِىٌّ حَكِيمٌ', 'وإنه في أم الكتاب لدينا لعلي حكيم', 25, 49, 489, 196, '43:4'),
(4330, NULL, NULL, 43, 5, 'أَفَنَضْرِبُ عَنكُمُ ٱلذِّكْرَ صَفْحًا أَن كُنتُمْ قَوْمًا مُّسْرِفِينَ', 'أفنضرب عنكم الذكر صفحا أن كنتم قوما مسرفين', 25, 49, 489, 196, '43:5'),
(4331, NULL, NULL, 43, 6, 'وَكَمْ أَرْسَلْنَا مِن نَّبِىٍّ فِى ٱلْأَوَّلِينَ', 'وكم أرسلنا من نبي في الأولين', 25, 49, 489, 196, '43:6'),
(4332, NULL, NULL, 43, 7, 'وَمَا يَأْتِيهِم مِّن نَّبِىٍّ إِلَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ', 'وما يأتيهم من نبي إلا كانوا به يستهزئون', 25, 49, 489, 196, '43:7'),
(4333, NULL, NULL, 43, 8, 'فَأَهْلَكْنَآ أَشَدَّ مِنْهُم بَطْشًا وَمَضَىٰ مَثَلُ ٱلْأَوَّلِينَ', 'فأهلكنا أشد منهم بطشا ومضى مثل الأولين', 25, 49, 489, 196, '43:8'),
(4334, NULL, NULL, 43, 9, 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ لَيَقُولُنَّ خَلَقَهُنَّ ٱلْعَزِيزُ ٱلْعَلِيمُ', 'ولئن سألتهم من خلق السماوات والأرض ليقولن خلقهن العزيز العليم', 25, 49, 489, 196, '43:9'),
(4335, NULL, NULL, 43, 10, 'ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ مَهْدًا وَجَعَلَ لَكُمْ فِيهَا سُبُلًا لَّعَلَّكُمْ تَهْتَدُونَ', 'الذي جعل لكم الأرض مهدا وجعل لكم فيها سبلا لعلكم تهتدون', 25, 49, 489, 196, '43:10'),
(4336, NULL, NULL, 43, 11, 'وَٱلَّذِى نَزَّلَ مِنَ ٱلسَّمَآءِ مَآءًۢ بِقَدَرٍ فَأَنشَرْنَا بِهِۦ بَلْدَةً مَّيْتًا كَذَٰلِكَ تُخْرَجُونَ', 'والذي نزل من السماء ماء بقدر فأنشرنا به بلدة ميتا كذلك تخرجون', 25, 49, 490, 196, '43:11'),
(4337, NULL, NULL, 43, 12, 'وَٱلَّذِى خَلَقَ ٱلْأَزْوَٰجَ كُلَّهَا وَجَعَلَ لَكُم مِّنَ ٱلْفُلْكِ وَٱلْأَنْعَٰمِ مَا تَرْكَبُونَ', 'والذي خلق الأزواج كلها وجعل لكم من الفلك والأنعام ما تركبون', 25, 49, 490, 196, '43:12'),
(4338, NULL, NULL, 43, 13, 'لِتَسْتَوُۥا۟ عَلَىٰ ظُهُورِهِۦ ثُمَّ تَذْكُرُوا۟ نِعْمَةَ رَبِّكُمْ إِذَا ٱسْتَوَيْتُمْ عَلَيْهِ وَتَقُولُوا۟ سُبْحَٰنَ ٱلَّذِى سَخَّرَ لَنَا هَٰذَا وَمَا كُنَّا لَهُۥ مُقْرِنِينَ', 'لتستووا على ظهوره ثم تذكروا نعمة ربكم إذا استويتم عليه وتقولوا سبحان الذي سخر لنا هذا وما كنا له مقرنين', 25, 49, 490, 196, '43:13'),
(4339, NULL, NULL, 43, 14, 'وَإِنَّآ إِلَىٰ رَبِّنَا لَمُنقَلِبُونَ', 'وإنا إلى ربنا لمنقلبون', 25, 49, 490, 196, '43:14'),
(4340, NULL, NULL, 43, 15, 'وَجَعَلُوا۟ لَهُۥ مِنْ عِبَادِهِۦ جُزْءًا إِنَّ ٱلْإِنسَٰنَ لَكَفُورٌ مُّبِينٌ', 'وجعلوا له من عباده جزءا إن الإنسان لكفور مبين', 25, 49, 490, 196, '43:15'),
(4341, NULL, NULL, 43, 16, 'أَمِ ٱتَّخَذَ مِمَّا يَخْلُقُ بَنَاتٍ وَأَصْفَىٰكُم بِٱلْبَنِينَ', 'أم اتخذ مما يخلق بنات وأصفاكم بالبنين', 25, 49, 490, 196, '43:16'),
(4342, NULL, NULL, 43, 17, 'وَإِذَا بُشِّرَ أَحَدُهُم بِمَا ضَرَبَ لِلرَّحْمَٰنِ مَثَلًا ظَلَّ وَجْهُهُۥ مُسْوَدًّا وَهُوَ كَظِيمٌ', 'وإذا بشر أحدهم بما ضرب للرحمن مثلا ظل وجهه مسودا وهو كظيم', 25, 49, 490, 196, '43:17'),
(4343, NULL, NULL, 43, 18, 'أَوَمَن يُنَشَّؤُا۟ فِى ٱلْحِلْيَةِ وَهُوَ فِى ٱلْخِصَامِ غَيْرُ مُبِينٍ', 'أومن ينشأ في الحلية وهو في الخصام غير مبين', 25, 49, 490, 196, '43:18'),
(4344, NULL, NULL, 43, 19, 'وَجَعَلُوا۟ ٱلْمَلَٰٓئِكَةَ ٱلَّذِينَ هُمْ عِبَٰدُ ٱلرَّحْمَٰنِ إِنَٰثًا أَشَهِدُوا۟ خَلْقَهُمْ سَتُكْتَبُ شَهَٰدَتُهُمْ وَيُسْـَٔلُونَ', 'وجعلوا الملائكة الذين هم عباد الرحمن إناثا أشهدوا خلقهم ستكتب شهادتهم ويسألون', 25, 49, 490, 196, '43:19'),
(4345, NULL, NULL, 43, 20, 'وَقَالُوا۟ لَوْ شَآءَ ٱلرَّحْمَٰنُ مَا عَبَدْنَٰهُم مَّا لَهُم بِذَٰلِكَ مِنْ عِلْمٍ إِنْ هُمْ إِلَّا يَخْرُصُونَ', 'وقالوا لو شاء الرحمن ما عبدناهم ما لهم بذلك من علم إن هم إلا يخرصون', 25, 49, 490, 196, '43:20'),
(4346, NULL, NULL, 43, 21, 'أَمْ ءَاتَيْنَٰهُمْ كِتَٰبًا مِّن قَبْلِهِۦ فَهُم بِهِۦ مُسْتَمْسِكُونَ', 'أم آتيناهم كتابا من قبله فهم به مستمسكون', 25, 49, 490, 196, '43:21'),
(4347, NULL, NULL, 43, 22, 'بَلْ قَالُوٓا۟ إِنَّا وَجَدْنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٍ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّهْتَدُونَ', 'بل قالوا إنا وجدنا آباءنا على أمة وإنا على آثارهم مهتدون', 25, 49, 490, 196, '43:22'),
(4348, NULL, NULL, 43, 23, 'وَكَذَٰلِكَ مَآ أَرْسَلْنَا مِن قَبْلِكَ فِى قَرْيَةٍ مِّن نَّذِيرٍ إِلَّا قَالَ مُتْرَفُوهَآ إِنَّا وَجَدْنَآ ءَابَآءَنَا عَلَىٰٓ أُمَّةٍ وَإِنَّا عَلَىٰٓ ءَاثَٰرِهِم مُّقْتَدُونَ', 'وكذلك ما أرسلنا من قبلك في قرية من نذير إلا قال مترفوها إنا وجدنا آباءنا على أمة وإنا على آثارهم مقتدون', 25, 49, 491, 196, '43:23'),
(4349, NULL, NULL, 43, 24, 'قَٰلَ أَوَلَوْ جِئْتُكُم بِأَهْدَىٰ مِمَّا وَجَدتُّمْ عَلَيْهِ ءَابَآءَكُمْ قَالُوٓا۟ إِنَّا بِمَآ أُرْسِلْتُم بِهِۦ كَٰفِرُونَ', 'قال أولو جئتكم بأهدى مما وجدتم عليه آباءكم قالوا إنا بما أرسلتم به كافرون', 25, 50, 491, 197, '43:24'),
(4350, NULL, NULL, 43, 25, 'فَٱنتَقَمْنَا مِنْهُمْ فَٱنظُرْ كَيْفَ كَانَ عَٰقِبَةُ ٱلْمُكَذِّبِينَ', 'فانتقمنا منهم فانظر كيف كان عاقبة المكذبين', 25, 50, 491, 197, '43:25'),
(4351, NULL, NULL, 43, 26, 'وَإِذْ قَالَ إِبْرَٰهِيمُ لِأَبِيهِ وَقَوْمِهِۦٓ إِنَّنِى بَرَآءٌ مِّمَّا تَعْبُدُونَ', 'وإذ قال إبراهيم لأبيه وقومه إنني براء مما تعبدون', 25, 50, 491, 197, '43:26'),
(4352, NULL, NULL, 43, 27, 'إِلَّا ٱلَّذِى فَطَرَنِى فَإِنَّهُۥ سَيَهْدِينِ', 'إلا الذي فطرني فإنه سيهدين', 25, 50, 491, 197, '43:27'),
(4353, NULL, NULL, 43, 28, 'وَجَعَلَهَا كَلِمَةًۢ بَاقِيَةً فِى عَقِبِهِۦ لَعَلَّهُمْ يَرْجِعُونَ', 'وجعلها كلمة باقية في عقبه لعلهم يرجعون', 25, 50, 491, 197, '43:28'),
(4354, NULL, NULL, 43, 29, 'بَلْ مَتَّعْتُ هَٰٓؤُلَآءِ وَءَابَآءَهُمْ حَتَّىٰ جَآءَهُمُ ٱلْحَقُّ وَرَسُولٌ مُّبِينٌ', 'بل متعت هؤلاء وآباءهم حتى جاءهم الحق ورسول مبين', 25, 50, 491, 197, '43:29'),
(4355, NULL, NULL, 43, 30, 'وَلَمَّا جَآءَهُمُ ٱلْحَقُّ قَالُوا۟ هَٰذَا سِحْرٌ وَإِنَّا بِهِۦ كَٰفِرُونَ', 'ولما جاءهم الحق قالوا هذا سحر وإنا به كافرون', 25, 50, 491, 197, '43:30'),
(4356, NULL, NULL, 43, 31, 'وَقَالُوا۟ لَوْلَا نُزِّلَ هَٰذَا ٱلْقُرْءَانُ عَلَىٰ رَجُلٍ مِّنَ ٱلْقَرْيَتَيْنِ عَظِيمٍ', 'وقالوا لولا نزل هذا القرآن على رجل من القريتين عظيم', 25, 50, 491, 197, '43:31'),
(4357, NULL, NULL, 43, 32, 'أَهُمْ يَقْسِمُونَ رَحْمَتَ رَبِّكَ نَحْنُ قَسَمْنَا بَيْنَهُم مَّعِيشَتَهُمْ فِى ٱلْحَيَوٰةِ ٱلدُّنْيَا وَرَفَعْنَا بَعْضَهُمْ فَوْقَ بَعْضٍ دَرَجَٰتٍ لِّيَتَّخِذَ بَعْضُهُم بَعْضًا سُخْرِيًّا وَرَحْمَتُ رَبِّكَ خَيْرٌ مِّمَّا يَجْمَعُونَ', 'أهم يقسمون رحمت ربك نحن قسمنا بينهم معيشتهم في الحياة الدنيا ورفعنا بعضهم فوق بعض درجات ليتخذ بعضهم بعضا سخريا ورحمت ربك خير مما يجمعون', 25, 50, 491, 197, '43:32'),
(4358, NULL, NULL, 43, 33, 'وَلَوْلَآ أَن يَكُونَ ٱلنَّاسُ أُمَّةً وَٰحِدَةً لَّجَعَلْنَا لِمَن يَكْفُرُ بِٱلرَّحْمَٰنِ لِبُيُوتِهِمْ سُقُفًا مِّن فِضَّةٍ وَمَعَارِجَ عَلَيْهَا يَظْهَرُونَ', 'ولولا أن يكون الناس أمة واحدة لجعلنا لمن يكفر بالرحمن لبيوتهم سقفا من فضة ومعارج عليها يظهرون', 25, 50, 491, 197, '43:33'),
(4359, NULL, NULL, 43, 34, 'وَلِبُيُوتِهِمْ أَبْوَٰبًا وَسُرُرًا عَلَيْهَا يَتَّكِـُٔونَ', 'ولبيوتهم أبوابا وسررا عليها يتكئون', 25, 50, 492, 197, '43:34'),
(4360, NULL, NULL, 43, 35, 'وَزُخْرُفًا وَإِن كُلُّ ذَٰلِكَ لَمَّا مَتَٰعُ ٱلْحَيَوٰةِ ٱلدُّنْيَا وَٱلْءَاخِرَةُ عِندَ رَبِّكَ لِلْمُتَّقِينَ', 'وزخرفا وإن كل ذلك لما متاع الحياة الدنيا والآخرة عند ربك للمتقين', 25, 50, 492, 197, '43:35'),
(4361, NULL, NULL, 43, 36, 'وَمَن يَعْشُ عَن ذِكْرِ ٱلرَّحْمَٰنِ نُقَيِّضْ لَهُۥ شَيْطَٰنًا فَهُوَ لَهُۥ قَرِينٌ', 'ومن يعش عن ذكر الرحمن نقيض له شيطانا فهو له قرين', 25, 50, 492, 197, '43:36'),
(4362, NULL, NULL, 43, 37, 'وَإِنَّهُمْ لَيَصُدُّونَهُمْ عَنِ ٱلسَّبِيلِ وَيَحْسَبُونَ أَنَّهُم مُّهْتَدُونَ', 'وإنهم ليصدونهم عن السبيل ويحسبون أنهم مهتدون', 25, 50, 492, 197, '43:37'),
(4363, NULL, NULL, 43, 38, 'حَتَّىٰٓ إِذَا جَآءَنَا قَالَ يَٰلَيْتَ بَيْنِى وَبَيْنَكَ بُعْدَ ٱلْمَشْرِقَيْنِ فَبِئْسَ ٱلْقَرِينُ', 'حتى إذا جاءنا قال يا ليت بيني وبينك بعد المشرقين فبئس القرين', 25, 50, 492, 197, '43:38'),
(4364, NULL, NULL, 43, 39, 'وَلَن يَنفَعَكُمُ ٱلْيَوْمَ إِذ ظَّلَمْتُمْ أَنَّكُمْ فِى ٱلْعَذَابِ مُشْتَرِكُونَ', 'ولن ينفعكم اليوم إذ ظلمتم أنكم في العذاب مشتركون', 25, 50, 492, 197, '43:39'),
(4365, NULL, NULL, 43, 40, 'أَفَأَنتَ تُسْمِعُ ٱلصُّمَّ أَوْ تَهْدِى ٱلْعُمْىَ وَمَن كَانَ فِى ضَلَٰلٍ مُّبِينٍ', 'أفأنت تسمع الصم أو تهدي العمي ومن كان في ضلال مبين', 25, 50, 492, 197, '43:40'),
(4366, NULL, NULL, 43, 41, 'فَإِمَّا نَذْهَبَنَّ بِكَ فَإِنَّا مِنْهُم مُّنتَقِمُونَ', 'فإما نذهبن بك فإنا منهم منتقمون', 25, 50, 492, 197, '43:41'),
(4367, NULL, NULL, 43, 42, 'أَوْ نُرِيَنَّكَ ٱلَّذِى وَعَدْنَٰهُمْ فَإِنَّا عَلَيْهِم مُّقْتَدِرُونَ', 'أو نرينك الذي وعدناهم فإنا عليهم مقتدرون', 25, 50, 492, 197, '43:42'),
(4368, NULL, NULL, 43, 43, 'فَٱسْتَمْسِكْ بِٱلَّذِىٓ أُوحِىَ إِلَيْكَ إِنَّكَ عَلَىٰ صِرَٰطٍ مُّسْتَقِيمٍ', 'فاستمسك بالذي أوحي إليك إنك على صراط مستقيم', 25, 50, 492, 197, '43:43'),
(4369, NULL, NULL, 43, 44, 'وَإِنَّهُۥ لَذِكْرٌ لَّكَ وَلِقَوْمِكَ وَسَوْفَ تُسْـَٔلُونَ', 'وإنه لذكر لك ولقومك وسوف تسألون', 25, 50, 492, 197, '43:44'),
(4370, NULL, NULL, 43, 45, 'وَسْـَٔلْ مَنْ أَرْسَلْنَا مِن قَبْلِكَ مِن رُّسُلِنَآ أَجَعَلْنَا مِن دُونِ ٱلرَّحْمَٰنِ ءَالِهَةً يُعْبَدُونَ', 'واسأل من أرسلنا من قبلك من رسلنا أجعلنا من دون الرحمن آلهة يعبدون', 25, 50, 492, 197, '43:45'),
(4371, NULL, NULL, 43, 46, 'وَلَقَدْ أَرْسَلْنَا مُوسَىٰ بِـَٔايَٰتِنَآ إِلَىٰ فِرْعَوْنَ وَمَلَإِي۟هِۦ فَقَالَ إِنِّى رَسُولُ رَبِّ ٱلْعَٰلَمِينَ', 'ولقد أرسلنا موسى بآياتنا إلى فرعون وملئه فقال إني رسول رب العالمين', 25, 50, 492, 197, '43:46'),
(4372, NULL, NULL, 43, 47, 'فَلَمَّا جَآءَهُم بِـَٔايَٰتِنَآ إِذَا هُم مِّنْهَا يَضْحَكُونَ', 'فلما جاءهم بآياتنا إذا هم منها يضحكون', 25, 50, 492, 197, '43:47'),
(4373, NULL, NULL, 43, 48, 'وَمَا نُرِيهِم مِّنْ ءَايَةٍ إِلَّا هِىَ أَكْبَرُ مِنْ أُخْتِهَا وَأَخَذْنَٰهُم بِٱلْعَذَابِ لَعَلَّهُمْ يَرْجِعُونَ', 'وما نريهم من آية إلا هي أكبر من أختها وأخذناهم بالعذاب لعلهم يرجعون', 25, 50, 493, 197, '43:48'),
(4374, NULL, NULL, 43, 49, 'وَقَالُوا۟ يَٰٓأَيُّهَ ٱلسَّاحِرُ ٱدْعُ لَنَا رَبَّكَ بِمَا عَهِدَ عِندَكَ إِنَّنَا لَمُهْتَدُونَ', 'وقالوا يا أيه الساحر ادع لنا ربك بما عهد عندك إننا لمهتدون', 25, 50, 493, 197, '43:49'),
(4375, NULL, NULL, 43, 50, 'فَلَمَّا كَشَفْنَا عَنْهُمُ ٱلْعَذَابَ إِذَا هُمْ يَنكُثُونَ', 'فلما كشفنا عنهم العذاب إذا هم ينكثون', 25, 50, 493, 197, '43:50'),
(4376, NULL, NULL, 43, 51, 'وَنَادَىٰ فِرْعَوْنُ فِى قَوْمِهِۦ قَالَ يَٰقَوْمِ أَلَيْسَ لِى مُلْكُ مِصْرَ وَهَٰذِهِ ٱلْأَنْهَٰرُ تَجْرِى مِن تَحْتِىٓ أَفَلَا تُبْصِرُونَ', 'ونادى فرعون في قومه قال يا قوم أليس لي ملك مصر وهذه الأنهار تجري من تحتي أفلا تبصرون', 25, 50, 493, 197, '43:51'),
(4377, NULL, NULL, 43, 52, 'أَمْ أَنَا۠ خَيْرٌ مِّنْ هَٰذَا ٱلَّذِى هُوَ مَهِينٌ وَلَا يَكَادُ يُبِينُ', 'أم أنا خير من هذا الذي هو مهين ولا يكاد يبين', 25, 50, 493, 197, '43:52'),
(4378, NULL, NULL, 43, 53, 'فَلَوْلَآ أُلْقِىَ عَلَيْهِ أَسْوِرَةٌ مِّن ذَهَبٍ أَوْ جَآءَ مَعَهُ ٱلْمَلَٰٓئِكَةُ مُقْتَرِنِينَ', 'فلولا ألقي عليه أسورة من ذهب أو جاء معه الملائكة مقترنين', 25, 50, 493, 197, '43:53'),
(4379, NULL, NULL, 43, 54, 'فَٱسْتَخَفَّ قَوْمَهُۥ فَأَطَاعُوهُ إِنَّهُمْ كَانُوا۟ قَوْمًا فَٰسِقِينَ', 'فاستخف قومه فأطاعوه إنهم كانوا قوما فاسقين', 25, 50, 493, 197, '43:54'),
(4380, NULL, NULL, 43, 55, 'فَلَمَّآ ءَاسَفُونَا ٱنتَقَمْنَا مِنْهُمْ فَأَغْرَقْنَٰهُمْ أَجْمَعِينَ', 'فلما آسفونا انتقمنا منهم فأغرقناهم أجمعين', 25, 50, 493, 197, '43:55'),
(4381, NULL, NULL, 43, 56, 'فَجَعَلْنَٰهُمْ سَلَفًا وَمَثَلًا لِّلْءَاخِرِينَ', 'فجعلناهم سلفا ومثلا للآخرين', 25, 50, 493, 197, '43:56'),
(4382, NULL, NULL, 43, 57, 'وَلَمَّا ضُرِبَ ٱبْنُ مَرْيَمَ مَثَلًا إِذَا قَوْمُكَ مِنْهُ يَصِدُّونَ', 'ولما ضرب ابن مريم مثلا إذا قومك منه يصدون', 25, 50, 493, 198, '43:57'),
(4383, NULL, NULL, 43, 58, 'وَقَالُوٓا۟ ءَأَٰلِهَتُنَا خَيْرٌ أَمْ هُوَ مَا ضَرَبُوهُ لَكَ إِلَّا جَدَلًۢا بَلْ هُمْ قَوْمٌ خَصِمُونَ', 'وقالوا أآلهتنا خير أم هو ما ضربوه لك إلا جدلا بل هم قوم خصمون', 25, 50, 493, 198, '43:58'),
(4384, NULL, NULL, 43, 59, 'إِنْ هُوَ إِلَّا عَبْدٌ أَنْعَمْنَا عَلَيْهِ وَجَعَلْنَٰهُ مَثَلًا لِّبَنِىٓ إِسْرَٰٓءِيلَ', 'إن هو إلا عبد أنعمنا عليه وجعلناه مثلا لبني إسرائيل', 25, 50, 493, 198, '43:59'),
(4385, NULL, NULL, 43, 60, 'وَلَوْ نَشَآءُ لَجَعَلْنَا مِنكُم مَّلَٰٓئِكَةً فِى ٱلْأَرْضِ يَخْلُفُونَ', 'ولو نشاء لجعلنا منكم ملائكة في الأرض يخلفون', 25, 50, 493, 198, '43:60'),
(4386, NULL, NULL, 43, 61, 'وَإِنَّهُۥ لَعِلْمٌ لِّلسَّاعَةِ فَلَا تَمْتَرُنَّ بِهَا وَٱتَّبِعُونِ هَٰذَا صِرَٰطٌ مُّسْتَقِيمٌ', 'وإنه لعلم للساعة فلا تمترن بها واتبعون هذا صراط مستقيم', 25, 50, 494, 198, '43:61'),
(4387, NULL, NULL, 43, 62, 'وَلَا يَصُدَّنَّكُمُ ٱلشَّيْطَٰنُ إِنَّهُۥ لَكُمْ عَدُوٌّ مُّبِينٌ', 'ولا يصدنكم الشيطان إنه لكم عدو مبين', 25, 50, 494, 198, '43:62'),
(4388, NULL, NULL, 43, 63, 'وَلَمَّا جَآءَ عِيسَىٰ بِٱلْبَيِّنَٰتِ قَالَ قَدْ جِئْتُكُم بِٱلْحِكْمَةِ وَلِأُبَيِّنَ لَكُم بَعْضَ ٱلَّذِى تَخْتَلِفُونَ فِيهِ فَٱتَّقُوا۟ ٱللَّهَ وَأَطِيعُونِ', 'ولما جاء عيسى بالبينات قال قد جئتكم بالحكمة ولأبين لكم بعض الذي تختلفون فيه فاتقوا الله وأطيعون', 25, 50, 494, 198, '43:63'),
(4389, NULL, NULL, 43, 64, 'إِنَّ ٱللَّهَ هُوَ رَبِّى وَرَبُّكُمْ فَٱعْبُدُوهُ هَٰذَا صِرَٰطٌ مُّسْتَقِيمٌ', 'إن الله هو ربي وربكم فاعبدوه هذا صراط مستقيم', 25, 50, 494, 198, '43:64'),
(4390, NULL, NULL, 43, 65, 'فَٱخْتَلَفَ ٱلْأَحْزَابُ مِنۢ بَيْنِهِمْ فَوَيْلٌ لِّلَّذِينَ ظَلَمُوا۟ مِنْ عَذَابِ يَوْمٍ أَلِيمٍ', 'فاختلف الأحزاب من بينهم فويل للذين ظلموا من عذاب يوم أليم', 25, 50, 494, 198, '43:65'),
(4391, NULL, NULL, 43, 66, 'هَلْ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأْتِيَهُم بَغْتَةً وَهُمْ لَا يَشْعُرُونَ', 'هل ينظرون إلا الساعة أن تأتيهم بغتة وهم لا يشعرون', 25, 50, 494, 198, '43:66'),
(4392, NULL, NULL, 43, 67, 'ٱلْأَخِلَّآءُ يَوْمَئِذٍۭ بَعْضُهُمْ لِبَعْضٍ عَدُوٌّ إِلَّا ٱلْمُتَّقِينَ', 'الأخلاء يومئذ بعضهم لبعض عدو إلا المتقين', 25, 50, 494, 198, '43:67'),
(4393, NULL, NULL, 43, 68, 'يَٰعِبَادِ لَا خَوْفٌ عَلَيْكُمُ ٱلْيَوْمَ وَلَآ أَنتُمْ تَحْزَنُونَ', 'يا عباد لا خوف عليكم اليوم ولا أنتم تحزنون', 25, 50, 494, 198, '43:68'),
(4394, NULL, NULL, 43, 69, 'ٱلَّذِينَ ءَامَنُوا۟ بِـَٔايَٰتِنَا وَكَانُوا۟ مُسْلِمِينَ', 'الذين آمنوا بآياتنا وكانوا مسلمين', 25, 50, 494, 198, '43:69'),
(4395, NULL, NULL, 43, 70, 'ٱدْخُلُوا۟ ٱلْجَنَّةَ أَنتُمْ وَأَزْوَٰجُكُمْ تُحْبَرُونَ', 'ادخلوا الجنة أنتم وأزواجكم تحبرون', 25, 50, 494, 198, '43:70'),
(4396, NULL, NULL, 43, 71, 'يُطَافُ عَلَيْهِم بِصِحَافٍ مِّن ذَهَبٍ وَأَكْوَابٍ وَفِيهَا مَا تَشْتَهِيهِ ٱلْأَنفُسُ وَتَلَذُّ ٱلْأَعْيُنُ وَأَنتُمْ فِيهَا خَٰلِدُونَ', 'يطاف عليهم بصحاف من ذهب وأكواب وفيها ما تشتهيه الأنفس وتلذ الأعين وأنتم فيها خالدون', 25, 50, 494, 198, '43:71'),
(4397, NULL, NULL, 43, 72, 'وَتِلْكَ ٱلْجَنَّةُ ٱلَّتِىٓ أُورِثْتُمُوهَا بِمَا كُنتُمْ تَعْمَلُونَ', 'وتلك الجنة التي أورثتموها بما كنتم تعملون', 25, 50, 494, 198, '43:72'),
(4398, NULL, NULL, 43, 73, 'لَكُمْ فِيهَا فَٰكِهَةٌ كَثِيرَةٌ مِّنْهَا تَأْكُلُونَ', 'لكم فيها فاكهة كثيرة منها تأكلون', 25, 50, 494, 198, '43:73'),
(4399, NULL, NULL, 43, 74, 'إِنَّ ٱلْمُجْرِمِينَ فِى عَذَابِ جَهَنَّمَ خَٰلِدُونَ', 'إن المجرمين في عذاب جهنم خالدون', 25, 50, 495, 198, '43:74'),
(4400, NULL, NULL, 43, 75, 'لَا يُفَتَّرُ عَنْهُمْ وَهُمْ فِيهِ مُبْلِسُونَ', 'لا يفتر عنهم وهم فيه مبلسون', 25, 50, 495, 198, '43:75'),
(4401, NULL, NULL, 43, 76, 'وَمَا ظَلَمْنَٰهُمْ وَلَٰكِن كَانُوا۟ هُمُ ٱلظَّٰلِمِينَ', 'وما ظلمناهم ولكن كانوا هم الظالمين', 25, 50, 495, 198, '43:76'),
(4402, NULL, NULL, 43, 77, 'وَنَادَوْا۟ يَٰمَٰلِكُ لِيَقْضِ عَلَيْنَا رَبُّكَ قَالَ إِنَّكُم مَّٰكِثُونَ', 'ونادوا يا مالك ليقض علينا ربك قال إنكم ماكثون', 25, 50, 495, 198, '43:77'),
(4403, NULL, NULL, 43, 78, 'لَقَدْ جِئْنَٰكُم بِٱلْحَقِّ وَلَٰكِنَّ أَكْثَرَكُمْ لِلْحَقِّ كَٰرِهُونَ', 'لقد جئناكم بالحق ولكن أكثركم للحق كارهون', 25, 50, 495, 198, '43:78'),
(4404, NULL, NULL, 43, 79, 'أَمْ أَبْرَمُوٓا۟ أَمْرًا فَإِنَّا مُبْرِمُونَ', 'أم أبرموا أمرا فإنا مبرمون', 25, 50, 495, 198, '43:79'),
(4405, NULL, NULL, 43, 80, 'أَمْ يَحْسَبُونَ أَنَّا لَا نَسْمَعُ سِرَّهُمْ وَنَجْوَىٰهُم بَلَىٰ وَرُسُلُنَا لَدَيْهِمْ يَكْتُبُونَ', 'أم يحسبون أنا لا نسمع سرهم ونجواهم بلى ورسلنا لديهم يكتبون', 25, 50, 495, 198, '43:80'),
(4406, NULL, NULL, 43, 81, 'قُلْ إِن كَانَ لِلرَّحْمَٰنِ وَلَدٌ فَأَنَا۠ أَوَّلُ ٱلْعَٰبِدِينَ', 'قل إن كان للرحمن ولد فأنا أول العابدين', 25, 50, 495, 198, '43:81'),
(4407, NULL, NULL, 43, 82, 'سُبْحَٰنَ رَبِّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ رَبِّ ٱلْعَرْشِ عَمَّا يَصِفُونَ', 'سبحان رب السماوات والأرض رب العرش عما يصفون', 25, 50, 495, 198, '43:82'),
(4408, NULL, NULL, 43, 83, 'فَذَرْهُمْ يَخُوضُوا۟ وَيَلْعَبُوا۟ حَتَّىٰ يُلَٰقُوا۟ يَوْمَهُمُ ٱلَّذِى يُوعَدُونَ', 'فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون', 25, 50, 495, 198, '43:83'),
(4409, NULL, NULL, 43, 84, 'وَهُوَ ٱلَّذِى فِى ٱلسَّمَآءِ إِلَٰهٌ وَفِى ٱلْأَرْضِ إِلَٰهٌ وَهُوَ ٱلْحَكِيمُ ٱلْعَلِيمُ', 'وهو الذي في السماء إله وفي الأرض إله وهو الحكيم العليم', 25, 50, 495, 198, '43:84'),
(4410, NULL, NULL, 43, 85, 'وَتَبَارَكَ ٱلَّذِى لَهُۥ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَا وَعِندَهُۥ عِلْمُ ٱلسَّاعَةِ وَإِلَيْهِ تُرْجَعُونَ', 'وتبارك الذي له ملك السماوات والأرض وما بينهما وعنده علم الساعة وإليه ترجعون', 25, 50, 495, 198, '43:85'),
(4411, NULL, NULL, 43, 86, 'وَلَا يَمْلِكُ ٱلَّذِينَ يَدْعُونَ مِن دُونِهِ ٱلشَّفَٰعَةَ إِلَّا مَن شَهِدَ بِٱلْحَقِّ وَهُمْ يَعْلَمُونَ', 'ولا يملك الذين يدعون من دونه الشفاعة إلا من شهد بالحق وهم يعلمون', 25, 50, 495, 198, '43:86'),
(4412, NULL, NULL, 43, 87, 'وَلَئِن سَأَلْتَهُم مَّنْ خَلَقَهُمْ لَيَقُولُنَّ ٱللَّهُ فَأَنَّىٰ يُؤْفَكُونَ', 'ولئن سألتهم من خلقهم ليقولن الله فأنى يؤفكون', 25, 50, 495, 198, '43:87'),
(4413, NULL, NULL, 43, 88, 'وَقِيلِهِۦ يَٰرَبِّ إِنَّ هَٰٓؤُلَآءِ قَوْمٌ لَّا يُؤْمِنُونَ', 'وقيله يا رب إن هؤلاء قوم لا يؤمنون', 25, 50, 495, 198, '43:88'),
(4414, NULL, NULL, 43, 89, 'فَٱصْفَحْ عَنْهُمْ وَقُلْ سَلَٰمٌ فَسَوْفَ يَعْلَمُونَ', 'فاصفح عنهم وقل سلام فسوف يعلمون', 25, 50, 495, 198, '43:89'),
(4415, NULL, NULL, 44, 1, 'حمٓ', 'حم', 25, 50, 496, 198, '44:1'),
(4416, NULL, NULL, 44, 2, 'وَٱلْكِتَٰبِ ٱلْمُبِينِ', 'والكتاب المبين', 25, 50, 496, 198, '44:2'),
(4417, NULL, NULL, 44, 3, 'إِنَّآ أَنزَلْنَٰهُ فِى لَيْلَةٍ مُّبَٰرَكَةٍ إِنَّا كُنَّا مُنذِرِينَ', 'إنا أنزلناه في ليلة مباركة إنا كنا منذرين', 25, 50, 496, 198, '44:3'),
(4418, NULL, NULL, 44, 4, 'فِيهَا يُفْرَقُ كُلُّ أَمْرٍ حَكِيمٍ', 'فيها يفرق كل أمر حكيم', 25, 50, 496, 198, '44:4'),
(4419, NULL, NULL, 44, 5, 'أَمْرًا مِّنْ عِندِنَآ إِنَّا كُنَّا مُرْسِلِينَ', 'أمرا من عندنا إنا كنا مرسلين', 25, 50, 496, 198, '44:5'),
(4420, NULL, NULL, 44, 6, 'رَحْمَةً مِّن رَّبِّكَ إِنَّهُۥ هُوَ ٱلسَّمِيعُ ٱلْعَلِيمُ', 'رحمة من ربك إنه هو السميع العليم', 25, 50, 496, 198, '44:6'),
(4421, NULL, NULL, 44, 7, 'رَبِّ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَمَا بَيْنَهُمَآ إِن كُنتُم مُّوقِنِينَ', 'رب السماوات والأرض وما بينهما إن كنتم موقنين', 25, 50, 496, 198, '44:7'),
(4422, NULL, NULL, 44, 8, 'لَآ إِلَٰهَ إِلَّا هُوَ يُحْىِۦ وَيُمِيتُ رَبُّكُمْ وَرَبُّ ءَابَآئِكُمُ ٱلْأَوَّلِينَ', 'لا إله إلا هو يحيي ويميت ربكم ورب آبائكم الأولين', 25, 50, 496, 198, '44:8'),
(4423, NULL, NULL, 44, 9, 'بَلْ هُمْ فِى شَكٍّ يَلْعَبُونَ', 'بل هم في شك يلعبون', 25, 50, 496, 198, '44:9'),
(4424, NULL, NULL, 44, 10, 'فَٱرْتَقِبْ يَوْمَ تَأْتِى ٱلسَّمَآءُ بِدُخَانٍ مُّبِينٍ', 'فارتقب يوم تأتي السماء بدخان مبين', 25, 50, 496, 198, '44:10'),
(4425, NULL, NULL, 44, 11, 'يَغْشَى ٱلنَّاسَ هَٰذَا عَذَابٌ أَلِيمٌ', 'يغشى الناس هذا عذاب أليم', 25, 50, 496, 198, '44:11'),
(4426, NULL, NULL, 44, 12, 'رَّبَّنَا ٱكْشِفْ عَنَّا ٱلْعَذَابَ إِنَّا مُؤْمِنُونَ', 'ربنا اكشف عنا العذاب إنا مؤمنون', 25, 50, 496, 198, '44:12'),
(4427, NULL, NULL, 44, 13, 'أَنَّىٰ لَهُمُ ٱلذِّكْرَىٰ وَقَدْ جَآءَهُمْ رَسُولٌ مُّبِينٌ', 'أنى لهم الذكرى وقد جاءهم رسول مبين', 25, 50, 496, 198, '44:13'),
(4428, NULL, NULL, 44, 14, 'ثُمَّ تَوَلَّوْا۟ عَنْهُ وَقَالُوا۟ مُعَلَّمٌ مَّجْنُونٌ', 'ثم تولوا عنه وقالوا معلم مجنون', 25, 50, 496, 198, '44:14'),
(4429, NULL, NULL, 44, 15, 'إِنَّا كَاشِفُوا۟ ٱلْعَذَابِ قَلِيلًا إِنَّكُمْ عَآئِدُونَ', 'إنا كاشفو العذاب قليلا إنكم عائدون', 25, 50, 496, 198, '44:15'),
(4430, NULL, NULL, 44, 16, 'يَوْمَ نَبْطِشُ ٱلْبَطْشَةَ ٱلْكُبْرَىٰٓ إِنَّا مُنتَقِمُونَ', 'يوم نبطش البطشة الكبرى إنا منتقمون', 25, 50, 496, 198, '44:16'),
(4431, NULL, NULL, 44, 17, 'وَلَقَدْ فَتَنَّا قَبْلَهُمْ قَوْمَ فِرْعَوْنَ وَجَآءَهُمْ رَسُولٌ كَرِيمٌ', 'ولقد فتنا قبلهم قوم فرعون وجاءهم رسول كريم', 25, 50, 496, 199, '44:17'),
(4432, NULL, NULL, 44, 18, 'أَنْ أَدُّوٓا۟ إِلَىَّ عِبَادَ ٱللَّهِ إِنِّى لَكُمْ رَسُولٌ أَمِينٌ', 'أن أدوا إلي عباد الله إني لكم رسول أمين', 25, 50, 496, 199, '44:18'),
(4433, NULL, NULL, 44, 19, 'وَأَن لَّا تَعْلُوا۟ عَلَى ٱللَّهِ إِنِّىٓ ءَاتِيكُم بِسُلْطَٰنٍ مُّبِينٍ', 'وأن لا تعلوا على الله إني آتيكم بسلطان مبين', 25, 50, 497, 199, '44:19'),
(4434, NULL, NULL, 44, 20, 'وَإِنِّى عُذْتُ بِرَبِّى وَرَبِّكُمْ أَن تَرْجُمُونِ', 'وإني عذت بربي وربكم أن ترجمون', 25, 50, 497, 199, '44:20'),
(4435, NULL, NULL, 44, 21, 'وَإِن لَّمْ تُؤْمِنُوا۟ لِى فَٱعْتَزِلُونِ', 'وإن لم تؤمنوا لي فاعتزلون', 25, 50, 497, 199, '44:21'),
(4436, NULL, NULL, 44, 22, 'فَدَعَا رَبَّهُۥٓ أَنَّ هَٰٓؤُلَآءِ قَوْمٌ مُّجْرِمُونَ', 'فدعا ربه أن هؤلاء قوم مجرمون', 25, 50, 497, 199, '44:22'),
(4437, NULL, NULL, 44, 23, 'فَأَسْرِ بِعِبَادِى لَيْلًا إِنَّكُم مُّتَّبَعُونَ', 'فأسر بعبادي ليلا إنكم متبعون', 25, 50, 497, 199, '44:23'),
(4438, NULL, NULL, 44, 24, 'وَٱتْرُكِ ٱلْبَحْرَ رَهْوًا إِنَّهُمْ جُندٌ مُّغْرَقُونَ', 'واترك البحر رهوا إنهم جند مغرقون', 25, 50, 497, 199, '44:24'),
(4439, NULL, NULL, 44, 25, 'كَمْ تَرَكُوا۟ مِن جَنَّٰتٍ وَعُيُونٍ', 'كم تركوا من جنات وعيون', 25, 50, 497, 199, '44:25'),
(4440, NULL, NULL, 44, 26, 'وَزُرُوعٍ وَمَقَامٍ كَرِيمٍ', 'وزروع ومقام كريم', 25, 50, 497, 199, '44:26'),
(4441, NULL, NULL, 44, 27, 'وَنَعْمَةٍ كَانُوا۟ فِيهَا فَٰكِهِينَ', 'ونعمة كانوا فيها فاكهين', 25, 50, 497, 199, '44:27'),
(4442, NULL, NULL, 44, 28, 'كَذَٰلِكَ وَأَوْرَثْنَٰهَا قَوْمًا ءَاخَرِينَ', 'كذلك وأورثناها قوما آخرين', 25, 50, 497, 199, '44:28'),
(4443, NULL, NULL, 44, 29, 'فَمَا بَكَتْ عَلَيْهِمُ ٱلسَّمَآءُ وَٱلْأَرْضُ وَمَا كَانُوا۟ مُنظَرِينَ', 'فما بكت عليهم السماء والأرض وما كانوا منظرين', 25, 50, 497, 199, '44:29'),
(4444, NULL, NULL, 44, 30, 'وَلَقَدْ نَجَّيْنَا بَنِىٓ إِسْرَٰٓءِيلَ مِنَ ٱلْعَذَابِ ٱلْمُهِينِ', 'ولقد نجينا بني إسرائيل من العذاب المهين', 25, 50, 497, 199, '44:30'),
(4445, NULL, NULL, 44, 31, 'مِن فِرْعَوْنَ إِنَّهُۥ كَانَ عَالِيًا مِّنَ ٱلْمُسْرِفِينَ', 'من فرعون إنه كان عاليا من المسرفين', 25, 50, 497, 199, '44:31'),
(4446, NULL, NULL, 44, 32, 'وَلَقَدِ ٱخْتَرْنَٰهُمْ عَلَىٰ عِلْمٍ عَلَى ٱلْعَٰلَمِينَ', 'ولقد اخترناهم على علم على العالمين', 25, 50, 497, 199, '44:32'),
(4447, NULL, NULL, 44, 33, 'وَءَاتَيْنَٰهُم مِّنَ ٱلْءَايَٰتِ مَا فِيهِ بَلَٰٓؤٌا۟ مُّبِينٌ', 'وآتيناهم من الآيات ما فيه بلاء مبين', 25, 50, 497, 199, '44:33'),
(4448, NULL, NULL, 44, 34, 'إِنَّ هَٰٓؤُلَآءِ لَيَقُولُونَ', 'إن هؤلاء ليقولون', 25, 50, 497, 199, '44:34'),
(4449, NULL, NULL, 44, 35, 'إِنْ هِىَ إِلَّا مَوْتَتُنَا ٱلْأُولَىٰ وَمَا نَحْنُ بِمُنشَرِينَ', 'إن هي إلا موتتنا الأولى وما نحن بمنشرين', 25, 50, 497, 199, '44:35'),
(4450, NULL, NULL, 44, 36, 'فَأْتُوا۟ بِـَٔابَآئِنَآ إِن كُنتُمْ صَٰدِقِينَ', 'فأتوا بآبائنا إن كنتم صادقين', 25, 50, 497, 199, '44:36'),
(4451, NULL, NULL, 44, 37, 'أَهُمْ خَيْرٌ أَمْ قَوْمُ تُبَّعٍ وَٱلَّذِينَ مِن قَبْلِهِمْ أَهْلَكْنَٰهُمْ إِنَّهُمْ كَانُوا۟ مُجْرِمِينَ', 'أهم خير أم قوم تبع والذين من قبلهم أهلكناهم إنهم كانوا مجرمين', 25, 50, 497, 199, '44:37'),
(4452, NULL, NULL, 44, 38, 'وَمَا خَلَقْنَا ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا لَٰعِبِينَ', 'وما خلقنا السماوات والأرض وما بينهما لاعبين', 25, 50, 497, 199, '44:38'),
(4453, NULL, NULL, 44, 39, 'مَا خَلَقْنَٰهُمَآ إِلَّا بِٱلْحَقِّ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 'ما خلقناهما إلا بالحق ولكن أكثرهم لا يعلمون', 25, 50, 497, 199, '44:39'),
(4454, NULL, NULL, 44, 40, 'إِنَّ يَوْمَ ٱلْفَصْلِ مِيقَٰتُهُمْ أَجْمَعِينَ', 'إن يوم الفصل ميقاتهم أجمعين', 25, 50, 498, 199, '44:40'),
(4455, NULL, NULL, 44, 41, 'يَوْمَ لَا يُغْنِى مَوْلًى عَن مَّوْلًى شَيْـًٔا وَلَا هُمْ يُنصَرُونَ', 'يوم لا يغني مولى عن مولى شيئا ولا هم ينصرون', 25, 50, 498, 199, '44:41'),
(4456, NULL, NULL, 44, 42, 'إِلَّا مَن رَّحِمَ ٱللَّهُ إِنَّهُۥ هُوَ ٱلْعَزِيزُ ٱلرَّحِيمُ', 'إلا من رحم الله إنه هو العزيز الرحيم', 25, 50, 498, 199, '44:42'),
(4457, NULL, NULL, 44, 43, 'إِنَّ شَجَرَتَ ٱلزَّقُّومِ', 'إن شجرت الزقوم', 25, 50, 498, 199, '44:43'),
(4458, NULL, NULL, 44, 44, 'طَعَامُ ٱلْأَثِيمِ', 'طعام الأثيم', 25, 50, 498, 199, '44:44'),
(4459, NULL, NULL, 44, 45, 'كَٱلْمُهْلِ يَغْلِى فِى ٱلْبُطُونِ', 'كالمهل يغلي في البطون', 25, 50, 498, 199, '44:45'),
(4460, NULL, NULL, 44, 46, 'كَغَلْىِ ٱلْحَمِيمِ', 'كغلي الحميم', 25, 50, 498, 199, '44:46'),
(4461, NULL, NULL, 44, 47, 'خُذُوهُ فَٱعْتِلُوهُ إِلَىٰ سَوَآءِ ٱلْجَحِيمِ', 'خذوه فاعتلوه إلى سواء الجحيم', 25, 50, 498, 199, '44:47'),
(4462, NULL, NULL, 44, 48, 'ثُمَّ صُبُّوا۟ فَوْقَ رَأْسِهِۦ مِنْ عَذَابِ ٱلْحَمِيمِ', 'ثم صبوا فوق رأسه من عذاب الحميم', 25, 50, 498, 199, '44:48'),
(4463, NULL, NULL, 44, 49, 'ذُقْ إِنَّكَ أَنتَ ٱلْعَزِيزُ ٱلْكَرِيمُ', 'ذق إنك أنت العزيز الكريم', 25, 50, 498, 199, '44:49'),
(4464, NULL, NULL, 44, 50, 'إِنَّ هَٰذَا مَا كُنتُم بِهِۦ تَمْتَرُونَ', 'إن هذا ما كنتم به تمترون', 25, 50, 498, 199, '44:50'),
(4465, NULL, NULL, 44, 51, 'إِنَّ ٱلْمُتَّقِينَ فِى مَقَامٍ أَمِينٍ', 'إن المتقين في مقام أمين', 25, 50, 498, 199, '44:51'),
(4466, NULL, NULL, 44, 52, 'فِى جَنَّٰتٍ وَعُيُونٍ', 'في جنات وعيون', 25, 50, 498, 199, '44:52'),
(4467, NULL, NULL, 44, 53, 'يَلْبَسُونَ مِن سُندُسٍ وَإِسْتَبْرَقٍ مُّتَقَٰبِلِينَ', 'يلبسون من سندس وإستبرق متقابلين', 25, 50, 498, 199, '44:53'),
(4468, NULL, NULL, 44, 54, 'كَذَٰلِكَ وَزَوَّجْنَٰهُم بِحُورٍ عِينٍ', 'كذلك وزوجناهم بحور عين', 25, 50, 498, 199, '44:54'),
(4469, NULL, NULL, 44, 55, 'يَدْعُونَ فِيهَا بِكُلِّ فَٰكِهَةٍ ءَامِنِينَ', 'يدعون فيها بكل فاكهة آمنين', 25, 50, 498, 199, '44:55'),
(4470, NULL, NULL, 44, 56, 'لَا يَذُوقُونَ فِيهَا ٱلْمَوْتَ إِلَّا ٱلْمَوْتَةَ ٱلْأُولَىٰ وَوَقَىٰهُمْ عَذَابَ ٱلْجَحِيمِ', 'لا يذوقون فيها الموت إلا الموتة الأولى ووقاهم عذاب الجحيم', 25, 50, 498, 199, '44:56'),
(4471, NULL, NULL, 44, 57, 'فَضْلًا مِّن رَّبِّكَ ذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْعَظِيمُ', 'فضلا من ربك ذلك هو الفوز العظيم', 25, 50, 498, 199, '44:57'),
(4472, NULL, NULL, 44, 58, 'فَإِنَّمَا يَسَّرْنَٰهُ بِلِسَانِكَ لَعَلَّهُمْ يَتَذَكَّرُونَ', 'فإنما يسرناه بلسانك لعلهم يتذكرون', 25, 50, 498, 199, '44:58'),
(4473, NULL, NULL, 44, 59, 'فَٱرْتَقِبْ إِنَّهُم مُّرْتَقِبُونَ', 'فارتقب إنهم مرتقبون', 25, 50, 498, 199, '44:59'),
(4474, NULL, NULL, 45, 1, 'حمٓ', 'حم', 25, 50, 499, 199, '45:1'),
(4475, NULL, NULL, 45, 2, 'تَنزِيلُ ٱلْكِتَٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْحَكِيمِ', 'تنزيل الكتاب من الله العزيز الحكيم', 25, 50, 499, 199, '45:2'),
(4476, NULL, NULL, 45, 3, 'إِنَّ فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ لَءَايَٰتٍ لِّلْمُؤْمِنِينَ', 'إن في السماوات والأرض لآيات للمؤمنين', 25, 50, 499, 199, '45:3'),
(4477, NULL, NULL, 45, 4, 'وَفِى خَلْقِكُمْ وَمَا يَبُثُّ مِن دَآبَّةٍ ءَايَٰتٌ لِّقَوْمٍ يُوقِنُونَ', 'وفي خلقكم وما يبث من دابة آيات لقوم يوقنون', 25, 50, 499, 199, '45:4'),
(4478, NULL, NULL, 45, 5, 'وَٱخْتِلَٰفِ ٱلَّيْلِ وَٱلنَّهَارِ وَمَآ أَنزَلَ ٱللَّهُ مِنَ ٱلسَّمَآءِ مِن رِّزْقٍ فَأَحْيَا بِهِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا وَتَصْرِيفِ ٱلرِّيَٰحِ ءَايَٰتٌ لِّقَوْمٍ يَعْقِلُونَ', 'واختلاف الليل والنهار وما أنزل الله من السماء من رزق فأحيا به الأرض بعد موتها وتصريف الرياح آيات لقوم يعقلون', 25, 50, 499, 199, '45:5'),
(4479, NULL, NULL, 45, 6, 'تِلْكَ ءَايَٰتُ ٱللَّهِ نَتْلُوهَا عَلَيْكَ بِٱلْحَقِّ فَبِأَىِّ حَدِيثٍۭ بَعْدَ ٱللَّهِ وَءَايَٰتِهِۦ يُؤْمِنُونَ', 'تلك آيات الله نتلوها عليك بالحق فبأي حديث بعد الله وآياته يؤمنون', 25, 50, 499, 199, '45:6'),
(4480, NULL, NULL, 45, 7, 'وَيْلٌ لِّكُلِّ أَفَّاكٍ أَثِيمٍ', 'ويل لكل أفاك أثيم', 25, 50, 499, 199, '45:7'),
(4481, NULL, NULL, 45, 8, 'يَسْمَعُ ءَايَٰتِ ٱللَّهِ تُتْلَىٰ عَلَيْهِ ثُمَّ يُصِرُّ مُسْتَكْبِرًا كَأَن لَّمْ يَسْمَعْهَا فَبَشِّرْهُ بِعَذَابٍ أَلِيمٍ', 'يسمع آيات الله تتلى عليه ثم يصر مستكبرا كأن لم يسمعها فبشره بعذاب أليم', 25, 50, 499, 199, '45:8'),
(4482, NULL, NULL, 45, 9, 'وَإِذَا عَلِمَ مِنْ ءَايَٰتِنَا شَيْـًٔا ٱتَّخَذَهَا هُزُوًا أُو۟لَٰٓئِكَ لَهُمْ عَذَابٌ مُّهِينٌ', 'وإذا علم من آياتنا شيئا اتخذها هزوا أولئك لهم عذاب مهين', 25, 50, 499, 199, '45:9'),
(4483, NULL, NULL, 45, 10, 'مِّن وَرَآئِهِمْ جَهَنَّمُ وَلَا يُغْنِى عَنْهُم مَّا كَسَبُوا۟ شَيْـًٔا وَلَا مَا ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ أَوْلِيَآءَ وَلَهُمْ عَذَابٌ عَظِيمٌ', 'من ورائهم جهنم ولا يغني عنهم ما كسبوا شيئا ولا ما اتخذوا من دون الله أولياء ولهم عذاب عظيم', 25, 50, 499, 199, '45:10'),
(4484, NULL, NULL, 45, 11, 'هَٰذَا هُدًى وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَٰتِ رَبِّهِمْ لَهُمْ عَذَابٌ مِّن رِّجْزٍ أَلِيمٌ', 'هذا هدى والذين كفروا بآيات ربهم لهم عذاب من رجز أليم', 25, 50, 499, 199, '45:11'),
(4485, NULL, NULL, 45, 12, 'ٱللَّهُ ٱلَّذِى سَخَّرَ لَكُمُ ٱلْبَحْرَ لِتَجْرِىَ ٱلْفُلْكُ فِيهِ بِأَمْرِهِۦ وَلِتَبْتَغُوا۟ مِن فَضْلِهِۦ وَلَعَلَّكُمْ تَشْكُرُونَ', 'الله الذي سخر لكم البحر لتجري الفلك فيه بأمره ولتبتغوا من فضله ولعلكم تشكرون', 25, 50, 499, 200, '45:12'),
(4486, NULL, NULL, 45, 13, 'وَسَخَّرَ لَكُم مَّا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ جَمِيعًا مِّنْهُ إِنَّ فِى ذَٰلِكَ لَءَايَٰتٍ لِّقَوْمٍ يَتَفَكَّرُونَ', 'وسخر لكم ما في السماوات وما في الأرض جميعا منه إن في ذلك لآيات لقوم يتفكرون', 25, 50, 499, 200, '45:13'),
(4487, NULL, NULL, 45, 14, 'قُل لِّلَّذِينَ ءَامَنُوا۟ يَغْفِرُوا۟ لِلَّذِينَ لَا يَرْجُونَ أَيَّامَ ٱللَّهِ لِيَجْزِىَ قَوْمًۢا بِمَا كَانُوا۟ يَكْسِبُونَ', 'قل للذين آمنوا يغفروا للذين لا يرجون أيام الله ليجزي قوما بما كانوا يكسبون', 25, 50, 500, 200, '45:14'),
(4488, NULL, NULL, 45, 15, 'مَنْ عَمِلَ صَٰلِحًا فَلِنَفْسِهِۦ وَمَنْ أَسَآءَ فَعَلَيْهَا ثُمَّ إِلَىٰ رَبِّكُمْ تُرْجَعُونَ', 'من عمل صالحا فلنفسه ومن أساء فعليها ثم إلى ربكم ترجعون', 25, 50, 500, 200, '45:15'),
(4489, NULL, NULL, 45, 16, 'وَلَقَدْ ءَاتَيْنَا بَنِىٓ إِسْرَٰٓءِيلَ ٱلْكِتَٰبَ وَٱلْحُكْمَ وَٱلنُّبُوَّةَ وَرَزَقْنَٰهُم مِّنَ ٱلطَّيِّبَٰتِ وَفَضَّلْنَٰهُمْ عَلَى ٱلْعَٰلَمِينَ', 'ولقد آتينا بني إسرائيل الكتاب والحكم والنبوة ورزقناهم من الطيبات وفضلناهم على العالمين', 25, 50, 500, 200, '45:16'),
(4490, NULL, NULL, 45, 17, 'وَءَاتَيْنَٰهُم بَيِّنَٰتٍ مِّنَ ٱلْأَمْرِ فَمَا ٱخْتَلَفُوٓا۟ إِلَّا مِنۢ بَعْدِ مَا جَآءَهُمُ ٱلْعِلْمُ بَغْيًۢا بَيْنَهُمْ إِنَّ رَبَّكَ يَقْضِى بَيْنَهُمْ يَوْمَ ٱلْقِيَٰمَةِ فِيمَا كَانُوا۟ فِيهِ يَخْتَلِفُونَ', 'وآتيناهم بينات من الأمر فما اختلفوا إلا من بعد ما جاءهم العلم بغيا بينهم إن ربك يقضي بينهم يوم القيامة فيما كانوا فيه يختلفون', 25, 50, 500, 200, '45:17'),
(4491, NULL, NULL, 45, 18, 'ثُمَّ جَعَلْنَٰكَ عَلَىٰ شَرِيعَةٍ مِّنَ ٱلْأَمْرِ فَٱتَّبِعْهَا وَلَا تَتَّبِعْ أَهْوَآءَ ٱلَّذِينَ لَا يَعْلَمُونَ', 'ثم جعلناك على شريعة من الأمر فاتبعها ولا تتبع أهواء الذين لا يعلمون', 25, 50, 500, 200, '45:18'),
(4492, NULL, NULL, 45, 19, 'إِنَّهُمْ لَن يُغْنُوا۟ عَنكَ مِنَ ٱللَّهِ شَيْـًٔا وَإِنَّ ٱلظَّٰلِمِينَ بَعْضُهُمْ أَوْلِيَآءُ بَعْضٍ وَٱللَّهُ وَلِىُّ ٱلْمُتَّقِينَ', 'إنهم لن يغنوا عنك من الله شيئا وإن الظالمين بعضهم أولياء بعض والله ولي المتقين', 25, 50, 500, 200, '45:19'),
(4493, NULL, NULL, 45, 20, 'هَٰذَا بَصَٰٓئِرُ لِلنَّاسِ وَهُدًى وَرَحْمَةٌ لِّقَوْمٍ يُوقِنُونَ', 'هذا بصائر للناس وهدى ورحمة لقوم يوقنون', 25, 50, 500, 200, '45:20'),
(4494, NULL, NULL, 45, 21, 'أَمْ حَسِبَ ٱلَّذِينَ ٱجْتَرَحُوا۟ ٱلسَّيِّـَٔاتِ أَن نَّجْعَلَهُمْ كَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ سَوَآءً مَّحْيَاهُمْ وَمَمَاتُهُمْ سَآءَ مَا يَحْكُمُونَ', 'أم حسب الذين اجترحوا السيئات أن نجعلهم كالذين آمنوا وعملوا الصالحات سواء محياهم ومماتهم ساء ما يحكمون', 25, 50, 500, 200, '45:21'),
(4495, NULL, NULL, 45, 22, 'وَخَلَقَ ٱللَّهُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ وَلِتُجْزَىٰ كُلُّ نَفْسٍۭ بِمَا كَسَبَتْ وَهُمْ لَا يُظْلَمُونَ', 'وخلق الله السماوات والأرض بالحق ولتجزى كل نفس بما كسبت وهم لا يظلمون', 25, 50, 500, 200, '45:22'),
(4496, NULL, NULL, 45, 23, 'أَفَرَءَيْتَ مَنِ ٱتَّخَذَ إِلَٰهَهُۥ هَوَىٰهُ وَأَضَلَّهُ ٱللَّهُ عَلَىٰ عِلْمٍ وَخَتَمَ عَلَىٰ سَمْعِهِۦ وَقَلْبِهِۦ وَجَعَلَ عَلَىٰ بَصَرِهِۦ غِشَٰوَةً فَمَن يَهْدِيهِ مِنۢ بَعْدِ ٱللَّهِ أَفَلَا تَذَكَّرُونَ', 'أفرأيت من اتخذ إلهه هواه وأضله الله على علم وختم على سمعه وقلبه وجعل على بصره غشاوة فمن يهديه من بعد الله أفلا تذكرون', 25, 50, 501, 200, '45:23'),
(4497, NULL, NULL, 45, 24, 'وَقَالُوا۟ مَا هِىَ إِلَّا حَيَاتُنَا ٱلدُّنْيَا نَمُوتُ وَنَحْيَا وَمَا يُهْلِكُنَآ إِلَّا ٱلدَّهْرُ وَمَا لَهُم بِذَٰلِكَ مِنْ عِلْمٍ إِنْ هُمْ إِلَّا يَظُنُّونَ', 'وقالوا ما هي إلا حياتنا الدنيا نموت ونحيا وما يهلكنا إلا الدهر وما لهم بذلك من علم إن هم إلا يظنون', 25, 50, 501, 200, '45:24'),
(4498, NULL, NULL, 45, 25, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَٰتُنَا بَيِّنَٰتٍ مَّا كَانَ حُجَّتَهُمْ إِلَّآ أَن قَالُوا۟ ٱئْتُوا۟ بِـَٔابَآئِنَآ إِن كُنتُمْ صَٰدِقِينَ', 'وإذا تتلى عليهم آياتنا بينات ما كان حجتهم إلا أن قالوا ائتوا بآبائنا إن كنتم صادقين', 25, 50, 501, 200, '45:25'),
(4499, NULL, NULL, 45, 26, 'قُلِ ٱللَّهُ يُحْيِيكُمْ ثُمَّ يُمِيتُكُمْ ثُمَّ يَجْمَعُكُمْ إِلَىٰ يَوْمِ ٱلْقِيَٰمَةِ لَا رَيْبَ فِيهِ وَلَٰكِنَّ أَكْثَرَ ٱلنَّاسِ لَا يَعْلَمُونَ', 'قل الله يحييكم ثم يميتكم ثم يجمعكم إلى يوم القيامة لا ريب فيه ولكن أكثر الناس لا يعلمون', 25, 50, 501, 200, '45:26'),
(4500, NULL, NULL, 45, 27, 'وَلِلَّهِ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَيَوْمَ تَقُومُ ٱلسَّاعَةُ يَوْمَئِذٍ يَخْسَرُ ٱلْمُبْطِلُونَ', 'ولله ملك السماوات والأرض ويوم تقوم الساعة يومئذ يخسر المبطلون', 25, 50, 501, 200, '45:27'),
(4501, NULL, NULL, 45, 28, 'وَتَرَىٰ كُلَّ أُمَّةٍ جَاثِيَةً كُلُّ أُمَّةٍ تُدْعَىٰٓ إِلَىٰ كِتَٰبِهَا ٱلْيَوْمَ تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ', 'وترى كل أمة جاثية كل أمة تدعى إلى كتابها اليوم تجزون ما كنتم تعملون', 25, 50, 501, 200, '45:28'),
(4502, NULL, NULL, 45, 29, 'هَٰذَا كِتَٰبُنَا يَنطِقُ عَلَيْكُم بِٱلْحَقِّ إِنَّا كُنَّا نَسْتَنسِخُ مَا كُنتُمْ تَعْمَلُونَ', 'هذا كتابنا ينطق عليكم بالحق إنا كنا نستنسخ ما كنتم تعملون', 25, 50, 501, 200, '45:29'),
(4503, NULL, NULL, 45, 30, 'فَأَمَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ فَيُدْخِلُهُمْ رَبُّهُمْ فِى رَحْمَتِهِۦ ذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْمُبِينُ', 'فأما الذين آمنوا وعملوا الصالحات فيدخلهم ربهم في رحمته ذلك هو الفوز المبين', 25, 50, 501, 200, '45:30'),
(4504, NULL, NULL, 45, 31, 'وَأَمَّا ٱلَّذِينَ كَفَرُوٓا۟ أَفَلَمْ تَكُنْ ءَايَٰتِى تُتْلَىٰ عَلَيْكُمْ فَٱسْتَكْبَرْتُمْ وَكُنتُمْ قَوْمًا مُّجْرِمِينَ', 'وأما الذين كفروا أفلم تكن آياتي تتلى عليكم فاستكبرتم وكنتم قوما مجرمين', 25, 50, 501, 200, '45:31'),
(4505, NULL, NULL, 45, 32, 'وَإِذَا قِيلَ إِنَّ وَعْدَ ٱللَّهِ حَقٌّ وَٱلسَّاعَةُ لَا رَيْبَ فِيهَا قُلْتُم مَّا نَدْرِى مَا ٱلسَّاعَةُ إِن نَّظُنُّ إِلَّا ظَنًّا وَمَا نَحْنُ بِمُسْتَيْقِنِينَ', 'وإذا قيل إن وعد الله حق والساعة لا ريب فيها قلتم ما ندري ما الساعة إن نظن إلا ظنا وما نحن بمستيقنين', 25, 50, 501, 200, '45:32'),
(4506, NULL, NULL, 45, 33, 'وَبَدَا لَهُمْ سَيِّـَٔاتُ مَا عَمِلُوا۟ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ', 'وبدا لهم سيئات ما عملوا وحاق بهم ما كانوا به يستهزئون', 25, 50, 502, 200, '45:33'),
(4507, NULL, NULL, 45, 34, 'وَقِيلَ ٱلْيَوْمَ نَنسَىٰكُمْ كَمَا نَسِيتُمْ لِقَآءَ يَوْمِكُمْ هَٰذَا وَمَأْوَىٰكُمُ ٱلنَّارُ وَمَا لَكُم مِّن نَّٰصِرِينَ', 'وقيل اليوم ننساكم كما نسيتم لقاء يومكم هذا ومأواكم النار وما لكم من ناصرين', 25, 50, 502, 200, '45:34'),
(4508, NULL, NULL, 45, 35, 'ذَٰلِكُم بِأَنَّكُمُ ٱتَّخَذْتُمْ ءَايَٰتِ ٱللَّهِ هُزُوًا وَغَرَّتْكُمُ ٱلْحَيَوٰةُ ٱلدُّنْيَا فَٱلْيَوْمَ لَا يُخْرَجُونَ مِنْهَا وَلَا هُمْ يُسْتَعْتَبُونَ', 'ذلكم بأنكم اتخذتم آيات الله هزوا وغرتكم الحياة الدنيا فاليوم لا يخرجون منها ولا هم يستعتبون', 25, 50, 502, 200, '45:35'),
(4509, NULL, NULL, 45, 36, 'فَلِلَّهِ ٱلْحَمْدُ رَبِّ ٱلسَّمَٰوَٰتِ وَرَبِّ ٱلْأَرْضِ رَبِّ ٱلْعَٰلَمِينَ', 'فلله الحمد رب السماوات ورب الأرض رب العالمين', 25, 50, 502, 200, '45:36'),
(4510, NULL, NULL, 45, 37, 'وَلَهُ ٱلْكِبْرِيَآءُ فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'وله الكبرياء في السماوات والأرض وهو العزيز الحكيم', 25, 50, 502, 200, '45:37'),
(4511, NULL, NULL, 46, 1, 'حمٓ', 'حم', 26, 51, 502, 201, '46:1'),
(4512, NULL, NULL, 46, 2, 'تَنزِيلُ ٱلْكِتَٰبِ مِنَ ٱللَّهِ ٱلْعَزِيزِ ٱلْحَكِيمِ', 'تنزيل الكتاب من الله العزيز الحكيم', 26, 51, 502, 201, '46:2'),
(4513, NULL, NULL, 46, 3, 'مَا خَلَقْنَا ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَآ إِلَّا بِٱلْحَقِّ وَأَجَلٍ مُّسَمًّى وَٱلَّذِينَ كَفَرُوا۟ عَمَّآ أُنذِرُوا۟ مُعْرِضُونَ', 'ما خلقنا السماوات والأرض وما بينهما إلا بالحق وأجل مسمى والذين كفروا عما أنذروا معرضون', 26, 51, 502, 201, '46:3'),
(4514, NULL, NULL, 46, 4, 'قُلْ أَرَءَيْتُم مَّا تَدْعُونَ مِن دُونِ ٱللَّهِ أَرُونِى مَاذَا خَلَقُوا۟ مِنَ ٱلْأَرْضِ أَمْ لَهُمْ شِرْكٌ فِى ٱلسَّمَٰوَٰتِ ٱئْتُونِى بِكِتَٰبٍ مِّن قَبْلِ هَٰذَآ أَوْ أَثَٰرَةٍ مِّنْ عِلْمٍ إِن كُنتُمْ صَٰدِقِينَ', 'قل أرأيتم ما تدعون من دون الله أروني ماذا خلقوا من الأرض أم لهم شرك في السماوات ائتوني بكتاب من قبل هذا أو أثارة من علم إن كنتم صادقين', 26, 51, 502, 201, '46:4'),
(4515, NULL, NULL, 46, 5, 'وَمَنْ أَضَلُّ مِمَّن يَدْعُوا۟ مِن دُونِ ٱللَّهِ مَن لَّا يَسْتَجِيبُ لَهُۥٓ إِلَىٰ يَوْمِ ٱلْقِيَٰمَةِ وَهُمْ عَن دُعَآئِهِمْ غَٰفِلُونَ', 'ومن أضل ممن يدعو من دون الله من لا يستجيب له إلى يوم القيامة وهم عن دعائهم غافلون', 26, 51, 502, 201, '46:5'),
(4516, NULL, NULL, 46, 6, 'وَإِذَا حُشِرَ ٱلنَّاسُ كَانُوا۟ لَهُمْ أَعْدَآءً وَكَانُوا۟ بِعِبَادَتِهِمْ كَٰفِرِينَ', 'وإذا حشر الناس كانوا لهم أعداء وكانوا بعبادتهم كافرين', 26, 51, 503, 201, '46:6'),
(4517, NULL, NULL, 46, 7, 'وَإِذَا تُتْلَىٰ عَلَيْهِمْ ءَايَٰتُنَا بَيِّنَٰتٍ قَالَ ٱلَّذِينَ كَفَرُوا۟ لِلْحَقِّ لَمَّا جَآءَهُمْ هَٰذَا سِحْرٌ مُّبِينٌ', 'وإذا تتلى عليهم آياتنا بينات قال الذين كفروا للحق لما جاءهم هذا سحر مبين', 26, 51, 503, 201, '46:7'),
(4518, NULL, NULL, 46, 8, 'أَمْ يَقُولُونَ ٱفْتَرَىٰهُ قُلْ إِنِ ٱفْتَرَيْتُهُۥ فَلَا تَمْلِكُونَ لِى مِنَ ٱللَّهِ شَيْـًٔا هُوَ أَعْلَمُ بِمَا تُفِيضُونَ فِيهِ كَفَىٰ بِهِۦ شَهِيدًۢا بَيْنِى وَبَيْنَكُمْ وَهُوَ ٱلْغَفُورُ ٱلرَّحِيمُ', 'أم يقولون افتراه قل إن افتريته فلا تملكون لي من الله شيئا هو أعلم بما تفيضون فيه كفى به شهيدا بيني وبينكم وهو الغفور الرحيم', 26, 51, 503, 201, '46:8'),
(4519, NULL, NULL, 46, 9, 'قُلْ مَا كُنتُ بِدْعًا مِّنَ ٱلرُّسُلِ وَمَآ أَدْرِى مَا يُفْعَلُ بِى وَلَا بِكُمْ إِنْ أَتَّبِعُ إِلَّا مَا يُوحَىٰٓ إِلَىَّ وَمَآ أَنَا۠ إِلَّا نَذِيرٌ مُّبِينٌ', 'قل ما كنت بدعا من الرسل وما أدري ما يفعل بي ولا بكم إن أتبع إلا ما يوحى إلي وما أنا إلا نذير مبين', 26, 51, 503, 201, '46:9'),
(4520, NULL, NULL, 46, 10, 'قُلْ أَرَءَيْتُمْ إِن كَانَ مِنْ عِندِ ٱللَّهِ وَكَفَرْتُم بِهِۦ وَشَهِدَ شَاهِدٌ مِّنۢ بَنِىٓ إِسْرَٰٓءِيلَ عَلَىٰ مِثْلِهِۦ فَـَٔامَنَ وَٱسْتَكْبَرْتُمْ إِنَّ ٱللَّهَ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّٰلِمِينَ', 'قل أرأيتم إن كان من عند الله وكفرتم به وشهد شاهد من بني إسرائيل على مثله فآمن واستكبرتم إن الله لا يهدي القوم الظالمين', 26, 51, 503, 201, '46:10'),
(4521, NULL, NULL, 46, 11, 'وَقَالَ ٱلَّذِينَ كَفَرُوا۟ لِلَّذِينَ ءَامَنُوا۟ لَوْ كَانَ خَيْرًا مَّا سَبَقُونَآ إِلَيْهِ وَإِذْ لَمْ يَهْتَدُوا۟ بِهِۦ فَسَيَقُولُونَ هَٰذَآ إِفْكٌ قَدِيمٌ', 'وقال الذين كفروا للذين آمنوا لو كان خيرا ما سبقونا إليه وإذ لم يهتدوا به فسيقولون هذا إفك قديم', 26, 51, 503, 201, '46:11'),
(4522, NULL, NULL, 46, 12, 'وَمِن قَبْلِهِۦ كِتَٰبُ مُوسَىٰٓ إِمَامًا وَرَحْمَةً وَهَٰذَا كِتَٰبٌ مُّصَدِّقٌ لِّسَانًا عَرَبِيًّا لِّيُنذِرَ ٱلَّذِينَ ظَلَمُوا۟ وَبُشْرَىٰ لِلْمُحْسِنِينَ', 'ومن قبله كتاب موسى إماما ورحمة وهذا كتاب مصدق لسانا عربيا لينذر الذين ظلموا وبشرى للمحسنين', 26, 51, 503, 201, '46:12'),
(4523, NULL, NULL, 46, 13, 'إِنَّ ٱلَّذِينَ قَالُوا۟ رَبُّنَا ٱللَّهُ ثُمَّ ٱسْتَقَٰمُوا۟ فَلَا خَوْفٌ عَلَيْهِمْ وَلَا هُمْ يَحْزَنُونَ', 'إن الذين قالوا ربنا الله ثم استقاموا فلا خوف عليهم ولا هم يحزنون', 26, 51, 503, 201, '46:13'),
(4524, NULL, NULL, 46, 14, 'أُو۟لَٰٓئِكَ أَصْحَٰبُ ٱلْجَنَّةِ خَٰلِدِينَ فِيهَا جَزَآءًۢ بِمَا كَانُوا۟ يَعْمَلُونَ', 'أولئك أصحاب الجنة خالدين فيها جزاء بما كانوا يعملون', 26, 51, 503, 201, '46:14'),
(4525, NULL, NULL, 46, 15, 'وَوَصَّيْنَا ٱلْإِنسَٰنَ بِوَٰلِدَيْهِ إِحْسَٰنًا حَمَلَتْهُ أُمُّهُۥ كُرْهًا وَوَضَعَتْهُ كُرْهًا وَحَمْلُهُۥ وَفِصَٰلُهُۥ ثَلَٰثُونَ شَهْرًا حَتَّىٰٓ إِذَا بَلَغَ أَشُدَّهُۥ وَبَلَغَ أَرْبَعِينَ سَنَةً قَالَ رَبِّ أَوْزِعْنِىٓ أَنْ أَشْكُرَ نِعْمَتَكَ ٱلَّتِىٓ أَنْعَمْتَ عَلَىَّ وَعَلَىٰ وَٰلِدَىَّ وَأَنْ أَعْمَلَ صَٰلِحًا تَرْضَىٰهُ وَأَصْلِحْ لِى فِى ذُرِّيَّتِىٓ إِنِّى تُبْتُ إِلَيْكَ وَإِنِّى مِنَ ٱلْمُسْلِمِينَ', 'ووصينا الإنسان بوالديه إحسانا حملته أمه كرها ووضعته كرها وحمله وفصاله ثلاثون شهرا حتى إذا بلغ أشده وبلغ أربعين سنة قال رب أوزعني أن أشكر نعمتك التي أنعمت علي وعلى والدي وأن أعمل صالحا ترضاه وأصلح لي في ذريتي إني تبت إليك وإني من المسلمين', 26, 51, 504, 201, '46:15'),
(4526, NULL, NULL, 46, 16, 'أُو۟لَٰٓئِكَ ٱلَّذِينَ نَتَقَبَّلُ عَنْهُمْ أَحْسَنَ مَا عَمِلُوا۟ وَنَتَجَاوَزُ عَن سَيِّـَٔاتِهِمْ فِىٓ أَصْحَٰبِ ٱلْجَنَّةِ وَعْدَ ٱلصِّدْقِ ٱلَّذِى كَانُوا۟ يُوعَدُونَ', 'أولئك الذين نتقبل عنهم أحسن ما عملوا ونتجاوز عن سيئاتهم في أصحاب الجنة وعد الصدق الذي كانوا يوعدون', 26, 51, 504, 201, '46:16'),
(4527, NULL, NULL, 46, 17, 'وَٱلَّذِى قَالَ لِوَٰلِدَيْهِ أُفٍّ لَّكُمَآ أَتَعِدَانِنِىٓ أَنْ أُخْرَجَ وَقَدْ خَلَتِ ٱلْقُرُونُ مِن قَبْلِى وَهُمَا يَسْتَغِيثَانِ ٱللَّهَ وَيْلَكَ ءَامِنْ إِنَّ وَعْدَ ٱللَّهِ حَقٌّ فَيَقُولُ مَا هَٰذَآ إِلَّآ أَسَٰطِيرُ ٱلْأَوَّلِينَ', 'والذي قال لوالديه أف لكما أتعدانني أن أخرج وقد خلت القرون من قبلي وهما يستغيثان الله ويلك آمن إن وعد الله حق فيقول ما هذا إلا أساطير الأولين', 26, 51, 504, 201, '46:17'),
(4528, NULL, NULL, 46, 18, 'أُو۟لَٰٓئِكَ ٱلَّذِينَ حَقَّ عَلَيْهِمُ ٱلْقَوْلُ فِىٓ أُمَمٍ قَدْ خَلَتْ مِن قَبْلِهِم مِّنَ ٱلْجِنِّ وَٱلْإِنسِ إِنَّهُمْ كَانُوا۟ خَٰسِرِينَ', 'أولئك الذين حق عليهم القول في أمم قد خلت من قبلهم من الجن والإنس إنهم كانوا خاسرين', 26, 51, 504, 201, '46:18'),
(4529, NULL, NULL, 46, 19, 'وَلِكُلٍّ دَرَجَٰتٌ مِّمَّا عَمِلُوا۟ وَلِيُوَفِّيَهُمْ أَعْمَٰلَهُمْ وَهُمْ لَا يُظْلَمُونَ', 'ولكل درجات مما عملوا وليوفيهم أعمالهم وهم لا يظلمون', 26, 51, 504, 201, '46:19'),
(4530, NULL, NULL, 46, 20, 'وَيَوْمَ يُعْرَضُ ٱلَّذِينَ كَفَرُوا۟ عَلَى ٱلنَّارِ أَذْهَبْتُمْ طَيِّبَٰتِكُمْ فِى حَيَاتِكُمُ ٱلدُّنْيَا وَٱسْتَمْتَعْتُم بِهَا فَٱلْيَوْمَ تُجْزَوْنَ عَذَابَ ٱلْهُونِ بِمَا كُنتُمْ تَسْتَكْبِرُونَ فِى ٱلْأَرْضِ بِغَيْرِ ٱلْحَقِّ وَبِمَا كُنتُمْ تَفْسُقُونَ', 'ويوم يعرض الذين كفروا على النار أذهبتم طيباتكم في حياتكم الدنيا واستمتعتم بها فاليوم تجزون عذاب الهون بما كنتم تستكبرون في الأرض بغير الحق وبما كنتم تفسقون', 26, 51, 504, 201, '46:20'),
(4531, NULL, NULL, 46, 21, 'وَٱذْكُرْ أَخَا عَادٍ إِذْ أَنذَرَ قَوْمَهُۥ بِٱلْأَحْقَافِ وَقَدْ خَلَتِ ٱلنُّذُرُ مِنۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِۦٓ أَلَّا تَعْبُدُوٓا۟ إِلَّا ٱللَّهَ إِنِّىٓ أَخَافُ عَلَيْكُمْ عَذَابَ يَوْمٍ عَظِيمٍ', 'واذكر أخا عاد إذ أنذر قومه بالأحقاف وقد خلت النذر من بين يديه ومن خلفه ألا تعبدوا إلا الله إني أخاف عليكم عذاب يوم عظيم', 26, 51, 505, 202, '46:21'),
(4532, NULL, NULL, 46, 22, 'قَالُوٓا۟ أَجِئْتَنَا لِتَأْفِكَنَا عَنْ ءَالِهَتِنَا فَأْتِنَا بِمَا تَعِدُنَآ إِن كُنتَ مِنَ ٱلصَّٰدِقِينَ', 'قالوا أجئتنا لتأفكنا عن آلهتنا فأتنا بما تعدنا إن كنت من الصادقين', 26, 51, 505, 202, '46:22'),
(4533, NULL, NULL, 46, 23, 'قَالَ إِنَّمَا ٱلْعِلْمُ عِندَ ٱللَّهِ وَأُبَلِّغُكُم مَّآ أُرْسِلْتُ بِهِۦ وَلَٰكِنِّىٓ أَرَىٰكُمْ قَوْمًا تَجْهَلُونَ', 'قال إنما العلم عند الله وأبلغكم ما أرسلت به ولكني أراكم قوما تجهلون', 26, 51, 505, 202, '46:23'),
(4534, NULL, NULL, 46, 24, 'فَلَمَّا رَأَوْهُ عَارِضًا مُّسْتَقْبِلَ أَوْدِيَتِهِمْ قَالُوا۟ هَٰذَا عَارِضٌ مُّمْطِرُنَا بَلْ هُوَ مَا ٱسْتَعْجَلْتُم بِهِۦ رِيحٌ فِيهَا عَذَابٌ أَلِيمٌ', 'فلما رأوه عارضا مستقبل أوديتهم قالوا هذا عارض ممطرنا بل هو ما استعجلتم به ريح فيها عذاب أليم', 26, 51, 505, 202, '46:24'),
(4535, NULL, NULL, 46, 25, 'تُدَمِّرُ كُلَّ شَىْءٍۭ بِأَمْرِ رَبِّهَا فَأَصْبَحُوا۟ لَا يُرَىٰٓ إِلَّا مَسَٰكِنُهُمْ كَذَٰلِكَ نَجْزِى ٱلْقَوْمَ ٱلْمُجْرِمِينَ', 'تدمر كل شيء بأمر ربها فأصبحوا لا يرى إلا مساكنهم كذلك نجزي القوم المجرمين', 26, 51, 505, 202, '46:25');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(4536, NULL, NULL, 46, 26, 'وَلَقَدْ مَكَّنَّٰهُمْ فِيمَآ إِن مَّكَّنَّٰكُمْ فِيهِ وَجَعَلْنَا لَهُمْ سَمْعًا وَأَبْصَٰرًا وَأَفْـِٔدَةً فَمَآ أَغْنَىٰ عَنْهُمْ سَمْعُهُمْ وَلَآ أَبْصَٰرُهُمْ وَلَآ أَفْـِٔدَتُهُم مِّن شَىْءٍ إِذْ كَانُوا۟ يَجْحَدُونَ بِـَٔايَٰتِ ٱللَّهِ وَحَاقَ بِهِم مَّا كَانُوا۟ بِهِۦ يَسْتَهْزِءُونَ', 'ولقد مكناهم فيما إن مكناكم فيه وجعلنا لهم سمعا وأبصارا وأفئدة فما أغنى عنهم سمعهم ولا أبصارهم ولا أفئدتهم من شيء إذ كانوا يجحدون بآيات الله وحاق بهم ما كانوا به يستهزئون', 26, 51, 505, 202, '46:26'),
(4537, NULL, NULL, 46, 27, 'وَلَقَدْ أَهْلَكْنَا مَا حَوْلَكُم مِّنَ ٱلْقُرَىٰ وَصَرَّفْنَا ٱلْءَايَٰتِ لَعَلَّهُمْ يَرْجِعُونَ', 'ولقد أهلكنا ما حولكم من القرى وصرفنا الآيات لعلهم يرجعون', 26, 51, 505, 202, '46:27'),
(4538, NULL, NULL, 46, 28, 'فَلَوْلَا نَصَرَهُمُ ٱلَّذِينَ ٱتَّخَذُوا۟ مِن دُونِ ٱللَّهِ قُرْبَانًا ءَالِهَةًۢ بَلْ ضَلُّوا۟ عَنْهُمْ وَذَٰلِكَ إِفْكُهُمْ وَمَا كَانُوا۟ يَفْتَرُونَ', 'فلولا نصرهم الذين اتخذوا من دون الله قربانا آلهة بل ضلوا عنهم وذلك إفكهم وما كانوا يفترون', 26, 51, 505, 202, '46:28'),
(4539, NULL, NULL, 46, 29, 'وَإِذْ صَرَفْنَآ إِلَيْكَ نَفَرًا مِّنَ ٱلْجِنِّ يَسْتَمِعُونَ ٱلْقُرْءَانَ فَلَمَّا حَضَرُوهُ قَالُوٓا۟ أَنصِتُوا۟ فَلَمَّا قُضِىَ وَلَّوْا۟ إِلَىٰ قَوْمِهِم مُّنذِرِينَ', 'وإذ صرفنا إليك نفرا من الجن يستمعون القرآن فلما حضروه قالوا أنصتوا فلما قضي ولوا إلى قومهم منذرين', 26, 51, 506, 202, '46:29'),
(4540, NULL, NULL, 46, 30, 'قَالُوا۟ يَٰقَوْمَنَآ إِنَّا سَمِعْنَا كِتَٰبًا أُنزِلَ مِنۢ بَعْدِ مُوسَىٰ مُصَدِّقًا لِّمَا بَيْنَ يَدَيْهِ يَهْدِىٓ إِلَى ٱلْحَقِّ وَإِلَىٰ طَرِيقٍ مُّسْتَقِيمٍ', 'قالوا يا قومنا إنا سمعنا كتابا أنزل من بعد موسى مصدقا لما بين يديه يهدي إلى الحق وإلى طريق مستقيم', 26, 51, 506, 202, '46:30'),
(4541, NULL, NULL, 46, 31, 'يَٰقَوْمَنَآ أَجِيبُوا۟ دَاعِىَ ٱللَّهِ وَءَامِنُوا۟ بِهِۦ يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُجِرْكُم مِّنْ عَذَابٍ أَلِيمٍ', 'يا قومنا أجيبوا داعي الله وآمنوا به يغفر لكم من ذنوبكم ويجركم من عذاب أليم', 26, 51, 506, 202, '46:31'),
(4542, NULL, NULL, 46, 32, 'وَمَن لَّا يُجِبْ دَاعِىَ ٱللَّهِ فَلَيْسَ بِمُعْجِزٍ فِى ٱلْأَرْضِ وَلَيْسَ لَهُۥ مِن دُونِهِۦٓ أَوْلِيَآءُ أُو۟لَٰٓئِكَ فِى ضَلَٰلٍ مُّبِينٍ', 'ومن لا يجب داعي الله فليس بمعجز في الأرض وليس له من دونه أولياء أولئك في ضلال مبين', 26, 51, 506, 202, '46:32'),
(4543, NULL, NULL, 46, 33, 'أَوَلَمْ يَرَوْا۟ أَنَّ ٱللَّهَ ٱلَّذِى خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَلَمْ يَعْىَ بِخَلْقِهِنَّ بِقَٰدِرٍ عَلَىٰٓ أَن يُحْۦِىَ ٱلْمَوْتَىٰ بَلَىٰٓ إِنَّهُۥ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'أولم يروا أن الله الذي خلق السماوات والأرض ولم يعي بخلقهن بقادر على أن يحيي الموتى بلى إنه على كل شيء قدير', 26, 51, 506, 202, '46:33'),
(4544, NULL, NULL, 46, 34, 'وَيَوْمَ يُعْرَضُ ٱلَّذِينَ كَفَرُوا۟ عَلَى ٱلنَّارِ أَلَيْسَ هَٰذَا بِٱلْحَقِّ قَالُوا۟ بَلَىٰ وَرَبِّنَا قَالَ فَذُوقُوا۟ ٱلْعَذَابَ بِمَا كُنتُمْ تَكْفُرُونَ', 'ويوم يعرض الذين كفروا على النار أليس هذا بالحق قالوا بلى وربنا قال فذوقوا العذاب بما كنتم تكفرون', 26, 51, 506, 202, '46:34'),
(4545, NULL, NULL, 46, 35, 'فَٱصْبِرْ كَمَا صَبَرَ أُو۟لُوا۟ ٱلْعَزْمِ مِنَ ٱلرُّسُلِ وَلَا تَسْتَعْجِل لَّهُمْ كَأَنَّهُمْ يَوْمَ يَرَوْنَ مَا يُوعَدُونَ لَمْ يَلْبَثُوٓا۟ إِلَّا سَاعَةً مِّن نَّهَارٍۭ بَلَٰغٌ فَهَلْ يُهْلَكُ إِلَّا ٱلْقَوْمُ ٱلْفَٰسِقُونَ', 'فاصبر كما صبر أولو العزم من الرسل ولا تستعجل لهم كأنهم يوم يرون ما يوعدون لم يلبثوا إلا ساعة من نهار بلاغ فهل يهلك إلا القوم الفاسقون', 26, 51, 506, 202, '46:35'),
(4546, NULL, NULL, 47, 1, 'ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ أَضَلَّ أَعْمَٰلَهُمْ', 'الذين كفروا وصدوا عن سبيل الله أضل أعمالهم', 26, 51, 507, 202, '47:1'),
(4547, NULL, NULL, 47, 2, 'وَٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ وَءَامَنُوا۟ بِمَا نُزِّلَ عَلَىٰ مُحَمَّدٍ وَهُوَ ٱلْحَقُّ مِن رَّبِّهِمْ كَفَّرَ عَنْهُمْ سَيِّـَٔاتِهِمْ وَأَصْلَحَ بَالَهُمْ', 'والذين آمنوا وعملوا الصالحات وآمنوا بما نزل على محمد وهو الحق من ربهم كفر عنهم سيئاتهم وأصلح بالهم', 26, 51, 507, 202, '47:2'),
(4548, NULL, NULL, 47, 3, 'ذَٰلِكَ بِأَنَّ ٱلَّذِينَ كَفَرُوا۟ ٱتَّبَعُوا۟ ٱلْبَٰطِلَ وَأَنَّ ٱلَّذِينَ ءَامَنُوا۟ ٱتَّبَعُوا۟ ٱلْحَقَّ مِن رَّبِّهِمْ كَذَٰلِكَ يَضْرِبُ ٱللَّهُ لِلنَّاسِ أَمْثَٰلَهُمْ', 'ذلك بأن الذين كفروا اتبعوا الباطل وأن الذين آمنوا اتبعوا الحق من ربهم كذلك يضرب الله للناس أمثالهم', 26, 51, 507, 202, '47:3'),
(4549, NULL, NULL, 47, 4, 'فَإِذَا لَقِيتُمُ ٱلَّذِينَ كَفَرُوا۟ فَضَرْبَ ٱلرِّقَابِ حَتَّىٰٓ إِذَآ أَثْخَنتُمُوهُمْ فَشُدُّوا۟ ٱلْوَثَاقَ فَإِمَّا مَنًّۢا بَعْدُ وَإِمَّا فِدَآءً حَتَّىٰ تَضَعَ ٱلْحَرْبُ أَوْزَارَهَا ذَٰلِكَ وَلَوْ يَشَآءُ ٱللَّهُ لَٱنتَصَرَ مِنْهُمْ وَلَٰكِن لِّيَبْلُوَا۟ بَعْضَكُم بِبَعْضٍ وَٱلَّذِينَ قُتِلُوا۟ فِى سَبِيلِ ٱللَّهِ فَلَن يُضِلَّ أَعْمَٰلَهُمْ', 'فإذا لقيتم الذين كفروا فضرب الرقاب حتى إذا أثخنتموهم فشدوا الوثاق فإما منا بعد وإما فداء حتى تضع الحرب أوزارها ذلك ولو يشاء الله لانتصر منهم ولكن ليبلو بعضكم ببعض والذين قتلوا في سبيل الله فلن يضل أعمالهم', 26, 51, 507, 202, '47:4'),
(4550, NULL, NULL, 47, 5, 'سَيَهْدِيهِمْ وَيُصْلِحُ بَالَهُمْ', 'سيهديهم ويصلح بالهم', 26, 51, 507, 202, '47:5'),
(4551, NULL, NULL, 47, 6, 'وَيُدْخِلُهُمُ ٱلْجَنَّةَ عَرَّفَهَا لَهُمْ', 'ويدخلهم الجنة عرفها لهم', 26, 51, 507, 202, '47:6'),
(4552, NULL, NULL, 47, 7, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِن تَنصُرُوا۟ ٱللَّهَ يَنصُرْكُمْ وَيُثَبِّتْ أَقْدَامَكُمْ', 'يا أيها الذين آمنوا إن تنصروا الله ينصركم ويثبت أقدامكم', 26, 51, 507, 202, '47:7'),
(4553, NULL, NULL, 47, 8, 'وَٱلَّذِينَ كَفَرُوا۟ فَتَعْسًا لَّهُمْ وَأَضَلَّ أَعْمَٰلَهُمْ', 'والذين كفروا فتعسا لهم وأضل أعمالهم', 26, 51, 507, 202, '47:8'),
(4554, NULL, NULL, 47, 9, 'ذَٰلِكَ بِأَنَّهُمْ كَرِهُوا۟ مَآ أَنزَلَ ٱللَّهُ فَأَحْبَطَ أَعْمَٰلَهُمْ', 'ذلك بأنهم كرهوا ما أنزل الله فأحبط أعمالهم', 26, 51, 507, 202, '47:9'),
(4555, NULL, NULL, 47, 10, 'أَفَلَمْ يَسِيرُوا۟ فِى ٱلْأَرْضِ فَيَنظُرُوا۟ كَيْفَ كَانَ عَٰقِبَةُ ٱلَّذِينَ مِن قَبْلِهِمْ دَمَّرَ ٱللَّهُ عَلَيْهِمْ وَلِلْكَٰفِرِينَ أَمْثَٰلُهَا', 'أفلم يسيروا في الأرض فينظروا كيف كان عاقبة الذين من قبلهم دمر الله عليهم وللكافرين أمثالها', 26, 51, 507, 203, '47:10'),
(4556, NULL, NULL, 47, 11, 'ذَٰلِكَ بِأَنَّ ٱللَّهَ مَوْلَى ٱلَّذِينَ ءَامَنُوا۟ وَأَنَّ ٱلْكَٰفِرِينَ لَا مَوْلَىٰ لَهُمْ', 'ذلك بأن الله مولى الذين آمنوا وأن الكافرين لا مولى لهم', 26, 51, 507, 203, '47:11'),
(4557, NULL, NULL, 47, 12, 'إِنَّ ٱللَّهَ يُدْخِلُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ وَٱلَّذِينَ كَفَرُوا۟ يَتَمَتَّعُونَ وَيَأْكُلُونَ كَمَا تَأْكُلُ ٱلْأَنْعَٰمُ وَٱلنَّارُ مَثْوًى لَّهُمْ', 'إن الله يدخل الذين آمنوا وعملوا الصالحات جنات تجري من تحتها الأنهار والذين كفروا يتمتعون ويأكلون كما تأكل الأنعام والنار مثوى لهم', 26, 51, 508, 203, '47:12'),
(4558, NULL, NULL, 47, 13, 'وَكَأَيِّن مِّن قَرْيَةٍ هِىَ أَشَدُّ قُوَّةً مِّن قَرْيَتِكَ ٱلَّتِىٓ أَخْرَجَتْكَ أَهْلَكْنَٰهُمْ فَلَا نَاصِرَ لَهُمْ', 'وكأين من قرية هي أشد قوة من قريتك التي أخرجتك أهلكناهم فلا ناصر لهم', 26, 51, 508, 203, '47:13'),
(4559, NULL, NULL, 47, 14, 'أَفَمَن كَانَ عَلَىٰ بَيِّنَةٍ مِّن رَّبِّهِۦ كَمَن زُيِّنَ لَهُۥ سُوٓءُ عَمَلِهِۦ وَٱتَّبَعُوٓا۟ أَهْوَآءَهُم', 'أفمن كان على بينة من ربه كمن زين له سوء عمله واتبعوا أهواءهم', 26, 51, 508, 203, '47:14'),
(4560, NULL, NULL, 47, 15, 'مَّثَلُ ٱلْجَنَّةِ ٱلَّتِى وُعِدَ ٱلْمُتَّقُونَ فِيهَآ أَنْهَٰرٌ مِّن مَّآءٍ غَيْرِ ءَاسِنٍ وَأَنْهَٰرٌ مِّن لَّبَنٍ لَّمْ يَتَغَيَّرْ طَعْمُهُۥ وَأَنْهَٰرٌ مِّنْ خَمْرٍ لَّذَّةٍ لِّلشَّٰرِبِينَ وَأَنْهَٰرٌ مِّنْ عَسَلٍ مُّصَفًّى وَلَهُمْ فِيهَا مِن كُلِّ ٱلثَّمَرَٰتِ وَمَغْفِرَةٌ مِّن رَّبِّهِمْ كَمَنْ هُوَ خَٰلِدٌ فِى ٱلنَّارِ وَسُقُوا۟ مَآءً حَمِيمًا فَقَطَّعَ أَمْعَآءَهُمْ', 'مثل الجنة التي وعد المتقون فيها أنهار من ماء غير آسن وأنهار من لبن لم يتغير طعمه وأنهار من خمر لذة للشاربين وأنهار من عسل مصفى ولهم فيها من كل الثمرات ومغفرة من ربهم كمن هو خالد في النار وسقوا ماء حميما فقطع أمعاءهم', 26, 51, 508, 203, '47:15'),
(4561, NULL, NULL, 47, 16, 'وَمِنْهُم مَّن يَسْتَمِعُ إِلَيْكَ حَتَّىٰٓ إِذَا خَرَجُوا۟ مِنْ عِندِكَ قَالُوا۟ لِلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ مَاذَا قَالَ ءَانِفًا أُو۟لَٰٓئِكَ ٱلَّذِينَ طَبَعَ ٱللَّهُ عَلَىٰ قُلُوبِهِمْ وَٱتَّبَعُوٓا۟ أَهْوَآءَهُمْ', 'ومنهم من يستمع إليك حتى إذا خرجوا من عندك قالوا للذين أوتوا العلم ماذا قال آنفا أولئك الذين طبع الله على قلوبهم واتبعوا أهواءهم', 26, 51, 508, 203, '47:16'),
(4562, NULL, NULL, 47, 17, 'وَٱلَّذِينَ ٱهْتَدَوْا۟ زَادَهُمْ هُدًى وَءَاتَىٰهُمْ تَقْوَىٰهُمْ', 'والذين اهتدوا زادهم هدى وآتاهم تقواهم', 26, 51, 508, 203, '47:17'),
(4563, NULL, NULL, 47, 18, 'فَهَلْ يَنظُرُونَ إِلَّا ٱلسَّاعَةَ أَن تَأْتِيَهُم بَغْتَةً فَقَدْ جَآءَ أَشْرَاطُهَا فَأَنَّىٰ لَهُمْ إِذَا جَآءَتْهُمْ ذِكْرَىٰهُمْ', 'فهل ينظرون إلا الساعة أن تأتيهم بغتة فقد جاء أشراطها فأنى لهم إذا جاءتهم ذكراهم', 26, 51, 508, 203, '47:18'),
(4564, NULL, NULL, 47, 19, 'فَٱعْلَمْ أَنَّهُۥ لَآ إِلَٰهَ إِلَّا ٱللَّهُ وَٱسْتَغْفِرْ لِذَنۢبِكَ وَلِلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ وَٱللَّهُ يَعْلَمُ مُتَقَلَّبَكُمْ وَمَثْوَىٰكُمْ', 'فاعلم أنه لا إله إلا الله واستغفر لذنبك وللمؤمنين والمؤمنات والله يعلم متقلبكم ومثواكم', 26, 51, 508, 203, '47:19'),
(4565, NULL, NULL, 47, 20, 'وَيَقُولُ ٱلَّذِينَ ءَامَنُوا۟ لَوْلَا نُزِّلَتْ سُورَةٌ فَإِذَآ أُنزِلَتْ سُورَةٌ مُّحْكَمَةٌ وَذُكِرَ فِيهَا ٱلْقِتَالُ رَأَيْتَ ٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌ يَنظُرُونَ إِلَيْكَ نَظَرَ ٱلْمَغْشِىِّ عَلَيْهِ مِنَ ٱلْمَوْتِ فَأَوْلَىٰ لَهُمْ', 'ويقول الذين آمنوا لولا نزلت سورة فإذا أنزلت سورة محكمة وذكر فيها القتال رأيت الذين في قلوبهم مرض ينظرون إليك نظر المغشي عليه من الموت فأولى لهم', 26, 51, 509, 203, '47:20'),
(4566, NULL, NULL, 47, 21, 'طَاعَةٌ وَقَوْلٌ مَّعْرُوفٌ فَإِذَا عَزَمَ ٱلْأَمْرُ فَلَوْ صَدَقُوا۟ ٱللَّهَ لَكَانَ خَيْرًا لَّهُمْ', 'طاعة وقول معروف فإذا عزم الأمر فلو صدقوا الله لكان خيرا لهم', 26, 51, 509, 203, '47:21'),
(4567, NULL, NULL, 47, 22, 'فَهَلْ عَسَيْتُمْ إِن تَوَلَّيْتُمْ أَن تُفْسِدُوا۟ فِى ٱلْأَرْضِ وَتُقَطِّعُوٓا۟ أَرْحَامَكُمْ', 'فهل عسيتم إن توليتم أن تفسدوا في الأرض وتقطعوا أرحامكم', 26, 51, 509, 203, '47:22'),
(4568, NULL, NULL, 47, 23, 'أُو۟لَٰٓئِكَ ٱلَّذِينَ لَعَنَهُمُ ٱللَّهُ فَأَصَمَّهُمْ وَأَعْمَىٰٓ أَبْصَٰرَهُمْ', 'أولئك الذين لعنهم الله فأصمهم وأعمى أبصارهم', 26, 51, 509, 203, '47:23'),
(4569, NULL, NULL, 47, 24, 'أَفَلَا يَتَدَبَّرُونَ ٱلْقُرْءَانَ أَمْ عَلَىٰ قُلُوبٍ أَقْفَالُهَآ', 'أفلا يتدبرون القرآن أم على قلوب أقفالها', 26, 51, 509, 203, '47:24'),
(4570, NULL, NULL, 47, 25, 'إِنَّ ٱلَّذِينَ ٱرْتَدُّوا۟ عَلَىٰٓ أَدْبَٰرِهِم مِّنۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ ٱلْهُدَى ٱلشَّيْطَٰنُ سَوَّلَ لَهُمْ وَأَمْلَىٰ لَهُمْ', 'إن الذين ارتدوا على أدبارهم من بعد ما تبين لهم الهدى الشيطان سول لهم وأملى لهم', 26, 51, 509, 203, '47:25'),
(4571, NULL, NULL, 47, 26, 'ذَٰلِكَ بِأَنَّهُمْ قَالُوا۟ لِلَّذِينَ كَرِهُوا۟ مَا نَزَّلَ ٱللَّهُ سَنُطِيعُكُمْ فِى بَعْضِ ٱلْأَمْرِ وَٱللَّهُ يَعْلَمُ إِسْرَارَهُمْ', 'ذلك بأنهم قالوا للذين كرهوا ما نزل الله سنطيعكم في بعض الأمر والله يعلم إسرارهم', 26, 51, 509, 203, '47:26'),
(4572, NULL, NULL, 47, 27, 'فَكَيْفَ إِذَا تَوَفَّتْهُمُ ٱلْمَلَٰٓئِكَةُ يَضْرِبُونَ وُجُوهَهُمْ وَأَدْبَٰرَهُمْ', 'فكيف إذا توفتهم الملائكة يضربون وجوههم وأدبارهم', 26, 51, 509, 203, '47:27'),
(4573, NULL, NULL, 47, 28, 'ذَٰلِكَ بِأَنَّهُمُ ٱتَّبَعُوا۟ مَآ أَسْخَطَ ٱللَّهَ وَكَرِهُوا۟ رِضْوَٰنَهُۥ فَأَحْبَطَ أَعْمَٰلَهُمْ', 'ذلك بأنهم اتبعوا ما أسخط الله وكرهوا رضوانه فأحبط أعمالهم', 26, 51, 509, 203, '47:28'),
(4574, NULL, NULL, 47, 29, 'أَمْ حَسِبَ ٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌ أَن لَّن يُخْرِجَ ٱللَّهُ أَضْغَٰنَهُمْ', 'أم حسب الذين في قلوبهم مرض أن لن يخرج الله أضغانهم', 26, 51, 509, 203, '47:29'),
(4575, NULL, NULL, 47, 30, 'وَلَوْ نَشَآءُ لَأَرَيْنَٰكَهُمْ فَلَعَرَفْتَهُم بِسِيمَٰهُمْ وَلَتَعْرِفَنَّهُمْ فِى لَحْنِ ٱلْقَوْلِ وَٱللَّهُ يَعْلَمُ أَعْمَٰلَكُمْ', 'ولو نشاء لأريناكهم فلعرفتهم بسيماهم ولتعرفنهم في لحن القول والله يعلم أعمالكم', 26, 51, 510, 203, '47:30'),
(4576, NULL, NULL, 47, 31, 'وَلَنَبْلُوَنَّكُمْ حَتَّىٰ نَعْلَمَ ٱلْمُجَٰهِدِينَ مِنكُمْ وَٱلصَّٰبِرِينَ وَنَبْلُوَا۟ أَخْبَارَكُمْ', 'ولنبلونكم حتى نعلم المجاهدين منكم والصابرين ونبلو أخباركم', 26, 51, 510, 203, '47:31'),
(4577, NULL, NULL, 47, 32, 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ وَشَآقُّوا۟ ٱلرَّسُولَ مِنۢ بَعْدِ مَا تَبَيَّنَ لَهُمُ ٱلْهُدَىٰ لَن يَضُرُّوا۟ ٱللَّهَ شَيْـًٔا وَسَيُحْبِطُ أَعْمَٰلَهُمْ', 'إن الذين كفروا وصدوا عن سبيل الله وشاقوا الرسول من بعد ما تبين لهم الهدى لن يضروا الله شيئا وسيحبط أعمالهم', 26, 51, 510, 203, '47:32'),
(4578, NULL, NULL, 47, 33, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ أَطِيعُوا۟ ٱللَّهَ وَأَطِيعُوا۟ ٱلرَّسُولَ وَلَا تُبْطِلُوٓا۟ أَعْمَٰلَكُمْ', 'يا أيها الذين آمنوا أطيعوا الله وأطيعوا الرسول ولا تبطلوا أعمالكم', 26, 51, 510, 204, '47:33'),
(4579, NULL, NULL, 47, 34, 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ ثُمَّ مَاتُوا۟ وَهُمْ كُفَّارٌ فَلَن يَغْفِرَ ٱللَّهُ لَهُمْ', 'إن الذين كفروا وصدوا عن سبيل الله ثم ماتوا وهم كفار فلن يغفر الله لهم', 26, 51, 510, 204, '47:34'),
(4580, NULL, NULL, 47, 35, 'فَلَا تَهِنُوا۟ وَتَدْعُوٓا۟ إِلَى ٱلسَّلْمِ وَأَنتُمُ ٱلْأَعْلَوْنَ وَٱللَّهُ مَعَكُمْ وَلَن يَتِرَكُمْ أَعْمَٰلَكُمْ', 'فلا تهنوا وتدعوا إلى السلم وأنتم الأعلون والله معكم ولن يتركم أعمالكم', 26, 51, 510, 204, '47:35'),
(4581, NULL, NULL, 47, 36, 'إِنَّمَا ٱلْحَيَوٰةُ ٱلدُّنْيَا لَعِبٌ وَلَهْوٌ وَإِن تُؤْمِنُوا۟ وَتَتَّقُوا۟ يُؤْتِكُمْ أُجُورَكُمْ وَلَا يَسْـَٔلْكُمْ أَمْوَٰلَكُمْ', 'إنما الحياة الدنيا لعب ولهو وإن تؤمنوا وتتقوا يؤتكم أجوركم ولا يسألكم أموالكم', 26, 51, 510, 204, '47:36'),
(4582, NULL, NULL, 47, 37, 'إِن يَسْـَٔلْكُمُوهَا فَيُحْفِكُمْ تَبْخَلُوا۟ وَيُخْرِجْ أَضْغَٰنَكُمْ', 'إن يسألكموها فيحفكم تبخلوا ويخرج أضغانكم', 26, 51, 510, 204, '47:37'),
(4583, NULL, NULL, 47, 38, 'هَٰٓأَنتُمْ هَٰٓؤُلَآءِ تُدْعَوْنَ لِتُنفِقُوا۟ فِى سَبِيلِ ٱللَّهِ فَمِنكُم مَّن يَبْخَلُ وَمَن يَبْخَلْ فَإِنَّمَا يَبْخَلُ عَن نَّفْسِهِۦ وَٱللَّهُ ٱلْغَنِىُّ وَأَنتُمُ ٱلْفُقَرَآءُ وَإِن تَتَوَلَّوْا۟ يَسْتَبْدِلْ قَوْمًا غَيْرَكُمْ ثُمَّ لَا يَكُونُوٓا۟ أَمْثَٰلَكُم', 'ها أنتم هؤلاء تدعون لتنفقوا في سبيل الله فمنكم من يبخل ومن يبخل فإنما يبخل عن نفسه والله الغني وأنتم الفقراء وإن تتولوا يستبدل قوما غيركم ثم لا يكونوا أمثالكم', 26, 51, 510, 204, '47:38'),
(4584, NULL, NULL, 48, 1, 'إِنَّا فَتَحْنَا لَكَ فَتْحًا مُّبِينًا', 'إنا فتحنا لك فتحا مبينا', 26, 51, 511, 204, '48:1'),
(4585, NULL, NULL, 48, 2, 'لِّيَغْفِرَ لَكَ ٱللَّهُ مَا تَقَدَّمَ مِن ذَنۢبِكَ وَمَا تَأَخَّرَ وَيُتِمَّ نِعْمَتَهُۥ عَلَيْكَ وَيَهْدِيَكَ صِرَٰطًا مُّسْتَقِيمًا', 'ليغفر لك الله ما تقدم من ذنبك وما تأخر ويتم نعمته عليك ويهديك صراطا مستقيما', 26, 51, 511, 204, '48:2'),
(4586, NULL, NULL, 48, 3, 'وَيَنصُرَكَ ٱللَّهُ نَصْرًا عَزِيزًا', 'وينصرك الله نصرا عزيزا', 26, 51, 511, 204, '48:3'),
(4587, NULL, NULL, 48, 4, 'هُوَ ٱلَّذِىٓ أَنزَلَ ٱلسَّكِينَةَ فِى قُلُوبِ ٱلْمُؤْمِنِينَ لِيَزْدَادُوٓا۟ إِيمَٰنًا مَّعَ إِيمَٰنِهِمْ وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَكَانَ ٱللَّهُ عَلِيمًا حَكِيمًا', 'هو الذي أنزل السكينة في قلوب المؤمنين ليزدادوا إيمانا مع إيمانهم ولله جنود السماوات والأرض وكان الله عليما حكيما', 26, 51, 511, 204, '48:4'),
(4588, NULL, NULL, 48, 5, 'لِّيُدْخِلَ ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَا وَيُكَفِّرَ عَنْهُمْ سَيِّـَٔاتِهِمْ وَكَانَ ذَٰلِكَ عِندَ ٱللَّهِ فَوْزًا عَظِيمًا', 'ليدخل المؤمنين والمؤمنات جنات تجري من تحتها الأنهار خالدين فيها ويكفر عنهم سيئاتهم وكان ذلك عند الله فوزا عظيما', 26, 51, 511, 204, '48:5'),
(4589, NULL, NULL, 48, 6, 'وَيُعَذِّبَ ٱلْمُنَٰفِقِينَ وَٱلْمُنَٰفِقَٰتِ وَٱلْمُشْرِكِينَ وَٱلْمُشْرِكَٰتِ ٱلظَّآنِّينَ بِٱللَّهِ ظَنَّ ٱلسَّوْءِ عَلَيْهِمْ دَآئِرَةُ ٱلسَّوْءِ وَغَضِبَ ٱللَّهُ عَلَيْهِمْ وَلَعَنَهُمْ وَأَعَدَّ لَهُمْ جَهَنَّمَ وَسَآءَتْ مَصِيرًا', 'ويعذب المنافقين والمنافقات والمشركين والمشركات الظانين بالله ظن السوء عليهم دائرة السوء وغضب الله عليهم ولعنهم وأعد لهم جهنم وساءت مصيرا', 26, 51, 511, 204, '48:6'),
(4590, NULL, NULL, 48, 7, 'وَلِلَّهِ جُنُودُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًا', 'ولله جنود السماوات والأرض وكان الله عزيزا حكيما', 26, 51, 511, 204, '48:7'),
(4591, NULL, NULL, 48, 8, 'إِنَّآ أَرْسَلْنَٰكَ شَٰهِدًا وَمُبَشِّرًا وَنَذِيرًا', 'إنا أرسلناك شاهدا ومبشرا ونذيرا', 26, 51, 511, 204, '48:8'),
(4592, NULL, NULL, 48, 9, 'لِّتُؤْمِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَتُعَزِّرُوهُ وَتُوَقِّرُوهُ وَتُسَبِّحُوهُ بُكْرَةً وَأَصِيلًا', 'لتؤمنوا بالله ورسوله وتعزروه وتوقروه وتسبحوه بكرة وأصيلا', 26, 51, 511, 204, '48:9'),
(4593, NULL, NULL, 48, 10, 'إِنَّ ٱلَّذِينَ يُبَايِعُونَكَ إِنَّمَا يُبَايِعُونَ ٱللَّهَ يَدُ ٱللَّهِ فَوْقَ أَيْدِيهِمْ فَمَن نَّكَثَ فَإِنَّمَا يَنكُثُ عَلَىٰ نَفْسِهِۦ وَمَنْ أَوْفَىٰ بِمَا عَٰهَدَ عَلَيْهُ ٱللَّهَ فَسَيُؤْتِيهِ أَجْرًا عَظِيمًا', 'إن الذين يبايعونك إنما يبايعون الله يد الله فوق أيديهم فمن نكث فإنما ينكث على نفسه ومن أوفى بما عاهد عليه الله فسيؤتيه أجرا عظيما', 26, 51, 512, 204, '48:10'),
(4594, NULL, NULL, 48, 11, 'سَيَقُولُ لَكَ ٱلْمُخَلَّفُونَ مِنَ ٱلْأَعْرَابِ شَغَلَتْنَآ أَمْوَٰلُنَا وَأَهْلُونَا فَٱسْتَغْفِرْ لَنَا يَقُولُونَ بِأَلْسِنَتِهِم مَّا لَيْسَ فِى قُلُوبِهِمْ قُلْ فَمَن يَمْلِكُ لَكُم مِّنَ ٱللَّهِ شَيْـًٔا إِنْ أَرَادَ بِكُمْ ضَرًّا أَوْ أَرَادَ بِكُمْ نَفْعًۢا بَلْ كَانَ ٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرًۢا', 'سيقول لك المخلفون من الأعراب شغلتنا أموالنا وأهلونا فاستغفر لنا يقولون بألسنتهم ما ليس في قلوبهم قل فمن يملك لكم من الله شيئا إن أراد بكم ضرا أو أراد بكم نفعا بل كان الله بما تعملون خبيرا', 26, 51, 512, 204, '48:11'),
(4595, NULL, NULL, 48, 12, 'بَلْ ظَنَنتُمْ أَن لَّن يَنقَلِبَ ٱلرَّسُولُ وَٱلْمُؤْمِنُونَ إِلَىٰٓ أَهْلِيهِمْ أَبَدًا وَزُيِّنَ ذَٰلِكَ فِى قُلُوبِكُمْ وَظَنَنتُمْ ظَنَّ ٱلسَّوْءِ وَكُنتُمْ قَوْمًۢا بُورًا', 'بل ظننتم أن لن ينقلب الرسول والمؤمنون إلى أهليهم أبدا وزين ذلك في قلوبكم وظننتم ظن السوء وكنتم قوما بورا', 26, 51, 512, 204, '48:12'),
(4596, NULL, NULL, 48, 13, 'وَمَن لَّمْ يُؤْمِنۢ بِٱللَّهِ وَرَسُولِهِۦ فَإِنَّآ أَعْتَدْنَا لِلْكَٰفِرِينَ سَعِيرًا', 'ومن لم يؤمن بالله ورسوله فإنا أعتدنا للكافرين سعيرا', 26, 51, 512, 204, '48:13'),
(4597, NULL, NULL, 48, 14, 'وَلِلَّهِ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ يَغْفِرُ لِمَن يَشَآءُ وَيُعَذِّبُ مَن يَشَآءُ وَكَانَ ٱللَّهُ غَفُورًا رَّحِيمًا', 'ولله ملك السماوات والأرض يغفر لمن يشاء ويعذب من يشاء وكان الله غفورا رحيما', 26, 51, 512, 204, '48:14'),
(4598, NULL, NULL, 48, 15, 'سَيَقُولُ ٱلْمُخَلَّفُونَ إِذَا ٱنطَلَقْتُمْ إِلَىٰ مَغَانِمَ لِتَأْخُذُوهَا ذَرُونَا نَتَّبِعْكُمْ يُرِيدُونَ أَن يُبَدِّلُوا۟ كَلَٰمَ ٱللَّهِ قُل لَّن تَتَّبِعُونَا كَذَٰلِكُمْ قَالَ ٱللَّهُ مِن قَبْلُ فَسَيَقُولُونَ بَلْ تَحْسُدُونَنَا بَلْ كَانُوا۟ لَا يَفْقَهُونَ إِلَّا قَلِيلًا', 'سيقول المخلفون إذا انطلقتم إلى مغانم لتأخذوها ذرونا نتبعكم يريدون أن يبدلوا كلام الله قل لن تتبعونا كذلكم قال الله من قبل فسيقولون بل تحسدوننا بل كانوا لا يفقهون إلا قليلا', 26, 51, 512, 204, '48:15'),
(4599, NULL, NULL, 48, 16, 'قُل لِّلْمُخَلَّفِينَ مِنَ ٱلْأَعْرَابِ سَتُدْعَوْنَ إِلَىٰ قَوْمٍ أُو۟لِى بَأْسٍ شَدِيدٍ تُقَٰتِلُونَهُمْ أَوْ يُسْلِمُونَ فَإِن تُطِيعُوا۟ يُؤْتِكُمُ ٱللَّهُ أَجْرًا حَسَنًا وَإِن تَتَوَلَّوْا۟ كَمَا تَوَلَّيْتُم مِّن قَبْلُ يُعَذِّبْكُمْ عَذَابًا أَلِيمًا', 'قل للمخلفين من الأعراب ستدعون إلى قوم أولي بأس شديد تقاتلونهم أو يسلمون فإن تطيعوا يؤتكم الله أجرا حسنا وإن تتولوا كما توليتم من قبل يعذبكم عذابا أليما', 26, 51, 513, 204, '48:16'),
(4600, NULL, NULL, 48, 17, 'لَّيْسَ عَلَى ٱلْأَعْمَىٰ حَرَجٌ وَلَا عَلَى ٱلْأَعْرَجِ حَرَجٌ وَلَا عَلَى ٱلْمَرِيضِ حَرَجٌ وَمَن يُطِعِ ٱللَّهَ وَرَسُولَهُۥ يُدْخِلْهُ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ وَمَن يَتَوَلَّ يُعَذِّبْهُ عَذَابًا أَلِيمًا', 'ليس على الأعمى حرج ولا على الأعرج حرج ولا على المريض حرج ومن يطع الله ورسوله يدخله جنات تجري من تحتها الأنهار ومن يتول يعذبه عذابا أليما', 26, 51, 513, 204, '48:17'),
(4601, NULL, NULL, 48, 18, 'لَّقَدْ رَضِىَ ٱللَّهُ عَنِ ٱلْمُؤْمِنِينَ إِذْ يُبَايِعُونَكَ تَحْتَ ٱلشَّجَرَةِ فَعَلِمَ مَا فِى قُلُوبِهِمْ فَأَنزَلَ ٱلسَّكِينَةَ عَلَيْهِمْ وَأَثَٰبَهُمْ فَتْحًا قَرِيبًا', 'لقد رضي الله عن المؤمنين إذ يبايعونك تحت الشجرة فعلم ما في قلوبهم فأنزل السكينة عليهم وأثابهم فتحا قريبا', 26, 52, 513, 205, '48:18'),
(4602, NULL, NULL, 48, 19, 'وَمَغَانِمَ كَثِيرَةً يَأْخُذُونَهَا وَكَانَ ٱللَّهُ عَزِيزًا حَكِيمًا', 'ومغانم كثيرة يأخذونها وكان الله عزيزا حكيما', 26, 52, 513, 205, '48:19'),
(4603, NULL, NULL, 48, 20, 'وَعَدَكُمُ ٱللَّهُ مَغَانِمَ كَثِيرَةً تَأْخُذُونَهَا فَعَجَّلَ لَكُمْ هَٰذِهِۦ وَكَفَّ أَيْدِىَ ٱلنَّاسِ عَنكُمْ وَلِتَكُونَ ءَايَةً لِّلْمُؤْمِنِينَ وَيَهْدِيَكُمْ صِرَٰطًا مُّسْتَقِيمًا', 'وعدكم الله مغانم كثيرة تأخذونها فعجل لكم هذه وكف أيدي الناس عنكم ولتكون آية للمؤمنين ويهديكم صراطا مستقيما', 26, 52, 513, 205, '48:20'),
(4604, NULL, NULL, 48, 21, 'وَأُخْرَىٰ لَمْ تَقْدِرُوا۟ عَلَيْهَا قَدْ أَحَاطَ ٱللَّهُ بِهَا وَكَانَ ٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ قَدِيرًا', 'وأخرى لم تقدروا عليها قد أحاط الله بها وكان الله على كل شيء قديرا', 26, 52, 513, 205, '48:21'),
(4605, NULL, NULL, 48, 22, 'وَلَوْ قَٰتَلَكُمُ ٱلَّذِينَ كَفَرُوا۟ لَوَلَّوُا۟ ٱلْأَدْبَٰرَ ثُمَّ لَا يَجِدُونَ وَلِيًّا وَلَا نَصِيرًا', 'ولو قاتلكم الذين كفروا لولوا الأدبار ثم لا يجدون وليا ولا نصيرا', 26, 52, 513, 205, '48:22'),
(4606, NULL, NULL, 48, 23, 'سُنَّةَ ٱللَّهِ ٱلَّتِى قَدْ خَلَتْ مِن قَبْلُ وَلَن تَجِدَ لِسُنَّةِ ٱللَّهِ تَبْدِيلًا', 'سنة الله التي قد خلت من قبل ولن تجد لسنة الله تبديلا', 26, 52, 513, 205, '48:23'),
(4607, NULL, NULL, 48, 24, 'وَهُوَ ٱلَّذِى كَفَّ أَيْدِيَهُمْ عَنكُمْ وَأَيْدِيَكُمْ عَنْهُم بِبَطْنِ مَكَّةَ مِنۢ بَعْدِ أَنْ أَظْفَرَكُمْ عَلَيْهِمْ وَكَانَ ٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرًا', 'وهو الذي كف أيديهم عنكم وأيديكم عنهم ببطن مكة من بعد أن أظفركم عليهم وكان الله بما تعملون بصيرا', 26, 52, 514, 205, '48:24'),
(4608, NULL, NULL, 48, 25, 'هُمُ ٱلَّذِينَ كَفَرُوا۟ وَصَدُّوكُمْ عَنِ ٱلْمَسْجِدِ ٱلْحَرَامِ وَٱلْهَدْىَ مَعْكُوفًا أَن يَبْلُغَ مَحِلَّهُۥ وَلَوْلَا رِجَالٌ مُّؤْمِنُونَ وَنِسَآءٌ مُّؤْمِنَٰتٌ لَّمْ تَعْلَمُوهُمْ أَن تَطَـُٔوهُمْ فَتُصِيبَكُم مِّنْهُم مَّعَرَّةٌۢ بِغَيْرِ عِلْمٍ لِّيُدْخِلَ ٱللَّهُ فِى رَحْمَتِهِۦ مَن يَشَآءُ لَوْ تَزَيَّلُوا۟ لَعَذَّبْنَا ٱلَّذِينَ كَفَرُوا۟ مِنْهُمْ عَذَابًا أَلِيمًا', 'هم الذين كفروا وصدوكم عن المسجد الحرام والهدي معكوفا أن يبلغ محله ولولا رجال مؤمنون ونساء مؤمنات لم تعلموهم أن تطئوهم فتصيبكم منهم معرة بغير علم ليدخل الله في رحمته من يشاء لو تزيلوا لعذبنا الذين كفروا منهم عذابا أليما', 26, 52, 514, 205, '48:25'),
(4609, NULL, NULL, 48, 26, 'إِذْ جَعَلَ ٱلَّذِينَ كَفَرُوا۟ فِى قُلُوبِهِمُ ٱلْحَمِيَّةَ حَمِيَّةَ ٱلْجَٰهِلِيَّةِ فَأَنزَلَ ٱللَّهُ سَكِينَتَهُۥ عَلَىٰ رَسُولِهِۦ وَعَلَى ٱلْمُؤْمِنِينَ وَأَلْزَمَهُمْ كَلِمَةَ ٱلتَّقْوَىٰ وَكَانُوٓا۟ أَحَقَّ بِهَا وَأَهْلَهَا وَكَانَ ٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمًا', 'إذ جعل الذين كفروا في قلوبهم الحمية حمية الجاهلية فأنزل الله سكينته على رسوله وعلى المؤمنين وألزمهم كلمة التقوى وكانوا أحق بها وأهلها وكان الله بكل شيء عليما', 26, 52, 514, 205, '48:26'),
(4610, NULL, NULL, 48, 27, 'لَّقَدْ صَدَقَ ٱللَّهُ رَسُولَهُ ٱلرُّءْيَا بِٱلْحَقِّ لَتَدْخُلُنَّ ٱلْمَسْجِدَ ٱلْحَرَامَ إِن شَآءَ ٱللَّهُ ءَامِنِينَ مُحَلِّقِينَ رُءُوسَكُمْ وَمُقَصِّرِينَ لَا تَخَافُونَ فَعَلِمَ مَا لَمْ تَعْلَمُوا۟ فَجَعَلَ مِن دُونِ ذَٰلِكَ فَتْحًا قَرِيبًا', 'لقد صدق الله رسوله الرؤيا بالحق لتدخلن المسجد الحرام إن شاء الله آمنين محلقين رءوسكم ومقصرين لا تخافون فعلم ما لم تعلموا فجعل من دون ذلك فتحا قريبا', 26, 52, 514, 205, '48:27'),
(4611, NULL, NULL, 48, 28, 'هُوَ ٱلَّذِىٓ أَرْسَلَ رَسُولَهُۥ بِٱلْهُدَىٰ وَدِينِ ٱلْحَقِّ لِيُظْهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَكَفَىٰ بِٱللَّهِ شَهِيدًا', 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله وكفى بالله شهيدا', 26, 52, 514, 205, '48:28'),
(4612, NULL, NULL, 48, 29, 'مُّحَمَّدٌ رَّسُولُ ٱللَّهِ وَٱلَّذِينَ مَعَهُۥٓ أَشِدَّآءُ عَلَى ٱلْكُفَّارِ رُحَمَآءُ بَيْنَهُمْ تَرَىٰهُمْ رُكَّعًا سُجَّدًا يَبْتَغُونَ فَضْلًا مِّنَ ٱللَّهِ وَرِضْوَٰنًا سِيمَاهُمْ فِى وُجُوهِهِم مِّنْ أَثَرِ ٱلسُّجُودِ ذَٰلِكَ مَثَلُهُمْ فِى ٱلتَّوْرَىٰةِ وَمَثَلُهُمْ فِى ٱلْإِنجِيلِ كَزَرْعٍ أَخْرَجَ شَطْـَٔهُۥ فَـَٔازَرَهُۥ فَٱسْتَغْلَظَ فَٱسْتَوَىٰ عَلَىٰ سُوقِهِۦ يُعْجِبُ ٱلزُّرَّاعَ لِيَغِيظَ بِهِمُ ٱلْكُفَّارَ وَعَدَ ٱللَّهُ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ مِنْهُم مَّغْفِرَةً وَأَجْرًا عَظِيمًۢا', 'محمد رسول الله والذين معه أشداء على الكفار رحماء بينهم تراهم ركعا سجدا يبتغون فضلا من الله ورضوانا سيماهم في وجوههم من أثر السجود ذلك مثلهم في التوراة ومثلهم في الإنجيل كزرع أخرج شطأه فآزره فاستغلظ فاستوى على سوقه يعجب الزراع ليغيظ بهم الكفار وعد الله الذين آمنوا وعملوا الصالحات منهم مغفرة وأجرا عظيما', 26, 52, 515, 205, '48:29'),
(4613, NULL, NULL, 49, 1, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تُقَدِّمُوا۟ بَيْنَ يَدَىِ ٱللَّهِ وَرَسُولِهِۦ وَٱتَّقُوا۟ ٱللَّهَ إِنَّ ٱللَّهَ سَمِيعٌ عَلِيمٌ', 'يا أيها الذين آمنوا لا تقدموا بين يدي الله ورسوله واتقوا الله إن الله سميع عليم', 26, 52, 515, 206, '49:1'),
(4614, NULL, NULL, 49, 2, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَرْفَعُوٓا۟ أَصْوَٰتَكُمْ فَوْقَ صَوْتِ ٱلنَّبِىِّ وَلَا تَجْهَرُوا۟ لَهُۥ بِٱلْقَوْلِ كَجَهْرِ بَعْضِكُمْ لِبَعْضٍ أَن تَحْبَطَ أَعْمَٰلُكُمْ وَأَنتُمْ لَا تَشْعُرُونَ', 'يا أيها الذين آمنوا لا ترفعوا أصواتكم فوق صوت النبي ولا تجهروا له بالقول كجهر بعضكم لبعض أن تحبط أعمالكم وأنتم لا تشعرون', 26, 52, 515, 206, '49:2'),
(4615, NULL, NULL, 49, 3, 'إِنَّ ٱلَّذِينَ يَغُضُّونَ أَصْوَٰتَهُمْ عِندَ رَسُولِ ٱللَّهِ أُو۟لَٰٓئِكَ ٱلَّذِينَ ٱمْتَحَنَ ٱللَّهُ قُلُوبَهُمْ لِلتَّقْوَىٰ لَهُم مَّغْفِرَةٌ وَأَجْرٌ عَظِيمٌ', 'إن الذين يغضون أصواتهم عند رسول الله أولئك الذين امتحن الله قلوبهم للتقوى لهم مغفرة وأجر عظيم', 26, 52, 515, 206, '49:3'),
(4616, NULL, NULL, 49, 4, 'إِنَّ ٱلَّذِينَ يُنَادُونَكَ مِن وَرَآءِ ٱلْحُجُرَٰتِ أَكْثَرُهُمْ لَا يَعْقِلُونَ', 'إن الذين ينادونك من وراء الحجرات أكثرهم لا يعقلون', 26, 52, 515, 206, '49:4'),
(4617, NULL, NULL, 49, 5, 'وَلَوْ أَنَّهُمْ صَبَرُوا۟ حَتَّىٰ تَخْرُجَ إِلَيْهِمْ لَكَانَ خَيْرًا لَّهُمْ وَٱللَّهُ غَفُورٌ رَّحِيمٌ', 'ولو أنهم صبروا حتى تخرج إليهم لكان خيرا لهم والله غفور رحيم', 26, 52, 516, 206, '49:5'),
(4618, NULL, NULL, 49, 6, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِن جَآءَكُمْ فَاسِقٌۢ بِنَبَإٍ فَتَبَيَّنُوٓا۟ أَن تُصِيبُوا۟ قَوْمًۢا بِجَهَٰلَةٍ فَتُصْبِحُوا۟ عَلَىٰ مَا فَعَلْتُمْ نَٰدِمِينَ', 'يا أيها الذين آمنوا إن جاءكم فاسق بنبإ فتبينوا أن تصيبوا قوما بجهالة فتصبحوا على ما فعلتم نادمين', 26, 52, 516, 206, '49:6'),
(4619, NULL, NULL, 49, 7, 'وَٱعْلَمُوٓا۟ أَنَّ فِيكُمْ رَسُولَ ٱللَّهِ لَوْ يُطِيعُكُمْ فِى كَثِيرٍ مِّنَ ٱلْأَمْرِ لَعَنِتُّمْ وَلَٰكِنَّ ٱللَّهَ حَبَّبَ إِلَيْكُمُ ٱلْإِيمَٰنَ وَزَيَّنَهُۥ فِى قُلُوبِكُمْ وَكَرَّهَ إِلَيْكُمُ ٱلْكُفْرَ وَٱلْفُسُوقَ وَٱلْعِصْيَانَ أُو۟لَٰٓئِكَ هُمُ ٱلرَّٰشِدُونَ', 'واعلموا أن فيكم رسول الله لو يطيعكم في كثير من الأمر لعنتم ولكن الله حبب إليكم الإيمان وزينه في قلوبكم وكره إليكم الكفر والفسوق والعصيان أولئك هم الراشدون', 26, 52, 516, 206, '49:7'),
(4620, NULL, NULL, 49, 8, 'فَضْلًا مِّنَ ٱللَّهِ وَنِعْمَةً وَٱللَّهُ عَلِيمٌ حَكِيمٌ', 'فضلا من الله ونعمة والله عليم حكيم', 26, 52, 516, 206, '49:8'),
(4621, NULL, NULL, 49, 9, 'وَإِن طَآئِفَتَانِ مِنَ ٱلْمُؤْمِنِينَ ٱقْتَتَلُوا۟ فَأَصْلِحُوا۟ بَيْنَهُمَا فَإِنۢ بَغَتْ إِحْدَىٰهُمَا عَلَى ٱلْأُخْرَىٰ فَقَٰتِلُوا۟ ٱلَّتِى تَبْغِى حَتَّىٰ تَفِىٓءَ إِلَىٰٓ أَمْرِ ٱللَّهِ فَإِن فَآءَتْ فَأَصْلِحُوا۟ بَيْنَهُمَا بِٱلْعَدْلِ وَأَقْسِطُوٓا۟ إِنَّ ٱللَّهَ يُحِبُّ ٱلْمُقْسِطِينَ', 'وإن طائفتان من المؤمنين اقتتلوا فأصلحوا بينهما فإن بغت إحداهما على الأخرى فقاتلوا التي تبغي حتى تفيء إلى أمر الله فإن فاءت فأصلحوا بينهما بالعدل وأقسطوا إن الله يحب المقسطين', 26, 52, 516, 206, '49:9'),
(4622, NULL, NULL, 49, 10, 'إِنَّمَا ٱلْمُؤْمِنُونَ إِخْوَةٌ فَأَصْلِحُوا۟ بَيْنَ أَخَوَيْكُمْ وَٱتَّقُوا۟ ٱللَّهَ لَعَلَّكُمْ تُرْحَمُونَ', 'إنما المؤمنون إخوة فأصلحوا بين أخويكم واتقوا الله لعلكم ترحمون', 26, 52, 516, 206, '49:10'),
(4623, NULL, NULL, 49, 11, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا يَسْخَرْ قَوْمٌ مِّن قَوْمٍ عَسَىٰٓ أَن يَكُونُوا۟ خَيْرًا مِّنْهُمْ وَلَا نِسَآءٌ مِّن نِّسَآءٍ عَسَىٰٓ أَن يَكُنَّ خَيْرًا مِّنْهُنَّ وَلَا تَلْمِزُوٓا۟ أَنفُسَكُمْ وَلَا تَنَابَزُوا۟ بِٱلْأَلْقَٰبِ بِئْسَ ٱلِٱسْمُ ٱلْفُسُوقُ بَعْدَ ٱلْإِيمَٰنِ وَمَن لَّمْ يَتُبْ فَأُو۟لَٰٓئِكَ هُمُ ٱلظَّٰلِمُونَ', 'يا أيها الذين آمنوا لا يسخر قوم من قوم عسى أن يكونوا خيرا منهم ولا نساء من نساء عسى أن يكن خيرا منهن ولا تلمزوا أنفسكم ولا تنابزوا بالألقاب بئس الاسم الفسوق بعد الإيمان ومن لم يتب فأولئك هم الظالمون', 26, 52, 516, 206, '49:11'),
(4624, NULL, NULL, 49, 12, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱجْتَنِبُوا۟ كَثِيرًا مِّنَ ٱلظَّنِّ إِنَّ بَعْضَ ٱلظَّنِّ إِثْمٌ وَلَا تَجَسَّسُوا۟ وَلَا يَغْتَب بَّعْضُكُم بَعْضًا أَيُحِبُّ أَحَدُكُمْ أَن يَأْكُلَ لَحْمَ أَخِيهِ مَيْتًا فَكَرِهْتُمُوهُ وَٱتَّقُوا۟ ٱللَّهَ إِنَّ ٱللَّهَ تَوَّابٌ رَّحِيمٌ', 'يا أيها الذين آمنوا اجتنبوا كثيرا من الظن إن بعض الظن إثم ولا تجسسوا ولا يغتب بعضكم بعضا أيحب أحدكم أن يأكل لحم أخيه ميتا فكرهتموه واتقوا الله إن الله تواب رحيم', 26, 52, 517, 206, '49:12'),
(4625, NULL, NULL, 49, 13, 'يَٰٓأَيُّهَا ٱلنَّاسُ إِنَّا خَلَقْنَٰكُم مِّن ذَكَرٍ وَأُنثَىٰ وَجَعَلْنَٰكُمْ شُعُوبًا وَقَبَآئِلَ لِتَعَارَفُوٓا۟ إِنَّ أَكْرَمَكُمْ عِندَ ٱللَّهِ أَتْقَىٰكُمْ إِنَّ ٱللَّهَ عَلِيمٌ خَبِيرٌ', 'يا أيها الناس إنا خلقناكم من ذكر وأنثى وجعلناكم شعوبا وقبائل لتعارفوا إن أكرمكم عند الله أتقاكم إن الله عليم خبير', 26, 52, 517, 206, '49:13'),
(4626, NULL, NULL, 49, 14, 'قَالَتِ ٱلْأَعْرَابُ ءَامَنَّا قُل لَّمْ تُؤْمِنُوا۟ وَلَٰكِن قُولُوٓا۟ أَسْلَمْنَا وَلَمَّا يَدْخُلِ ٱلْإِيمَٰنُ فِى قُلُوبِكُمْ وَإِن تُطِيعُوا۟ ٱللَّهَ وَرَسُولَهُۥ لَا يَلِتْكُم مِّنْ أَعْمَٰلِكُمْ شَيْـًٔا إِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ', 'قالت الأعراب آمنا قل لم تؤمنوا ولكن قولوا أسلمنا ولما يدخل الإيمان في قلوبكم وإن تطيعوا الله ورسوله لا يلتكم من أعمالكم شيئا إن الله غفور رحيم', 26, 52, 517, 207, '49:14'),
(4627, NULL, NULL, 49, 15, 'إِنَّمَا ٱلْمُؤْمِنُونَ ٱلَّذِينَ ءَامَنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ ثُمَّ لَمْ يَرْتَابُوا۟ وَجَٰهَدُوا۟ بِأَمْوَٰلِهِمْ وَأَنفُسِهِمْ فِى سَبِيلِ ٱللَّهِ أُو۟لَٰٓئِكَ هُمُ ٱلصَّٰدِقُونَ', 'إنما المؤمنون الذين آمنوا بالله ورسوله ثم لم يرتابوا وجاهدوا بأموالهم وأنفسهم في سبيل الله أولئك هم الصادقون', 26, 52, 517, 207, '49:15'),
(4628, NULL, NULL, 49, 16, 'قُلْ أَتُعَلِّمُونَ ٱللَّهَ بِدِينِكُمْ وَٱللَّهُ يَعْلَمُ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمٌ', 'قل أتعلمون الله بدينكم والله يعلم ما في السماوات وما في الأرض والله بكل شيء عليم', 26, 52, 517, 207, '49:16'),
(4629, NULL, NULL, 49, 17, 'يَمُنُّونَ عَلَيْكَ أَنْ أَسْلَمُوا۟ قُل لَّا تَمُنُّوا۟ عَلَىَّ إِسْلَٰمَكُم بَلِ ٱللَّهُ يَمُنُّ عَلَيْكُمْ أَنْ هَدَىٰكُمْ لِلْإِيمَٰنِ إِن كُنتُمْ صَٰدِقِينَ', 'يمنون عليك أن أسلموا قل لا تمنوا علي إسلامكم بل الله يمن عليكم أن هداكم للإيمان إن كنتم صادقين', 26, 52, 517, 207, '49:17'),
(4630, NULL, NULL, 49, 18, 'إِنَّ ٱللَّهَ يَعْلَمُ غَيْبَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَٱللَّهُ بَصِيرٌۢ بِمَا تَعْمَلُونَ', 'إن الله يعلم غيب السماوات والأرض والله بصير بما تعملون', 26, 52, 517, 207, '49:18'),
(4631, NULL, NULL, 50, 1, 'قٓ وَٱلْقُرْءَانِ ٱلْمَجِيدِ', 'ق والقرآن المجيد', 26, 52, 518, 207, '50:1'),
(4632, NULL, NULL, 50, 2, 'بَلْ عَجِبُوٓا۟ أَن جَآءَهُم مُّنذِرٌ مِّنْهُمْ فَقَالَ ٱلْكَٰفِرُونَ هَٰذَا شَىْءٌ عَجِيبٌ', 'بل عجبوا أن جاءهم منذر منهم فقال الكافرون هذا شيء عجيب', 26, 52, 518, 207, '50:2'),
(4633, NULL, NULL, 50, 3, 'أَءِذَا مِتْنَا وَكُنَّا تُرَابًا ذَٰلِكَ رَجْعٌۢ بَعِيدٌ', 'أإذا متنا وكنا ترابا ذلك رجع بعيد', 26, 52, 518, 207, '50:3'),
(4634, NULL, NULL, 50, 4, 'قَدْ عَلِمْنَا مَا تَنقُصُ ٱلْأَرْضُ مِنْهُمْ وَعِندَنَا كِتَٰبٌ حَفِيظٌۢ', 'قد علمنا ما تنقص الأرض منهم وعندنا كتاب حفيظ', 26, 52, 518, 207, '50:4'),
(4635, NULL, NULL, 50, 5, 'بَلْ كَذَّبُوا۟ بِٱلْحَقِّ لَمَّا جَآءَهُمْ فَهُمْ فِىٓ أَمْرٍ مَّرِيجٍ', 'بل كذبوا بالحق لما جاءهم فهم في أمر مريج', 26, 52, 518, 207, '50:5'),
(4636, NULL, NULL, 50, 6, 'أَفَلَمْ يَنظُرُوٓا۟ إِلَى ٱلسَّمَآءِ فَوْقَهُمْ كَيْفَ بَنَيْنَٰهَا وَزَيَّنَّٰهَا وَمَا لَهَا مِن فُرُوجٍ', 'أفلم ينظروا إلى السماء فوقهم كيف بنيناها وزيناها وما لها من فروج', 26, 52, 518, 207, '50:6'),
(4637, NULL, NULL, 50, 7, 'وَٱلْأَرْضَ مَدَدْنَٰهَا وَأَلْقَيْنَا فِيهَا رَوَٰسِىَ وَأَنۢبَتْنَا فِيهَا مِن كُلِّ زَوْجٍۭ بَهِيجٍ', 'والأرض مددناها وألقينا فيها رواسي وأنبتنا فيها من كل زوج بهيج', 26, 52, 518, 207, '50:7'),
(4638, NULL, NULL, 50, 8, 'تَبْصِرَةً وَذِكْرَىٰ لِكُلِّ عَبْدٍ مُّنِيبٍ', 'تبصرة وذكرى لكل عبد منيب', 26, 52, 518, 207, '50:8'),
(4639, NULL, NULL, 50, 9, 'وَنَزَّلْنَا مِنَ ٱلسَّمَآءِ مَآءً مُّبَٰرَكًا فَأَنۢبَتْنَا بِهِۦ جَنَّٰتٍ وَحَبَّ ٱلْحَصِيدِ', 'ونزلنا من السماء ماء مباركا فأنبتنا به جنات وحب الحصيد', 26, 52, 518, 207, '50:9'),
(4640, NULL, NULL, 50, 10, 'وَٱلنَّخْلَ بَاسِقَٰتٍ لَّهَا طَلْعٌ نَّضِيدٌ', 'والنخل باسقات لها طلع نضيد', 26, 52, 518, 207, '50:10'),
(4641, NULL, NULL, 50, 11, 'رِّزْقًا لِّلْعِبَادِ وَأَحْيَيْنَا بِهِۦ بَلْدَةً مَّيْتًا كَذَٰلِكَ ٱلْخُرُوجُ', 'رزقا للعباد وأحيينا به بلدة ميتا كذلك الخروج', 26, 52, 518, 207, '50:11'),
(4642, NULL, NULL, 50, 12, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ وَأَصْحَٰبُ ٱلرَّسِّ وَثَمُودُ', 'كذبت قبلهم قوم نوح وأصحاب الرس وثمود', 26, 52, 518, 207, '50:12'),
(4643, NULL, NULL, 50, 13, 'وَعَادٌ وَفِرْعَوْنُ وَإِخْوَٰنُ لُوطٍ', 'وعاد وفرعون وإخوان لوط', 26, 52, 518, 207, '50:13'),
(4644, NULL, NULL, 50, 14, 'وَأَصْحَٰبُ ٱلْأَيْكَةِ وَقَوْمُ تُبَّعٍ كُلٌّ كَذَّبَ ٱلرُّسُلَ فَحَقَّ وَعِيدِ', 'وأصحاب الأيكة وقوم تبع كل كذب الرسل فحق وعيد', 26, 52, 518, 207, '50:14'),
(4645, NULL, NULL, 50, 15, 'أَفَعَيِينَا بِٱلْخَلْقِ ٱلْأَوَّلِ بَلْ هُمْ فِى لَبْسٍ مِّنْ خَلْقٍ جَدِيدٍ', 'أفعيينا بالخلق الأول بل هم في لبس من خلق جديد', 26, 52, 518, 207, '50:15'),
(4646, NULL, NULL, 50, 16, 'وَلَقَدْ خَلَقْنَا ٱلْإِنسَٰنَ وَنَعْلَمُ مَا تُوَسْوِسُ بِهِۦ نَفْسُهُۥ وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنْ حَبْلِ ٱلْوَرِيدِ', 'ولقد خلقنا الإنسان ونعلم ما توسوس به نفسه ونحن أقرب إليه من حبل الوريد', 26, 52, 519, 207, '50:16'),
(4647, NULL, NULL, 50, 17, 'إِذْ يَتَلَقَّى ٱلْمُتَلَقِّيَانِ عَنِ ٱلْيَمِينِ وَعَنِ ٱلشِّمَالِ قَعِيدٌ', 'إذ يتلقى المتلقيان عن اليمين وعن الشمال قعيد', 26, 52, 519, 207, '50:17'),
(4648, NULL, NULL, 50, 18, 'مَّا يَلْفِظُ مِن قَوْلٍ إِلَّا لَدَيْهِ رَقِيبٌ عَتِيدٌ', 'ما يلفظ من قول إلا لديه رقيب عتيد', 26, 52, 519, 207, '50:18'),
(4649, NULL, NULL, 50, 19, 'وَجَآءَتْ سَكْرَةُ ٱلْمَوْتِ بِٱلْحَقِّ ذَٰلِكَ مَا كُنتَ مِنْهُ تَحِيدُ', 'وجاءت سكرة الموت بالحق ذلك ما كنت منه تحيد', 26, 52, 519, 207, '50:19'),
(4650, NULL, NULL, 50, 20, 'وَنُفِخَ فِى ٱلصُّورِ ذَٰلِكَ يَوْمُ ٱلْوَعِيدِ', 'ونفخ في الصور ذلك يوم الوعيد', 26, 52, 519, 207, '50:20'),
(4651, NULL, NULL, 50, 21, 'وَجَآءَتْ كُلُّ نَفْسٍ مَّعَهَا سَآئِقٌ وَشَهِيدٌ', 'وجاءت كل نفس معها سائق وشهيد', 26, 52, 519, 207, '50:21'),
(4652, NULL, NULL, 50, 22, 'لَّقَدْ كُنتَ فِى غَفْلَةٍ مِّنْ هَٰذَا فَكَشَفْنَا عَنكَ غِطَآءَكَ فَبَصَرُكَ ٱلْيَوْمَ حَدِيدٌ', 'لقد كنت في غفلة من هذا فكشفنا عنك غطاءك فبصرك اليوم حديد', 26, 52, 519, 207, '50:22'),
(4653, NULL, NULL, 50, 23, 'وَقَالَ قَرِينُهُۥ هَٰذَا مَا لَدَىَّ عَتِيدٌ', 'وقال قرينه هذا ما لدي عتيد', 26, 52, 519, 207, '50:23'),
(4654, NULL, NULL, 50, 24, 'أَلْقِيَا فِى جَهَنَّمَ كُلَّ كَفَّارٍ عَنِيدٍ', 'ألقيا في جهنم كل كفار عنيد', 26, 52, 519, 207, '50:24'),
(4655, NULL, NULL, 50, 25, 'مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ مُّرِيبٍ', 'مناع للخير معتد مريب', 26, 52, 519, 207, '50:25'),
(4656, NULL, NULL, 50, 26, 'ٱلَّذِى جَعَلَ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ فَأَلْقِيَاهُ فِى ٱلْعَذَابِ ٱلشَّدِيدِ', 'الذي جعل مع الله إلها آخر فألقياه في العذاب الشديد', 26, 52, 519, 207, '50:26'),
(4657, NULL, NULL, 50, 27, 'قَالَ قَرِينُهُۥ رَبَّنَا مَآ أَطْغَيْتُهُۥ وَلَٰكِن كَانَ فِى ضَلَٰلٍۭ بَعِيدٍ', 'قال قرينه ربنا ما أطغيته ولكن كان في ضلال بعيد', 26, 52, 519, 208, '50:27'),
(4658, NULL, NULL, 50, 28, 'قَالَ لَا تَخْتَصِمُوا۟ لَدَىَّ وَقَدْ قَدَّمْتُ إِلَيْكُم بِٱلْوَعِيدِ', 'قال لا تختصموا لدي وقد قدمت إليكم بالوعيد', 26, 52, 519, 208, '50:28'),
(4659, NULL, NULL, 50, 29, 'مَا يُبَدَّلُ ٱلْقَوْلُ لَدَىَّ وَمَآ أَنَا۠ بِظَلَّٰمٍ لِّلْعَبِيدِ', 'ما يبدل القول لدي وما أنا بظلام للعبيد', 26, 52, 519, 208, '50:29'),
(4660, NULL, NULL, 50, 30, 'يَوْمَ نَقُولُ لِجَهَنَّمَ هَلِ ٱمْتَلَأْتِ وَتَقُولُ هَلْ مِن مَّزِيدٍ', 'يوم نقول لجهنم هل امتلأت وتقول هل من مزيد', 26, 52, 519, 208, '50:30'),
(4661, NULL, NULL, 50, 31, 'وَأُزْلِفَتِ ٱلْجَنَّةُ لِلْمُتَّقِينَ غَيْرَ بَعِيدٍ', 'وأزلفت الجنة للمتقين غير بعيد', 26, 52, 519, 208, '50:31'),
(4662, NULL, NULL, 50, 32, 'هَٰذَا مَا تُوعَدُونَ لِكُلِّ أَوَّابٍ حَفِيظٍ', 'هذا ما توعدون لكل أواب حفيظ', 26, 52, 519, 208, '50:32'),
(4663, NULL, NULL, 50, 33, 'مَّنْ خَشِىَ ٱلرَّحْمَٰنَ بِٱلْغَيْبِ وَجَآءَ بِقَلْبٍ مُّنِيبٍ', 'من خشي الرحمن بالغيب وجاء بقلب منيب', 26, 52, 519, 208, '50:33'),
(4664, NULL, NULL, 50, 34, 'ٱدْخُلُوهَا بِسَلَٰمٍ ذَٰلِكَ يَوْمُ ٱلْخُلُودِ', 'ادخلوها بسلام ذلك يوم الخلود', 26, 52, 519, 208, '50:34'),
(4665, NULL, NULL, 50, 35, 'لَهُم مَّا يَشَآءُونَ فِيهَا وَلَدَيْنَا مَزِيدٌ', 'لهم ما يشاءون فيها ولدينا مزيد', 26, 52, 519, 208, '50:35'),
(4666, NULL, NULL, 50, 36, 'وَكَمْ أَهْلَكْنَا قَبْلَهُم مِّن قَرْنٍ هُمْ أَشَدُّ مِنْهُم بَطْشًا فَنَقَّبُوا۟ فِى ٱلْبِلَٰدِ هَلْ مِن مَّحِيصٍ', 'وكم أهلكنا قبلهم من قرن هم أشد منهم بطشا فنقبوا في البلاد هل من محيص', 26, 52, 520, 208, '50:36'),
(4667, NULL, NULL, 50, 37, 'إِنَّ فِى ذَٰلِكَ لَذِكْرَىٰ لِمَن كَانَ لَهُۥ قَلْبٌ أَوْ أَلْقَى ٱلسَّمْعَ وَهُوَ شَهِيدٌ', 'إن في ذلك لذكرى لمن كان له قلب أو ألقى السمع وهو شهيد', 26, 52, 520, 208, '50:37'),
(4668, NULL, NULL, 50, 38, 'وَلَقَدْ خَلَقْنَا ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ وَمَا بَيْنَهُمَا فِى سِتَّةِ أَيَّامٍ وَمَا مَسَّنَا مِن لُّغُوبٍ', 'ولقد خلقنا السماوات والأرض وما بينهما في ستة أيام وما مسنا من لغوب', 26, 52, 520, 208, '50:38'),
(4669, NULL, NULL, 50, 39, 'فَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَسَبِّحْ بِحَمْدِ رَبِّكَ قَبْلَ طُلُوعِ ٱلشَّمْسِ وَقَبْلَ ٱلْغُرُوبِ', 'فاصبر على ما يقولون وسبح بحمد ربك قبل طلوع الشمس وقبل الغروب', 26, 52, 520, 208, '50:39'),
(4670, NULL, NULL, 50, 40, 'وَمِنَ ٱلَّيْلِ فَسَبِّحْهُ وَأَدْبَٰرَ ٱلسُّجُودِ', 'ومن الليل فسبحه وأدبار السجود', 26, 52, 520, 208, '50:40'),
(4671, NULL, NULL, 50, 41, 'وَٱسْتَمِعْ يَوْمَ يُنَادِ ٱلْمُنَادِ مِن مَّكَانٍ قَرِيبٍ', 'واستمع يوم يناد المناد من مكان قريب', 26, 52, 520, 208, '50:41'),
(4672, NULL, NULL, 50, 42, 'يَوْمَ يَسْمَعُونَ ٱلصَّيْحَةَ بِٱلْحَقِّ ذَٰلِكَ يَوْمُ ٱلْخُرُوجِ', 'يوم يسمعون الصيحة بالحق ذلك يوم الخروج', 26, 52, 520, 208, '50:42'),
(4673, NULL, NULL, 50, 43, 'إِنَّا نَحْنُ نُحْىِۦ وَنُمِيتُ وَإِلَيْنَا ٱلْمَصِيرُ', 'إنا نحن نحيي ونميت وإلينا المصير', 26, 52, 520, 208, '50:43'),
(4674, NULL, NULL, 50, 44, 'يَوْمَ تَشَقَّقُ ٱلْأَرْضُ عَنْهُمْ سِرَاعًا ذَٰلِكَ حَشْرٌ عَلَيْنَا يَسِيرٌ', 'يوم تشقق الأرض عنهم سراعا ذلك حشر علينا يسير', 26, 52, 520, 208, '50:44'),
(4675, NULL, NULL, 50, 45, 'نَّحْنُ أَعْلَمُ بِمَا يَقُولُونَ وَمَآ أَنتَ عَلَيْهِم بِجَبَّارٍ فَذَكِّرْ بِٱلْقُرْءَانِ مَن يَخَافُ وَعِيدِ', 'نحن أعلم بما يقولون وما أنت عليهم بجبار فذكر بالقرآن من يخاف وعيد', 26, 52, 520, 208, '50:45'),
(4676, NULL, NULL, 51, 1, 'وَٱلذَّٰرِيَٰتِ ذَرْوًا', 'والذاريات ذروا', 26, 52, 520, 208, '51:1'),
(4677, NULL, NULL, 51, 2, 'فَٱلْحَٰمِلَٰتِ وِقْرًا', 'فالحاملات وقرا', 26, 52, 520, 208, '51:2'),
(4678, NULL, NULL, 51, 3, 'فَٱلْجَٰرِيَٰتِ يُسْرًا', 'فالجاريات يسرا', 26, 52, 520, 208, '51:3'),
(4679, NULL, NULL, 51, 4, 'فَٱلْمُقَسِّمَٰتِ أَمْرًا', 'فالمقسمات أمرا', 26, 52, 520, 208, '51:4'),
(4680, NULL, NULL, 51, 5, 'إِنَّمَا تُوعَدُونَ لَصَادِقٌ', 'إنما توعدون لصادق', 26, 52, 520, 208, '51:5'),
(4681, NULL, NULL, 51, 6, 'وَإِنَّ ٱلدِّينَ لَوَٰقِعٌ', 'وإن الدين لواقع', 26, 52, 520, 208, '51:6'),
(4682, NULL, NULL, 51, 7, 'وَٱلسَّمَآءِ ذَاتِ ٱلْحُبُكِ', 'والسماء ذات الحبك', 26, 52, 521, 208, '51:7'),
(4683, NULL, NULL, 51, 8, 'إِنَّكُمْ لَفِى قَوْلٍ مُّخْتَلِفٍ', 'إنكم لفي قول مختلف', 26, 52, 521, 208, '51:8'),
(4684, NULL, NULL, 51, 9, 'يُؤْفَكُ عَنْهُ مَنْ أُفِكَ', 'يؤفك عنه من أفك', 26, 52, 521, 208, '51:9'),
(4685, NULL, NULL, 51, 10, 'قُتِلَ ٱلْخَرَّٰصُونَ', 'قتل الخراصون', 26, 52, 521, 208, '51:10'),
(4686, NULL, NULL, 51, 11, 'ٱلَّذِينَ هُمْ فِى غَمْرَةٍ سَاهُونَ', 'الذين هم في غمرة ساهون', 26, 52, 521, 208, '51:11'),
(4687, NULL, NULL, 51, 12, 'يَسْـَٔلُونَ أَيَّانَ يَوْمُ ٱلدِّينِ', 'يسألون أيان يوم الدين', 26, 52, 521, 208, '51:12'),
(4688, NULL, NULL, 51, 13, 'يَوْمَ هُمْ عَلَى ٱلنَّارِ يُفْتَنُونَ', 'يوم هم على النار يفتنون', 26, 52, 521, 208, '51:13'),
(4689, NULL, NULL, 51, 14, 'ذُوقُوا۟ فِتْنَتَكُمْ هَٰذَا ٱلَّذِى كُنتُم بِهِۦ تَسْتَعْجِلُونَ', 'ذوقوا فتنتكم هذا الذي كنتم به تستعجلون', 26, 52, 521, 208, '51:14'),
(4690, NULL, NULL, 51, 15, 'إِنَّ ٱلْمُتَّقِينَ فِى جَنَّٰتٍ وَعُيُونٍ', 'إن المتقين في جنات وعيون', 26, 52, 521, 208, '51:15'),
(4691, NULL, NULL, 51, 16, 'ءَاخِذِينَ مَآ ءَاتَىٰهُمْ رَبُّهُمْ إِنَّهُمْ كَانُوا۟ قَبْلَ ذَٰلِكَ مُحْسِنِينَ', 'آخذين ما آتاهم ربهم إنهم كانوا قبل ذلك محسنين', 26, 52, 521, 208, '51:16'),
(4692, NULL, NULL, 51, 17, 'كَانُوا۟ قَلِيلًا مِّنَ ٱلَّيْلِ مَا يَهْجَعُونَ', 'كانوا قليلا من الليل ما يهجعون', 26, 52, 521, 208, '51:17'),
(4693, NULL, NULL, 51, 18, 'وَبِٱلْأَسْحَارِ هُمْ يَسْتَغْفِرُونَ', 'وبالأسحار هم يستغفرون', 26, 52, 521, 208, '51:18'),
(4694, NULL, NULL, 51, 19, 'وَفِىٓ أَمْوَٰلِهِمْ حَقٌّ لِّلسَّآئِلِ وَٱلْمَحْرُومِ', 'وفي أموالهم حق للسائل والمحروم', 26, 52, 521, 208, '51:19'),
(4695, NULL, NULL, 51, 20, 'وَفِى ٱلْأَرْضِ ءَايَٰتٌ لِّلْمُوقِنِينَ', 'وفي الأرض آيات للموقنين', 26, 52, 521, 208, '51:20'),
(4696, NULL, NULL, 51, 21, 'وَفِىٓ أَنفُسِكُمْ أَفَلَا تُبْصِرُونَ', 'وفي أنفسكم أفلا تبصرون', 26, 52, 521, 208, '51:21'),
(4697, NULL, NULL, 51, 22, 'وَفِى ٱلسَّمَآءِ رِزْقُكُمْ وَمَا تُوعَدُونَ', 'وفي السماء رزقكم وما توعدون', 26, 52, 521, 208, '51:22'),
(4698, NULL, NULL, 51, 23, 'فَوَرَبِّ ٱلسَّمَآءِ وَٱلْأَرْضِ إِنَّهُۥ لَحَقٌّ مِّثْلَ مَآ أَنَّكُمْ تَنطِقُونَ', 'فورب السماء والأرض إنه لحق مثل ما أنكم تنطقون', 26, 52, 521, 208, '51:23'),
(4699, NULL, NULL, 51, 24, 'هَلْ أَتَىٰكَ حَدِيثُ ضَيْفِ إِبْرَٰهِيمَ ٱلْمُكْرَمِينَ', 'هل أتاك حديث ضيف إبراهيم المكرمين', 26, 52, 521, 208, '51:24'),
(4700, NULL, NULL, 51, 25, 'إِذْ دَخَلُوا۟ عَلَيْهِ فَقَالُوا۟ سَلَٰمًا قَالَ سَلَٰمٌ قَوْمٌ مُّنكَرُونَ', 'إذ دخلوا عليه فقالوا سلاما قال سلام قوم منكرون', 26, 52, 521, 208, '51:25'),
(4701, NULL, NULL, 51, 26, 'فَرَاغَ إِلَىٰٓ أَهْلِهِۦ فَجَآءَ بِعِجْلٍ سَمِينٍ', 'فراغ إلى أهله فجاء بعجل سمين', 26, 52, 521, 208, '51:26'),
(4702, NULL, NULL, 51, 27, 'فَقَرَّبَهُۥٓ إِلَيْهِمْ قَالَ أَلَا تَأْكُلُونَ', 'فقربه إليهم قال ألا تأكلون', 26, 52, 521, 208, '51:27'),
(4703, NULL, NULL, 51, 28, 'فَأَوْجَسَ مِنْهُمْ خِيفَةً قَالُوا۟ لَا تَخَفْ وَبَشَّرُوهُ بِغُلَٰمٍ عَلِيمٍ', 'فأوجس منهم خيفة قالوا لا تخف وبشروه بغلام عليم', 26, 52, 521, 208, '51:28'),
(4704, NULL, NULL, 51, 29, 'فَأَقْبَلَتِ ٱمْرَأَتُهُۥ فِى صَرَّةٍ فَصَكَّتْ وَجْهَهَا وَقَالَتْ عَجُوزٌ عَقِيمٌ', 'فأقبلت امرأته في صرة فصكت وجهها وقالت عجوز عقيم', 26, 52, 521, 208, '51:29'),
(4705, NULL, NULL, 51, 30, 'قَالُوا۟ كَذَٰلِكِ قَالَ رَبُّكِ إِنَّهُۥ هُوَ ٱلْحَكِيمُ ٱلْعَلِيمُ', 'قالوا كذلك قال ربك إنه هو الحكيم العليم', 26, 52, 521, 208, '51:30'),
(4706, NULL, NULL, 51, 31, 'قَالَ فَمَا خَطْبُكُمْ أَيُّهَا ٱلْمُرْسَلُونَ', 'قال فما خطبكم أيها المرسلون', 27, 53, 522, 209, '51:31'),
(4707, NULL, NULL, 51, 32, 'قَالُوٓا۟ إِنَّآ أُرْسِلْنَآ إِلَىٰ قَوْمٍ مُّجْرِمِينَ', 'قالوا إنا أرسلنا إلى قوم مجرمين', 27, 53, 522, 209, '51:32'),
(4708, NULL, NULL, 51, 33, 'لِنُرْسِلَ عَلَيْهِمْ حِجَارَةً مِّن طِينٍ', 'لنرسل عليهم حجارة من طين', 27, 53, 522, 209, '51:33'),
(4709, NULL, NULL, 51, 34, 'مُّسَوَّمَةً عِندَ رَبِّكَ لِلْمُسْرِفِينَ', 'مسومة عند ربك للمسرفين', 27, 53, 522, 209, '51:34'),
(4710, NULL, NULL, 51, 35, 'فَأَخْرَجْنَا مَن كَانَ فِيهَا مِنَ ٱلْمُؤْمِنِينَ', 'فأخرجنا من كان فيها من المؤمنين', 27, 53, 522, 209, '51:35'),
(4711, NULL, NULL, 51, 36, 'فَمَا وَجَدْنَا فِيهَا غَيْرَ بَيْتٍ مِّنَ ٱلْمُسْلِمِينَ', 'فما وجدنا فيها غير بيت من المسلمين', 27, 53, 522, 209, '51:36'),
(4712, NULL, NULL, 51, 37, 'وَتَرَكْنَا فِيهَآ ءَايَةً لِّلَّذِينَ يَخَافُونَ ٱلْعَذَابَ ٱلْأَلِيمَ', 'وتركنا فيها آية للذين يخافون العذاب الأليم', 27, 53, 522, 209, '51:37'),
(4713, NULL, NULL, 51, 38, 'وَفِى مُوسَىٰٓ إِذْ أَرْسَلْنَٰهُ إِلَىٰ فِرْعَوْنَ بِسُلْطَٰنٍ مُّبِينٍ', 'وفي موسى إذ أرسلناه إلى فرعون بسلطان مبين', 27, 53, 522, 209, '51:38'),
(4714, NULL, NULL, 51, 39, 'فَتَوَلَّىٰ بِرُكْنِهِۦ وَقَالَ سَٰحِرٌ أَوْ مَجْنُونٌ', 'فتولى بركنه وقال ساحر أو مجنون', 27, 53, 522, 209, '51:39'),
(4715, NULL, NULL, 51, 40, 'فَأَخَذْنَٰهُ وَجُنُودَهُۥ فَنَبَذْنَٰهُمْ فِى ٱلْيَمِّ وَهُوَ مُلِيمٌ', 'فأخذناه وجنوده فنبذناهم في اليم وهو مليم', 27, 53, 522, 209, '51:40'),
(4716, NULL, NULL, 51, 41, 'وَفِى عَادٍ إِذْ أَرْسَلْنَا عَلَيْهِمُ ٱلرِّيحَ ٱلْعَقِيمَ', 'وفي عاد إذ أرسلنا عليهم الريح العقيم', 27, 53, 522, 209, '51:41'),
(4717, NULL, NULL, 51, 42, 'مَا تَذَرُ مِن شَىْءٍ أَتَتْ عَلَيْهِ إِلَّا جَعَلَتْهُ كَٱلرَّمِيمِ', 'ما تذر من شيء أتت عليه إلا جعلته كالرميم', 27, 53, 522, 209, '51:42'),
(4718, NULL, NULL, 51, 43, 'وَفِى ثَمُودَ إِذْ قِيلَ لَهُمْ تَمَتَّعُوا۟ حَتَّىٰ حِينٍ', 'وفي ثمود إذ قيل لهم تمتعوا حتى حين', 27, 53, 522, 209, '51:43'),
(4719, NULL, NULL, 51, 44, 'فَعَتَوْا۟ عَنْ أَمْرِ رَبِّهِمْ فَأَخَذَتْهُمُ ٱلصَّٰعِقَةُ وَهُمْ يَنظُرُونَ', 'فعتوا عن أمر ربهم فأخذتهم الصاعقة وهم ينظرون', 27, 53, 522, 209, '51:44'),
(4720, NULL, NULL, 51, 45, 'فَمَا ٱسْتَطَٰعُوا۟ مِن قِيَامٍ وَمَا كَانُوا۟ مُنتَصِرِينَ', 'فما استطاعوا من قيام وما كانوا منتصرين', 27, 53, 522, 209, '51:45'),
(4721, NULL, NULL, 51, 46, 'وَقَوْمَ نُوحٍ مِّن قَبْلُ إِنَّهُمْ كَانُوا۟ قَوْمًا فَٰسِقِينَ', 'وقوم نوح من قبل إنهم كانوا قوما فاسقين', 27, 53, 522, 209, '51:46'),
(4722, NULL, NULL, 51, 47, 'وَٱلسَّمَآءَ بَنَيْنَٰهَا بِأَيْي۟دٍ وَإِنَّا لَمُوسِعُونَ', 'والسماء بنيناها بأيد وإنا لموسعون', 27, 53, 522, 209, '51:47'),
(4723, NULL, NULL, 51, 48, 'وَٱلْأَرْضَ فَرَشْنَٰهَا فَنِعْمَ ٱلْمَٰهِدُونَ', 'والأرض فرشناها فنعم الماهدون', 27, 53, 522, 209, '51:48'),
(4724, NULL, NULL, 51, 49, 'وَمِن كُلِّ شَىْءٍ خَلَقْنَا زَوْجَيْنِ لَعَلَّكُمْ تَذَكَّرُونَ', 'ومن كل شيء خلقنا زوجين لعلكم تذكرون', 27, 53, 522, 209, '51:49'),
(4725, NULL, NULL, 51, 50, 'فَفِرُّوٓا۟ إِلَى ٱللَّهِ إِنِّى لَكُم مِّنْهُ نَذِيرٌ مُّبِينٌ', 'ففروا إلى الله إني لكم منه نذير مبين', 27, 53, 522, 209, '51:50'),
(4726, NULL, NULL, 51, 51, 'وَلَا تَجْعَلُوا۟ مَعَ ٱللَّهِ إِلَٰهًا ءَاخَرَ إِنِّى لَكُم مِّنْهُ نَذِيرٌ مُّبِينٌ', 'ولا تجعلوا مع الله إلها آخر إني لكم منه نذير مبين', 27, 53, 522, 209, '51:51'),
(4727, NULL, NULL, 51, 52, 'كَذَٰلِكَ مَآ أَتَى ٱلَّذِينَ مِن قَبْلِهِم مِّن رَّسُولٍ إِلَّا قَالُوا۟ سَاحِرٌ أَوْ مَجْنُونٌ', 'كذلك ما أتى الذين من قبلهم من رسول إلا قالوا ساحر أو مجنون', 27, 53, 523, 209, '51:52'),
(4728, NULL, NULL, 51, 53, 'أَتَوَاصَوْا۟ بِهِۦ بَلْ هُمْ قَوْمٌ طَاغُونَ', 'أتواصوا به بل هم قوم طاغون', 27, 53, 523, 209, '51:53'),
(4729, NULL, NULL, 51, 54, 'فَتَوَلَّ عَنْهُمْ فَمَآ أَنتَ بِمَلُومٍ', 'فتول عنهم فما أنت بملوم', 27, 53, 523, 209, '51:54'),
(4730, NULL, NULL, 51, 55, 'وَذَكِّرْ فَإِنَّ ٱلذِّكْرَىٰ تَنفَعُ ٱلْمُؤْمِنِينَ', 'وذكر فإن الذكرى تنفع المؤمنين', 27, 53, 523, 209, '51:55'),
(4731, NULL, NULL, 51, 56, 'وَمَا خَلَقْتُ ٱلْجِنَّ وَٱلْإِنسَ إِلَّا لِيَعْبُدُونِ', 'وما خلقت الجن والإنس إلا ليعبدون', 27, 53, 523, 209, '51:56'),
(4732, NULL, NULL, 51, 57, 'مَآ أُرِيدُ مِنْهُم مِّن رِّزْقٍ وَمَآ أُرِيدُ أَن يُطْعِمُونِ', 'ما أريد منهم من رزق وما أريد أن يطعمون', 27, 53, 523, 209, '51:57'),
(4733, NULL, NULL, 51, 58, 'إِنَّ ٱللَّهَ هُوَ ٱلرَّزَّاقُ ذُو ٱلْقُوَّةِ ٱلْمَتِينُ', 'إن الله هو الرزاق ذو القوة المتين', 27, 53, 523, 209, '51:58'),
(4734, NULL, NULL, 51, 59, 'فَإِنَّ لِلَّذِينَ ظَلَمُوا۟ ذَنُوبًا مِّثْلَ ذَنُوبِ أَصْحَٰبِهِمْ فَلَا يَسْتَعْجِلُونِ', 'فإن للذين ظلموا ذنوبا مثل ذنوب أصحابهم فلا يستعجلون', 27, 53, 523, 209, '51:59'),
(4735, NULL, NULL, 51, 60, 'فَوَيْلٌ لِّلَّذِينَ كَفَرُوا۟ مِن يَوْمِهِمُ ٱلَّذِى يُوعَدُونَ', 'فويل للذين كفروا من يومهم الذي يوعدون', 27, 53, 523, 209, '51:60'),
(4736, NULL, NULL, 52, 1, 'وَٱلطُّورِ', 'والطور', 27, 53, 523, 209, '52:1'),
(4737, NULL, NULL, 52, 2, 'وَكِتَٰبٍ مَّسْطُورٍ', 'وكتاب مسطور', 27, 53, 523, 209, '52:2'),
(4738, NULL, NULL, 52, 3, 'فِى رَقٍّ مَّنشُورٍ', 'في رق منشور', 27, 53, 523, 209, '52:3'),
(4739, NULL, NULL, 52, 4, 'وَٱلْبَيْتِ ٱلْمَعْمُورِ', 'والبيت المعمور', 27, 53, 523, 209, '52:4'),
(4740, NULL, NULL, 52, 5, 'وَٱلسَّقْفِ ٱلْمَرْفُوعِ', 'والسقف المرفوع', 27, 53, 523, 209, '52:5'),
(4741, NULL, NULL, 52, 6, 'وَٱلْبَحْرِ ٱلْمَسْجُورِ', 'والبحر المسجور', 27, 53, 523, 209, '52:6'),
(4742, NULL, NULL, 52, 7, 'إِنَّ عَذَابَ رَبِّكَ لَوَٰقِعٌ', 'إن عذاب ربك لواقع', 27, 53, 523, 209, '52:7'),
(4743, NULL, NULL, 52, 8, 'مَّا لَهُۥ مِن دَافِعٍ', 'ما له من دافع', 27, 53, 523, 209, '52:8'),
(4744, NULL, NULL, 52, 9, 'يَوْمَ تَمُورُ ٱلسَّمَآءُ مَوْرًا', 'يوم تمور السماء مورا', 27, 53, 523, 209, '52:9'),
(4745, NULL, NULL, 52, 10, 'وَتَسِيرُ ٱلْجِبَالُ سَيْرًا', 'وتسير الجبال سيرا', 27, 53, 523, 209, '52:10'),
(4746, NULL, NULL, 52, 11, 'فَوَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'فويل يومئذ للمكذبين', 27, 53, 523, 209, '52:11'),
(4747, NULL, NULL, 52, 12, 'ٱلَّذِينَ هُمْ فِى خَوْضٍ يَلْعَبُونَ', 'الذين هم في خوض يلعبون', 27, 53, 523, 209, '52:12'),
(4748, NULL, NULL, 52, 13, 'يَوْمَ يُدَعُّونَ إِلَىٰ نَارِ جَهَنَّمَ دَعًّا', 'يوم يدعون إلى نار جهنم دعا', 27, 53, 523, 209, '52:13'),
(4749, NULL, NULL, 52, 14, 'هَٰذِهِ ٱلنَّارُ ٱلَّتِى كُنتُم بِهَا تُكَذِّبُونَ', 'هذه النار التي كنتم بها تكذبون', 27, 53, 523, 209, '52:14'),
(4750, NULL, NULL, 52, 15, 'أَفَسِحْرٌ هَٰذَآ أَمْ أَنتُمْ لَا تُبْصِرُونَ', 'أفسحر هذا أم أنتم لا تبصرون', 27, 53, 524, 209, '52:15'),
(4751, NULL, NULL, 52, 16, 'ٱصْلَوْهَا فَٱصْبِرُوٓا۟ أَوْ لَا تَصْبِرُوا۟ سَوَآءٌ عَلَيْكُمْ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ', 'اصلوها فاصبروا أو لا تصبروا سواء عليكم إنما تجزون ما كنتم تعملون', 27, 53, 524, 209, '52:16'),
(4752, NULL, NULL, 52, 17, 'إِنَّ ٱلْمُتَّقِينَ فِى جَنَّٰتٍ وَنَعِيمٍ', 'إن المتقين في جنات ونعيم', 27, 53, 524, 209, '52:17'),
(4753, NULL, NULL, 52, 18, 'فَٰكِهِينَ بِمَآ ءَاتَىٰهُمْ رَبُّهُمْ وَوَقَىٰهُمْ رَبُّهُمْ عَذَابَ ٱلْجَحِيمِ', 'فاكهين بما آتاهم ربهم ووقاهم ربهم عذاب الجحيم', 27, 53, 524, 209, '52:18'),
(4754, NULL, NULL, 52, 19, 'كُلُوا۟ وَٱشْرَبُوا۟ هَنِيٓـًٔۢا بِمَا كُنتُمْ تَعْمَلُونَ', 'كلوا واشربوا هنيئا بما كنتم تعملون', 27, 53, 524, 209, '52:19'),
(4755, NULL, NULL, 52, 20, 'مُتَّكِـِٔينَ عَلَىٰ سُرُرٍ مَّصْفُوفَةٍ وَزَوَّجْنَٰهُم بِحُورٍ عِينٍ', 'متكئين على سرر مصفوفة وزوجناهم بحور عين', 27, 53, 524, 209, '52:20'),
(4756, NULL, NULL, 52, 21, 'وَٱلَّذِينَ ءَامَنُوا۟ وَٱتَّبَعَتْهُمْ ذُرِّيَّتُهُم بِإِيمَٰنٍ أَلْحَقْنَا بِهِمْ ذُرِّيَّتَهُمْ وَمَآ أَلَتْنَٰهُم مِّنْ عَمَلِهِم مِّن شَىْءٍ كُلُّ ٱمْرِئٍۭ بِمَا كَسَبَ رَهِينٌ', 'والذين آمنوا واتبعتهم ذريتهم بإيمان ألحقنا بهم ذريتهم وما ألتناهم من عملهم من شيء كل امرئ بما كسب رهين', 27, 53, 524, 209, '52:21'),
(4757, NULL, NULL, 52, 22, 'وَأَمْدَدْنَٰهُم بِفَٰكِهَةٍ وَلَحْمٍ مِّمَّا يَشْتَهُونَ', 'وأمددناهم بفاكهة ولحم مما يشتهون', 27, 53, 524, 209, '52:22'),
(4758, NULL, NULL, 52, 23, 'يَتَنَٰزَعُونَ فِيهَا كَأْسًا لَّا لَغْوٌ فِيهَا وَلَا تَأْثِيمٌ', 'يتنازعون فيها كأسا لا لغو فيها ولا تأثيم', 27, 53, 524, 209, '52:23'),
(4759, NULL, NULL, 52, 24, 'وَيَطُوفُ عَلَيْهِمْ غِلْمَانٌ لَّهُمْ كَأَنَّهُمْ لُؤْلُؤٌ مَّكْنُونٌ', 'ويطوف عليهم غلمان لهم كأنهم لؤلؤ مكنون', 27, 53, 524, 210, '52:24'),
(4760, NULL, NULL, 52, 25, 'وَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَسَآءَلُونَ', 'وأقبل بعضهم على بعض يتساءلون', 27, 53, 524, 210, '52:25');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(4761, NULL, NULL, 52, 26, 'قَالُوٓا۟ إِنَّا كُنَّا قَبْلُ فِىٓ أَهْلِنَا مُشْفِقِينَ', 'قالوا إنا كنا قبل في أهلنا مشفقين', 27, 53, 524, 210, '52:26'),
(4762, NULL, NULL, 52, 27, 'فَمَنَّ ٱللَّهُ عَلَيْنَا وَوَقَىٰنَا عَذَابَ ٱلسَّمُومِ', 'فمن الله علينا ووقانا عذاب السموم', 27, 53, 524, 210, '52:27'),
(4763, NULL, NULL, 52, 28, 'إِنَّا كُنَّا مِن قَبْلُ نَدْعُوهُ إِنَّهُۥ هُوَ ٱلْبَرُّ ٱلرَّحِيمُ', 'إنا كنا من قبل ندعوه إنه هو البر الرحيم', 27, 53, 524, 210, '52:28'),
(4764, NULL, NULL, 52, 29, 'فَذَكِّرْ فَمَآ أَنتَ بِنِعْمَتِ رَبِّكَ بِكَاهِنٍ وَلَا مَجْنُونٍ', 'فذكر فما أنت بنعمت ربك بكاهن ولا مجنون', 27, 53, 524, 210, '52:29'),
(4765, NULL, NULL, 52, 30, 'أَمْ يَقُولُونَ شَاعِرٌ نَّتَرَبَّصُ بِهِۦ رَيْبَ ٱلْمَنُونِ', 'أم يقولون شاعر نتربص به ريب المنون', 27, 53, 524, 210, '52:30'),
(4766, NULL, NULL, 52, 31, 'قُلْ تَرَبَّصُوا۟ فَإِنِّى مَعَكُم مِّنَ ٱلْمُتَرَبِّصِينَ', 'قل تربصوا فإني معكم من المتربصين', 27, 53, 524, 210, '52:31'),
(4767, NULL, NULL, 52, 32, 'أَمْ تَأْمُرُهُمْ أَحْلَٰمُهُم بِهَٰذَآ أَمْ هُمْ قَوْمٌ طَاغُونَ', 'أم تأمرهم أحلامهم بهذا أم هم قوم طاغون', 27, 53, 525, 210, '52:32'),
(4768, NULL, NULL, 52, 33, 'أَمْ يَقُولُونَ تَقَوَّلَهُۥ بَل لَّا يُؤْمِنُونَ', 'أم يقولون تقوله بل لا يؤمنون', 27, 53, 525, 210, '52:33'),
(4769, NULL, NULL, 52, 34, 'فَلْيَأْتُوا۟ بِحَدِيثٍ مِّثْلِهِۦٓ إِن كَانُوا۟ صَٰدِقِينَ', 'فليأتوا بحديث مثله إن كانوا صادقين', 27, 53, 525, 210, '52:34'),
(4770, NULL, NULL, 52, 35, 'أَمْ خُلِقُوا۟ مِنْ غَيْرِ شَىْءٍ أَمْ هُمُ ٱلْخَٰلِقُونَ', 'أم خلقوا من غير شيء أم هم الخالقون', 27, 53, 525, 210, '52:35'),
(4771, NULL, NULL, 52, 36, 'أَمْ خَلَقُوا۟ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ بَل لَّا يُوقِنُونَ', 'أم خلقوا السماوات والأرض بل لا يوقنون', 27, 53, 525, 210, '52:36'),
(4772, NULL, NULL, 52, 37, 'أَمْ عِندَهُمْ خَزَآئِنُ رَبِّكَ أَمْ هُمُ ٱلْمُصَۣيْطِرُونَ', 'أم عندهم خزائن ربك أم هم المصيطرون', 27, 53, 525, 210, '52:37'),
(4773, NULL, NULL, 52, 38, 'أَمْ لَهُمْ سُلَّمٌ يَسْتَمِعُونَ فِيهِ فَلْيَأْتِ مُسْتَمِعُهُم بِسُلْطَٰنٍ مُّبِينٍ', 'أم لهم سلم يستمعون فيه فليأت مستمعهم بسلطان مبين', 27, 53, 525, 210, '52:38'),
(4774, NULL, NULL, 52, 39, 'أَمْ لَهُ ٱلْبَنَٰتُ وَلَكُمُ ٱلْبَنُونَ', 'أم له البنات ولكم البنون', 27, 53, 525, 210, '52:39'),
(4775, NULL, NULL, 52, 40, 'أَمْ تَسْـَٔلُهُمْ أَجْرًا فَهُم مِّن مَّغْرَمٍ مُّثْقَلُونَ', 'أم تسألهم أجرا فهم من مغرم مثقلون', 27, 53, 525, 210, '52:40'),
(4776, NULL, NULL, 52, 41, 'أَمْ عِندَهُمُ ٱلْغَيْبُ فَهُمْ يَكْتُبُونَ', 'أم عندهم الغيب فهم يكتبون', 27, 53, 525, 210, '52:41'),
(4777, NULL, NULL, 52, 42, 'أَمْ يُرِيدُونَ كَيْدًا فَٱلَّذِينَ كَفَرُوا۟ هُمُ ٱلْمَكِيدُونَ', 'أم يريدون كيدا فالذين كفروا هم المكيدون', 27, 53, 525, 210, '52:42'),
(4778, NULL, NULL, 52, 43, 'أَمْ لَهُمْ إِلَٰهٌ غَيْرُ ٱللَّهِ سُبْحَٰنَ ٱللَّهِ عَمَّا يُشْرِكُونَ', 'أم لهم إله غير الله سبحان الله عما يشركون', 27, 53, 525, 210, '52:43'),
(4779, NULL, NULL, 52, 44, 'وَإِن يَرَوْا۟ كِسْفًا مِّنَ ٱلسَّمَآءِ سَاقِطًا يَقُولُوا۟ سَحَابٌ مَّرْكُومٌ', 'وإن يروا كسفا من السماء ساقطا يقولوا سحاب مركوم', 27, 53, 525, 210, '52:44'),
(4780, NULL, NULL, 52, 45, 'فَذَرْهُمْ حَتَّىٰ يُلَٰقُوا۟ يَوْمَهُمُ ٱلَّذِى فِيهِ يُصْعَقُونَ', 'فذرهم حتى يلاقوا يومهم الذي فيه يصعقون', 27, 53, 525, 210, '52:45'),
(4781, NULL, NULL, 52, 46, 'يَوْمَ لَا يُغْنِى عَنْهُمْ كَيْدُهُمْ شَيْـًٔا وَلَا هُمْ يُنصَرُونَ', 'يوم لا يغني عنهم كيدهم شيئا ولا هم ينصرون', 27, 53, 525, 210, '52:46'),
(4782, NULL, NULL, 52, 47, 'وَإِنَّ لِلَّذِينَ ظَلَمُوا۟ عَذَابًا دُونَ ذَٰلِكَ وَلَٰكِنَّ أَكْثَرَهُمْ لَا يَعْلَمُونَ', 'وإن للذين ظلموا عذابا دون ذلك ولكن أكثرهم لا يعلمون', 27, 53, 525, 210, '52:47'),
(4783, NULL, NULL, 52, 48, 'وَٱصْبِرْ لِحُكْمِ رَبِّكَ فَإِنَّكَ بِأَعْيُنِنَا وَسَبِّحْ بِحَمْدِ رَبِّكَ حِينَ تَقُومُ', 'واصبر لحكم ربك فإنك بأعيننا وسبح بحمد ربك حين تقوم', 27, 53, 525, 210, '52:48'),
(4784, NULL, NULL, 52, 49, 'وَمِنَ ٱلَّيْلِ فَسَبِّحْهُ وَإِدْبَٰرَ ٱلنُّجُومِ', 'ومن الليل فسبحه وإدبار النجوم', 27, 53, 525, 210, '52:49'),
(4785, NULL, NULL, 53, 1, 'وَٱلنَّجْمِ إِذَا هَوَىٰ', 'والنجم إذا هوى', 27, 53, 526, 210, '53:1'),
(4786, NULL, NULL, 53, 2, 'مَا ضَلَّ صَاحِبُكُمْ وَمَا غَوَىٰ', 'ما ضل صاحبكم وما غوى', 27, 53, 526, 210, '53:2'),
(4787, NULL, NULL, 53, 3, 'وَمَا يَنطِقُ عَنِ ٱلْهَوَىٰٓ', 'وما ينطق عن الهوى', 27, 53, 526, 210, '53:3'),
(4788, NULL, NULL, 53, 4, 'إِنْ هُوَ إِلَّا وَحْىٌ يُوحَىٰ', 'إن هو إلا وحي يوحى', 27, 53, 526, 210, '53:4'),
(4789, NULL, NULL, 53, 5, 'عَلَّمَهُۥ شَدِيدُ ٱلْقُوَىٰ', 'علمه شديد القوى', 27, 53, 526, 210, '53:5'),
(4790, NULL, NULL, 53, 6, 'ذُو مِرَّةٍ فَٱسْتَوَىٰ', 'ذو مرة فاستوى', 27, 53, 526, 210, '53:6'),
(4791, NULL, NULL, 53, 7, 'وَهُوَ بِٱلْأُفُقِ ٱلْأَعْلَىٰ', 'وهو بالأفق الأعلى', 27, 53, 526, 210, '53:7'),
(4792, NULL, NULL, 53, 8, 'ثُمَّ دَنَا فَتَدَلَّىٰ', 'ثم دنا فتدلى', 27, 53, 526, 210, '53:8'),
(4793, NULL, NULL, 53, 9, 'فَكَانَ قَابَ قَوْسَيْنِ أَوْ أَدْنَىٰ', 'فكان قاب قوسين أو أدنى', 27, 53, 526, 210, '53:9'),
(4794, NULL, NULL, 53, 10, 'فَأَوْحَىٰٓ إِلَىٰ عَبْدِهِۦ مَآ أَوْحَىٰ', 'فأوحى إلى عبده ما أوحى', 27, 53, 526, 210, '53:10'),
(4795, NULL, NULL, 53, 11, 'مَا كَذَبَ ٱلْفُؤَادُ مَا رَأَىٰٓ', 'ما كذب الفؤاد ما رأى', 27, 53, 526, 210, '53:11'),
(4796, NULL, NULL, 53, 12, 'أَفَتُمَٰرُونَهُۥ عَلَىٰ مَا يَرَىٰ', 'أفتمارونه على ما يرى', 27, 53, 526, 210, '53:12'),
(4797, NULL, NULL, 53, 13, 'وَلَقَدْ رَءَاهُ نَزْلَةً أُخْرَىٰ', 'ولقد رآه نزلة أخرى', 27, 53, 526, 210, '53:13'),
(4798, NULL, NULL, 53, 14, 'عِندَ سِدْرَةِ ٱلْمُنتَهَىٰ', 'عند سدرة المنتهى', 27, 53, 526, 210, '53:14'),
(4799, NULL, NULL, 53, 15, 'عِندَهَا جَنَّةُ ٱلْمَأْوَىٰٓ', 'عندها جنة المأوى', 27, 53, 526, 210, '53:15'),
(4800, NULL, NULL, 53, 16, 'إِذْ يَغْشَى ٱلسِّدْرَةَ مَا يَغْشَىٰ', 'إذ يغشى السدرة ما يغشى', 27, 53, 526, 210, '53:16'),
(4801, NULL, NULL, 53, 17, 'مَا زَاغَ ٱلْبَصَرُ وَمَا طَغَىٰ', 'ما زاغ البصر وما طغى', 27, 53, 526, 210, '53:17'),
(4802, NULL, NULL, 53, 18, 'لَقَدْ رَأَىٰ مِنْ ءَايَٰتِ رَبِّهِ ٱلْكُبْرَىٰٓ', 'لقد رأى من آيات ربه الكبرى', 27, 53, 526, 210, '53:18'),
(4803, NULL, NULL, 53, 19, 'أَفَرَءَيْتُمُ ٱللَّٰتَ وَٱلْعُزَّىٰ', 'أفرأيتم اللات والعزى', 27, 53, 526, 210, '53:19'),
(4804, NULL, NULL, 53, 20, 'وَمَنَوٰةَ ٱلثَّالِثَةَ ٱلْأُخْرَىٰٓ', 'ومناة الثالثة الأخرى', 27, 53, 526, 210, '53:20'),
(4805, NULL, NULL, 53, 21, 'أَلَكُمُ ٱلذَّكَرُ وَلَهُ ٱلْأُنثَىٰ', 'ألكم الذكر وله الأنثى', 27, 53, 526, 210, '53:21'),
(4806, NULL, NULL, 53, 22, 'تِلْكَ إِذًا قِسْمَةٌ ضِيزَىٰٓ', 'تلك إذا قسمة ضيزى', 27, 53, 526, 210, '53:22'),
(4807, NULL, NULL, 53, 23, 'إِنْ هِىَ إِلَّآ أَسْمَآءٌ سَمَّيْتُمُوهَآ أَنتُمْ وَءَابَآؤُكُم مَّآ أَنزَلَ ٱللَّهُ بِهَا مِن سُلْطَٰنٍ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَمَا تَهْوَى ٱلْأَنفُسُ وَلَقَدْ جَآءَهُم مِّن رَّبِّهِمُ ٱلْهُدَىٰٓ', 'إن هي إلا أسماء سميتموها أنتم وآباؤكم ما أنزل الله بها من سلطان إن يتبعون إلا الظن وما تهوى الأنفس ولقد جاءهم من ربهم الهدى', 27, 53, 526, 210, '53:23'),
(4808, NULL, NULL, 53, 24, 'أَمْ لِلْإِنسَٰنِ مَا تَمَنَّىٰ', 'أم للإنسان ما تمنى', 27, 53, 526, 210, '53:24'),
(4809, NULL, NULL, 53, 25, 'فَلِلَّهِ ٱلْءَاخِرَةُ وَٱلْأُولَىٰ', 'فلله الآخرة والأولى', 27, 53, 526, 210, '53:25'),
(4810, NULL, NULL, 53, 26, 'وَكَم مِّن مَّلَكٍ فِى ٱلسَّمَٰوَٰتِ لَا تُغْنِى شَفَٰعَتُهُمْ شَيْـًٔا إِلَّا مِنۢ بَعْدِ أَن يَأْذَنَ ٱللَّهُ لِمَن يَشَآءُ وَيَرْضَىٰٓ', 'وكم من ملك في السماوات لا تغني شفاعتهم شيئا إلا من بعد أن يأذن الله لمن يشاء ويرضى', 27, 53, 526, 211, '53:26'),
(4811, NULL, NULL, 53, 27, 'إِنَّ ٱلَّذِينَ لَا يُؤْمِنُونَ بِٱلْءَاخِرَةِ لَيُسَمُّونَ ٱلْمَلَٰٓئِكَةَ تَسْمِيَةَ ٱلْأُنثَىٰ', 'إن الذين لا يؤمنون بالآخرة ليسمون الملائكة تسمية الأنثى', 27, 53, 527, 211, '53:27'),
(4812, NULL, NULL, 53, 28, 'وَمَا لَهُم بِهِۦ مِنْ عِلْمٍ إِن يَتَّبِعُونَ إِلَّا ٱلظَّنَّ وَإِنَّ ٱلظَّنَّ لَا يُغْنِى مِنَ ٱلْحَقِّ شَيْـًٔا', 'وما لهم به من علم إن يتبعون إلا الظن وإن الظن لا يغني من الحق شيئا', 27, 53, 527, 211, '53:28'),
(4813, NULL, NULL, 53, 29, 'فَأَعْرِضْ عَن مَّن تَوَلَّىٰ عَن ذِكْرِنَا وَلَمْ يُرِدْ إِلَّا ٱلْحَيَوٰةَ ٱلدُّنْيَا', 'فأعرض عن من تولى عن ذكرنا ولم يرد إلا الحياة الدنيا', 27, 53, 527, 211, '53:29'),
(4814, NULL, NULL, 53, 30, 'ذَٰلِكَ مَبْلَغُهُم مِّنَ ٱلْعِلْمِ إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعْلَمُ بِمَنِ ٱهْتَدَىٰ', 'ذلك مبلغهم من العلم إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بمن اهتدى', 27, 53, 527, 211, '53:30'),
(4815, NULL, NULL, 53, 31, 'وَلِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ لِيَجْزِىَ ٱلَّذِينَ أَسَٰٓـُٔوا۟ بِمَا عَمِلُوا۟ وَيَجْزِىَ ٱلَّذِينَ أَحْسَنُوا۟ بِٱلْحُسْنَى', 'ولله ما في السماوات وما في الأرض ليجزي الذين أساءوا بما عملوا ويجزي الذين أحسنوا بالحسنى', 27, 53, 527, 211, '53:31'),
(4816, NULL, NULL, 53, 32, 'ٱلَّذِينَ يَجْتَنِبُونَ كَبَٰٓئِرَ ٱلْإِثْمِ وَٱلْفَوَٰحِشَ إِلَّا ٱللَّمَمَ إِنَّ رَبَّكَ وَٰسِعُ ٱلْمَغْفِرَةِ هُوَ أَعْلَمُ بِكُمْ إِذْ أَنشَأَكُم مِّنَ ٱلْأَرْضِ وَإِذْ أَنتُمْ أَجِنَّةٌ فِى بُطُونِ أُمَّهَٰتِكُمْ فَلَا تُزَكُّوٓا۟ أَنفُسَكُمْ هُوَ أَعْلَمُ بِمَنِ ٱتَّقَىٰٓ', 'الذين يجتنبون كبائر الإثم والفواحش إلا اللمم إن ربك واسع المغفرة هو أعلم بكم إذ أنشأكم من الأرض وإذ أنتم أجنة في بطون أمهاتكم فلا تزكوا أنفسكم هو أعلم بمن اتقى', 27, 53, 527, 211, '53:32'),
(4817, NULL, NULL, 53, 33, 'أَفَرَءَيْتَ ٱلَّذِى تَوَلَّىٰ', 'أفرأيت الذي تولى', 27, 53, 527, 211, '53:33'),
(4818, NULL, NULL, 53, 34, 'وَأَعْطَىٰ قَلِيلًا وَأَكْدَىٰٓ', 'وأعطى قليلا وأكدى', 27, 53, 527, 211, '53:34'),
(4819, NULL, NULL, 53, 35, 'أَعِندَهُۥ عِلْمُ ٱلْغَيْبِ فَهُوَ يَرَىٰٓ', 'أعنده علم الغيب فهو يرى', 27, 53, 527, 211, '53:35'),
(4820, NULL, NULL, 53, 36, 'أَمْ لَمْ يُنَبَّأْ بِمَا فِى صُحُفِ مُوسَىٰ', 'أم لم ينبأ بما في صحف موسى', 27, 53, 527, 211, '53:36'),
(4821, NULL, NULL, 53, 37, 'وَإِبْرَٰهِيمَ ٱلَّذِى وَفَّىٰٓ', 'وإبراهيم الذي وفى', 27, 53, 527, 211, '53:37'),
(4822, NULL, NULL, 53, 38, 'أَلَّا تَزِرُ وَازِرَةٌ وِزْرَ أُخْرَىٰ', 'ألا تزر وازرة وزر أخرى', 27, 53, 527, 211, '53:38'),
(4823, NULL, NULL, 53, 39, 'وَأَن لَّيْسَ لِلْإِنسَٰنِ إِلَّا مَا سَعَىٰ', 'وأن ليس للإنسان إلا ما سعى', 27, 53, 527, 211, '53:39'),
(4824, NULL, NULL, 53, 40, 'وَأَنَّ سَعْيَهُۥ سَوْفَ يُرَىٰ', 'وأن سعيه سوف يرى', 27, 53, 527, 211, '53:40'),
(4825, NULL, NULL, 53, 41, 'ثُمَّ يُجْزَىٰهُ ٱلْجَزَآءَ ٱلْأَوْفَىٰ', 'ثم يجزاه الجزاء الأوفى', 27, 53, 527, 211, '53:41'),
(4826, NULL, NULL, 53, 42, 'وَأَنَّ إِلَىٰ رَبِّكَ ٱلْمُنتَهَىٰ', 'وأن إلى ربك المنتهى', 27, 53, 527, 211, '53:42'),
(4827, NULL, NULL, 53, 43, 'وَأَنَّهُۥ هُوَ أَضْحَكَ وَأَبْكَىٰ', 'وأنه هو أضحك وأبكى', 27, 53, 527, 211, '53:43'),
(4828, NULL, NULL, 53, 44, 'وَأَنَّهُۥ هُوَ أَمَاتَ وَأَحْيَا', 'وأنه هو أمات وأحيا', 27, 53, 527, 211, '53:44'),
(4829, NULL, NULL, 53, 45, 'وَأَنَّهُۥ خَلَقَ ٱلزَّوْجَيْنِ ٱلذَّكَرَ وَٱلْأُنثَىٰ', 'وأنه خلق الزوجين الذكر والأنثى', 27, 53, 528, 211, '53:45'),
(4830, NULL, NULL, 53, 46, 'مِن نُّطْفَةٍ إِذَا تُمْنَىٰ', 'من نطفة إذا تمنى', 27, 53, 528, 211, '53:46'),
(4831, NULL, NULL, 53, 47, 'وَأَنَّ عَلَيْهِ ٱلنَّشْأَةَ ٱلْأُخْرَىٰ', 'وأن عليه النشأة الأخرى', 27, 53, 528, 211, '53:47'),
(4832, NULL, NULL, 53, 48, 'وَأَنَّهُۥ هُوَ أَغْنَىٰ وَأَقْنَىٰ', 'وأنه هو أغنى وأقنى', 27, 53, 528, 211, '53:48'),
(4833, NULL, NULL, 53, 49, 'وَأَنَّهُۥ هُوَ رَبُّ ٱلشِّعْرَىٰ', 'وأنه هو رب الشعرى', 27, 53, 528, 211, '53:49'),
(4834, NULL, NULL, 53, 50, 'وَأَنَّهُۥٓ أَهْلَكَ عَادًا ٱلْأُولَىٰ', 'وأنه أهلك عادا الأولى', 27, 53, 528, 211, '53:50'),
(4835, NULL, NULL, 53, 51, 'وَثَمُودَا۟ فَمَآ أَبْقَىٰ', 'وثمود فما أبقى', 27, 53, 528, 211, '53:51'),
(4836, NULL, NULL, 53, 52, 'وَقَوْمَ نُوحٍ مِّن قَبْلُ إِنَّهُمْ كَانُوا۟ هُمْ أَظْلَمَ وَأَطْغَىٰ', 'وقوم نوح من قبل إنهم كانوا هم أظلم وأطغى', 27, 53, 528, 211, '53:52'),
(4837, NULL, NULL, 53, 53, 'وَٱلْمُؤْتَفِكَةَ أَهْوَىٰ', 'والمؤتفكة أهوى', 27, 53, 528, 211, '53:53'),
(4838, NULL, NULL, 53, 54, 'فَغَشَّىٰهَا مَا غَشَّىٰ', 'فغشاها ما غشى', 27, 53, 528, 211, '53:54'),
(4839, NULL, NULL, 53, 55, 'فَبِأَىِّ ءَالَآءِ رَبِّكَ تَتَمَارَىٰ', 'فبأي آلاء ربك تتمارى', 27, 53, 528, 211, '53:55'),
(4840, NULL, NULL, 53, 56, 'هَٰذَا نَذِيرٌ مِّنَ ٱلنُّذُرِ ٱلْأُولَىٰٓ', 'هذا نذير من النذر الأولى', 27, 53, 528, 211, '53:56'),
(4841, NULL, NULL, 53, 57, 'أَزِفَتِ ٱلْءَازِفَةُ', 'أزفت الآزفة', 27, 53, 528, 211, '53:57'),
(4842, NULL, NULL, 53, 58, 'لَيْسَ لَهَا مِن دُونِ ٱللَّهِ كَاشِفَةٌ', 'ليس لها من دون الله كاشفة', 27, 53, 528, 211, '53:58'),
(4843, NULL, NULL, 53, 59, 'أَفَمِنْ هَٰذَا ٱلْحَدِيثِ تَعْجَبُونَ', 'أفمن هذا الحديث تعجبون', 27, 53, 528, 211, '53:59'),
(4844, NULL, NULL, 53, 60, 'وَتَضْحَكُونَ وَلَا تَبْكُونَ', 'وتضحكون ولا تبكون', 27, 53, 528, 211, '53:60'),
(4845, NULL, NULL, 53, 61, 'وَأَنتُمْ سَٰمِدُونَ', 'وأنتم سامدون', 27, 53, 528, 211, '53:61'),
(4846, NULL, NULL, 53, 62, 'فَٱسْجُدُوا۟ لِلَّهِ وَٱعْبُدُوا۟', 'فاسجدوا لله واعبدوا', 27, 53, 528, 211, '53:62'),
(4847, NULL, NULL, 54, 1, 'ٱقْتَرَبَتِ ٱلسَّاعَةُ وَٱنشَقَّ ٱلْقَمَرُ', 'اقتربت الساعة وانشق القمر', 27, 53, 528, 211, '54:1'),
(4848, NULL, NULL, 54, 2, 'وَإِن يَرَوْا۟ ءَايَةً يُعْرِضُوا۟ وَيَقُولُوا۟ سِحْرٌ مُّسْتَمِرٌّ', 'وإن يروا آية يعرضوا ويقولوا سحر مستمر', 27, 53, 528, 211, '54:2'),
(4849, NULL, NULL, 54, 3, 'وَكَذَّبُوا۟ وَٱتَّبَعُوٓا۟ أَهْوَآءَهُمْ وَكُلُّ أَمْرٍ مُّسْتَقِرٌّ', 'وكذبوا واتبعوا أهواءهم وكل أمر مستقر', 27, 53, 528, 211, '54:3'),
(4850, NULL, NULL, 54, 4, 'وَلَقَدْ جَآءَهُم مِّنَ ٱلْأَنۢبَآءِ مَا فِيهِ مُزْدَجَرٌ', 'ولقد جاءهم من الأنباء ما فيه مزدجر', 27, 53, 528, 211, '54:4'),
(4851, NULL, NULL, 54, 5, 'حِكْمَةٌۢ بَٰلِغَةٌ فَمَا تُغْنِ ٱلنُّذُرُ', 'حكمة بالغة فما تغن النذر', 27, 53, 528, 211, '54:5'),
(4852, NULL, NULL, 54, 6, 'فَتَوَلَّ عَنْهُمْ يَوْمَ يَدْعُ ٱلدَّاعِ إِلَىٰ شَىْءٍ نُّكُرٍ', 'فتول عنهم يوم يدع الداع إلى شيء نكر', 27, 53, 528, 211, '54:6'),
(4853, NULL, NULL, 54, 7, 'خُشَّعًا أَبْصَٰرُهُمْ يَخْرُجُونَ مِنَ ٱلْأَجْدَاثِ كَأَنَّهُمْ جَرَادٌ مُّنتَشِرٌ', 'خشعا أبصارهم يخرجون من الأجداث كأنهم جراد منتشر', 27, 53, 529, 211, '54:7'),
(4854, NULL, NULL, 54, 8, 'مُّهْطِعِينَ إِلَى ٱلدَّاعِ يَقُولُ ٱلْكَٰفِرُونَ هَٰذَا يَوْمٌ عَسِرٌ', 'مهطعين إلى الداع يقول الكافرون هذا يوم عسر', 27, 53, 529, 211, '54:8'),
(4855, NULL, NULL, 54, 9, 'كَذَّبَتْ قَبْلَهُمْ قَوْمُ نُوحٍ فَكَذَّبُوا۟ عَبْدَنَا وَقَالُوا۟ مَجْنُونٌ وَٱزْدُجِرَ', 'كذبت قبلهم قوم نوح فكذبوا عبدنا وقالوا مجنون وازدجر', 27, 53, 529, 212, '54:9'),
(4856, NULL, NULL, 54, 10, 'فَدَعَا رَبَّهُۥٓ أَنِّى مَغْلُوبٌ فَٱنتَصِرْ', 'فدعا ربه أني مغلوب فانتصر', 27, 53, 529, 212, '54:10'),
(4857, NULL, NULL, 54, 11, 'فَفَتَحْنَآ أَبْوَٰبَ ٱلسَّمَآءِ بِمَآءٍ مُّنْهَمِرٍ', 'ففتحنا أبواب السماء بماء منهمر', 27, 53, 529, 212, '54:11'),
(4858, NULL, NULL, 54, 12, 'وَفَجَّرْنَا ٱلْأَرْضَ عُيُونًا فَٱلْتَقَى ٱلْمَآءُ عَلَىٰٓ أَمْرٍ قَدْ قُدِرَ', 'وفجرنا الأرض عيونا فالتقى الماء على أمر قد قدر', 27, 53, 529, 212, '54:12'),
(4859, NULL, NULL, 54, 13, 'وَحَمَلْنَٰهُ عَلَىٰ ذَاتِ أَلْوَٰحٍ وَدُسُرٍ', 'وحملناه على ذات ألواح ودسر', 27, 53, 529, 212, '54:13'),
(4860, NULL, NULL, 54, 14, 'تَجْرِى بِأَعْيُنِنَا جَزَآءً لِّمَن كَانَ كُفِرَ', 'تجري بأعيننا جزاء لمن كان كفر', 27, 53, 529, 212, '54:14'),
(4861, NULL, NULL, 54, 15, 'وَلَقَد تَّرَكْنَٰهَآ ءَايَةً فَهَلْ مِن مُّدَّكِرٍ', 'ولقد تركناها آية فهل من مدكر', 27, 53, 529, 212, '54:15'),
(4862, NULL, NULL, 54, 16, 'فَكَيْفَ كَانَ عَذَابِى وَنُذُرِ', 'فكيف كان عذابي ونذر', 27, 53, 529, 212, '54:16'),
(4863, NULL, NULL, 54, 17, 'وَلَقَدْ يَسَّرْنَا ٱلْقُرْءَانَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ', 'ولقد يسرنا القرآن للذكر فهل من مدكر', 27, 53, 529, 212, '54:17'),
(4864, NULL, NULL, 54, 18, 'كَذَّبَتْ عَادٌ فَكَيْفَ كَانَ عَذَابِى وَنُذُرِ', 'كذبت عاد فكيف كان عذابي ونذر', 27, 53, 529, 212, '54:18'),
(4865, NULL, NULL, 54, 19, 'إِنَّآ أَرْسَلْنَا عَلَيْهِمْ رِيحًا صَرْصَرًا فِى يَوْمِ نَحْسٍ مُّسْتَمِرٍّ', 'إنا أرسلنا عليهم ريحا صرصرا في يوم نحس مستمر', 27, 53, 529, 212, '54:19'),
(4866, NULL, NULL, 54, 20, 'تَنزِعُ ٱلنَّاسَ كَأَنَّهُمْ أَعْجَازُ نَخْلٍ مُّنقَعِرٍ', 'تنزع الناس كأنهم أعجاز نخل منقعر', 27, 53, 529, 212, '54:20'),
(4867, NULL, NULL, 54, 21, 'فَكَيْفَ كَانَ عَذَابِى وَنُذُرِ', 'فكيف كان عذابي ونذر', 27, 53, 529, 212, '54:21'),
(4868, NULL, NULL, 54, 22, 'وَلَقَدْ يَسَّرْنَا ٱلْقُرْءَانَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ', 'ولقد يسرنا القرآن للذكر فهل من مدكر', 27, 53, 529, 212, '54:22'),
(4869, NULL, NULL, 54, 23, 'كَذَّبَتْ ثَمُودُ بِٱلنُّذُرِ', 'كذبت ثمود بالنذر', 27, 53, 529, 212, '54:23'),
(4870, NULL, NULL, 54, 24, 'فَقَالُوٓا۟ أَبَشَرًا مِّنَّا وَٰحِدًا نَّتَّبِعُهُۥٓ إِنَّآ إِذًا لَّفِى ضَلَٰلٍ وَسُعُرٍ', 'فقالوا أبشرا منا واحدا نتبعه إنا إذا لفي ضلال وسعر', 27, 53, 529, 212, '54:24'),
(4871, NULL, NULL, 54, 25, 'أَءُلْقِىَ ٱلذِّكْرُ عَلَيْهِ مِنۢ بَيْنِنَا بَلْ هُوَ كَذَّابٌ أَشِرٌ', 'أألقي الذكر عليه من بيننا بل هو كذاب أشر', 27, 53, 529, 212, '54:25'),
(4872, NULL, NULL, 54, 26, 'سَيَعْلَمُونَ غَدًا مَّنِ ٱلْكَذَّابُ ٱلْأَشِرُ', 'سيعلمون غدا من الكذاب الأشر', 27, 53, 529, 212, '54:26'),
(4873, NULL, NULL, 54, 27, 'إِنَّا مُرْسِلُوا۟ ٱلنَّاقَةِ فِتْنَةً لَّهُمْ فَٱرْتَقِبْهُمْ وَٱصْطَبِرْ', 'إنا مرسلو الناقة فتنة لهم فارتقبهم واصطبر', 27, 53, 529, 212, '54:27'),
(4874, NULL, NULL, 54, 28, 'وَنَبِّئْهُمْ أَنَّ ٱلْمَآءَ قِسْمَةٌۢ بَيْنَهُمْ كُلُّ شِرْبٍ مُّحْتَضَرٌ', 'ونبئهم أن الماء قسمة بينهم كل شرب محتضر', 27, 53, 530, 212, '54:28'),
(4875, NULL, NULL, 54, 29, 'فَنَادَوْا۟ صَاحِبَهُمْ فَتَعَاطَىٰ فَعَقَرَ', 'فنادوا صاحبهم فتعاطى فعقر', 27, 53, 530, 212, '54:29'),
(4876, NULL, NULL, 54, 30, 'فَكَيْفَ كَانَ عَذَابِى وَنُذُرِ', 'فكيف كان عذابي ونذر', 27, 53, 530, 212, '54:30'),
(4877, NULL, NULL, 54, 31, 'إِنَّآ أَرْسَلْنَا عَلَيْهِمْ صَيْحَةً وَٰحِدَةً فَكَانُوا۟ كَهَشِيمِ ٱلْمُحْتَظِرِ', 'إنا أرسلنا عليهم صيحة واحدة فكانوا كهشيم المحتظر', 27, 53, 530, 212, '54:31'),
(4878, NULL, NULL, 54, 32, 'وَلَقَدْ يَسَّرْنَا ٱلْقُرْءَانَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ', 'ولقد يسرنا القرآن للذكر فهل من مدكر', 27, 53, 530, 212, '54:32'),
(4879, NULL, NULL, 54, 33, 'كَذَّبَتْ قَوْمُ لُوطٍۭ بِٱلنُّذُرِ', 'كذبت قوم لوط بالنذر', 27, 53, 530, 212, '54:33'),
(4880, NULL, NULL, 54, 34, 'إِنَّآ أَرْسَلْنَا عَلَيْهِمْ حَاصِبًا إِلَّآ ءَالَ لُوطٍ نَّجَّيْنَٰهُم بِسَحَرٍ', 'إنا أرسلنا عليهم حاصبا إلا آل لوط نجيناهم بسحر', 27, 53, 530, 212, '54:34'),
(4881, NULL, NULL, 54, 35, 'نِّعْمَةً مِّنْ عِندِنَا كَذَٰلِكَ نَجْزِى مَن شَكَرَ', 'نعمة من عندنا كذلك نجزي من شكر', 27, 53, 530, 212, '54:35'),
(4882, NULL, NULL, 54, 36, 'وَلَقَدْ أَنذَرَهُم بَطْشَتَنَا فَتَمَارَوْا۟ بِٱلنُّذُرِ', 'ولقد أنذرهم بطشتنا فتماروا بالنذر', 27, 53, 530, 212, '54:36'),
(4883, NULL, NULL, 54, 37, 'وَلَقَدْ رَٰوَدُوهُ عَن ضَيْفِهِۦ فَطَمَسْنَآ أَعْيُنَهُمْ فَذُوقُوا۟ عَذَابِى وَنُذُرِ', 'ولقد راودوه عن ضيفه فطمسنا أعينهم فذوقوا عذابي ونذر', 27, 53, 530, 212, '54:37'),
(4884, NULL, NULL, 54, 38, 'وَلَقَدْ صَبَّحَهُم بُكْرَةً عَذَابٌ مُّسْتَقِرٌّ', 'ولقد صبحهم بكرة عذاب مستقر', 27, 53, 530, 212, '54:38'),
(4885, NULL, NULL, 54, 39, 'فَذُوقُوا۟ عَذَابِى وَنُذُرِ', 'فذوقوا عذابي ونذر', 27, 53, 530, 212, '54:39'),
(4886, NULL, NULL, 54, 40, 'وَلَقَدْ يَسَّرْنَا ٱلْقُرْءَانَ لِلذِّكْرِ فَهَلْ مِن مُّدَّكِرٍ', 'ولقد يسرنا القرآن للذكر فهل من مدكر', 27, 53, 530, 212, '54:40'),
(4887, NULL, NULL, 54, 41, 'وَلَقَدْ جَآءَ ءَالَ فِرْعَوْنَ ٱلنُّذُرُ', 'ولقد جاء آل فرعون النذر', 27, 53, 530, 212, '54:41'),
(4888, NULL, NULL, 54, 42, 'كَذَّبُوا۟ بِـَٔايَٰتِنَا كُلِّهَا فَأَخَذْنَٰهُمْ أَخْذَ عَزِيزٍ مُّقْتَدِرٍ', 'كذبوا بآياتنا كلها فأخذناهم أخذ عزيز مقتدر', 27, 53, 530, 212, '54:42'),
(4889, NULL, NULL, 54, 43, 'أَكُفَّارُكُمْ خَيْرٌ مِّنْ أُو۟لَٰٓئِكُمْ أَمْ لَكُم بَرَآءَةٌ فِى ٱلزُّبُرِ', 'أكفاركم خير من أولئكم أم لكم براءة في الزبر', 27, 53, 530, 212, '54:43'),
(4890, NULL, NULL, 54, 44, 'أَمْ يَقُولُونَ نَحْنُ جَمِيعٌ مُّنتَصِرٌ', 'أم يقولون نحن جميع منتصر', 27, 53, 530, 212, '54:44'),
(4891, NULL, NULL, 54, 45, 'سَيُهْزَمُ ٱلْجَمْعُ وَيُوَلُّونَ ٱلدُّبُرَ', 'سيهزم الجمع ويولون الدبر', 27, 53, 530, 212, '54:45'),
(4892, NULL, NULL, 54, 46, 'بَلِ ٱلسَّاعَةُ مَوْعِدُهُمْ وَٱلسَّاعَةُ أَدْهَىٰ وَأَمَرُّ', 'بل الساعة موعدهم والساعة أدهى وأمر', 27, 53, 530, 212, '54:46'),
(4893, NULL, NULL, 54, 47, 'إِنَّ ٱلْمُجْرِمِينَ فِى ضَلَٰلٍ وَسُعُرٍ', 'إن المجرمين في ضلال وسعر', 27, 53, 530, 212, '54:47'),
(4894, NULL, NULL, 54, 48, 'يَوْمَ يُسْحَبُونَ فِى ٱلنَّارِ عَلَىٰ وُجُوهِهِمْ ذُوقُوا۟ مَسَّ سَقَرَ', 'يوم يسحبون في النار على وجوههم ذوقوا مس سقر', 27, 53, 530, 212, '54:48'),
(4895, NULL, NULL, 54, 49, 'إِنَّا كُلَّ شَىْءٍ خَلَقْنَٰهُ بِقَدَرٍ', 'إنا كل شيء خلقناه بقدر', 27, 53, 530, 212, '54:49'),
(4896, NULL, NULL, 54, 50, 'وَمَآ أَمْرُنَآ إِلَّا وَٰحِدَةٌ كَلَمْحٍۭ بِٱلْبَصَرِ', 'وما أمرنا إلا واحدة كلمح بالبصر', 27, 53, 531, 212, '54:50'),
(4897, NULL, NULL, 54, 51, 'وَلَقَدْ أَهْلَكْنَآ أَشْيَاعَكُمْ فَهَلْ مِن مُّدَّكِرٍ', 'ولقد أهلكنا أشياعكم فهل من مدكر', 27, 53, 531, 212, '54:51'),
(4898, NULL, NULL, 54, 52, 'وَكُلُّ شَىْءٍ فَعَلُوهُ فِى ٱلزُّبُرِ', 'وكل شيء فعلوه في الزبر', 27, 53, 531, 212, '54:52'),
(4899, NULL, NULL, 54, 53, 'وَكُلُّ صَغِيرٍ وَكَبِيرٍ مُّسْتَطَرٌ', 'وكل صغير وكبير مستطر', 27, 53, 531, 212, '54:53'),
(4900, NULL, NULL, 54, 54, 'إِنَّ ٱلْمُتَّقِينَ فِى جَنَّٰتٍ وَنَهَرٍ', 'إن المتقين في جنات ونهر', 27, 53, 531, 212, '54:54'),
(4901, NULL, NULL, 54, 55, 'فِى مَقْعَدِ صِدْقٍ عِندَ مَلِيكٍ مُّقْتَدِرٍۭ', 'في مقعد صدق عند مليك مقتدر', 27, 53, 531, 212, '54:55'),
(4902, NULL, NULL, 55, 1, 'ٱلرَّحْمَٰنُ', 'الرحمن', 27, 54, 531, 213, '55:1'),
(4903, NULL, NULL, 55, 2, 'عَلَّمَ ٱلْقُرْءَانَ', 'علم القرآن', 27, 54, 531, 213, '55:2'),
(4904, NULL, NULL, 55, 3, 'خَلَقَ ٱلْإِنسَٰنَ', 'خلق الإنسان', 27, 54, 531, 213, '55:3'),
(4905, NULL, NULL, 55, 4, 'عَلَّمَهُ ٱلْبَيَانَ', 'علمه البيان', 27, 54, 531, 213, '55:4'),
(4906, NULL, NULL, 55, 5, 'ٱلشَّمْسُ وَٱلْقَمَرُ بِحُسْبَانٍ', 'الشمس والقمر بحسبان', 27, 54, 531, 213, '55:5'),
(4907, NULL, NULL, 55, 6, 'وَٱلنَّجْمُ وَٱلشَّجَرُ يَسْجُدَانِ', 'والنجم والشجر يسجدان', 27, 54, 531, 213, '55:6'),
(4908, NULL, NULL, 55, 7, 'وَٱلسَّمَآءَ رَفَعَهَا وَوَضَعَ ٱلْمِيزَانَ', 'والسماء رفعها ووضع الميزان', 27, 54, 531, 213, '55:7'),
(4909, NULL, NULL, 55, 8, 'أَلَّا تَطْغَوْا۟ فِى ٱلْمِيزَانِ', 'ألا تطغوا في الميزان', 27, 54, 531, 213, '55:8'),
(4910, NULL, NULL, 55, 9, 'وَأَقِيمُوا۟ ٱلْوَزْنَ بِٱلْقِسْطِ وَلَا تُخْسِرُوا۟ ٱلْمِيزَانَ', 'وأقيموا الوزن بالقسط ولا تخسروا الميزان', 27, 54, 531, 213, '55:9'),
(4911, NULL, NULL, 55, 10, 'وَٱلْأَرْضَ وَضَعَهَا لِلْأَنَامِ', 'والأرض وضعها للأنام', 27, 54, 531, 213, '55:10'),
(4912, NULL, NULL, 55, 11, 'فِيهَا فَٰكِهَةٌ وَٱلنَّخْلُ ذَاتُ ٱلْأَكْمَامِ', 'فيها فاكهة والنخل ذات الأكمام', 27, 54, 531, 213, '55:11'),
(4913, NULL, NULL, 55, 12, 'وَٱلْحَبُّ ذُو ٱلْعَصْفِ وَٱلرَّيْحَانُ', 'والحب ذو العصف والريحان', 27, 54, 531, 213, '55:12'),
(4914, NULL, NULL, 55, 13, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 531, 213, '55:13'),
(4915, NULL, NULL, 55, 14, 'خَلَقَ ٱلْإِنسَٰنَ مِن صَلْصَٰلٍ كَٱلْفَخَّارِ', 'خلق الإنسان من صلصال كالفخار', 27, 54, 531, 213, '55:14'),
(4916, NULL, NULL, 55, 15, 'وَخَلَقَ ٱلْجَآنَّ مِن مَّارِجٍ مِّن نَّارٍ', 'وخلق الجان من مارج من نار', 27, 54, 531, 213, '55:15'),
(4917, NULL, NULL, 55, 16, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 531, 213, '55:16'),
(4918, NULL, NULL, 55, 17, 'رَبُّ ٱلْمَشْرِقَيْنِ وَرَبُّ ٱلْمَغْرِبَيْنِ', 'رب المشرقين ورب المغربين', 27, 54, 532, 213, '55:17'),
(4919, NULL, NULL, 55, 18, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:18'),
(4920, NULL, NULL, 55, 19, 'مَرَجَ ٱلْبَحْرَيْنِ يَلْتَقِيَانِ', 'مرج البحرين يلتقيان', 27, 54, 532, 213, '55:19'),
(4921, NULL, NULL, 55, 20, 'بَيْنَهُمَا بَرْزَخٌ لَّا يَبْغِيَانِ', 'بينهما برزخ لا يبغيان', 27, 54, 532, 213, '55:20'),
(4922, NULL, NULL, 55, 21, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:21'),
(4923, NULL, NULL, 55, 22, 'يَخْرُجُ مِنْهُمَا ٱللُّؤْلُؤُ وَٱلْمَرْجَانُ', 'يخرج منهما اللؤلؤ والمرجان', 27, 54, 532, 213, '55:22'),
(4924, NULL, NULL, 55, 23, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:23'),
(4925, NULL, NULL, 55, 24, 'وَلَهُ ٱلْجَوَارِ ٱلْمُنشَـَٔاتُ فِى ٱلْبَحْرِ كَٱلْأَعْلَٰمِ', 'وله الجوار المنشآت في البحر كالأعلام', 27, 54, 532, 213, '55:24'),
(4926, NULL, NULL, 55, 25, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:25'),
(4927, NULL, NULL, 55, 26, 'كُلُّ مَنْ عَلَيْهَا فَانٍ', 'كل من عليها فان', 27, 54, 532, 213, '55:26'),
(4928, NULL, NULL, 55, 27, 'وَيَبْقَىٰ وَجْهُ رَبِّكَ ذُو ٱلْجَلَٰلِ وَٱلْإِكْرَامِ', 'ويبقى وجه ربك ذو الجلال والإكرام', 27, 54, 532, 213, '55:27'),
(4929, NULL, NULL, 55, 28, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:28'),
(4930, NULL, NULL, 55, 29, 'يَسْـَٔلُهُۥ مَن فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ كُلَّ يَوْمٍ هُوَ فِى شَأْنٍ', 'يسأله من في السماوات والأرض كل يوم هو في شأن', 27, 54, 532, 213, '55:29'),
(4931, NULL, NULL, 55, 30, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:30'),
(4932, NULL, NULL, 55, 31, 'سَنَفْرُغُ لَكُمْ أَيُّهَ ٱلثَّقَلَانِ', 'سنفرغ لكم أيه الثقلان', 27, 54, 532, 213, '55:31'),
(4933, NULL, NULL, 55, 32, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:32'),
(4934, NULL, NULL, 55, 33, 'يَٰمَعْشَرَ ٱلْجِنِّ وَٱلْإِنسِ إِنِ ٱسْتَطَعْتُمْ أَن تَنفُذُوا۟ مِنْ أَقْطَارِ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ فَٱنفُذُوا۟ لَا تَنفُذُونَ إِلَّا بِسُلْطَٰنٍ', 'يا معشر الجن والإنس إن استطعتم أن تنفذوا من أقطار السماوات والأرض فانفذوا لا تنفذون إلا بسلطان', 27, 54, 532, 213, '55:33'),
(4935, NULL, NULL, 55, 34, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:34'),
(4936, NULL, NULL, 55, 35, 'يُرْسَلُ عَلَيْكُمَا شُوَاظٌ مِّن نَّارٍ وَنُحَاسٌ فَلَا تَنتَصِرَانِ', 'يرسل عليكما شواظ من نار ونحاس فلا تنتصران', 27, 54, 532, 213, '55:35'),
(4937, NULL, NULL, 55, 36, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:36'),
(4938, NULL, NULL, 55, 37, 'فَإِذَا ٱنشَقَّتِ ٱلسَّمَآءُ فَكَانَتْ وَرْدَةً كَٱلدِّهَانِ', 'فإذا انشقت السماء فكانت وردة كالدهان', 27, 54, 532, 213, '55:37'),
(4939, NULL, NULL, 55, 38, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:38'),
(4940, NULL, NULL, 55, 39, 'فَيَوْمَئِذٍ لَّا يُسْـَٔلُ عَن ذَنۢبِهِۦٓ إِنسٌ وَلَا جَآنٌّ', 'فيومئذ لا يسأل عن ذنبه إنس ولا جان', 27, 54, 532, 213, '55:39'),
(4941, NULL, NULL, 55, 40, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 532, 213, '55:40'),
(4942, NULL, NULL, 55, 41, 'يُعْرَفُ ٱلْمُجْرِمُونَ بِسِيمَٰهُمْ فَيُؤْخَذُ بِٱلنَّوَٰصِى وَٱلْأَقْدَامِ', 'يعرف المجرمون بسيماهم فيؤخذ بالنواصي والأقدام', 27, 54, 533, 213, '55:41'),
(4943, NULL, NULL, 55, 42, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 533, 213, '55:42'),
(4944, NULL, NULL, 55, 43, 'هَٰذِهِۦ جَهَنَّمُ ٱلَّتِى يُكَذِّبُ بِهَا ٱلْمُجْرِمُونَ', 'هذه جهنم التي يكذب بها المجرمون', 27, 54, 533, 213, '55:43'),
(4945, NULL, NULL, 55, 44, 'يَطُوفُونَ بَيْنَهَا وَبَيْنَ حَمِيمٍ ءَانٍ', 'يطوفون بينها وبين حميم آن', 27, 54, 533, 213, '55:44'),
(4946, NULL, NULL, 55, 45, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 533, 213, '55:45'),
(4947, NULL, NULL, 55, 46, 'وَلِمَنْ خَافَ مَقَامَ رَبِّهِۦ جَنَّتَانِ', 'ولمن خاف مقام ربه جنتان', 27, 54, 533, 213, '55:46'),
(4948, NULL, NULL, 55, 47, 'فَبِأَىِّ ءَالَآءِ رَبِّكُمَا تُكَذِّبَانِ', 'فبأي آلاء ربكما تكذبان', 27, 54, 533, 213, '55:47'),
