(5037, NULL, NULL, 56, 58, 'أَفَرَءَيْتُم مَّا تُمْنُونَ', 'أفرأيتم ما تمنون', 27, 54, 536, 214, '56:58'),
(5038, NULL, NULL, 56, 59, 'ءَأَنتُمْ تَخْلُقُونَهُۥٓ أَمْ نَحْنُ ٱلْخَٰلِقُونَ', 'أأنتم تخلقونه أم نحن الخالقون', 27, 54, 536, 214, '56:59'),
(5039, NULL, NULL, 56, 60, 'نَحْنُ قَدَّرْنَا بَيْنَكُمُ ٱلْمَوْتَ وَمَا نَحْنُ بِمَسْبُوقِينَ', 'نحن قدرنا بينكم الموت وما نحن بمسبوقين', 27, 54, 536, 214, '56:60'),
(5040, NULL, NULL, 56, 61, 'عَلَىٰٓ أَن نُّبَدِّلَ أَمْثَٰلَكُمْ وَنُنشِئَكُمْ فِى مَا لَا تَعْلَمُونَ', 'على أن نبدل أمثالكم وننشئكم في ما لا تعلمون', 27, 54, 536, 214, '56:61'),
(5041, NULL, NULL, 56, 62, 'وَلَقَدْ عَلِمْتُمُ ٱلنَّشْأَةَ ٱلْأُولَىٰ فَلَوْلَا تَذَكَّرُونَ', 'ولقد علمتم النشأة الأولى فلولا تذكرون', 27, 54, 536, 214, '56:62'),
(5042, NULL, NULL, 56, 63, 'أَفَرَءَيْتُم مَّا تَحْرُثُونَ', 'أفرأيتم ما تحرثون', 27, 54, 536, 214, '56:63'),
(5043, NULL, NULL, 56, 64, 'ءَأَنتُمْ تَزْرَعُونَهُۥٓ أَمْ نَحْنُ ٱلزَّٰرِعُونَ', 'أأنتم تزرعونه أم نحن الزارعون', 27, 54, 536, 214, '56:64'),
(5044, NULL, NULL, 56, 65, 'لَوْ نَشَآءُ لَجَعَلْنَٰهُ حُطَٰمًا فَظَلْتُمْ تَفَكَّهُونَ', 'لو نشاء لجعلناه حطاما فظلتم تفكهون', 27, 54, 536, 214, '56:65'),
(5045, NULL, NULL, 56, 66, 'إِنَّا لَمُغْرَمُونَ', 'إنا لمغرمون', 27, 54, 536, 214, '56:66'),
(5046, NULL, NULL, 56, 67, 'بَلْ نَحْنُ مَحْرُومُونَ', 'بل نحن محرومون', 27, 54, 536, 214, '56:67'),
(5047, NULL, NULL, 56, 68, 'أَفَرَءَيْتُمُ ٱلْمَآءَ ٱلَّذِى تَشْرَبُونَ', 'أفرأيتم الماء الذي تشربون', 27, 54, 536, 214, '56:68'),
(5048, NULL, NULL, 56, 69, 'ءَأَنتُمْ أَنزَلْتُمُوهُ مِنَ ٱلْمُزْنِ أَمْ نَحْنُ ٱلْمُنزِلُونَ', 'أأنتم أنزلتموه من المزن أم نحن المنزلون', 27, 54, 536, 214, '56:69'),
(5049, NULL, NULL, 56, 70, 'لَوْ نَشَآءُ جَعَلْنَٰهُ أُجَاجًا فَلَوْلَا تَشْكُرُونَ', 'لو نشاء جعلناه أجاجا فلولا تشكرون', 27, 54, 536, 214, '56:70'),
(5050, NULL, NULL, 56, 71, 'أَفَرَءَيْتُمُ ٱلنَّارَ ٱلَّتِى تُورُونَ', 'أفرأيتم النار التي تورون', 27, 54, 536, 214, '56:71'),
(5051, NULL, NULL, 56, 72, 'ءَأَنتُمْ أَنشَأْتُمْ شَجَرَتَهَآ أَمْ نَحْنُ ٱلْمُنشِـُٔونَ', 'أأنتم أنشأتم شجرتها أم نحن المنشئون', 27, 54, 536, 214, '56:72'),
(5052, NULL, NULL, 56, 73, 'نَحْنُ جَعَلْنَٰهَا تَذْكِرَةً وَمَتَٰعًا لِّلْمُقْوِينَ', 'نحن جعلناها تذكرة ومتاعا للمقوين', 27, 54, 536, 214, '56:73'),
(5053, NULL, NULL, 56, 74, 'فَسَبِّحْ بِٱسْمِ رَبِّكَ ٱلْعَظِيمِ', 'فسبح باسم ربك العظيم', 27, 54, 536, 214, '56:74'),
(5054, NULL, NULL, 56, 75, 'فَلَآ أُقْسِمُ بِمَوَٰقِعِ ٱلنُّجُومِ', 'فلا أقسم بمواقع النجوم', 27, 54, 536, 215, '56:75'),
(5055, NULL, NULL, 56, 76, 'وَإِنَّهُۥ لَقَسَمٌ لَّوْ تَعْلَمُونَ عَظِيمٌ', 'وإنه لقسم لو تعلمون عظيم', 27, 54, 536, 215, '56:76'),
(5056, NULL, NULL, 56, 77, 'إِنَّهُۥ لَقُرْءَانٌ كَرِيمٌ', 'إنه لقرآن كريم', 27, 54, 537, 215, '56:77'),
(5057, NULL, NULL, 56, 78, 'فِى كِتَٰبٍ مَّكْنُونٍ', 'في كتاب مكنون', 27, 54, 537, 215, '56:78'),
(5058, NULL, NULL, 56, 79, 'لَّا يَمَسُّهُۥٓ إِلَّا ٱلْمُطَهَّرُونَ', 'لا يمسه إلا المطهرون', 27, 54, 537, 215, '56:79'),
(5059, NULL, NULL, 56, 80, 'تَنزِيلٌ مِّن رَّبِّ ٱلْعَٰلَمِينَ', 'تنزيل من رب العالمين', 27, 54, 537, 215, '56:80'),
(5060, NULL, NULL, 56, 81, 'أَفَبِهَٰذَا ٱلْحَدِيثِ أَنتُم مُّدْهِنُونَ', 'أفبهذا الحديث أنتم مدهنون', 27, 54, 537, 215, '56:81'),
(5061, NULL, NULL, 56, 82, 'وَتَجْعَلُونَ رِزْقَكُمْ أَنَّكُمْ تُكَذِّبُونَ', 'وتجعلون رزقكم أنكم تكذبون', 27, 54, 537, 215, '56:82'),
(5062, NULL, NULL, 56, 83, 'فَلَوْلَآ إِذَا بَلَغَتِ ٱلْحُلْقُومَ', 'فلولا إذا بلغت الحلقوم', 27, 54, 537, 215, '56:83'),
(5063, NULL, NULL, 56, 84, 'وَأَنتُمْ حِينَئِذٍ تَنظُرُونَ', 'وأنتم حينئذ تنظرون', 27, 54, 537, 215, '56:84'),
(5064, NULL, NULL, 56, 85, 'وَنَحْنُ أَقْرَبُ إِلَيْهِ مِنكُمْ وَلَٰكِن لَّا تُبْصِرُونَ', 'ونحن أقرب إليه منكم ولكن لا تبصرون', 27, 54, 537, 215, '56:85'),
(5065, NULL, NULL, 56, 86, 'فَلَوْلَآ إِن كُنتُمْ غَيْرَ مَدِينِينَ', 'فلولا إن كنتم غير مدينين', 27, 54, 537, 215, '56:86'),
(5066, NULL, NULL, 56, 87, 'تَرْجِعُونَهَآ إِن كُنتُمْ صَٰدِقِينَ', 'ترجعونها إن كنتم صادقين', 27, 54, 537, 215, '56:87'),
(5067, NULL, NULL, 56, 88, 'فَأَمَّآ إِن كَانَ مِنَ ٱلْمُقَرَّبِينَ', 'فأما إن كان من المقربين', 27, 54, 537, 215, '56:88'),
(5068, NULL, NULL, 56, 89, 'فَرَوْحٌ وَرَيْحَانٌ وَجَنَّتُ نَعِيمٍ', 'فروح وريحان وجنت نعيم', 27, 54, 537, 215, '56:89'),
(5069, NULL, NULL, 56, 90, 'وَأَمَّآ إِن كَانَ مِنْ أَصْحَٰبِ ٱلْيَمِينِ', 'وأما إن كان من أصحاب اليمين', 27, 54, 537, 215, '56:90'),
(5070, NULL, NULL, 56, 91, 'فَسَلَٰمٌ لَّكَ مِنْ أَصْحَٰبِ ٱلْيَمِينِ', 'فسلام لك من أصحاب اليمين', 27, 54, 537, 215, '56:91'),
(5071, NULL, NULL, 56, 92, 'وَأَمَّآ إِن كَانَ مِنَ ٱلْمُكَذِّبِينَ ٱلضَّآلِّينَ', 'وأما إن كان من المكذبين الضالين', 27, 54, 537, 215, '56:92'),
(5072, NULL, NULL, 56, 93, 'فَنُزُلٌ مِّنْ حَمِيمٍ', 'فنزل من حميم', 27, 54, 537, 215, '56:93'),
(5073, NULL, NULL, 56, 94, 'وَتَصْلِيَةُ جَحِيمٍ', 'وتصلية جحيم', 27, 54, 537, 215, '56:94'),
(5074, NULL, NULL, 56, 95, 'إِنَّ هَٰذَا لَهُوَ حَقُّ ٱلْيَقِينِ', 'إن هذا لهو حق اليقين', 27, 54, 537, 215, '56:95'),
(5075, NULL, NULL, 56, 96, 'فَسَبِّحْ بِٱسْمِ رَبِّكَ ٱلْعَظِيمِ', 'فسبح باسم ربك العظيم', 27, 54, 537, 215, '56:96'),
(5076, NULL, NULL, 57, 1, 'سَبَّحَ لِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'سبح لله ما في السماوات والأرض وهو العزيز الحكيم', 27, 54, 537, 215, '57:1'),
(5077, NULL, NULL, 57, 2, 'لَهُۥ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ يُحْىِۦ وَيُمِيتُ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'له ملك السماوات والأرض يحيي ويميت وهو على كل شيء قدير', 27, 54, 537, 215, '57:2'),
(5078, NULL, NULL, 57, 3, 'هُوَ ٱلْأَوَّلُ وَٱلْءَاخِرُ وَٱلظَّٰهِرُ وَٱلْبَاطِنُ وَهُوَ بِكُلِّ شَىْءٍ عَلِيمٌ', 'هو الأول والآخر والظاهر والباطن وهو بكل شيء عليم', 27, 54, 537, 215, '57:3'),
(5079, NULL, NULL, 57, 4, 'هُوَ ٱلَّذِى خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ فِى سِتَّةِ أَيَّامٍ ثُمَّ ٱسْتَوَىٰ عَلَى ٱلْعَرْشِ يَعْلَمُ مَا يَلِجُ فِى ٱلْأَرْضِ وَمَا يَخْرُجُ مِنْهَا وَمَا يَنزِلُ مِنَ ٱلسَّمَآءِ وَمَا يَعْرُجُ فِيهَا وَهُوَ مَعَكُمْ أَيْنَ مَا كُنتُمْ وَٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 'هو الذي خلق السماوات والأرض في ستة أيام ثم استوى على العرش يعلم ما يلج في الأرض وما يخرج منها وما ينزل من السماء وما يعرج فيها وهو معكم أين ما كنتم والله بما تعملون بصير', 27, 54, 538, 215, '57:4'),
(5080, NULL, NULL, 57, 5, 'لَّهُۥ مُلْكُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَإِلَى ٱللَّهِ تُرْجَعُ ٱلْأُمُورُ', 'له ملك السماوات والأرض وإلى الله ترجع الأمور', 27, 54, 538, 215, '57:5'),
(5081, NULL, NULL, 57, 6, 'يُولِجُ ٱلَّيْلَ فِى ٱلنَّهَارِ وَيُولِجُ ٱلنَّهَارَ فِى ٱلَّيْلِ وَهُوَ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ', 'يولج الليل في النهار ويولج النهار في الليل وهو عليم بذات الصدور', 27, 54, 538, 215, '57:6'),
(5082, NULL, NULL, 57, 7, 'ءَامِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَأَنفِقُوا۟ مِمَّا جَعَلَكُم مُّسْتَخْلَفِينَ فِيهِ فَٱلَّذِينَ ءَامَنُوا۟ مِنكُمْ وَأَنفَقُوا۟ لَهُمْ أَجْرٌ كَبِيرٌ', 'آمنوا بالله ورسوله وأنفقوا مما جعلكم مستخلفين فيه فالذين آمنوا منكم وأنفقوا لهم أجر كبير', 27, 54, 538, 215, '57:7'),
(5083, NULL, NULL, 57, 8, 'وَمَا لَكُمْ لَا تُؤْمِنُونَ بِٱللَّهِ وَٱلرَّسُولُ يَدْعُوكُمْ لِتُؤْمِنُوا۟ بِرَبِّكُمْ وَقَدْ أَخَذَ مِيثَٰقَكُمْ إِن كُنتُم مُّؤْمِنِينَ', 'وما لكم لا تؤمنون بالله والرسول يدعوكم لتؤمنوا بربكم وقد أخذ ميثاقكم إن كنتم مؤمنين', 27, 54, 538, 215, '57:8'),
(5084, NULL, NULL, 57, 9, 'هُوَ ٱلَّذِى يُنَزِّلُ عَلَىٰ عَبْدِهِۦٓ ءَايَٰتٍۭ بَيِّنَٰتٍ لِّيُخْرِجَكُم مِّنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ وَإِنَّ ٱللَّهَ بِكُمْ لَرَءُوفٌ رَّحِيمٌ', 'هو الذي ينزل على عبده آيات بينات ليخرجكم من الظلمات إلى النور وإن الله بكم لرءوف رحيم', 27, 54, 538, 215, '57:9'),
(5085, NULL, NULL, 57, 10, 'وَمَا لَكُمْ أَلَّا تُنفِقُوا۟ فِى سَبِيلِ ٱللَّهِ وَلِلَّهِ مِيرَٰثُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ لَا يَسْتَوِى مِنكُم مَّنْ أَنفَقَ مِن قَبْلِ ٱلْفَتْحِ وَقَٰتَلَ أُو۟لَٰٓئِكَ أَعْظَمُ دَرَجَةً مِّنَ ٱلَّذِينَ أَنفَقُوا۟ مِنۢ بَعْدُ وَقَٰتَلُوا۟ وَكُلًّا وَعَدَ ٱللَّهُ ٱلْحُسْنَىٰ وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'وما لكم ألا تنفقوا في سبيل الله ولله ميراث السماوات والأرض لا يستوي منكم من أنفق من قبل الفتح وقاتل أولئك أعظم درجة من الذين أنفقوا من بعد وقاتلوا وكلا وعد الله الحسنى والله بما تعملون خبير', 27, 54, 538, 215, '57:10'),
(5086, NULL, NULL, 57, 11, 'مَّن ذَا ٱلَّذِى يُقْرِضُ ٱللَّهَ قَرْضًا حَسَنًا فَيُضَٰعِفَهُۥ لَهُۥ وَلَهُۥٓ أَجْرٌ كَرِيمٌ', 'من ذا الذي يقرض الله قرضا حسنا فيضاعفه له وله أجر كريم', 27, 54, 538, 215, '57:11'),
(5087, NULL, NULL, 57, 12, 'يَوْمَ تَرَى ٱلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ يَسْعَىٰ نُورُهُم بَيْنَ أَيْدِيهِمْ وَبِأَيْمَٰنِهِم بُشْرَىٰكُمُ ٱلْيَوْمَ جَنَّٰتٌ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَا ذَٰلِكَ هُوَ ٱلْفَوْزُ ٱلْعَظِيمُ', 'يوم ترى المؤمنين والمؤمنات يسعى نورهم بين أيديهم وبأيمانهم بشراكم اليوم جنات تجري من تحتها الأنهار خالدين فيها ذلك هو الفوز العظيم', 27, 54, 539, 215, '57:12'),
(5088, NULL, NULL, 57, 13, 'يَوْمَ يَقُولُ ٱلْمُنَٰفِقُونَ وَٱلْمُنَٰفِقَٰتُ لِلَّذِينَ ءَامَنُوا۟ ٱنظُرُونَا نَقْتَبِسْ مِن نُّورِكُمْ قِيلَ ٱرْجِعُوا۟ وَرَآءَكُمْ فَٱلْتَمِسُوا۟ نُورًا فَضُرِبَ بَيْنَهُم بِسُورٍ لَّهُۥ بَابٌۢ بَاطِنُهُۥ فِيهِ ٱلرَّحْمَةُ وَظَٰهِرُهُۥ مِن قِبَلِهِ ٱلْعَذَابُ', 'يوم يقول المنافقون والمنافقات للذين آمنوا انظرونا نقتبس من نوركم قيل ارجعوا وراءكم فالتمسوا نورا فضرب بينهم بسور له باب باطنه فيه الرحمة وظاهره من قبله العذاب', 27, 54, 539, 215, '57:13'),
(5089, NULL, NULL, 57, 14, 'يُنَادُونَهُمْ أَلَمْ نَكُن مَّعَكُمْ قَالُوا۟ بَلَىٰ وَلَٰكِنَّكُمْ فَتَنتُمْ أَنفُسَكُمْ وَتَرَبَّصْتُمْ وَٱرْتَبْتُمْ وَغَرَّتْكُمُ ٱلْأَمَانِىُّ حَتَّىٰ جَآءَ أَمْرُ ٱللَّهِ وَغَرَّكُم بِٱللَّهِ ٱلْغَرُورُ', 'ينادونهم ألم نكن معكم قالوا بلى ولكنكم فتنتم أنفسكم وتربصتم وارتبتم وغرتكم الأماني حتى جاء أمر الله وغركم بالله الغرور', 27, 54, 539, 215, '57:14'),
(5090, NULL, NULL, 57, 15, 'فَٱلْيَوْمَ لَا يُؤْخَذُ مِنكُمْ فِدْيَةٌ وَلَا مِنَ ٱلَّذِينَ كَفَرُوا۟ مَأْوَىٰكُمُ ٱلنَّارُ هِىَ مَوْلَىٰكُمْ وَبِئْسَ ٱلْمَصِيرُ', 'فاليوم لا يؤخذ منكم فدية ولا من الذين كفروا مأواكم النار هي مولاكم وبئس المصير', 27, 54, 539, 215, '57:15'),
(5091, NULL, NULL, 57, 16, 'أَلَمْ يَأْنِ لِلَّذِينَ ءَامَنُوٓا۟ أَن تَخْشَعَ قُلُوبُهُمْ لِذِكْرِ ٱللَّهِ وَمَا نَزَلَ مِنَ ٱلْحَقِّ وَلَا يَكُونُوا۟ كَٱلَّذِينَ أُوتُوا۟ ٱلْكِتَٰبَ مِن قَبْلُ فَطَالَ عَلَيْهِمُ ٱلْأَمَدُ فَقَسَتْ قُلُوبُهُمْ وَكَثِيرٌ مِّنْهُمْ فَٰسِقُونَ', 'ألم يأن للذين آمنوا أن تخشع قلوبهم لذكر الله وما نزل من الحق ولا يكونوا كالذين أوتوا الكتاب من قبل فطال عليهم الأمد فقست قلوبهم وكثير منهم فاسقون', 27, 54, 539, 216, '57:16'),
(5092, NULL, NULL, 57, 17, 'ٱعْلَمُوٓا۟ أَنَّ ٱللَّهَ يُحْىِ ٱلْأَرْضَ بَعْدَ مَوْتِهَا قَدْ بَيَّنَّا لَكُمُ ٱلْءَايَٰتِ لَعَلَّكُمْ تَعْقِلُونَ', 'اعلموا أن الله يحيي الأرض بعد موتها قد بينا لكم الآيات لعلكم تعقلون', 27, 54, 539, 216, '57:17'),
(5093, NULL, NULL, 57, 18, 'إِنَّ ٱلْمُصَّدِّقِينَ وَٱلْمُصَّدِّقَٰتِ وَأَقْرَضُوا۟ ٱللَّهَ قَرْضًا حَسَنًا يُضَٰعَفُ لَهُمْ وَلَهُمْ أَجْرٌ كَرِيمٌ', 'إن المصدقين والمصدقات وأقرضوا الله قرضا حسنا يضاعف لهم ولهم أجر كريم', 27, 54, 539, 216, '57:18'),
(5094, NULL, NULL, 57, 19, 'وَٱلَّذِينَ ءَامَنُوا۟ بِٱللَّهِ وَرُسُلِهِۦٓ أُو۟لَٰٓئِكَ هُمُ ٱلصِّدِّيقُونَ وَٱلشُّهَدَآءُ عِندَ رَبِّهِمْ لَهُمْ أَجْرُهُمْ وَنُورُهُمْ وَٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔايَٰتِنَآ أُو۟لَٰٓئِكَ أَصْحَٰبُ ٱلْجَحِيمِ', 'والذين آمنوا بالله ورسله أولئك هم الصديقون والشهداء عند ربهم لهم أجرهم ونورهم والذين كفروا وكذبوا بآياتنا أولئك أصحاب الجحيم', 27, 54, 540, 216, '57:19'),
(5095, NULL, NULL, 57, 20, 'ٱعْلَمُوٓا۟ أَنَّمَا ٱلْحَيَوٰةُ ٱلدُّنْيَا لَعِبٌ وَلَهْوٌ وَزِينَةٌ وَتَفَاخُرٌۢ بَيْنَكُمْ وَتَكَاثُرٌ فِى ٱلْأَمْوَٰلِ وَٱلْأَوْلَٰدِ كَمَثَلِ غَيْثٍ أَعْجَبَ ٱلْكُفَّارَ نَبَاتُهُۥ ثُمَّ يَهِيجُ فَتَرَىٰهُ مُصْفَرًّا ثُمَّ يَكُونُ حُطَٰمًا وَفِى ٱلْءَاخِرَةِ عَذَابٌ شَدِيدٌ وَمَغْفِرَةٌ مِّنَ ٱللَّهِ وَرِضْوَٰنٌ وَمَا ٱلْحَيَوٰةُ ٱلدُّنْيَآ إِلَّا مَتَٰعُ ٱلْغُرُورِ', 'اعلموا أنما الحياة الدنيا لعب ولهو وزينة وتفاخر بينكم وتكاثر في الأموال والأولاد كمثل غيث أعجب الكفار نباته ثم يهيج فتراه مصفرا ثم يكون حطاما وفي الآخرة عذاب شديد ومغفرة من الله ورضوان وما الحياة الدنيا إلا متاع الغرور', 27, 54, 540, 216, '57:20'),
(5096, NULL, NULL, 57, 21, 'سَابِقُوٓا۟ إِلَىٰ مَغْفِرَةٍ مِّن رَّبِّكُمْ وَجَنَّةٍ عَرْضُهَا كَعَرْضِ ٱلسَّمَآءِ وَٱلْأَرْضِ أُعِدَّتْ لِلَّذِينَ ءَامَنُوا۟ بِٱللَّهِ وَرُسُلِهِۦ ذَٰلِكَ فَضْلُ ٱللَّهِ يُؤْتِيهِ مَن يَشَآءُ وَٱللَّهُ ذُو ٱلْفَضْلِ ٱلْعَظِيمِ', 'سابقوا إلى مغفرة من ربكم وجنة عرضها كعرض السماء والأرض أعدت للذين آمنوا بالله ورسله ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم', 27, 54, 540, 216, '57:21'),
(5097, NULL, NULL, 57, 22, 'مَآ أَصَابَ مِن مُّصِيبَةٍ فِى ٱلْأَرْضِ وَلَا فِىٓ أَنفُسِكُمْ إِلَّا فِى كِتَٰبٍ مِّن قَبْلِ أَن نَّبْرَأَهَآ إِنَّ ذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌ', 'ما أصاب من مصيبة في الأرض ولا في أنفسكم إلا في كتاب من قبل أن نبرأها إن ذلك على الله يسير', 27, 54, 540, 216, '57:22');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(5098, NULL, NULL, 57, 23, 'لِّكَيْلَا تَأْسَوْا۟ عَلَىٰ مَا فَاتَكُمْ وَلَا تَفْرَحُوا۟ بِمَآ ءَاتَىٰكُمْ وَٱللَّهُ لَا يُحِبُّ كُلَّ مُخْتَالٍ فَخُورٍ', 'لكيلا تأسوا على ما فاتكم ولا تفرحوا بما آتاكم والله لا يحب كل مختال فخور', 27, 54, 540, 216, '57:23'),
(5099, NULL, NULL, 57, 24, 'ٱلَّذِينَ يَبْخَلُونَ وَيَأْمُرُونَ ٱلنَّاسَ بِٱلْبُخْلِ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ', 'الذين يبخلون ويأمرون الناس بالبخل ومن يتول فإن الله هو الغني الحميد', 27, 54, 540, 216, '57:24'),
(5100, NULL, NULL, 57, 25, 'لَقَدْ أَرْسَلْنَا رُسُلَنَا بِٱلْبَيِّنَٰتِ وَأَنزَلْنَا مَعَهُمُ ٱلْكِتَٰبَ وَٱلْمِيزَانَ لِيَقُومَ ٱلنَّاسُ بِٱلْقِسْطِ وَأَنزَلْنَا ٱلْحَدِيدَ فِيهِ بَأْسٌ شَدِيدٌ وَمَنَٰفِعُ لِلنَّاسِ وَلِيَعْلَمَ ٱللَّهُ مَن يَنصُرُهُۥ وَرُسُلَهُۥ بِٱلْغَيْبِ إِنَّ ٱللَّهَ قَوِىٌّ عَزِيزٌ', 'لقد أرسلنا رسلنا بالبينات وأنزلنا معهم الكتاب والميزان ليقوم الناس بالقسط وأنزلنا الحديد فيه بأس شديد ومنافع للناس وليعلم الله من ينصره ورسله بالغيب إن الله قوي عزيز', 27, 54, 541, 216, '57:25'),
(5101, NULL, NULL, 57, 26, 'وَلَقَدْ أَرْسَلْنَا نُوحًا وَإِبْرَٰهِيمَ وَجَعَلْنَا فِى ذُرِّيَّتِهِمَا ٱلنُّبُوَّةَ وَٱلْكِتَٰبَ فَمِنْهُم مُّهْتَدٍ وَكَثِيرٌ مِّنْهُمْ فَٰسِقُونَ', 'ولقد أرسلنا نوحا وإبراهيم وجعلنا في ذريتهما النبوة والكتاب فمنهم مهتد وكثير منهم فاسقون', 27, 54, 541, 216, '57:26'),
(5102, NULL, NULL, 57, 27, 'ثُمَّ قَفَّيْنَا عَلَىٰٓ ءَاثَٰرِهِم بِرُسُلِنَا وَقَفَّيْنَا بِعِيسَى ٱبْنِ مَرْيَمَ وَءَاتَيْنَٰهُ ٱلْإِنجِيلَ وَجَعَلْنَا فِى قُلُوبِ ٱلَّذِينَ ٱتَّبَعُوهُ رَأْفَةً وَرَحْمَةً وَرَهْبَانِيَّةً ٱبْتَدَعُوهَا مَا كَتَبْنَٰهَا عَلَيْهِمْ إِلَّا ٱبْتِغَآءَ رِضْوَٰنِ ٱللَّهِ فَمَا رَعَوْهَا حَقَّ رِعَايَتِهَا فَـَٔاتَيْنَا ٱلَّذِينَ ءَامَنُوا۟ مِنْهُمْ أَجْرَهُمْ وَكَثِيرٌ مِّنْهُمْ فَٰسِقُونَ', 'ثم قفينا على آثارهم برسلنا وقفينا بعيسى ابن مريم وآتيناه الإنجيل وجعلنا في قلوب الذين اتبعوه رأفة ورحمة ورهبانية ابتدعوها ما كتبناها عليهم إلا ابتغاء رضوان الله فما رعوها حق رعايتها فآتينا الذين آمنوا منهم أجرهم وكثير منهم فاسقون', 27, 54, 541, 216, '57:27'),
(5103, NULL, NULL, 57, 28, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَءَامِنُوا۟ بِرَسُولِهِۦ يُؤْتِكُمْ كِفْلَيْنِ مِن رَّحْمَتِهِۦ وَيَجْعَل لَّكُمْ نُورًا تَمْشُونَ بِهِۦ وَيَغْفِرْ لَكُمْ وَٱللَّهُ غَفُورٌ رَّحِيمٌ', 'يا أيها الذين آمنوا اتقوا الله وآمنوا برسوله يؤتكم كفلين من رحمته ويجعل لكم نورا تمشون به ويغفر لكم والله غفور رحيم', 27, 54, 541, 216, '57:28'),
(5104, NULL, NULL, 57, 29, 'لِّئَلَّا يَعْلَمَ أَهْلُ ٱلْكِتَٰبِ أَلَّا يَقْدِرُونَ عَلَىٰ شَىْءٍ مِّن فَضْلِ ٱللَّهِ وَأَنَّ ٱلْفَضْلَ بِيَدِ ٱللَّهِ يُؤْتِيهِ مَن يَشَآءُ وَٱللَّهُ ذُو ٱلْفَضْلِ ٱلْعَظِيمِ', 'لئلا يعلم أهل الكتاب ألا يقدرون على شيء من فضل الله وأن الفضل بيد الله يؤتيه من يشاء والله ذو الفضل العظيم', 27, 54, 541, 216, '57:29'),
(5105, NULL, NULL, 58, 1, 'قَدْ سَمِعَ ٱللَّهُ قَوْلَ ٱلَّتِى تُجَٰدِلُكَ فِى زَوْجِهَا وَتَشْتَكِىٓ إِلَى ٱللَّهِ وَٱللَّهُ يَسْمَعُ تَحَاوُرَكُمَآ إِنَّ ٱللَّهَ سَمِيعٌۢ بَصِيرٌ', 'قد سمع الله قول التي تجادلك في زوجها وتشتكي إلى الله والله يسمع تحاوركما إن الله سميع بصير', 28, 55, 542, 217, '58:1'),
(5106, NULL, NULL, 58, 2, 'ٱلَّذِينَ يُظَٰهِرُونَ مِنكُم مِّن نِّسَآئِهِم مَّا هُنَّ أُمَّهَٰتِهِمْ إِنْ أُمَّهَٰتُهُمْ إِلَّا ٱلَّٰٓـِٔى وَلَدْنَهُمْ وَإِنَّهُمْ لَيَقُولُونَ مُنكَرًا مِّنَ ٱلْقَوْلِ وَزُورًا وَإِنَّ ٱللَّهَ لَعَفُوٌّ غَفُورٌ', 'الذين يظاهرون منكم من نسائهم ما هن أمهاتهم إن أمهاتهم إلا اللائي ولدنهم وإنهم ليقولون منكرا من القول وزورا وإن الله لعفو غفور', 28, 55, 542, 217, '58:2'),
(5107, NULL, NULL, 58, 3, 'وَٱلَّذِينَ يُظَٰهِرُونَ مِن نِّسَآئِهِمْ ثُمَّ يَعُودُونَ لِمَا قَالُوا۟ فَتَحْرِيرُ رَقَبَةٍ مِّن قَبْلِ أَن يَتَمَآسَّا ذَٰلِكُمْ تُوعَظُونَ بِهِۦ وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'والذين يظاهرون من نسائهم ثم يعودون لما قالوا فتحرير رقبة من قبل أن يتماسا ذلكم توعظون به والله بما تعملون خبير', 28, 55, 542, 217, '58:3'),
(5108, NULL, NULL, 58, 4, 'فَمَن لَّمْ يَجِدْ فَصِيَامُ شَهْرَيْنِ مُتَتَابِعَيْنِ مِن قَبْلِ أَن يَتَمَآسَّا فَمَن لَّمْ يَسْتَطِعْ فَإِطْعَامُ سِتِّينَ مِسْكِينًا ذَٰلِكَ لِتُؤْمِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَتِلْكَ حُدُودُ ٱللَّهِ وَلِلْكَٰفِرِينَ عَذَابٌ أَلِيمٌ', 'فمن لم يجد فصيام شهرين متتابعين من قبل أن يتماسا فمن لم يستطع فإطعام ستين مسكينا ذلك لتؤمنوا بالله ورسوله وتلك حدود الله وللكافرين عذاب أليم', 28, 55, 542, 217, '58:4'),
(5109, NULL, NULL, 58, 5, 'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥ كُبِتُوا۟ كَمَا كُبِتَ ٱلَّذِينَ مِن قَبْلِهِمْ وَقَدْ أَنزَلْنَآ ءَايَٰتٍۭ بَيِّنَٰتٍ وَلِلْكَٰفِرِينَ عَذَابٌ مُّهِينٌ', 'إن الذين يحادون الله ورسوله كبتوا كما كبت الذين من قبلهم وقد أنزلنا آيات بينات وللكافرين عذاب مهين', 28, 55, 542, 217, '58:5'),
(5110, NULL, NULL, 58, 6, 'يَوْمَ يَبْعَثُهُمُ ٱللَّهُ جَمِيعًا فَيُنَبِّئُهُم بِمَا عَمِلُوٓا۟ أَحْصَىٰهُ ٱللَّهُ وَنَسُوهُ وَٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ شَهِيدٌ', 'يوم يبعثهم الله جميعا فينبئهم بما عملوا أحصاه الله ونسوه والله على كل شيء شهيد', 28, 55, 542, 217, '58:6'),
(5111, NULL, NULL, 58, 7, 'أَلَمْ تَرَ أَنَّ ٱللَّهَ يَعْلَمُ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ مَا يَكُونُ مِن نَّجْوَىٰ ثَلَٰثَةٍ إِلَّا هُوَ رَابِعُهُمْ وَلَا خَمْسَةٍ إِلَّا هُوَ سَادِسُهُمْ وَلَآ أَدْنَىٰ مِن ذَٰلِكَ وَلَآ أَكْثَرَ إِلَّا هُوَ مَعَهُمْ أَيْنَ مَا كَانُوا۟ ثُمَّ يُنَبِّئُهُم بِمَا عَمِلُوا۟ يَوْمَ ٱلْقِيَٰمَةِ إِنَّ ٱللَّهَ بِكُلِّ شَىْءٍ عَلِيمٌ', 'ألم تر أن الله يعلم ما في السماوات وما في الأرض ما يكون من نجوى ثلاثة إلا هو رابعهم ولا خمسة إلا هو سادسهم ولا أدنى من ذلك ولا أكثر إلا هو معهم أين ما كانوا ثم ينبئهم بما عملوا يوم القيامة إن الله بكل شيء عليم', 28, 55, 543, 217, '58:7'),
(5112, NULL, NULL, 58, 8, 'أَلَمْ تَرَ إِلَى ٱلَّذِينَ نُهُوا۟ عَنِ ٱلنَّجْوَىٰ ثُمَّ يَعُودُونَ لِمَا نُهُوا۟ عَنْهُ وَيَتَنَٰجَوْنَ بِٱلْإِثْمِ وَٱلْعُدْوَٰنِ وَمَعْصِيَتِ ٱلرَّسُولِ وَإِذَا جَآءُوكَ حَيَّوْكَ بِمَا لَمْ يُحَيِّكَ بِهِ ٱللَّهُ وَيَقُولُونَ فِىٓ أَنفُسِهِمْ لَوْلَا يُعَذِّبُنَا ٱللَّهُ بِمَا نَقُولُ حَسْبُهُمْ جَهَنَّمُ يَصْلَوْنَهَا فَبِئْسَ ٱلْمَصِيرُ', 'ألم تر إلى الذين نهوا عن النجوى ثم يعودون لما نهوا عنه ويتناجون بالإثم والعدوان ومعصيت الرسول وإذا جاءوك حيوك بما لم يحيك به الله ويقولون في أنفسهم لولا يعذبنا الله بما نقول حسبهم جهنم يصلونها فبئس المصير', 28, 55, 543, 217, '58:8'),
(5113, NULL, NULL, 58, 9, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا تَنَٰجَيْتُمْ فَلَا تَتَنَٰجَوْا۟ بِٱلْإِثْمِ وَٱلْعُدْوَٰنِ وَمَعْصِيَتِ ٱلرَّسُولِ وَتَنَٰجَوْا۟ بِٱلْبِرِّ وَٱلتَّقْوَىٰ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِىٓ إِلَيْهِ تُحْشَرُونَ', 'يا أيها الذين آمنوا إذا تناجيتم فلا تتناجوا بالإثم والعدوان ومعصيت الرسول وتناجوا بالبر والتقوى واتقوا الله الذي إليه تحشرون', 28, 55, 543, 217, '58:9'),
(5114, NULL, NULL, 58, 10, 'إِنَّمَا ٱلنَّجْوَىٰ مِنَ ٱلشَّيْطَٰنِ لِيَحْزُنَ ٱلَّذِينَ ءَامَنُوا۟ وَلَيْسَ بِضَآرِّهِمْ شَيْـًٔا إِلَّا بِإِذْنِ ٱللَّهِ وَعَلَى ٱللَّهِ فَلْيَتَوَكَّلِ ٱلْمُؤْمِنُونَ', 'إنما النجوى من الشيطان ليحزن الذين آمنوا وليس بضارهم شيئا إلا بإذن الله وعلى الله فليتوكل المؤمنون', 28, 55, 543, 217, '58:10'),
(5115, NULL, NULL, 58, 11, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا قِيلَ لَكُمْ تَفَسَّحُوا۟ فِى ٱلْمَجَٰلِسِ فَٱفْسَحُوا۟ يَفْسَحِ ٱللَّهُ لَكُمْ وَإِذَا قِيلَ ٱنشُزُوا۟ فَٱنشُزُوا۟ يَرْفَعِ ٱللَّهُ ٱلَّذِينَ ءَامَنُوا۟ مِنكُمْ وَٱلَّذِينَ أُوتُوا۟ ٱلْعِلْمَ دَرَجَٰتٍ وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'يا أيها الذين آمنوا إذا قيل لكم تفسحوا في المجالس فافسحوا يفسح الله لكم وإذا قيل انشزوا فانشزوا يرفع الله الذين آمنوا منكم والذين أوتوا العلم درجات والله بما تعملون خبير', 28, 55, 543, 217, '58:11'),
(5116, NULL, NULL, 58, 12, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا نَٰجَيْتُمُ ٱلرَّسُولَ فَقَدِّمُوا۟ بَيْنَ يَدَىْ نَجْوَىٰكُمْ صَدَقَةً ذَٰلِكَ خَيْرٌ لَّكُمْ وَأَطْهَرُ فَإِن لَّمْ تَجِدُوا۟ فَإِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ', 'يا أيها الذين آمنوا إذا ناجيتم الرسول فقدموا بين يدي نجواكم صدقة ذلك خير لكم وأطهر فإن لم تجدوا فإن الله غفور رحيم', 28, 55, 544, 217, '58:12'),
(5117, NULL, NULL, 58, 13, 'ءَأَشْفَقْتُمْ أَن تُقَدِّمُوا۟ بَيْنَ يَدَىْ نَجْوَىٰكُمْ صَدَقَٰتٍ فَإِذْ لَمْ تَفْعَلُوا۟ وَتَابَ ٱللَّهُ عَلَيْكُمْ فَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَطِيعُوا۟ ٱللَّهَ وَرَسُولَهُۥ وَٱللَّهُ خَبِيرٌۢ بِمَا تَعْمَلُونَ', 'أأشفقتم أن تقدموا بين يدي نجواكم صدقات فإذ لم تفعلوا وتاب الله عليكم فأقيموا الصلاة وآتوا الزكاة وأطيعوا الله ورسوله والله خبير بما تعملون', 28, 55, 544, 217, '58:13'),
(5118, NULL, NULL, 58, 14, 'أَلَمْ تَرَ إِلَى ٱلَّذِينَ تَوَلَّوْا۟ قَوْمًا غَضِبَ ٱللَّهُ عَلَيْهِم مَّا هُم مِّنكُمْ وَلَا مِنْهُمْ وَيَحْلِفُونَ عَلَى ٱلْكَذِبِ وَهُمْ يَعْلَمُونَ', 'ألم تر إلى الذين تولوا قوما غضب الله عليهم ما هم منكم ولا منهم ويحلفون على الكذب وهم يعلمون', 28, 55, 544, 218, '58:14'),
(5119, NULL, NULL, 58, 15, 'أَعَدَّ ٱللَّهُ لَهُمْ عَذَابًا شَدِيدًا إِنَّهُمْ سَآءَ مَا كَانُوا۟ يَعْمَلُونَ', 'أعد الله لهم عذابا شديدا إنهم ساء ما كانوا يعملون', 28, 55, 544, 218, '58:15'),
(5120, NULL, NULL, 58, 16, 'ٱتَّخَذُوٓا۟ أَيْمَٰنَهُمْ جُنَّةً فَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ فَلَهُمْ عَذَابٌ مُّهِينٌ', 'اتخذوا أيمانهم جنة فصدوا عن سبيل الله فلهم عذاب مهين', 28, 55, 544, 218, '58:16'),
(5121, NULL, NULL, 58, 17, 'لَّن تُغْنِىَ عَنْهُمْ أَمْوَٰلُهُمْ وَلَآ أَوْلَٰدُهُم مِّنَ ٱللَّهِ شَيْـًٔا أُو۟لَٰٓئِكَ أَصْحَٰبُ ٱلنَّارِ هُمْ فِيهَا خَٰلِدُونَ', 'لن تغني عنهم أموالهم ولا أولادهم من الله شيئا أولئك أصحاب النار هم فيها خالدون', 28, 55, 544, 218, '58:17'),
(5122, NULL, NULL, 58, 18, 'يَوْمَ يَبْعَثُهُمُ ٱللَّهُ جَمِيعًا فَيَحْلِفُونَ لَهُۥ كَمَا يَحْلِفُونَ لَكُمْ وَيَحْسَبُونَ أَنَّهُمْ عَلَىٰ شَىْءٍ أَلَآ إِنَّهُمْ هُمُ ٱلْكَٰذِبُونَ', 'يوم يبعثهم الله جميعا فيحلفون له كما يحلفون لكم ويحسبون أنهم على شيء ألا إنهم هم الكاذبون', 28, 55, 544, 218, '58:18'),
(5123, NULL, NULL, 58, 19, 'ٱسْتَحْوَذَ عَلَيْهِمُ ٱلشَّيْطَٰنُ فَأَنسَىٰهُمْ ذِكْرَ ٱللَّهِ أُو۟لَٰٓئِكَ حِزْبُ ٱلشَّيْطَٰنِ أَلَآ إِنَّ حِزْبَ ٱلشَّيْطَٰنِ هُمُ ٱلْخَٰسِرُونَ', 'استحوذ عليهم الشيطان فأنساهم ذكر الله أولئك حزب الشيطان ألا إن حزب الشيطان هم الخاسرون', 28, 55, 544, 218, '58:19'),
(5124, NULL, NULL, 58, 20, 'إِنَّ ٱلَّذِينَ يُحَآدُّونَ ٱللَّهَ وَرَسُولَهُۥٓ أُو۟لَٰٓئِكَ فِى ٱلْأَذَلِّينَ', 'إن الذين يحادون الله ورسوله أولئك في الأذلين', 28, 55, 544, 218, '58:20'),
(5125, NULL, NULL, 58, 21, 'كَتَبَ ٱللَّهُ لَأَغْلِبَنَّ أَنَا۠ وَرُسُلِىٓ إِنَّ ٱللَّهَ قَوِىٌّ عَزِيزٌ', 'كتب الله لأغلبن أنا ورسلي إن الله قوي عزيز', 28, 55, 544, 218, '58:21'),
(5126, NULL, NULL, 58, 22, 'لَّا تَجِدُ قَوْمًا يُؤْمِنُونَ بِٱللَّهِ وَٱلْيَوْمِ ٱلْءَاخِرِ يُوَآدُّونَ مَنْ حَآدَّ ٱللَّهَ وَرَسُولَهُۥ وَلَوْ كَانُوٓا۟ ءَابَآءَهُمْ أَوْ أَبْنَآءَهُمْ أَوْ إِخْوَٰنَهُمْ أَوْ عَشِيرَتَهُمْ أُو۟لَٰٓئِكَ كَتَبَ فِى قُلُوبِهِمُ ٱلْإِيمَٰنَ وَأَيَّدَهُم بِرُوحٍ مِّنْهُ وَيُدْخِلُهُمْ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَا رَضِىَ ٱللَّهُ عَنْهُمْ وَرَضُوا۟ عَنْهُ أُو۟لَٰٓئِكَ حِزْبُ ٱللَّهِ أَلَآ إِنَّ حِزْبَ ٱللَّهِ هُمُ ٱلْمُفْلِحُونَ', 'لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله ولو كانوا آباءهم أو أبناءهم أو إخوانهم أو عشيرتهم أولئك كتب في قلوبهم الإيمان وأيدهم بروح منه ويدخلهم جنات تجري من تحتها الأنهار خالدين فيها رضي الله عنهم ورضوا عنه أولئك حزب الله ألا إن حزب الله هم المفلحون', 28, 55, 545, 218, '58:22'),
(5127, NULL, NULL, 59, 1, 'سَبَّحَ لِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'سبح لله ما في السماوات وما في الأرض وهو العزيز الحكيم', 28, 55, 545, 218, '59:1'),
(5128, NULL, NULL, 59, 2, 'هُوَ ٱلَّذِىٓ أَخْرَجَ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَٰبِ مِن دِيَٰرِهِمْ لِأَوَّلِ ٱلْحَشْرِ مَا ظَنَنتُمْ أَن يَخْرُجُوا۟ وَظَنُّوٓا۟ أَنَّهُم مَّانِعَتُهُمْ حُصُونُهُم مِّنَ ٱللَّهِ فَأَتَىٰهُمُ ٱللَّهُ مِنْ حَيْثُ لَمْ يَحْتَسِبُوا۟ وَقَذَفَ فِى قُلُوبِهِمُ ٱلرُّعْبَ يُخْرِبُونَ بُيُوتَهُم بِأَيْدِيهِمْ وَأَيْدِى ٱلْمُؤْمِنِينَ فَٱعْتَبِرُوا۟ يَٰٓأُو۟لِى ٱلْأَبْصَٰرِ', 'هو الذي أخرج الذين كفروا من أهل الكتاب من ديارهم لأول الحشر ما ظننتم أن يخرجوا وظنوا أنهم مانعتهم حصونهم من الله فأتاهم الله من حيث لم يحتسبوا وقذف في قلوبهم الرعب يخربون بيوتهم بأيديهم وأيدي المؤمنين فاعتبروا يا أولي الأبصار', 28, 55, 545, 218, '59:2'),
(5129, NULL, NULL, 59, 3, 'وَلَوْلَآ أَن كَتَبَ ٱللَّهُ عَلَيْهِمُ ٱلْجَلَآءَ لَعَذَّبَهُمْ فِى ٱلدُّنْيَا وَلَهُمْ فِى ٱلْءَاخِرَةِ عَذَابُ ٱلنَّارِ', 'ولولا أن كتب الله عليهم الجلاء لعذبهم في الدنيا ولهم في الآخرة عذاب النار', 28, 55, 545, 218, '59:3'),
(5130, NULL, NULL, 59, 4, 'ذَٰلِكَ بِأَنَّهُمْ شَآقُّوا۟ ٱللَّهَ وَرَسُولَهُۥ وَمَن يُشَآقِّ ٱللَّهَ فَإِنَّ ٱللَّهَ شَدِيدُ ٱلْعِقَابِ', 'ذلك بأنهم شاقوا الله ورسوله ومن يشاق الله فإن الله شديد العقاب', 28, 55, 546, 218, '59:4'),
(5131, NULL, NULL, 59, 5, 'مَا قَطَعْتُم مِّن لِّينَةٍ أَوْ تَرَكْتُمُوهَا قَآئِمَةً عَلَىٰٓ أُصُولِهَا فَبِإِذْنِ ٱللَّهِ وَلِيُخْزِىَ ٱلْفَٰسِقِينَ', 'ما قطعتم من لينة أو تركتموها قائمة على أصولها فبإذن الله وليخزي الفاسقين', 28, 55, 546, 218, '59:5'),
(5132, NULL, NULL, 59, 6, 'وَمَآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنْهُمْ فَمَآ أَوْجَفْتُمْ عَلَيْهِ مِنْ خَيْلٍ وَلَا رِكَابٍ وَلَٰكِنَّ ٱللَّهَ يُسَلِّطُ رُسُلَهُۥ عَلَىٰ مَن يَشَآءُ وَٱللَّهُ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'وما أفاء الله على رسوله منهم فما أوجفتم عليه من خيل ولا ركاب ولكن الله يسلط رسله على من يشاء والله على كل شيء قدير', 28, 55, 546, 218, '59:6'),
(5133, NULL, NULL, 59, 7, 'مَّآ أَفَآءَ ٱللَّهُ عَلَىٰ رَسُولِهِۦ مِنْ أَهْلِ ٱلْقُرَىٰ فَلِلَّهِ وَلِلرَّسُولِ وَلِذِى ٱلْقُرْبَىٰ وَٱلْيَتَٰمَىٰ وَٱلْمَسَٰكِينِ وَٱبْنِ ٱلسَّبِيلِ كَىْ لَا يَكُونَ دُولَةًۢ بَيْنَ ٱلْأَغْنِيَآءِ مِنكُمْ وَمَآ ءَاتَىٰكُمُ ٱلرَّسُولُ فَخُذُوهُ وَمَا نَهَىٰكُمْ عَنْهُ فَٱنتَهُوا۟ وَٱتَّقُوا۟ ٱللَّهَ إِنَّ ٱللَّهَ شَدِيدُ ٱلْعِقَابِ', 'ما أفاء الله على رسوله من أهل القرى فلله وللرسول ولذي القربى واليتامى والمساكين وابن السبيل كي لا يكون دولة بين الأغنياء منكم وما آتاكم الرسول فخذوه وما نهاكم عنه فانتهوا واتقوا الله إن الله شديد العقاب', 28, 55, 546, 218, '59:7'),
(5134, NULL, NULL, 59, 8, 'لِلْفُقَرَآءِ ٱلْمُهَٰجِرِينَ ٱلَّذِينَ أُخْرِجُوا۟ مِن دِيَٰرِهِمْ وَأَمْوَٰلِهِمْ يَبْتَغُونَ فَضْلًا مِّنَ ٱللَّهِ وَرِضْوَٰنًا وَيَنصُرُونَ ٱللَّهَ وَرَسُولَهُۥٓ أُو۟لَٰٓئِكَ هُمُ ٱلصَّٰدِقُونَ', 'للفقراء المهاجرين الذين أخرجوا من ديارهم وأموالهم يبتغون فضلا من الله ورضوانا وينصرون الله ورسوله أولئك هم الصادقون', 28, 55, 546, 218, '59:8'),
(5135, NULL, NULL, 59, 9, 'وَٱلَّذِينَ تَبَوَّءُو ٱلدَّارَ وَٱلْإِيمَٰنَ مِن قَبْلِهِمْ يُحِبُّونَ مَنْ هَاجَرَ إِلَيْهِمْ وَلَا يَجِدُونَ فِى صُدُورِهِمْ حَاجَةً مِّمَّآ أُوتُوا۟ وَيُؤْثِرُونَ عَلَىٰٓ أَنفُسِهِمْ وَلَوْ كَانَ بِهِمْ خَصَاصَةٌ وَمَن يُوقَ شُحَّ نَفْسِهِۦ فَأُو۟لَٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ', 'والذين تبوءوا الدار والإيمان من قبلهم يحبون من هاجر إليهم ولا يجدون في صدورهم حاجة مما أوتوا ويؤثرون على أنفسهم ولو كان بهم خصاصة ومن يوق شح نفسه فأولئك هم المفلحون', 28, 55, 546, 218, '59:9'),
(5136, NULL, NULL, 59, 10, 'وَٱلَّذِينَ جَآءُو مِنۢ بَعْدِهِمْ يَقُولُونَ رَبَّنَا ٱغْفِرْ لَنَا وَلِإِخْوَٰنِنَا ٱلَّذِينَ سَبَقُونَا بِٱلْإِيمَٰنِ وَلَا تَجْعَلْ فِى قُلُوبِنَا غِلًّا لِّلَّذِينَ ءَامَنُوا۟ رَبَّنَآ إِنَّكَ رَءُوفٌ رَّحِيمٌ', 'والذين جاءوا من بعدهم يقولون ربنا اغفر لنا ولإخواننا الذين سبقونا بالإيمان ولا تجعل في قلوبنا غلا للذين آمنوا ربنا إنك رءوف رحيم', 28, 55, 547, 218, '59:10'),
(5137, NULL, NULL, 59, 11, 'أَلَمْ تَرَ إِلَى ٱلَّذِينَ نَافَقُوا۟ يَقُولُونَ لِإِخْوَٰنِهِمُ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَٰبِ لَئِنْ أُخْرِجْتُمْ لَنَخْرُجَنَّ مَعَكُمْ وَلَا نُطِيعُ فِيكُمْ أَحَدًا أَبَدًا وَإِن قُوتِلْتُمْ لَنَنصُرَنَّكُمْ وَٱللَّهُ يَشْهَدُ إِنَّهُمْ لَكَٰذِبُونَ', 'ألم تر إلى الذين نافقوا يقولون لإخوانهم الذين كفروا من أهل الكتاب لئن أخرجتم لنخرجن معكم ولا نطيع فيكم أحدا أبدا وإن قوتلتم لننصرنكم والله يشهد إنهم لكاذبون', 28, 55, 547, 219, '59:11'),
(5138, NULL, NULL, 59, 12, 'لَئِنْ أُخْرِجُوا۟ لَا يَخْرُجُونَ مَعَهُمْ وَلَئِن قُوتِلُوا۟ لَا يَنصُرُونَهُمْ وَلَئِن نَّصَرُوهُمْ لَيُوَلُّنَّ ٱلْأَدْبَٰرَ ثُمَّ لَا يُنصَرُونَ', 'لئن أخرجوا لا يخرجون معهم ولئن قوتلوا لا ينصرونهم ولئن نصروهم ليولن الأدبار ثم لا ينصرون', 28, 55, 547, 219, '59:12'),
(5139, NULL, NULL, 59, 13, 'لَأَنتُمْ أَشَدُّ رَهْبَةً فِى صُدُورِهِم مِّنَ ٱللَّهِ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَفْقَهُونَ', 'لأنتم أشد رهبة في صدورهم من الله ذلك بأنهم قوم لا يفقهون', 28, 55, 547, 219, '59:13'),
(5140, NULL, NULL, 59, 14, 'لَا يُقَٰتِلُونَكُمْ جَمِيعًا إِلَّا فِى قُرًى مُّحَصَّنَةٍ أَوْ مِن وَرَآءِ جُدُرٍۭ بَأْسُهُم بَيْنَهُمْ شَدِيدٌ تَحْسَبُهُمْ جَمِيعًا وَقُلُوبُهُمْ شَتَّىٰ ذَٰلِكَ بِأَنَّهُمْ قَوْمٌ لَّا يَعْقِلُونَ', 'لا يقاتلونكم جميعا إلا في قرى محصنة أو من وراء جدر بأسهم بينهم شديد تحسبهم جميعا وقلوبهم شتى ذلك بأنهم قوم لا يعقلون', 28, 55, 547, 219, '59:14'),
(5141, NULL, NULL, 59, 15, 'كَمَثَلِ ٱلَّذِينَ مِن قَبْلِهِمْ قَرِيبًا ذَاقُوا۟ وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 'كمثل الذين من قبلهم قريبا ذاقوا وبال أمرهم ولهم عذاب أليم', 28, 55, 547, 219, '59:15'),
(5142, NULL, NULL, 59, 16, 'كَمَثَلِ ٱلشَّيْطَٰنِ إِذْ قَالَ لِلْإِنسَٰنِ ٱكْفُرْ فَلَمَّا كَفَرَ قَالَ إِنِّى بَرِىٓءٌ مِّنكَ إِنِّىٓ أَخَافُ ٱللَّهَ رَبَّ ٱلْعَٰلَمِينَ', 'كمثل الشيطان إذ قال للإنسان اكفر فلما كفر قال إني بريء منك إني أخاف الله رب العالمين', 28, 55, 547, 219, '59:16'),
(5143, NULL, NULL, 59, 17, 'فَكَانَ عَٰقِبَتَهُمَآ أَنَّهُمَا فِى ٱلنَّارِ خَٰلِدَيْنِ فِيهَا وَذَٰلِكَ جَزَٰٓؤُا۟ ٱلظَّٰلِمِينَ', 'فكان عاقبتهما أنهما في النار خالدين فيها وذلك جزاء الظالمين', 28, 55, 548, 219, '59:17'),
(5144, NULL, NULL, 59, 18, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ ٱتَّقُوا۟ ٱللَّهَ وَلْتَنظُرْ نَفْسٌ مَّا قَدَّمَتْ لِغَدٍ وَٱتَّقُوا۟ ٱللَّهَ إِنَّ ٱللَّهَ خَبِيرٌۢ بِمَا تَعْمَلُونَ', 'يا أيها الذين آمنوا اتقوا الله ولتنظر نفس ما قدمت لغد واتقوا الله إن الله خبير بما تعملون', 28, 55, 548, 219, '59:18'),
(5145, NULL, NULL, 59, 19, 'وَلَا تَكُونُوا۟ كَٱلَّذِينَ نَسُوا۟ ٱللَّهَ فَأَنسَىٰهُمْ أَنفُسَهُمْ أُو۟لَٰٓئِكَ هُمُ ٱلْفَٰسِقُونَ', 'ولا تكونوا كالذين نسوا الله فأنساهم أنفسهم أولئك هم الفاسقون', 28, 55, 548, 219, '59:19'),
(5146, NULL, NULL, 59, 20, 'لَا يَسْتَوِىٓ أَصْحَٰبُ ٱلنَّارِ وَأَصْحَٰبُ ٱلْجَنَّةِ أَصْحَٰبُ ٱلْجَنَّةِ هُمُ ٱلْفَآئِزُونَ', 'لا يستوي أصحاب النار وأصحاب الجنة أصحاب الجنة هم الفائزون', 28, 55, 548, 219, '59:20'),
(5147, NULL, NULL, 59, 21, 'لَوْ أَنزَلْنَا هَٰذَا ٱلْقُرْءَانَ عَلَىٰ جَبَلٍ لَّرَأَيْتَهُۥ خَٰشِعًا مُّتَصَدِّعًا مِّنْ خَشْيَةِ ٱللَّهِ وَتِلْكَ ٱلْأَمْثَٰلُ نَضْرِبُهَا لِلنَّاسِ لَعَلَّهُمْ يَتَفَكَّرُونَ', 'لو أنزلنا هذا القرآن على جبل لرأيته خاشعا متصدعا من خشية الله وتلك الأمثال نضربها للناس لعلهم يتفكرون', 28, 55, 548, 219, '59:21'),
(5148, NULL, NULL, 59, 22, 'هُوَ ٱللَّهُ ٱلَّذِى لَآ إِلَٰهَ إِلَّا هُوَ عَٰلِمُ ٱلْغَيْبِ وَٱلشَّهَٰدَةِ هُوَ ٱلرَّحْمَٰنُ ٱلرَّحِيمُ', 'هو الله الذي لا إله إلا هو عالم الغيب والشهادة هو الرحمن الرحيم', 28, 55, 548, 219, '59:22'),
(5149, NULL, NULL, 59, 23, 'هُوَ ٱللَّهُ ٱلَّذِى لَآ إِلَٰهَ إِلَّا هُوَ ٱلْمَلِكُ ٱلْقُدُّوسُ ٱلسَّلَٰمُ ٱلْمُؤْمِنُ ٱلْمُهَيْمِنُ ٱلْعَزِيزُ ٱلْجَبَّارُ ٱلْمُتَكَبِّرُ سُبْحَٰنَ ٱللَّهِ عَمَّا يُشْرِكُونَ', 'هو الله الذي لا إله إلا هو الملك القدوس السلام المؤمن المهيمن العزيز الجبار المتكبر سبحان الله عما يشركون', 28, 55, 548, 219, '59:23'),
(5150, NULL, NULL, 59, 24, 'هُوَ ٱللَّهُ ٱلْخَٰلِقُ ٱلْبَارِئُ ٱلْمُصَوِّرُ لَهُ ٱلْأَسْمَآءُ ٱلْحُسْنَىٰ يُسَبِّحُ لَهُۥ مَا فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'هو الله الخالق البارئ المصور له الأسماء الحسنى يسبح له ما في السماوات والأرض وهو العزيز الحكيم', 28, 55, 548, 219, '59:24'),
(5151, NULL, NULL, 60, 1, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَتَّخِذُوا۟ عَدُوِّى وَعَدُوَّكُمْ أَوْلِيَآءَ تُلْقُونَ إِلَيْهِم بِٱلْمَوَدَّةِ وَقَدْ كَفَرُوا۟ بِمَا جَآءَكُم مِّنَ ٱلْحَقِّ يُخْرِجُونَ ٱلرَّسُولَ وَإِيَّاكُمْ أَن تُؤْمِنُوا۟ بِٱللَّهِ رَبِّكُمْ إِن كُنتُمْ خَرَجْتُمْ جِهَٰدًا فِى سَبِيلِى وَٱبْتِغَآءَ مَرْضَاتِى تُسِرُّونَ إِلَيْهِم بِٱلْمَوَدَّةِ وَأَنَا۠ أَعْلَمُ بِمَآ أَخْفَيْتُمْ وَمَآ أَعْلَنتُمْ وَمَن يَفْعَلْهُ مِنكُمْ فَقَدْ ضَلَّ سَوَآءَ ٱلسَّبِيلِ', 'يا أيها الذين آمنوا لا تتخذوا عدوي وعدوكم أولياء تلقون إليهم بالمودة وقد كفروا بما جاءكم من الحق يخرجون الرسول وإياكم أن تؤمنوا بالله ربكم إن كنتم خرجتم جهادا في سبيلي وابتغاء مرضاتي تسرون إليهم بالمودة وأنا أعلم بما أخفيتم وما أعلنتم ومن يفعله منكم فقد ضل سواء السبيل', 28, 55, 549, 219, '60:1'),
(5152, NULL, NULL, 60, 2, 'إِن يَثْقَفُوكُمْ يَكُونُوا۟ لَكُمْ أَعْدَآءً وَيَبْسُطُوٓا۟ إِلَيْكُمْ أَيْدِيَهُمْ وَأَلْسِنَتَهُم بِٱلسُّوٓءِ وَوَدُّوا۟ لَوْ تَكْفُرُونَ', 'إن يثقفوكم يكونوا لكم أعداء ويبسطوا إليكم أيديهم وألسنتهم بالسوء وودوا لو تكفرون', 28, 55, 549, 219, '60:2'),
(5153, NULL, NULL, 60, 3, 'لَن تَنفَعَكُمْ أَرْحَامُكُمْ وَلَآ أَوْلَٰدُكُمْ يَوْمَ ٱلْقِيَٰمَةِ يَفْصِلُ بَيْنَكُمْ وَٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 'لن تنفعكم أرحامكم ولا أولادكم يوم القيامة يفصل بينكم والله بما تعملون بصير', 28, 55, 549, 219, '60:3'),
(5154, NULL, NULL, 60, 4, 'قَدْ كَانَتْ لَكُمْ أُسْوَةٌ حَسَنَةٌ فِىٓ إِبْرَٰهِيمَ وَٱلَّذِينَ مَعَهُۥٓ إِذْ قَالُوا۟ لِقَوْمِهِمْ إِنَّا بُرَءَٰٓؤُا۟ مِنكُمْ وَمِمَّا تَعْبُدُونَ مِن دُونِ ٱللَّهِ كَفَرْنَا بِكُمْ وَبَدَا بَيْنَنَا وَبَيْنَكُمُ ٱلْعَدَٰوَةُ وَٱلْبَغْضَآءُ أَبَدًا حَتَّىٰ تُؤْمِنُوا۟ بِٱللَّهِ وَحْدَهُۥٓ إِلَّا قَوْلَ إِبْرَٰهِيمَ لِأَبِيهِ لَأَسْتَغْفِرَنَّ لَكَ وَمَآ أَمْلِكُ لَكَ مِنَ ٱللَّهِ مِن شَىْءٍ رَّبَّنَا عَلَيْكَ تَوَكَّلْنَا وَإِلَيْكَ أَنَبْنَا وَإِلَيْكَ ٱلْمَصِيرُ', 'قد كانت لكم أسوة حسنة في إبراهيم والذين معه إذ قالوا لقومهم إنا برآء منكم ومما تعبدون من دون الله كفرنا بكم وبدا بيننا وبينكم العداوة والبغضاء أبدا حتى تؤمنوا بالله وحده إلا قول إبراهيم لأبيه لأستغفرن لك وما أملك لك من الله من شيء ربنا عليك توكلنا وإليك أنبنا وإليك المصير', 28, 55, 549, 219, '60:4'),
(5155, NULL, NULL, 60, 5, 'رَبَّنَا لَا تَجْعَلْنَا فِتْنَةً لِّلَّذِينَ كَفَرُوا۟ وَٱغْفِرْ لَنَا رَبَّنَآ إِنَّكَ أَنتَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'ربنا لا تجعلنا فتنة للذين كفروا واغفر لنا ربنا إنك أنت العزيز الحكيم', 28, 55, 549, 219, '60:5'),
(5156, NULL, NULL, 60, 6, 'لَقَدْ كَانَ لَكُمْ فِيهِمْ أُسْوَةٌ حَسَنَةٌ لِّمَن كَانَ يَرْجُوا۟ ٱللَّهَ وَٱلْيَوْمَ ٱلْءَاخِرَ وَمَن يَتَوَلَّ فَإِنَّ ٱللَّهَ هُوَ ٱلْغَنِىُّ ٱلْحَمِيدُ', 'لقد كان لكم فيهم أسوة حسنة لمن كان يرجو الله واليوم الآخر ومن يتول فإن الله هو الغني الحميد', 28, 55, 550, 219, '60:6'),
(5157, NULL, NULL, 60, 7, 'عَسَى ٱللَّهُ أَن يَجْعَلَ بَيْنَكُمْ وَبَيْنَ ٱلَّذِينَ عَادَيْتُم مِّنْهُم مَّوَدَّةً وَٱللَّهُ قَدِيرٌ وَٱللَّهُ غَفُورٌ رَّحِيمٌ', 'عسى الله أن يجعل بينكم وبين الذين عاديتم منهم مودة والله قدير والله غفور رحيم', 28, 55, 550, 220, '60:7'),
(5158, NULL, NULL, 60, 8, 'لَّا يَنْهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ لَمْ يُقَٰتِلُوكُمْ فِى ٱلدِّينِ وَلَمْ يُخْرِجُوكُم مِّن دِيَٰرِكُمْ أَن تَبَرُّوهُمْ وَتُقْسِطُوٓا۟ إِلَيْهِمْ إِنَّ ٱللَّهَ يُحِبُّ ٱلْمُقْسِطِينَ', 'لا ينهاكم الله عن الذين لم يقاتلوكم في الدين ولم يخرجوكم من دياركم أن تبروهم وتقسطوا إليهم إن الله يحب المقسطين', 28, 55, 550, 220, '60:8'),
(5159, NULL, NULL, 60, 9, 'إِنَّمَا يَنْهَىٰكُمُ ٱللَّهُ عَنِ ٱلَّذِينَ قَٰتَلُوكُمْ فِى ٱلدِّينِ وَأَخْرَجُوكُم مِّن دِيَٰرِكُمْ وَظَٰهَرُوا۟ عَلَىٰٓ إِخْرَاجِكُمْ أَن تَوَلَّوْهُمْ وَمَن يَتَوَلَّهُمْ فَأُو۟لَٰٓئِكَ هُمُ ٱلظَّٰلِمُونَ', 'إنما ينهاكم الله عن الذين قاتلوكم في الدين وأخرجوكم من دياركم وظاهروا على إخراجكم أن تولوهم ومن يتولهم فأولئك هم الظالمون', 28, 55, 550, 220, '60:9'),
(5160, NULL, NULL, 60, 10, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا جَآءَكُمُ ٱلْمُؤْمِنَٰتُ مُهَٰجِرَٰتٍ فَٱمْتَحِنُوهُنَّ ٱللَّهُ أَعْلَمُ بِإِيمَٰنِهِنَّ فَإِنْ عَلِمْتُمُوهُنَّ مُؤْمِنَٰتٍ فَلَا تَرْجِعُوهُنَّ إِلَى ٱلْكُفَّارِ لَا هُنَّ حِلٌّ لَّهُمْ وَلَا هُمْ يَحِلُّونَ لَهُنَّ وَءَاتُوهُم مَّآ أَنفَقُوا۟ وَلَا جُنَاحَ عَلَيْكُمْ أَن تَنكِحُوهُنَّ إِذَآ ءَاتَيْتُمُوهُنَّ أُجُورَهُنَّ وَلَا تُمْسِكُوا۟ بِعِصَمِ ٱلْكَوَافِرِ وَسْـَٔلُوا۟ مَآ أَنفَقْتُمْ وَلْيَسْـَٔلُوا۟ مَآ أَنفَقُوا۟ ذَٰلِكُمْ حُكْمُ ٱللَّهِ يَحْكُمُ بَيْنَكُمْ وَٱللَّهُ عَلِيمٌ حَكِيمٌ', 'يا أيها الذين آمنوا إذا جاءكم المؤمنات مهاجرات فامتحنوهن الله أعلم بإيمانهن فإن علمتموهن مؤمنات فلا ترجعوهن إلى الكفار لا هن حل لهم ولا هم يحلون لهن وآتوهم ما أنفقوا ولا جناح عليكم أن تنكحوهن إذا آتيتموهن أجورهن ولا تمسكوا بعصم الكوافر واسألوا ما أنفقتم وليسألوا ما أنفقوا ذلكم حكم الله يحكم بينكم والله عليم حكيم', 28, 55, 550, 220, '60:10'),
(5161, NULL, NULL, 60, 11, 'وَإِن فَاتَكُمْ شَىْءٌ مِّنْ أَزْوَٰجِكُمْ إِلَى ٱلْكُفَّارِ فَعَاقَبْتُمْ فَـَٔاتُوا۟ ٱلَّذِينَ ذَهَبَتْ أَزْوَٰجُهُم مِّثْلَ مَآ أَنفَقُوا۟ وَٱتَّقُوا۟ ٱللَّهَ ٱلَّذِىٓ أَنتُم بِهِۦ مُؤْمِنُونَ', 'وإن فاتكم شيء من أزواجكم إلى الكفار فعاقبتم فآتوا الذين ذهبت أزواجهم مثل ما أنفقوا واتقوا الله الذي أنتم به مؤمنون', 28, 55, 550, 220, '60:11'),
(5162, NULL, NULL, 60, 12, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ إِذَا جَآءَكَ ٱلْمُؤْمِنَٰتُ يُبَايِعْنَكَ عَلَىٰٓ أَن لَّا يُشْرِكْنَ بِٱللَّهِ شَيْـًٔا وَلَا يَسْرِقْنَ وَلَا يَزْنِينَ وَلَا يَقْتُلْنَ أَوْلَٰدَهُنَّ وَلَا يَأْتِينَ بِبُهْتَٰنٍ يَفْتَرِينَهُۥ بَيْنَ أَيْدِيهِنَّ وَأَرْجُلِهِنَّ وَلَا يَعْصِينَكَ فِى مَعْرُوفٍ فَبَايِعْهُنَّ وَٱسْتَغْفِرْ لَهُنَّ ٱللَّهَ إِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ', 'يا أيها النبي إذا جاءك المؤمنات يبايعنك على أن لا يشركن بالله شيئا ولا يسرقن ولا يزنين ولا يقتلن أولادهن ولا يأتين ببهتان يفترينه بين أيديهن وأرجلهن ولا يعصينك في معروف فبايعهن واستغفر لهن الله إن الله غفور رحيم', 28, 55, 551, 220, '60:12'),
(5163, NULL, NULL, 60, 13, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تَتَوَلَّوْا۟ قَوْمًا غَضِبَ ٱللَّهُ عَلَيْهِمْ قَدْ يَئِسُوا۟ مِنَ ٱلْءَاخِرَةِ كَمَا يَئِسَ ٱلْكُفَّارُ مِنْ أَصْحَٰبِ ٱلْقُبُورِ', 'يا أيها الذين آمنوا لا تتولوا قوما غضب الله عليهم قد يئسوا من الآخرة كما يئس الكفار من أصحاب القبور', 28, 55, 551, 220, '60:13'),
(5164, NULL, NULL, 61, 1, 'سَبَّحَ لِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'سبح لله ما في السماوات وما في الأرض وهو العزيز الحكيم', 28, 55, 551, 220, '61:1'),
(5165, NULL, NULL, 61, 2, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لِمَ تَقُولُونَ مَا لَا تَفْعَلُونَ', 'يا أيها الذين آمنوا لم تقولون ما لا تفعلون', 28, 55, 551, 220, '61:2'),
(5166, NULL, NULL, 61, 3, 'كَبُرَ مَقْتًا عِندَ ٱللَّهِ أَن تَقُولُوا۟ مَا لَا تَفْعَلُونَ', 'كبر مقتا عند الله أن تقولوا ما لا تفعلون', 28, 55, 551, 220, '61:3'),
(5167, NULL, NULL, 61, 4, 'إِنَّ ٱللَّهَ يُحِبُّ ٱلَّذِينَ يُقَٰتِلُونَ فِى سَبِيلِهِۦ صَفًّا كَأَنَّهُم بُنْيَٰنٌ مَّرْصُوصٌ', 'إن الله يحب الذين يقاتلون في سبيله صفا كأنهم بنيان مرصوص', 28, 55, 551, 220, '61:4'),
(5168, NULL, NULL, 61, 5, 'وَإِذْ قَالَ مُوسَىٰ لِقَوْمِهِۦ يَٰقَوْمِ لِمَ تُؤْذُونَنِى وَقَد تَّعْلَمُونَ أَنِّى رَسُولُ ٱللَّهِ إِلَيْكُمْ فَلَمَّا زَاغُوٓا۟ أَزَاغَ ٱللَّهُ قُلُوبَهُمْ وَٱللَّهُ لَا يَهْدِى ٱلْقَوْمَ ٱلْفَٰسِقِينَ', 'وإذ قال موسى لقومه يا قوم لم تؤذونني وقد تعلمون أني رسول الله إليكم فلما زاغوا أزاغ الله قلوبهم والله لا يهدي القوم الفاسقين', 28, 55, 551, 220, '61:5'),
(5169, NULL, NULL, 61, 6, 'وَإِذْ قَالَ عِيسَى ٱبْنُ مَرْيَمَ يَٰبَنِىٓ إِسْرَٰٓءِيلَ إِنِّى رَسُولُ ٱللَّهِ إِلَيْكُم مُّصَدِّقًا لِّمَا بَيْنَ يَدَىَّ مِنَ ٱلتَّوْرَىٰةِ وَمُبَشِّرًۢا بِرَسُولٍ يَأْتِى مِنۢ بَعْدِى ٱسْمُهُۥٓ أَحْمَدُ فَلَمَّا جَآءَهُم بِٱلْبَيِّنَٰتِ قَالُوا۟ هَٰذَا سِحْرٌ مُّبِينٌ', 'وإذ قال عيسى ابن مريم يا بني إسرائيل إني رسول الله إليكم مصدقا لما بين يدي من التوراة ومبشرا برسول يأتي من بعدي اسمه أحمد فلما جاءهم بالبينات قالوا هذا سحر مبين', 28, 55, 552, 220, '61:6'),
(5170, NULL, NULL, 61, 7, 'وَمَنْ أَظْلَمُ مِمَّنِ ٱفْتَرَىٰ عَلَى ٱللَّهِ ٱلْكَذِبَ وَهُوَ يُدْعَىٰٓ إِلَى ٱلْإِسْلَٰمِ وَٱللَّهُ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّٰلِمِينَ', 'ومن أظلم ممن افترى على الله الكذب وهو يدعى إلى الإسلام والله لا يهدي القوم الظالمين', 28, 55, 552, 220, '61:7'),
(5171, NULL, NULL, 61, 8, 'يُرِيدُونَ لِيُطْفِـُٔوا۟ نُورَ ٱللَّهِ بِأَفْوَٰهِهِمْ وَٱللَّهُ مُتِمُّ نُورِهِۦ وَلَوْ كَرِهَ ٱلْكَٰفِرُونَ', 'يريدون ليطفئوا نور الله بأفواههم والله متم نوره ولو كره الكافرون', 28, 55, 552, 220, '61:8'),
(5172, NULL, NULL, 61, 9, 'هُوَ ٱلَّذِىٓ أَرْسَلَ رَسُولَهُۥ بِٱلْهُدَىٰ وَدِينِ ٱلْحَقِّ لِيُظْهِرَهُۥ عَلَى ٱلدِّينِ كُلِّهِۦ وَلَوْ كَرِهَ ٱلْمُشْرِكُونَ', 'هو الذي أرسل رسوله بالهدى ودين الحق ليظهره على الدين كله ولو كره المشركون', 28, 55, 552, 220, '61:9'),
(5173, NULL, NULL, 61, 10, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ هَلْ أَدُلُّكُمْ عَلَىٰ تِجَٰرَةٍ تُنجِيكُم مِّنْ عَذَابٍ أَلِيمٍ', 'يا أيها الذين آمنوا هل أدلكم على تجارة تنجيكم من عذاب أليم', 28, 55, 552, 220, '61:10'),
(5174, NULL, NULL, 61, 11, 'تُؤْمِنُونَ بِٱللَّهِ وَرَسُولِهِۦ وَتُجَٰهِدُونَ فِى سَبِيلِ ٱللَّهِ بِأَمْوَٰلِكُمْ وَأَنفُسِكُمْ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ', 'تؤمنون بالله ورسوله وتجاهدون في سبيل الله بأموالكم وأنفسكم ذلكم خير لكم إن كنتم تعلمون', 28, 55, 552, 220, '61:11'),
(5175, NULL, NULL, 61, 12, 'يَغْفِرْ لَكُمْ ذُنُوبَكُمْ وَيُدْخِلْكُمْ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ وَمَسَٰكِنَ طَيِّبَةً فِى جَنَّٰتِ عَدْنٍ ذَٰلِكَ ٱلْفَوْزُ ٱلْعَظِيمُ', 'يغفر لكم ذنوبكم ويدخلكم جنات تجري من تحتها الأنهار ومساكن طيبة في جنات عدن ذلك الفوز العظيم', 28, 55, 552, 220, '61:12'),
(5176, NULL, NULL, 61, 13, 'وَأُخْرَىٰ تُحِبُّونَهَا نَصْرٌ مِّنَ ٱللَّهِ وَفَتْحٌ قَرِيبٌ وَبَشِّرِ ٱلْمُؤْمِنِينَ', 'وأخرى تحبونها نصر من الله وفتح قريب وبشر المؤمنين', 28, 55, 552, 220, '61:13'),
(5177, NULL, NULL, 61, 14, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ كُونُوٓا۟ أَنصَارَ ٱللَّهِ كَمَا قَالَ عِيسَى ٱبْنُ مَرْيَمَ لِلْحَوَارِيِّۦنَ مَنْ أَنصَارِىٓ إِلَى ٱللَّهِ قَالَ ٱلْحَوَارِيُّونَ نَحْنُ أَنصَارُ ٱللَّهِ فَـَٔامَنَت طَّآئِفَةٌ مِّنۢ بَنِىٓ إِسْرَٰٓءِيلَ وَكَفَرَت طَّآئِفَةٌ فَأَيَّدْنَا ٱلَّذِينَ ءَامَنُوا۟ عَلَىٰ عَدُوِّهِمْ فَأَصْبَحُوا۟ ظَٰهِرِينَ', 'يا أيها الذين آمنوا كونوا أنصار الله كما قال عيسى ابن مريم للحواريين من أنصاري إلى الله قال الحواريون نحن أنصار الله فآمنت طائفة من بني إسرائيل وكفرت طائفة فأيدنا الذين آمنوا على عدوهم فأصبحوا ظاهرين', 28, 55, 552, 220, '61:14'),
(5178, NULL, NULL, 62, 1, 'يُسَبِّحُ لِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ ٱلْمَلِكِ ٱلْقُدُّوسِ ٱلْعَزِيزِ ٱلْحَكِيمِ', 'يسبح لله ما في السماوات وما في الأرض الملك القدوس العزيز الحكيم', 28, 56, 553, 221, '62:1'),
(5179, NULL, NULL, 62, 2, 'هُوَ ٱلَّذِى بَعَثَ فِى ٱلْأُمِّيِّۦنَ رَسُولًا مِّنْهُمْ يَتْلُوا۟ عَلَيْهِمْ ءَايَٰتِهِۦ وَيُزَكِّيهِمْ وَيُعَلِّمُهُمُ ٱلْكِتَٰبَ وَٱلْحِكْمَةَ وَإِن كَانُوا۟ مِن قَبْلُ لَفِى ضَلَٰلٍ مُّبِينٍ', 'هو الذي بعث في الأميين رسولا منهم يتلو عليهم آياته ويزكيهم ويعلمهم الكتاب والحكمة وإن كانوا من قبل لفي ضلال مبين', 28, 56, 553, 221, '62:2'),
(5180, NULL, NULL, 62, 3, 'وَءَاخَرِينَ مِنْهُمْ لَمَّا يَلْحَقُوا۟ بِهِمْ وَهُوَ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'وآخرين منهم لما يلحقوا بهم وهو العزيز الحكيم', 28, 56, 553, 221, '62:3'),
(5181, NULL, NULL, 62, 4, 'ذَٰلِكَ فَضْلُ ٱللَّهِ يُؤْتِيهِ مَن يَشَآءُ وَٱللَّهُ ذُو ٱلْفَضْلِ ٱلْعَظِيمِ', 'ذلك فضل الله يؤتيه من يشاء والله ذو الفضل العظيم', 28, 56, 553, 221, '62:4'),
(5182, NULL, NULL, 62, 5, 'مَثَلُ ٱلَّذِينَ حُمِّلُوا۟ ٱلتَّوْرَىٰةَ ثُمَّ لَمْ يَحْمِلُوهَا كَمَثَلِ ٱلْحِمَارِ يَحْمِلُ أَسْفَارًۢا بِئْسَ مَثَلُ ٱلْقَوْمِ ٱلَّذِينَ كَذَّبُوا۟ بِـَٔايَٰتِ ٱللَّهِ وَٱللَّهُ لَا يَهْدِى ٱلْقَوْمَ ٱلظَّٰلِمِينَ', 'مثل الذين حملوا التوراة ثم لم يحملوها كمثل الحمار يحمل أسفارا بئس مثل القوم الذين كذبوا بآيات الله والله لا يهدي القوم الظالمين', 28, 56, 553, 221, '62:5'),
(5183, NULL, NULL, 62, 6, 'قُلْ يَٰٓأَيُّهَا ٱلَّذِينَ هَادُوٓا۟ إِن زَعَمْتُمْ أَنَّكُمْ أَوْلِيَآءُ لِلَّهِ مِن دُونِ ٱلنَّاسِ فَتَمَنَّوُا۟ ٱلْمَوْتَ إِن كُنتُمْ صَٰدِقِينَ', 'قل يا أيها الذين هادوا إن زعمتم أنكم أولياء لله من دون الناس فتمنوا الموت إن كنتم صادقين', 28, 56, 553, 221, '62:6'),
(5184, NULL, NULL, 62, 7, 'وَلَا يَتَمَنَّوْنَهُۥٓ أَبَدًۢا بِمَا قَدَّمَتْ أَيْدِيهِمْ وَٱللَّهُ عَلِيمٌۢ بِٱلظَّٰلِمِينَ', 'ولا يتمنونه أبدا بما قدمت أيديهم والله عليم بالظالمين', 28, 56, 553, 221, '62:7'),
(5185, NULL, NULL, 62, 8, 'قُلْ إِنَّ ٱلْمَوْتَ ٱلَّذِى تَفِرُّونَ مِنْهُ فَإِنَّهُۥ مُلَٰقِيكُمْ ثُمَّ تُرَدُّونَ إِلَىٰ عَٰلِمِ ٱلْغَيْبِ وَٱلشَّهَٰدَةِ فَيُنَبِّئُكُم بِمَا كُنتُمْ تَعْمَلُونَ', 'قل إن الموت الذي تفرون منه فإنه ملاقيكم ثم تردون إلى عالم الغيب والشهادة فينبئكم بما كنتم تعملون', 28, 56, 553, 221, '62:8'),
(5186, NULL, NULL, 62, 9, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِذَا نُودِىَ لِلصَّلَوٰةِ مِن يَوْمِ ٱلْجُمُعَةِ فَٱسْعَوْا۟ إِلَىٰ ذِكْرِ ٱللَّهِ وَذَرُوا۟ ٱلْبَيْعَ ذَٰلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُمْ تَعْلَمُونَ', 'يا أيها الذين آمنوا إذا نودي للصلاة من يوم الجمعة فاسعوا إلى ذكر الله وذروا البيع ذلكم خير لكم إن كنتم تعلمون', 28, 56, 554, 221, '62:9'),
(5187, NULL, NULL, 62, 10, 'فَإِذَا قُضِيَتِ ٱلصَّلَوٰةُ فَٱنتَشِرُوا۟ فِى ٱلْأَرْضِ وَٱبْتَغُوا۟ مِن فَضْلِ ٱللَّهِ وَٱذْكُرُوا۟ ٱللَّهَ كَثِيرًا لَّعَلَّكُمْ تُفْلِحُونَ', 'فإذا قضيت الصلاة فانتشروا في الأرض وابتغوا من فضل الله واذكروا الله كثيرا لعلكم تفلحون', 28, 56, 554, 221, '62:10'),
(5188, NULL, NULL, 62, 11, 'وَإِذَا رَأَوْا۟ تِجَٰرَةً أَوْ لَهْوًا ٱنفَضُّوٓا۟ إِلَيْهَا وَتَرَكُوكَ قَآئِمًا قُلْ مَا عِندَ ٱللَّهِ خَيْرٌ مِّنَ ٱللَّهْوِ وَمِنَ ٱلتِّجَٰرَةِ وَٱللَّهُ خَيْرُ ٱلرَّٰزِقِينَ', 'وإذا رأوا تجارة أو لهوا انفضوا إليها وتركوك قائما قل ما عند الله خير من اللهو ومن التجارة والله خير الرازقين', 28, 56, 554, 221, '62:11'),
(5189, NULL, NULL, 63, 1, 'إِذَا جَآءَكَ ٱلْمُنَٰفِقُونَ قَالُوا۟ نَشْهَدُ إِنَّكَ لَرَسُولُ ٱللَّهِ وَٱللَّهُ يَعْلَمُ إِنَّكَ لَرَسُولُهُۥ وَٱللَّهُ يَشْهَدُ إِنَّ ٱلْمُنَٰفِقِينَ لَكَٰذِبُونَ', 'إذا جاءك المنافقون قالوا نشهد إنك لرسول الله والله يعلم إنك لرسوله والله يشهد إن المنافقين لكاذبون', 28, 56, 554, 221, '63:1'),
(5190, NULL, NULL, 63, 2, 'ٱتَّخَذُوٓا۟ أَيْمَٰنَهُمْ جُنَّةً فَصَدُّوا۟ عَن سَبِيلِ ٱللَّهِ إِنَّهُمْ سَآءَ مَا كَانُوا۟ يَعْمَلُونَ', 'اتخذوا أيمانهم جنة فصدوا عن سبيل الله إنهم ساء ما كانوا يعملون', 28, 56, 554, 221, '63:2'),
(5191, NULL, NULL, 63, 3, 'ذَٰلِكَ بِأَنَّهُمْ ءَامَنُوا۟ ثُمَّ كَفَرُوا۟ فَطُبِعَ عَلَىٰ قُلُوبِهِمْ فَهُمْ لَا يَفْقَهُونَ', 'ذلك بأنهم آمنوا ثم كفروا فطبع على قلوبهم فهم لا يفقهون', 28, 56, 554, 221, '63:3'),
(5192, NULL, NULL, 63, 4, 'وَإِذَا رَأَيْتَهُمْ تُعْجِبُكَ أَجْسَامُهُمْ وَإِن يَقُولُوا۟ تَسْمَعْ لِقَوْلِهِمْ كَأَنَّهُمْ خُشُبٌ مُّسَنَّدَةٌ يَحْسَبُونَ كُلَّ صَيْحَةٍ عَلَيْهِمْ هُمُ ٱلْعَدُوُّ فَٱحْذَرْهُمْ قَٰتَلَهُمُ ٱللَّهُ أَنَّىٰ يُؤْفَكُونَ', 'وإذا رأيتهم تعجبك أجسامهم وإن يقولوا تسمع لقولهم كأنهم خشب مسندة يحسبون كل صيحة عليهم هم العدو فاحذرهم قاتلهم الله أنى يؤفكون', 28, 56, 554, 222, '63:4'),
(5193, NULL, NULL, 63, 5, 'وَإِذَا قِيلَ لَهُمْ تَعَالَوْا۟ يَسْتَغْفِرْ لَكُمْ رَسُولُ ٱللَّهِ لَوَّوْا۟ رُءُوسَهُمْ وَرَأَيْتَهُمْ يَصُدُّونَ وَهُم مُّسْتَكْبِرُونَ', 'وإذا قيل لهم تعالوا يستغفر لكم رسول الله لووا رءوسهم ورأيتهم يصدون وهم مستكبرون', 28, 56, 555, 222, '63:5'),
(5194, NULL, NULL, 63, 6, 'سَوَآءٌ عَلَيْهِمْ أَسْتَغْفَرْتَ لَهُمْ أَمْ لَمْ تَسْتَغْفِرْ لَهُمْ لَن يَغْفِرَ ٱللَّهُ لَهُمْ إِنَّ ٱللَّهَ لَا يَهْدِى ٱلْقَوْمَ ٱلْفَٰسِقِينَ', 'سواء عليهم أستغفرت لهم أم لم تستغفر لهم لن يغفر الله لهم إن الله لا يهدي القوم الفاسقين', 28, 56, 555, 222, '63:6'),
(5195, NULL, NULL, 63, 7, 'هُمُ ٱلَّذِينَ يَقُولُونَ لَا تُنفِقُوا۟ عَلَىٰ مَنْ عِندَ رَسُولِ ٱللَّهِ حَتَّىٰ يَنفَضُّوا۟ وَلِلَّهِ خَزَآئِنُ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَلَٰكِنَّ ٱلْمُنَٰفِقِينَ لَا يَفْقَهُونَ', 'هم الذين يقولون لا تنفقوا على من عند رسول الله حتى ينفضوا ولله خزائن السماوات والأرض ولكن المنافقين لا يفقهون', 28, 56, 555, 222, '63:7'),
(5196, NULL, NULL, 63, 8, 'يَقُولُونَ لَئِن رَّجَعْنَآ إِلَى ٱلْمَدِينَةِ لَيُخْرِجَنَّ ٱلْأَعَزُّ مِنْهَا ٱلْأَذَلَّ وَلِلَّهِ ٱلْعِزَّةُ وَلِرَسُولِهِۦ وَلِلْمُؤْمِنِينَ وَلَٰكِنَّ ٱلْمُنَٰفِقِينَ لَا يَعْلَمُونَ', 'يقولون لئن رجعنا إلى المدينة ليخرجن الأعز منها الأذل ولله العزة ولرسوله وللمؤمنين ولكن المنافقين لا يعلمون', 28, 56, 555, 222, '63:8'),
(5197, NULL, NULL, 63, 9, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ لَا تُلْهِكُمْ أَمْوَٰلُكُمْ وَلَآ أَوْلَٰدُكُمْ عَن ذِكْرِ ٱللَّهِ وَمَن يَفْعَلْ ذَٰلِكَ فَأُو۟لَٰٓئِكَ هُمُ ٱلْخَٰسِرُونَ', 'يا أيها الذين آمنوا لا تلهكم أموالكم ولا أولادكم عن ذكر الله ومن يفعل ذلك فأولئك هم الخاسرون', 28, 56, 555, 222, '63:9'),
(5198, NULL, NULL, 63, 10, 'وَأَنفِقُوا۟ مِن مَّا رَزَقْنَٰكُم مِّن قَبْلِ أَن يَأْتِىَ أَحَدَكُمُ ٱلْمَوْتُ فَيَقُولَ رَبِّ لَوْلَآ أَخَّرْتَنِىٓ إِلَىٰٓ أَجَلٍ قَرِيبٍ فَأَصَّدَّقَ وَأَكُن مِّنَ ٱلصَّٰلِحِينَ', 'وأنفقوا من ما رزقناكم من قبل أن يأتي أحدكم الموت فيقول رب لولا أخرتني إلى أجل قريب فأصدق وأكن من الصالحين', 28, 56, 555, 222, '63:10'),
(5199, NULL, NULL, 63, 11, 'وَلَن يُؤَخِّرَ ٱللَّهُ نَفْسًا إِذَا جَآءَ أَجَلُهَا وَٱللَّهُ خَبِيرٌۢ بِمَا تَعْمَلُونَ', 'ولن يؤخر الله نفسا إذا جاء أجلها والله خبير بما تعملون', 28, 56, 555, 222, '63:11'),
(5200, NULL, NULL, 64, 1, 'يُسَبِّحُ لِلَّهِ مَا فِى ٱلسَّمَٰوَٰتِ وَمَا فِى ٱلْأَرْضِ لَهُ ٱلْمُلْكُ وَلَهُ ٱلْحَمْدُ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'يسبح لله ما في السماوات وما في الأرض له الملك وله الحمد وهو على كل شيء قدير', 28, 56, 556, 222, '64:1'),
(5201, NULL, NULL, 64, 2, 'هُوَ ٱلَّذِى خَلَقَكُمْ فَمِنكُمْ كَافِرٌ وَمِنكُم مُّؤْمِنٌ وَٱللَّهُ بِمَا تَعْمَلُونَ بَصِيرٌ', 'هو الذي خلقكم فمنكم كافر ومنكم مؤمن والله بما تعملون بصير', 28, 56, 556, 222, '64:2'),
(5202, NULL, NULL, 64, 3, 'خَلَقَ ٱلسَّمَٰوَٰتِ وَٱلْأَرْضَ بِٱلْحَقِّ وَصَوَّرَكُمْ فَأَحْسَنَ صُوَرَكُمْ وَإِلَيْهِ ٱلْمَصِيرُ', 'خلق السماوات والأرض بالحق وصوركم فأحسن صوركم وإليه المصير', 28, 56, 556, 222, '64:3'),
(5203, NULL, NULL, 64, 4, 'يَعْلَمُ مَا فِى ٱلسَّمَٰوَٰتِ وَٱلْأَرْضِ وَيَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ وَٱللَّهُ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ', 'يعلم ما في السماوات والأرض ويعلم ما تسرون وما تعلنون والله عليم بذات الصدور', 28, 56, 556, 222, '64:4'),
(5204, NULL, NULL, 64, 5, 'أَلَمْ يَأْتِكُمْ نَبَؤُا۟ ٱلَّذِينَ كَفَرُوا۟ مِن قَبْلُ فَذَاقُوا۟ وَبَالَ أَمْرِهِمْ وَلَهُمْ عَذَابٌ أَلِيمٌ', 'ألم يأتكم نبأ الذين كفروا من قبل فذاقوا وبال أمرهم ولهم عذاب أليم', 28, 56, 556, 222, '64:5'),
(5205, NULL, NULL, 64, 6, 'ذَٰلِكَ بِأَنَّهُۥ كَانَت تَّأْتِيهِمْ رُسُلُهُم بِٱلْبَيِّنَٰتِ فَقَالُوٓا۟ أَبَشَرٌ يَهْدُونَنَا فَكَفَرُوا۟ وَتَوَلَّوا۟ وَّٱسْتَغْنَى ٱللَّهُ وَٱللَّهُ غَنِىٌّ حَمِيدٌ', 'ذلك بأنه كانت تأتيهم رسلهم بالبينات فقالوا أبشر يهدوننا فكفروا وتولوا واستغنى الله والله غني حميد', 28, 56, 556, 222, '64:6'),
(5206, NULL, NULL, 64, 7, 'زَعَمَ ٱلَّذِينَ كَفَرُوٓا۟ أَن لَّن يُبْعَثُوا۟ قُلْ بَلَىٰ وَرَبِّى لَتُبْعَثُنَّ ثُمَّ لَتُنَبَّؤُنَّ بِمَا عَمِلْتُمْ وَذَٰلِكَ عَلَى ٱللَّهِ يَسِيرٌ', 'زعم الذين كفروا أن لن يبعثوا قل بلى وربي لتبعثن ثم لتنبؤن بما عملتم وذلك على الله يسير', 28, 56, 556, 222, '64:7'),
(5207, NULL, NULL, 64, 8, 'فَـَٔامِنُوا۟ بِٱللَّهِ وَرَسُولِهِۦ وَٱلنُّورِ ٱلَّذِىٓ أَنزَلْنَا وَٱللَّهُ بِمَا تَعْمَلُونَ خَبِيرٌ', 'فآمنوا بالله ورسوله والنور الذي أنزلنا والله بما تعملون خبير', 28, 56, 556, 222, '64:8'),
(5208, NULL, NULL, 64, 9, 'يَوْمَ يَجْمَعُكُمْ لِيَوْمِ ٱلْجَمْعِ ذَٰلِكَ يَوْمُ ٱلتَّغَابُنِ وَمَن يُؤْمِنۢ بِٱللَّهِ وَيَعْمَلْ صَٰلِحًا يُكَفِّرْ عَنْهُ سَيِّـَٔاتِهِۦ وَيُدْخِلْهُ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدًا ذَٰلِكَ ٱلْفَوْزُ ٱلْعَظِيمُ', 'يوم يجمعكم ليوم الجمع ذلك يوم التغابن ومن يؤمن بالله ويعمل صالحا يكفر عنه سيئاته ويدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا ذلك الفوز العظيم', 28, 56, 556, 222, '64:9'),
(5209, NULL, NULL, 64, 10, 'وَٱلَّذِينَ كَفَرُوا۟ وَكَذَّبُوا۟ بِـَٔايَٰتِنَآ أُو۟لَٰٓئِكَ أَصْحَٰبُ ٱلنَّارِ خَٰلِدِينَ فِيهَا وَبِئْسَ ٱلْمَصِيرُ', 'والذين كفروا وكذبوا بآياتنا أولئك أصحاب النار خالدين فيها وبئس المصير', 28, 56, 557, 222, '64:10'),
(5210, NULL, NULL, 64, 11, 'مَآ أَصَابَ مِن مُّصِيبَةٍ إِلَّا بِإِذْنِ ٱللَّهِ وَمَن يُؤْمِنۢ بِٱللَّهِ يَهْدِ قَلْبَهُۥ وَٱللَّهُ بِكُلِّ شَىْءٍ عَلِيمٌ', 'ما أصاب من مصيبة إلا بإذن الله ومن يؤمن بالله يهد قلبه والله بكل شيء عليم', 28, 56, 557, 222, '64:11'),
(5211, NULL, NULL, 64, 12, 'وَأَطِيعُوا۟ ٱللَّهَ وَأَطِيعُوا۟ ٱلرَّسُولَ فَإِن تَوَلَّيْتُمْ فَإِنَّمَا عَلَىٰ رَسُولِنَا ٱلْبَلَٰغُ ٱلْمُبِينُ', 'وأطيعوا الله وأطيعوا الرسول فإن توليتم فإنما على رسولنا البلاغ المبين', 28, 56, 557, 222, '64:12'),
(5212, NULL, NULL, 64, 13, 'ٱللَّهُ لَآ إِلَٰهَ إِلَّا هُوَ وَعَلَى ٱللَّهِ فَلْيَتَوَكَّلِ ٱلْمُؤْمِنُونَ', 'الله لا إله إلا هو وعلى الله فليتوكل المؤمنون', 28, 56, 557, 222, '64:13'),
(5213, NULL, NULL, 64, 14, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوٓا۟ إِنَّ مِنْ أَزْوَٰجِكُمْ وَأَوْلَٰدِكُمْ عَدُوًّا لَّكُمْ فَٱحْذَرُوهُمْ وَإِن تَعْفُوا۟ وَتَصْفَحُوا۟ وَتَغْفِرُوا۟ فَإِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌ', 'يا أيها الذين آمنوا إن من أزواجكم وأولادكم عدوا لكم فاحذروهم وإن تعفوا وتصفحوا وتغفروا فإن الله غفور رحيم', 28, 56, 557, 222, '64:14'),
(5214, NULL, NULL, 64, 15, 'إِنَّمَآ أَمْوَٰلُكُمْ وَأَوْلَٰدُكُمْ فِتْنَةٌ وَٱللَّهُ عِندَهُۥٓ أَجْرٌ عَظِيمٌ', 'إنما أموالكم وأولادكم فتنة والله عنده أجر عظيم', 28, 56, 557, 222, '64:15'),
(5215, NULL, NULL, 64, 16, 'فَٱتَّقُوا۟ ٱللَّهَ مَا ٱسْتَطَعْتُمْ وَٱسْمَعُوا۟ وَأَطِيعُوا۟ وَأَنفِقُوا۟ خَيْرًا لِّأَنفُسِكُمْ وَمَن يُوقَ شُحَّ نَفْسِهِۦ فَأُو۟لَٰٓئِكَ هُمُ ٱلْمُفْلِحُونَ', 'فاتقوا الله ما استطعتم واسمعوا وأطيعوا وأنفقوا خيرا لأنفسكم ومن يوق شح نفسه فأولئك هم المفلحون', 28, 56, 557, 222, '64:16'),
(5216, NULL, NULL, 64, 17, 'إِن تُقْرِضُوا۟ ٱللَّهَ قَرْضًا حَسَنًا يُضَٰعِفْهُ لَكُمْ وَيَغْفِرْ لَكُمْ وَٱللَّهُ شَكُورٌ حَلِيمٌ', 'إن تقرضوا الله قرضا حسنا يضاعفه لكم ويغفر لكم والله شكور حليم', 28, 56, 557, 222, '64:17'),
(5217, NULL, NULL, 64, 18, 'عَٰلِمُ ٱلْغَيْبِ وَٱلشَّهَٰدَةِ ٱلْعَزِيزُ ٱلْحَكِيمُ', 'عالم الغيب والشهادة العزيز الحكيم', 28, 56, 557, 222, '64:18'),
(5218, NULL, NULL, 65, 1, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ إِذَا طَلَّقْتُمُ ٱلنِّسَآءَ فَطَلِّقُوهُنَّ لِعِدَّتِهِنَّ وَأَحْصُوا۟ ٱلْعِدَّةَ وَٱتَّقُوا۟ ٱللَّهَ رَبَّكُمْ لَا تُخْرِجُوهُنَّ مِنۢ بُيُوتِهِنَّ وَلَا يَخْرُجْنَ إِلَّآ أَن يَأْتِينَ بِفَٰحِشَةٍ مُّبَيِّنَةٍ وَتِلْكَ حُدُودُ ٱللَّهِ وَمَن يَتَعَدَّ حُدُودَ ٱللَّهِ فَقَدْ ظَلَمَ نَفْسَهُۥ لَا تَدْرِى لَعَلَّ ٱللَّهَ يُحْدِثُ بَعْدَ ذَٰلِكَ أَمْرًا', 'يا أيها النبي إذا طلقتم النساء فطلقوهن لعدتهن وأحصوا العدة واتقوا الله ربكم لا تخرجوهن من بيوتهن ولا يخرجن إلا أن يأتين بفاحشة مبينة وتلك حدود الله ومن يتعد حدود الله فقد ظلم نفسه لا تدري لعل الله يحدث بعد ذلك أمرا', 28, 56, 558, 223, '65:1'),
(5219, NULL, NULL, 65, 2, 'فَإِذَا بَلَغْنَ أَجَلَهُنَّ فَأَمْسِكُوهُنَّ بِمَعْرُوفٍ أَوْ فَارِقُوهُنَّ بِمَعْرُوفٍ وَأَشْهِدُوا۟ ذَوَىْ عَدْلٍ مِّنكُمْ وَأَقِيمُوا۟ ٱلشَّهَٰدَةَ لِلَّهِ ذَٰلِكُمْ يُوعَظُ بِهِۦ مَن كَانَ يُؤْمِنُ بِٱللَّهِ وَٱلْيَوْمِ ٱلْءَاخِرِ وَمَن يَتَّقِ ٱللَّهَ يَجْعَل لَّهُۥ مَخْرَجًا', 'فإذا بلغن أجلهن فأمسكوهن بمعروف أو فارقوهن بمعروف وأشهدوا ذوي عدل منكم وأقيموا الشهادة لله ذلكم يوعظ به من كان يؤمن بالله واليوم الآخر ومن يتق الله يجعل له مخرجا', 28, 56, 558, 223, '65:2'),
(5220, NULL, NULL, 65, 3, 'وَيَرْزُقْهُ مِنْ حَيْثُ لَا يَحْتَسِبُ وَمَن يَتَوَكَّلْ عَلَى ٱللَّهِ فَهُوَ حَسْبُهُۥٓ إِنَّ ٱللَّهَ بَٰلِغُ أَمْرِهِۦ قَدْ جَعَلَ ٱللَّهُ لِكُلِّ شَىْءٍ قَدْرًا', 'ويرزقه من حيث لا يحتسب ومن يتوكل على الله فهو حسبه إن الله بالغ أمره قد جعل الله لكل شيء قدرا', 28, 56, 558, 223, '65:3'),
(5221, NULL, NULL, 65, 4, 'وَٱلَّٰٓـِٔى يَئِسْنَ مِنَ ٱلْمَحِيضِ مِن نِّسَآئِكُمْ إِنِ ٱرْتَبْتُمْ فَعِدَّتُهُنَّ ثَلَٰثَةُ أَشْهُرٍ وَٱلَّٰٓـِٔى لَمْ يَحِضْنَ وَأُو۟لَٰتُ ٱلْأَحْمَالِ أَجَلُهُنَّ أَن يَضَعْنَ حَمْلَهُنَّ وَمَن يَتَّقِ ٱللَّهَ يَجْعَل لَّهُۥ مِنْ أَمْرِهِۦ يُسْرًا', 'واللائي يئسن من المحيض من نسائكم إن ارتبتم فعدتهن ثلاثة أشهر واللائي لم يحضن وأولات الأحمال أجلهن أن يضعن حملهن ومن يتق الله يجعل له من أمره يسرا', 28, 56, 558, 223, '65:4'),
(5222, NULL, NULL, 65, 5, 'ذَٰلِكَ أَمْرُ ٱللَّهِ أَنزَلَهُۥٓ إِلَيْكُمْ وَمَن يَتَّقِ ٱللَّهَ يُكَفِّرْ عَنْهُ سَيِّـَٔاتِهِۦ وَيُعْظِمْ لَهُۥٓ أَجْرًا', 'ذلك أمر الله أنزله إليكم ومن يتق الله يكفر عنه سيئاته ويعظم له أجرا', 28, 56, 558, 223, '65:5'),
(5223, NULL, NULL, 65, 6, 'أَسْكِنُوهُنَّ مِنْ حَيْثُ سَكَنتُم مِّن وُجْدِكُمْ وَلَا تُضَآرُّوهُنَّ لِتُضَيِّقُوا۟ عَلَيْهِنَّ وَإِن كُنَّ أُو۟لَٰتِ حَمْلٍ فَأَنفِقُوا۟ عَلَيْهِنَّ حَتَّىٰ يَضَعْنَ حَمْلَهُنَّ فَإِنْ أَرْضَعْنَ لَكُمْ فَـَٔاتُوهُنَّ أُجُورَهُنَّ وَأْتَمِرُوا۟ بَيْنَكُم بِمَعْرُوفٍ وَإِن تَعَاسَرْتُمْ فَسَتُرْضِعُ لَهُۥٓ أُخْرَىٰ', 'أسكنوهن من حيث سكنتم من وجدكم ولا تضاروهن لتضيقوا عليهن وإن كن أولات حمل فأنفقوا عليهن حتى يضعن حملهن فإن أرضعن لكم فآتوهن أجورهن وأتمروا بينكم بمعروف وإن تعاسرتم فسترضع له أخرى', 28, 56, 559, 223, '65:6'),
(5224, NULL, NULL, 65, 7, 'لِيُنفِقْ ذُو سَعَةٍ مِّن سَعَتِهِۦ وَمَن قُدِرَ عَلَيْهِ رِزْقُهُۥ فَلْيُنفِقْ مِمَّآ ءَاتَىٰهُ ٱللَّهُ لَا يُكَلِّفُ ٱللَّهُ نَفْسًا إِلَّا مَآ ءَاتَىٰهَا سَيَجْعَلُ ٱللَّهُ بَعْدَ عُسْرٍ يُسْرًا', 'لينفق ذو سعة من سعته ومن قدر عليه رزقه فلينفق مما آتاه الله لا يكلف الله نفسا إلا ما آتاها سيجعل الله بعد عسر يسرا', 28, 56, 559, 223, '65:7'),
(5225, NULL, NULL, 65, 8, 'وَكَأَيِّن مِّن قَرْيَةٍ عَتَتْ عَنْ أَمْرِ رَبِّهَا وَرُسُلِهِۦ فَحَاسَبْنَٰهَا حِسَابًا شَدِيدًا وَعَذَّبْنَٰهَا عَذَابًا نُّكْرًا', 'وكأين من قرية عتت عن أمر ربها ورسله فحاسبناها حسابا شديدا وعذبناها عذابا نكرا', 28, 56, 559, 223, '65:8'),
(5226, NULL, NULL, 65, 9, 'فَذَاقَتْ وَبَالَ أَمْرِهَا وَكَانَ عَٰقِبَةُ أَمْرِهَا خُسْرًا', 'فذاقت وبال أمرها وكان عاقبة أمرها خسرا', 28, 56, 559, 223, '65:9'),
(5227, NULL, NULL, 65, 10, 'أَعَدَّ ٱللَّهُ لَهُمْ عَذَابًا شَدِيدًا فَٱتَّقُوا۟ ٱللَّهَ يَٰٓأُو۟لِى ٱلْأَلْبَٰبِ ٱلَّذِينَ ءَامَنُوا۟ قَدْ أَنزَلَ ٱللَّهُ إِلَيْكُمْ ذِكْرًا', 'أعد الله لهم عذابا شديدا فاتقوا الله يا أولي الألباب الذين آمنوا قد أنزل الله إليكم ذكرا', 28, 56, 559, 223, '65:10'),
(5228, NULL, NULL, 65, 11, 'رَّسُولًا يَتْلُوا۟ عَلَيْكُمْ ءَايَٰتِ ٱللَّهِ مُبَيِّنَٰتٍ لِّيُخْرِجَ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ مِنَ ٱلظُّلُمَٰتِ إِلَى ٱلنُّورِ وَمَن يُؤْمِنۢ بِٱللَّهِ وَيَعْمَلْ صَٰلِحًا يُدْخِلْهُ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدًا قَدْ أَحْسَنَ ٱللَّهُ لَهُۥ رِزْقًا', 'رسولا يتلو عليكم آيات الله مبينات ليخرج الذين آمنوا وعملوا الصالحات من الظلمات إلى النور ومن يؤمن بالله ويعمل صالحا يدخله جنات تجري من تحتها الأنهار خالدين فيها أبدا قد أحسن الله له رزقا', 28, 56, 559, 223, '65:11'),
(5229, NULL, NULL, 65, 12, 'ٱللَّهُ ٱلَّذِى خَلَقَ سَبْعَ سَمَٰوَٰتٍ وَمِنَ ٱلْأَرْضِ مِثْلَهُنَّ يَتَنَزَّلُ ٱلْأَمْرُ بَيْنَهُنَّ لِتَعْلَمُوٓا۟ أَنَّ ٱللَّهَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ وَأَنَّ ٱللَّهَ قَدْ أَحَاطَ بِكُلِّ شَىْءٍ عِلْمًۢا', 'الله الذي خلق سبع سماوات ومن الأرض مثلهن يتنزل الأمر بينهن لتعلموا أن الله على كل شيء قدير وأن الله قد أحاط بكل شيء علما', 28, 56, 559, 223, '65:12'),
(5230, NULL, NULL, 66, 1, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ لِمَ تُحَرِّمُ مَآ أَحَلَّ ٱللَّهُ لَكَ تَبْتَغِى مَرْضَاتَ أَزْوَٰجِكَ وَٱللَّهُ غَفُورٌ رَّحِيمٌ', 'يا أيها النبي لم تحرم ما أحل الله لك تبتغي مرضات أزواجك والله غفور رحيم', 28, 56, 560, 224, '66:1'),
(5231, NULL, NULL, 66, 2, 'قَدْ فَرَضَ ٱللَّهُ لَكُمْ تَحِلَّةَ أَيْمَٰنِكُمْ وَٱللَّهُ مَوْلَىٰكُمْ وَهُوَ ٱلْعَلِيمُ ٱلْحَكِيمُ', 'قد فرض الله لكم تحلة أيمانكم والله مولاكم وهو العليم الحكيم', 28, 56, 560, 224, '66:2'),
(5232, NULL, NULL, 66, 3, 'وَإِذْ أَسَرَّ ٱلنَّبِىُّ إِلَىٰ بَعْضِ أَزْوَٰجِهِۦ حَدِيثًا فَلَمَّا نَبَّأَتْ بِهِۦ وَأَظْهَرَهُ ٱللَّهُ عَلَيْهِ عَرَّفَ بَعْضَهُۥ وَأَعْرَضَ عَنۢ بَعْضٍ فَلَمَّا نَبَّأَهَا بِهِۦ قَالَتْ مَنْ أَنۢبَأَكَ هَٰذَا قَالَ نَبَّأَنِىَ ٱلْعَلِيمُ ٱلْخَبِيرُ', 'وإذ أسر النبي إلى بعض أزواجه حديثا فلما نبأت به وأظهره الله عليه عرف بعضه وأعرض عن بعض فلما نبأها به قالت من أنبأك هذا قال نبأني العليم الخبير', 28, 56, 560, 224, '66:3'),
(5233, NULL, NULL, 66, 4, 'إِن تَتُوبَآ إِلَى ٱللَّهِ فَقَدْ صَغَتْ قُلُوبُكُمَا وَإِن تَظَٰهَرَا عَلَيْهِ فَإِنَّ ٱللَّهَ هُوَ مَوْلَىٰهُ وَجِبْرِيلُ وَصَٰلِحُ ٱلْمُؤْمِنِينَ وَٱلْمَلَٰٓئِكَةُ بَعْدَ ذَٰلِكَ ظَهِيرٌ', 'إن تتوبا إلى الله فقد صغت قلوبكما وإن تظاهرا عليه فإن الله هو مولاه وجبريل وصالح المؤمنين والملائكة بعد ذلك ظهير', 28, 56, 560, 224, '66:4'),
(5234, NULL, NULL, 66, 5, 'عَسَىٰ رَبُّهُۥٓ إِن طَلَّقَكُنَّ أَن يُبْدِلَهُۥٓ أَزْوَٰجًا خَيْرًا مِّنكُنَّ مُسْلِمَٰتٍ مُّؤْمِنَٰتٍ قَٰنِتَٰتٍ تَٰٓئِبَٰتٍ عَٰبِدَٰتٍ سَٰٓئِحَٰتٍ ثَيِّبَٰتٍ وَأَبْكَارًا', 'عسى ربه إن طلقكن أن يبدله أزواجا خيرا منكن مسلمات مؤمنات قانتات تائبات عابدات سائحات ثيبات وأبكارا', 28, 56, 560, 224, '66:5'),
(5235, NULL, NULL, 66, 6, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ قُوٓا۟ أَنفُسَكُمْ وَأَهْلِيكُمْ نَارًا وَقُودُهَا ٱلنَّاسُ وَٱلْحِجَارَةُ عَلَيْهَا مَلَٰٓئِكَةٌ غِلَاظٌ شِدَادٌ لَّا يَعْصُونَ ٱللَّهَ مَآ أَمَرَهُمْ وَيَفْعَلُونَ مَا يُؤْمَرُونَ', 'يا أيها الذين آمنوا قوا أنفسكم وأهليكم نارا وقودها الناس والحجارة عليها ملائكة غلاظ شداد لا يعصون الله ما أمرهم ويفعلون ما يؤمرون', 28, 56, 560, 224, '66:6'),
(5236, NULL, NULL, 66, 7, 'يَٰٓأَيُّهَا ٱلَّذِينَ كَفَرُوا۟ لَا تَعْتَذِرُوا۟ ٱلْيَوْمَ إِنَّمَا تُجْزَوْنَ مَا كُنتُمْ تَعْمَلُونَ', 'يا أيها الذين كفروا لا تعتذروا اليوم إنما تجزون ما كنتم تعملون', 28, 56, 560, 224, '66:7'),
(5237, NULL, NULL, 66, 8, 'يَٰٓأَيُّهَا ٱلَّذِينَ ءَامَنُوا۟ تُوبُوٓا۟ إِلَى ٱللَّهِ تَوْبَةً نَّصُوحًا عَسَىٰ رَبُّكُمْ أَن يُكَفِّرَ عَنكُمْ سَيِّـَٔاتِكُمْ وَيُدْخِلَكُمْ جَنَّٰتٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ يَوْمَ لَا يُخْزِى ٱللَّهُ ٱلنَّبِىَّ وَٱلَّذِينَ ءَامَنُوا۟ مَعَهُۥ نُورُهُمْ يَسْعَىٰ بَيْنَ أَيْدِيهِمْ وَبِأَيْمَٰنِهِمْ يَقُولُونَ رَبَّنَآ أَتْمِمْ لَنَا نُورَنَا وَٱغْفِرْ لَنَآ إِنَّكَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'يا أيها الذين آمنوا توبوا إلى الله توبة نصوحا عسى ربكم أن يكفر عنكم سيئاتكم ويدخلكم جنات تجري من تحتها الأنهار يوم لا يخزي الله النبي والذين آمنوا معه نورهم يسعى بين أيديهم وبأيمانهم يقولون ربنا أتمم لنا نورنا واغفر لنا إنك على كل شيء قدير', 28, 56, 561, 224, '66:8'),
(5238, NULL, NULL, 66, 9, 'يَٰٓأَيُّهَا ٱلنَّبِىُّ جَٰهِدِ ٱلْكُفَّارَ وَٱلْمُنَٰفِقِينَ وَٱغْلُظْ عَلَيْهِمْ وَمَأْوَىٰهُمْ جَهَنَّمُ وَبِئْسَ ٱلْمَصِيرُ', 'يا أيها النبي جاهد الكفار والمنافقين واغلظ عليهم ومأواهم جهنم وبئس المصير', 28, 56, 561, 224, '66:9'),
(5239, NULL, NULL, 66, 10, 'ضَرَبَ ٱللَّهُ مَثَلًا لِّلَّذِينَ كَفَرُوا۟ ٱمْرَأَتَ نُوحٍ وَٱمْرَأَتَ لُوطٍ كَانَتَا تَحْتَ عَبْدَيْنِ مِنْ عِبَادِنَا صَٰلِحَيْنِ فَخَانَتَاهُمَا فَلَمْ يُغْنِيَا عَنْهُمَا مِنَ ٱللَّهِ شَيْـًٔا وَقِيلَ ٱدْخُلَا ٱلنَّارَ مَعَ ٱلدَّٰخِلِينَ', 'ضرب الله مثلا للذين كفروا امرأت نوح وامرأت لوط كانتا تحت عبدين من عبادنا صالحين فخانتاهما فلم يغنيا عنهما من الله شيئا وقيل ادخلا النار مع الداخلين', 28, 56, 561, 224, '66:10'),
(5240, NULL, NULL, 66, 11, 'وَضَرَبَ ٱللَّهُ مَثَلًا لِّلَّذِينَ ءَامَنُوا۟ ٱمْرَأَتَ فِرْعَوْنَ إِذْ قَالَتْ رَبِّ ٱبْنِ لِى عِندَكَ بَيْتًا فِى ٱلْجَنَّةِ وَنَجِّنِى مِن فِرْعَوْنَ وَعَمَلِهِۦ وَنَجِّنِى مِنَ ٱلْقَوْمِ ٱلظَّٰلِمِينَ', 'وضرب الله مثلا للذين آمنوا امرأت فرعون إذ قالت رب ابن لي عندك بيتا في الجنة ونجني من فرعون وعمله ونجني من القوم الظالمين', 28, 56, 561, 224, '66:11'),
(5241, NULL, NULL, 66, 12, 'وَمَرْيَمَ ٱبْنَتَ عِمْرَٰنَ ٱلَّتِىٓ أَحْصَنَتْ فَرْجَهَا فَنَفَخْنَا فِيهِ مِن رُّوحِنَا وَصَدَّقَتْ بِكَلِمَٰتِ رَبِّهَا وَكُتُبِهِۦ وَكَانَتْ مِنَ ٱلْقَٰنِتِينَ', 'ومريم ابنت عمران التي أحصنت فرجها فنفخنا فيه من روحنا وصدقت بكلمات ربها وكتبه وكانت من القانتين', 28, 56, 561, 224, '66:12'),
(5242, NULL, NULL, 67, 1, 'تَبَٰرَكَ ٱلَّذِى بِيَدِهِ ٱلْمُلْكُ وَهُوَ عَلَىٰ كُلِّ شَىْءٍ قَدِيرٌ', 'تبارك الذي بيده الملك وهو على كل شيء قدير', 29, 57, 562, 225, '67:1'),
(5243, NULL, NULL, 67, 2, 'ٱلَّذِى خَلَقَ ٱلْمَوْتَ وَٱلْحَيَوٰةَ لِيَبْلُوَكُمْ أَيُّكُمْ أَحْسَنُ عَمَلًا وَهُوَ ٱلْعَزِيزُ ٱلْغَفُورُ', 'الذي خلق الموت والحياة ليبلوكم أيكم أحسن عملا وهو العزيز الغفور', 29, 57, 562, 225, '67:2'),
(5244, NULL, NULL, 67, 3, 'ٱلَّذِى خَلَقَ سَبْعَ سَمَٰوَٰتٍ طِبَاقًا مَّا تَرَىٰ فِى خَلْقِ ٱلرَّحْمَٰنِ مِن تَفَٰوُتٍ فَٱرْجِعِ ٱلْبَصَرَ هَلْ تَرَىٰ مِن فُطُورٍ', 'الذي خلق سبع سماوات طباقا ما ترى في خلق الرحمن من تفاوت فارجع البصر هل ترى من فطور', 29, 57, 562, 225, '67:3');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(5245, NULL, NULL, 67, 4, 'ثُمَّ ٱرْجِعِ ٱلْبَصَرَ كَرَّتَيْنِ يَنقَلِبْ إِلَيْكَ ٱلْبَصَرُ خَاسِئًا وَهُوَ حَسِيرٌ', 'ثم ارجع البصر كرتين ينقلب إليك البصر خاسئا وهو حسير', 29, 57, 562, 225, '67:4'),
(5246, NULL, NULL, 67, 5, 'وَلَقَدْ زَيَّنَّا ٱلسَّمَآءَ ٱلدُّنْيَا بِمَصَٰبِيحَ وَجَعَلْنَٰهَا رُجُومًا لِّلشَّيَٰطِينِ وَأَعْتَدْنَا لَهُمْ عَذَابَ ٱلسَّعِيرِ', 'ولقد زينا السماء الدنيا بمصابيح وجعلناها رجوما للشياطين وأعتدنا لهم عذاب السعير', 29, 57, 562, 225, '67:5'),
(5247, NULL, NULL, 67, 6, 'وَلِلَّذِينَ كَفَرُوا۟ بِرَبِّهِمْ عَذَابُ جَهَنَّمَ وَبِئْسَ ٱلْمَصِيرُ', 'وللذين كفروا بربهم عذاب جهنم وبئس المصير', 29, 57, 562, 225, '67:6'),
(5248, NULL, NULL, 67, 7, 'إِذَآ أُلْقُوا۟ فِيهَا سَمِعُوا۟ لَهَا شَهِيقًا وَهِىَ تَفُورُ', 'إذا ألقوا فيها سمعوا لها شهيقا وهي تفور', 29, 57, 562, 225, '67:7'),
(5249, NULL, NULL, 67, 8, 'تَكَادُ تَمَيَّزُ مِنَ ٱلْغَيْظِ كُلَّمَآ أُلْقِىَ فِيهَا فَوْجٌ سَأَلَهُمْ خَزَنَتُهَآ أَلَمْ يَأْتِكُمْ نَذِيرٌ', 'تكاد تميز من الغيظ كلما ألقي فيها فوج سألهم خزنتها ألم يأتكم نذير', 29, 57, 562, 225, '67:8'),
(5250, NULL, NULL, 67, 9, 'قَالُوا۟ بَلَىٰ قَدْ جَآءَنَا نَذِيرٌ فَكَذَّبْنَا وَقُلْنَا مَا نَزَّلَ ٱللَّهُ مِن شَىْءٍ إِنْ أَنتُمْ إِلَّا فِى ضَلَٰلٍ كَبِيرٍ', 'قالوا بلى قد جاءنا نذير فكذبنا وقلنا ما نزل الله من شيء إن أنتم إلا في ضلال كبير', 29, 57, 562, 225, '67:9'),
(5251, NULL, NULL, 67, 10, 'وَقَالُوا۟ لَوْ كُنَّا نَسْمَعُ أَوْ نَعْقِلُ مَا كُنَّا فِىٓ أَصْحَٰبِ ٱلسَّعِيرِ', 'وقالوا لو كنا نسمع أو نعقل ما كنا في أصحاب السعير', 29, 57, 562, 225, '67:10'),
(5252, NULL, NULL, 67, 11, 'فَٱعْتَرَفُوا۟ بِذَنۢبِهِمْ فَسُحْقًا لِّأَصْحَٰبِ ٱلسَّعِيرِ', 'فاعترفوا بذنبهم فسحقا لأصحاب السعير', 29, 57, 562, 225, '67:11'),
(5253, NULL, NULL, 67, 12, 'إِنَّ ٱلَّذِينَ يَخْشَوْنَ رَبَّهُم بِٱلْغَيْبِ لَهُم مَّغْفِرَةٌ وَأَجْرٌ كَبِيرٌ', 'إن الذين يخشون ربهم بالغيب لهم مغفرة وأجر كبير', 29, 57, 562, 225, '67:12'),
(5254, NULL, NULL, 67, 13, 'وَأَسِرُّوا۟ قَوْلَكُمْ أَوِ ٱجْهَرُوا۟ بِهِۦٓ إِنَّهُۥ عَلِيمٌۢ بِذَاتِ ٱلصُّدُورِ', 'وأسروا قولكم أو اجهروا به إنه عليم بذات الصدور', 29, 57, 563, 225, '67:13'),
(5255, NULL, NULL, 67, 14, 'أَلَا يَعْلَمُ مَنْ خَلَقَ وَهُوَ ٱللَّطِيفُ ٱلْخَبِيرُ', 'ألا يعلم من خلق وهو اللطيف الخبير', 29, 57, 563, 225, '67:14'),
(5256, NULL, NULL, 67, 15, 'هُوَ ٱلَّذِى جَعَلَ لَكُمُ ٱلْأَرْضَ ذَلُولًا فَٱمْشُوا۟ فِى مَنَاكِبِهَا وَكُلُوا۟ مِن رِّزْقِهِۦ وَإِلَيْهِ ٱلنُّشُورُ', 'هو الذي جعل لكم الأرض ذلولا فامشوا في مناكبها وكلوا من رزقه وإليه النشور', 29, 57, 563, 225, '67:15'),
(5257, NULL, NULL, 67, 16, 'ءَأَمِنتُم مَّن فِى ٱلسَّمَآءِ أَن يَخْسِفَ بِكُمُ ٱلْأَرْضَ فَإِذَا هِىَ تَمُورُ', 'أأمنتم من في السماء أن يخسف بكم الأرض فإذا هي تمور', 29, 57, 563, 225, '67:16'),
(5258, NULL, NULL, 67, 17, 'أَمْ أَمِنتُم مَّن فِى ٱلسَّمَآءِ أَن يُرْسِلَ عَلَيْكُمْ حَاصِبًا فَسَتَعْلَمُونَ كَيْفَ نَذِيرِ', 'أم أمنتم من في السماء أن يرسل عليكم حاصبا فستعلمون كيف نذير', 29, 57, 563, 225, '67:17'),
(5259, NULL, NULL, 67, 18, 'وَلَقَدْ كَذَّبَ ٱلَّذِينَ مِن قَبْلِهِمْ فَكَيْفَ كَانَ نَكِيرِ', 'ولقد كذب الذين من قبلهم فكيف كان نكير', 29, 57, 563, 225, '67:18'),
(5260, NULL, NULL, 67, 19, 'أَوَلَمْ يَرَوْا۟ إِلَى ٱلطَّيْرِ فَوْقَهُمْ صَٰٓفَّٰتٍ وَيَقْبِضْنَ مَا يُمْسِكُهُنَّ إِلَّا ٱلرَّحْمَٰنُ إِنَّهُۥ بِكُلِّ شَىْءٍۭ بَصِيرٌ', 'أولم يروا إلى الطير فوقهم صافات ويقبضن ما يمسكهن إلا الرحمن إنه بكل شيء بصير', 29, 57, 563, 225, '67:19'),
(5261, NULL, NULL, 67, 20, 'أَمَّنْ هَٰذَا ٱلَّذِى هُوَ جُندٌ لَّكُمْ يَنصُرُكُم مِّن دُونِ ٱلرَّحْمَٰنِ إِنِ ٱلْكَٰفِرُونَ إِلَّا فِى غُرُورٍ', 'أمن هذا الذي هو جند لكم ينصركم من دون الرحمن إن الكافرون إلا في غرور', 29, 57, 563, 225, '67:20'),
(5262, NULL, NULL, 67, 21, 'أَمَّنْ هَٰذَا ٱلَّذِى يَرْزُقُكُمْ إِنْ أَمْسَكَ رِزْقَهُۥ بَل لَّجُّوا۟ فِى عُتُوٍّ وَنُفُورٍ', 'أمن هذا الذي يرزقكم إن أمسك رزقه بل لجوا في عتو ونفور', 29, 57, 563, 225, '67:21'),
(5263, NULL, NULL, 67, 22, 'أَفَمَن يَمْشِى مُكِبًّا عَلَىٰ وَجْهِهِۦٓ أَهْدَىٰٓ أَمَّن يَمْشِى سَوِيًّا عَلَىٰ صِرَٰطٍ مُّسْتَقِيمٍ', 'أفمن يمشي مكبا على وجهه أهدى أمن يمشي سويا على صراط مستقيم', 29, 57, 563, 225, '67:22'),
(5264, NULL, NULL, 67, 23, 'قُلْ هُوَ ٱلَّذِىٓ أَنشَأَكُمْ وَجَعَلَ لَكُمُ ٱلسَّمْعَ وَٱلْأَبْصَٰرَ وَٱلْأَفْـِٔدَةَ قَلِيلًا مَّا تَشْكُرُونَ', 'قل هو الذي أنشأكم وجعل لكم السمع والأبصار والأفئدة قليلا ما تشكرون', 29, 57, 563, 225, '67:23'),
(5265, NULL, NULL, 67, 24, 'قُلْ هُوَ ٱلَّذِى ذَرَأَكُمْ فِى ٱلْأَرْضِ وَإِلَيْهِ تُحْشَرُونَ', 'قل هو الذي ذرأكم في الأرض وإليه تحشرون', 29, 57, 563, 225, '67:24'),
(5266, NULL, NULL, 67, 25, 'وَيَقُولُونَ مَتَىٰ هَٰذَا ٱلْوَعْدُ إِن كُنتُمْ صَٰدِقِينَ', 'ويقولون متى هذا الوعد إن كنتم صادقين', 29, 57, 563, 225, '67:25'),
(5267, NULL, NULL, 67, 26, 'قُلْ إِنَّمَا ٱلْعِلْمُ عِندَ ٱللَّهِ وَإِنَّمَآ أَنَا۠ نَذِيرٌ مُّبِينٌ', 'قل إنما العلم عند الله وإنما أنا نذير مبين', 29, 57, 563, 225, '67:26'),
(5268, NULL, NULL, 67, 27, 'فَلَمَّا رَأَوْهُ زُلْفَةً سِيٓـَٔتْ وُجُوهُ ٱلَّذِينَ كَفَرُوا۟ وَقِيلَ هَٰذَا ٱلَّذِى كُنتُم بِهِۦ تَدَّعُونَ', 'فلما رأوه زلفة سيئت وجوه الذين كفروا وقيل هذا الذي كنتم به تدعون', 29, 57, 564, 225, '67:27'),
(5269, NULL, NULL, 67, 28, 'قُلْ أَرَءَيْتُمْ إِنْ أَهْلَكَنِىَ ٱللَّهُ وَمَن مَّعِىَ أَوْ رَحِمَنَا فَمَن يُجِيرُ ٱلْكَٰفِرِينَ مِنْ عَذَابٍ أَلِيمٍ', 'قل أرأيتم إن أهلكني الله ومن معي أو رحمنا فمن يجير الكافرين من عذاب أليم', 29, 57, 564, 225, '67:28'),
(5270, NULL, NULL, 67, 29, 'قُلْ هُوَ ٱلرَّحْمَٰنُ ءَامَنَّا بِهِۦ وَعَلَيْهِ تَوَكَّلْنَا فَسَتَعْلَمُونَ مَنْ هُوَ فِى ضَلَٰلٍ مُّبِينٍ', 'قل هو الرحمن آمنا به وعليه توكلنا فستعلمون من هو في ضلال مبين', 29, 57, 564, 225, '67:29'),
(5271, NULL, NULL, 67, 30, 'قُلْ أَرَءَيْتُمْ إِنْ أَصْبَحَ مَآؤُكُمْ غَوْرًا فَمَن يَأْتِيكُم بِمَآءٍ مَّعِينٍۭ', 'قل أرأيتم إن أصبح ماؤكم غورا فمن يأتيكم بماء معين', 29, 57, 564, 225, '67:30'),
(5272, NULL, NULL, 68, 1, 'نٓ وَٱلْقَلَمِ وَمَا يَسْطُرُونَ', 'ن والقلم وما يسطرون', 29, 57, 564, 226, '68:1'),
(5273, NULL, NULL, 68, 2, 'مَآ أَنتَ بِنِعْمَةِ رَبِّكَ بِمَجْنُونٍ', 'ما أنت بنعمة ربك بمجنون', 29, 57, 564, 226, '68:2'),
(5274, NULL, NULL, 68, 3, 'وَإِنَّ لَكَ لَأَجْرًا غَيْرَ مَمْنُونٍ', 'وإن لك لأجرا غير ممنون', 29, 57, 564, 226, '68:3'),
(5275, NULL, NULL, 68, 4, 'وَإِنَّكَ لَعَلَىٰ خُلُقٍ عَظِيمٍ', 'وإنك لعلى خلق عظيم', 29, 57, 564, 226, '68:4'),
(5276, NULL, NULL, 68, 5, 'فَسَتُبْصِرُ وَيُبْصِرُونَ', 'فستبصر ويبصرون', 29, 57, 564, 226, '68:5'),
(5277, NULL, NULL, 68, 6, 'بِأَييِّكُمُ ٱلْمَفْتُونُ', 'بأييكم المفتون', 29, 57, 564, 226, '68:6'),
(5278, NULL, NULL, 68, 7, 'إِنَّ رَبَّكَ هُوَ أَعْلَمُ بِمَن ضَلَّ عَن سَبِيلِهِۦ وَهُوَ أَعْلَمُ بِٱلْمُهْتَدِينَ', 'إن ربك هو أعلم بمن ضل عن سبيله وهو أعلم بالمهتدين', 29, 57, 564, 226, '68:7'),
(5279, NULL, NULL, 68, 8, 'فَلَا تُطِعِ ٱلْمُكَذِّبِينَ', 'فلا تطع المكذبين', 29, 57, 564, 226, '68:8'),
(5280, NULL, NULL, 68, 9, 'وَدُّوا۟ لَوْ تُدْهِنُ فَيُدْهِنُونَ', 'ودوا لو تدهن فيدهنون', 29, 57, 564, 226, '68:9'),
(5281, NULL, NULL, 68, 10, 'وَلَا تُطِعْ كُلَّ حَلَّافٍ مَّهِينٍ', 'ولا تطع كل حلاف مهين', 29, 57, 564, 226, '68:10'),
(5282, NULL, NULL, 68, 11, 'هَمَّازٍ مَّشَّآءٍۭ بِنَمِيمٍ', 'هماز مشاء بنميم', 29, 57, 564, 226, '68:11'),
(5283, NULL, NULL, 68, 12, 'مَّنَّاعٍ لِّلْخَيْرِ مُعْتَدٍ أَثِيمٍ', 'مناع للخير معتد أثيم', 29, 57, 564, 226, '68:12'),
(5284, NULL, NULL, 68, 13, 'عُتُلٍّۭ بَعْدَ ذَٰلِكَ زَنِيمٍ', 'عتل بعد ذلك زنيم', 29, 57, 564, 226, '68:13'),
(5285, NULL, NULL, 68, 14, 'أَن كَانَ ذَا مَالٍ وَبَنِينَ', 'أن كان ذا مال وبنين', 29, 57, 564, 226, '68:14'),
(5286, NULL, NULL, 68, 15, 'إِذَا تُتْلَىٰ عَلَيْهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلْأَوَّلِينَ', 'إذا تتلى عليه آياتنا قال أساطير الأولين', 29, 57, 564, 226, '68:15'),
(5287, NULL, NULL, 68, 16, 'سَنَسِمُهُۥ عَلَى ٱلْخُرْطُومِ', 'سنسمه على الخرطوم', 29, 57, 565, 226, '68:16'),
(5288, NULL, NULL, 68, 17, 'إِنَّا بَلَوْنَٰهُمْ كَمَا بَلَوْنَآ أَصْحَٰبَ ٱلْجَنَّةِ إِذْ أَقْسَمُوا۟ لَيَصْرِمُنَّهَا مُصْبِحِينَ', 'إنا بلوناهم كما بلونا أصحاب الجنة إذ أقسموا ليصرمنها مصبحين', 29, 57, 565, 226, '68:17'),
(5289, NULL, NULL, 68, 18, 'وَلَا يَسْتَثْنُونَ', 'ولا يستثنون', 29, 57, 565, 226, '68:18'),
(5290, NULL, NULL, 68, 19, 'فَطَافَ عَلَيْهَا طَآئِفٌ مِّن رَّبِّكَ وَهُمْ نَآئِمُونَ', 'فطاف عليها طائف من ربك وهم نائمون', 29, 57, 565, 226, '68:19'),
(5291, NULL, NULL, 68, 20, 'فَأَصْبَحَتْ كَٱلصَّرِيمِ', 'فأصبحت كالصريم', 29, 57, 565, 226, '68:20'),
(5292, NULL, NULL, 68, 21, 'فَتَنَادَوْا۟ مُصْبِحِينَ', 'فتنادوا مصبحين', 29, 57, 565, 226, '68:21'),
(5293, NULL, NULL, 68, 22, 'أَنِ ٱغْدُوا۟ عَلَىٰ حَرْثِكُمْ إِن كُنتُمْ صَٰرِمِينَ', 'أن اغدوا على حرثكم إن كنتم صارمين', 29, 57, 565, 226, '68:22'),
(5294, NULL, NULL, 68, 23, 'فَٱنطَلَقُوا۟ وَهُمْ يَتَخَٰفَتُونَ', 'فانطلقوا وهم يتخافتون', 29, 57, 565, 226, '68:23'),
(5295, NULL, NULL, 68, 24, 'أَن لَّا يَدْخُلَنَّهَا ٱلْيَوْمَ عَلَيْكُم مِّسْكِينٌ', 'أن لا يدخلنها اليوم عليكم مسكين', 29, 57, 565, 226, '68:24'),
(5296, NULL, NULL, 68, 25, 'وَغَدَوْا۟ عَلَىٰ حَرْدٍ قَٰدِرِينَ', 'وغدوا على حرد قادرين', 29, 57, 565, 226, '68:25'),
(5297, NULL, NULL, 68, 26, 'فَلَمَّا رَأَوْهَا قَالُوٓا۟ إِنَّا لَضَآلُّونَ', 'فلما رأوها قالوا إنا لضالون', 29, 57, 565, 226, '68:26'),
(5298, NULL, NULL, 68, 27, 'بَلْ نَحْنُ مَحْرُومُونَ', 'بل نحن محرومون', 29, 57, 565, 226, '68:27'),
(5299, NULL, NULL, 68, 28, 'قَالَ أَوْسَطُهُمْ أَلَمْ أَقُل لَّكُمْ لَوْلَا تُسَبِّحُونَ', 'قال أوسطهم ألم أقل لكم لولا تسبحون', 29, 57, 565, 226, '68:28'),
(5300, NULL, NULL, 68, 29, 'قَالُوا۟ سُبْحَٰنَ رَبِّنَآ إِنَّا كُنَّا ظَٰلِمِينَ', 'قالوا سبحان ربنا إنا كنا ظالمين', 29, 57, 565, 226, '68:29'),
(5301, NULL, NULL, 68, 30, 'فَأَقْبَلَ بَعْضُهُمْ عَلَىٰ بَعْضٍ يَتَلَٰوَمُونَ', 'فأقبل بعضهم على بعض يتلاومون', 29, 57, 565, 226, '68:30'),
(5302, NULL, NULL, 68, 31, 'قَالُوا۟ يَٰوَيْلَنَآ إِنَّا كُنَّا طَٰغِينَ', 'قالوا يا ويلنا إنا كنا طاغين', 29, 57, 565, 226, '68:31'),
(5303, NULL, NULL, 68, 32, 'عَسَىٰ رَبُّنَآ أَن يُبْدِلَنَا خَيْرًا مِّنْهَآ إِنَّآ إِلَىٰ رَبِّنَا رَٰغِبُونَ', 'عسى ربنا أن يبدلنا خيرا منها إنا إلى ربنا راغبون', 29, 57, 565, 226, '68:32'),
(5304, NULL, NULL, 68, 33, 'كَذَٰلِكَ ٱلْعَذَابُ وَلَعَذَابُ ٱلْءَاخِرَةِ أَكْبَرُ لَوْ كَانُوا۟ يَعْلَمُونَ', 'كذلك العذاب ولعذاب الآخرة أكبر لو كانوا يعلمون', 29, 57, 565, 226, '68:33'),
(5305, NULL, NULL, 68, 34, 'إِنَّ لِلْمُتَّقِينَ عِندَ رَبِّهِمْ جَنَّٰتِ ٱلنَّعِيمِ', 'إن للمتقين عند ربهم جنات النعيم', 29, 57, 565, 226, '68:34'),
(5306, NULL, NULL, 68, 35, 'أَفَنَجْعَلُ ٱلْمُسْلِمِينَ كَٱلْمُجْرِمِينَ', 'أفنجعل المسلمين كالمجرمين', 29, 57, 565, 226, '68:35'),
(5307, NULL, NULL, 68, 36, 'مَا لَكُمْ كَيْفَ تَحْكُمُونَ', 'ما لكم كيف تحكمون', 29, 57, 565, 226, '68:36'),
(5308, NULL, NULL, 68, 37, 'أَمْ لَكُمْ كِتَٰبٌ فِيهِ تَدْرُسُونَ', 'أم لكم كتاب فيه تدرسون', 29, 57, 565, 226, '68:37'),
(5309, NULL, NULL, 68, 38, 'إِنَّ لَكُمْ فِيهِ لَمَا تَخَيَّرُونَ', 'إن لكم فيه لما تخيرون', 29, 57, 565, 226, '68:38'),
(5310, NULL, NULL, 68, 39, 'أَمْ لَكُمْ أَيْمَٰنٌ عَلَيْنَا بَٰلِغَةٌ إِلَىٰ يَوْمِ ٱلْقِيَٰمَةِ إِنَّ لَكُمْ لَمَا تَحْكُمُونَ', 'أم لكم أيمان علينا بالغة إلى يوم القيامة إن لكم لما تحكمون', 29, 57, 565, 226, '68:39'),
(5311, NULL, NULL, 68, 40, 'سَلْهُمْ أَيُّهُم بِذَٰلِكَ زَعِيمٌ', 'سلهم أيهم بذلك زعيم', 29, 57, 565, 226, '68:40'),
(5312, NULL, NULL, 68, 41, 'أَمْ لَهُمْ شُرَكَآءُ فَلْيَأْتُوا۟ بِشُرَكَآئِهِمْ إِن كَانُوا۟ صَٰدِقِينَ', 'أم لهم شركاء فليأتوا بشركائهم إن كانوا صادقين', 29, 57, 565, 226, '68:41'),
(5313, NULL, NULL, 68, 42, 'يَوْمَ يُكْشَفُ عَن سَاقٍ وَيُدْعَوْنَ إِلَى ٱلسُّجُودِ فَلَا يَسْتَطِيعُونَ', 'يوم يكشف عن ساق ويدعون إلى السجود فلا يستطيعون', 29, 57, 565, 226, '68:42'),
(5314, NULL, NULL, 68, 43, 'خَٰشِعَةً أَبْصَٰرُهُمْ تَرْهَقُهُمْ ذِلَّةٌ وَقَدْ كَانُوا۟ يُدْعَوْنَ إِلَى ٱلسُّجُودِ وَهُمْ سَٰلِمُونَ', 'خاشعة أبصارهم ترهقهم ذلة وقد كانوا يدعون إلى السجود وهم سالمون', 29, 57, 566, 226, '68:43'),
(5315, NULL, NULL, 68, 44, 'فَذَرْنِى وَمَن يُكَذِّبُ بِهَٰذَا ٱلْحَدِيثِ سَنَسْتَدْرِجُهُم مِّنْ حَيْثُ لَا يَعْلَمُونَ', 'فذرني ومن يكذب بهذا الحديث سنستدرجهم من حيث لا يعلمون', 29, 57, 566, 226, '68:44'),
(5316, NULL, NULL, 68, 45, 'وَأُمْلِى لَهُمْ إِنَّ كَيْدِى مَتِينٌ', 'وأملي لهم إن كيدي متين', 29, 57, 566, 226, '68:45'),
(5317, NULL, NULL, 68, 46, 'أَمْ تَسْـَٔلُهُمْ أَجْرًا فَهُم مِّن مَّغْرَمٍ مُّثْقَلُونَ', 'أم تسألهم أجرا فهم من مغرم مثقلون', 29, 57, 566, 226, '68:46'),
(5318, NULL, NULL, 68, 47, 'أَمْ عِندَهُمُ ٱلْغَيْبُ فَهُمْ يَكْتُبُونَ', 'أم عندهم الغيب فهم يكتبون', 29, 57, 566, 226, '68:47'),
(5319, NULL, NULL, 68, 48, 'فَٱصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تَكُن كَصَاحِبِ ٱلْحُوتِ إِذْ نَادَىٰ وَهُوَ مَكْظُومٌ', 'فاصبر لحكم ربك ولا تكن كصاحب الحوت إذ نادى وهو مكظوم', 29, 57, 566, 226, '68:48'),
(5320, NULL, NULL, 68, 49, 'لَّوْلَآ أَن تَدَٰرَكَهُۥ نِعْمَةٌ مِّن رَّبِّهِۦ لَنُبِذَ بِٱلْعَرَآءِ وَهُوَ مَذْمُومٌ', 'لولا أن تداركه نعمة من ربه لنبذ بالعراء وهو مذموم', 29, 57, 566, 226, '68:49'),
(5321, NULL, NULL, 68, 50, 'فَٱجْتَبَٰهُ رَبُّهُۥ فَجَعَلَهُۥ مِنَ ٱلصَّٰلِحِينَ', 'فاجتباه ربه فجعله من الصالحين', 29, 57, 566, 226, '68:50'),
(5322, NULL, NULL, 68, 51, 'وَإِن يَكَادُ ٱلَّذِينَ كَفَرُوا۟ لَيُزْلِقُونَكَ بِأَبْصَٰرِهِمْ لَمَّا سَمِعُوا۟ ٱلذِّكْرَ وَيَقُولُونَ إِنَّهُۥ لَمَجْنُونٌ', 'وإن يكاد الذين كفروا ليزلقونك بأبصارهم لما سمعوا الذكر ويقولون إنه لمجنون', 29, 57, 566, 226, '68:51'),
(5323, NULL, NULL, 68, 52, 'وَمَا هُوَ إِلَّا ذِكْرٌ لِّلْعَٰلَمِينَ', 'وما هو إلا ذكر للعالمين', 29, 57, 566, 226, '68:52'),
(5324, NULL, NULL, 69, 1, 'ٱلْحَآقَّةُ', 'الحاقة', 29, 57, 566, 227, '69:1'),
(5325, NULL, NULL, 69, 2, 'مَا ٱلْحَآقَّةُ', 'ما الحاقة', 29, 57, 566, 227, '69:2'),
(5326, NULL, NULL, 69, 3, 'وَمَآ أَدْرَىٰكَ مَا ٱلْحَآقَّةُ', 'وما أدراك ما الحاقة', 29, 57, 566, 227, '69:3'),
(5327, NULL, NULL, 69, 4, 'كَذَّبَتْ ثَمُودُ وَعَادٌۢ بِٱلْقَارِعَةِ', 'كذبت ثمود وعاد بالقارعة', 29, 57, 566, 227, '69:4'),
(5328, NULL, NULL, 69, 5, 'فَأَمَّا ثَمُودُ فَأُهْلِكُوا۟ بِٱلطَّاغِيَةِ', 'فأما ثمود فأهلكوا بالطاغية', 29, 57, 566, 227, '69:5'),
(5329, NULL, NULL, 69, 6, 'وَأَمَّا عَادٌ فَأُهْلِكُوا۟ بِرِيحٍ صَرْصَرٍ عَاتِيَةٍ', 'وأما عاد فأهلكوا بريح صرصر عاتية', 29, 57, 566, 227, '69:6'),
(5330, NULL, NULL, 69, 7, 'سَخَّرَهَا عَلَيْهِمْ سَبْعَ لَيَالٍ وَثَمَٰنِيَةَ أَيَّامٍ حُسُومًا فَتَرَى ٱلْقَوْمَ فِيهَا صَرْعَىٰ كَأَنَّهُمْ أَعْجَازُ نَخْلٍ خَاوِيَةٍ', 'سخرها عليهم سبع ليال وثمانية أيام حسوما فترى القوم فيها صرعى كأنهم أعجاز نخل خاوية', 29, 57, 566, 227, '69:7'),
(5331, NULL, NULL, 69, 8, 'فَهَلْ تَرَىٰ لَهُم مِّنۢ بَاقِيَةٍ', 'فهل ترى لهم من باقية', 29, 57, 566, 227, '69:8'),
(5332, NULL, NULL, 69, 9, 'وَجَآءَ فِرْعَوْنُ وَمَن قَبْلَهُۥ وَٱلْمُؤْتَفِكَٰتُ بِٱلْخَاطِئَةِ', 'وجاء فرعون ومن قبله والمؤتفكات بالخاطئة', 29, 57, 567, 227, '69:9'),
(5333, NULL, NULL, 69, 10, 'فَعَصَوْا۟ رَسُولَ رَبِّهِمْ فَأَخَذَهُمْ أَخْذَةً رَّابِيَةً', 'فعصوا رسول ربهم فأخذهم أخذة رابية', 29, 57, 567, 227, '69:10'),
(5334, NULL, NULL, 69, 11, 'إِنَّا لَمَّا طَغَا ٱلْمَآءُ حَمَلْنَٰكُمْ فِى ٱلْجَارِيَةِ', 'إنا لما طغى الماء حملناكم في الجارية', 29, 57, 567, 227, '69:11'),
(5335, NULL, NULL, 69, 12, 'لِنَجْعَلَهَا لَكُمْ تَذْكِرَةً وَتَعِيَهَآ أُذُنٌ وَٰعِيَةٌ', 'لنجعلها لكم تذكرة وتعيها أذن واعية', 29, 57, 567, 227, '69:12'),
(5336, NULL, NULL, 69, 13, 'فَإِذَا نُفِخَ فِى ٱلصُّورِ نَفْخَةٌ وَٰحِدَةٌ', 'فإذا نفخ في الصور نفخة واحدة', 29, 57, 567, 227, '69:13'),
(5337, NULL, NULL, 69, 14, 'وَحُمِلَتِ ٱلْأَرْضُ وَٱلْجِبَالُ فَدُكَّتَا دَكَّةً وَٰحِدَةً', 'وحملت الأرض والجبال فدكتا دكة واحدة', 29, 57, 567, 227, '69:14'),
(5338, NULL, NULL, 69, 15, 'فَيَوْمَئِذٍ وَقَعَتِ ٱلْوَاقِعَةُ', 'فيومئذ وقعت الواقعة', 29, 57, 567, 227, '69:15'),
(5339, NULL, NULL, 69, 16, 'وَٱنشَقَّتِ ٱلسَّمَآءُ فَهِىَ يَوْمَئِذٍ وَاهِيَةٌ', 'وانشقت السماء فهي يومئذ واهية', 29, 57, 567, 227, '69:16'),
(5340, NULL, NULL, 69, 17, 'وَٱلْمَلَكُ عَلَىٰٓ أَرْجَآئِهَا وَيَحْمِلُ عَرْشَ رَبِّكَ فَوْقَهُمْ يَوْمَئِذٍ ثَمَٰنِيَةٌ', 'والملك على أرجائها ويحمل عرش ربك فوقهم يومئذ ثمانية', 29, 57, 567, 227, '69:17'),
(5341, NULL, NULL, 69, 18, 'يَوْمَئِذٍ تُعْرَضُونَ لَا تَخْفَىٰ مِنكُمْ خَافِيَةٌ', 'يومئذ تعرضون لا تخفى منكم خافية', 29, 57, 567, 227, '69:18'),
(5342, NULL, NULL, 69, 19, 'فَأَمَّا مَنْ أُوتِىَ كِتَٰبَهُۥ بِيَمِينِهِۦ فَيَقُولُ هَآؤُمُ ٱقْرَءُوا۟ كِتَٰبِيَهْ', 'فأما من أوتي كتابه بيمينه فيقول هاؤم اقرءوا كتابيه', 29, 57, 567, 227, '69:19'),
(5343, NULL, NULL, 69, 20, 'إِنِّى ظَنَنتُ أَنِّى مُلَٰقٍ حِسَابِيَهْ', 'إني ظننت أني ملاق حسابيه', 29, 57, 567, 227, '69:20'),
(5344, NULL, NULL, 69, 21, 'فَهُوَ فِى عِيشَةٍ رَّاضِيَةٍ', 'فهو في عيشة راضية', 29, 57, 567, 227, '69:21'),
(5345, NULL, NULL, 69, 22, 'فِى جَنَّةٍ عَالِيَةٍ', 'في جنة عالية', 29, 57, 567, 227, '69:22'),
(5346, NULL, NULL, 69, 23, 'قُطُوفُهَا دَانِيَةٌ', 'قطوفها دانية', 29, 57, 567, 227, '69:23'),
(5347, NULL, NULL, 69, 24, 'كُلُوا۟ وَٱشْرَبُوا۟ هَنِيٓـًٔۢا بِمَآ أَسْلَفْتُمْ فِى ٱلْأَيَّامِ ٱلْخَالِيَةِ', 'كلوا واشربوا هنيئا بما أسلفتم في الأيام الخالية', 29, 57, 567, 227, '69:24'),
(5348, NULL, NULL, 69, 25, 'وَأَمَّا مَنْ أُوتِىَ كِتَٰبَهُۥ بِشِمَالِهِۦ فَيَقُولُ يَٰلَيْتَنِى لَمْ أُوتَ كِتَٰبِيَهْ', 'وأما من أوتي كتابه بشماله فيقول يا ليتني لم أوت كتابيه', 29, 57, 567, 227, '69:25'),
(5349, NULL, NULL, 69, 26, 'وَلَمْ أَدْرِ مَا حِسَابِيَهْ', 'ولم أدر ما حسابيه', 29, 57, 567, 227, '69:26'),
(5350, NULL, NULL, 69, 27, 'يَٰلَيْتَهَا كَانَتِ ٱلْقَاضِيَةَ', 'يا ليتها كانت القاضية', 29, 57, 567, 227, '69:27'),
(5351, NULL, NULL, 69, 28, 'مَآ أَغْنَىٰ عَنِّى مَالِيَهْ', 'ما أغنى عني ماليه', 29, 57, 567, 227, '69:28'),
(5352, NULL, NULL, 69, 29, 'هَلَكَ عَنِّى سُلْطَٰنِيَهْ', 'هلك عني سلطانيه', 29, 57, 567, 227, '69:29'),
(5353, NULL, NULL, 69, 30, 'خُذُوهُ فَغُلُّوهُ', 'خذوه فغلوه', 29, 57, 567, 227, '69:30'),
(5354, NULL, NULL, 69, 31, 'ثُمَّ ٱلْجَحِيمَ صَلُّوهُ', 'ثم الجحيم صلوه', 29, 57, 567, 227, '69:31'),
(5355, NULL, NULL, 69, 32, 'ثُمَّ فِى سِلْسِلَةٍ ذَرْعُهَا سَبْعُونَ ذِرَاعًا فَٱسْلُكُوهُ', 'ثم في سلسلة ذرعها سبعون ذراعا فاسلكوه', 29, 57, 567, 227, '69:32'),
(5356, NULL, NULL, 69, 33, 'إِنَّهُۥ كَانَ لَا يُؤْمِنُ بِٱللَّهِ ٱلْعَظِيمِ', 'إنه كان لا يؤمن بالله العظيم', 29, 57, 567, 227, '69:33'),
(5357, NULL, NULL, 69, 34, 'وَلَا يَحُضُّ عَلَىٰ طَعَامِ ٱلْمِسْكِينِ', 'ولا يحض على طعام المسكين', 29, 57, 567, 227, '69:34'),
(5358, NULL, NULL, 69, 35, 'فَلَيْسَ لَهُ ٱلْيَوْمَ هَٰهُنَا حَمِيمٌ', 'فليس له اليوم هاهنا حميم', 29, 57, 568, 227, '69:35'),
(5359, NULL, NULL, 69, 36, 'وَلَا طَعَامٌ إِلَّا مِنْ غِسْلِينٍ', 'ولا طعام إلا من غسلين', 29, 57, 568, 227, '69:36'),
(5360, NULL, NULL, 69, 37, 'لَّا يَأْكُلُهُۥٓ إِلَّا ٱلْخَٰطِـُٔونَ', 'لا يأكله إلا الخاطئون', 29, 57, 568, 227, '69:37'),
(5361, NULL, NULL, 69, 38, 'فَلَآ أُقْسِمُ بِمَا تُبْصِرُونَ', 'فلا أقسم بما تبصرون', 29, 57, 568, 227, '69:38'),
(5362, NULL, NULL, 69, 39, 'وَمَا لَا تُبْصِرُونَ', 'وما لا تبصرون', 29, 57, 568, 227, '69:39'),
(5363, NULL, NULL, 69, 40, 'إِنَّهُۥ لَقَوْلُ رَسُولٍ كَرِيمٍ', 'إنه لقول رسول كريم', 29, 57, 568, 227, '69:40'),
(5364, NULL, NULL, 69, 41, 'وَمَا هُوَ بِقَوْلِ شَاعِرٍ قَلِيلًا مَّا تُؤْمِنُونَ', 'وما هو بقول شاعر قليلا ما تؤمنون', 29, 57, 568, 227, '69:41'),
(5365, NULL, NULL, 69, 42, 'وَلَا بِقَوْلِ كَاهِنٍ قَلِيلًا مَّا تَذَكَّرُونَ', 'ولا بقول كاهن قليلا ما تذكرون', 29, 57, 568, 227, '69:42'),
(5366, NULL, NULL, 69, 43, 'تَنزِيلٌ مِّن رَّبِّ ٱلْعَٰلَمِينَ', 'تنزيل من رب العالمين', 29, 57, 568, 227, '69:43'),
(5367, NULL, NULL, 69, 44, 'وَلَوْ تَقَوَّلَ عَلَيْنَا بَعْضَ ٱلْأَقَاوِيلِ', 'ولو تقول علينا بعض الأقاويل', 29, 57, 568, 227, '69:44'),
(5368, NULL, NULL, 69, 45, 'لَأَخَذْنَا مِنْهُ بِٱلْيَمِينِ', 'لأخذنا منه باليمين', 29, 57, 568, 227, '69:45'),
(5369, NULL, NULL, 69, 46, 'ثُمَّ لَقَطَعْنَا مِنْهُ ٱلْوَتِينَ', 'ثم لقطعنا منه الوتين', 29, 57, 568, 227, '69:46'),
(5370, NULL, NULL, 69, 47, 'فَمَا مِنكُم مِّنْ أَحَدٍ عَنْهُ حَٰجِزِينَ', 'فما منكم من أحد عنه حاجزين', 29, 57, 568, 227, '69:47'),
(5371, NULL, NULL, 69, 48, 'وَإِنَّهُۥ لَتَذْكِرَةٌ لِّلْمُتَّقِينَ', 'وإنه لتذكرة للمتقين', 29, 57, 568, 227, '69:48'),
(5372, NULL, NULL, 69, 49, 'وَإِنَّا لَنَعْلَمُ أَنَّ مِنكُم مُّكَذِّبِينَ', 'وإنا لنعلم أن منكم مكذبين', 29, 57, 568, 227, '69:49'),
(5373, NULL, NULL, 69, 50, 'وَإِنَّهُۥ لَحَسْرَةٌ عَلَى ٱلْكَٰفِرِينَ', 'وإنه لحسرة على الكافرين', 29, 57, 568, 227, '69:50'),
(5374, NULL, NULL, 69, 51, 'وَإِنَّهُۥ لَحَقُّ ٱلْيَقِينِ', 'وإنه لحق اليقين', 29, 57, 568, 227, '69:51'),
(5375, NULL, NULL, 69, 52, 'فَسَبِّحْ بِٱسْمِ رَبِّكَ ٱلْعَظِيمِ', 'فسبح باسم ربك العظيم', 29, 57, 568, 227, '69:52'),
(5376, NULL, NULL, 70, 1, 'سَأَلَ سَآئِلٌۢ بِعَذَابٍ وَاقِعٍ', 'سأل سائل بعذاب واقع', 29, 57, 568, 227, '70:1'),
(5377, NULL, NULL, 70, 2, 'لِّلْكَٰفِرِينَ لَيْسَ لَهُۥ دَافِعٌ', 'للكافرين ليس له دافع', 29, 57, 568, 227, '70:2'),
(5378, NULL, NULL, 70, 3, 'مِّنَ ٱللَّهِ ذِى ٱلْمَعَارِجِ', 'من الله ذي المعارج', 29, 57, 568, 227, '70:3'),
(5379, NULL, NULL, 70, 4, 'تَعْرُجُ ٱلْمَلَٰٓئِكَةُ وَٱلرُّوحُ إِلَيْهِ فِى يَوْمٍ كَانَ مِقْدَارُهُۥ خَمْسِينَ أَلْفَ سَنَةٍ', 'تعرج الملائكة والروح إليه في يوم كان مقداره خمسين ألف سنة', 29, 57, 568, 227, '70:4'),
(5380, NULL, NULL, 70, 5, 'فَٱصْبِرْ صَبْرًا جَمِيلًا', 'فاصبر صبرا جميلا', 29, 57, 568, 227, '70:5'),
(5381, NULL, NULL, 70, 6, 'إِنَّهُمْ يَرَوْنَهُۥ بَعِيدًا', 'إنهم يرونه بعيدا', 29, 57, 568, 227, '70:6'),
(5382, NULL, NULL, 70, 7, 'وَنَرَىٰهُ قَرِيبًا', 'ونراه قريبا', 29, 57, 568, 227, '70:7'),
(5383, NULL, NULL, 70, 8, 'يَوْمَ تَكُونُ ٱلسَّمَآءُ كَٱلْمُهْلِ', 'يوم تكون السماء كالمهل', 29, 57, 568, 227, '70:8'),
(5384, NULL, NULL, 70, 9, 'وَتَكُونُ ٱلْجِبَالُ كَٱلْعِهْنِ', 'وتكون الجبال كالعهن', 29, 57, 568, 227, '70:9'),
(5385, NULL, NULL, 70, 10, 'وَلَا يَسْـَٔلُ حَمِيمٌ حَمِيمًا', 'ولا يسأل حميم حميما', 29, 57, 568, 227, '70:10'),
(5386, NULL, NULL, 70, 11, 'يُبَصَّرُونَهُمْ يَوَدُّ ٱلْمُجْرِمُ لَوْ يَفْتَدِى مِنْ عَذَابِ يَوْمِئِذٍۭ بِبَنِيهِ', 'يبصرونهم يود المجرم لو يفتدي من عذاب يومئذ ببنيه', 29, 57, 569, 227, '70:11'),
(5387, NULL, NULL, 70, 12, 'وَصَٰحِبَتِهِۦ وَأَخِيهِ', 'وصاحبته وأخيه', 29, 57, 569, 227, '70:12'),
(5388, NULL, NULL, 70, 13, 'وَفَصِيلَتِهِ ٱلَّتِى تُـْٔوِيهِ', 'وفصيلته التي تؤويه', 29, 57, 569, 227, '70:13'),
(5389, NULL, NULL, 70, 14, 'وَمَن فِى ٱلْأَرْضِ جَمِيعًا ثُمَّ يُنجِيهِ', 'ومن في الأرض جميعا ثم ينجيه', 29, 57, 569, 227, '70:14'),
(5390, NULL, NULL, 70, 15, 'كَلَّآ إِنَّهَا لَظَىٰ', 'كلا إنها لظى', 29, 57, 569, 227, '70:15'),
(5391, NULL, NULL, 70, 16, 'نَزَّاعَةً لِّلشَّوَىٰ', 'نزاعة للشوى', 29, 57, 569, 227, '70:16'),
(5392, NULL, NULL, 70, 17, 'تَدْعُوا۟ مَنْ أَدْبَرَ وَتَوَلَّىٰ', 'تدعو من أدبر وتولى', 29, 57, 569, 227, '70:17'),
(5393, NULL, NULL, 70, 18, 'وَجَمَعَ فَأَوْعَىٰٓ', 'وجمع فأوعى', 29, 57, 569, 227, '70:18'),
(5394, NULL, NULL, 70, 19, 'إِنَّ ٱلْإِنسَٰنَ خُلِقَ هَلُوعًا', 'إن الإنسان خلق هلوعا', 29, 57, 569, 228, '70:19'),
(5395, NULL, NULL, 70, 20, 'إِذَا مَسَّهُ ٱلشَّرُّ جَزُوعًا', 'إذا مسه الشر جزوعا', 29, 57, 569, 228, '70:20'),
(5396, NULL, NULL, 70, 21, 'وَإِذَا مَسَّهُ ٱلْخَيْرُ مَنُوعًا', 'وإذا مسه الخير منوعا', 29, 57, 569, 228, '70:21'),
(5397, NULL, NULL, 70, 22, 'إِلَّا ٱلْمُصَلِّينَ', 'إلا المصلين', 29, 57, 569, 228, '70:22'),
(5398, NULL, NULL, 70, 23, 'ٱلَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ دَآئِمُونَ', 'الذين هم على صلاتهم دائمون', 29, 57, 569, 228, '70:23'),
(5399, NULL, NULL, 70, 24, 'وَٱلَّذِينَ فِىٓ أَمْوَٰلِهِمْ حَقٌّ مَّعْلُومٌ', 'والذين في أموالهم حق معلوم', 29, 57, 569, 228, '70:24'),
(5400, NULL, NULL, 70, 25, 'لِّلسَّآئِلِ وَٱلْمَحْرُومِ', 'للسائل والمحروم', 29, 57, 569, 228, '70:25'),
(5401, NULL, NULL, 70, 26, 'وَٱلَّذِينَ يُصَدِّقُونَ بِيَوْمِ ٱلدِّينِ', 'والذين يصدقون بيوم الدين', 29, 57, 569, 228, '70:26'),
(5402, NULL, NULL, 70, 27, 'وَٱلَّذِينَ هُم مِّنْ عَذَابِ رَبِّهِم مُّشْفِقُونَ', 'والذين هم من عذاب ربهم مشفقون', 29, 57, 569, 228, '70:27'),
(5403, NULL, NULL, 70, 28, 'إِنَّ عَذَابَ رَبِّهِمْ غَيْرُ مَأْمُونٍ', 'إن عذاب ربهم غير مأمون', 29, 57, 569, 228, '70:28'),
(5404, NULL, NULL, 70, 29, 'وَٱلَّذِينَ هُمْ لِفُرُوجِهِمْ حَٰفِظُونَ', 'والذين هم لفروجهم حافظون', 29, 57, 569, 228, '70:29'),
(5405, NULL, NULL, 70, 30, 'إِلَّا عَلَىٰٓ أَزْوَٰجِهِمْ أَوْ مَا مَلَكَتْ أَيْمَٰنُهُمْ فَإِنَّهُمْ غَيْرُ مَلُومِينَ', 'إلا على أزواجهم أو ما ملكت أيمانهم فإنهم غير ملومين', 29, 57, 569, 228, '70:30'),
(5406, NULL, NULL, 70, 31, 'فَمَنِ ٱبْتَغَىٰ وَرَآءَ ذَٰلِكَ فَأُو۟لَٰٓئِكَ هُمُ ٱلْعَادُونَ', 'فمن ابتغى وراء ذلك فأولئك هم العادون', 29, 57, 569, 228, '70:31'),
(5407, NULL, NULL, 70, 32, 'وَٱلَّذِينَ هُمْ لِأَمَٰنَٰتِهِمْ وَعَهْدِهِمْ رَٰعُونَ', 'والذين هم لأماناتهم وعهدهم راعون', 29, 57, 569, 228, '70:32'),
(5408, NULL, NULL, 70, 33, 'وَٱلَّذِينَ هُم بِشَهَٰدَٰتِهِمْ قَآئِمُونَ', 'والذين هم بشهاداتهم قائمون', 29, 57, 569, 228, '70:33'),
(5409, NULL, NULL, 70, 34, 'وَٱلَّذِينَ هُمْ عَلَىٰ صَلَاتِهِمْ يُحَافِظُونَ', 'والذين هم على صلاتهم يحافظون', 29, 57, 569, 228, '70:34'),
(5410, NULL, NULL, 70, 35, 'أُو۟لَٰٓئِكَ فِى جَنَّٰتٍ مُّكْرَمُونَ', 'أولئك في جنات مكرمون', 29, 57, 569, 228, '70:35'),
(5411, NULL, NULL, 70, 36, 'فَمَالِ ٱلَّذِينَ كَفَرُوا۟ قِبَلَكَ مُهْطِعِينَ', 'فمال الذين كفروا قبلك مهطعين', 29, 57, 569, 228, '70:36'),
(5412, NULL, NULL, 70, 37, 'عَنِ ٱلْيَمِينِ وَعَنِ ٱلشِّمَالِ عِزِينَ', 'عن اليمين وعن الشمال عزين', 29, 57, 569, 228, '70:37'),
(5413, NULL, NULL, 70, 38, 'أَيَطْمَعُ كُلُّ ٱمْرِئٍ مِّنْهُمْ أَن يُدْخَلَ جَنَّةَ نَعِيمٍ', 'أيطمع كل امرئ منهم أن يدخل جنة نعيم', 29, 57, 569, 228, '70:38'),
(5414, NULL, NULL, 70, 39, 'كَلَّآ إِنَّا خَلَقْنَٰهُم مِّمَّا يَعْلَمُونَ', 'كلا إنا خلقناهم مما يعلمون', 29, 57, 569, 228, '70:39'),
(5415, NULL, NULL, 70, 40, 'فَلَآ أُقْسِمُ بِرَبِّ ٱلْمَشَٰرِقِ وَٱلْمَغَٰرِبِ إِنَّا لَقَٰدِرُونَ', 'فلا أقسم برب المشارق والمغارب إنا لقادرون', 29, 57, 570, 228, '70:40'),
(5416, NULL, NULL, 70, 41, 'عَلَىٰٓ أَن نُّبَدِّلَ خَيْرًا مِّنْهُمْ وَمَا نَحْنُ بِمَسْبُوقِينَ', 'على أن نبدل خيرا منهم وما نحن بمسبوقين', 29, 57, 570, 228, '70:41'),
(5417, NULL, NULL, 70, 42, 'فَذَرْهُمْ يَخُوضُوا۟ وَيَلْعَبُوا۟ حَتَّىٰ يُلَٰقُوا۟ يَوْمَهُمُ ٱلَّذِى يُوعَدُونَ', 'فذرهم يخوضوا ويلعبوا حتى يلاقوا يومهم الذي يوعدون', 29, 57, 570, 228, '70:42'),
(5418, NULL, NULL, 70, 43, 'يَوْمَ يَخْرُجُونَ مِنَ ٱلْأَجْدَاثِ سِرَاعًا كَأَنَّهُمْ إِلَىٰ نُصُبٍ يُوفِضُونَ', 'يوم يخرجون من الأجداث سراعا كأنهم إلى نصب يوفضون', 29, 57, 570, 228, '70:43'),
(5419, NULL, NULL, 70, 44, 'خَٰشِعَةً أَبْصَٰرُهُمْ تَرْهَقُهُمْ ذِلَّةٌ ذَٰلِكَ ٱلْيَوْمُ ٱلَّذِى كَانُوا۟ يُوعَدُونَ', 'خاشعة أبصارهم ترهقهم ذلة ذلك اليوم الذي كانوا يوعدون', 29, 57, 570, 228, '70:44'),
(5420, NULL, NULL, 71, 1, 'إِنَّآ أَرْسَلْنَا نُوحًا إِلَىٰ قَوْمِهِۦٓ أَنْ أَنذِرْ قَوْمَكَ مِن قَبْلِ أَن يَأْتِيَهُمْ عَذَابٌ أَلِيمٌ', 'إنا أرسلنا نوحا إلى قومه أن أنذر قومك من قبل أن يأتيهم عذاب أليم', 29, 57, 570, 228, '71:1'),
(5421, NULL, NULL, 71, 2, 'قَالَ يَٰقَوْمِ إِنِّى لَكُمْ نَذِيرٌ مُّبِينٌ', 'قال يا قوم إني لكم نذير مبين', 29, 57, 570, 228, '71:2'),
(5422, NULL, NULL, 71, 3, 'أَنِ ٱعْبُدُوا۟ ٱللَّهَ وَٱتَّقُوهُ وَأَطِيعُونِ', 'أن اعبدوا الله واتقوه وأطيعون', 29, 57, 570, 228, '71:3'),
(5423, NULL, NULL, 71, 4, 'يَغْفِرْ لَكُم مِّن ذُنُوبِكُمْ وَيُؤَخِّرْكُمْ إِلَىٰٓ أَجَلٍ مُّسَمًّى إِنَّ أَجَلَ ٱللَّهِ إِذَا جَآءَ لَا يُؤَخَّرُ لَوْ كُنتُمْ تَعْلَمُونَ', 'يغفر لكم من ذنوبكم ويؤخركم إلى أجل مسمى إن أجل الله إذا جاء لا يؤخر لو كنتم تعلمون', 29, 57, 570, 228, '71:4'),
(5424, NULL, NULL, 71, 5, 'قَالَ رَبِّ إِنِّى دَعَوْتُ قَوْمِى لَيْلًا وَنَهَارًا', 'قال رب إني دعوت قومي ليلا ونهارا', 29, 57, 570, 228, '71:5'),
(5425, NULL, NULL, 71, 6, 'فَلَمْ يَزِدْهُمْ دُعَآءِىٓ إِلَّا فِرَارًا', 'فلم يزدهم دعائي إلا فرارا', 29, 57, 570, 228, '71:6'),
(5426, NULL, NULL, 71, 7, 'وَإِنِّى كُلَّمَا دَعَوْتُهُمْ لِتَغْفِرَ لَهُمْ جَعَلُوٓا۟ أَصَٰبِعَهُمْ فِىٓ ءَاذَانِهِمْ وَٱسْتَغْشَوْا۟ ثِيَابَهُمْ وَأَصَرُّوا۟ وَٱسْتَكْبَرُوا۟ ٱسْتِكْبَارًا', 'وإني كلما دعوتهم لتغفر لهم جعلوا أصابعهم في آذانهم واستغشوا ثيابهم وأصروا واستكبروا استكبارا', 29, 57, 570, 228, '71:7'),
(5427, NULL, NULL, 71, 8, 'ثُمَّ إِنِّى دَعَوْتُهُمْ جِهَارًا', 'ثم إني دعوتهم جهارا', 29, 57, 570, 228, '71:8'),
(5428, NULL, NULL, 71, 9, 'ثُمَّ إِنِّىٓ أَعْلَنتُ لَهُمْ وَأَسْرَرْتُ لَهُمْ إِسْرَارًا', 'ثم إني أعلنت لهم وأسررت لهم إسرارا', 29, 57, 570, 228, '71:9'),
(5429, NULL, NULL, 71, 10, 'فَقُلْتُ ٱسْتَغْفِرُوا۟ رَبَّكُمْ إِنَّهُۥ كَانَ غَفَّارًا', 'فقلت استغفروا ربكم إنه كان غفارا', 29, 57, 570, 228, '71:10'),
(5430, NULL, NULL, 71, 11, 'يُرْسِلِ ٱلسَّمَآءَ عَلَيْكُم مِّدْرَارًا', 'يرسل السماء عليكم مدرارا', 29, 57, 571, 228, '71:11'),
(5431, NULL, NULL, 71, 12, 'وَيُمْدِدْكُم بِأَمْوَٰلٍ وَبَنِينَ وَيَجْعَل لَّكُمْ جَنَّٰتٍ وَيَجْعَل لَّكُمْ أَنْهَٰرًا', 'ويمددكم بأموال وبنين ويجعل لكم جنات ويجعل لكم أنهارا', 29, 57, 571, 228, '71:12'),
(5432, NULL, NULL, 71, 13, 'مَّا لَكُمْ لَا تَرْجُونَ لِلَّهِ وَقَارًا', 'ما لكم لا ترجون لله وقارا', 29, 57, 571, 228, '71:13'),
(5433, NULL, NULL, 71, 14, 'وَقَدْ خَلَقَكُمْ أَطْوَارًا', 'وقد خلقكم أطوارا', 29, 57, 571, 228, '71:14'),
(5434, NULL, NULL, 71, 15, 'أَلَمْ تَرَوْا۟ كَيْفَ خَلَقَ ٱللَّهُ سَبْعَ سَمَٰوَٰتٍ طِبَاقًا', 'ألم تروا كيف خلق الله سبع سماوات طباقا', 29, 57, 571, 228, '71:15'),
(5435, NULL, NULL, 71, 16, 'وَجَعَلَ ٱلْقَمَرَ فِيهِنَّ نُورًا وَجَعَلَ ٱلشَّمْسَ سِرَاجًا', 'وجعل القمر فيهن نورا وجعل الشمس سراجا', 29, 57, 571, 228, '71:16'),
(5436, NULL, NULL, 71, 17, 'وَٱللَّهُ أَنۢبَتَكُم مِّنَ ٱلْأَرْضِ نَبَاتًا', 'والله أنبتكم من الأرض نباتا', 29, 57, 571, 228, '71:17'),
(5437, NULL, NULL, 71, 18, 'ثُمَّ يُعِيدُكُمْ فِيهَا وَيُخْرِجُكُمْ إِخْرَاجًا', 'ثم يعيدكم فيها ويخرجكم إخراجا', 29, 57, 571, 228, '71:18'),
(5438, NULL, NULL, 71, 19, 'وَٱللَّهُ جَعَلَ لَكُمُ ٱلْأَرْضَ بِسَاطًا', 'والله جعل لكم الأرض بساطا', 29, 57, 571, 228, '71:19'),
(5439, NULL, NULL, 71, 20, 'لِّتَسْلُكُوا۟ مِنْهَا سُبُلًا فِجَاجًا', 'لتسلكوا منها سبلا فجاجا', 29, 57, 571, 228, '71:20'),
(5440, NULL, NULL, 71, 21, 'قَالَ نُوحٌ رَّبِّ إِنَّهُمْ عَصَوْنِى وَٱتَّبَعُوا۟ مَن لَّمْ يَزِدْهُ مَالُهُۥ وَوَلَدُهُۥٓ إِلَّا خَسَارًا', 'قال نوح رب إنهم عصوني واتبعوا من لم يزده ماله وولده إلا خسارا', 29, 57, 571, 228, '71:21'),
(5441, NULL, NULL, 71, 22, 'وَمَكَرُوا۟ مَكْرًا كُبَّارًا', 'ومكروا مكرا كبارا', 29, 57, 571, 228, '71:22'),
(5442, NULL, NULL, 71, 23, 'وَقَالُوا۟ لَا تَذَرُنَّ ءَالِهَتَكُمْ وَلَا تَذَرُنَّ وَدًّا وَلَا سُوَاعًا وَلَا يَغُوثَ وَيَعُوقَ وَنَسْرًا', 'وقالوا لا تذرن آلهتكم ولا تذرن ودا ولا سواعا ولا يغوث ويعوق ونسرا', 29, 57, 571, 228, '71:23'),
(5443, NULL, NULL, 71, 24, 'وَقَدْ أَضَلُّوا۟ كَثِيرًا وَلَا تَزِدِ ٱلظَّٰلِمِينَ إِلَّا ضَلَٰلًا', 'وقد أضلوا كثيرا ولا تزد الظالمين إلا ضلالا', 29, 57, 571, 228, '71:24'),
(5444, NULL, NULL, 71, 25, 'مِّمَّا خَطِيٓـَٰٔتِهِمْ أُغْرِقُوا۟ فَأُدْخِلُوا۟ نَارًا فَلَمْ يَجِدُوا۟ لَهُم مِّن دُونِ ٱللَّهِ أَنصَارًا', 'مما خطيئاتهم أغرقوا فأدخلوا نارا فلم يجدوا لهم من دون الله أنصارا', 29, 57, 571, 228, '71:25'),
(5445, NULL, NULL, 71, 26, 'وَقَالَ نُوحٌ رَّبِّ لَا تَذَرْ عَلَى ٱلْأَرْضِ مِنَ ٱلْكَٰفِرِينَ دَيَّارًا', 'وقال نوح رب لا تذر على الأرض من الكافرين ديارا', 29, 57, 571, 228, '71:26'),
(5446, NULL, NULL, 71, 27, 'إِنَّكَ إِن تَذَرْهُمْ يُضِلُّوا۟ عِبَادَكَ وَلَا يَلِدُوٓا۟ إِلَّا فَاجِرًا كَفَّارًا', 'إنك إن تذرهم يضلوا عبادك ولا يلدوا إلا فاجرا كفارا', 29, 57, 571, 228, '71:27'),
(5447, NULL, NULL, 71, 28, 'رَّبِّ ٱغْفِرْ لِى وَلِوَٰلِدَىَّ وَلِمَن دَخَلَ بَيْتِىَ مُؤْمِنًا وَلِلْمُؤْمِنِينَ وَٱلْمُؤْمِنَٰتِ وَلَا تَزِدِ ٱلظَّٰلِمِينَ إِلَّا تَبَارًۢا', 'رب اغفر لي ولوالدي ولمن دخل بيتي مؤمنا وللمؤمنين والمؤمنات ولا تزد الظالمين إلا تبارا', 29, 57, 571, 228, '71:28'),
(5448, NULL, NULL, 72, 1, 'قُلْ أُوحِىَ إِلَىَّ أَنَّهُ ٱسْتَمَعَ نَفَرٌ مِّنَ ٱلْجِنِّ فَقَالُوٓا۟ إِنَّا سَمِعْنَا قُرْءَانًا عَجَبًا', 'قل أوحي إلي أنه استمع نفر من الجن فقالوا إنا سمعنا قرآنا عجبا', 29, 58, 572, 229, '72:1'),
(5449, NULL, NULL, 72, 2, 'يَهْدِىٓ إِلَى ٱلرُّشْدِ فَـَٔامَنَّا بِهِۦ وَلَن نُّشْرِكَ بِرَبِّنَآ أَحَدًا', 'يهدي إلى الرشد فآمنا به ولن نشرك بربنا أحدا', 29, 58, 572, 229, '72:2'),
(5450, NULL, NULL, 72, 3, 'وَأَنَّهُۥ تَعَٰلَىٰ جَدُّ رَبِّنَا مَا ٱتَّخَذَ صَٰحِبَةً وَلَا وَلَدًا', 'وأنه تعالى جد ربنا ما اتخذ صاحبة ولا ولدا', 29, 58, 572, 229, '72:3'),
(5451, NULL, NULL, 72, 4, 'وَأَنَّهُۥ كَانَ يَقُولُ سَفِيهُنَا عَلَى ٱللَّهِ شَطَطًا', 'وأنه كان يقول سفيهنا على الله شططا', 29, 58, 572, 229, '72:4'),
(5452, NULL, NULL, 72, 5, 'وَأَنَّا ظَنَنَّآ أَن لَّن تَقُولَ ٱلْإِنسُ وَٱلْجِنُّ عَلَى ٱللَّهِ كَذِبًا', 'وأنا ظننا أن لن تقول الإنس والجن على الله كذبا', 29, 58, 572, 229, '72:5'),
(5453, NULL, NULL, 72, 6, 'وَأَنَّهُۥ كَانَ رِجَالٌ مِّنَ ٱلْإِنسِ يَعُوذُونَ بِرِجَالٍ مِّنَ ٱلْجِنِّ فَزَادُوهُمْ رَهَقًا', 'وأنه كان رجال من الإنس يعوذون برجال من الجن فزادوهم رهقا', 29, 58, 572, 229, '72:6'),
(5454, NULL, NULL, 72, 7, 'وَأَنَّهُمْ ظَنُّوا۟ كَمَا ظَنَنتُمْ أَن لَّن يَبْعَثَ ٱللَّهُ أَحَدًا', 'وأنهم ظنوا كما ظننتم أن لن يبعث الله أحدا', 29, 58, 572, 229, '72:7'),
(5455, NULL, NULL, 72, 8, 'وَأَنَّا لَمَسْنَا ٱلسَّمَآءَ فَوَجَدْنَٰهَا مُلِئَتْ حَرَسًا شَدِيدًا وَشُهُبًا', 'وأنا لمسنا السماء فوجدناها ملئت حرسا شديدا وشهبا', 29, 58, 572, 229, '72:8'),
(5456, NULL, NULL, 72, 9, 'وَأَنَّا كُنَّا نَقْعُدُ مِنْهَا مَقَٰعِدَ لِلسَّمْعِ فَمَن يَسْتَمِعِ ٱلْءَانَ يَجِدْ لَهُۥ شِهَابًا رَّصَدًا', 'وأنا كنا نقعد منها مقاعد للسمع فمن يستمع الآن يجد له شهابا رصدا', 29, 58, 572, 229, '72:9'),
(5457, NULL, NULL, 72, 10, 'وَأَنَّا لَا نَدْرِىٓ أَشَرٌّ أُرِيدَ بِمَن فِى ٱلْأَرْضِ أَمْ أَرَادَ بِهِمْ رَبُّهُمْ رَشَدًا', 'وأنا لا ندري أشر أريد بمن في الأرض أم أراد بهم ربهم رشدا', 29, 58, 572, 229, '72:10'),
(5458, NULL, NULL, 72, 11, 'وَأَنَّا مِنَّا ٱلصَّٰلِحُونَ وَمِنَّا دُونَ ذَٰلِكَ كُنَّا طَرَآئِقَ قِدَدًا', 'وأنا منا الصالحون ومنا دون ذلك كنا طرائق قددا', 29, 58, 572, 229, '72:11'),
(5459, NULL, NULL, 72, 12, 'وَأَنَّا ظَنَنَّآ أَن لَّن نُّعْجِزَ ٱللَّهَ فِى ٱلْأَرْضِ وَلَن نُّعْجِزَهُۥ هَرَبًا', 'وأنا ظننا أن لن نعجز الله في الأرض ولن نعجزه هربا', 29, 58, 572, 229, '72:12'),
(5460, NULL, NULL, 72, 13, 'وَأَنَّا لَمَّا سَمِعْنَا ٱلْهُدَىٰٓ ءَامَنَّا بِهِۦ فَمَن يُؤْمِنۢ بِرَبِّهِۦ فَلَا يَخَافُ بَخْسًا وَلَا رَهَقًا', 'وأنا لما سمعنا الهدى آمنا به فمن يؤمن بربه فلا يخاف بخسا ولا رهقا', 29, 58, 572, 229, '72:13'),
(5461, NULL, NULL, 72, 14, 'وَأَنَّا مِنَّا ٱلْمُسْلِمُونَ وَمِنَّا ٱلْقَٰسِطُونَ فَمَنْ أَسْلَمَ فَأُو۟لَٰٓئِكَ تَحَرَّوْا۟ رَشَدًا', 'وأنا منا المسلمون ومنا القاسطون فمن أسلم فأولئك تحروا رشدا', 29, 58, 573, 229, '72:14'),
(5462, NULL, NULL, 72, 15, 'وَأَمَّا ٱلْقَٰسِطُونَ فَكَانُوا۟ لِجَهَنَّمَ حَطَبًا', 'وأما القاسطون فكانوا لجهنم حطبا', 29, 58, 573, 229, '72:15'),
(5463, NULL, NULL, 72, 16, 'وَأَلَّوِ ٱسْتَقَٰمُوا۟ عَلَى ٱلطَّرِيقَةِ لَأَسْقَيْنَٰهُم مَّآءً غَدَقًا', 'وأن لو استقاموا على الطريقة لأسقيناهم ماء غدقا', 29, 58, 573, 229, '72:16'),
(5464, NULL, NULL, 72, 17, 'لِّنَفْتِنَهُمْ فِيهِ وَمَن يُعْرِضْ عَن ذِكْرِ رَبِّهِۦ يَسْلُكْهُ عَذَابًا صَعَدًا', 'لنفتنهم فيه ومن يعرض عن ذكر ربه يسلكه عذابا صعدا', 29, 58, 573, 229, '72:17'),
(5465, NULL, NULL, 72, 18, 'وَأَنَّ ٱلْمَسَٰجِدَ لِلَّهِ فَلَا تَدْعُوا۟ مَعَ ٱللَّهِ أَحَدًا', 'وأن المساجد لله فلا تدعوا مع الله أحدا', 29, 58, 573, 229, '72:18'),
(5466, NULL, NULL, 72, 19, 'وَأَنَّهُۥ لَمَّا قَامَ عَبْدُ ٱللَّهِ يَدْعُوهُ كَادُوا۟ يَكُونُونَ عَلَيْهِ لِبَدًا', 'وأنه لما قام عبد الله يدعوه كادوا يكونون عليه لبدا', 29, 58, 573, 229, '72:19'),
(5467, NULL, NULL, 72, 20, 'قُلْ إِنَّمَآ أَدْعُوا۟ رَبِّى وَلَآ أُشْرِكُ بِهِۦٓ أَحَدًا', 'قل إنما أدعو ربي ولا أشرك به أحدا', 29, 58, 573, 229, '72:20'),
(5468, NULL, NULL, 72, 21, 'قُلْ إِنِّى لَآ أَمْلِكُ لَكُمْ ضَرًّا وَلَا رَشَدًا', 'قل إني لا أملك لكم ضرا ولا رشدا', 29, 58, 573, 229, '72:21'),
(5469, NULL, NULL, 72, 22, 'قُلْ إِنِّى لَن يُجِيرَنِى مِنَ ٱللَّهِ أَحَدٌ وَلَنْ أَجِدَ مِن دُونِهِۦ مُلْتَحَدًا', 'قل إني لن يجيرني من الله أحد ولن أجد من دونه ملتحدا', 29, 58, 573, 229, '72:22'),
(5470, NULL, NULL, 72, 23, 'إِلَّا بَلَٰغًا مِّنَ ٱللَّهِ وَرِسَٰلَٰتِهِۦ وَمَن يَعْصِ ٱللَّهَ وَرَسُولَهُۥ فَإِنَّ لَهُۥ نَارَ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أَبَدًا', 'إلا بلاغا من الله ورسالاته ومن يعص الله ورسوله فإن له نار جهنم خالدين فيها أبدا', 29, 58, 573, 229, '72:23'),
(5471, NULL, NULL, 72, 24, 'حَتَّىٰٓ إِذَا رَأَوْا۟ مَا يُوعَدُونَ فَسَيَعْلَمُونَ مَنْ أَضْعَفُ نَاصِرًا وَأَقَلُّ عَدَدًا', 'حتى إذا رأوا ما يوعدون فسيعلمون من أضعف ناصرا وأقل عددا', 29, 58, 573, 229, '72:24'),
(5472, NULL, NULL, 72, 25, 'قُلْ إِنْ أَدْرِىٓ أَقَرِيبٌ مَّا تُوعَدُونَ أَمْ يَجْعَلُ لَهُۥ رَبِّىٓ أَمَدًا', 'قل إن أدري أقريب ما توعدون أم يجعل له ربي أمدا', 29, 58, 573, 229, '72:25'),
(5473, NULL, NULL, 72, 26, 'عَٰلِمُ ٱلْغَيْبِ فَلَا يُظْهِرُ عَلَىٰ غَيْبِهِۦٓ أَحَدًا', 'عالم الغيب فلا يظهر على غيبه أحدا', 29, 58, 573, 229, '72:26'),
(5474, NULL, NULL, 72, 27, 'إِلَّا مَنِ ٱرْتَضَىٰ مِن رَّسُولٍ فَإِنَّهُۥ يَسْلُكُ مِنۢ بَيْنِ يَدَيْهِ وَمِنْ خَلْفِهِۦ رَصَدًا', 'إلا من ارتضى من رسول فإنه يسلك من بين يديه ومن خلفه رصدا', 29, 58, 573, 229, '72:27'),
(5475, NULL, NULL, 72, 28, 'لِّيَعْلَمَ أَن قَدْ أَبْلَغُوا۟ رِسَٰلَٰتِ رَبِّهِمْ وَأَحَاطَ بِمَا لَدَيْهِمْ وَأَحْصَىٰ كُلَّ شَىْءٍ عَدَدًۢا', 'ليعلم أن قد أبلغوا رسالات ربهم وأحاط بما لديهم وأحصى كل شيء عددا', 29, 58, 573, 229, '72:28'),
(5476, NULL, NULL, 73, 1, 'يَٰٓأَيُّهَا ٱلْمُزَّمِّلُ', 'يا أيها المزمل', 29, 58, 574, 229, '73:1'),
(5477, NULL, NULL, 73, 2, 'قُمِ ٱلَّيْلَ إِلَّا قَلِيلًا', 'قم الليل إلا قليلا', 29, 58, 574, 229, '73:2'),
(5478, NULL, NULL, 73, 3, 'نِّصْفَهُۥٓ أَوِ ٱنقُصْ مِنْهُ قَلِيلًا', 'نصفه أو انقص منه قليلا', 29, 58, 574, 229, '73:3'),
(5479, NULL, NULL, 73, 4, 'أَوْ زِدْ عَلَيْهِ وَرَتِّلِ ٱلْقُرْءَانَ تَرْتِيلًا', 'أو زد عليه ورتل القرآن ترتيلا', 29, 58, 574, 229, '73:4'),
(5480, NULL, NULL, 73, 5, 'إِنَّا سَنُلْقِى عَلَيْكَ قَوْلًا ثَقِيلًا', 'إنا سنلقي عليك قولا ثقيلا', 29, 58, 574, 229, '73:5'),
(5481, NULL, NULL, 73, 6, 'إِنَّ نَاشِئَةَ ٱلَّيْلِ هِىَ أَشَدُّ وَطْـًٔا وَأَقْوَمُ قِيلًا', 'إن ناشئة الليل هي أشد وطئا وأقوم قيلا', 29, 58, 574, 229, '73:6'),
(5482, NULL, NULL, 73, 7, 'إِنَّ لَكَ فِى ٱلنَّهَارِ سَبْحًا طَوِيلًا', 'إن لك في النهار سبحا طويلا', 29, 58, 574, 229, '73:7'),
(5483, NULL, NULL, 73, 8, 'وَٱذْكُرِ ٱسْمَ رَبِّكَ وَتَبَتَّلْ إِلَيْهِ تَبْتِيلًا', 'واذكر اسم ربك وتبتل إليه تبتيلا', 29, 58, 574, 229, '73:8'),
(5484, NULL, NULL, 73, 9, 'رَّبُّ ٱلْمَشْرِقِ وَٱلْمَغْرِبِ لَآ إِلَٰهَ إِلَّا هُوَ فَٱتَّخِذْهُ وَكِيلًا', 'رب المشرق والمغرب لا إله إلا هو فاتخذه وكيلا', 29, 58, 574, 229, '73:9'),
(5485, NULL, NULL, 73, 10, 'وَٱصْبِرْ عَلَىٰ مَا يَقُولُونَ وَٱهْجُرْهُمْ هَجْرًا جَمِيلًا', 'واصبر على ما يقولون واهجرهم هجرا جميلا', 29, 58, 574, 229, '73:10'),
(5486, NULL, NULL, 73, 11, 'وَذَرْنِى وَٱلْمُكَذِّبِينَ أُو۟لِى ٱلنَّعْمَةِ وَمَهِّلْهُمْ قَلِيلًا', 'وذرني والمكذبين أولي النعمة ومهلهم قليلا', 29, 58, 574, 229, '73:11'),
(5487, NULL, NULL, 73, 12, 'إِنَّ لَدَيْنَآ أَنكَالًا وَجَحِيمًا', 'إن لدينا أنكالا وجحيما', 29, 58, 574, 229, '73:12'),
(5488, NULL, NULL, 73, 13, 'وَطَعَامًا ذَا غُصَّةٍ وَعَذَابًا أَلِيمًا', 'وطعاما ذا غصة وعذابا أليما', 29, 58, 574, 229, '73:13'),
(5489, NULL, NULL, 73, 14, 'يَوْمَ تَرْجُفُ ٱلْأَرْضُ وَٱلْجِبَالُ وَكَانَتِ ٱلْجِبَالُ كَثِيبًا مَّهِيلًا', 'يوم ترجف الأرض والجبال وكانت الجبال كثيبا مهيلا', 29, 58, 574, 229, '73:14'),
(5490, NULL, NULL, 73, 15, 'إِنَّآ أَرْسَلْنَآ إِلَيْكُمْ رَسُولًا شَٰهِدًا عَلَيْكُمْ كَمَآ أَرْسَلْنَآ إِلَىٰ فِرْعَوْنَ رَسُولًا', 'إنا أرسلنا إليكم رسولا شاهدا عليكم كما أرسلنا إلى فرعون رسولا', 29, 58, 574, 229, '73:15'),
(5491, NULL, NULL, 73, 16, 'فَعَصَىٰ فِرْعَوْنُ ٱلرَّسُولَ فَأَخَذْنَٰهُ أَخْذًا وَبِيلًا', 'فعصى فرعون الرسول فأخذناه أخذا وبيلا', 29, 58, 574, 229, '73:16'),
(5492, NULL, NULL, 73, 17, 'فَكَيْفَ تَتَّقُونَ إِن كَفَرْتُمْ يَوْمًا يَجْعَلُ ٱلْوِلْدَٰنَ شِيبًا', 'فكيف تتقون إن كفرتم يوما يجعل الولدان شيبا', 29, 58, 574, 229, '73:17'),
(5493, NULL, NULL, 73, 18, 'ٱلسَّمَآءُ مُنفَطِرٌۢ بِهِۦ كَانَ وَعْدُهُۥ مَفْعُولًا', 'السماء منفطر به كان وعده مفعولا', 29, 58, 574, 229, '73:18'),
(5494, NULL, NULL, 73, 19, 'إِنَّ هَٰذِهِۦ تَذْكِرَةٌ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا', 'إن هذه تذكرة فمن شاء اتخذ إلى ربه سبيلا', 29, 58, 574, 229, '73:19'),
(5495, NULL, NULL, 73, 20, 'إِنَّ رَبَّكَ يَعْلَمُ أَنَّكَ تَقُومُ أَدْنَىٰ مِن ثُلُثَىِ ٱلَّيْلِ وَنِصْفَهُۥ وَثُلُثَهُۥ وَطَآئِفَةٌ مِّنَ ٱلَّذِينَ مَعَكَ وَٱللَّهُ يُقَدِّرُ ٱلَّيْلَ وَٱلنَّهَارَ عَلِمَ أَن لَّن تُحْصُوهُ فَتَابَ عَلَيْكُمْ فَٱقْرَءُوا۟ مَا تَيَسَّرَ مِنَ ٱلْقُرْءَانِ عَلِمَ أَن سَيَكُونُ مِنكُم مَّرْضَىٰ وَءَاخَرُونَ يَضْرِبُونَ فِى ٱلْأَرْضِ يَبْتَغُونَ مِن فَضْلِ ٱللَّهِ وَءَاخَرُونَ يُقَٰتِلُونَ فِى سَبِيلِ ٱللَّهِ فَٱقْرَءُوا۟ مَا تَيَسَّرَ مِنْهُ وَأَقِيمُوا۟ ٱلصَّلَوٰةَ وَءَاتُوا۟ ٱلزَّكَوٰةَ وَأَقْرِضُوا۟ ٱللَّهَ قَرْضًا حَسَنًا وَمَا تُقَدِّمُوا۟ لِأَنفُسِكُم مِّنْ خَيْرٍ تَجِدُوهُ عِندَ ٱللَّهِ هُوَ خَيْرًا وَأَعْظَمَ أَجْرًا وَٱسْتَغْفِرُوا۟ ٱللَّهَ إِنَّ ٱللَّهَ غَفُورٌ رَّحِيمٌۢ', 'إن ربك يعلم أنك تقوم أدنى من ثلثي الليل ونصفه وثلثه وطائفة من الذين معك والله يقدر الليل والنهار علم أن لن تحصوه فتاب عليكم فاقرءوا ما تيسر من القرآن علم أن سيكون منكم مرضى وآخرون يضربون في الأرض يبتغون من فضل الله وآخرون يقاتلون في سبيل الله فاقرءوا ما تيسر منه وأقيموا الصلاة وآتوا الزكاة وأقرضوا الله قرضا حسنا وما تقدموا لأنفسكم من خير تجدوه عند الله هو خيرا وأعظم أجرا واستغفروا الله إن الله غفور رحيم', 29, 58, 575, 230, '73:20'),
(5496, NULL, NULL, 74, 1, 'يَٰٓأَيُّهَا ٱلْمُدَّثِّرُ', 'يا أيها المدثر', 29, 58, 575, 230, '74:1'),
(5497, NULL, NULL, 74, 2, 'قُمْ فَأَنذِرْ', 'قم فأنذر', 29, 58, 575, 230, '74:2'),
(5498, NULL, NULL, 74, 3, 'وَرَبَّكَ فَكَبِّرْ', 'وربك فكبر', 29, 58, 575, 230, '74:3'),
(5499, NULL, NULL, 74, 4, 'وَثِيَابَكَ فَطَهِّرْ', 'وثيابك فطهر', 29, 58, 575, 230, '74:4'),
(5500, NULL, NULL, 74, 5, 'وَٱلرُّجْزَ فَٱهْجُرْ', 'والرجز فاهجر', 29, 58, 575, 230, '74:5'),
(5501, NULL, NULL, 74, 6, 'وَلَا تَمْنُن تَسْتَكْثِرُ', 'ولا تمنن تستكثر', 29, 58, 575, 230, '74:6'),
(5502, NULL, NULL, 74, 7, 'وَلِرَبِّكَ فَٱصْبِرْ', 'ولربك فاصبر', 29, 58, 575, 230, '74:7'),
(5503, NULL, NULL, 74, 8, 'فَإِذَا نُقِرَ فِى ٱلنَّاقُورِ', 'فإذا نقر في الناقور', 29, 58, 575, 230, '74:8'),
(5504, NULL, NULL, 74, 9, 'فَذَٰلِكَ يَوْمَئِذٍ يَوْمٌ عَسِيرٌ', 'فذلك يومئذ يوم عسير', 29, 58, 575, 230, '74:9'),
(5505, NULL, NULL, 74, 10, 'عَلَى ٱلْكَٰفِرِينَ غَيْرُ يَسِيرٍ', 'على الكافرين غير يسير', 29, 58, 575, 230, '74:10'),
(5506, NULL, NULL, 74, 11, 'ذَرْنِى وَمَنْ خَلَقْتُ وَحِيدًا', 'ذرني ومن خلقت وحيدا', 29, 58, 575, 230, '74:11'),
(5507, NULL, NULL, 74, 12, 'وَجَعَلْتُ لَهُۥ مَالًا مَّمْدُودًا', 'وجعلت له مالا ممدودا', 29, 58, 575, 230, '74:12'),
(5508, NULL, NULL, 74, 13, 'وَبَنِينَ شُهُودًا', 'وبنين شهودا', 29, 58, 575, 230, '74:13'),
(5509, NULL, NULL, 74, 14, 'وَمَهَّدتُّ لَهُۥ تَمْهِيدًا', 'ومهدت له تمهيدا', 29, 58, 575, 230, '74:14'),
(5510, NULL, NULL, 74, 15, 'ثُمَّ يَطْمَعُ أَنْ أَزِيدَ', 'ثم يطمع أن أزيد', 29, 58, 575, 230, '74:15'),
(5511, NULL, NULL, 74, 16, 'كَلَّآ إِنَّهُۥ كَانَ لِءَايَٰتِنَا عَنِيدًا', 'كلا إنه كان لآياتنا عنيدا', 29, 58, 575, 230, '74:16'),
(5512, NULL, NULL, 74, 17, 'سَأُرْهِقُهُۥ صَعُودًا', 'سأرهقه صعودا', 29, 58, 575, 230, '74:17'),
(5513, NULL, NULL, 74, 18, 'إِنَّهُۥ فَكَّرَ وَقَدَّرَ', 'إنه فكر وقدر', 29, 58, 576, 230, '74:18'),
(5514, NULL, NULL, 74, 19, 'فَقُتِلَ كَيْفَ قَدَّرَ', 'فقتل كيف قدر', 29, 58, 576, 230, '74:19'),
(5515, NULL, NULL, 74, 20, 'ثُمَّ قُتِلَ كَيْفَ قَدَّرَ', 'ثم قتل كيف قدر', 29, 58, 576, 230, '74:20'),
(5516, NULL, NULL, 74, 21, 'ثُمَّ نَظَرَ', 'ثم نظر', 29, 58, 576, 230, '74:21'),
(5517, NULL, NULL, 74, 22, 'ثُمَّ عَبَسَ وَبَسَرَ', 'ثم عبس وبسر', 29, 58, 576, 230, '74:22'),
(5518, NULL, NULL, 74, 23, 'ثُمَّ أَدْبَرَ وَٱسْتَكْبَرَ', 'ثم أدبر واستكبر', 29, 58, 576, 230, '74:23'),
(5519, NULL, NULL, 74, 24, 'فَقَالَ إِنْ هَٰذَآ إِلَّا سِحْرٌ يُؤْثَرُ', 'فقال إن هذا إلا سحر يؤثر', 29, 58, 576, 230, '74:24'),
(5520, NULL, NULL, 74, 25, 'إِنْ هَٰذَآ إِلَّا قَوْلُ ٱلْبَشَرِ', 'إن هذا إلا قول البشر', 29, 58, 576, 230, '74:25'),
(5521, NULL, NULL, 74, 26, 'سَأُصْلِيهِ سَقَرَ', 'سأصليه سقر', 29, 58, 576, 230, '74:26'),
(5522, NULL, NULL, 74, 27, 'وَمَآ أَدْرَىٰكَ مَا سَقَرُ', 'وما أدراك ما سقر', 29, 58, 576, 230, '74:27'),
(5523, NULL, NULL, 74, 28, 'لَا تُبْقِى وَلَا تَذَرُ', 'لا تبقي ولا تذر', 29, 58, 576, 230, '74:28'),
(5524, NULL, NULL, 74, 29, 'لَوَّاحَةٌ لِّلْبَشَرِ', 'لواحة للبشر', 29, 58, 576, 230, '74:29'),
(5525, NULL, NULL, 74, 30, 'عَلَيْهَا تِسْعَةَ عَشَرَ', 'عليها تسعة عشر', 29, 58, 576, 230, '74:30'),
(5526, NULL, NULL, 74, 31, 'وَمَا جَعَلْنَآ أَصْحَٰبَ ٱلنَّارِ إِلَّا مَلَٰٓئِكَةً وَمَا جَعَلْنَا عِدَّتَهُمْ إِلَّا فِتْنَةً لِّلَّذِينَ كَفَرُوا۟ لِيَسْتَيْقِنَ ٱلَّذِينَ أُوتُوا۟ ٱلْكِتَٰبَ وَيَزْدَادَ ٱلَّذِينَ ءَامَنُوٓا۟ إِيمَٰنًا وَلَا يَرْتَابَ ٱلَّذِينَ أُوتُوا۟ ٱلْكِتَٰبَ وَٱلْمُؤْمِنُونَ وَلِيَقُولَ ٱلَّذِينَ فِى قُلُوبِهِم مَّرَضٌ وَٱلْكَٰفِرُونَ مَاذَآ أَرَادَ ٱللَّهُ بِهَٰذَا مَثَلًا كَذَٰلِكَ يُضِلُّ ٱللَّهُ مَن يَشَآءُ وَيَهْدِى مَن يَشَآءُ وَمَا يَعْلَمُ جُنُودَ رَبِّكَ إِلَّا هُوَ وَمَا هِىَ إِلَّا ذِكْرَىٰ لِلْبَشَرِ', 'وما جعلنا أصحاب النار إلا ملائكة وما جعلنا عدتهم إلا فتنة للذين كفروا ليستيقن الذين أوتوا الكتاب ويزداد الذين آمنوا إيمانا ولا يرتاب الذين أوتوا الكتاب والمؤمنون وليقول الذين في قلوبهم مرض والكافرون ماذا أراد الله بهذا مثلا كذلك يضل الله من يشاء ويهدي من يشاء وما يعلم جنود ربك إلا هو وما هي إلا ذكرى للبشر', 29, 58, 576, 230, '74:31'),
(5527, NULL, NULL, 74, 32, 'كَلَّا وَٱلْقَمَرِ', 'كلا والقمر', 29, 58, 576, 230, '74:32'),
(5528, NULL, NULL, 74, 33, 'وَٱلَّيْلِ إِذْ أَدْبَرَ', 'والليل إذ أدبر', 29, 58, 576, 230, '74:33'),
(5529, NULL, NULL, 74, 34, 'وَٱلصُّبْحِ إِذَآ أَسْفَرَ', 'والصبح إذا أسفر', 29, 58, 576, 230, '74:34'),
(5530, NULL, NULL, 74, 35, 'إِنَّهَا لَإِحْدَى ٱلْكُبَرِ', 'إنها لإحدى الكبر', 29, 58, 576, 230, '74:35'),
(5531, NULL, NULL, 74, 36, 'نَذِيرًا لِّلْبَشَرِ', 'نذيرا للبشر', 29, 58, 576, 230, '74:36'),
(5532, NULL, NULL, 74, 37, 'لِمَن شَآءَ مِنكُمْ أَن يَتَقَدَّمَ أَوْ يَتَأَخَّرَ', 'لمن شاء منكم أن يتقدم أو يتأخر', 29, 58, 576, 230, '74:37'),
(5533, NULL, NULL, 74, 38, 'كُلُّ نَفْسٍۭ بِمَا كَسَبَتْ رَهِينَةٌ', 'كل نفس بما كسبت رهينة', 29, 58, 576, 230, '74:38'),
(5534, NULL, NULL, 74, 39, 'إِلَّآ أَصْحَٰبَ ٱلْيَمِينِ', 'إلا أصحاب اليمين', 29, 58, 576, 230, '74:39'),
(5535, NULL, NULL, 74, 40, 'فِى جَنَّٰتٍ يَتَسَآءَلُونَ', 'في جنات يتساءلون', 29, 58, 576, 230, '74:40'),
(5536, NULL, NULL, 74, 41, 'عَنِ ٱلْمُجْرِمِينَ', 'عن المجرمين', 29, 58, 576, 230, '74:41'),
(5537, NULL, NULL, 74, 42, 'مَا سَلَكَكُمْ فِى سَقَرَ', 'ما سلككم في سقر', 29, 58, 576, 230, '74:42'),
(5538, NULL, NULL, 74, 43, 'قَالُوا۟ لَمْ نَكُ مِنَ ٱلْمُصَلِّينَ', 'قالوا لم نك من المصلين', 29, 58, 576, 230, '74:43'),
(5539, NULL, NULL, 74, 44, 'وَلَمْ نَكُ نُطْعِمُ ٱلْمِسْكِينَ', 'ولم نك نطعم المسكين', 29, 58, 576, 230, '74:44'),
(5540, NULL, NULL, 74, 45, 'وَكُنَّا نَخُوضُ مَعَ ٱلْخَآئِضِينَ', 'وكنا نخوض مع الخائضين', 29, 58, 576, 230, '74:45'),
(5541, NULL, NULL, 74, 46, 'وَكُنَّا نُكَذِّبُ بِيَوْمِ ٱلدِّينِ', 'وكنا نكذب بيوم الدين', 29, 58, 576, 230, '74:46'),
(5542, NULL, NULL, 74, 47, 'حَتَّىٰٓ أَتَىٰنَا ٱلْيَقِينُ', 'حتى أتانا اليقين', 29, 58, 576, 230, '74:47'),
(5543, NULL, NULL, 74, 48, 'فَمَا تَنفَعُهُمْ شَفَٰعَةُ ٱلشَّٰفِعِينَ', 'فما تنفعهم شفاعة الشافعين', 29, 58, 577, 230, '74:48'),
(5544, NULL, NULL, 74, 49, 'فَمَا لَهُمْ عَنِ ٱلتَّذْكِرَةِ مُعْرِضِينَ', 'فما لهم عن التذكرة معرضين', 29, 58, 577, 230, '74:49'),
(5545, NULL, NULL, 74, 50, 'كَأَنَّهُمْ حُمُرٌ مُّسْتَنفِرَةٌ', 'كأنهم حمر مستنفرة', 29, 58, 577, 230, '74:50'),
(5546, NULL, NULL, 74, 51, 'فَرَّتْ مِن قَسْوَرَةٍۭ', 'فرت من قسورة', 29, 58, 577, 230, '74:51'),
(5547, NULL, NULL, 74, 52, 'بَلْ يُرِيدُ كُلُّ ٱمْرِئٍ مِّنْهُمْ أَن يُؤْتَىٰ صُحُفًا مُّنَشَّرَةً', 'بل يريد كل امرئ منهم أن يؤتى صحفا منشرة', 29, 58, 577, 230, '74:52'),
(5548, NULL, NULL, 74, 53, 'كَلَّا بَل لَّا يَخَافُونَ ٱلْءَاخِرَةَ', 'كلا بل لا يخافون الآخرة', 29, 58, 577, 230, '74:53'),
(5549, NULL, NULL, 74, 54, 'كَلَّآ إِنَّهُۥ تَذْكِرَةٌ', 'كلا إنه تذكرة', 29, 58, 577, 230, '74:54'),
(5550, NULL, NULL, 74, 55, 'فَمَن شَآءَ ذَكَرَهُۥ', 'فمن شاء ذكره', 29, 58, 577, 230, '74:55'),
(5551, NULL, NULL, 74, 56, 'وَمَا يَذْكُرُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ هُوَ أَهْلُ ٱلتَّقْوَىٰ وَأَهْلُ ٱلْمَغْفِرَةِ', 'وما يذكرون إلا أن يشاء الله هو أهل التقوى وأهل المغفرة', 29, 58, 577, 230, '74:56'),
(5552, NULL, NULL, 75, 1, 'لَآ أُقْسِمُ بِيَوْمِ ٱلْقِيَٰمَةِ', 'لا أقسم بيوم القيامة', 29, 58, 577, 231, '75:1'),
(5553, NULL, NULL, 75, 2, 'وَلَآ أُقْسِمُ بِٱلنَّفْسِ ٱللَّوَّامَةِ', 'ولا أقسم بالنفس اللوامة', 29, 58, 577, 231, '75:2'),
(5554, NULL, NULL, 75, 3, 'أَيَحْسَبُ ٱلْإِنسَٰنُ أَلَّن نَّجْمَعَ عِظَامَهُۥ', 'أيحسب الإنسان ألن نجمع عظامه', 29, 58, 577, 231, '75:3'),
(5555, NULL, NULL, 75, 4, 'بَلَىٰ قَٰدِرِينَ عَلَىٰٓ أَن نُّسَوِّىَ بَنَانَهُۥ', 'بلى قادرين على أن نسوي بنانه', 29, 58, 577, 231, '75:4'),
(5556, NULL, NULL, 75, 5, 'بَلْ يُرِيدُ ٱلْإِنسَٰنُ لِيَفْجُرَ أَمَامَهُۥ', 'بل يريد الإنسان ليفجر أمامه', 29, 58, 577, 231, '75:5'),
(5557, NULL, NULL, 75, 6, 'يَسْـَٔلُ أَيَّانَ يَوْمُ ٱلْقِيَٰمَةِ', 'يسأل أيان يوم القيامة', 29, 58, 577, 231, '75:6'),
(5558, NULL, NULL, 75, 7, 'فَإِذَا بَرِقَ ٱلْبَصَرُ', 'فإذا برق البصر', 29, 58, 577, 231, '75:7'),
(5559, NULL, NULL, 75, 8, 'وَخَسَفَ ٱلْقَمَرُ', 'وخسف القمر', 29, 58, 577, 231, '75:8'),
(5560, NULL, NULL, 75, 9, 'وَجُمِعَ ٱلشَّمْسُ وَٱلْقَمَرُ', 'وجمع الشمس والقمر', 29, 58, 577, 231, '75:9'),
(5561, NULL, NULL, 75, 10, 'يَقُولُ ٱلْإِنسَٰنُ يَوْمَئِذٍ أَيْنَ ٱلْمَفَرُّ', 'يقول الإنسان يومئذ أين المفر', 29, 58, 577, 231, '75:10'),
(5562, NULL, NULL, 75, 11, 'كَلَّا لَا وَزَرَ', 'كلا لا وزر', 29, 58, 577, 231, '75:11'),
(5563, NULL, NULL, 75, 12, 'إِلَىٰ رَبِّكَ يَوْمَئِذٍ ٱلْمُسْتَقَرُّ', 'إلى ربك يومئذ المستقر', 29, 58, 577, 231, '75:12'),
(5564, NULL, NULL, 75, 13, 'يُنَبَّؤُا۟ ٱلْإِنسَٰنُ يَوْمَئِذٍۭ بِمَا قَدَّمَ وَأَخَّرَ', 'ينبأ الإنسان يومئذ بما قدم وأخر', 29, 58, 577, 231, '75:13'),
(5565, NULL, NULL, 75, 14, 'بَلِ ٱلْإِنسَٰنُ عَلَىٰ نَفْسِهِۦ بَصِيرَةٌ', 'بل الإنسان على نفسه بصيرة', 29, 58, 577, 231, '75:14'),
(5566, NULL, NULL, 75, 15, 'وَلَوْ أَلْقَىٰ مَعَاذِيرَهُۥ', 'ولو ألقى معاذيره', 29, 58, 577, 231, '75:15'),
(5567, NULL, NULL, 75, 16, 'لَا تُحَرِّكْ بِهِۦ لِسَانَكَ لِتَعْجَلَ بِهِۦٓ', 'لا تحرك به لسانك لتعجل به', 29, 58, 577, 231, '75:16'),
(5568, NULL, NULL, 75, 17, 'إِنَّ عَلَيْنَا جَمْعَهُۥ وَقُرْءَانَهُۥ', 'إن علينا جمعه وقرآنه', 29, 58, 577, 231, '75:17'),
(5569, NULL, NULL, 75, 18, 'فَإِذَا قَرَأْنَٰهُ فَٱتَّبِعْ قُرْءَانَهُۥ', 'فإذا قرأناه فاتبع قرآنه', 29, 58, 577, 231, '75:18'),
(5570, NULL, NULL, 75, 19, 'ثُمَّ إِنَّ عَلَيْنَا بَيَانَهُۥ', 'ثم إن علينا بيانه', 29, 58, 577, 231, '75:19'),
(5571, NULL, NULL, 75, 20, 'كَلَّا بَلْ تُحِبُّونَ ٱلْعَاجِلَةَ', 'كلا بل تحبون العاجلة', 29, 58, 578, 231, '75:20'),
(5572, NULL, NULL, 75, 21, 'وَتَذَرُونَ ٱلْءَاخِرَةَ', 'وتذرون الآخرة', 29, 58, 578, 231, '75:21'),
(5573, NULL, NULL, 75, 22, 'وُجُوهٌ يَوْمَئِذٍ نَّاضِرَةٌ', 'وجوه يومئذ ناضرة', 29, 58, 578, 231, '75:22'),
(5574, NULL, NULL, 75, 23, 'إِلَىٰ رَبِّهَا نَاظِرَةٌ', 'إلى ربها ناظرة', 29, 58, 578, 231, '75:23'),
(5575, NULL, NULL, 75, 24, 'وَوُجُوهٌ يَوْمَئِذٍۭ بَاسِرَةٌ', 'ووجوه يومئذ باسرة', 29, 58, 578, 231, '75:24'),
(5576, NULL, NULL, 75, 25, 'تَظُنُّ أَن يُفْعَلَ بِهَا فَاقِرَةٌ', 'تظن أن يفعل بها فاقرة', 29, 58, 578, 231, '75:25'),
(5577, NULL, NULL, 75, 26, 'كَلَّآ إِذَا بَلَغَتِ ٱلتَّرَاقِىَ', 'كلا إذا بلغت التراقي', 29, 58, 578, 231, '75:26');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(5578, NULL, NULL, 75, 27, 'وَقِيلَ مَنْ رَاقٍ', 'وقيل من راق', 29, 58, 578, 231, '75:27'),
(5579, NULL, NULL, 75, 28, 'وَظَنَّ أَنَّهُ ٱلْفِرَاقُ', 'وظن أنه الفراق', 29, 58, 578, 231, '75:28'),
(5580, NULL, NULL, 75, 29, 'وَٱلْتَفَّتِ ٱلسَّاقُ بِٱلسَّاقِ', 'والتفت الساق بالساق', 29, 58, 578, 231, '75:29'),
(5581, NULL, NULL, 75, 30, 'إِلَىٰ رَبِّكَ يَوْمَئِذٍ ٱلْمَسَاقُ', 'إلى ربك يومئذ المساق', 29, 58, 578, 231, '75:30'),
(5582, NULL, NULL, 75, 31, 'فَلَا صَدَّقَ وَلَا صَلَّىٰ', 'فلا صدق ولا صلى', 29, 58, 578, 231, '75:31'),
(5583, NULL, NULL, 75, 32, 'وَلَٰكِن كَذَّبَ وَتَوَلَّىٰ', 'ولكن كذب وتولى', 29, 58, 578, 231, '75:32'),
(5584, NULL, NULL, 75, 33, 'ثُمَّ ذَهَبَ إِلَىٰٓ أَهْلِهِۦ يَتَمَطَّىٰٓ', 'ثم ذهب إلى أهله يتمطى', 29, 58, 578, 231, '75:33'),
(5585, NULL, NULL, 75, 34, 'أَوْلَىٰ لَكَ فَأَوْلَىٰ', 'أولى لك فأولى', 29, 58, 578, 231, '75:34'),
(5586, NULL, NULL, 75, 35, 'ثُمَّ أَوْلَىٰ لَكَ فَأَوْلَىٰٓ', 'ثم أولى لك فأولى', 29, 58, 578, 231, '75:35'),
(5587, NULL, NULL, 75, 36, 'أَيَحْسَبُ ٱلْإِنسَٰنُ أَن يُتْرَكَ سُدًى', 'أيحسب الإنسان أن يترك سدى', 29, 58, 578, 231, '75:36'),
(5588, NULL, NULL, 75, 37, 'أَلَمْ يَكُ نُطْفَةً مِّن مَّنِىٍّ يُمْنَىٰ', 'ألم يك نطفة من مني يمنى', 29, 58, 578, 231, '75:37'),
(5589, NULL, NULL, 75, 38, 'ثُمَّ كَانَ عَلَقَةً فَخَلَقَ فَسَوَّىٰ', 'ثم كان علقة فخلق فسوى', 29, 58, 578, 231, '75:38'),
(5590, NULL, NULL, 75, 39, 'فَجَعَلَ مِنْهُ ٱلزَّوْجَيْنِ ٱلذَّكَرَ وَٱلْأُنثَىٰٓ', 'فجعل منه الزوجين الذكر والأنثى', 29, 58, 578, 231, '75:39'),
(5591, NULL, NULL, 75, 40, 'أَلَيْسَ ذَٰلِكَ بِقَٰدِرٍ عَلَىٰٓ أَن يُحْۦِىَ ٱلْمَوْتَىٰ', 'أليس ذلك بقادر على أن يحيي الموتى', 29, 58, 578, 231, '75:40'),
(5592, NULL, NULL, 76, 1, 'هَلْ أَتَىٰ عَلَى ٱلْإِنسَٰنِ حِينٌ مِّنَ ٱلدَّهْرِ لَمْ يَكُن شَيْـًٔا مَّذْكُورًا', 'هل أتى على الإنسان حين من الدهر لم يكن شيئا مذكورا', 29, 58, 578, 231, '76:1'),
(5593, NULL, NULL, 76, 2, 'إِنَّا خَلَقْنَا ٱلْإِنسَٰنَ مِن نُّطْفَةٍ أَمْشَاجٍ نَّبْتَلِيهِ فَجَعَلْنَٰهُ سَمِيعًۢا بَصِيرًا', 'إنا خلقنا الإنسان من نطفة أمشاج نبتليه فجعلناه سميعا بصيرا', 29, 58, 578, 231, '76:2'),
(5594, NULL, NULL, 76, 3, 'إِنَّا هَدَيْنَٰهُ ٱلسَّبِيلَ إِمَّا شَاكِرًا وَإِمَّا كَفُورًا', 'إنا هديناه السبيل إما شاكرا وإما كفورا', 29, 58, 578, 231, '76:3'),
(5595, NULL, NULL, 76, 4, 'إِنَّآ أَعْتَدْنَا لِلْكَٰفِرِينَ سَلَٰسِلَا۟ وَأَغْلَٰلًا وَسَعِيرًا', 'إنا أعتدنا للكافرين سلاسل وأغلالا وسعيرا', 29, 58, 578, 231, '76:4'),
(5596, NULL, NULL, 76, 5, 'إِنَّ ٱلْأَبْرَارَ يَشْرَبُونَ مِن كَأْسٍ كَانَ مِزَاجُهَا كَافُورًا', 'إن الأبرار يشربون من كأس كان مزاجها كافورا', 29, 58, 578, 231, '76:5'),
(5597, NULL, NULL, 76, 6, 'عَيْنًا يَشْرَبُ بِهَا عِبَادُ ٱللَّهِ يُفَجِّرُونَهَا تَفْجِيرًا', 'عينا يشرب بها عباد الله يفجرونها تفجيرا', 29, 58, 579, 231, '76:6'),
(5598, NULL, NULL, 76, 7, 'يُوفُونَ بِٱلنَّذْرِ وَيَخَافُونَ يَوْمًا كَانَ شَرُّهُۥ مُسْتَطِيرًا', 'يوفون بالنذر ويخافون يوما كان شره مستطيرا', 29, 58, 579, 231, '76:7'),
(5599, NULL, NULL, 76, 8, 'وَيُطْعِمُونَ ٱلطَّعَامَ عَلَىٰ حُبِّهِۦ مِسْكِينًا وَيَتِيمًا وَأَسِيرًا', 'ويطعمون الطعام على حبه مسكينا ويتيما وأسيرا', 29, 58, 579, 231, '76:8'),
(5600, NULL, NULL, 76, 9, 'إِنَّمَا نُطْعِمُكُمْ لِوَجْهِ ٱللَّهِ لَا نُرِيدُ مِنكُمْ جَزَآءً وَلَا شُكُورًا', 'إنما نطعمكم لوجه الله لا نريد منكم جزاء ولا شكورا', 29, 58, 579, 231, '76:9'),
(5601, NULL, NULL, 76, 10, 'إِنَّا نَخَافُ مِن رَّبِّنَا يَوْمًا عَبُوسًا قَمْطَرِيرًا', 'إنا نخاف من ربنا يوما عبوسا قمطريرا', 29, 58, 579, 231, '76:10'),
(5602, NULL, NULL, 76, 11, 'فَوَقَىٰهُمُ ٱللَّهُ شَرَّ ذَٰلِكَ ٱلْيَوْمِ وَلَقَّىٰهُمْ نَضْرَةً وَسُرُورًا', 'فوقاهم الله شر ذلك اليوم ولقاهم نضرة وسرورا', 29, 58, 579, 231, '76:11'),
(5603, NULL, NULL, 76, 12, 'وَجَزَىٰهُم بِمَا صَبَرُوا۟ جَنَّةً وَحَرِيرًا', 'وجزاهم بما صبروا جنة وحريرا', 29, 58, 579, 231, '76:12'),
(5604, NULL, NULL, 76, 13, 'مُّتَّكِـِٔينَ فِيهَا عَلَى ٱلْأَرَآئِكِ لَا يَرَوْنَ فِيهَا شَمْسًا وَلَا زَمْهَرِيرًا', 'متكئين فيها على الأرائك لا يرون فيها شمسا ولا زمهريرا', 29, 58, 579, 231, '76:13'),
(5605, NULL, NULL, 76, 14, 'وَدَانِيَةً عَلَيْهِمْ ظِلَٰلُهَا وَذُلِّلَتْ قُطُوفُهَا تَذْلِيلًا', 'ودانية عليهم ظلالها وذللت قطوفها تذليلا', 29, 58, 579, 231, '76:14'),
(5606, NULL, NULL, 76, 15, 'وَيُطَافُ عَلَيْهِم بِـَٔانِيَةٍ مِّن فِضَّةٍ وَأَكْوَابٍ كَانَتْ قَوَارِيرَا۠', 'ويطاف عليهم بآنية من فضة وأكواب كانت قواريرا', 29, 58, 579, 231, '76:15'),
(5607, NULL, NULL, 76, 16, 'قَوَارِيرَا۟ مِن فِضَّةٍ قَدَّرُوهَا تَقْدِيرًا', 'قوارير من فضة قدروها تقديرا', 29, 58, 579, 231, '76:16'),
(5608, NULL, NULL, 76, 17, 'وَيُسْقَوْنَ فِيهَا كَأْسًا كَانَ مِزَاجُهَا زَنجَبِيلًا', 'ويسقون فيها كأسا كان مزاجها زنجبيلا', 29, 58, 579, 231, '76:17'),
(5609, NULL, NULL, 76, 18, 'عَيْنًا فِيهَا تُسَمَّىٰ سَلْسَبِيلًا', 'عينا فيها تسمى سلسبيلا', 29, 58, 579, 231, '76:18'),
(5610, NULL, NULL, 76, 19, 'وَيَطُوفُ عَلَيْهِمْ وِلْدَٰنٌ مُّخَلَّدُونَ إِذَا رَأَيْتَهُمْ حَسِبْتَهُمْ لُؤْلُؤًا مَّنثُورًا', 'ويطوف عليهم ولدان مخلدون إذا رأيتهم حسبتهم لؤلؤا منثورا', 29, 58, 579, 232, '76:19'),
(5611, NULL, NULL, 76, 20, 'وَإِذَا رَأَيْتَ ثَمَّ رَأَيْتَ نَعِيمًا وَمُلْكًا كَبِيرًا', 'وإذا رأيت ثم رأيت نعيما وملكا كبيرا', 29, 58, 579, 232, '76:20'),
(5612, NULL, NULL, 76, 21, 'عَٰلِيَهُمْ ثِيَابُ سُندُسٍ خُضْرٌ وَإِسْتَبْرَقٌ وَحُلُّوٓا۟ أَسَاوِرَ مِن فِضَّةٍ وَسَقَىٰهُمْ رَبُّهُمْ شَرَابًا طَهُورًا', 'عاليهم ثياب سندس خضر وإستبرق وحلوا أساور من فضة وسقاهم ربهم شرابا طهورا', 29, 58, 579, 232, '76:21'),
(5613, NULL, NULL, 76, 22, 'إِنَّ هَٰذَا كَانَ لَكُمْ جَزَآءً وَكَانَ سَعْيُكُم مَّشْكُورًا', 'إن هذا كان لكم جزاء وكان سعيكم مشكورا', 29, 58, 579, 232, '76:22'),
(5614, NULL, NULL, 76, 23, 'إِنَّا نَحْنُ نَزَّلْنَا عَلَيْكَ ٱلْقُرْءَانَ تَنزِيلًا', 'إنا نحن نزلنا عليك القرآن تنزيلا', 29, 58, 579, 232, '76:23'),
(5615, NULL, NULL, 76, 24, 'فَٱصْبِرْ لِحُكْمِ رَبِّكَ وَلَا تُطِعْ مِنْهُمْ ءَاثِمًا أَوْ كَفُورًا', 'فاصبر لحكم ربك ولا تطع منهم آثما أو كفورا', 29, 58, 579, 232, '76:24'),
(5616, NULL, NULL, 76, 25, 'وَٱذْكُرِ ٱسْمَ رَبِّكَ بُكْرَةً وَأَصِيلًا', 'واذكر اسم ربك بكرة وأصيلا', 29, 58, 579, 232, '76:25'),
(5617, NULL, NULL, 76, 26, 'وَمِنَ ٱلَّيْلِ فَٱسْجُدْ لَهُۥ وَسَبِّحْهُ لَيْلًا طَوِيلًا', 'ومن الليل فاسجد له وسبحه ليلا طويلا', 29, 58, 580, 232, '76:26'),
(5618, NULL, NULL, 76, 27, 'إِنَّ هَٰٓؤُلَآءِ يُحِبُّونَ ٱلْعَاجِلَةَ وَيَذَرُونَ وَرَآءَهُمْ يَوْمًا ثَقِيلًا', 'إن هؤلاء يحبون العاجلة ويذرون وراءهم يوما ثقيلا', 29, 58, 580, 232, '76:27'),
(5619, NULL, NULL, 76, 28, 'نَّحْنُ خَلَقْنَٰهُمْ وَشَدَدْنَآ أَسْرَهُمْ وَإِذَا شِئْنَا بَدَّلْنَآ أَمْثَٰلَهُمْ تَبْدِيلًا', 'نحن خلقناهم وشددنا أسرهم وإذا شئنا بدلنا أمثالهم تبديلا', 29, 58, 580, 232, '76:28'),
(5620, NULL, NULL, 76, 29, 'إِنَّ هَٰذِهِۦ تَذْكِرَةٌ فَمَن شَآءَ ٱتَّخَذَ إِلَىٰ رَبِّهِۦ سَبِيلًا', 'إن هذه تذكرة فمن شاء اتخذ إلى ربه سبيلا', 29, 58, 580, 232, '76:29'),
(5621, NULL, NULL, 76, 30, 'وَمَا تَشَآءُونَ إِلَّآ أَن يَشَآءَ ٱللَّهُ إِنَّ ٱللَّهَ كَانَ عَلِيمًا حَكِيمًا', 'وما تشاءون إلا أن يشاء الله إن الله كان عليما حكيما', 29, 58, 580, 232, '76:30'),
(5622, NULL, NULL, 76, 31, 'يُدْخِلُ مَن يَشَآءُ فِى رَحْمَتِهِۦ وَٱلظَّٰلِمِينَ أَعَدَّ لَهُمْ عَذَابًا أَلِيمًۢا', 'يدخل من يشاء في رحمته والظالمين أعد لهم عذابا أليما', 29, 58, 580, 232, '76:31'),
(5623, NULL, NULL, 77, 1, 'وَٱلْمُرْسَلَٰتِ عُرْفًا', 'والمرسلات عرفا', 29, 58, 580, 232, '77:1'),
(5624, NULL, NULL, 77, 2, 'فَٱلْعَٰصِفَٰتِ عَصْفًا', 'فالعاصفات عصفا', 29, 58, 580, 232, '77:2'),
(5625, NULL, NULL, 77, 3, 'وَٱلنَّٰشِرَٰتِ نَشْرًا', 'والناشرات نشرا', 29, 58, 580, 232, '77:3'),
(5626, NULL, NULL, 77, 4, 'فَٱلْفَٰرِقَٰتِ فَرْقًا', 'فالفارقات فرقا', 29, 58, 580, 232, '77:4'),
(5627, NULL, NULL, 77, 5, 'فَٱلْمُلْقِيَٰتِ ذِكْرًا', 'فالملقيات ذكرا', 29, 58, 580, 232, '77:5'),
(5628, NULL, NULL, 77, 6, 'عُذْرًا أَوْ نُذْرًا', 'عذرا أو نذرا', 29, 58, 580, 232, '77:6'),
(5629, NULL, NULL, 77, 7, 'إِنَّمَا تُوعَدُونَ لَوَٰقِعٌ', 'إنما توعدون لواقع', 29, 58, 580, 232, '77:7'),
(5630, NULL, NULL, 77, 8, 'فَإِذَا ٱلنُّجُومُ طُمِسَتْ', 'فإذا النجوم طمست', 29, 58, 580, 232, '77:8'),
(5631, NULL, NULL, 77, 9, 'وَإِذَا ٱلسَّمَآءُ فُرِجَتْ', 'وإذا السماء فرجت', 29, 58, 580, 232, '77:9'),
(5632, NULL, NULL, 77, 10, 'وَإِذَا ٱلْجِبَالُ نُسِفَتْ', 'وإذا الجبال نسفت', 29, 58, 580, 232, '77:10'),
(5633, NULL, NULL, 77, 11, 'وَإِذَا ٱلرُّسُلُ أُقِّتَتْ', 'وإذا الرسل أقتت', 29, 58, 580, 232, '77:11'),
(5634, NULL, NULL, 77, 12, 'لِأَىِّ يَوْمٍ أُجِّلَتْ', 'لأي يوم أجلت', 29, 58, 580, 232, '77:12'),
(5635, NULL, NULL, 77, 13, 'لِيَوْمِ ٱلْفَصْلِ', 'ليوم الفصل', 29, 58, 580, 232, '77:13'),
(5636, NULL, NULL, 77, 14, 'وَمَآ أَدْرَىٰكَ مَا يَوْمُ ٱلْفَصْلِ', 'وما أدراك ما يوم الفصل', 29, 58, 580, 232, '77:14'),
(5637, NULL, NULL, 77, 15, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 29, 58, 580, 232, '77:15'),
(5638, NULL, NULL, 77, 16, 'أَلَمْ نُهْلِكِ ٱلْأَوَّلِينَ', 'ألم نهلك الأولين', 29, 58, 580, 232, '77:16'),
(5639, NULL, NULL, 77, 17, 'ثُمَّ نُتْبِعُهُمُ ٱلْءَاخِرِينَ', 'ثم نتبعهم الآخرين', 29, 58, 580, 232, '77:17'),
(5640, NULL, NULL, 77, 18, 'كَذَٰلِكَ نَفْعَلُ بِٱلْمُجْرِمِينَ', 'كذلك نفعل بالمجرمين', 29, 58, 580, 232, '77:18'),
(5641, NULL, NULL, 77, 19, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 29, 58, 580, 232, '77:19'),
(5642, NULL, NULL, 77, 20, 'أَلَمْ نَخْلُقكُّم مِّن مَّآءٍ مَّهِينٍ', 'ألم نخلقكم من ماء مهين', 29, 58, 581, 232, '77:20'),
(5643, NULL, NULL, 77, 21, 'فَجَعَلْنَٰهُ فِى قَرَارٍ مَّكِينٍ', 'فجعلناه في قرار مكين', 29, 58, 581, 232, '77:21'),
(5644, NULL, NULL, 77, 22, 'إِلَىٰ قَدَرٍ مَّعْلُومٍ', 'إلى قدر معلوم', 29, 58, 581, 232, '77:22'),
(5645, NULL, NULL, 77, 23, 'فَقَدَرْنَا فَنِعْمَ ٱلْقَٰدِرُونَ', 'فقدرنا فنعم القادرون', 29, 58, 581, 232, '77:23'),
(5646, NULL, NULL, 77, 24, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 29, 58, 581, 232, '77:24'),
(5647, NULL, NULL, 77, 25, 'أَلَمْ نَجْعَلِ ٱلْأَرْضَ كِفَاتًا', 'ألم نجعل الأرض كفاتا', 29, 58, 581, 232, '77:25'),
(5648, NULL, NULL, 77, 26, 'أَحْيَآءً وَأَمْوَٰتًا', 'أحياء وأمواتا', 29, 58, 581, 232, '77:26'),
(5649, NULL, NULL, 77, 27, 'وَجَعَلْنَا فِيهَا رَوَٰسِىَ شَٰمِخَٰتٍ وَأَسْقَيْنَٰكُم مَّآءً فُرَاتًا', 'وجعلنا فيها رواسي شامخات وأسقيناكم ماء فراتا', 29, 58, 581, 232, '77:27'),
(5650, NULL, NULL, 77, 28, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 29, 58, 581, 232, '77:28'),
(5651, NULL, NULL, 77, 29, 'ٱنطَلِقُوٓا۟ إِلَىٰ مَا كُنتُم بِهِۦ تُكَذِّبُونَ', 'انطلقوا إلى ما كنتم به تكذبون', 29, 58, 581, 232, '77:29'),
(5652, NULL, NULL, 77, 30, 'ٱنطَلِقُوٓا۟ إِلَىٰ ظِلٍّ ذِى ثَلَٰثِ شُعَبٍ', 'انطلقوا إلى ظل ذي ثلاث شعب', 29, 58, 581, 232, '77:30'),
(5653, NULL, NULL, 77, 31, 'لَّا ظَلِيلٍ وَلَا يُغْنِى مِنَ ٱللَّهَبِ', 'لا ظليل ولا يغني من اللهب', 29, 58, 581, 232, '77:31'),
(5654, NULL, NULL, 77, 32, 'إِنَّهَا تَرْمِى بِشَرَرٍ كَٱلْقَصْرِ', 'إنها ترمي بشرر كالقصر', 29, 58, 581, 232, '77:32'),
(5655, NULL, NULL, 77, 33, 'كَأَنَّهُۥ جِمَٰلَتٌ صُفْرٌ', 'كأنه جمالت صفر', 29, 58, 581, 232, '77:33'),
(5656, NULL, NULL, 77, 34, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 29, 58, 581, 232, '77:34'),
(5657, NULL, NULL, 77, 35, 'هَٰذَا يَوْمُ لَا يَنطِقُونَ', 'هذا يوم لا ينطقون', 29, 58, 581, 232, '77:35'),
(5658, NULL, NULL, 77, 36, 'وَلَا يُؤْذَنُ لَهُمْ فَيَعْتَذِرُونَ', 'ولا يؤذن لهم فيعتذرون', 29, 58, 581, 232, '77:36'),
(5659, NULL, NULL, 77, 37, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 29, 58, 581, 232, '77:37'),
(5660, NULL, NULL, 77, 38, 'هَٰذَا يَوْمُ ٱلْفَصْلِ جَمَعْنَٰكُمْ وَٱلْأَوَّلِينَ', 'هذا يوم الفصل جمعناكم والأولين', 29, 58, 581, 232, '77:38'),
(5661, NULL, NULL, 77, 39, 'فَإِن كَانَ لَكُمْ كَيْدٌ فَكِيدُونِ', 'فإن كان لكم كيد فكيدون', 29, 58, 581, 232, '77:39'),
(5662, NULL, NULL, 77, 40, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 29, 58, 581, 232, '77:40'),
(5663, NULL, NULL, 77, 41, 'إِنَّ ٱلْمُتَّقِينَ فِى ظِلَٰلٍ وَعُيُونٍ', 'إن المتقين في ظلال وعيون', 29, 58, 581, 232, '77:41'),
(5664, NULL, NULL, 77, 42, 'وَفَوَٰكِهَ مِمَّا يَشْتَهُونَ', 'وفواكه مما يشتهون', 29, 58, 581, 232, '77:42'),
