(5848, NULL, NULL, 82, 19, 'يَوْمَ لَا تَمْلِكُ نَفْسٌ لِّنَفْسٍ شَيْـًٔا وَٱلْأَمْرُ يَوْمَئِذٍ لِّلَّهِ', 'يوم لا تملك نفس لنفس شيئا والأمر يومئذ لله', 30, 59, 587, 235, '82:19'),
(5849, NULL, NULL, 83, 1, 'وَيْلٌ لِّلْمُطَفِّفِينَ', 'ويل للمطففين', 30, 59, 587, 235, '83:1'),
(5850, NULL, NULL, 83, 2, 'ٱلَّذِينَ إِذَا ٱكْتَالُوا۟ عَلَى ٱلنَّاسِ يَسْتَوْفُونَ', 'الذين إذا اكتالوا على الناس يستوفون', 30, 59, 587, 235, '83:2'),
(5851, NULL, NULL, 83, 3, 'وَإِذَا كَالُوهُمْ أَو وَّزَنُوهُمْ يُخْسِرُونَ', 'وإذا كالوهم أو وزنوهم يخسرون', 30, 59, 587, 235, '83:3'),
(5852, NULL, NULL, 83, 4, 'أَلَا يَظُنُّ أُو۟لَٰٓئِكَ أَنَّهُم مَّبْعُوثُونَ', 'ألا يظن أولئك أنهم مبعوثون', 30, 59, 587, 235, '83:4'),
(5853, NULL, NULL, 83, 5, 'لِيَوْمٍ عَظِيمٍ', 'ليوم عظيم', 30, 59, 587, 235, '83:5'),
(5854, NULL, NULL, 83, 6, 'يَوْمَ يَقُومُ ٱلنَّاسُ لِرَبِّ ٱلْعَٰلَمِينَ', 'يوم يقوم الناس لرب العالمين', 30, 59, 587, 235, '83:6'),
(5855, NULL, NULL, 83, 7, 'كَلَّآ إِنَّ كِتَٰبَ ٱلْفُجَّارِ لَفِى سِجِّينٍ', 'كلا إن كتاب الفجار لفي سجين', 30, 59, 588, 235, '83:7'),
(5856, NULL, NULL, 83, 8, 'وَمَآ أَدْرَىٰكَ مَا سِجِّينٌ', 'وما أدراك ما سجين', 30, 59, 588, 235, '83:8'),
(5857, NULL, NULL, 83, 9, 'كِتَٰبٌ مَّرْقُومٌ', 'كتاب مرقوم', 30, 59, 588, 235, '83:9'),
(5858, NULL, NULL, 83, 10, 'وَيْلٌ يَوْمَئِذٍ لِّلْمُكَذِّبِينَ', 'ويل يومئذ للمكذبين', 30, 59, 588, 235, '83:10'),
(5859, NULL, NULL, 83, 11, 'ٱلَّذِينَ يُكَذِّبُونَ بِيَوْمِ ٱلدِّينِ', 'الذين يكذبون بيوم الدين', 30, 59, 588, 235, '83:11'),
(5860, NULL, NULL, 83, 12, 'وَمَا يُكَذِّبُ بِهِۦٓ إِلَّا كُلُّ مُعْتَدٍ أَثِيمٍ', 'وما يكذب به إلا كل معتد أثيم', 30, 59, 588, 235, '83:12'),
(5861, NULL, NULL, 83, 13, 'إِذَا تُتْلَىٰ عَلَيْهِ ءَايَٰتُنَا قَالَ أَسَٰطِيرُ ٱلْأَوَّلِينَ', 'إذا تتلى عليه آياتنا قال أساطير الأولين', 30, 59, 588, 235, '83:13'),
(5862, NULL, NULL, 83, 14, 'كَلَّا بَلْ رَانَ عَلَىٰ قُلُوبِهِم مَّا كَانُوا۟ يَكْسِبُونَ', 'كلا بل ران على قلوبهم ما كانوا يكسبون', 30, 59, 588, 235, '83:14'),
(5863, NULL, NULL, 83, 15, 'كَلَّآ إِنَّهُمْ عَن رَّبِّهِمْ يَوْمَئِذٍ لَّمَحْجُوبُونَ', 'كلا إنهم عن ربهم يومئذ لمحجوبون', 30, 59, 588, 235, '83:15'),
(5864, NULL, NULL, 83, 16, 'ثُمَّ إِنَّهُمْ لَصَالُوا۟ ٱلْجَحِيمِ', 'ثم إنهم لصالو الجحيم', 30, 59, 588, 235, '83:16'),
(5865, NULL, NULL, 83, 17, 'ثُمَّ يُقَالُ هَٰذَا ٱلَّذِى كُنتُم بِهِۦ تُكَذِّبُونَ', 'ثم يقال هذا الذي كنتم به تكذبون', 30, 59, 588, 235, '83:17'),
(5866, NULL, NULL, 83, 18, 'كَلَّآ إِنَّ كِتَٰبَ ٱلْأَبْرَارِ لَفِى عِلِّيِّينَ', 'كلا إن كتاب الأبرار لفي عليين', 30, 59, 588, 235, '83:18'),
(5867, NULL, NULL, 83, 19, 'وَمَآ أَدْرَىٰكَ مَا عِلِّيُّونَ', 'وما أدراك ما عليون', 30, 59, 588, 235, '83:19'),
(5868, NULL, NULL, 83, 20, 'كِتَٰبٌ مَّرْقُومٌ', 'كتاب مرقوم', 30, 59, 588, 235, '83:20'),
(5869, NULL, NULL, 83, 21, 'يَشْهَدُهُ ٱلْمُقَرَّبُونَ', 'يشهده المقربون', 30, 59, 588, 235, '83:21'),
(5870, NULL, NULL, 83, 22, 'إِنَّ ٱلْأَبْرَارَ لَفِى نَعِيمٍ', 'إن الأبرار لفي نعيم', 30, 59, 588, 235, '83:22'),
(5871, NULL, NULL, 83, 23, 'عَلَى ٱلْأَرَآئِكِ يَنظُرُونَ', 'على الأرائك ينظرون', 30, 59, 588, 235, '83:23'),
(5872, NULL, NULL, 83, 24, 'تَعْرِفُ فِى وُجُوهِهِمْ نَضْرَةَ ٱلنَّعِيمِ', 'تعرف في وجوههم نضرة النعيم', 30, 59, 588, 235, '83:24'),
(5873, NULL, NULL, 83, 25, 'يُسْقَوْنَ مِن رَّحِيقٍ مَّخْتُومٍ', 'يسقون من رحيق مختوم', 30, 59, 588, 235, '83:25'),
(5874, NULL, NULL, 83, 26, 'خِتَٰمُهُۥ مِسْكٌ وَفِى ذَٰلِكَ فَلْيَتَنَافَسِ ٱلْمُتَنَٰفِسُونَ', 'ختامه مسك وفي ذلك فليتنافس المتنافسون', 30, 59, 588, 235, '83:26'),
(5875, NULL, NULL, 83, 27, 'وَمِزَاجُهُۥ مِن تَسْنِيمٍ', 'ومزاجه من تسنيم', 30, 59, 588, 235, '83:27'),
(5876, NULL, NULL, 83, 28, 'عَيْنًا يَشْرَبُ بِهَا ٱلْمُقَرَّبُونَ', 'عينا يشرب بها المقربون', 30, 59, 588, 235, '83:28'),
(5877, NULL, NULL, 83, 29, 'إِنَّ ٱلَّذِينَ أَجْرَمُوا۟ كَانُوا۟ مِنَ ٱلَّذِينَ ءَامَنُوا۟ يَضْحَكُونَ', 'إن الذين أجرموا كانوا من الذين آمنوا يضحكون', 30, 59, 588, 235, '83:29'),
(5878, NULL, NULL, 83, 30, 'وَإِذَا مَرُّوا۟ بِهِمْ يَتَغَامَزُونَ', 'وإذا مروا بهم يتغامزون', 30, 59, 588, 235, '83:30'),
(5879, NULL, NULL, 83, 31, 'وَإِذَا ٱنقَلَبُوٓا۟ إِلَىٰٓ أَهْلِهِمُ ٱنقَلَبُوا۟ فَكِهِينَ', 'وإذا انقلبوا إلى أهلهم انقلبوا فكهين', 30, 59, 588, 235, '83:31'),
(5880, NULL, NULL, 83, 32, 'وَإِذَا رَأَوْهُمْ قَالُوٓا۟ إِنَّ هَٰٓؤُلَآءِ لَضَآلُّونَ', 'وإذا رأوهم قالوا إن هؤلاء لضالون', 30, 59, 588, 235, '83:32'),
(5881, NULL, NULL, 83, 33, 'وَمَآ أُرْسِلُوا۟ عَلَيْهِمْ حَٰفِظِينَ', 'وما أرسلوا عليهم حافظين', 30, 59, 588, 235, '83:33'),
(5882, NULL, NULL, 83, 34, 'فَٱلْيَوْمَ ٱلَّذِينَ ءَامَنُوا۟ مِنَ ٱلْكُفَّارِ يَضْحَكُونَ', 'فاليوم الذين آمنوا من الكفار يضحكون', 30, 59, 588, 235, '83:34'),
(5883, NULL, NULL, 83, 35, 'عَلَى ٱلْأَرَآئِكِ يَنظُرُونَ', 'على الأرائك ينظرون', 30, 59, 589, 235, '83:35'),
(5884, NULL, NULL, 83, 36, 'هَلْ ثُوِّبَ ٱلْكُفَّارُ مَا كَانُوا۟ يَفْعَلُونَ', 'هل ثوب الكفار ما كانوا يفعلون', 30, 59, 589, 235, '83:36'),
(5885, NULL, NULL, 84, 1, 'إِذَا ٱلسَّمَآءُ ٱنشَقَّتْ', 'إذا السماء انشقت', 30, 59, 589, 236, '84:1'),
(5886, NULL, NULL, 84, 2, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 'وأذنت لربها وحقت', 30, 59, 589, 236, '84:2'),
(5887, NULL, NULL, 84, 3, 'وَإِذَا ٱلْأَرْضُ مُدَّتْ', 'وإذا الأرض مدت', 30, 59, 589, 236, '84:3'),
(5888, NULL, NULL, 84, 4, 'وَأَلْقَتْ مَا فِيهَا وَتَخَلَّتْ', 'وألقت ما فيها وتخلت', 30, 59, 589, 236, '84:4'),
(5889, NULL, NULL, 84, 5, 'وَأَذِنَتْ لِرَبِّهَا وَحُقَّتْ', 'وأذنت لربها وحقت', 30, 59, 589, 236, '84:5'),
(5890, NULL, NULL, 84, 6, 'يَٰٓأَيُّهَا ٱلْإِنسَٰنُ إِنَّكَ كَادِحٌ إِلَىٰ رَبِّكَ كَدْحًا فَمُلَٰقِيهِ', 'يا أيها الإنسان إنك كادح إلى ربك كدحا فملاقيه', 30, 59, 589, 236, '84:6'),
(5891, NULL, NULL, 84, 7, 'فَأَمَّا مَنْ أُوتِىَ كِتَٰبَهُۥ بِيَمِينِهِۦ', 'فأما من أوتي كتابه بيمينه', 30, 59, 589, 236, '84:7'),
(5892, NULL, NULL, 84, 8, 'فَسَوْفَ يُحَاسَبُ حِسَابًا يَسِيرًا', 'فسوف يحاسب حسابا يسيرا', 30, 59, 589, 236, '84:8'),
(5893, NULL, NULL, 84, 9, 'وَيَنقَلِبُ إِلَىٰٓ أَهْلِهِۦ مَسْرُورًا', 'وينقلب إلى أهله مسرورا', 30, 59, 589, 236, '84:9'),
(5894, NULL, NULL, 84, 10, 'وَأَمَّا مَنْ أُوتِىَ كِتَٰبَهُۥ وَرَآءَ ظَهْرِهِۦ', 'وأما من أوتي كتابه وراء ظهره', 30, 59, 589, 236, '84:10'),
(5895, NULL, NULL, 84, 11, 'فَسَوْفَ يَدْعُوا۟ ثُبُورًا', 'فسوف يدعو ثبورا', 30, 59, 589, 236, '84:11'),
(5896, NULL, NULL, 84, 12, 'وَيَصْلَىٰ سَعِيرًا', 'ويصلى سعيرا', 30, 59, 589, 236, '84:12'),
(5897, NULL, NULL, 84, 13, 'إِنَّهُۥ كَانَ فِىٓ أَهْلِهِۦ مَسْرُورًا', 'إنه كان في أهله مسرورا', 30, 59, 589, 236, '84:13'),
(5898, NULL, NULL, 84, 14, 'إِنَّهُۥ ظَنَّ أَن لَّن يَحُورَ', 'إنه ظن أن لن يحور', 30, 59, 589, 236, '84:14'),
(5899, NULL, NULL, 84, 15, 'بَلَىٰٓ إِنَّ رَبَّهُۥ كَانَ بِهِۦ بَصِيرًا', 'بلى إن ربه كان به بصيرا', 30, 59, 589, 236, '84:15'),
(5900, NULL, NULL, 84, 16, 'فَلَآ أُقْسِمُ بِٱلشَّفَقِ', 'فلا أقسم بالشفق', 30, 59, 589, 236, '84:16'),
(5901, NULL, NULL, 84, 17, 'وَٱلَّيْلِ وَمَا وَسَقَ', 'والليل وما وسق', 30, 59, 589, 236, '84:17'),
