(5907, NULL, NULL, 84, 23, 'وَٱللَّهُ أَعْلَمُ بِمَا يُوعُونَ', 'والله أعلم بما يوعون', 30, 59, 589, 236, '84:23'),
(5908, NULL, NULL, 84, 24, 'فَبَشِّرْهُم بِعَذَابٍ أَلِيمٍ', 'فبشرهم بعذاب أليم', 30, 59, 589, 236, '84:24'),
(5909, NULL, NULL, 84, 25, 'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ لَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍۭ', 'إلا الذين آمنوا وعملوا الصالحات لهم أجر غير ممنون', 30, 59, 589, 236, '84:25'),
(5910, NULL, NULL, 85, 1, 'وَٱلسَّمَآءِ ذَاتِ ٱلْبُرُوجِ', 'والسماء ذات البروج', 30, 59, 590, 236, '85:1'),
(5911, NULL, NULL, 85, 2, 'وَٱلْيَوْمِ ٱلْمَوْعُودِ', 'واليوم الموعود', 30, 59, 590, 236, '85:2'),
(5912, NULL, NULL, 85, 3, 'وَشَاهِدٍ وَمَشْهُودٍ', 'وشاهد ومشهود', 30, 59, 590, 236, '85:3'),
(5913, NULL, NULL, 85, 4, 'قُتِلَ أَصْحَٰبُ ٱلْأُخْدُودِ', 'قتل أصحاب الأخدود', 30, 59, 590, 236, '85:4'),
(5914, NULL, NULL, 85, 5, 'ٱلنَّارِ ذَاتِ ٱلْوَقُودِ', 'النار ذات الوقود', 30, 59, 590, 236, '85:5'),
(5915, NULL, NULL, 85, 6, 'إِذْ هُمْ عَلَيْهَا قُعُودٌ', 'إذ هم عليها قعود', 30, 59, 590, 236, '85:6'),
(5916, NULL, NULL, 85, 7, 'وَهُمْ عَلَىٰ مَا يَفْعَلُونَ بِٱلْمُؤْمِنِينَ شُهُودٌ', 'وهم على ما يفعلون بالمؤمنين شهود', 30, 59, 590, 236, '85:7'),
(5917, NULL, NULL, 85, 8, 'وَمَا نَقَمُوا۟ مِنْهُمْ إِلَّآ أَن يُؤْمِنُوا۟ بِٱللَّهِ ٱلْعَزِيزِ ٱلْحَمِيدِ', 'وما نقموا منهم إلا أن يؤمنوا بالله العزيز الحميد', 30, 59, 590, 236, '85:8'),
