(5925, NULL, NULL, 85, 16, 'فَعَّالٌ لِّمَا يُرِيدُ', 'فعال لما يريد', 30, 59, 590, 236, '85:16'),
(5926, NULL, NULL, 85, 17, 'هَلْ أَتَىٰكَ حَدِيثُ ٱلْجُنُودِ', 'هل أتاك حديث الجنود', 30, 59, 590, 236, '85:17'),
(5927, NULL, NULL, 85, 18, 'فِرْعَوْنَ وَثَمُودَ', 'فرعون وثمود', 30, 59, 590, 236, '85:18'),
(5928, NULL, NULL, 85, 19, 'بَلِ ٱلَّذِينَ كَفَرُوا۟ فِى تَكْذِيبٍ', 'بل الذين كفروا في تكذيب', 30, 59, 590, 236, '85:19'),
(5929, NULL, NULL, 85, 20, 'وَٱللَّهُ مِن وَرَآئِهِم مُّحِيطٌۢ', 'والله من ورائهم محيط', 30, 59, 590, 236, '85:20'),
(5930, NULL, NULL, 85, 21, 'بَلْ هُوَ قُرْءَانٌ مَّجِيدٌ', 'بل هو قرآن مجيد', 30, 59, 590, 236, '85:21'),
(5931, NULL, NULL, 85, 22, 'فِى لَوْحٍ مَّحْفُوظٍۭ', 'في لوح محفوظ', 30, 59, 590, 236, '85:22'),
(5932, NULL, NULL, 86, 1, 'وَٱلسَّمَآءِ وَٱلطَّارِقِ', 'والسماء والطارق', 30, 59, 591, 236, '86:1'),
(5933, NULL, NULL, 86, 2, 'وَمَآ أَدْرَىٰكَ مَا ٱلطَّارِقُ', 'وما أدراك ما الطارق', 30, 59, 591, 236, '86:2'),
(5934, NULL, NULL, 86, 3, 'ٱلنَّجْمُ ٱلثَّاقِبُ', 'النجم الثاقب', 30, 59, 591, 236, '86:3'),
(5935, NULL, NULL, 86, 4, 'إِن كُلُّ نَفْسٍ لَّمَّا عَلَيْهَا حَافِظٌ', 'إن كل نفس لما عليها حافظ', 30, 59, 591, 236, '86:4'),
(5936, NULL, NULL, 86, 5, 'فَلْيَنظُرِ ٱلْإِنسَٰنُ مِمَّ خُلِقَ', 'فلينظر الإنسان مم خلق', 30, 59, 591, 236, '86:5'),
(5937, NULL, NULL, 86, 6, 'خُلِقَ مِن مَّآءٍ دَافِقٍ', 'خلق من ماء دافق', 30, 59, 591, 236, '86:6'),
(5938, NULL, NULL, 86, 7, 'يَخْرُجُ مِنۢ بَيْنِ ٱلصُّلْبِ وَٱلتَّرَآئِبِ', 'يخرج من بين الصلب والترائب', 30, 59, 591, 236, '86:7'),
(5939, NULL, NULL, 86, 8, 'إِنَّهُۥ عَلَىٰ رَجْعِهِۦ لَقَادِرٌ', 'إنه على رجعه لقادر', 30, 59, 591, 236, '86:8'),
(5940, NULL, NULL, 86, 9, 'يَوْمَ تُبْلَى ٱلسَّرَآئِرُ', 'يوم تبلى السرائر', 30, 59, 591, 236, '86:9'),
(5941, NULL, NULL, 86, 10, 'فَمَا لَهُۥ مِن قُوَّةٍ وَلَا نَاصِرٍ', 'فما له من قوة ولا ناصر', 30, 59, 591, 236, '86:10'),
(5942, NULL, NULL, 86, 11, 'وَٱلسَّمَآءِ ذَاتِ ٱلرَّجْعِ', 'والسماء ذات الرجع', 30, 59, 591, 236, '86:11'),
(5943, NULL, NULL, 86, 12, 'وَٱلْأَرْضِ ذَاتِ ٱلصَّدْعِ', 'والأرض ذات الصدع', 30, 59, 591, 236, '86:12'),
(5944, NULL, NULL, 86, 13, 'إِنَّهُۥ لَقَوْلٌ فَصْلٌ', 'إنه لقول فصل', 30, 59, 591, 236, '86:13'),
(5945, NULL, NULL, 86, 14, 'وَمَا هُوَ بِٱلْهَزْلِ', 'وما هو بالهزل', 30, 59, 591, 236, '86:14'),
(5946, NULL, NULL, 86, 15, 'إِنَّهُمْ يَكِيدُونَ كَيْدًا', 'إنهم يكيدون كيدا', 30, 59, 591, 236, '86:15'),
(5947, NULL, NULL, 86, 16, 'وَأَكِيدُ كَيْدًا', 'وأكيد كيدا', 30, 59, 591, 236, '86:16'),
(5948, NULL, NULL, 86, 17, 'فَمَهِّلِ ٱلْكَٰفِرِينَ أَمْهِلْهُمْ رُوَيْدًۢا', 'فمهل الكافرين أمهلهم رويدا', 30, 59, 591, 236, '86:17'),
(5949, NULL, NULL, 87, 1, 'سَبِّحِ ٱسْمَ رَبِّكَ ٱلْأَعْلَى', 'سبح اسم ربك الأعلى', 30, 60, 591, 237, '87:1'),
(5950, NULL, NULL, 87, 2, 'ٱلَّذِى خَلَقَ فَسَوَّىٰ', 'الذي خلق فسوى', 30, 60, 591, 237, '87:2'),
(5951, NULL, NULL, 87, 3, 'وَٱلَّذِى قَدَّرَ فَهَدَىٰ', 'والذي قدر فهدى', 30, 60, 591, 237, '87:3'),
(5952, NULL, NULL, 87, 4, 'وَٱلَّذِىٓ أَخْرَجَ ٱلْمَرْعَىٰ', 'والذي أخرج المرعى', 30, 60, 591, 237, '87:4'),
(5953, NULL, NULL, 87, 5, 'فَجَعَلَهُۥ غُثَآءً أَحْوَىٰ', 'فجعله غثاء أحوى', 30, 60, 591, 237, '87:5'),
(5954, NULL, NULL, 87, 6, 'سَنُقْرِئُكَ فَلَا تَنسَىٰٓ', 'سنقرئك فلا تنسى', 30, 60, 591, 237, '87:6'),
(5955, NULL, NULL, 87, 7, 'إِلَّا مَا شَآءَ ٱللَّهُ إِنَّهُۥ يَعْلَمُ ٱلْجَهْرَ وَمَا يَخْفَىٰ', 'إلا ما شاء الله إنه يعلم الجهر وما يخفى', 30, 60, 591, 237, '87:7'),
(5956, NULL, NULL, 87, 8, 'وَنُيَسِّرُكَ لِلْيُسْرَىٰ', 'ونيسرك لليسرى', 30, 60, 591, 237, '87:8'),
(5957, NULL, NULL, 87, 9, 'فَذَكِّرْ إِن نَّفَعَتِ ٱلذِّكْرَىٰ', 'فذكر إن نفعت الذكرى', 30, 60, 591, 237, '87:9'),
(5958, NULL, NULL, 87, 10, 'سَيَذَّكَّرُ مَن يَخْشَىٰ', 'سيذكر من يخشى', 30, 60, 591, 237, '87:10'),
(5959, NULL, NULL, 87, 11, 'وَيَتَجَنَّبُهَا ٱلْأَشْقَى', 'ويتجنبها الأشقى', 30, 60, 591, 237, '87:11'),
(5960, NULL, NULL, 87, 12, 'ٱلَّذِى يَصْلَى ٱلنَّارَ ٱلْكُبْرَىٰ', 'الذي يصلى النار الكبرى', 30, 60, 591, 237, '87:12'),
(5961, NULL, NULL, 87, 13, 'ثُمَّ لَا يَمُوتُ فِيهَا وَلَا يَحْيَىٰ', 'ثم لا يموت فيها ولا يحيى', 30, 60, 591, 237, '87:13'),
(5962, NULL, NULL, 87, 14, 'قَدْ أَفْلَحَ مَن تَزَكَّىٰ', 'قد أفلح من تزكى', 30, 60, 591, 237, '87:14'),
(5963, NULL, NULL, 87, 15, 'وَذَكَرَ ٱسْمَ رَبِّهِۦ فَصَلَّىٰ', 'وذكر اسم ربه فصلى', 30, 60, 591, 237, '87:15'),
(5964, NULL, NULL, 87, 16, 'بَلْ تُؤْثِرُونَ ٱلْحَيَوٰةَ ٱلدُّنْيَا', 'بل تؤثرون الحياة الدنيا', 30, 60, 592, 237, '87:16'),
(5965, NULL, NULL, 87, 17, 'وَٱلْءَاخِرَةُ خَيْرٌ وَأَبْقَىٰٓ', 'والآخرة خير وأبقى', 30, 60, 592, 237, '87:17'),
(5966, NULL, NULL, 87, 18, 'إِنَّ هَٰذَا لَفِى ٱلصُّحُفِ ٱلْأُولَىٰ', 'إن هذا لفي الصحف الأولى', 30, 60, 592, 237, '87:18'),
(5967, NULL, NULL, 87, 19, 'صُحُفِ إِبْرَٰهِيمَ وَمُوسَىٰ', 'صحف إبراهيم وموسى', 30, 60, 592, 237, '87:19'),
(5968, NULL, NULL, 88, 1, 'هَلْ أَتَىٰكَ حَدِيثُ ٱلْغَٰشِيَةِ', 'هل أتاك حديث الغاشية', 30, 60, 592, 237, '88:1'),
(5969, NULL, NULL, 88, 2, 'وُجُوهٌ يَوْمَئِذٍ خَٰشِعَةٌ', 'وجوه يومئذ خاشعة', 30, 60, 592, 237, '88:2'),
(5970, NULL, NULL, 88, 3, 'عَامِلَةٌ نَّاصِبَةٌ', 'عاملة ناصبة', 30, 60, 592, 237, '88:3'),
(5971, NULL, NULL, 88, 4, 'تَصْلَىٰ نَارًا حَامِيَةً', 'تصلى نارا حامية', 30, 60, 592, 237, '88:4'),
(5972, NULL, NULL, 88, 5, 'تُسْقَىٰ مِنْ عَيْنٍ ءَانِيَةٍ', 'تسقى من عين آنية', 30, 60, 592, 237, '88:5'),
(5973, NULL, NULL, 88, 6, 'لَّيْسَ لَهُمْ طَعَامٌ إِلَّا مِن ضَرِيعٍ', 'ليس لهم طعام إلا من ضريع', 30, 60, 592, 237, '88:6'),
(5974, NULL, NULL, 88, 7, 'لَّا يُسْمِنُ وَلَا يُغْنِى مِن جُوعٍ', 'لا يسمن ولا يغني من جوع', 30, 60, 592, 237, '88:7'),
(5975, NULL, NULL, 88, 8, 'وُجُوهٌ يَوْمَئِذٍ نَّاعِمَةٌ', 'وجوه يومئذ ناعمة', 30, 60, 592, 237, '88:8'),
(5976, NULL, NULL, 88, 9, 'لِّسَعْيِهَا رَاضِيَةٌ', 'لسعيها راضية', 30, 60, 592, 237, '88:9'),
(5977, NULL, NULL, 88, 10, 'فِى جَنَّةٍ عَالِيَةٍ', 'في جنة عالية', 30, 60, 592, 237, '88:10'),
(5978, NULL, NULL, 88, 11, 'لَّا تَسْمَعُ فِيهَا لَٰغِيَةً', 'لا تسمع فيها لاغية', 30, 60, 592, 237, '88:11'),
(5979, NULL, NULL, 88, 12, 'فِيهَا عَيْنٌ جَارِيَةٌ', 'فيها عين جارية', 30, 60, 592, 237, '88:12'),
(5980, NULL, NULL, 88, 13, 'فِيهَا سُرُرٌ مَّرْفُوعَةٌ', 'فيها سرر مرفوعة', 30, 60, 592, 237, '88:13'),
(5981, NULL, NULL, 88, 14, 'وَأَكْوَابٌ مَّوْضُوعَةٌ', 'وأكواب موضوعة', 30, 60, 592, 237, '88:14'),
(5982, NULL, NULL, 88, 15, 'وَنَمَارِقُ مَصْفُوفَةٌ', 'ونمارق مصفوفة', 30, 60, 592, 237, '88:15'),
(5983, NULL, NULL, 88, 16, 'وَزَرَابِىُّ مَبْثُوثَةٌ', 'وزرابي مبثوثة', 30, 60, 592, 237, '88:16'),
(5984, NULL, NULL, 88, 17, 'أَفَلَا يَنظُرُونَ إِلَى ٱلْإِبِلِ كَيْفَ خُلِقَتْ', 'أفلا ينظرون إلى الإبل كيف خلقت', 30, 60, 592, 237, '88:17'),
(5985, NULL, NULL, 88, 18, 'وَإِلَى ٱلسَّمَآءِ كَيْفَ رُفِعَتْ', 'وإلى السماء كيف رفعت', 30, 60, 592, 237, '88:18'),
(5986, NULL, NULL, 88, 19, 'وَإِلَى ٱلْجِبَالِ كَيْفَ نُصِبَتْ', 'وإلى الجبال كيف نصبت', 30, 60, 592, 237, '88:19'),
(5987, NULL, NULL, 88, 20, 'وَإِلَى ٱلْأَرْضِ كَيْفَ سُطِحَتْ', 'وإلى الأرض كيف سطحت', 30, 60, 592, 237, '88:20'),
(5988, NULL, NULL, 88, 21, 'فَذَكِّرْ إِنَّمَآ أَنتَ مُذَكِّرٌ', 'فذكر إنما أنت مذكر', 30, 60, 592, 237, '88:21'),
(5989, NULL, NULL, 88, 22, 'لَّسْتَ عَلَيْهِم بِمُصَيْطِرٍ', 'لست عليهم بمصيطر', 30, 60, 592, 237, '88:22'),
(5990, NULL, NULL, 88, 23, 'إِلَّا مَن تَوَلَّىٰ وَكَفَرَ', 'إلا من تولى وكفر', 30, 60, 592, 237, '88:23'),
(5991, NULL, NULL, 88, 24, 'فَيُعَذِّبُهُ ٱللَّهُ ٱلْعَذَابَ ٱلْأَكْبَرَ', 'فيعذبه الله العذاب الأكبر', 30, 60, 592, 237, '88:24'),
(5992, NULL, NULL, 88, 25, 'إِنَّ إِلَيْنَآ إِيَابَهُمْ', 'إن إلينا إيابهم', 30, 60, 592, 237, '88:25');
INSERT INTO `ar_ayats` (`id`, `created_at`, `updated_at`, `surah_id`, `aya`, `text`, `simple`, `juz_id`, `hezb`, `page_id`, `rub`, `verse_key`) VALUES
(5993, NULL, NULL, 88, 26, 'ثُمَّ إِنَّ عَلَيْنَا حِسَابَهُم', 'ثم إن علينا حسابهم', 30, 60, 592, 237, '88:26'),
(5994, NULL, NULL, 89, 1, 'وَٱلْفَجْرِ', 'والفجر', 30, 60, 593, 237, '89:1'),
(5995, NULL, NULL, 89, 2, 'وَلَيَالٍ عَشْرٍ', 'وليال عشر', 30, 60, 593, 237, '89:2'),
(5996, NULL, NULL, 89, 3, 'وَٱلشَّفْعِ وَٱلْوَتْرِ', 'والشفع والوتر', 30, 60, 593, 237, '89:3'),
(5997, NULL, NULL, 89, 4, 'وَٱلَّيْلِ إِذَا يَسْرِ', 'والليل إذا يسر', 30, 60, 593, 237, '89:4'),
(5998, NULL, NULL, 89, 5, 'هَلْ فِى ذَٰلِكَ قَسَمٌ لِّذِى حِجْرٍ', 'هل في ذلك قسم لذي حجر', 30, 60, 593, 237, '89:5'),
(5999, NULL, NULL, 89, 6, 'أَلَمْ تَرَ كَيْفَ فَعَلَ رَبُّكَ بِعَادٍ', 'ألم تر كيف فعل ربك بعاد', 30, 60, 593, 237, '89:6'),
(6000, NULL, NULL, 89, 7, 'إِرَمَ ذَاتِ ٱلْعِمَادِ', 'إرم ذات العماد', 30, 60, 593, 237, '89:7'),
(6001, NULL, NULL, 89, 8, 'ٱلَّتِى لَمْ يُخْلَقْ مِثْلُهَا فِى ٱلْبِلَٰدِ', 'التي لم يخلق مثلها في البلاد', 30, 60, 593, 237, '89:8'),
(6002, NULL, NULL, 89, 9, 'وَثَمُودَ ٱلَّذِينَ جَابُوا۟ ٱلصَّخْرَ بِٱلْوَادِ', 'وثمود الذين جابوا الصخر بالواد', 30, 60, 593, 237, '89:9'),
(6003, NULL, NULL, 89, 10, 'وَفِرْعَوْنَ ذِى ٱلْأَوْتَادِ', 'وفرعون ذي الأوتاد', 30, 60, 593, 237, '89:10'),
(6004, NULL, NULL, 89, 11, 'ٱلَّذِينَ طَغَوْا۟ فِى ٱلْبِلَٰدِ', 'الذين طغوا في البلاد', 30, 60, 593, 237, '89:11'),
(6005, NULL, NULL, 89, 12, 'فَأَكْثَرُوا۟ فِيهَا ٱلْفَسَادَ', 'فأكثروا فيها الفساد', 30, 60, 593, 237, '89:12'),
(6006, NULL, NULL, 89, 13, 'فَصَبَّ عَلَيْهِمْ رَبُّكَ سَوْطَ عَذَابٍ', 'فصب عليهم ربك سوط عذاب', 30, 60, 593, 237, '89:13'),
(6007, NULL, NULL, 89, 14, 'إِنَّ رَبَّكَ لَبِٱلْمِرْصَادِ', 'إن ربك لبالمرصاد', 30, 60, 593, 237, '89:14'),
(6008, NULL, NULL, 89, 15, 'فَأَمَّا ٱلْإِنسَٰنُ إِذَا مَا ٱبْتَلَىٰهُ رَبُّهُۥ فَأَكْرَمَهُۥ وَنَعَّمَهُۥ فَيَقُولُ رَبِّىٓ أَكْرَمَنِ', 'فأما الإنسان إذا ما ابتلاه ربه فأكرمه ونعمه فيقول ربي أكرمن', 30, 60, 593, 237, '89:15'),
(6009, NULL, NULL, 89, 16, 'وَأَمَّآ إِذَا مَا ٱبْتَلَىٰهُ فَقَدَرَ عَلَيْهِ رِزْقَهُۥ فَيَقُولُ رَبِّىٓ أَهَٰنَنِ', 'وأما إذا ما ابتلاه فقدر عليه رزقه فيقول ربي أهانن', 30, 60, 593, 237, '89:16'),
(6010, NULL, NULL, 89, 17, 'كَلَّا بَل لَّا تُكْرِمُونَ ٱلْيَتِيمَ', 'كلا بل لا تكرمون اليتيم', 30, 60, 593, 237, '89:17'),
(6011, NULL, NULL, 89, 18, 'وَلَا تَحَٰٓضُّونَ عَلَىٰ طَعَامِ ٱلْمِسْكِينِ', 'ولا تحاضون على طعام المسكين', 30, 60, 593, 237, '89:18'),
(6012, NULL, NULL, 89, 19, 'وَتَأْكُلُونَ ٱلتُّرَاثَ أَكْلًا لَّمًّا', 'وتأكلون التراث أكلا لما', 30, 60, 593, 237, '89:19'),
(6013, NULL, NULL, 89, 20, 'وَتُحِبُّونَ ٱلْمَالَ حُبًّا جَمًّا', 'وتحبون المال حبا جما', 30, 60, 593, 237, '89:20'),
(6014, NULL, NULL, 89, 21, 'كَلَّآ إِذَا دُكَّتِ ٱلْأَرْضُ دَكًّا دَكًّا', 'كلا إذا دكت الأرض دكا دكا', 30, 60, 593, 237, '89:21'),
(6015, NULL, NULL, 89, 22, 'وَجَآءَ رَبُّكَ وَٱلْمَلَكُ صَفًّا صَفًّا', 'وجاء ربك والملك صفا صفا', 30, 60, 593, 237, '89:22'),
(6016, NULL, NULL, 89, 23, 'وَجِا۟ىٓءَ يَوْمَئِذٍۭ بِجَهَنَّمَ يَوْمَئِذٍ يَتَذَكَّرُ ٱلْإِنسَٰنُ وَأَنَّىٰ لَهُ ٱلذِّكْرَىٰ', 'وجيء يومئذ بجهنم يومئذ يتذكر الإنسان وأنى له الذكرى', 30, 60, 593, 237, '89:23'),
(6017, NULL, NULL, 89, 24, 'يَقُولُ يَٰلَيْتَنِى قَدَّمْتُ لِحَيَاتِى', 'يقول يا ليتني قدمت لحياتي', 30, 60, 594, 237, '89:24'),
(6018, NULL, NULL, 89, 25, 'فَيَوْمَئِذٍ لَّا يُعَذِّبُ عَذَابَهُۥٓ أَحَدٌ', 'فيومئذ لا يعذب عذابه أحد', 30, 60, 594, 237, '89:25'),
(6019, NULL, NULL, 89, 26, 'وَلَا يُوثِقُ وَثَاقَهُۥٓ أَحَدٌ', 'ولا يوثق وثاقه أحد', 30, 60, 594, 237, '89:26'),
(6020, NULL, NULL, 89, 27, 'يَٰٓأَيَّتُهَا ٱلنَّفْسُ ٱلْمُطْمَئِنَّةُ', 'يا أيتها النفس المطمئنة', 30, 60, 594, 237, '89:27'),
(6021, NULL, NULL, 89, 28, 'ٱرْجِعِىٓ إِلَىٰ رَبِّكِ رَاضِيَةً مَّرْضِيَّةً', 'ارجعي إلى ربك راضية مرضية', 30, 60, 594, 237, '89:28'),
(6022, NULL, NULL, 89, 29, 'فَٱدْخُلِى فِى عِبَٰدِى', 'فادخلي في عبادي', 30, 60, 594, 237, '89:29'),
(6023, NULL, NULL, 89, 30, 'وَٱدْخُلِى جَنَّتِى', 'وادخلي جنتي', 30, 60, 594, 237, '89:30'),
(6024, NULL, NULL, 90, 1, 'لَآ أُقْسِمُ بِهَٰذَا ٱلْبَلَدِ', 'لا أقسم بهذا البلد', 30, 60, 594, 238, '90:1'),
(6025, NULL, NULL, 90, 2, 'وَأَنتَ حِلٌّۢ بِهَٰذَا ٱلْبَلَدِ', 'وأنت حل بهذا البلد', 30, 60, 594, 238, '90:2'),
(6026, NULL, NULL, 90, 3, 'وَوَالِدٍ وَمَا وَلَدَ', 'ووالد وما ولد', 30, 60, 594, 238, '90:3'),
(6027, NULL, NULL, 90, 4, 'لَقَدْ خَلَقْنَا ٱلْإِنسَٰنَ فِى كَبَدٍ', 'لقد خلقنا الإنسان في كبد', 30, 60, 594, 238, '90:4'),
(6028, NULL, NULL, 90, 5, 'أَيَحْسَبُ أَن لَّن يَقْدِرَ عَلَيْهِ أَحَدٌ', 'أيحسب أن لن يقدر عليه أحد', 30, 60, 594, 238, '90:5'),
(6029, NULL, NULL, 90, 6, 'يَقُولُ أَهْلَكْتُ مَالًا لُّبَدًا', 'يقول أهلكت مالا لبدا', 30, 60, 594, 238, '90:6'),
(6030, NULL, NULL, 90, 7, 'أَيَحْسَبُ أَن لَّمْ يَرَهُۥٓ أَحَدٌ', 'أيحسب أن لم يره أحد', 30, 60, 594, 238, '90:7'),
(6031, NULL, NULL, 90, 8, 'أَلَمْ نَجْعَل لَّهُۥ عَيْنَيْنِ', 'ألم نجعل له عينين', 30, 60, 594, 238, '90:8'),
(6032, NULL, NULL, 90, 9, 'وَلِسَانًا وَشَفَتَيْنِ', 'ولسانا وشفتين', 30, 60, 594, 238, '90:9'),
(6033, NULL, NULL, 90, 10, 'وَهَدَيْنَٰهُ ٱلنَّجْدَيْنِ', 'وهديناه النجدين', 30, 60, 594, 238, '90:10'),
(6034, NULL, NULL, 90, 11, 'فَلَا ٱقْتَحَمَ ٱلْعَقَبَةَ', 'فلا اقتحم العقبة', 30, 60, 594, 238, '90:11'),
(6035, NULL, NULL, 90, 12, 'وَمَآ أَدْرَىٰكَ مَا ٱلْعَقَبَةُ', 'وما أدراك ما العقبة', 30, 60, 594, 238, '90:12'),
(6036, NULL, NULL, 90, 13, 'فَكُّ رَقَبَةٍ', 'فك رقبة', 30, 60, 594, 238, '90:13'),
(6037, NULL, NULL, 90, 14, 'أَوْ إِطْعَٰمٌ فِى يَوْمٍ ذِى مَسْغَبَةٍ', 'أو إطعام في يوم ذي مسغبة', 30, 60, 594, 238, '90:14'),
(6038, NULL, NULL, 90, 15, 'يَتِيمًا ذَا مَقْرَبَةٍ', 'يتيما ذا مقربة', 30, 60, 594, 238, '90:15'),
(6039, NULL, NULL, 90, 16, 'أَوْ مِسْكِينًا ذَا مَتْرَبَةٍ', 'أو مسكينا ذا متربة', 30, 60, 594, 238, '90:16'),
(6040, NULL, NULL, 90, 17, 'ثُمَّ كَانَ مِنَ ٱلَّذِينَ ءَامَنُوا۟ وَتَوَاصَوْا۟ بِٱلصَّبْرِ وَتَوَاصَوْا۟ بِٱلْمَرْحَمَةِ', 'ثم كان من الذين آمنوا وتواصوا بالصبر وتواصوا بالمرحمة', 30, 60, 594, 238, '90:17'),
(6041, NULL, NULL, 90, 18, 'أُو۟لَٰٓئِكَ أَصْحَٰبُ ٱلْمَيْمَنَةِ', 'أولئك أصحاب الميمنة', 30, 60, 594, 238, '90:18'),
(6042, NULL, NULL, 90, 19, 'وَٱلَّذِينَ كَفَرُوا۟ بِـَٔايَٰتِنَا هُمْ أَصْحَٰبُ ٱلْمَشْـَٔمَةِ', 'والذين كفروا بآياتنا هم أصحاب المشأمة', 30, 60, 594, 238, '90:19'),
(6043, NULL, NULL, 90, 20, 'عَلَيْهِمْ نَارٌ مُّؤْصَدَةٌۢ', 'عليهم نار مؤصدة', 30, 60, 594, 238, '90:20'),
(6044, NULL, NULL, 91, 1, 'وَٱلشَّمْسِ وَضُحَىٰهَا', 'والشمس وضحاها', 30, 60, 595, 238, '91:1'),
(6045, NULL, NULL, 91, 2, 'وَٱلْقَمَرِ إِذَا تَلَىٰهَا', 'والقمر إذا تلاها', 30, 60, 595, 238, '91:2'),
(6046, NULL, NULL, 91, 3, 'وَٱلنَّهَارِ إِذَا جَلَّىٰهَا', 'والنهار إذا جلاها', 30, 60, 595, 238, '91:3'),
(6047, NULL, NULL, 91, 4, 'وَٱلَّيْلِ إِذَا يَغْشَىٰهَا', 'والليل إذا يغشاها', 30, 60, 595, 238, '91:4'),
(6048, NULL, NULL, 91, 5, 'وَٱلسَّمَآءِ وَمَا بَنَىٰهَا', 'والسماء وما بناها', 30, 60, 595, 238, '91:5'),
(6049, NULL, NULL, 91, 6, 'وَٱلْأَرْضِ وَمَا طَحَىٰهَا', 'والأرض وما طحاها', 30, 60, 595, 238, '91:6'),
(6050, NULL, NULL, 91, 7, 'وَنَفْسٍ وَمَا سَوَّىٰهَا', 'ونفس وما سواها', 30, 60, 595, 238, '91:7'),
(6051, NULL, NULL, 91, 8, 'فَأَلْهَمَهَا فُجُورَهَا وَتَقْوَىٰهَا', 'فألهمها فجورها وتقواها', 30, 60, 595, 238, '91:8'),
(6052, NULL, NULL, 91, 9, 'قَدْ أَفْلَحَ مَن زَكَّىٰهَا', 'قد أفلح من زكاها', 30, 60, 595, 238, '91:9'),
(6053, NULL, NULL, 91, 10, 'وَقَدْ خَابَ مَن دَسَّىٰهَا', 'وقد خاب من دساها', 30, 60, 595, 238, '91:10'),
(6054, NULL, NULL, 91, 11, 'كَذَّبَتْ ثَمُودُ بِطَغْوَىٰهَآ', 'كذبت ثمود بطغواها', 30, 60, 595, 238, '91:11'),
(6055, NULL, NULL, 91, 12, 'إِذِ ٱنۢبَعَثَ أَشْقَىٰهَا', 'إذ انبعث أشقاها', 30, 60, 595, 238, '91:12'),
(6056, NULL, NULL, 91, 13, 'فَقَالَ لَهُمْ رَسُولُ ٱللَّهِ نَاقَةَ ٱللَّهِ وَسُقْيَٰهَا', 'فقال لهم رسول الله ناقة الله وسقياها', 30, 60, 595, 238, '91:13'),
(6057, NULL, NULL, 91, 14, 'فَكَذَّبُوهُ فَعَقَرُوهَا فَدَمْدَمَ عَلَيْهِمْ رَبُّهُم بِذَنۢبِهِمْ فَسَوَّىٰهَا', 'فكذبوه فعقروها فدمدم عليهم ربهم بذنبهم فسواها', 30, 60, 595, 238, '91:14'),
(6058, NULL, NULL, 91, 15, 'وَلَا يَخَافُ عُقْبَٰهَا', 'ولا يخاف عقباها', 30, 60, 595, 238, '91:15'),
(6059, NULL, NULL, 92, 1, 'وَٱلَّيْلِ إِذَا يَغْشَىٰ', 'والليل إذا يغشى', 30, 60, 595, 238, '92:1'),
(6060, NULL, NULL, 92, 2, 'وَٱلنَّهَارِ إِذَا تَجَلَّىٰ', 'والنهار إذا تجلى', 30, 60, 595, 238, '92:2'),
(6061, NULL, NULL, 92, 3, 'وَمَا خَلَقَ ٱلذَّكَرَ وَٱلْأُنثَىٰٓ', 'وما خلق الذكر والأنثى', 30, 60, 595, 238, '92:3'),
(6062, NULL, NULL, 92, 4, 'إِنَّ سَعْيَكُمْ لَشَتَّىٰ', 'إن سعيكم لشتى', 30, 60, 595, 238, '92:4'),
(6063, NULL, NULL, 92, 5, 'فَأَمَّا مَنْ أَعْطَىٰ وَٱتَّقَىٰ', 'فأما من أعطى واتقى', 30, 60, 595, 238, '92:5'),
(6064, NULL, NULL, 92, 6, 'وَصَدَّقَ بِٱلْحُسْنَىٰ', 'وصدق بالحسنى', 30, 60, 595, 238, '92:6'),
(6065, NULL, NULL, 92, 7, 'فَسَنُيَسِّرُهُۥ لِلْيُسْرَىٰ', 'فسنيسره لليسرى', 30, 60, 595, 238, '92:7'),
(6066, NULL, NULL, 92, 8, 'وَأَمَّا مَنۢ بَخِلَ وَٱسْتَغْنَىٰ', 'وأما من بخل واستغنى', 30, 60, 595, 238, '92:8'),
(6067, NULL, NULL, 92, 9, 'وَكَذَّبَ بِٱلْحُسْنَىٰ', 'وكذب بالحسنى', 30, 60, 595, 238, '92:9'),
(6068, NULL, NULL, 92, 10, 'فَسَنُيَسِّرُهُۥ لِلْعُسْرَىٰ', 'فسنيسره للعسرى', 30, 60, 595, 238, '92:10'),
(6069, NULL, NULL, 92, 11, 'وَمَا يُغْنِى عَنْهُ مَالُهُۥٓ إِذَا تَرَدَّىٰٓ', 'وما يغني عنه ماله إذا تردى', 30, 60, 595, 238, '92:11'),
(6070, NULL, NULL, 92, 12, 'إِنَّ عَلَيْنَا لَلْهُدَىٰ', 'إن علينا للهدى', 30, 60, 595, 238, '92:12'),
(6071, NULL, NULL, 92, 13, 'وَإِنَّ لَنَا لَلْءَاخِرَةَ وَٱلْأُولَىٰ', 'وإن لنا للآخرة والأولى', 30, 60, 595, 238, '92:13'),
(6072, NULL, NULL, 92, 14, 'فَأَنذَرْتُكُمْ نَارًا تَلَظَّىٰ', 'فأنذرتكم نارا تلظى', 30, 60, 595, 238, '92:14'),
(6073, NULL, NULL, 92, 15, 'لَا يَصْلَىٰهَآ إِلَّا ٱلْأَشْقَى', 'لا يصلاها إلا الأشقى', 30, 60, 596, 238, '92:15'),
(6074, NULL, NULL, 92, 16, 'ٱلَّذِى كَذَّبَ وَتَوَلَّىٰ', 'الذي كذب وتولى', 30, 60, 596, 238, '92:16'),
(6075, NULL, NULL, 92, 17, 'وَسَيُجَنَّبُهَا ٱلْأَتْقَى', 'وسيجنبها الأتقى', 30, 60, 596, 238, '92:17'),
(6076, NULL, NULL, 92, 18, 'ٱلَّذِى يُؤْتِى مَالَهُۥ يَتَزَكَّىٰ', 'الذي يؤتي ماله يتزكى', 30, 60, 596, 238, '92:18'),
(6077, NULL, NULL, 92, 19, 'وَمَا لِأَحَدٍ عِندَهُۥ مِن نِّعْمَةٍ تُجْزَىٰٓ', 'وما لأحد عنده من نعمة تجزى', 30, 60, 596, 238, '92:19'),
(6078, NULL, NULL, 92, 20, 'إِلَّا ٱبْتِغَآءَ وَجْهِ رَبِّهِ ٱلْأَعْلَىٰ', 'إلا ابتغاء وجه ربه الأعلى', 30, 60, 596, 238, '92:20'),
(6079, NULL, NULL, 92, 21, 'وَلَسَوْفَ يَرْضَىٰ', 'ولسوف يرضى', 30, 60, 596, 238, '92:21'),
(6080, NULL, NULL, 93, 1, 'وَٱلضُّحَىٰ', 'والضحى', 30, 60, 596, 238, '93:1'),
(6081, NULL, NULL, 93, 2, 'وَٱلَّيْلِ إِذَا سَجَىٰ', 'والليل إذا سجى', 30, 60, 596, 238, '93:2'),
(6082, NULL, NULL, 93, 3, 'مَا وَدَّعَكَ رَبُّكَ وَمَا قَلَىٰ', 'ما ودعك ربك وما قلى', 30, 60, 596, 238, '93:3'),
(6083, NULL, NULL, 93, 4, 'وَلَلْءَاخِرَةُ خَيْرٌ لَّكَ مِنَ ٱلْأُولَىٰ', 'وللآخرة خير لك من الأولى', 30, 60, 596, 238, '93:4'),
(6084, NULL, NULL, 93, 5, 'وَلَسَوْفَ يُعْطِيكَ رَبُّكَ فَتَرْضَىٰٓ', 'ولسوف يعطيك ربك فترضى', 30, 60, 596, 238, '93:5'),
(6085, NULL, NULL, 93, 6, 'أَلَمْ يَجِدْكَ يَتِيمًا فَـَٔاوَىٰ', 'ألم يجدك يتيما فآوى', 30, 60, 596, 238, '93:6'),
(6086, NULL, NULL, 93, 7, 'وَوَجَدَكَ ضَآلًّا فَهَدَىٰ', 'ووجدك ضالا فهدى', 30, 60, 596, 238, '93:7'),
(6087, NULL, NULL, 93, 8, 'وَوَجَدَكَ عَآئِلًا فَأَغْنَىٰ', 'ووجدك عائلا فأغنى', 30, 60, 596, 238, '93:8'),
(6088, NULL, NULL, 93, 9, 'فَأَمَّا ٱلْيَتِيمَ فَلَا تَقْهَرْ', 'فأما اليتيم فلا تقهر', 30, 60, 596, 238, '93:9'),
(6089, NULL, NULL, 93, 10, 'وَأَمَّا ٱلسَّآئِلَ فَلَا تَنْهَرْ', 'وأما السائل فلا تنهر', 30, 60, 596, 238, '93:10'),
(6090, NULL, NULL, 93, 11, 'وَأَمَّا بِنِعْمَةِ رَبِّكَ فَحَدِّثْ', 'وأما بنعمة ربك فحدث', 30, 60, 596, 238, '93:11'),
(6091, NULL, NULL, 94, 1, 'أَلَمْ نَشْرَحْ لَكَ صَدْرَكَ', 'ألم نشرح لك صدرك', 30, 60, 596, 239, '94:1'),
(6092, NULL, NULL, 94, 2, 'وَوَضَعْنَا عَنكَ وِزْرَكَ', 'ووضعنا عنك وزرك', 30, 60, 596, 239, '94:2'),
(6093, NULL, NULL, 94, 3, 'ٱلَّذِىٓ أَنقَضَ ظَهْرَكَ', 'الذي أنقض ظهرك', 30, 60, 596, 239, '94:3'),
(6094, NULL, NULL, 94, 4, 'وَرَفَعْنَا لَكَ ذِكْرَكَ', 'ورفعنا لك ذكرك', 30, 60, 596, 239, '94:4'),
(6095, NULL, NULL, 94, 5, 'فَإِنَّ مَعَ ٱلْعُسْرِ يُسْرًا', 'فإن مع العسر يسرا', 30, 60, 596, 239, '94:5'),
(6096, NULL, NULL, 94, 6, 'إِنَّ مَعَ ٱلْعُسْرِ يُسْرًا', 'إن مع العسر يسرا', 30, 60, 596, 239, '94:6'),
(6097, NULL, NULL, 94, 7, 'فَإِذَا فَرَغْتَ فَٱنصَبْ', 'فإذا فرغت فانصب', 30, 60, 596, 239, '94:7'),
(6098, NULL, NULL, 94, 8, 'وَإِلَىٰ رَبِّكَ فَٱرْغَب', 'وإلى ربك فارغب', 30, 60, 596, 239, '94:8'),
(6099, NULL, NULL, 95, 1, 'وَٱلتِّينِ وَٱلزَّيْتُونِ', 'والتين والزيتون', 30, 60, 597, 239, '95:1'),
(6100, NULL, NULL, 95, 2, 'وَطُورِ سِينِينَ', 'وطور سينين', 30, 60, 597, 239, '95:2'),
(6101, NULL, NULL, 95, 3, 'وَهَٰذَا ٱلْبَلَدِ ٱلْأَمِينِ', 'وهذا البلد الأمين', 30, 60, 597, 239, '95:3'),
(6102, NULL, NULL, 95, 4, 'لَقَدْ خَلَقْنَا ٱلْإِنسَٰنَ فِىٓ أَحْسَنِ تَقْوِيمٍ', 'لقد خلقنا الإنسان في أحسن تقويم', 30, 60, 597, 239, '95:4'),
(6103, NULL, NULL, 95, 5, 'ثُمَّ رَدَدْنَٰهُ أَسْفَلَ سَٰفِلِينَ', 'ثم رددناه أسفل سافلين', 30, 60, 597, 239, '95:5'),
(6104, NULL, NULL, 95, 6, 'إِلَّا ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ فَلَهُمْ أَجْرٌ غَيْرُ مَمْنُونٍ', 'إلا الذين آمنوا وعملوا الصالحات فلهم أجر غير ممنون', 30, 60, 597, 239, '95:6'),
(6105, NULL, NULL, 95, 7, 'فَمَا يُكَذِّبُكَ بَعْدُ بِٱلدِّينِ', 'فما يكذبك بعد بالدين', 30, 60, 597, 239, '95:7'),
(6106, NULL, NULL, 95, 8, 'أَلَيْسَ ٱللَّهُ بِأَحْكَمِ ٱلْحَٰكِمِينَ', 'أليس الله بأحكم الحاكمين', 30, 60, 597, 239, '95:8'),
(6107, NULL, NULL, 96, 1, 'ٱقْرَأْ بِٱسْمِ رَبِّكَ ٱلَّذِى خَلَقَ', 'اقرأ باسم ربك الذي خلق', 30, 60, 597, 239, '96:1'),
(6108, NULL, NULL, 96, 2, 'خَلَقَ ٱلْإِنسَٰنَ مِنْ عَلَقٍ', 'خلق الإنسان من علق', 30, 60, 597, 239, '96:2'),
(6109, NULL, NULL, 96, 3, 'ٱقْرَأْ وَرَبُّكَ ٱلْأَكْرَمُ', 'اقرأ وربك الأكرم', 30, 60, 597, 239, '96:3'),
(6110, NULL, NULL, 96, 4, 'ٱلَّذِى عَلَّمَ بِٱلْقَلَمِ', 'الذي علم بالقلم', 30, 60, 597, 239, '96:4'),
(6111, NULL, NULL, 96, 5, 'عَلَّمَ ٱلْإِنسَٰنَ مَا لَمْ يَعْلَمْ', 'علم الإنسان ما لم يعلم', 30, 60, 597, 239, '96:5'),
(6112, NULL, NULL, 96, 6, 'كَلَّآ إِنَّ ٱلْإِنسَٰنَ لَيَطْغَىٰٓ', 'كلا إن الإنسان ليطغى', 30, 60, 597, 239, '96:6'),
(6113, NULL, NULL, 96, 7, 'أَن رَّءَاهُ ٱسْتَغْنَىٰٓ', 'أن رآه استغنى', 30, 60, 597, 239, '96:7'),
(6114, NULL, NULL, 96, 8, 'إِنَّ إِلَىٰ رَبِّكَ ٱلرُّجْعَىٰٓ', 'إن إلى ربك الرجعى', 30, 60, 597, 239, '96:8'),
(6115, NULL, NULL, 96, 9, 'أَرَءَيْتَ ٱلَّذِى يَنْهَىٰ', 'أرأيت الذي ينهى', 30, 60, 597, 239, '96:9'),
(6116, NULL, NULL, 96, 10, 'عَبْدًا إِذَا صَلَّىٰٓ', 'عبدا إذا صلى', 30, 60, 597, 239, '96:10'),
(6117, NULL, NULL, 96, 11, 'أَرَءَيْتَ إِن كَانَ عَلَى ٱلْهُدَىٰٓ', 'أرأيت إن كان على الهدى', 30, 60, 597, 239, '96:11'),
(6118, NULL, NULL, 96, 12, 'أَوْ أَمَرَ بِٱلتَّقْوَىٰٓ', 'أو أمر بالتقوى', 30, 60, 597, 239, '96:12'),
(6119, NULL, NULL, 96, 13, 'أَرَءَيْتَ إِن كَذَّبَ وَتَوَلَّىٰٓ', 'أرأيت إن كذب وتولى', 30, 60, 597, 239, '96:13'),
(6120, NULL, NULL, 96, 14, 'أَلَمْ يَعْلَم بِأَنَّ ٱللَّهَ يَرَىٰ', 'ألم يعلم بأن الله يرى', 30, 60, 597, 239, '96:14'),
(6121, NULL, NULL, 96, 15, 'كَلَّا لَئِن لَّمْ يَنتَهِ لَنَسْفَعًۢا بِٱلنَّاصِيَةِ', 'كلا لئن لم ينته لنسفعا بالناصية', 30, 60, 597, 239, '96:15'),
(6122, NULL, NULL, 96, 16, 'نَاصِيَةٍ كَٰذِبَةٍ خَاطِئَةٍ', 'ناصية كاذبة خاطئة', 30, 60, 597, 239, '96:16'),
(6123, NULL, NULL, 96, 17, 'فَلْيَدْعُ نَادِيَهُۥ', 'فليدع ناديه', 30, 60, 597, 239, '96:17'),
(6124, NULL, NULL, 96, 18, 'سَنَدْعُ ٱلزَّبَانِيَةَ', 'سندع الزبانية', 30, 60, 597, 239, '96:18'),
(6125, NULL, NULL, 96, 19, 'كَلَّا لَا تُطِعْهُ وَٱسْجُدْ وَٱقْتَرِب', 'كلا لا تطعه واسجد واقترب', 30, 60, 597, 239, '96:19'),
(6126, NULL, NULL, 97, 1, 'بِّسْمِ ٱللَّهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ إِنَّآ أَنزَلْنَٰهُ فِى لَيْلَةِ ٱلْقَدْرِ', 'إنا أنزلناه في ليلة القدر', 30, 60, 598, 239, '97:1'),
(6127, NULL, NULL, 97, 2, 'وَمَآ أَدْرَىٰكَ مَا لَيْلَةُ ٱلْقَدْرِ', 'وما أدراك ما ليلة القدر', 30, 60, 598, 239, '97:2'),
(6128, NULL, NULL, 97, 3, 'لَيْلَةُ ٱلْقَدْرِ خَيْرٌ مِّنْ أَلْفِ شَهْرٍ', 'ليلة القدر خير من ألف شهر', 30, 60, 598, 239, '97:3'),
(6129, NULL, NULL, 97, 4, 'تَنَزَّلُ ٱلْمَلَٰٓئِكَةُ وَٱلرُّوحُ فِيهَا بِإِذْنِ رَبِّهِم مِّن كُلِّ أَمْرٍ', 'تنزل الملائكة والروح فيها بإذن ربهم من كل أمر', 30, 60, 598, 239, '97:4'),
(6130, NULL, NULL, 97, 5, 'سَلَٰمٌ هِىَ حَتَّىٰ مَطْلَعِ ٱلْفَجْرِ', 'سلام هي حتى مطلع الفجر', 30, 60, 598, 239, '97:5'),
(6131, NULL, NULL, 98, 1, 'لَمْ يَكُنِ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَٰبِ وَٱلْمُشْرِكِينَ مُنفَكِّينَ حَتَّىٰ تَأْتِيَهُمُ ٱلْبَيِّنَةُ', 'لم يكن الذين كفروا من أهل الكتاب والمشركين منفكين حتى تأتيهم البينة', 30, 60, 598, 239, '98:1'),
(6132, NULL, NULL, 98, 2, 'رَسُولٌ مِّنَ ٱللَّهِ يَتْلُوا۟ صُحُفًا مُّطَهَّرَةً', 'رسول من الله يتلو صحفا مطهرة', 30, 60, 598, 239, '98:2'),
(6133, NULL, NULL, 98, 3, 'فِيهَا كُتُبٌ قَيِّمَةٌ', 'فيها كتب قيمة', 30, 60, 598, 239, '98:3'),
(6134, NULL, NULL, 98, 4, 'وَمَا تَفَرَّقَ ٱلَّذِينَ أُوتُوا۟ ٱلْكِتَٰبَ إِلَّا مِنۢ بَعْدِ مَا جَآءَتْهُمُ ٱلْبَيِّنَةُ', 'وما تفرق الذين أوتوا الكتاب إلا من بعد ما جاءتهم البينة', 30, 60, 598, 239, '98:4'),
(6135, NULL, NULL, 98, 5, 'وَمَآ أُمِرُوٓا۟ إِلَّا لِيَعْبُدُوا۟ ٱللَّهَ مُخْلِصِينَ لَهُ ٱلدِّينَ حُنَفَآءَ وَيُقِيمُوا۟ ٱلصَّلَوٰةَ وَيُؤْتُوا۟ ٱلزَّكَوٰةَ وَذَٰلِكَ دِينُ ٱلْقَيِّمَةِ', 'وما أمروا إلا ليعبدوا الله مخلصين له الدين حنفاء ويقيموا الصلاة ويؤتوا الزكاة وذلك دين القيمة', 30, 60, 598, 239, '98:5'),
(6136, NULL, NULL, 98, 6, 'إِنَّ ٱلَّذِينَ كَفَرُوا۟ مِنْ أَهْلِ ٱلْكِتَٰبِ وَٱلْمُشْرِكِينَ فِى نَارِ جَهَنَّمَ خَٰلِدِينَ فِيهَآ أُو۟لَٰٓئِكَ هُمْ شَرُّ ٱلْبَرِيَّةِ', 'إن الذين كفروا من أهل الكتاب والمشركين في نار جهنم خالدين فيها أولئك هم شر البرية', 30, 60, 598, 239, '98:6'),
(6137, NULL, NULL, 98, 7, 'إِنَّ ٱلَّذِينَ ءَامَنُوا۟ وَعَمِلُوا۟ ٱلصَّٰلِحَٰتِ أُو۟لَٰٓئِكَ هُمْ خَيْرُ ٱلْبَرِيَّةِ', 'إن الذين آمنوا وعملوا الصالحات أولئك هم خير البرية', 30, 60, 598, 239, '98:7'),
(6138, NULL, NULL, 98, 8, 'جَزَآؤُهُمْ عِندَ رَبِّهِمْ جَنَّٰتُ عَدْنٍ تَجْرِى مِن تَحْتِهَا ٱلْأَنْهَٰرُ خَٰلِدِينَ فِيهَآ أَبَدًا رَّضِىَ ٱللَّهُ عَنْهُمْ وَرَضُوا۟ عَنْهُ ذَٰلِكَ لِمَنْ خَشِىَ رَبَّهُۥ', 'جزاؤهم عند ربهم جنات عدن تجري من تحتها الأنهار خالدين فيها أبدا رضي الله عنهم ورضوا عنه ذلك لمن خشي ربه', 30, 60, 599, 239, '98:8'),
(6139, NULL, NULL, 99, 1, 'إِذَا زُلْزِلَتِ ٱلْأَرْضُ زِلْزَالَهَا', 'إذا زلزلت الأرض زلزالها', 30, 60, 599, 239, '99:1'),
(6140, NULL, NULL, 99, 2, 'وَأَخْرَجَتِ ٱلْأَرْضُ أَثْقَالَهَا', 'وأخرجت الأرض أثقالها', 30, 60, 599, 239, '99:2'),
(6141, NULL, NULL, 99, 3, 'وَقَالَ ٱلْإِنسَٰنُ مَا لَهَا', 'وقال الإنسان ما لها', 30, 60, 599, 239, '99:3'),
(6142, NULL, NULL, 99, 4, 'يَوْمَئِذٍ تُحَدِّثُ أَخْبَارَهَا', 'يومئذ تحدث أخبارها', 30, 60, 599, 239, '99:4'),
(6143, NULL, NULL, 99, 5, 'بِأَنَّ رَبَّكَ أَوْحَىٰ لَهَا', 'بأن ربك أوحى لها', 30, 60, 599, 239, '99:5'),
(6144, NULL, NULL, 99, 6, 'يَوْمَئِذٍ يَصْدُرُ ٱلنَّاسُ أَشْتَاتًا لِّيُرَوْا۟ أَعْمَٰلَهُمْ', 'يومئذ يصدر الناس أشتاتا ليروا أعمالهم', 30, 60, 599, 239, '99:6'),
(6145, NULL, NULL, 99, 7, 'فَمَن يَعْمَلْ مِثْقَالَ ذَرَّةٍ خَيْرًا يَرَهُۥ', 'فمن يعمل مثقال ذرة خيرا يره', 30, 60, 599, 239, '99:7'),
(6146, NULL, NULL, 99, 8, 'وَمَن يَعْمَلْ مِثْقَالَ ذَرَّةٍ شَرًّا يَرَهُۥ', 'ومن يعمل مثقال ذرة شرا يره', 30, 60, 599, 239, '99:8'),
(6147, NULL, NULL, 100, 1, 'وَٱلْعَٰدِيَٰتِ ضَبْحًا', 'والعاديات ضبحا', 30, 60, 599, 239, '100:1'),
(6148, NULL, NULL, 100, 2, 'فَٱلْمُورِيَٰتِ قَدْحًا', 'فالموريات قدحا', 30, 60, 599, 239, '100:2'),
(6149, NULL, NULL, 100, 3, 'فَٱلْمُغِيرَٰتِ صُبْحًا', 'فالمغيرات صبحا', 30, 60, 599, 239, '100:3'),
(6150, NULL, NULL, 100, 4, 'فَأَثَرْنَ بِهِۦ نَقْعًا', 'فأثرن به نقعا', 30, 60, 599, 239, '100:4'),
(6151, NULL, NULL, 100, 5, 'فَوَسَطْنَ بِهِۦ جَمْعًا', 'فوسطن به جمعا', 30, 60, 599, 239, '100:5'),
(6152, NULL, NULL, 100, 6, 'إِنَّ ٱلْإِنسَٰنَ لِرَبِّهِۦ لَكَنُودٌ', 'إن الإنسان لربه لكنود', 30, 60, 599, 239, '100:6'),
(6153, NULL, NULL, 100, 7, 'وَإِنَّهُۥ عَلَىٰ ذَٰلِكَ لَشَهِيدٌ', 'وإنه على ذلك لشهيد', 30, 60, 599, 239, '100:7'),
(6154, NULL, NULL, 100, 8, 'وَإِنَّهُۥ لِحُبِّ ٱلْخَيْرِ لَشَدِيدٌ', 'وإنه لحب الخير لشديد', 30, 60, 599, 239, '100:8'),
(6155, NULL, NULL, 100, 9, 'أَفَلَا يَعْلَمُ إِذَا بُعْثِرَ مَا فِى ٱلْقُبُورِ', 'أفلا يعلم إذا بعثر ما في القبور', 30, 60, 599, 240, '100:9'),
(6156, NULL, NULL, 100, 10, 'وَحُصِّلَ مَا فِى ٱلصُّدُورِ', 'وحصل ما في الصدور', 30, 60, 600, 240, '100:10'),
(6157, NULL, NULL, 100, 11, 'إِنَّ رَبَّهُم بِهِمْ يَوْمَئِذٍ لَّخَبِيرٌۢ', 'إن ربهم بهم يومئذ لخبير', 30, 60, 600, 240, '100:11'),
