(6162, NULL, NULL, 101, 5, 'وَتَكُونُ ٱلْجِبَالُ كَٱلْعِهْنِ ٱلْمَنفُوشِ', 'وتكون الجبال كالعهن المنفوش', 30, 60, 600, 240, '101:5'),
(6163, NULL, NULL, 101, 6, 'فَأَمَّا مَن ثَقُلَتْ مَوَٰزِينُهُۥ', 'فأما من ثقلت موازينه', 30, 60, 600, 240, '101:6'),
(6164, NULL, NULL, 101, 7, 'فَهُوَ فِى عِيشَةٍ رَّاضِيَةٍ', 'فهو في عيشة راضية', 30, 60, 600, 240, '101:7'),
(6165, NULL, NULL, 101, 8, 'وَأَمَّا مَنْ خَفَّتْ مَوَٰزِينُهُۥ', 'وأما من خفت موازينه', 30, 60, 600, 240, '101:8'),
