(6168, NULL, NULL, 101, 11, 'نَارٌ حَامِيَةٌۢ', 'نار حامية', 30, 60, 600, 240, '101:11'),
(6169, NULL, NULL, 102, 1, 'أَلْهَىٰكُمُ ٱلتَّكَاثُرُ', 'ألهاكم التكاثر', 30, 60, 600, 240, '102:1'),
(6170, NULL, NULL, 102, 2, 'حَتَّىٰ زُرْتُمُ ٱلْمَقَابِرَ', 'حتى زرتم المقابر', 30, 60, 600, 240, '102:2'),
(6171, NULL, NULL, 102, 3, 'كَلَّا سَوْفَ تَعْلَمُونَ', 'كلا سوف تعلمون', 30, 60, 600, 240, '102:3'),
(6172, NULL, NULL, 102, 4, 'ثُمَّ كَلَّا سَوْفَ تَعْلَمُونَ', 'ثم كلا سوف تعلمون', 30, 60, 600, 240, '102:4'),
(6173, NULL, NULL, 102, 5, 'كَلَّا لَوْ تَعْلَمُونَ عِلْمَ ٱلْيَقِينِ', 'كلا لو تعلمون علم اليقين', 30, 60, 600, 240, '102:5'),
(6174, NULL, NULL, 102, 6, 'لَتَرَوُنَّ ٱلْجَحِيمَ', 'لترون الجحيم', 30, 60, 600, 240, '102:6'),
